namespace Hafezoon8;
public class Soore
{
    public string Name = "";
    public List<string> Ayaat = new();
    public int Tartib;
}
public static class Quran
{
    public static List<Soore> Text = new();
    public static string Adad_to_English(string s)
    {
        s = s.Replace("١", "1").Replace("٢", "2").Replace("٣", "3").Replace("۴", "4").Replace("۵", "5");
        s = s.Replace("۶", "6").Replace("٧", "7").Replace("٨", "8").Replace("٩", "9").Replace("٠", "0");
        return s;
    }
    public static string Adad_to_Farsi(string s)
    {
        s = s.Replace("1", "١").Replace("2", "٢").Replace("3", "٣").Replace("4", "۴").Replace("5", "۵");
        s = s.Replace("6", "۶").Replace("7", "٧").Replace("8", "٨").Replace("9", "٩").Replace("0", "٠");
        return s;
    }
    private static int Index;
    private static void salam(Soore xx)
    {
        xx.Tartib = Index;
        Index++;
        Text.Add(xx);
    }
    public static void Init()
    {
        Index = 0;
        var sor = new Soore();
        sor.Name = "١ - حمد";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ");
        sor.Ayaat.Add("الْحَمْدُ لِلّٰهِ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("الرَّحْمٰنِ الرَّحِيمِ");
        sor.Ayaat.Add("مٰالِكِ يَوْمِ الدِّينِ");
        sor.Ayaat.Add("إِيّٰاكَ نَعْبُدُ وَ إِيّٰاكَ نَسْتَعِينُ");
        sor.Ayaat.Add("اهْدِنَا الصِّرٰاطَ الْمُسْتَقِيمَ");
        sor.Ayaat.Add("صِرٰاطَ الَّذِينَ أَنْعَمْتَ عَلَيْهِمْ غَيْرِ الْمَغْضُوبِ عَلَيْهِمْ وَ لاَ الضّٰالِّينَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢ - بقره";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الم");
        sor.Ayaat.Add("ذٰلِكَ الْكِتٰابُ لاٰ رَيْبَ فِيهِ هُدىً لِلْمُتَّقِينَ");
        sor.Ayaat.Add("الَّذِينَ يُؤْمِنُونَ بِالْغَيْبِ وَ يُقِيمُونَ الصَّلاٰةَ وَ مِمّٰا رَزَقْنٰاهُمْ يُنْفِقُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ يُؤْمِنُونَ بِمٰا أُنْزِلَ إِلَيْكَ وَ مٰا أُنْزِلَ مِنْ قَبْلِكَ وَ بِالْآخِرَةِ هُمْ يُوقِنُونَ");
        sor.Ayaat.Add("أُولٰئِكَ عَلىٰ هُدىً مِنْ رَبِّهِمْ وَ أُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا سَوٰاءٌ عَلَيْهِمْ أَ أَنْذَرْتَهُمْ أَمْ لَمْ تُنْذِرْهُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("خَتَمَ اللّٰهُ عَلىٰ قُلُوبِهِمْ وَ عَلىٰ سَمْعِهِمْ وَ عَلىٰ أَبْصٰارِهِمْ غِشٰاوَةٌ وَ لَهُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يَقُولُ آمَنّٰا بِاللّٰهِ وَ بِالْيَوْمِ الْآخِرِ وَ مٰا هُمْ بِمُؤْمِنِينَ");
        sor.Ayaat.Add("يُخٰادِعُونَ اللّٰهَ وَ الَّذِينَ آمَنُوا وَ مٰا يَخْدَعُونَ إِلاّٰ أَنْفُسَهُمْ وَ مٰا يَشْعُرُونَ");
        sor.Ayaat.Add("فِي قُلُوبِهِمْ مَرَضٌ فَزٰادَهُمُ اللّٰهُ مَرَضاً وَ لَهُمْ عَذٰابٌ أَلِيمٌ بِمٰا كٰانُوا يَكْذِبُونَ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمْ لاٰ تُفْسِدُوا فِي الْأَرْضِ قٰالُوا إِنَّمٰا نَحْنُ مُصْلِحُونَ");
        sor.Ayaat.Add("أَلاٰ إِنَّهُمْ هُمُ الْمُفْسِدُونَ وَ لٰكِنْ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمْ آمِنُوا كَمٰا آمَنَ النّٰاسُ قٰالُوا أَ نُؤْمِنُ كَمٰا آمَنَ السُّفَهٰاءُ أَلاٰ إِنَّهُمْ هُمُ السُّفَهٰاءُ وَ لٰكِنْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِذٰا لَقُوا الَّذِينَ آمَنُوا قٰالُوا آمَنّٰا وَ إِذٰا خَلَوْا إِلىٰ شَيٰاطِينِهِمْ قٰالُوا إِنّٰا مَعَكُمْ إِنَّمٰا نَحْنُ مُسْتَهْزِؤُنَ");
        sor.Ayaat.Add("اللّٰهُ يَسْتَهْزِئُ بِهِمْ وَ يَمُدُّهُمْ فِي طُغْيٰانِهِمْ يَعْمَهُونَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلاٰلَةَ بِالْهُدىٰ فَمٰا رَبِحَتْ تِجٰارَتُهُمْ وَ مٰا كٰانُوا مُهْتَدِينَ");
        sor.Ayaat.Add("مَثَلُهُمْ كَمَثَلِ الَّذِي اسْتَوْقَدَ نٰاراً فَلَمّٰا أَضٰاءَتْ مٰا حَوْلَهُ ذَهَبَ اللّٰهُ بِنُورِهِمْ وَ تَرَكَهُمْ فِي ظُلُمٰاتٍ لاٰ يُبْصِرُونَ");
        sor.Ayaat.Add("صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لاٰ يَرْجِعُونَ");
        sor.Ayaat.Add("أَوْ كَصَيِّبٍ مِنَ السَّمٰاءِ فِيهِ ظُلُمٰاتٌ وَ رَعْدٌ وَ بَرْقٌ يَجْعَلُونَ أَصٰابِعَهُمْ فِي آذٰانِهِمْ مِنَ الصَّوٰاعِقِ حَذَرَ الْمَوْتِ وَ اللّٰهُ مُحِيطٌ بِالْكٰافِرِينَ");
        sor.Ayaat.Add("يَكٰادُ الْبَرْقُ يَخْطَفُ أَبْصٰارَهُمْ كُلَّمٰا أَضٰاءَ لَهُمْ مَشَوْا فِيهِ وَ إِذٰا أَظْلَمَ عَلَيْهِمْ قٰامُوا وَ لَوْ شٰاءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَ أَبْصٰارِهِمْ إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ اعْبُدُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ وَ الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ");
        sor.Ayaat.Add("الَّذِي جَعَلَ لَكُمُ الْأَرْضَ فِرٰاشاً وَ السَّمٰاءَ بِنٰاءً وَ أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرٰاتِ رِزْقاً لَكُمْ فَلاٰ تَجْعَلُوا لِلّٰهِ أَنْدٰاداً وَ أَنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِنْ كُنْتُمْ فِي رَيْبٍ مِمّٰا نَزَّلْنٰا عَلىٰ عَبْدِنٰا فَأْتُوا بِسُورَةٍ مِنْ مِثْلِهِ وَ ادْعُوا شُهَدٰاءَكُمْ مِنْ دُونِ اللّٰهِ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("فَإِنْ لَمْ تَفْعَلُوا وَ لَنْ تَفْعَلُوا فَاتَّقُوا النّٰارَ الَّتِي وَقُودُهَا النّٰاسُ وَ الْحِجٰارَةُ أُعِدَّتْ لِلْكٰافِرِينَ");
        sor.Ayaat.Add("وَ بَشِّرِ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ أَنَّ لَهُمْ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ كُلَّمٰا رُزِقُوا مِنْهٰا مِنْ ثَمَرَةٍ رِزْقاً قٰالُوا هٰذَا الَّذِي رُزِقْنٰا مِنْ قَبْلُ وَ أُتُوا بِهِ مُتَشٰابِهاً وَ لَهُمْ فِيهٰا أَزْوٰاجٌ مُطَهَّرَةٌ وَ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لاٰ يَسْتَحْيِي أَنْ يَضْرِبَ مَثَلاً مٰا بَعُوضَةً فَمٰا فَوْقَهٰا فَأَمَّا الَّذِينَ آمَنُوا فَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ وَ أَمَّا الَّذِينَ كَفَرُوا فَيَقُولُونَ مٰاذٰا أَرٰادَ اللّٰهُ بِهٰذٰا مَثَلاً يُضِلُّ بِهِ كَثِيراً وَ يَهْدِي بِهِ كَثِيراً وَ مٰا يُضِلُّ بِهِ إِلاَّ الْفٰاسِقِينَ");
        sor.Ayaat.Add("الَّذِينَ يَنْقُضُونَ عَهْدَ اللّٰهِ مِنْ بَعْدِ مِيثٰاقِهِ وَ يَقْطَعُونَ مٰا أَمَرَ اللّٰهُ بِهِ أَنْ يُوصَلَ وَ يُفْسِدُونَ فِي الْأَرْضِ أُولٰئِكَ هُمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("كَيْفَ تَكْفُرُونَ بِاللّٰهِ وَ كُنْتُمْ أَمْوٰاتاً فَأَحْيٰاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ ثُمَّ إِلَيْهِ تُرْجَعُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي خَلَقَ لَكُمْ مٰا فِي الْأَرْضِ جَمِيعاً ثُمَّ اسْتَوىٰ إِلَى السَّمٰاءِ فَسَوّٰاهُنَّ سَبْعَ سَمٰاوٰاتٍ وَ هُوَ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ رَبُّكَ لِلْمَلاٰئِكَةِ إِنِّي جٰاعِلٌ فِي الْأَرْضِ خَلِيفَةً قٰالُوا أَ تَجْعَلُ فِيهٰا مَنْ يُفْسِدُ فِيهٰا وَ يَسْفِكُ الدِّمٰاءَ وَ نَحْنُ نُسَبِّحُ بِحَمْدِكَ وَ نُقَدِّسُ لَكَ قٰالَ إِنِّي أَعْلَمُ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ عَلَّمَ آدَمَ الْأَسْمٰاءَ كُلَّهٰا ثُمَّ عَرَضَهُمْ عَلَى الْمَلاٰئِكَةِ فَقٰالَ أَنْبِئُونِي بِأَسْمٰاءِ هٰؤُلاٰءِ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("قٰالُوا سُبْحٰانَكَ لاٰ عِلْمَ لَنٰا إِلاّٰ مٰا عَلَّمْتَنٰا إِنَّكَ أَنْتَ الْعَلِيمُ الْحَكِيمُ");
        sor.Ayaat.Add("قٰالَ يٰا آدَمُ أَنْبِئْهُمْ بِأَسْمٰائِهِمْ فَلَمّٰا أَنْبَأَهُمْ بِأَسْمٰائِهِمْ قٰالَ أَ لَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ غَيْبَ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ أَعْلَمُ مٰا تُبْدُونَ وَ مٰا كُنْتُمْ تَكْتُمُونَ");
        sor.Ayaat.Add("وَ إِذْ قُلْنٰا لِلْمَلاٰئِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاّٰ إِبْلِيسَ أَبىٰ وَ اسْتَكْبَرَ وَ كٰانَ مِنَ الْكٰافِرِينَ");
        sor.Ayaat.Add("وَ قُلْنٰا يٰا آدَمُ اسْكُنْ أَنْتَ وَ زَوْجُكَ الْجَنَّةَ وَ كُلاٰ مِنْهٰا رَغَداً حَيْثُ شِئْتُمٰا وَ لاٰ تَقْرَبٰا هٰذِهِ الشَّجَرَةَ فَتَكُونٰا مِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("فَأَزَلَّهُمَا الشَّيْطٰانُ عَنْهٰا فَأَخْرَجَهُمٰا مِمّٰا كٰانٰا فِيهِ وَ قُلْنَا اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَ لَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَ مَتٰاعٌ إِلىٰ حِينٍ");
        sor.Ayaat.Add("فَتَلَقّٰى آدَمُ مِنْ رَبِّهِ كَلِمٰاتٍ فَتٰابَ عَلَيْهِ إِنَّهُ هُوَ التَّوّٰابُ الرَّحِيمُ");
        sor.Ayaat.Add("قُلْنَا اهْبِطُوا مِنْهٰا جَمِيعاً فَإِمّٰا يَأْتِيَنَّكُمْ مِنِّي هُدىً فَمَنْ تَبِعَ هُدٰايَ فَلاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآيٰاتِنٰا أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("يٰا بَنِي إِسْرٰائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَ أَوْفُوا بِعَهْدِي أُوفِ بِعَهْدِكُمْ وَ إِيّٰايَ فَارْهَبُونِ");
        sor.Ayaat.Add("وَ آمِنُوا بِمٰا أَنْزَلْتُ مُصَدِّقاً لِمٰا مَعَكُمْ وَ لاٰ تَكُونُوا أَوَّلَ كٰافِرٍ بِهِ وَ لاٰ تَشْتَرُوا بِآيٰاتِي ثَمَناً قَلِيلاً وَ إِيّٰايَ فَاتَّقُونِ");
        sor.Ayaat.Add("وَ لاٰ تَلْبِسُوا الْحَقَّ بِالْبٰاطِلِ وَ تَكْتُمُوا الْحَقَّ وَ أَنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ أَقِيمُوا الصَّلاٰةَ وَ آتُوا الزَّكٰاةَ وَ ارْكَعُوا مَعَ الرّٰاكِعِينَ");
        sor.Ayaat.Add("أَ تَأْمُرُونَ النّٰاسَ بِالْبِرِّ وَ تَنْسَوْنَ أَنْفُسَكُمْ وَ أَنْتُمْ تَتْلُونَ الْكِتٰابَ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("وَ اسْتَعِينُوا بِالصَّبْرِ وَ الصَّلاٰةِ وَ إِنَّهٰا لَكَبِيرَةٌ إِلاّٰ عَلَى الْخٰاشِعِينَ");
        sor.Ayaat.Add("الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلاٰقُوا رَبِّهِمْ وَ أَنَّهُمْ إِلَيْهِ رٰاجِعُونَ");
        sor.Ayaat.Add("يٰا بَنِي إِسْرٰائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَ أَنِّي فَضَّلْتُكُمْ عَلَى الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ اتَّقُوا يَوْماً لاٰ تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئاً وَ لاٰ يُقْبَلُ مِنْهٰا شَفٰاعَةٌ وَ لاٰ يُؤْخَذُ مِنْهٰا عَدْلٌ وَ لاٰ هُمْ يُنْصَرُونَ");
        sor.Ayaat.Add("وَ إِذْ نَجَّيْنٰاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذٰابِ يُذَبِّحُونَ أَبْنٰاءَكُمْ وَ يَسْتَحْيُونَ نِسٰاءَكُمْ وَ فِي ذٰلِكُمْ بَلاٰءٌ مِنْ رَبِّكُمْ عَظِيمٌ");
        sor.Ayaat.Add("وَ إِذْ فَرَقْنٰا بِكُمُ الْبَحْرَ فَأَنْجَيْنٰاكُمْ وَ أَغْرَقْنٰا آلَ فِرْعَوْنَ وَ أَنْتُمْ تَنْظُرُونَ");
        sor.Ayaat.Add("وَ إِذْ وٰاعَدْنٰا مُوسىٰ أَرْبَعِينَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَ أَنْتُمْ ظٰالِمُونَ");
        sor.Ayaat.Add("ثُمَّ عَفَوْنٰا عَنْكُمْ مِنْ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("وَ إِذْ آتَيْنٰا مُوسَى الْكِتٰابَ وَ الْفُرْقٰانَ لَعَلَّكُمْ تَهْتَدُونَ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ مُوسىٰ لِقَوْمِهِ يٰا قَوْمِ إِنَّكُمْ ظَلَمْتُمْ أَنْفُسَكُمْ بِاتِّخٰاذِكُمُ الْعِجْلَ فَتُوبُوا إِلىٰ بٰارِئِكُمْ فَاقْتُلُوا أَنْفُسَكُمْ ذٰلِكُمْ خَيْرٌ لَكُمْ عِنْدَ بٰارِئِكُمْ فَتٰابَ عَلَيْكُمْ إِنَّهُ هُوَ التَّوّٰابُ الرَّحِيمُ");
        sor.Ayaat.Add("وَ إِذْ قُلْتُمْ يٰا مُوسىٰ لَنْ نُؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَأَخَذَتْكُمُ الصّٰاعِقَةُ وَ أَنْتُمْ تَنْظُرُونَ");
        sor.Ayaat.Add("ثُمَّ بَعَثْنٰاكُمْ مِنْ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("وَ ظَلَّلْنٰا عَلَيْكُمُ الْغَمٰامَ وَ أَنْزَلْنٰا عَلَيْكُمُ الْمَنَّ وَ السَّلْوىٰ كُلُوا مِنْ طَيِّبٰاتِ مٰا رَزَقْنٰاكُمْ وَ مٰا ظَلَمُونٰا وَ لٰكِنْ كٰانُوا أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("وَ إِذْ قُلْنَا ادْخُلُوا هٰذِهِ الْقَرْيَةَ فَكُلُوا مِنْهٰا حَيْثُ شِئْتُمْ رَغَداً وَ ادْخُلُوا الْبٰابَ سُجَّداً وَ قُولُوا حِطَّةٌ نَغْفِرْ لَكُمْ خَطٰايٰاكُمْ وَ سَنَزِيدُ الْمُحْسِنِينَ");
        sor.Ayaat.Add("فَبَدَّلَ الَّذِينَ ظَلَمُوا قَوْلاً غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَنْزَلْنٰا عَلَى الَّذِينَ ظَلَمُوا رِجْزاً مِنَ السَّمٰاءِ بِمٰا كٰانُوا يَفْسُقُونَ");
        sor.Ayaat.Add("وَ إِذِ اسْتَسْقىٰ مُوسىٰ لِقَوْمِهِ فَقُلْنَا اضْرِبْ بِعَصٰاكَ الْحَجَرَ فَانْفَجَرَتْ مِنْهُ اثْنَتٰا عَشْرَةَ عَيْناً قَدْ عَلِمَ كُلُّ أُنٰاسٍ مَشْرَبَهُمْ كُلُوا وَ اشْرَبُوا مِنْ رِزْقِ اللّٰهِ وَ لاٰ تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ");
        sor.Ayaat.Add("وَ إِذْ قُلْتُمْ يٰا مُوسىٰ لَنْ نَصْبِرَ عَلىٰ طَعٰامٍ وٰاحِدٍ فَادْعُ لَنٰا رَبَّكَ يُخْرِجْ لَنٰا مِمّٰا تُنْبِتُ الْأَرْضُ مِنْ بَقْلِهٰا وَ قِثّٰائِهٰا وَ فُومِهٰا وَ عَدَسِهٰا وَ بَصَلِهٰا قٰالَ أَ تَسْتَبْدِلُونَ الَّذِي هُوَ أَدْنىٰ بِالَّذِي هُوَ خَيْرٌ اهْبِطُوا مِصْراً فَإِنَّ لَكُمْ مٰا سَأَلْتُمْ وَ ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَ الْمَسْكَنَةُ وَ بٰاؤُ بِغَضَبٍ مِنَ اللّٰهِ ذٰلِكَ بِأَنَّهُمْ كٰانُوا يَكْفُرُونَ بِآيٰاتِ اللّٰهِ وَ يَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ الْحَقِّ ذٰلِكَ بِمٰا عَصَوْا وَ كٰانُوا يَعْتَدُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هٰادُوا وَ النَّصٰارىٰ وَ الصّٰابِئِينَ مَنْ آمَنَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ عَمِلَ صٰالِحاً فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("وَ إِذْ أَخَذْنٰا مِيثٰاقَكُمْ وَ رَفَعْنٰا فَوْقَكُمُ الطُّورَ خُذُوا مٰا آتَيْنٰاكُمْ بِقُوَّةٍ وَ اذْكُرُوا مٰا فِيهِ لَعَلَّكُمْ تَتَّقُونَ");
        sor.Ayaat.Add("ثُمَّ تَوَلَّيْتُمْ مِنْ بَعْدِ ذٰلِكَ فَلَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكُمْ وَ رَحْمَتُهُ لَكُنْتُمْ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("وَ لَقَدْ عَلِمْتُمُ الَّذِينَ اعْتَدَوْا مِنْكُمْ فِي السَّبْتِ فَقُلْنٰا لَهُمْ كُونُوا قِرَدَةً خٰاسِئِينَ");
        sor.Ayaat.Add("فَجَعَلْنٰاهٰا نَكٰالاً لِمٰا بَيْنَ يَدَيْهٰا وَ مٰا خَلْفَهٰا وَ مَوْعِظَةً لِلْمُتَّقِينَ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ مُوسىٰ لِقَوْمِهِ إِنَّ اللّٰهَ يَأْمُرُكُمْ أَنْ تَذْبَحُوا بَقَرَةً قٰالُوا أَ تَتَّخِذُنٰا هُزُواً قٰالَ أَعُوذُ بِاللّٰهِ أَنْ أَكُونَ مِنَ الْجٰاهِلِينَ");
        sor.Ayaat.Add("قٰالُوا ادْعُ لَنٰا رَبَّكَ يُبَيِّنْ لَنٰا مٰا هِيَ قٰالَ إِنَّهُ يَقُولُ إِنَّهٰا بَقَرَةٌ لاٰ فٰارِضٌ وَ لاٰ بِكْرٌ عَوٰانٌ بَيْنَ ذٰلِكَ فَافْعَلُوا مٰا تُؤْمَرُونَ");
        sor.Ayaat.Add("قٰالُوا ادْعُ لَنٰا رَبَّكَ يُبَيِّنْ لَنٰا مٰا لَوْنُهٰا قٰالَ إِنَّهُ يَقُولُ إِنَّهٰا بَقَرَةٌ صَفْرٰاءُ فٰاقِعٌ لَوْنُهٰا تَسُرُّ النّٰاظِرِينَ");
        sor.Ayaat.Add("قٰالُوا ادْعُ لَنٰا رَبَّكَ يُبَيِّنْ لَنٰا مٰا هِيَ إِنَّ الْبَقَرَ تَشٰابَهَ عَلَيْنٰا وَ إِنّٰا إِنْ شٰاءَ اللّٰهُ لَمُهْتَدُونَ");
        sor.Ayaat.Add("قٰالَ إِنَّهُ يَقُولُ إِنَّهٰا بَقَرَةٌ لاٰ ذَلُولٌ تُثِيرُ الْأَرْضَ وَ لاٰ تَسْقِي الْحَرْثَ مُسَلَّمَةٌ لاٰ شِيَةَ فِيهٰا قٰالُوا الْآنَ جِئْتَ بِالْحَقِّ فَذَبَحُوهٰا وَ مٰا كٰادُوا يَفْعَلُونَ");
        sor.Ayaat.Add("وَ إِذْ قَتَلْتُمْ نَفْساً فَادّٰارَأْتُمْ فِيهٰا وَ اللّٰهُ مُخْرِجٌ مٰا كُنْتُمْ تَكْتُمُونَ");
        sor.Ayaat.Add("فَقُلْنٰا اضْرِبُوهُ بِبَعْضِهٰا كَذٰلِكَ يُحْيِ اللّٰهُ الْمَوْتىٰ وَ يُرِيكُمْ آيٰاتِهِ لَعَلَّكُمْ تَعْقِلُونَ");
        sor.Ayaat.Add("ثُمَّ قَسَتْ قُلُوبُكُمْ مِنْ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجٰارَةِ أَوْ أَشَدُّ قَسْوَةً وَ إِنَّ مِنَ الْحِجٰارَةِ لَمٰا يَتَفَجَّرُ مِنْهُ الْأَنْهٰارُ وَ إِنَّ مِنْهٰا لَمٰا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمٰاءُ وَ إِنَّ مِنْهٰا لَمٰا يَهْبِطُ مِنْ خَشْيَةِ اللّٰهِ وَ مَا اللّٰهُ بِغٰافِلٍ عَمّٰا تَعْمَلُونَ");
        sor.Ayaat.Add("أَ فَتَطْمَعُونَ أَنْ يُؤْمِنُوا لَكُمْ وَ قَدْ كٰانَ فَرِيقٌ مِنْهُمْ يَسْمَعُونَ كَلاٰمَ اللّٰهِ ثُمَّ يُحَرِّفُونَهُ مِنْ بَعْدِ مٰا عَقَلُوهُ وَ هُمْ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِذٰا لَقُوا الَّذِينَ آمَنُوا قٰالُوا آمَنّٰا وَ إِذٰا خَلاٰ بَعْضُهُمْ إِلىٰ بَعْضٍ قٰالُوا أَ تُحَدِّثُونَهُمْ بِمٰا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَاجُّوكُمْ بِهِ عِنْدَ رَبِّكُمْ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("أَ وَ لاٰ يَعْلَمُونَ أَنَّ اللّٰهَ يَعْلَمُ مٰا يُسِرُّونَ وَ مٰا يُعْلِنُونَ");
        sor.Ayaat.Add("وَ مِنْهُمْ أُمِّيُّونَ لاٰ يَعْلَمُونَ الْكِتٰابَ إِلاّٰ أَمٰانِيَّ وَ إِنْ هُمْ إِلاّٰ يَظُنُّونَ");
        sor.Ayaat.Add("فَوَيْلٌ لِلَّذِينَ يَكْتُبُونَ الْكِتٰابَ بِأَيْدِيهِمْ ثُمَّ يَقُولُونَ هٰذٰا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوا بِهِ ثَمَناً قَلِيلاً فَوَيْلٌ لَهُمْ مِمّٰا كَتَبَتْ أَيْدِيهِمْ وَ وَيْلٌ لَهُمْ مِمّٰا يَكْسِبُونَ");
        sor.Ayaat.Add("وَ قٰالُوا لَنْ تَمَسَّنَا النّٰارُ إِلاّٰ أَيّٰاماً مَعْدُودَةً قُلْ أَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْداً فَلَنْ يُخْلِفَ اللّٰهُ عَهْدَهُ أَمْ تَقُولُونَ عَلَى اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("بَلىٰ مَنْ كَسَبَ سَيِّئَةً وَ أَحٰاطَتْ بِهِ خَطِيئَتُهُ فَأُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ أُولٰئِكَ أَصْحٰابُ الْجَنَّةِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ إِذْ أَخَذْنٰا مِيثٰاقَ بَنِي إِسْرٰائِيلَ لاٰ تَعْبُدُونَ إِلاَّ اللّٰهَ وَ بِالْوٰالِدَيْنِ إِحْسٰاناً وَ ذِي الْقُرْبىٰ وَ الْيَتٰامىٰ وَ الْمَسٰاكِينِ وَ قُولُوا لِلنّٰاسِ حُسْناً وَ أَقِيمُوا الصَّلاٰةَ وَ آتُوا الزَّكٰاةَ ثُمَّ تَوَلَّيْتُمْ إِلاّٰ قَلِيلاً مِنْكُمْ وَ أَنْتُمْ مُعْرِضُونَ");
        sor.Ayaat.Add("وَ إِذْ أَخَذْنٰا مِيثٰاقَكُمْ لاٰ تَسْفِكُونَ دِمٰاءَكُمْ وَ لاٰ تُخْرِجُونَ أَنْفُسَكُمْ مِنْ دِيٰارِكُمْ ثُمَّ أَقْرَرْتُمْ وَ أَنْتُمْ تَشْهَدُونَ");
        sor.Ayaat.Add("ثُمَّ أَنْتُمْ هٰؤُلاٰءِ تَقْتُلُونَ أَنْفُسَكُمْ وَ تُخْرِجُونَ فَرِيقاً مِنْكُمْ مِنْ دِيٰارِهِمْ تَظٰاهَرُونَ عَلَيْهِمْ بِالْإِثْمِ وَ الْعُدْوٰانِ وَ إِنْ يَأْتُوكُمْ أُسٰارىٰ تُفٰادُوهُمْ وَ هُوَ مُحَرَّمٌ عَلَيْكُمْ إِخْرٰاجُهُمْ أَ فَتُؤْمِنُونَ بِبَعْضِ الْكِتٰابِ وَ تَكْفُرُونَ بِبَعْضٍ فَمٰا جَزٰاءُ مَنْ يَفْعَلُ ذٰلِكَ مِنْكُمْ إِلاّٰ خِزْيٌ فِي الْحَيٰاةِ الدُّنْيٰا وَ يَوْمَ الْقِيٰامَةِ يُرَدُّونَ إِلىٰ أَشَدِّ الْعَذٰابِ وَ مَا اللّٰهُ بِغٰافِلٍ عَمّٰا تَعْمَلُونَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ اشْتَرَوُا الْحَيٰاةَ الدُّنْيٰا بِالْآخِرَةِ فَلاٰ يُخَفَّفُ عَنْهُمُ الْعَذٰابُ وَ لاٰ هُمْ يُنْصَرُونَ");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا مُوسَى الْكِتٰابَ وَ قَفَّيْنٰا مِنْ بَعْدِهِ بِالرُّسُلِ وَ آتَيْنٰا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنٰاتِ وَ أَيَّدْنٰاهُ بِرُوحِ الْقُدُسِ أَ فَكُلَّمٰا جٰاءَكُمْ رَسُولٌ بِمٰا لاٰ تَهْوىٰ أَنْفُسُكُمُ اسْتَكْبَرْتُمْ فَفَرِيقاً كَذَّبْتُمْ وَ فَرِيقاً تَقْتُلُونَ");
        sor.Ayaat.Add("وَ قٰالُوا قُلُوبُنٰا غُلْفٌ بَلْ لَعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَلِيلاً مٰا يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ لَمّٰا جٰاءَهُمْ كِتٰابٌ مِنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِمٰا مَعَهُمْ وَ كٰانُوا مِنْ قَبْلُ يَسْتَفْتِحُونَ عَلَى الَّذِينَ كَفَرُوا فَلَمّٰا جٰاءَهُمْ مٰا عَرَفُوا كَفَرُوا بِهِ فَلَعْنَةُ اللّٰهِ عَلَى الْكٰافِرِينَ");
        sor.Ayaat.Add("بِئْسَمَا اشْتَرَوْا بِهِ أَنْفُسَهُمْ أَنْ يَكْفُرُوا بِمٰا أَنْزَلَ اللّٰهُ بَغْياً أَنْ يُنَزِّلَ اللّٰهُ مِنْ فَضْلِهِ عَلىٰ مَنْ يَشٰاءُ مِنْ عِبٰادِهِ فَبٰاؤُ بِغَضَبٍ عَلىٰ غَضَبٍ وَ لِلْكٰافِرِينَ عَذٰابٌ مُهِينٌ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمْ آمِنُوا بِمٰا أَنْزَلَ اللّٰهُ قٰالُوا نُؤْمِنُ بِمٰا أُنْزِلَ عَلَيْنٰا وَ يَكْفُرُونَ بِمٰا وَرٰاءَهُ وَ هُوَ الْحَقُّ مُصَدِّقاً لِمٰا مَعَهُمْ قُلْ فَلِمَ تَقْتُلُونَ أَنْبِيٰاءَ اللّٰهِ مِنْ قَبْلُ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ لَقَدْ جٰاءَكُمْ مُوسىٰ بِالْبَيِّنٰاتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْ بَعْدِهِ وَ أَنْتُمْ ظٰالِمُونَ");
        sor.Ayaat.Add("وَ إِذْ أَخَذْنٰا مِيثٰاقَكُمْ وَ رَفَعْنٰا فَوْقَكُمُ الطُّورَ خُذُوا مٰا آتَيْنٰاكُمْ بِقُوَّةٍ وَ اسْمَعُوا قٰالُوا سَمِعْنٰا وَ عَصَيْنٰا وَ أُشْرِبُوا فِي قُلُوبِهِمُ الْعِجْلَ بِكُفْرِهِمْ قُلْ بِئْسَمٰا يَأْمُرُكُمْ بِهِ إِيمٰانُكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("قُلْ إِنْ كٰانَتْ لَكُمُ الدّٰارُ الْآخِرَةُ عِنْدَ اللّٰهِ خٰالِصَةً مِنْ دُونِ النّٰاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("وَ لَنْ يَتَمَنَّوْهُ أَبَداً بِمٰا قَدَّمَتْ أَيْدِيهِمْ وَ اللّٰهُ عَلِيمٌ بِالظّٰالِمِينَ");
        sor.Ayaat.Add("وَ لَتَجِدَنَّهُمْ أَحْرَصَ النّٰاسِ عَلىٰ حَيٰاةٍ وَ مِنَ الَّذِينَ أَشْرَكُوا يَوَدُّ أَحَدُهُمْ لَوْ يُعَمَّرُ أَلْفَ سَنَةٍ وَ مٰا هُوَ بِمُزَحْزِحِهِ مِنَ الْعَذٰابِ أَنْ يُعَمَّرَ وَ اللّٰهُ بَصِيرٌ بِمٰا يَعْمَلُونَ");
        sor.Ayaat.Add("قُلْ مَنْ كٰانَ عَدُوًّا لِجِبْرِيلَ فَإِنَّهُ نَزَّلَهُ عَلىٰ قَلْبِكَ بِإِذْنِ اللّٰهِ مُصَدِّقاً لِمٰا بَيْنَ يَدَيْهِ وَ هُدىً وَ بُشْرىٰ لِلْمُؤْمِنِينَ");
        sor.Ayaat.Add("مَنْ كٰانَ عَدُوًّا لِلّٰهِ وَ مَلاٰئِكَتِهِ وَ رُسُلِهِ وَ جِبْرِيلَ وَ مِيكٰالَ فَإِنَّ اللّٰهَ عَدُوٌّ لِلْكٰافِرِينَ");
        sor.Ayaat.Add("وَ لَقَدْ أَنْزَلْنٰا إِلَيْكَ آيٰاتٍ بَيِّنٰاتٍ وَ مٰا يَكْفُرُ بِهٰا إِلاَّ الْفٰاسِقُونَ");
        sor.Ayaat.Add("أَ وَ كُلَّمٰا عٰاهَدُوا عَهْداً نَبَذَهُ فَرِيقٌ مِنْهُمْ بَلْ أَكْثَرُهُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ لَمّٰا جٰاءَهُمْ رَسُولٌ مِنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِمٰا مَعَهُمْ نَبَذَ فَرِيقٌ مِنَ الَّذِينَ أُوتُوا الْكِتٰابَ كِتٰابَ اللّٰهِ وَرٰاءَ ظُهُورِهِمْ كَأَنَّهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ اتَّبَعُوا مٰا تَتْلُوا الشَّيٰاطِينُ عَلىٰ مُلْكِ سُلَيْمٰانَ وَ مٰا كَفَرَ سُلَيْمٰانُ وَ لٰكِنَّ الشَّيٰاطِينَ كَفَرُوا يُعَلِّمُونَ النّٰاسَ السِّحْرَ وَ مٰا أُنْزِلَ عَلَى الْمَلَكَيْنِ بِبٰابِلَ هٰارُوتَ وَ مٰارُوتَ وَ مٰا يُعَلِّمٰانِ مِنْ أَحَدٍ حَتّٰى يَقُولاٰ إِنَّمٰا نَحْنُ فِتْنَةٌ فَلاٰ تَكْفُرْ فَيَتَعَلَّمُونَ مِنْهُمٰا مٰا يُفَرِّقُونَ بِهِ بَيْنَ الْمَرْءِ وَ زَوْجِهِ وَ مٰا هُمْ بِضٰارِّينَ بِهِ مِنْ أَحَدٍ إِلاّٰ بِإِذْنِ اللّٰهِ وَ يَتَعَلَّمُونَ مٰا يَضُرُّهُمْ وَ لاٰ يَنْفَعُهُمْ وَ لَقَدْ عَلِمُوا لَمَنِ اشْتَرٰاهُ مٰا لَهُ فِي الْآخِرَةِ مِنْ خَلاٰقٍ وَ لَبِئْسَ مٰا شَرَوْا بِهِ أَنْفُسَهُمْ لَوْ كٰانُوا يَعْلَمُونَ");
        sor.Ayaat.Add("وَ لَوْ أَنَّهُمْ آمَنُوا وَ اتَّقَوْا لَمَثُوبَةٌ مِنْ عِنْدِ اللّٰهِ خَيْرٌ لَوْ كٰانُوا يَعْلَمُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَقُولُوا رٰاعِنٰا وَ قُولُوا انْظُرْنٰا وَ اسْمَعُوا وَ لِلْكٰافِرِينَ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("مٰا يَوَدُّ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتٰابِ وَ لاَ الْمُشْرِكِينَ أَنْ يُنَزَّلَ عَلَيْكُمْ مِنْ خَيْرٍ مِنْ رَبِّكُمْ وَ اللّٰهُ يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشٰاءُ وَ اللّٰهُ ذُو الْفَضْلِ الْعَظِيمِ");
        sor.Ayaat.Add("مٰا نَنْسَخْ مِنْ آيَةٍ أَوْ نُنْسِهٰا نَأْتِ بِخَيْرٍ مِنْهٰا أَوْ مِثْلِهٰا أَ لَمْ تَعْلَمْ أَنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("أَ لَمْ تَعْلَمْ أَنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَ لاٰ نَصِيرٍ");
        sor.Ayaat.Add("أَمْ تُرِيدُونَ أَنْ تَسْئَلُوا رَسُولَكُمْ كَمٰا سُئِلَ مُوسىٰ مِنْ قَبْلُ وَ مَنْ يَتَبَدَّلِ الْكُفْرَ بِالْإِيمٰانِ فَقَدْ ضَلَّ سَوٰاءَ السَّبِيلِ");
        sor.Ayaat.Add("وَدَّ كَثِيرٌ مِنْ أَهْلِ الْكِتٰابِ لَوْ يَرُدُّونَكُمْ مِنْ بَعْدِ إِيمٰانِكُمْ كُفّٰاراً حَسَداً مِنْ عِنْدِ أَنْفُسِهِمْ مِنْ بَعْدِ مٰا تَبَيَّنَ لَهُمُ الْحَقُّ فَاعْفُوا وَ اصْفَحُوا حَتّٰى يَأْتِيَ اللّٰهُ بِأَمْرِهِ إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ أَقِيمُوا الصَّلاٰةَ وَ آتُوا الزَّكٰاةَ وَ مٰا تُقَدِّمُوا لِأَنْفُسِكُمْ مِنْ خَيْرٍ تَجِدُوهُ عِنْدَ اللّٰهِ إِنَّ اللّٰهَ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("وَ قٰالُوا لَنْ يَدْخُلَ الْجَنَّةَ إِلاّٰ مَنْ كٰانَ هُوداً أَوْ نَصٰارىٰ تِلْكَ أَمٰانِيُّهُمْ قُلْ هٰاتُوا بُرْهٰانَكُمْ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("بَلىٰ مَنْ أَسْلَمَ وَجْهَهُ لِلّٰهِ وَ هُوَ مُحْسِنٌ فَلَهُ أَجْرُهُ عِنْدَ رَبِّهِ وَ لاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("وَ قٰالَتِ الْيَهُودُ لَيْسَتِ النَّصٰارىٰ عَلىٰ شَيْءٍ وَ قٰالَتِ النَّصٰارىٰ لَيْسَتِ الْيَهُودُ عَلىٰ شَيْءٍ وَ هُمْ يَتْلُونَ الْكِتٰابَ كَذٰلِكَ قٰالَ الَّذِينَ لاٰ يَعْلَمُونَ مِثْلَ قَوْلِهِمْ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰامَةِ فِيمٰا كٰانُوا فِيهِ يَخْتَلِفُونَ");
        sor.Ayaat.Add("وَ مَنْ أَظْلَمُ مِمَّنْ مَنَعَ مَسٰاجِدَ اللّٰهِ أَنْ يُذْكَرَ فِيهَا اسْمُهُ وَ سَعىٰ فِي خَرٰابِهٰا أُولٰئِكَ مٰا كٰانَ لَهُمْ أَنْ يَدْخُلُوهٰا إِلاّٰ خٰائِفِينَ لَهُمْ فِي الدُّنْيٰا خِزْيٌ وَ لَهُمْ فِي الْآخِرَةِ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("وَ لِلّٰهِ الْمَشْرِقُ وَ الْمَغْرِبُ فَأَيْنَمٰا تُوَلُّوا فَثَمَّ وَجْهُ اللّٰهِ إِنَّ اللّٰهَ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ قٰالُوا اتَّخَذَ اللّٰهُ وَلَداً سُبْحٰانَهُ بَلْ لَهُ مٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ كُلٌّ لَهُ قٰانِتُونَ");
        sor.Ayaat.Add("بَدِيعُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ إِذٰا قَضىٰ أَمْراً فَإِنَّمٰا يَقُولُ لَهُ كُنْ فَيَكُونُ");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ لاٰ يَعْلَمُونَ لَوْ لاٰ يُكَلِّمُنَا اللّٰهُ أَوْ تَأْتِينٰا آيَةٌ كَذٰلِكَ قٰالَ الَّذِينَ مِنْ قَبْلِهِمْ مِثْلَ قَوْلِهِمْ تَشٰابَهَتْ قُلُوبُهُمْ قَدْ بَيَّنَّا الْآيٰاتِ لِقَوْمٍ يُوقِنُونَ");
        sor.Ayaat.Add("إِنّٰا أَرْسَلْنٰاكَ بِالْحَقِّ بَشِيراً وَ نَذِيراً وَ لاٰ تُسْئَلُ عَنْ أَصْحٰابِ الْجَحِيمِ");
        sor.Ayaat.Add("وَ لَنْ تَرْضىٰ عَنْكَ الْيَهُودُ وَ لاَ النَّصٰارىٰ حَتّٰى تَتَّبِعَ مِلَّتَهُمْ قُلْ إِنَّ هُدَى اللّٰهِ هُوَ الْهُدىٰ وَ لَئِنِ اتَّبَعْتَ أَهْوٰاءَهُمْ بَعْدَ الَّذِي جٰاءَكَ مِنَ الْعِلْمِ مٰا لَكَ مِنَ اللّٰهِ مِنْ وَلِيٍّ وَ لاٰ نَصِيرٍ");
        sor.Ayaat.Add("الَّذِينَ آتَيْنٰاهُمُ الْكِتٰابَ يَتْلُونَهُ حَقَّ تِلاٰوَتِهِ أُولٰئِكَ يُؤْمِنُونَ بِهِ وَ مَنْ يَكْفُرْ بِهِ فَأُولٰئِكَ هُمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("يٰا بَنِي إِسْرٰائِيلَ اذْكُرُوا نِعْمَتِيَ الَّتِي أَنْعَمْتُ عَلَيْكُمْ وَ أَنِّي فَضَّلْتُكُمْ عَلَى الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ اتَّقُوا يَوْماً لاٰ تَجْزِي نَفْسٌ عَنْ نَفْسٍ شَيْئاً وَ لاٰ يُقْبَلُ مِنْهٰا عَدْلٌ وَ لاٰ تَنْفَعُهٰا شَفٰاعَةٌ وَ لاٰ هُمْ يُنْصَرُونَ");
        sor.Ayaat.Add("وَ إِذِ ابْتَلىٰ إِبْرٰاهِيمَ رَبُّهُ بِكَلِمٰاتٍ فَأَتَمَّهُنَّ قٰالَ إِنِّي جٰاعِلُكَ لِلنّٰاسِ إِمٰاماً قٰالَ وَ مِنْ ذُرِّيَّتِي قٰالَ لاٰ يَنٰالُ عَهْدِي الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ إِذْ جَعَلْنَا الْبَيْتَ مَثٰابَةً لِلنّٰاسِ وَ أَمْناً وَ اتَّخِذُوا مِنْ مَقٰامِ إِبْرٰاهِيمَ مُصَلًّى وَ عَهِدْنٰا إِلىٰ إِبْرٰاهِيمَ وَ إِسْمٰاعِيلَ أَنْ طَهِّرٰا بَيْتِيَ لِلطّٰائِفِينَ وَ الْعٰاكِفِينَ وَ الرُّكَّعِ السُّجُودِ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ إِبْرٰاهِيمُ رَبِّ اجْعَلْ هٰذٰا بَلَداً آمِناً وَ ارْزُقْ أَهْلَهُ مِنَ الثَّمَرٰاتِ مَنْ آمَنَ مِنْهُمْ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ قٰالَ وَ مَنْ كَفَرَ فَأُمَتِّعُهُ قَلِيلاً ثُمَّ أَضْطَرُّهُ إِلىٰ عَذٰابِ النّٰارِ وَ بِئْسَ الْمَصِيرُ");
        sor.Ayaat.Add("وَ إِذْ يَرْفَعُ إِبْرٰاهِيمُ الْقَوٰاعِدَ مِنَ الْبَيْتِ وَ إِسْمٰاعِيلُ رَبَّنٰا تَقَبَّلْ مِنّٰا إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("رَبَّنٰا وَ اجْعَلْنٰا مُسْلِمَيْنِ لَكَ وَ مِنْ ذُرِّيَّتِنٰا أُمَّةً مُسْلِمَةً لَكَ وَ أَرِنٰا مَنٰاسِكَنٰا وَ تُبْ عَلَيْنٰا إِنَّكَ أَنْتَ التَّوّٰابُ الرَّحِيمُ");
        sor.Ayaat.Add("رَبَّنٰا وَ ابْعَثْ فِيهِمْ رَسُولاً مِنْهُمْ يَتْلُوا عَلَيْهِمْ آيٰاتِكَ وَ يُعَلِّمُهُمُ الْكِتٰابَ وَ الْحِكْمَةَ وَ يُزَكِّيهِمْ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("وَ مَنْ يَرْغَبُ عَنْ مِلَّةِ إِبْرٰاهِيمَ إِلاّٰ مَنْ سَفِهَ نَفْسَهُ وَ لَقَدِ اصْطَفَيْنٰاهُ فِي الدُّنْيٰا وَ إِنَّهُ فِي الْآخِرَةِ لَمِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("إِذْ قٰالَ لَهُ رَبُّهُ أَسْلِمْ قٰالَ أَسْلَمْتُ لِرَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ وَصّٰى بِهٰا إِبْرٰاهِيمُ بَنِيهِ وَ يَعْقُوبُ يٰا بَنِيَّ إِنَّ اللّٰهَ اصْطَفىٰ لَكُمُ الدِّينَ فَلاٰ تَمُوتُنَّ إِلاّٰ وَ أَنْتُمْ مُسْلِمُونَ");
        sor.Ayaat.Add("أَمْ كُنْتُمْ شُهَدٰاءَ إِذْ حَضَرَ يَعْقُوبَ الْمَوْتُ إِذْ قٰالَ لِبَنِيهِ مٰا تَعْبُدُونَ مِنْ بَعْدِي قٰالُوا نَعْبُدُ إِلٰهَكَ وَ إِلٰهَ آبٰائِكَ إِبْرٰاهِيمَ وَ إِسْمٰاعِيلَ وَ إِسْحٰاقَ إِلٰهاً وٰاحِداً وَ نَحْنُ لَهُ مُسْلِمُونَ");
        sor.Ayaat.Add("تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَهٰا مٰا كَسَبَتْ وَ لَكُمْ مٰا كَسَبْتُمْ وَ لاٰ تُسْئَلُونَ عَمّٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ قٰالُوا كُونُوا هُوداً أَوْ نَصٰارىٰ تَهْتَدُوا قُلْ بَلْ مِلَّةَ إِبْرٰاهِيمَ حَنِيفاً وَ مٰا كٰانَ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("قُولُوا آمَنّٰا بِاللّٰهِ وَ مٰا أُنْزِلَ إِلَيْنٰا وَ مٰا أُنْزِلَ إِلىٰ إِبْرٰاهِيمَ وَ إِسْمٰاعِيلَ وَ إِسْحٰاقَ وَ يَعْقُوبَ وَ الْأَسْبٰاطِ وَ مٰا أُوتِيَ مُوسىٰ وَ عِيسىٰ وَ مٰا أُوتِيَ النَّبِيُّونَ مِنْ رَبِّهِمْ لاٰ نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَ نَحْنُ لَهُ مُسْلِمُونَ");
        sor.Ayaat.Add("فَإِنْ آمَنُوا بِمِثْلِ مٰا آمَنْتُمْ بِهِ فَقَدِ اهْتَدَوْا وَ إِنْ تَوَلَّوْا فَإِنَّمٰا هُمْ فِي شِقٰاقٍ فَسَيَكْفِيكَهُمُ اللّٰهُ وَ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("صِبْغَةَ اللّٰهِ وَ مَنْ أَحْسَنُ مِنَ اللّٰهِ صِبْغَةً وَ نَحْنُ لَهُ عٰابِدُونَ");
        sor.Ayaat.Add("قُلْ أَ تُحَاجُّونَنٰا فِي اللّٰهِ وَ هُوَ رَبُّنٰا وَ رَبُّكُمْ وَ لَنٰا أَعْمٰالُنٰا وَ لَكُمْ أَعْمٰالُكُمْ وَ نَحْنُ لَهُ مُخْلِصُونَ");
        sor.Ayaat.Add("أَمْ تَقُولُونَ إِنَّ إِبْرٰاهِيمَ وَ إِسْمٰاعِيلَ وَ إِسْحٰاقَ وَ يَعْقُوبَ وَ الْأَسْبٰاطَ كٰانُوا هُوداً أَوْ نَصٰارىٰ قُلْ أَ أَنْتُمْ أَعْلَمُ أَمِ اللّٰهُ وَ مَنْ أَظْلَمُ مِمَّنْ كَتَمَ شَهٰادَةً عِنْدَهُ مِنَ اللّٰهِ وَ مَا اللّٰهُ بِغٰافِلٍ عَمّٰا تَعْمَلُونَ");
        sor.Ayaat.Add("تِلْكَ أُمَّةٌ قَدْ خَلَتْ لَهٰا مٰا كَسَبَتْ وَ لَكُمْ مٰا كَسَبْتُمْ وَ لاٰ تُسْئَلُونَ عَمّٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("سَيَقُولُ السُّفَهٰاءُ مِنَ النّٰاسِ مٰا وَلاّٰهُمْ عَنْ قِبْلَتِهِمُ الَّتِي كٰانُوا عَلَيْهٰا قُلْ لِلّٰهِ الْمَشْرِقُ وَ الْمَغْرِبُ يَهْدِي مَنْ يَشٰاءُ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ كَذٰلِكَ جَعَلْنٰاكُمْ أُمَّةً وَسَطاً لِتَكُونُوا شُهَدٰاءَ عَلَى النّٰاسِ وَ يَكُونَ الرَّسُولُ عَلَيْكُمْ شَهِيداً وَ مٰا جَعَلْنَا الْقِبْلَةَ الَّتِي كُنْتَ عَلَيْهٰا إِلاّٰ لِنَعْلَمَ مَنْ يَتَّبِعُ الرَّسُولَ مِمَّنْ يَنْقَلِبُ عَلىٰ عَقِبَيْهِ وَ إِنْ كٰانَتْ لَكَبِيرَةً إِلاّٰ عَلَى الَّذِينَ هَدَى اللّٰهُ وَ مٰا كٰانَ اللّٰهُ لِيُضِيعَ إِيمٰانَكُمْ إِنَّ اللّٰهَ بِالنّٰاسِ لَرَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("قَدْ نَرىٰ تَقَلُّبَ وَجْهِكَ فِي السَّمٰاءِ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰاهٰا فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرٰامِ وَ حَيْثُ مٰا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ وَ إِنَّ الَّذِينَ أُوتُوا الْكِتٰابَ لَيَعْلَمُونَ أَنَّهُ الْحَقُّ مِنْ رَبِّهِمْ وَ مَا اللّٰهُ بِغٰافِلٍ عَمّٰا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ لَئِنْ أَتَيْتَ الَّذِينَ أُوتُوا الْكِتٰابَ بِكُلِّ آيَةٍ مٰا تَبِعُوا قِبْلَتَكَ وَ مٰا أَنْتَ بِتٰابِعٍ قِبْلَتَهُمْ وَ مٰا بَعْضُهُمْ بِتٰابِعٍ قِبْلَةَ بَعْضٍ وَ لَئِنِ اتَّبَعْتَ أَهْوٰاءَهُمْ مِنْ بَعْدِ مٰا جٰاءَكَ مِنَ الْعِلْمِ إِنَّكَ إِذاً لَمِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("الَّذِينَ آتَيْنٰاهُمُ الْكِتٰابَ يَعْرِفُونَهُ كَمٰا يَعْرِفُونَ أَبْنٰاءَهُمْ وَ إِنَّ فَرِيقاً مِنْهُمْ لَيَكْتُمُونَ الْحَقَّ وَ هُمْ يَعْلَمُونَ");
        sor.Ayaat.Add("الْحَقُّ مِنْ رَبِّكَ فَلاٰ تَكُونَنَّ مِنَ الْمُمْتَرِينَ");
        sor.Ayaat.Add("وَ لِكُلٍّ وِجْهَةٌ هُوَ مُوَلِّيهٰا فَاسْتَبِقُوا الْخَيْرٰاتِ أَيْنَ مٰا تَكُونُوا يَأْتِ بِكُمُ اللّٰهُ جَمِيعاً إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ مِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرٰامِ وَ إِنَّهُ لَلْحَقُّ مِنْ رَبِّكَ وَ مَا اللّٰهُ بِغٰافِلٍ عَمّٰا تَعْمَلُونَ");
        sor.Ayaat.Add("وَ مِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرٰامِ وَ حَيْثُ مٰا كُنْتُمْ فَوَلُّوا وُجُوهَكُمْ شَطْرَهُ لِئَلاّٰ يَكُونَ لِلنّٰاسِ عَلَيْكُمْ حُجَّةٌ إِلاَّ الَّذِينَ ظَلَمُوا مِنْهُمْ فَلاٰ تَخْشَوْهُمْ وَ اخْشَوْنِي وَ لِأُتِمَّ نِعْمَتِي عَلَيْكُمْ وَ لَعَلَّكُمْ تَهْتَدُونَ");
        sor.Ayaat.Add("كَمٰا أَرْسَلْنٰا فِيكُمْ رَسُولاً مِنْكُمْ يَتْلُوا عَلَيْكُمْ آيٰاتِنٰا وَ يُزَكِّيكُمْ وَ يُعَلِّمُكُمُ الْكِتٰابَ وَ الْحِكْمَةَ وَ يُعَلِّمُكُمْ مٰا لَمْ تَكُونُوا تَعْلَمُونَ");
        sor.Ayaat.Add("فَاذْكُرُونِي أَذْكُرْكُمْ وَ اشْكُرُوا لِي وَ لاٰ تَكْفُرُونِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اسْتَعِينُوا بِالصَّبْرِ وَ الصَّلاٰةِ إِنَّ اللّٰهَ مَعَ الصّٰابِرِينَ");
        sor.Ayaat.Add("وَ لاٰ تَقُولُوا لِمَنْ يُقْتَلُ فِي سَبِيلِ اللّٰهِ أَمْوٰاتٌ بَلْ أَحْيٰاءٌ وَ لٰكِنْ لاٰ تَشْعُرُونَ");
        sor.Ayaat.Add("وَ لَنَبْلُوَنَّكُمْ بِشَيْءٍ مِنَ الْخَوْفِ وَ الْجُوعِ وَ نَقْصٍ مِنَ الْأَمْوٰالِ وَ الْأَنْفُسِ وَ الثَّمَرٰاتِ وَ بَشِّرِ الصّٰابِرِينَ");
        sor.Ayaat.Add("الَّذِينَ إِذٰا أَصٰابَتْهُمْ مُصِيبَةٌ قٰالُوا إِنّٰا لِلّٰهِ وَ إِنّٰا إِلَيْهِ رٰاجِعُونَ");
        sor.Ayaat.Add("أُولٰئِكَ عَلَيْهِمْ صَلَوٰاتٌ مِنْ رَبِّهِمْ وَ رَحْمَةٌ وَ أُولٰئِكَ هُمُ الْمُهْتَدُونَ");
        sor.Ayaat.Add("إِنَّ الصَّفٰا وَ الْمَرْوَةَ مِنْ شَعٰائِرِ اللّٰهِ فَمَنْ حَجَّ الْبَيْتَ أَوِ اعْتَمَرَ فَلاٰ جُنٰاحَ عَلَيْهِ أَنْ يَطَّوَّفَ بِهِمٰا وَ مَنْ تَطَوَّعَ خَيْراً فَإِنَّ اللّٰهَ شٰاكِرٌ عَلِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَكْتُمُونَ مٰا أَنْزَلْنٰا مِنَ الْبَيِّنٰاتِ وَ الْهُدىٰ مِنْ بَعْدِ مٰا بَيَّنّٰاهُ لِلنّٰاسِ فِي الْكِتٰابِ أُولٰئِكَ يَلْعَنُهُمُ اللّٰهُ وَ يَلْعَنُهُمُ اللاّٰعِنُونَ");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ تٰابُوا وَ أَصْلَحُوا وَ بَيَّنُوا فَأُولٰئِكَ أَتُوبُ عَلَيْهِمْ وَ أَنَا التَّوّٰابُ الرَّحِيمُ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا وَ مٰاتُوا وَ هُمْ كُفّٰارٌ أُولٰئِكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَ الْمَلاٰئِكَةِ وَ النّٰاسِ أَجْمَعِينَ");
        sor.Ayaat.Add("خٰالِدِينَ فِيهٰا لاٰ يُخَفَّفُ عَنْهُمُ الْعَذٰابُ وَ لاٰ هُمْ يُنْظَرُونَ");
        sor.Ayaat.Add("وَ إِلٰهُكُمْ إِلٰهٌ وٰاحِدٌ لاٰ إِلٰهَ إِلاّٰ هُوَ الرَّحْمٰنُ الرَّحِيمُ");
        sor.Ayaat.Add("إِنَّ فِي خَلْقِ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ اخْتِلاٰفِ اللَّيْلِ وَ النَّهٰارِ وَ الْفُلْكِ الَّتِي تَجْرِي فِي الْبَحْرِ بِمٰا يَنْفَعُ النّٰاسَ وَ مٰا أَنْزَلَ اللّٰهُ مِنَ السَّمٰاءِ مِنْ مٰاءٍ فَأَحْيٰا بِهِ الْأَرْضَ بَعْدَ مَوْتِهٰا وَ بَثَّ فِيهٰا مِنْ كُلِّ دَابَّةٍ وَ تَصْرِيفِ الرِّيٰاحِ وَ السَّحٰابِ الْمُسَخَّرِ بَيْنَ السَّمٰاءِ وَ الْأَرْضِ لَآيٰاتٍ لِقَوْمٍ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يَتَّخِذُ مِنْ دُونِ اللّٰهِ أَنْدٰاداً يُحِبُّونَهُمْ كَحُبِّ اللّٰهِ وَ الَّذِينَ آمَنُوا أَشَدُّ حُبًّا لِلّٰهِ وَ لَوْ يَرَى الَّذِينَ ظَلَمُوا إِذْ يَرَوْنَ الْعَذٰابَ أَنَّ الْقُوَّةَ لِلّٰهِ جَمِيعاً وَ أَنَّ اللّٰهَ شَدِيدُ الْعَذٰابِ");
        sor.Ayaat.Add("إِذْ تَبَرَّأَ الَّذِينَ اتُّبِعُوا مِنَ الَّذِينَ اتَّبَعُوا وَ رَأَوُا الْعَذٰابَ وَ تَقَطَّعَتْ بِهِمُ الْأَسْبٰابُ");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ اتَّبَعُوا لَوْ أَنَّ لَنٰا كَرَّةً فَنَتَبَرَّأَ مِنْهُمْ كَمٰا تَبَرَّؤُا مِنّٰا كَذٰلِكَ يُرِيهِمُ اللّٰهُ أَعْمٰالَهُمْ حَسَرٰاتٍ عَلَيْهِمْ وَ مٰا هُمْ بِخٰارِجِينَ مِنَ النّٰارِ");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ كُلُوا مِمّٰا فِي الْأَرْضِ حَلاٰلاً طَيِّباً وَ لاٰ تَتَّبِعُوا خُطُوٰاتِ الشَّيْطٰانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ");
        sor.Ayaat.Add("إِنَّمٰا يَأْمُرُكُمْ بِالسُّوءِ وَ الْفَحْشٰاءِ وَ أَنْ تَقُولُوا عَلَى اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمُ اتَّبِعُوا مٰا أَنْزَلَ اللّٰهُ قٰالُوا بَلْ نَتَّبِعُ مٰا أَلْفَيْنٰا عَلَيْهِ آبٰاءَنٰا أَ وَ لَوْ كٰانَ آبٰاؤُهُمْ لاٰ يَعْقِلُونَ شَيْئاً وَ لاٰ يَهْتَدُونَ");
        sor.Ayaat.Add("وَ مَثَلُ الَّذِينَ كَفَرُوا كَمَثَلِ الَّذِي يَنْعِقُ بِمٰا لاٰ يَسْمَعُ إِلاّٰ دُعٰاءً وَ نِدٰاءً صُمٌّ بُكْمٌ عُمْيٌ فَهُمْ لاٰ يَعْقِلُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا كُلُوا مِنْ طَيِّبٰاتِ مٰا رَزَقْنٰاكُمْ وَ اشْكُرُوا لِلّٰهِ إِنْ كُنْتُمْ إِيّٰاهُ تَعْبُدُونَ");
        sor.Ayaat.Add("إِنَّمٰا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَ الدَّمَ وَ لَحْمَ الْخِنْزِيرِ وَ مٰا أُهِلَّ بِهِ لِغَيْرِ اللّٰهِ فَمَنِ اضْطُرَّ غَيْرَ بٰاغٍ وَ لاٰ عٰادٍ فَلاٰ إِثْمَ عَلَيْهِ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَكْتُمُونَ مٰا أَنْزَلَ اللّٰهُ مِنَ الْكِتٰابِ وَ يَشْتَرُونَ بِهِ ثَمَناً قَلِيلاً أُولٰئِكَ مٰا يَأْكُلُونَ فِي بُطُونِهِمْ إِلاَّ النّٰارَ وَ لاٰ يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰامَةِ وَ لاٰ يُزَكِّيهِمْ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ اشْتَرَوُا الضَّلاٰلَةَ بِالْهُدىٰ وَ الْعَذٰابَ بِالْمَغْفِرَةِ فَمٰا أَصْبَرَهُمْ عَلَى النّٰارِ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّ اللّٰهَ نَزَّلَ الْكِتٰابَ بِالْحَقِّ وَ إِنَّ الَّذِينَ اخْتَلَفُوا فِي الْكِتٰابِ لَفِي شِقٰاقٍ بَعِيدٍ");
        sor.Ayaat.Add("لَيْسَ الْبِرَّ أَنْ تُوَلُّوا وُجُوهَكُمْ قِبَلَ الْمَشْرِقِ وَ الْمَغْرِبِ وَ لٰكِنَّ الْبِرَّ مَنْ آمَنَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ الْمَلاٰئِكَةِ وَ الْكِتٰابِ وَ النَّبِيِّينَ وَ آتَى الْمٰالَ عَلىٰ حُبِّهِ ذَوِي الْقُرْبىٰ وَ الْيَتٰامىٰ وَ الْمَسٰاكِينَ وَ ابْنَ السَّبِيلِ وَ السّٰائِلِينَ وَ فِي الرِّقٰابِ وَ أَقٰامَ الصَّلاٰةَ وَ آتَى الزَّكٰاةَ وَ الْمُوفُونَ بِعَهْدِهِمْ إِذٰا عٰاهَدُوا وَ الصّٰابِرِينَ فِي الْبَأْسٰاءِ وَ الضَّرّٰاءِ وَ حِينَ الْبَأْسِ أُولٰئِكَ الَّذِينَ صَدَقُوا وَ أُولٰئِكَ هُمُ الْمُتَّقُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الْقِصٰاصُ فِي الْقَتْلىٰ الْحُرُّ بِالْحُرِّ وَ الْعَبْدُ بِالْعَبْدِ وَ الْأُنْثىٰ بِالْأُنْثىٰ فَمَنْ عُفِيَ لَهُ مِنْ أَخِيهِ شَيْءٌ فَاتِّبٰاعٌ بِالْمَعْرُوفِ وَ أَدٰاءٌ إِلَيْهِ بِإِحْسٰانٍ ذٰلِكَ تَخْفِيفٌ مِنْ رَبِّكُمْ وَ رَحْمَةٌ فَمَنِ اعْتَدىٰ بَعْدَ ذٰلِكَ فَلَهُ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ لَكُمْ فِي الْقِصٰاصِ حَيٰاةٌ يٰا أُولِي الْأَلْبٰابِ لَعَلَّكُمْ تَتَّقُونَ");
        sor.Ayaat.Add("كُتِبَ عَلَيْكُمْ إِذٰا حَضَرَ أَحَدَكُمُ الْمَوْتُ إِنْ تَرَكَ خَيْراً الْوَصِيَّةُ لِلْوٰالِدَيْنِ وَ الْأَقْرَبِينَ بِالْمَعْرُوفِ حَقًّا عَلَى الْمُتَّقِينَ");
        sor.Ayaat.Add("فَمَنْ بَدَّلَهُ بَعْدَ مٰا سَمِعَهُ فَإِنَّمٰا إِثْمُهُ عَلَى الَّذِينَ يُبَدِّلُونَهُ إِنَّ اللّٰهَ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("فَمَنْ خٰافَ مِنْ مُوصٍ جَنَفاً أَوْ إِثْماً فَأَصْلَحَ بَيْنَهُمْ فَلاٰ إِثْمَ عَلَيْهِ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا كُتِبَ عَلَيْكُمُ الصِّيٰامُ كَمٰا كُتِبَ عَلَى الَّذِينَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُونَ");
        sor.Ayaat.Add("أَيّٰاماً مَعْدُودٰاتٍ فَمَنْ كٰانَ مِنْكُمْ مَرِيضاً أَوْ عَلىٰ سَفَرٍ فَعِدَّةٌ مِنْ أَيّٰامٍ أُخَرَ وَ عَلَى الَّذِينَ يُطِيقُونَهُ فِدْيَةٌ طَعٰامُ مِسْكِينٍ فَمَنْ تَطَوَّعَ خَيْراً فَهُوَ خَيْرٌ لَهُ وَ أَنْ تَصُومُوا خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("شَهْرُ رَمَضٰانَ الَّذِي أُنْزِلَ فِيهِ الْقُرْآنُ هُدىً لِلنّٰاسِ وَ بَيِّنٰاتٍ مِنَ الْهُدىٰ وَ الْفُرْقٰانِ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ وَ مَنْ كٰانَ مَرِيضاً أَوْ عَلىٰ سَفَرٍ فَعِدَّةٌ مِنْ أَيّٰامٍ أُخَرَ يُرِيدُ اللّٰهُ بِكُمُ الْيُسْرَ وَ لاٰ يُرِيدُ بِكُمُ الْعُسْرَ وَ لِتُكْمِلُوا الْعِدَّةَ وَ لِتُكَبِّرُوا اللّٰهَ عَلىٰ مٰا هَدٰاكُمْ وَ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("وَ إِذٰا سَأَلَكَ عِبٰادِي عَنِّي فَإِنِّي قَرِيبٌ أُجِيبُ دَعْوَةَ الدّٰاعِ إِذٰا دَعٰانِ فَلْيَسْتَجِيبُوا لِي وَ لْيُؤْمِنُوا بِي لَعَلَّهُمْ يَرْشُدُونَ");
        sor.Ayaat.Add("أُحِلَّ لَكُمْ لَيْلَةَ الصِّيٰامِ الرَّفَثُ إِلىٰ نِسٰائِكُمْ هُنَّ لِبٰاسٌ لَكُمْ وَ أَنْتُمْ لِبٰاسٌ لَهُنَّ عَلِمَ اللّٰهُ أَنَّكُمْ كُنْتُمْ تَخْتٰانُونَ أَنْفُسَكُمْ فَتٰابَ عَلَيْكُمْ وَ عَفٰا عَنْكُمْ فَالْآنَ بَاشِرُوهُنَّ وَ ابْتَغُوا مٰا كَتَبَ اللّٰهُ لَكُمْ وَ كُلُوا وَ اشْرَبُوا حَتّٰى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْأَبْيَضُ مِنَ الْخَيْطِ الْأَسْوَدِ مِنَ الْفَجْرِ ثُمَّ أَتِمُّوا الصِّيٰامَ إِلَى اللَّيْلِ وَ لاٰ تُبَاشِرُوهُنَّ وَ أَنْتُمْ عٰاكِفُونَ فِي الْمَسٰاجِدِ تِلْكَ حُدُودُ اللّٰهِ فَلاٰ تَقْرَبُوهٰا كَذٰلِكَ يُبَيِّنُ اللّٰهُ آيٰاتِهِ لِلنّٰاسِ لَعَلَّهُمْ يَتَّقُونَ");
        sor.Ayaat.Add("وَ لاٰ تَأْكُلُوا أَمْوٰالَكُمْ بَيْنَكُمْ بِالْبٰاطِلِ وَ تُدْلُوا بِهٰا إِلَى الْحُكّٰامِ لِتَأْكُلُوا فَرِيقاً مِنْ أَمْوٰالِ النّٰاسِ بِالْإِثْمِ وَ أَنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("يَسْئَلُونَكَ عَنِ الْأَهِلَّةِ قُلْ هِيَ مَوٰاقِيتُ لِلنّٰاسِ وَ الْحَجِّ وَ لَيْسَ الْبِرُّ بِأَنْ تَأْتُوا الْبُيُوتَ مِنْ ظُهُورِهٰا وَ لٰكِنَّ الْبِرَّ مَنِ اتَّقىٰ وَ أْتُوا الْبُيُوتَ مِنْ أَبْوٰابِهٰا وَ اتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("وَ قٰاتِلُوا فِي سَبِيلِ اللّٰهِ الَّذِينَ يُقٰاتِلُونَكُمْ وَ لاٰ تَعْتَدُوا إِنَّ اللّٰهَ لاٰ يُحِبُّ الْمُعْتَدِينَ");
        sor.Ayaat.Add("وَ اقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَ أَخْرِجُوهُمْ مِنْ حَيْثُ أَخْرَجُوكُمْ وَ الْفِتْنَةُ أَشَدُّ مِنَ الْقَتْلِ وَ لاٰ تُقٰاتِلُوهُمْ عِنْدَ الْمَسْجِدِ الْحَرٰامِ حَتّٰى يُقٰاتِلُوكُمْ فِيهِ فَإِنْ قٰاتَلُوكُمْ فَاقْتُلُوهُمْ كَذٰلِكَ جَزٰاءُ الْكٰافِرِينَ");
        sor.Ayaat.Add("فَإِنِ انْتَهَوْا فَإِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ قٰاتِلُوهُمْ حَتّٰى لاٰ تَكُونَ فِتْنَةٌ وَ يَكُونَ الدِّينُ لِلّٰهِ فَإِنِ انْتَهَوْا فَلاٰ عُدْوٰانَ إِلاّٰ عَلَى الظّٰالِمِينَ");
        sor.Ayaat.Add("الشَّهْرُ الْحَرٰامُ بِالشَّهْرِ الْحَرٰامِ وَ الْحُرُمٰاتُ قِصٰاصٌ فَمَنِ اعْتَدىٰ عَلَيْكُمْ فَاعْتَدُوا عَلَيْهِ بِمِثْلِ مَا اعْتَدىٰ عَلَيْكُمْ وَ اتَّقُوا اللّٰهَ وَ اعْلَمُوا أَنَّ اللّٰهَ مَعَ الْمُتَّقِينَ");
        sor.Ayaat.Add("وَ أَنْفِقُوا فِي سَبِيلِ اللّٰهِ وَ لاٰ تُلْقُوا بِأَيْدِيكُمْ إِلَى التَّهْلُكَةِ وَ أَحْسِنُوا إِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ أَتِمُّوا الْحَجَّ وَ الْعُمْرَةَ لِلّٰهِ فَإِنْ أُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ وَ لاٰ تَحْلِقُوا رُؤُسَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهُ فَمَنْ كٰانَ مِنْكُمْ مَرِيضاً أَوْ بِهِ أَذىً مِنْ رَأْسِهِ فَفِدْيَةٌ مِنْ صِيٰامٍ أَوْ صَدَقَةٍ أَوْ نُسُكٍ فَإِذٰا أَمِنْتُمْ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ إِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِ فَمَنْ لَمْ يَجِدْ فَصِيٰامُ ثَلاٰثَةِ أَيّٰامٍ فِي الْحَجِّ وَ سَبْعَةٍ إِذٰا رَجَعْتُمْ تِلْكَ عَشَرَةٌ كٰامِلَةٌ ذٰلِكَ لِمَنْ لَمْ يَكُنْ أَهْلُهُ حٰاضِرِي الْمَسْجِدِ الْحَرٰامِ وَ اتَّقُوا اللّٰهَ وَ اعْلَمُوا أَنَّ اللّٰهَ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("الْحَجُّ أَشْهُرٌ مَعْلُومٰاتٌ فَمَنْ فَرَضَ فِيهِنَّ الْحَجَّ فَلاٰ رَفَثَ وَ لاٰ فُسُوقَ وَ لاٰ جِدٰالَ فِي الْحَجِّ وَ مٰا تَفْعَلُوا مِنْ خَيْرٍ يَعْلَمْهُ اللّٰهُ وَ تَزَوَّدُوا فَإِنَّ خَيْرَ الزّٰادِ التَّقْوىٰ وَ اتَّقُونِ يٰا أُولِي الْأَلْبٰابِ");
        sor.Ayaat.Add("لَيْسَ عَلَيْكُمْ جُنٰاحٌ أَنْ تَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْ فَإِذٰا أَفَضْتُمْ مِنْ عَرَفٰاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرٰامِ وَ اذْكُرُوهُ كَمٰا هَدٰاكُمْ وَ إِنْ كُنْتُمْ مِنْ قَبْلِهِ لَمِنَ الضّٰالِّينَ");
        sor.Ayaat.Add("ثُمَّ أَفِيضُوا مِنْ حَيْثُ أَفٰاضَ النّٰاسُ وَ اسْتَغْفِرُوا اللّٰهَ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("فَإِذٰا قَضَيْتُمْ مَنٰاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ آبٰاءَكُمْ أَوْ أَشَدَّ ذِكْراً فَمِنَ النّٰاسِ مَنْ يَقُولُ رَبَّنٰا آتِنٰا فِي الدُّنْيٰا وَ مٰا لَهُ فِي الْآخِرَةِ مِنْ خَلاٰقٍ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يَقُولُ رَبَّنٰا آتِنٰا فِي الدُّنْيٰا حَسَنَةً وَ فِي الْآخِرَةِ حَسَنَةً وَ قِنٰا عَذٰابَ النّٰارِ");
        sor.Ayaat.Add("أُولٰئِكَ لَهُمْ نَصِيبٌ مِمّٰا كَسَبُوا وَ اللّٰهُ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("وَ اذْكُرُوا اللّٰهَ فِي أَيّٰامٍ مَعْدُودٰاتٍ فَمَنْ تَعَجَّلَ فِي يَوْمَيْنِ فَلاٰ إِثْمَ عَلَيْهِ وَ مَنْ تَأَخَّرَ فَلاٰ إِثْمَ عَلَيْهِ لِمَنِ اتَّقىٰ وَ اتَّقُوا اللّٰهَ وَ اعْلَمُوا أَنَّكُمْ إِلَيْهِ تُحْشَرُونَ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يُعْجِبُكَ قَوْلُهُ فِي الْحَيٰاةِ الدُّنْيٰا وَ يُشْهِدُ اللّٰهَ عَلىٰ مٰا فِي قَلْبِهِ وَ هُوَ أَلَدُّ الْخِصٰامِ");
        sor.Ayaat.Add("وَ إِذٰا تَوَلّٰى سَعىٰ فِي الْأَرْضِ لِيُفْسِدَ فِيهٰا وَ يُهْلِكَ الْحَرْثَ وَ النَّسْلَ وَ اللّٰهُ لاٰ يُحِبُّ الْفَسٰادَ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُ اتَّقِ اللّٰهَ أَخَذَتْهُ الْعِزَّةُ بِالْإِثْمِ فَحَسْبُهُ جَهَنَّمُ وَ لَبِئْسَ الْمِهٰادُ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يَشْرِي نَفْسَهُ ابْتِغٰاءَ مَرْضٰاتِ اللّٰهِ وَ اللّٰهُ رَؤُفٌ بِالْعِبٰادِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا ادْخُلُوا فِي السِّلْمِ كَافَّةً وَ لاٰ تَتَّبِعُوا خُطُوٰاتِ الشَّيْطٰانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ");
        sor.Ayaat.Add("فَإِنْ زَلَلْتُمْ مِنْ بَعْدِ مٰا جٰاءَتْكُمُ الْبَيِّنٰاتُ فَاعْلَمُوا أَنَّ اللّٰهَ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("هَلْ يَنْظُرُونَ إِلاّٰ أَنْ يَأْتِيَهُمُ اللّٰهُ فِي ظُلَلٍ مِنَ الْغَمٰامِ وَ الْمَلاٰئِكَةُ وَ قُضِيَ الْأَمْرُ وَ إِلَى اللّٰهِ تُرْجَعُ الْأُمُورُ");
        sor.Ayaat.Add("سَلْ بَنِي إِسْرٰائِيلَ كَمْ آتَيْنٰاهُمْ مِنْ آيَةٍ بَيِّنَةٍ وَ مَنْ يُبَدِّلْ نِعْمَةَ اللّٰهِ مِنْ بَعْدِ مٰا جٰاءَتْهُ فَإِنَّ اللّٰهَ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("زُيِّنَ لِلَّذِينَ كَفَرُوا الْحَيٰاةُ الدُّنْيٰا وَ يَسْخَرُونَ مِنَ الَّذِينَ آمَنُوا وَ الَّذِينَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰامَةِ وَ اللّٰهُ يَرْزُقُ مَنْ يَشٰاءُ بِغَيْرِ حِسٰابٍ");
        sor.Ayaat.Add("كٰانَ النّٰاسُ أُمَّةً وٰاحِدَةً فَبَعَثَ اللّٰهُ النَّبِيِّينَ مُبَشِّرِينَ وَ مُنْذِرِينَ وَ أَنْزَلَ مَعَهُمُ الْكِتٰابَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النّٰاسِ فِيمَا اخْتَلَفُوا فِيهِ وَ مَا اخْتَلَفَ فِيهِ إِلاَّ الَّذِينَ أُوتُوهُ مِنْ بَعْدِ مٰا جٰاءَتْهُمُ الْبَيِّنٰاتُ بَغْياً بَيْنَهُمْ فَهَدَى اللّٰهُ الَّذِينَ آمَنُوا لِمَا اخْتَلَفُوا فِيهِ مِنَ الْحَقِّ بِإِذْنِهِ وَ اللّٰهُ يَهْدِي مَنْ يَشٰاءُ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَ لَمّٰا يَأْتِكُمْ مَثَلُ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ مَسَّتْهُمُ الْبَأْسٰاءُ وَ الضَّرّٰاءُ وَ زُلْزِلُوا حَتّٰى يَقُولَ الرَّسُولُ وَ الَّذِينَ آمَنُوا مَعَهُ مَتىٰ نَصْرُ اللّٰهِ أَلاٰ إِنَّ نَصْرَ اللّٰهِ قَرِيبٌ");
        sor.Ayaat.Add("يَسْئَلُونَكَ مٰاذٰا يُنْفِقُونَ قُلْ مٰا أَنْفَقْتُمْ مِنْ خَيْرٍ فَلِلْوٰالِدَيْنِ وَ الْأَقْرَبِينَ وَ الْيَتٰامىٰ وَ الْمَسٰاكِينِ وَ ابْنِ السَّبِيلِ وَ مٰا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللّٰهَ بِهِ عَلِيمٌ");
        sor.Ayaat.Add("كُتِبَ عَلَيْكُمُ الْقِتٰالُ وَ هُوَ كُرْهٌ لَكُمْ وَ عَسىٰ أَنْ تَكْرَهُوا شَيْئاً وَ هُوَ خَيْرٌ لَكُمْ وَ عَسىٰ أَنْ تُحِبُّوا شَيْئاً وَ هُوَ شَرٌّ لَكُمْ وَ اللّٰهُ يَعْلَمُ وَ أَنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("يَسْئَلُونَكَ عَنِ الشَّهْرِ الْحَرٰامِ قِتٰالٍ فِيهِ قُلْ قِتٰالٌ فِيهِ كَبِيرٌ وَ صَدٌّ عَنْ سَبِيلِ اللّٰهِ وَ كُفْرٌ بِهِ وَ الْمَسْجِدِ الْحَرٰامِ وَ إِخْرٰاجُ أَهْلِهِ مِنْهُ أَكْبَرُ عِنْدَ اللّٰهِ وَ الْفِتْنَةُ أَكْبَرُ مِنَ الْقَتْلِ وَ لاٰ يَزٰالُونَ يُقٰاتِلُونَكُمْ حَتّٰى يَرُدُّوكُمْ عَنْ دِينِكُمْ إِنِ اسْتَطٰاعُوا وَ مَنْ يَرْتَدِدْ مِنْكُمْ عَنْ دِينِهِ فَيَمُتْ وَ هُوَ كٰافِرٌ فَأُولٰئِكَ حَبِطَتْ أَعْمٰالُهُمْ فِي الدُّنْيٰا وَ الْآخِرَةِ وَ أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هٰاجَرُوا وَ جٰاهَدُوا فِي سَبِيلِ اللّٰهِ أُولٰئِكَ يَرْجُونَ رَحْمَتَ اللّٰهِ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يَسْئَلُونَكَ عَنِ الْخَمْرِ وَ الْمَيْسِرِ قُلْ فِيهِمٰا إِثْمٌ كَبِيرٌ وَ مَنٰافِعُ لِلنّٰاسِ وَ إِثْمُهُمٰا أَكْبَرُ مِنْ نَفْعِهِمٰا وَ يَسْئَلُونَكَ مٰاذٰا يُنْفِقُونَ قُلِ الْعَفْوَ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْآيٰاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ");
        sor.Ayaat.Add("فِي الدُّنْيٰا وَ الْآخِرَةِ وَ يَسْئَلُونَكَ عَنِ الْيَتٰامىٰ قُلْ إِصْلاٰحٌ لَهُمْ خَيْرٌ وَ إِنْ تُخٰالِطُوهُمْ فَإِخْوٰانُكُمْ وَ اللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ وَ لَوْ شٰاءَ اللّٰهُ لَأَعْنَتَكُمْ إِنَّ اللّٰهَ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ لاٰ تَنْكِحُوا الْمُشْرِكٰاتِ حَتّٰى يُؤْمِنَّ وَ لَأَمَةٌ مُؤْمِنَةٌ خَيْرٌ مِنْ مُشْرِكَةٍ وَ لَوْ أَعْجَبَتْكُمْ وَ لاٰ تُنْكِحُوا الْمُشْرِكِينَ حَتّٰى يُؤْمِنُوا وَ لَعَبْدٌ مُؤْمِنٌ خَيْرٌ مِنْ مُشْرِكٍ وَ لَوْ أَعْجَبَكُمْ أُولٰئِكَ يَدْعُونَ إِلَى النّٰارِ وَ اللّٰهُ يَدْعُوا إِلَى الْجَنَّةِ وَ الْمَغْفِرَةِ بِإِذْنِهِ وَ يُبَيِّنُ آيٰاتِهِ لِلنّٰاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ");
        sor.Ayaat.Add("وَ يَسْئَلُونَكَ عَنِ الْمَحِيضِ قُلْ هُوَ أَذىً فَاعْتَزِلُوا النِّسٰاءَ فِي الْمَحِيضِ وَ لاٰ تَقْرَبُوهُنَّ حَتّٰى يَطْهُرْنَ فَإِذٰا تَطَهَّرْنَ فَأْتُوهُنَّ مِنْ حَيْثُ أَمَرَكُمُ اللّٰهُ إِنَّ اللّٰهَ يُحِبُّ التَّوّٰابِينَ وَ يُحِبُّ الْمُتَطَهِّرِينَ");
        sor.Ayaat.Add("نِسٰاؤُكُمْ حَرْثٌ لَكُمْ فَأْتُوا حَرْثَكُمْ أَنّٰى شِئْتُمْ وَ قَدِّمُوا لِأَنْفُسِكُمْ وَ اتَّقُوا اللّٰهَ وَ اعْلَمُوا أَنَّكُمْ مُلاٰقُوهُ وَ بَشِّرِ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ لاٰ تَجْعَلُوا اللّٰهَ عُرْضَةً لِأَيْمٰانِكُمْ أَنْ تَبَرُّوا وَ تَتَّقُوا وَ تُصْلِحُوا بَيْنَ النّٰاسِ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("لاٰ يُؤٰاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِي أَيْمٰانِكُمْ وَ لٰكِنْ يُؤٰاخِذُكُمْ بِمٰا كَسَبَتْ قُلُوبُكُمْ وَ اللّٰهُ غَفُورٌ حَلِيمٌ");
        sor.Ayaat.Add("لِلَّذِينَ يُؤْلُونَ مِنْ نِسٰائِهِمْ تَرَبُّصُ أَرْبَعَةِ أَشْهُرٍ فَإِنْ فٰاؤُ فَإِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ إِنْ عَزَمُوا الطَّلاٰقَ فَإِنَّ اللّٰهَ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ الْمُطَلَّقٰاتُ يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ ثَلاٰثَةَ قُرُوءٍ وَ لاٰ يَحِلُّ لَهُنَّ أَنْ يَكْتُمْنَ مٰا خَلَقَ اللّٰهُ فِي أَرْحٰامِهِنَّ إِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ بُعُولَتُهُنَّ أَحَقُّ بِرَدِّهِنَّ فِي ذٰلِكَ إِنْ أَرٰادُوا إِصْلاٰحاً وَ لَهُنَّ مِثْلُ الَّذِي عَلَيْهِنَّ بِالْمَعْرُوفِ وَ لِلرِّجٰالِ عَلَيْهِنَّ دَرَجَةٌ وَ اللّٰهُ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("الطَّلاٰقُ مَرَّتٰانِ فَإِمْسٰاكٌ بِمَعْرُوفٍ أَوْ تَسْرِيحٌ بِإِحْسٰانٍ وَ لاٰ يَحِلُّ لَكُمْ أَنْ تَأْخُذُوا مِمّٰا آتَيْتُمُوهُنَّ شَيْئاً إِلاّٰ أَنْ يَخٰافٰا أَلاّٰ يُقِيمٰا حُدُودَ اللّٰهِ فَإِنْ خِفْتُمْ أَلاّٰ يُقِيمٰا حُدُودَ اللّٰهِ فَلاٰ جُنٰاحَ عَلَيْهِمٰا فِيمَا افْتَدَتْ بِهِ تِلْكَ حُدُودُ اللّٰهِ فَلاٰ تَعْتَدُوهٰا وَ مَنْ يَتَعَدَّ حُدُودَ اللّٰهِ فَأُولٰئِكَ هُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("فَإِنْ طَلَّقَهٰا فَلاٰ تَحِلُّ لَهُ مِنْ بَعْدُ حَتّٰى تَنْكِحَ زَوْجاً غَيْرَهُ فَإِنْ طَلَّقَهٰا فَلاٰ جُنٰاحَ عَلَيْهِمٰا أَنْ يَتَرٰاجَعٰا إِنْ ظَنّٰا أَنْ يُقِيمٰا حُدُودَ اللّٰهِ وَ تِلْكَ حُدُودُ اللّٰهِ يُبَيِّنُهٰا لِقَوْمٍ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِذٰا طَلَّقْتُمُ النِّسٰاءَ فَبَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ سَرِّحُوهُنَّ بِمَعْرُوفٍ وَ لاٰ تُمْسِكُوهُنَّ ضِرٰاراً لِتَعْتَدُوا وَ مَنْ يَفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهُ وَ لاٰ تَتَّخِذُوا آيٰاتِ اللّٰهِ هُزُواً وَ اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ وَ مٰا أَنْزَلَ عَلَيْكُمْ مِنَ الْكِتٰابِ وَ الْحِكْمَةِ يَعِظُكُمْ بِهِ وَ اتَّقُوا اللّٰهَ وَ اعْلَمُوا أَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("وَ إِذٰا طَلَّقْتُمُ النِّسٰاءَ فَبَلَغْنَ أَجَلَهُنَّ فَلاٰ تَعْضُلُوهُنَّ أَنْ يَنْكِحْنَ أَزْوٰاجَهُنَّ إِذٰا تَرٰاضَوْا بَيْنَهُمْ بِالْمَعْرُوفِ ذٰلِكَ يُوعَظُ بِهِ مَنْ كٰانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ ذٰلِكُمْ أَزْكىٰ لَكُمْ وَ أَطْهَرُ وَ اللّٰهُ يَعْلَمُ وَ أَنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ الْوٰالِدٰاتُ يُرْضِعْنَ أَوْلاٰدَهُنَّ حَوْلَيْنِ كٰامِلَيْنِ لِمَنْ أَرٰادَ أَنْ يُتِمَّ الرَّضٰاعَةَ وَ عَلَى الْمَوْلُودِ لَهُ رِزْقُهُنَّ وَ كِسْوَتُهُنَّ بِالْمَعْرُوفِ لاٰ تُكَلَّفُ نَفْسٌ إِلاّٰ وُسْعَهٰا لاٰ تُضَارَّ وٰالِدَةٌ بِوَلَدِهٰا وَ لاٰ مَوْلُودٌ لَهُ بِوَلَدِهِ وَ عَلَى الْوٰارِثِ مِثْلُ ذٰلِكَ فَإِنْ أَرٰادٰا فِصٰالاً عَنْ تَرٰاضٍ مِنْهُمٰا وَ تَشٰاوُرٍ فَلاٰ جُنٰاحَ عَلَيْهِمٰا وَ إِنْ أَرَدْتُمْ أَنْ تَسْتَرْضِعُوا أَوْلاٰدَكُمْ فَلاٰ جُنٰاحَ عَلَيْكُمْ إِذٰا سَلَّمْتُمْ مٰا آتَيْتُمْ بِالْمَعْرُوفِ وَ اتَّقُوا اللّٰهَ وَ اعْلَمُوا أَنَّ اللّٰهَ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("وَ الَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَ يَذَرُونَ أَزْوٰاجاً يَتَرَبَّصْنَ بِأَنْفُسِهِنَّ أَرْبَعَةَ أَشْهُرٍ وَ عَشْراً فَإِذٰا بَلَغْنَ أَجَلَهُنَّ فَلاٰ جُنٰاحَ عَلَيْكُمْ فِيمٰا فَعَلْنَ فِي أَنْفُسِهِنَّ بِالْمَعْرُوفِ وَ اللّٰهُ بِمٰا تَعْمَلُونَ خَبِيرٌ");
        sor.Ayaat.Add("وَ لاٰ جُنٰاحَ عَلَيْكُمْ فِيمٰا عَرَّضْتُمْ بِهِ مِنْ خِطْبَةِ النِّسٰاءِ أَوْ أَكْنَنْتُمْ فِي أَنْفُسِكُمْ عَلِمَ اللّٰهُ أَنَّكُمْ سَتَذْكُرُونَهُنَّ وَ لٰكِنْ لاٰ تُوٰاعِدُوهُنَّ سِرًّا إِلاّٰ أَنْ تَقُولُوا قَوْلاً مَعْرُوفاً وَ لاٰ تَعْزِمُوا عُقْدَةَ النِّكٰاحِ حَتّٰى يَبْلُغَ الْكِتٰابُ أَجَلَهُ وَ اعْلَمُوا أَنَّ اللّٰهَ يَعْلَمُ مٰا فِي أَنْفُسِكُمْ فَاحْذَرُوهُ وَ اعْلَمُوا أَنَّ اللّٰهَ غَفُورٌ حَلِيمٌ");
        sor.Ayaat.Add("لاٰ جُنٰاحَ عَلَيْكُمْ إِنْ طَلَّقْتُمُ النِّسٰاءَ مٰا لَمْ تَمَسُّوهُنَّ أَوْ تَفْرِضُوا لَهُنَّ فَرِيضَةً وَ مَتِّعُوهُنَّ عَلَى الْمُوسِعِ قَدَرُهُ وَ عَلَى الْمُقْتِرِ قَدَرُهُ مَتٰاعاً بِالْمَعْرُوفِ حَقًّا عَلَى الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ إِنْ طَلَّقْتُمُوهُنَّ مِنْ قَبْلِ أَنْ تَمَسُّوهُنَّ وَ قَدْ فَرَضْتُمْ لَهُنَّ فَرِيضَةً فَنِصْفُ مٰا فَرَضْتُمْ إِلاّٰ أَنْ يَعْفُونَ أَوْ يَعْفُوَا الَّذِي بِيَدِهِ عُقْدَةُ النِّكٰاحِ وَ أَنْ تَعْفُوا أَقْرَبُ لِلتَّقْوىٰ وَ لاٰ تَنْسَوُا الْفَضْلَ بَيْنَكُمْ إِنَّ اللّٰهَ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("حٰافِظُوا عَلَى الصَّلَوٰاتِ وَ الصَّلاٰةِ الْوُسْطىٰ وَ قُومُوا لِلّٰهِ قٰانِتِينَ");
        sor.Ayaat.Add("فَإِنْ خِفْتُمْ فَرِجٰالاً أَوْ رُكْبٰاناً فَإِذٰا أَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمٰا عَلَّمَكُمْ مٰا لَمْ تَكُونُوا تَعْلَمُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ يُتَوَفَّوْنَ مِنْكُمْ وَ يَذَرُونَ أَزْوٰاجاً وَصِيَّةً لِأَزْوٰاجِهِمْ مَتٰاعاً إِلَى الْحَوْلِ غَيْرَ إِخْرٰاجٍ فَإِنْ خَرَجْنَ فَلاٰ جُنٰاحَ عَلَيْكُمْ فِي مٰا فَعَلْنَ فِي أَنْفُسِهِنَّ مِنْ مَعْرُوفٍ وَ اللّٰهُ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ لِلْمُطَلَّقٰاتِ مَتٰاعٌ بِالْمَعْرُوفِ حَقًّا عَلَى الْمُتَّقِينَ");
        sor.Ayaat.Add("كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ آيٰاتِهِ لَعَلَّكُمْ تَعْقِلُونَ");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ خَرَجُوا مِنْ دِيٰارِهِمْ وَ هُمْ أُلُوفٌ حَذَرَ الْمَوْتِ فَقٰالَ لَهُمُ اللّٰهُ مُوتُوا ثُمَّ أَحْيٰاهُمْ إِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النّٰاسِ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَشْكُرُونَ");
        sor.Ayaat.Add("وَ قٰاتِلُوا فِي سَبِيلِ اللّٰهِ وَ اعْلَمُوا أَنَّ اللّٰهَ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("مَنْ ذَا الَّذِي يُقْرِضُ اللّٰهَ قَرْضاً حَسَناً فَيُضٰاعِفَهُ لَهُ أَضْعٰافاً كَثِيرَةً وَ اللّٰهُ يَقْبِضُ وَ يَبْصُطُ وَ إِلَيْهِ تُرْجَعُونَ");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الْمَلَإِ مِنْ بَنِي إِسْرٰائِيلَ مِنْ بَعْدِ مُوسىٰ إِذْ قٰالُوا لِنَبِيٍّ لَهُمُ ابْعَثْ لَنٰا مَلِكاً نُقٰاتِلْ فِي سَبِيلِ اللّٰهِ قٰالَ هَلْ عَسَيْتُمْ إِنْ كُتِبَ عَلَيْكُمُ الْقِتٰالُ أَلاّٰ تُقٰاتِلُوا قٰالُوا وَ مٰا لَنٰا أَلاّٰ نُقٰاتِلَ فِي سَبِيلِ اللّٰهِ وَ قَدْ أُخْرِجْنٰا مِنْ دِيٰارِنٰا وَ أَبْنٰائِنٰا فَلَمّٰا كُتِبَ عَلَيْهِمُ الْقِتٰالُ تَوَلَّوْا إِلاّٰ قَلِيلاً مِنْهُمْ وَ اللّٰهُ عَلِيمٌ بِالظّٰالِمِينَ");
        sor.Ayaat.Add("وَ قٰالَ لَهُمْ نَبِيُّهُمْ إِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طٰالُوتَ مَلِكاً قٰالُوا أَنّٰى يَكُونُ لَهُ الْمُلْكُ عَلَيْنٰا وَ نَحْنُ أَحَقُّ بِالْمُلْكِ مِنْهُ وَ لَمْ يُؤْتَ سَعَةً مِنَ الْمٰالِ قٰالَ إِنَّ اللّٰهَ اصْطَفٰاهُ عَلَيْكُمْ وَ زٰادَهُ بَسْطَةً فِي الْعِلْمِ وَ الْجِسْمِ وَ اللّٰهُ يُؤْتِي مُلْكَهُ مَنْ يَشٰاءُ وَ اللّٰهُ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ قٰالَ لَهُمْ نَبِيُّهُمْ إِنَّ آيَةَ مُلْكِهِ أَنْ يَأْتِيَكُمُ التّٰابُوتُ فِيهِ سَكِينَةٌ مِنْ رَبِّكُمْ وَ بَقِيَّةٌ مِمّٰا تَرَكَ آلُ مُوسىٰ وَ آلُ هٰارُونَ تَحْمِلُهُ الْمَلاٰئِكَةُ إِنَّ فِي ذٰلِكَ لَآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("فَلَمّٰا فَصَلَ طٰالُوتُ بِالْجُنُودِ قٰالَ إِنَّ اللّٰهَ مُبْتَلِيكُمْ بِنَهَرٍ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّي وَ مَنْ لَمْ يَطْعَمْهُ فَإِنَّهُ مِنِّي إِلاّٰ مَنِ اغْتَرَفَ غُرْفَةً بِيَدِهِ فَشَرِبُوا مِنْهُ إِلاّٰ قَلِيلاً مِنْهُمْ فَلَمّٰا جٰاوَزَهُ هُوَ وَ الَّذِينَ آمَنُوا مَعَهُ قٰالُوا لاٰ طٰاقَةَ لَنَا الْيَوْمَ بِجٰالُوتَ وَ جُنُودِهِ قٰالَ الَّذِينَ يَظُنُّونَ أَنَّهُمْ مُلاٰقُوا اللّٰهِ كَمْ مِنْ فِئَةٍ قَلِيلَةٍ غَلَبَتْ فِئَةً كَثِيرَةً بِإِذْنِ اللّٰهِ وَ اللّٰهُ مَعَ الصّٰابِرِينَ");
        sor.Ayaat.Add("وَ لَمّٰا بَرَزُوا لِجٰالُوتَ وَ جُنُودِهِ قٰالُوا رَبَّنٰا أَفْرِغْ عَلَيْنٰا صَبْراً وَ ثَبِّتْ أَقْدٰامَنٰا وَ انْصُرْنٰا عَلَى الْقَوْمِ الْكٰافِرِينَ");
        sor.Ayaat.Add("فَهَزَمُوهُمْ بِإِذْنِ اللّٰهِ وَ قَتَلَ دٰاوُدُ جٰالُوتَ وَ آتٰاهُ اللّٰهُ الْمُلْكَ وَ الْحِكْمَةَ وَ عَلَّمَهُ مِمّٰا يَشٰاءُ وَ لَوْ لاٰ دَفْعُ اللّٰهِ النّٰاسَ بَعْضَهُمْ بِبَعْضٍ لَفَسَدَتِ الْأَرْضُ وَ لٰكِنَّ اللّٰهَ ذُو فَضْلٍ عَلَى الْعٰالَمِينَ");
        sor.Ayaat.Add("تِلْكَ آيٰاتُ اللّٰهِ نَتْلُوهٰا عَلَيْكَ بِالْحَقِّ وَ إِنَّكَ لَمِنَ الْمُرْسَلِينَ");
        sor.Ayaat.Add("تِلْكَ الرُّسُلُ فَضَّلْنٰا بَعْضَهُمْ عَلىٰ بَعْضٍ مِنْهُمْ مَنْ كَلَّمَ اللّٰهُ وَ رَفَعَ بَعْضَهُمْ دَرَجٰاتٍ وَ آتَيْنٰا عِيسَى ابْنَ مَرْيَمَ الْبَيِّنٰاتِ وَ أَيَّدْنٰاهُ بِرُوحِ الْقُدُسِ وَ لَوْ شٰاءَ اللّٰهُ مَا اقْتَتَلَ الَّذِينَ مِنْ بَعْدِهِمْ مِنْ بَعْدِ مٰا جٰاءَتْهُمُ الْبَيِّنٰاتُ وَ لٰكِنِ اخْتَلَفُوا فَمِنْهُمْ مَنْ آمَنَ وَ مِنْهُمْ مَنْ كَفَرَ وَ لَوْ شٰاءَ اللّٰهُ مَا اقْتَتَلُوا وَ لٰكِنَّ اللّٰهَ يَفْعَلُ مٰا يُرِيدُ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِمّٰا رَزَقْنٰاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لاٰ بَيْعٌ فِيهِ وَ لاٰ خُلَّةٌ وَ لاٰ شَفٰاعَةٌ وَ الْكٰافِرُونَ هُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("اللّٰهُ لاٰ إِلٰهَ إِلاّٰ هُوَ الْحَيُّ الْقَيُّومُ لاٰ تَأْخُذُهُ سِنَةٌ وَ لاٰ نَوْمٌ لَهُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ مَنْ ذَا الَّذِي يَشْفَعُ عِنْدَهُ إِلاّٰ بِإِذْنِهِ يَعْلَمُ مٰا بَيْنَ أَيْدِيهِمْ وَ مٰا خَلْفَهُمْ وَ لاٰ يُحِيطُونَ بِشَيْءٍ مِنْ عِلْمِهِ إِلاّٰ بِمٰا شٰاءَ وَسِعَ كُرْسِيُّهُ السَّمٰاوٰاتِ وَ الْأَرْضَ وَ لاٰ يَؤُدُهُ حِفْظُهُمٰا وَ هُوَ الْعَلِيُّ الْعَظِيمُ");
        sor.Ayaat.Add("لاٰ إِكْرٰاهَ فِي الدِّينِ قَدْ تَبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ فَمَنْ يَكْفُرْ بِالطّٰاغُوتِ وَ يُؤْمِنْ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقىٰ لاَ انْفِصٰامَ لَهٰا وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("اللّٰهُ وَلِيُّ الَّذِينَ آمَنُوا يُخْرِجُهُمْ مِنَ الظُّلُمٰاتِ إِلَى النُّورِ وَ الَّذِينَ كَفَرُوا أَوْلِيٰاؤُهُمُ الطّٰاغُوتُ يُخْرِجُونَهُمْ مِنَ النُّورِ إِلَى الظُّلُمٰاتِ أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِي حَاجَّ إِبْرٰاهِيمَ فِي رَبِّهِ أَنْ آتٰاهُ اللّٰهُ الْمُلْكَ إِذْ قٰالَ إِبْرٰاهِيمُ رَبِّيَ الَّذِي يُحْيِي وَ يُمِيتُ قٰالَ أَنَا أُحْيِي وَ أُمِيتُ قٰالَ إِبْرٰاهِيمُ فَإِنَّ اللّٰهَ يَأْتِي بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهٰا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِي كَفَرَ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الظّٰالِمِينَ");
        sor.Ayaat.Add("أَوْ كَالَّذِي مَرَّ عَلىٰ قَرْيَةٍ وَ هِيَ خٰاوِيَةٌ عَلىٰ عُرُوشِهٰا قٰالَ أَنّٰى يُحْيِي هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهٰا فَأَمٰاتَهُ اللّٰهُ مِائَةَ عٰامٍ ثُمَّ بَعَثَهُ قٰالَ كَمْ لَبِثْتَ قٰالَ لَبِثْتُ يَوْماً أَوْ بَعْضَ يَوْمٍ قٰالَ بَلْ لَبِثْتَ مِائَةَ عٰامٍ فَانْظُرْ إِلىٰ طَعٰامِكَ وَ شَرٰابِكَ لَمْ يَتَسَنَّهْ وَ انْظُرْ إِلىٰ حِمٰارِكَ وَ لِنَجْعَلَكَ آيَةً لِلنّٰاسِ وَ انْظُرْ إِلَى الْعِظٰامِ كَيْفَ نُنْشِزُهٰا ثُمَّ نَكْسُوهٰا لَحْماً فَلَمّٰا تَبَيَّنَ لَهُ قٰالَ أَعْلَمُ أَنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ إِبْرٰاهِيمُ رَبِّ أَرِنِي كَيْفَ تُحْيِ الْمَوْتىٰ قٰالَ أَ وَ لَمْ تُؤْمِنْ قٰالَ بَلىٰ وَ لٰكِنْ لِيَطْمَئِنَّ قَلْبِي قٰالَ فَخُذْ أَرْبَعَةً مِنَ الطَّيْرِ فَصُرْهُنَّ إِلَيْكَ ثُمَّ اجْعَلْ عَلىٰ كُلِّ جَبَلٍ مِنْهُنَّ جُزْءاً ثُمَّ ادْعُهُنَّ يَأْتِينَكَ سَعْياً وَ اعْلَمْ أَنَّ اللّٰهَ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوٰالَهُمْ فِي سَبِيلِ اللّٰهِ كَمَثَلِ حَبَّةٍ أَنْبَتَتْ سَبْعَ سَنٰابِلَ فِي كُلِّ سُنْبُلَةٍ مِائَةُ حَبَّةٍ وَ اللّٰهُ يُضٰاعِفُ لِمَنْ يَشٰاءُ وَ اللّٰهُ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("الَّذِينَ يُنْفِقُونَ أَمْوٰالَهُمْ فِي سَبِيلِ اللّٰهِ ثُمَّ لاٰ يُتْبِعُونَ مٰا أَنْفَقُوا مَنًّا وَ لاٰ أَذىً لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("قَوْلٌ مَعْرُوفٌ وَ مَغْفِرَةٌ خَيْرٌ مِنْ صَدَقَةٍ يَتْبَعُهٰا أَذىً وَ اللّٰهُ غَنِيٌّ حَلِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تُبْطِلُوا صَدَقٰاتِكُمْ بِالْمَنِّ وَ الْأَذىٰ كَالَّذِي يُنْفِقُ مٰالَهُ رِئٰاءَ النّٰاسِ وَ لاٰ يُؤْمِنُ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ فَمَثَلُهُ كَمَثَلِ صَفْوٰانٍ عَلَيْهِ تُرٰابٌ فَأَصٰابَهُ وٰابِلٌ فَتَرَكَهُ صَلْداً لاٰ يَقْدِرُونَ عَلىٰ شَيْءٍ مِمّٰا كَسَبُوا وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الْكٰافِرِينَ");
        sor.Ayaat.Add("وَ مَثَلُ الَّذِينَ يُنْفِقُونَ أَمْوٰالَهُمُ ابْتِغٰاءَ مَرْضٰاتِ اللّٰهِ وَ تَثْبِيتاً مِنْ أَنْفُسِهِمْ كَمَثَلِ جَنَّةٍ بِرَبْوَةٍ أَصٰابَهٰا وٰابِلٌ فَآتَتْ أُكُلَهٰا ضِعْفَيْنِ فَإِنْ لَمْ يُصِبْهٰا وٰابِلٌ فَطَلٌّ وَ اللّٰهُ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("أَ يَوَدُّ أَحَدُكُمْ أَنْ تَكُونَ لَهُ جَنَّةٌ مِنْ نَخِيلٍ وَ أَعْنٰابٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ لَهُ فِيهٰا مِنْ كُلِّ الثَّمَرٰاتِ وَ أَصٰابَهُ الْكِبَرُ وَ لَهُ ذُرِّيَّةٌ ضُعَفٰاءُ فَأَصٰابَهٰا إِعْصٰارٌ فِيهِ نٰارٌ فَاحْتَرَقَتْ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْآيٰاتِ لَعَلَّكُمْ تَتَفَكَّرُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا أَنْفِقُوا مِنْ طَيِّبٰاتِ مٰا كَسَبْتُمْ وَ مِمّٰا أَخْرَجْنٰا لَكُمْ مِنَ الْأَرْضِ وَ لاٰ تَيَمَّمُوا الْخَبِيثَ مِنْهُ تُنْفِقُونَ وَ لَسْتُمْ بِآخِذِيهِ إِلاّٰ أَنْ تُغْمِضُوا فِيهِ وَ اعْلَمُوا أَنَّ اللّٰهَ غَنِيٌّ حَمِيدٌ");
        sor.Ayaat.Add("الشَّيْطٰانُ يَعِدُكُمُ الْفَقْرَ وَ يَأْمُرُكُمْ بِالْفَحْشٰاءِ وَ اللّٰهُ يَعِدُكُمْ مَغْفِرَةً مِنْهُ وَ فَضْلاً وَ اللّٰهُ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("يُؤْتِي الْحِكْمَةَ مَنْ يَشٰاءُ وَ مَنْ يُؤْتَ الْحِكْمَةَ فَقَدْ أُوتِيَ خَيْراً كَثِيراً وَ مٰا يَذَّكَّرُ إِلاّٰ أُولُوا الْأَلْبٰابِ");
        sor.Ayaat.Add("وَ مٰا أَنْفَقْتُمْ مِنْ نَفَقَةٍ أَوْ نَذَرْتُمْ مِنْ نَذْرٍ فَإِنَّ اللّٰهَ يَعْلَمُهُ وَ مٰا لِلظّٰالِمِينَ مِنْ أَنْصٰارٍ");
        sor.Ayaat.Add("إِنْ تُبْدُوا الصَّدَقٰاتِ فَنِعِمّٰا هِيَ وَ إِنْ تُخْفُوهٰا وَ تُؤْتُوهَا الْفُقَرٰاءَ فَهُوَ خَيْرٌ لَكُمْ وَ يُكَفِّرُ عَنْكُمْ مِنْ سَيِّئٰاتِكُمْ وَ اللّٰهُ بِمٰا تَعْمَلُونَ خَبِيرٌ");
        sor.Ayaat.Add("لَيْسَ عَلَيْكَ هُدٰاهُمْ وَ لٰكِنَّ اللّٰهَ يَهْدِي مَنْ يَشٰاءُ وَ مٰا تُنْفِقُوا مِنْ خَيْرٍ فَلِأَنْفُسِكُمْ وَ مٰا تُنْفِقُونَ إِلاَّ ابْتِغٰاءَ وَجْهِ اللّٰهِ وَ مٰا تُنْفِقُوا مِنْ خَيْرٍ يُوَفَّ إِلَيْكُمْ وَ أَنْتُمْ لاٰ تُظْلَمُونَ");
        sor.Ayaat.Add("لِلْفُقَرٰاءِ الَّذِينَ أُحْصِرُوا فِي سَبِيلِ اللّٰهِ لاٰ يَسْتَطِيعُونَ ضَرْباً فِي الْأَرْضِ يَحْسَبُهُمُ الْجٰاهِلُ أَغْنِيٰاءَ مِنَ التَّعَفُّفِ تَعْرِفُهُمْ بِسِيمٰاهُمْ لاٰ يَسْئَلُونَ النّٰاسَ إِلْحٰافاً وَ مٰا تُنْفِقُوا مِنْ خَيْرٍ فَإِنَّ اللّٰهَ بِهِ عَلِيمٌ");
        sor.Ayaat.Add("الَّذِينَ يُنْفِقُونَ أَمْوٰالَهُمْ بِاللَّيْلِ وَ النَّهٰارِ سِرًّا وَ عَلاٰنِيَةً فَلَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("الَّذِينَ يَأْكُلُونَ الرِّبٰا لاٰ يَقُومُونَ إِلاّٰ كَمٰا يَقُومُ الَّذِي يَتَخَبَّطُهُ الشَّيْطٰانُ مِنَ الْمَسِّ ذٰلِكَ بِأَنَّهُمْ قٰالُوا إِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰا وَ أَحَلَّ اللّٰهُ الْبَيْعَ وَ حَرَّمَ الرِّبٰا فَمَنْ جٰاءَهُ مَوْعِظَةٌ مِنْ رَبِّهِ فَانْتَهىٰ فَلَهُ مٰا سَلَفَ وَ أَمْرُهُ إِلَى اللّٰهِ وَ مَنْ عٰادَ فَأُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("يَمْحَقُ اللّٰهُ الرِّبٰا وَ يُرْبِي الصَّدَقٰاتِ وَ اللّٰهُ لاٰ يُحِبُّ كُلَّ كَفّٰارٍ أَثِيمٍ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ وَ أَقٰامُوا الصَّلاٰةَ وَ آتَوُا الزَّكٰاةَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ وَ لاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللّٰهَ وَ ذَرُوا مٰا بَقِيَ مِنَ الرِّبٰا إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("فَإِنْ لَمْ تَفْعَلُوا فَأْذَنُوا بِحَرْبٍ مِنَ اللّٰهِ وَ رَسُولِهِ وَ إِنْ تُبْتُمْ فَلَكُمْ رُؤُسُ أَمْوٰالِكُمْ لاٰ تَظْلِمُونَ وَ لاٰ تُظْلَمُونَ");
        sor.Ayaat.Add("وَ إِنْ كٰانَ ذُو عُسْرَةٍ فَنَظِرَةٌ إِلىٰ مَيْسَرَةٍ وَ أَنْ تَصَدَّقُوا خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ اتَّقُوا يَوْماً تُرْجَعُونَ فِيهِ إِلَى اللّٰهِ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مٰا كَسَبَتْ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِذٰا تَدٰايَنْتُمْ بِدَيْنٍ إِلىٰ أَجَلٍ مُسَمًّى فَاكْتُبُوهُ وَ لْيَكْتُبْ بَيْنَكُمْ كٰاتِبٌ بِالْعَدْلِ وَ لاٰ يَأْبَ كٰاتِبٌ أَنْ يَكْتُبَ كَمٰا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْ وَ لْيُمْلِلِ الَّذِي عَلَيْهِ الْحَقُّ وَ لْيَتَّقِ اللّٰهَ رَبَّهُ وَ لاٰ يَبْخَسْ مِنْهُ شَيْئاً فَإِنْ كٰانَ الَّذِي عَلَيْهِ الْحَقُّ سَفِيهاً أَوْ ضَعِيفاً أَوْ لاٰ يَسْتَطِيعُ أَنْ يُمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهُ بِالْعَدْلِ وَ اسْتَشْهِدُوا شَهِيدَيْنِ مِنْ رِجٰالِكُمْ فَإِنْ لَمْ يَكُونٰا رَجُلَيْنِ فَرَجُلٌ وَ امْرَأَتٰانِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدٰاءِ أَنْ تَضِلَّ إِحْدٰاهُمٰا فَتُذَكِّرَ إِحْدٰاهُمَا الْأُخْرىٰ وَ لاٰ يَأْبَ الشُّهَدٰاءُ إِذٰا مٰا دُعُوا وَ لاٰ تَسْئَمُوا أَنْ تَكْتُبُوهُ صَغِيراً أَوْ كَبِيراً إِلىٰ أَجَلِهِ ذٰلِكُمْ أَقْسَطُ عِنْدَ اللّٰهِ وَ أَقْوَمُ لِلشَّهٰادَةِ وَ أَدْنىٰ أَلاّٰ تَرْتٰابُوا إِلاّٰ أَنْ تَكُونَ تِجٰارَةً حٰاضِرَةً تُدِيرُونَهٰا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنٰاحٌ أَلاّٰ تَكْتُبُوهٰا وَ أَشْهِدُوا إِذٰا تَبٰايَعْتُمْ وَ لاٰ يُضَارَّ كٰاتِبٌ وَ لاٰ شَهِيدٌ وَ إِنْ تَفْعَلُوا فَإِنَّهُ فُسُوقٌ بِكُمْ وَ اتَّقُوا اللّٰهَ وَ يُعَلِّمُكُمُ اللّٰهُ وَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("وَ إِنْ كُنْتُمْ عَلىٰ سَفَرٍ وَ لَمْ تَجِدُوا كٰاتِباً فَرِهٰانٌ مَقْبُوضَةٌ فَإِنْ أَمِنَ بَعْضُكُمْ بَعْضاً فَلْيُؤَدِّ الَّذِي اؤْتُمِنَ أَمٰانَتَهُ وَ لْيَتَّقِ اللّٰهَ رَبَّهُ وَ لاٰ تَكْتُمُوا الشَّهٰادَةَ وَ مَنْ يَكْتُمْهٰا فَإِنَّهُ آثِمٌ قَلْبُهُ وَ اللّٰهُ بِمٰا تَعْمَلُونَ عَلِيمٌ");
        sor.Ayaat.Add("لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ إِنْ تُبْدُوا مٰا فِي أَنْفُسِكُمْ أَوْ تُخْفُوهُ يُحٰاسِبْكُمْ بِهِ اللّٰهُ فَيَغْفِرُ لِمَنْ يَشٰاءُ وَ يُعَذِّبُ مَنْ يَشٰاءُ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("آمَنَ الرَّسُولُ بِمٰا أُنْزِلَ إِلَيْهِ مِنْ رَبِّهِ وَ الْمُؤْمِنُونَ كُلٌّ آمَنَ بِاللّٰهِ وَ مَلاٰئِكَتِهِ وَ كُتُبِهِ وَ رُسُلِهِ لاٰ نُفَرِّقُ بَيْنَ أَحَدٍ مِنْ رُسُلِهِ وَ قٰالُوا سَمِعْنٰا وَ أَطَعْنٰا غُفْرٰانَكَ رَبَّنٰا وَ إِلَيْكَ الْمَصِيرُ");
        sor.Ayaat.Add("لاٰ يُكَلِّفُ اللّٰهُ نَفْساً إِلاّٰ وُسْعَهٰا لَهٰا مٰا كَسَبَتْ وَ عَلَيْهٰا مَا اكْتَسَبَتْ رَبَّنٰا لاٰ تُؤٰاخِذْنٰا إِنْ نَسِينٰا أَوْ أَخْطَأْنٰا رَبَّنٰا وَ لاٰ تَحْمِلْ عَلَيْنٰا إِصْراً كَمٰا حَمَلْتَهُ عَلَى الَّذِينَ مِنْ قَبْلِنٰا رَبَّنٰا وَ لاٰ تُحَمِّلْنٰا مٰا لاٰ طٰاقَةَ لَنٰا بِهِ وَ اعْفُ عَنّٰا وَ اغْفِرْ لَنٰا وَ ارْحَمْنٰا أَنْتَ مَوْلاٰنٰا فَانْصُرْنٰا عَلَى الْقَوْمِ الْكٰافِرِينَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٣ - آل عمران";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الم");
        sor.Ayaat.Add("اللّٰهُ لاٰ إِلٰهَ إِلاّٰ هُوَ الْحَيُّ الْقَيُّومُ");
        sor.Ayaat.Add("نَزَّلَ عَلَيْكَ الْكِتٰابَ بِالْحَقِّ مُصَدِّقاً لِمٰا بَيْنَ يَدَيْهِ وَ أَنْزَلَ التَّوْرٰاةَ وَ الْإِنْجِيلَ");
        sor.Ayaat.Add("مِنْ قَبْلُ هُدىً لِلنّٰاسِ وَ أَنْزَلَ الْفُرْقٰانَ إِنَّ الَّذِينَ كَفَرُوا بِآيٰاتِ اللّٰهِ لَهُمْ عَذٰابٌ شَدِيدٌ وَ اللّٰهُ عَزِيزٌ ذُو انْتِقٰامٍ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لاٰ يَخْفىٰ عَلَيْهِ شَيْءٌ فِي الْأَرْضِ وَ لاٰ فِي السَّمٰاءِ");
        sor.Ayaat.Add("هُوَ الَّذِي يُصَوِّرُكُمْ فِي الْأَرْحٰامِ كَيْفَ يَشٰاءُ لاٰ إِلٰهَ إِلاّٰ هُوَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("هُوَ الَّذِي أَنْزَلَ عَلَيْكَ الْكِتٰابَ مِنْهُ آيٰاتٌ مُحْكَمٰاتٌ هُنَّ أُمُّ الْكِتٰابِ وَ أُخَرُ مُتَشٰابِهٰاتٌ فَأَمَّا الَّذِينَ فِي قُلُوبِهِمْ زَيْغٌ فَيَتَّبِعُونَ مٰا تَشٰابَهَ مِنْهُ ابْتِغٰاءَ الْفِتْنَةِ وَ ابْتِغٰاءَ تَأْوِيلِهِ وَ مٰا يَعْلَمُ تَأْوِيلَهُ إِلاَّ اللّٰهُ وَ الرّٰاسِخُونَ فِي الْعِلْمِ يَقُولُونَ آمَنّٰا بِهِ كُلٌّ مِنْ عِنْدِ رَبِّنٰا وَ مٰا يَذَّكَّرُ إِلاّٰ أُولُوا الْأَلْبٰابِ");
        sor.Ayaat.Add("رَبَّنٰا لاٰ تُزِغْ قُلُوبَنٰا بَعْدَ إِذْ هَدَيْتَنٰا وَ هَبْ لَنٰا مِنْ لَدُنْكَ رَحْمَةً إِنَّكَ أَنْتَ الْوَهّٰابُ");
        sor.Ayaat.Add("رَبَّنٰا إِنَّكَ جٰامِعُ النّٰاسِ لِيَوْمٍ لاٰ رَيْبَ فِيهِ إِنَّ اللّٰهَ لاٰ يُخْلِفُ الْمِيعٰادَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوٰالُهُمْ وَ لاٰ أَوْلاٰدُهُمْ مِنَ اللّٰهِ شَيْئاً وَ أُولٰئِكَ هُمْ وَقُودُ النّٰارِ");
        sor.Ayaat.Add("كَدَأْبِ آلِ فِرْعَوْنَ وَ الَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآيٰاتِنٰا فَأَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْ وَ اللّٰهُ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("قُلْ لِلَّذِينَ كَفَرُوا سَتُغْلَبُونَ وَ تُحْشَرُونَ إِلىٰ جَهَنَّمَ وَ بِئْسَ الْمِهٰادُ");
        sor.Ayaat.Add("قَدْ كٰانَ لَكُمْ آيَةٌ فِي فِئَتَيْنِ الْتَقَتٰا فِئَةٌ تُقٰاتِلُ فِي سَبِيلِ اللّٰهِ وَ أُخْرىٰ كٰافِرَةٌ يَرَوْنَهُمْ مِثْلَيْهِمْ رَأْيَ الْعَيْنِ وَ اللّٰهُ يُؤَيِّدُ بِنَصْرِهِ مَنْ يَشٰاءُ إِنَّ فِي ذٰلِكَ لَعِبْرَةً لِأُولِي الْأَبْصٰارِ");
        sor.Ayaat.Add("زُيِّنَ لِلنّٰاسِ حُبُّ الشَّهَوٰاتِ مِنَ النِّسٰاءِ وَ الْبَنِينَ وَ الْقَنٰاطِيرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَ الْفِضَّةِ وَ الْخَيْلِ الْمُسَوَّمَةِ وَ الْأَنْعٰامِ وَ الْحَرْثِ ذٰلِكَ مَتٰاعُ الْحَيٰاةِ الدُّنْيٰا وَ اللّٰهُ عِنْدَهُ حُسْنُ الْمَآبِ");
        sor.Ayaat.Add("قُلْ أَ أُنَبِّئُكُمْ بِخَيْرٍ مِنْ ذٰلِكُمْ لِلَّذِينَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنّٰاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا وَ أَزْوٰاجٌ مُطَهَّرَةٌ وَ رِضْوٰانٌ مِنَ اللّٰهِ وَ اللّٰهُ بَصِيرٌ بِالْعِبٰادِ");
        sor.Ayaat.Add("الَّذِينَ يَقُولُونَ رَبَّنٰا إِنَّنٰا آمَنّٰا فَاغْفِرْ لَنٰا ذُنُوبَنٰا وَ قِنٰا عَذٰابَ النّٰارِ");
        sor.Ayaat.Add("الصّٰابِرِينَ وَ الصّٰادِقِينَ وَ الْقٰانِتِينَ وَ الْمُنْفِقِينَ وَ الْمُسْتَغْفِرِينَ بِالْأَسْحٰارِ");
        sor.Ayaat.Add("شَهِدَ اللّٰهُ أَنَّهُ لاٰ إِلٰهَ إِلاّٰ هُوَ وَ الْمَلاٰئِكَةُ وَ أُولُوا الْعِلْمِ قٰائِماً بِالْقِسْطِ لاٰ إِلٰهَ إِلاّٰ هُوَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("إِنَّ الدِّينَ عِنْدَ اللّٰهِ الْإِسْلاٰمُ وَ مَا اخْتَلَفَ الَّذِينَ أُوتُوا الْكِتٰابَ إِلاّٰ مِنْ بَعْدِ مٰا جٰاءَهُمُ الْعِلْمُ بَغْياً بَيْنَهُمْ وَ مَنْ يَكْفُرْ بِآيٰاتِ اللّٰهِ فَإِنَّ اللّٰهَ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("فَإِنْ حَاجُّوكَ فَقُلْ أَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَ مَنِ اتَّبَعَنِ وَ قُلْ لِلَّذِينَ أُوتُوا الْكِتٰابَ وَ الْأُمِّيِّينَ أَ أَسْلَمْتُمْ فَإِنْ أَسْلَمُوا فَقَدِ اهْتَدَوْا وَ إِنْ تَوَلَّوْا فَإِنَّمٰا عَلَيْكَ الْبَلاٰغُ وَ اللّٰهُ بَصِيرٌ بِالْعِبٰادِ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَكْفُرُونَ بِآيٰاتِ اللّٰهِ وَ يَقْتُلُونَ النَّبِيِّينَ بِغَيْرِ حَقٍّ وَ يَقْتُلُونَ الَّذِينَ يَأْمُرُونَ بِالْقِسْطِ مِنَ النّٰاسِ فَبَشِّرْهُمْ بِعَذٰابٍ أَلِيمٍ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ حَبِطَتْ أَعْمٰالُهُمْ فِي الدُّنْيٰا وَ الْآخِرَةِ وَ مٰا لَهُمْ مِنْ نٰاصِرِينَ");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيباً مِنَ الْكِتٰابِ يُدْعَوْنَ إِلىٰ كِتٰابِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَرِيقٌ مِنْهُمْ وَ هُمْ مُعْرِضُونَ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّهُمْ قٰالُوا لَنْ تَمَسَّنَا النّٰارُ إِلاّٰ أَيّٰاماً مَعْدُودٰاتٍ وَ غَرَّهُمْ فِي دِينِهِمْ مٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("فَكَيْفَ إِذٰا جَمَعْنٰاهُمْ لِيَوْمٍ لاٰ رَيْبَ فِيهِ وَ وُفِّيَتْ كُلُّ نَفْسٍ مٰا كَسَبَتْ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("قُلِ اللّٰهُمَّ مٰالِكَ الْمُلْكِ تُؤْتِي الْمُلْكَ مَنْ تَشٰاءُ وَ تَنْزِعُ الْمُلْكَ مِمَّنْ تَشٰاءُ وَ تُعِزُّ مَنْ تَشٰاءُ وَ تُذِلُّ مَنْ تَشٰاءُ بِيَدِكَ الْخَيْرُ إِنَّكَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("تُولِجُ اللَّيْلَ فِي النَّهٰارِ وَ تُولِجُ النَّهٰارَ فِي اللَّيْلِ وَ تُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ تُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَ تَرْزُقُ مَنْ تَشٰاءُ بِغَيْرِ حِسٰابٍ");
        sor.Ayaat.Add("لاٰ يَتَّخِذِ الْمُؤْمِنُونَ الْكٰافِرِينَ أَوْلِيٰاءَ مِنْ دُونِ الْمُؤْمِنِينَ وَ مَنْ يَفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ فِي شَيْءٍ إِلاّٰ أَنْ تَتَّقُوا مِنْهُمْ تُقٰاةً وَ يُحَذِّرُكُمُ اللّٰهُ نَفْسَهُ وَ إِلَى اللّٰهِ الْمَصِيرُ");
        sor.Ayaat.Add("قُلْ إِنْ تُخْفُوا مٰا فِي صُدُورِكُمْ أَوْ تُبْدُوهُ يَعْلَمْهُ اللّٰهُ وَ يَعْلَمُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("يَوْمَ تَجِدُ كُلُّ نَفْسٍ مٰا عَمِلَتْ مِنْ خَيْرٍ مُحْضَراً وَ مٰا عَمِلَتْ مِنْ سُوءٍ تَوَدُّ لَوْ أَنَّ بَيْنَهٰا وَ بَيْنَهُ أَمَداً بَعِيداً وَ يُحَذِّرُكُمُ اللّٰهُ نَفْسَهُ وَ اللّٰهُ رَؤُفٌ بِالْعِبٰادِ");
        sor.Ayaat.Add("قُلْ إِنْ كُنْتُمْ تُحِبُّونَ اللّٰهَ فَاتَّبِعُونِي يُحْبِبْكُمُ اللّٰهُ وَ يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("قُلْ أَطِيعُوا اللّٰهَ وَ الرَّسُولَ فَإِنْ تَوَلَّوْا فَإِنَّ اللّٰهَ لاٰ يُحِبُّ الْكٰافِرِينَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ اصْطَفىٰ آدَمَ وَ نُوحاً وَ آلَ إِبْرٰاهِيمَ وَ آلَ عِمْرٰانَ عَلَى الْعٰالَمِينَ");
        sor.Ayaat.Add("ذُرِّيَّةً بَعْضُهٰا مِنْ بَعْضٍ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("إِذْ قٰالَتِ امْرَأَتُ عِمْرٰانَ رَبِّ إِنِّي نَذَرْتُ لَكَ مٰا فِي بَطْنِي مُحَرَّراً فَتَقَبَّلْ مِنِّي إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("فَلَمّٰا وَضَعَتْهٰا قٰالَتْ رَبِّ إِنِّي وَضَعْتُهٰا أُنْثىٰ وَ اللّٰهُ أَعْلَمُ بِمٰا وَضَعَتْ وَ لَيْسَ الذَّكَرُ كَالْأُنْثىٰ وَ إِنِّي سَمَّيْتُهٰا مَرْيَمَ وَ إِنِّي أُعِيذُهٰا بِكَ وَ ذُرِّيَّتَهٰا مِنَ الشَّيْطٰانِ الرَّجِيمِ");
        sor.Ayaat.Add("فَتَقَبَّلَهٰا رَبُّهٰا بِقَبُولٍ حَسَنٍ وَ أَنْبَتَهٰا نَبٰاتاً حَسَناً وَ كَفَّلَهٰا زَكَرِيّٰا كُلَّمٰا دَخَلَ عَلَيْهٰا زَكَرِيَّا الْمِحْرٰابَ وَجَدَ عِنْدَهٰا رِزْقاً قٰالَ يٰا مَرْيَمُ أَنّٰى لَكِ هٰذٰا قٰالَتْ هُوَ مِنْ عِنْدِ اللّٰهِ إِنَّ اللّٰهَ يَرْزُقُ مَنْ يَشٰاءُ بِغَيْرِ حِسٰابٍ");
        sor.Ayaat.Add("هُنٰالِكَ دَعٰا زَكَرِيّٰا رَبَّهُ قٰالَ رَبِّ هَبْ لِي مِنْ لَدُنْكَ ذُرِّيَّةً طَيِّبَةً إِنَّكَ سَمِيعُ الدُّعٰاءِ");
        sor.Ayaat.Add("فَنٰادَتْهُ الْمَلاٰئِكَةُ وَ هُوَ قٰائِمٌ يُصَلِّي فِي الْمِحْرٰابِ أَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيىٰ مُصَدِّقاً بِكَلِمَةٍ مِنَ اللّٰهِ وَ سَيِّداً وَ حَصُوراً وَ نَبِيًّا مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("قٰالَ رَبِّ أَنّٰى يَكُونُ لِي غُلاٰمٌ وَ قَدْ بَلَغَنِيَ الْكِبَرُ وَ امْرَأَتِي عٰاقِرٌ قٰالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مٰا يَشٰاءُ");
        sor.Ayaat.Add("قٰالَ رَبِّ اجْعَلْ لِي آيَةً قٰالَ آيَتُكَ أَلاّٰ تُكَلِّمَ النّٰاسَ ثَلاٰثَةَ أَيّٰامٍ إِلاّٰ رَمْزاً وَ اذْكُرْ رَبَّكَ كَثِيراً وَ سَبِّحْ بِالْعَشِيِّ وَ الْإِبْكٰارِ");
        sor.Ayaat.Add("وَ إِذْ قٰالَتِ الْمَلاٰئِكَةُ يٰا مَرْيَمُ إِنَّ اللّٰهَ اصْطَفٰاكِ وَ طَهَّرَكِ وَ اصْطَفٰاكِ عَلىٰ نِسٰاءِ الْعٰالَمِينَ");
        sor.Ayaat.Add("يٰا مَرْيَمُ اقْنُتِي لِرَبِّكِ وَ اسْجُدِي وَ ارْكَعِي مَعَ الرّٰاكِعِينَ");
        sor.Ayaat.Add("ذٰلِكَ مِنْ أَنْبٰاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَ مٰا كُنْتَ لَدَيْهِمْ إِذْ يُلْقُونَ أَقْلاٰمَهُمْ أَيُّهُمْ يَكْفُلُ مَرْيَمَ وَ مٰا كُنْتَ لَدَيْهِمْ إِذْ يَخْتَصِمُونَ");
        sor.Ayaat.Add("إِذْ قٰالَتِ الْمَلاٰئِكَةُ يٰا مَرْيَمُ إِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِنْهُ اسْمُهُ الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ وَجِيهاً فِي الدُّنْيٰا وَ الْآخِرَةِ وَ مِنَ الْمُقَرَّبِينَ");
        sor.Ayaat.Add("وَ يُكَلِّمُ النّٰاسَ فِي الْمَهْدِ وَ كَهْلاً وَ مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("قٰالَتْ رَبِّ أَنّٰى يَكُونُ لِي وَلَدٌ وَ لَمْ يَمْسَسْنِي بَشَرٌ قٰالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مٰا يَشٰاءُ إِذٰا قَضىٰ أَمْراً فَإِنَّمٰا يَقُولُ لَهُ كُنْ فَيَكُونُ");
        sor.Ayaat.Add("وَ يُعَلِّمُهُ الْكِتٰابَ وَ الْحِكْمَةَ وَ التَّوْرٰاةَ وَ الْإِنْجِيلَ");
        sor.Ayaat.Add("وَ رَسُولاً إِلىٰ بَنِي إِسْرٰائِيلَ أَنِّي قَدْ جِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ أَنِّي أَخْلُقُ لَكُمْ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ فَأَنْفُخُ فِيهِ فَيَكُونُ طَيْراً بِإِذْنِ اللّٰهِ وَ أُبْرِئُ الْأَكْمَهَ وَ الْأَبْرَصَ وَ أُحْيِ الْمَوْتىٰ بِإِذْنِ اللّٰهِ وَ أُنَبِّئُكُمْ بِمٰا تَأْكُلُونَ وَ مٰا تَدَّخِرُونَ فِي بُيُوتِكُمْ إِنَّ فِي ذٰلِكَ لَآيَةً لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ مُصَدِّقاً لِمٰا بَيْنَ يَدَيَّ مِنَ التَّوْرٰاةِ وَ لِأُحِلَّ لَكُمْ بَعْضَ الَّذِي حُرِّمَ عَلَيْكُمْ وَ جِئْتُكُمْ بِآيَةٍ مِنْ رَبِّكُمْ فَاتَّقُوا اللّٰهَ وَ أَطِيعُونِ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ رَبِّي وَ رَبُّكُمْ فَاعْبُدُوهُ هٰذٰا صِرٰاطٌ مُسْتَقِيمٌ");
        sor.Ayaat.Add("فَلَمّٰا أَحَسَّ عِيسىٰ مِنْهُمُ الْكُفْرَ قٰالَ مَنْ أَنْصٰارِي إِلَى اللّٰهِ قٰالَ الْحَوٰارِيُّونَ نَحْنُ أَنْصٰارُ اللّٰهِ آمَنّٰا بِاللّٰهِ وَ اشْهَدْ بِأَنّٰا مُسْلِمُونَ");
        sor.Ayaat.Add("رَبَّنٰا آمَنّٰا بِمٰا أَنْزَلْتَ وَ اتَّبَعْنَا الرَّسُولَ فَاكْتُبْنٰا مَعَ الشّٰاهِدِينَ");
        sor.Ayaat.Add("وَ مَكَرُوا وَ مَكَرَ اللّٰهُ وَ اللّٰهُ خَيْرُ الْمٰاكِرِينَ");
        sor.Ayaat.Add("إِذْ قٰالَ اللّٰهُ يٰا عِيسىٰ إِنِّي مُتَوَفِّيكَ وَ رٰافِعُكَ إِلَيَّ وَ مُطَهِّرُكَ مِنَ الَّذِينَ كَفَرُوا وَ جٰاعِلُ الَّذِينَ اتَّبَعُوكَ فَوْقَ الَّذِينَ كَفَرُوا إِلىٰ يَوْمِ الْقِيٰامَةِ ثُمَّ إِلَيَّ مَرْجِعُكُمْ فَأَحْكُمُ بَيْنَكُمْ فِيمٰا كُنْتُمْ فِيهِ تَخْتَلِفُونَ");
        sor.Ayaat.Add("فَأَمَّا الَّذِينَ كَفَرُوا فَأُعَذِّبُهُمْ عَذٰاباً شَدِيداً فِي الدُّنْيٰا وَ الْآخِرَةِ وَ مٰا لَهُمْ مِنْ نٰاصِرِينَ");
        sor.Ayaat.Add("وَ أَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَ اللّٰهُ لاٰ يُحِبُّ الظّٰالِمِينَ");
        sor.Ayaat.Add("ذٰلِكَ نَتْلُوهُ عَلَيْكَ مِنَ الْآيٰاتِ وَ الذِّكْرِ الْحَكِيمِ");
        sor.Ayaat.Add("إِنَّ مَثَلَ عِيسىٰ عِنْدَ اللّٰهِ كَمَثَلِ آدَمَ خَلَقَهُ مِنْ تُرٰابٍ ثُمَّ قٰالَ لَهُ كُنْ فَيَكُونُ");
        sor.Ayaat.Add("الْحَقُّ مِنْ رَبِّكَ فَلاٰ تَكُنْ مِنَ الْمُمْتَرِينَ");
        sor.Ayaat.Add("فَمَنْ حَاجَّكَ فِيهِ مِنْ بَعْدِ مٰا جٰاءَكَ مِنَ الْعِلْمِ فَقُلْ تَعٰالَوْا نَدْعُ أَبْنٰاءَنٰا وَ أَبْنٰاءَكُمْ وَ نِسٰاءَنٰا وَ نِسٰاءَكُمْ وَ أَنْفُسَنٰا وَ أَنْفُسَكُمْ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَعْنَتَ اللّٰهِ عَلَى الْكٰاذِبِينَ");
        sor.Ayaat.Add("إِنَّ هٰذٰا لَهُوَ الْقَصَصُ الْحَقُّ وَ مٰا مِنْ إِلٰهٍ إِلاَّ اللّٰهُ وَ إِنَّ اللّٰهَ لَهُوَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("فَإِنْ تَوَلَّوْا فَإِنَّ اللّٰهَ عَلِيمٌ بِالْمُفْسِدِينَ");
        sor.Ayaat.Add("قُلْ يٰا أَهْلَ الْكِتٰابِ تَعٰالَوْا إِلىٰ كَلِمَةٍ سَوٰاءٍ بَيْنَنٰا وَ بَيْنَكُمْ أَلاّٰ نَعْبُدَ إِلاَّ اللّٰهَ وَ لاٰ نُشْرِكَ بِهِ شَيْئاً وَ لاٰ يَتَّخِذَ بَعْضُنٰا بَعْضاً أَرْبٰاباً مِنْ دُونِ اللّٰهِ فَإِنْ تَوَلَّوْا فَقُولُوا اشْهَدُوا بِأَنّٰا مُسْلِمُونَ");
        sor.Ayaat.Add("يٰا أَهْلَ الْكِتٰابِ لِمَ تُحَاجُّونَ فِي إِبْرٰاهِيمَ وَ مٰا أُنْزِلَتِ التَّوْرٰاةُ وَ الْإِنْجِيلُ إِلاّٰ مِنْ بَعْدِهِ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("هٰا أَنْتُمْ هٰؤُلاٰءِ حٰاجَجْتُمْ فِيمٰا لَكُمْ بِهِ عِلْمٌ فَلِمَ تُحَاجُّونَ فِيمٰا لَيْسَ لَكُمْ بِهِ عِلْمٌ وَ اللّٰهُ يَعْلَمُ وَ أَنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("مٰا كٰانَ إِبْرٰاهِيمُ يَهُودِيًّا وَ لاٰ نَصْرٰانِيًّا وَ لٰكِنْ كٰانَ حَنِيفاً مُسْلِماً وَ مٰا كٰانَ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("إِنَّ أَوْلَى النّٰاسِ بِإِبْرٰاهِيمَ لَلَّذِينَ اتَّبَعُوهُ وَ هٰذَا النَّبِيُّ وَ الَّذِينَ آمَنُوا وَ اللّٰهُ وَلِيُّ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَدَّتْ طٰائِفَةٌ مِنْ أَهْلِ الْكِتٰابِ لَوْ يُضِلُّونَكُمْ وَ مٰا يُضِلُّونَ إِلاّٰ أَنْفُسَهُمْ وَ مٰا يَشْعُرُونَ");
        sor.Ayaat.Add("يٰا أَهْلَ الْكِتٰابِ لِمَ تَكْفُرُونَ بِآيٰاتِ اللّٰهِ وَ أَنْتُمْ تَشْهَدُونَ");
        sor.Ayaat.Add("يٰا أَهْلَ الْكِتٰابِ لِمَ تَلْبِسُونَ الْحَقَّ بِالْبٰاطِلِ وَ تَكْتُمُونَ الْحَقَّ وَ أَنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ قٰالَتْ طٰائِفَةٌ مِنْ أَهْلِ الْكِتٰابِ آمِنُوا بِالَّذِي أُنْزِلَ عَلَى الَّذِينَ آمَنُوا وَجْهَ النَّهٰارِ وَ اكْفُرُوا آخِرَهُ لَعَلَّهُمْ يَرْجِعُونَ");
        sor.Ayaat.Add("وَ لاٰ تُؤْمِنُوا إِلاّٰ لِمَنْ تَبِعَ دِينَكُمْ قُلْ إِنَّ الْهُدىٰ هُدَى اللّٰهِ أَنْ يُؤْتىٰ أَحَدٌ مِثْلَ مٰا أُوتِيتُمْ أَوْ يُحٰاجُّوكُمْ عِنْدَ رَبِّكُمْ قُلْ إِنَّ الْفَضْلَ بِيَدِ اللّٰهِ يُؤْتِيهِ مَنْ يَشٰاءُ وَ اللّٰهُ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("يَخْتَصُّ بِرَحْمَتِهِ مَنْ يَشٰاءُ وَ اللّٰهُ ذُو الْفَضْلِ الْعَظِيمِ");
        sor.Ayaat.Add("وَ مِنْ أَهْلِ الْكِتٰابِ مَنْ إِنْ تَأْمَنْهُ بِقِنْطٰارٍ يُؤَدِّهِ إِلَيْكَ وَ مِنْهُمْ مَنْ إِنْ تَأْمَنْهُ بِدِينٰارٍ لاٰ يُؤَدِّهِ إِلَيْكَ إِلاّٰ مٰا دُمْتَ عَلَيْهِ قٰائِماً ذٰلِكَ بِأَنَّهُمْ قٰالُوا لَيْسَ عَلَيْنٰا فِي الْأُمِّيِّينَ سَبِيلٌ وَ يَقُولُونَ عَلَى اللّٰهِ الْكَذِبَ وَ هُمْ يَعْلَمُونَ");
        sor.Ayaat.Add("بَلىٰ مَنْ أَوْفىٰ بِعَهْدِهِ وَ اتَّقىٰ فَإِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِينَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَشْتَرُونَ بِعَهْدِ اللّٰهِ وَ أَيْمٰانِهِمْ ثَمَناً قَلِيلاً أُولٰئِكَ لاٰ خَلاٰقَ لَهُمْ فِي الْآخِرَةِ وَ لاٰ يُكَلِّمُهُمُ اللّٰهُ وَ لاٰ يَنْظُرُ إِلَيْهِمْ يَوْمَ الْقِيٰامَةِ وَ لاٰ يُزَكِّيهِمْ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ إِنَّ مِنْهُمْ لَفَرِيقاً يَلْوُونَ أَلْسِنَتَهُمْ بِالْكِتٰابِ لِتَحْسَبُوهُ مِنَ الْكِتٰابِ وَ مٰا هُوَ مِنَ الْكِتٰابِ وَ يَقُولُونَ هُوَ مِنْ عِنْدِ اللّٰهِ وَ مٰا هُوَ مِنْ عِنْدِ اللّٰهِ وَ يَقُولُونَ عَلَى اللّٰهِ الْكَذِبَ وَ هُمْ يَعْلَمُونَ");
        sor.Ayaat.Add("مٰا كٰانَ لِبَشَرٍ أَنْ يُؤْتِيَهُ اللّٰهُ الْكِتٰابَ وَ الْحُكْمَ وَ النُّبُوَّةَ ثُمَّ يَقُولَ لِلنّٰاسِ كُونُوا عِبٰاداً لِي مِنْ دُونِ اللّٰهِ وَ لٰكِنْ كُونُوا رَبّٰانِيِّينَ بِمٰا كُنْتُمْ تُعَلِّمُونَ الْكِتٰابَ وَ بِمٰا كُنْتُمْ تَدْرُسُونَ");
        sor.Ayaat.Add("وَ لاٰ يَأْمُرَكُمْ أَنْ تَتَّخِذُوا الْمَلاٰئِكَةَ وَ النَّبِيِّينَ أَرْبٰاباً أَ يَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ إِذْ أَنْتُمْ مُسْلِمُونَ");
        sor.Ayaat.Add("وَ إِذْ أَخَذَ اللّٰهُ مِيثٰاقَ النَّبِيِّينَ لَمٰا آتَيْتُكُمْ مِنْ كِتٰابٍ وَ حِكْمَةٍ ثُمَّ جٰاءَكُمْ رَسُولٌ مُصَدِّقٌ لِمٰا مَعَكُمْ لَتُؤْمِنُنَّ بِهِ وَ لَتَنْصُرُنَّهُ قٰالَ أَ أَقْرَرْتُمْ وَ أَخَذْتُمْ عَلىٰ ذٰلِكُمْ إِصْرِي قٰالُوا أَقْرَرْنٰا قٰالَ فَاشْهَدُوا وَ أَنَا مَعَكُمْ مِنَ الشّٰاهِدِينَ");
        sor.Ayaat.Add("فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَأُولٰئِكَ هُمُ الْفٰاسِقُونَ");
        sor.Ayaat.Add("أَ فَغَيْرَ دِينِ اللّٰهِ يَبْغُونَ وَ لَهُ أَسْلَمَ مَنْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ طَوْعاً وَ كَرْهاً وَ إِلَيْهِ يُرْجَعُونَ");
        sor.Ayaat.Add("قُلْ آمَنّٰا بِاللّٰهِ وَ مٰا أُنْزِلَ عَلَيْنٰا وَ مٰا أُنْزِلَ عَلىٰ إِبْرٰاهِيمَ وَ إِسْمٰاعِيلَ وَ إِسْحٰاقَ وَ يَعْقُوبَ وَ الْأَسْبٰاطِ وَ مٰا أُوتِيَ مُوسىٰ وَ عِيسىٰ وَ النَّبِيُّونَ مِنْ رَبِّهِمْ لاٰ نُفَرِّقُ بَيْنَ أَحَدٍ مِنْهُمْ وَ نَحْنُ لَهُ مُسْلِمُونَ");
        sor.Ayaat.Add("وَ مَنْ يَبْتَغِ غَيْرَ الْإِسْلاٰمِ دِيناً فَلَنْ يُقْبَلَ مِنْهُ وَ هُوَ فِي الْآخِرَةِ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("كَيْفَ يَهْدِي اللّٰهُ قَوْماً كَفَرُوا بَعْدَ إِيمٰانِهِمْ وَ شَهِدُوا أَنَّ الرَّسُولَ حَقٌّ وَ جٰاءَهُمُ الْبَيِّنٰاتُ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الظّٰالِمِينَ");
        sor.Ayaat.Add("أُولٰئِكَ جَزٰاؤُهُمْ أَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَ الْمَلاٰئِكَةِ وَ النّٰاسِ أَجْمَعِينَ");
        sor.Ayaat.Add("خٰالِدِينَ فِيهٰا لاٰ يُخَفَّفُ عَنْهُمُ الْعَذٰابُ وَ لاٰ هُمْ يُنْظَرُونَ");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ تٰابُوا مِنْ بَعْدِ ذٰلِكَ وَ أَصْلَحُوا فَإِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا بَعْدَ إِيمٰانِهِمْ ثُمَّ ازْدٰادُوا كُفْراً لَنْ تُقْبَلَ تَوْبَتُهُمْ وَ أُولٰئِكَ هُمُ الضّٰالُّونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا وَ مٰاتُوا وَ هُمْ كُفّٰارٌ فَلَنْ يُقْبَلَ مِنْ أَحَدِهِمْ مِلْءُ الْأَرْضِ ذَهَباً وَ لَوِ افْتَدىٰ بِهِ أُولٰئِكَ لَهُمْ عَذٰابٌ أَلِيمٌ وَ مٰا لَهُمْ مِنْ نٰاصِرِينَ");
        sor.Ayaat.Add("لَنْ تَنٰالُوا الْبِرَّ حَتّٰى تُنْفِقُوا مِمّٰا تُحِبُّونَ وَ مٰا تُنْفِقُوا مِنْ شَيْءٍ فَإِنَّ اللّٰهَ بِهِ عَلِيمٌ");
        sor.Ayaat.Add("كُلُّ الطَّعٰامِ كٰانَ حِلاًّ لِبَنِي إِسْرٰائِيلَ إِلاّٰ مٰا حَرَّمَ إِسْرٰائِيلُ عَلىٰ نَفْسِهِ مِنْ قَبْلِ أَنْ تُنَزَّلَ التَّوْرٰاةُ قُلْ فَأْتُوا بِالتَّوْرٰاةِ فَاتْلُوهٰا إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("فَمَنِ افْتَرىٰ عَلَى اللّٰهِ الْكَذِبَ مِنْ بَعْدِ ذٰلِكَ فَأُولٰئِكَ هُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("قُلْ صَدَقَ اللّٰهُ فَاتَّبِعُوا مِلَّةَ إِبْرٰاهِيمَ حَنِيفاً وَ مٰا كٰانَ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("إِنَّ أَوَّلَ بَيْتٍ وُضِعَ لِلنّٰاسِ لَلَّذِي بِبَكَّةَ مُبٰارَكاً وَ هُدىً لِلْعٰالَمِينَ");
        sor.Ayaat.Add("فِيهِ آيٰاتٌ بَيِّنٰاتٌ مَقٰامُ إِبْرٰاهِيمَ وَ مَنْ دَخَلَهُ كٰانَ آمِناً وَ لِلّٰهِ عَلَى النّٰاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطٰاعَ إِلَيْهِ سَبِيلاً وَ مَنْ كَفَرَ فَإِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعٰالَمِينَ");
        sor.Ayaat.Add("قُلْ يٰا أَهْلَ الْكِتٰابِ لِمَ تَكْفُرُونَ بِآيٰاتِ اللّٰهِ وَ اللّٰهُ شَهِيدٌ عَلىٰ مٰا تَعْمَلُونَ");
        sor.Ayaat.Add("قُلْ يٰا أَهْلَ الْكِتٰابِ لِمَ تَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ مَنْ آمَنَ تَبْغُونَهٰا عِوَجاً وَ أَنْتُمْ شُهَدٰاءُ وَ مَا اللّٰهُ بِغٰافِلٍ عَمّٰا تَعْمَلُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا فَرِيقاً مِنَ الَّذِينَ أُوتُوا الْكِتٰابَ يَرُدُّوكُمْ بَعْدَ إِيمٰانِكُمْ كٰافِرِينَ");
        sor.Ayaat.Add("وَ كَيْفَ تَكْفُرُونَ وَ أَنْتُمْ تُتْلىٰ عَلَيْكُمْ آيٰاتُ اللّٰهِ وَ فِيكُمْ رَسُولُهُ وَ مَنْ يَعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقٰاتِهِ وَ لاٰ تَمُوتُنَّ إِلاّٰ وَ أَنْتُمْ مُسْلِمُونَ");
        sor.Ayaat.Add("وَ اعْتَصِمُوا بِحَبْلِ اللّٰهِ جَمِيعاً وَ لاٰ تَفَرَّقُوا وَ اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ إِذْ كُنْتُمْ أَعْدٰاءً فَأَلَّفَ بَيْنَ قُلُوبِكُمْ فَأَصْبَحْتُمْ بِنِعْمَتِهِ إِخْوٰاناً وَ كُنْتُمْ عَلىٰ شَفٰا حُفْرَةٍ مِنَ النّٰارِ فَأَنْقَذَكُمْ مِنْهٰا كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ آيٰاتِهِ لَعَلَّكُمْ تَهْتَدُونَ");
        sor.Ayaat.Add("وَ لْتَكُنْ مِنْكُمْ أُمَّةٌ يَدْعُونَ إِلَى الْخَيْرِ وَ يَأْمُرُونَ بِالْمَعْرُوفِ وَ يَنْهَوْنَ عَنِ الْمُنْكَرِ وَ أُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("وَ لاٰ تَكُونُوا كَالَّذِينَ تَفَرَّقُوا وَ اخْتَلَفُوا مِنْ بَعْدِ مٰا جٰاءَهُمُ الْبَيِّنٰاتُ وَ أُولٰئِكَ لَهُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("يَوْمَ تَبْيَضُّ وُجُوهٌ وَ تَسْوَدُّ وُجُوهٌ فَأَمَّا الَّذِينَ اسْوَدَّتْ وُجُوهُهُمْ أَ كَفَرْتُمْ بَعْدَ إِيمٰانِكُمْ فَذُوقُوا الْعَذٰابَ بِمٰا كُنْتُمْ تَكْفُرُونَ");
        sor.Ayaat.Add("وَ أَمَّا الَّذِينَ ابْيَضَّتْ وُجُوهُهُمْ فَفِي رَحْمَتِ اللّٰهِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("تِلْكَ آيٰاتُ اللّٰهِ نَتْلُوهٰا عَلَيْكَ بِالْحَقِّ وَ مَا اللّٰهُ يُرِيدُ ظُلْماً لِلْعٰالَمِينَ");
        sor.Ayaat.Add("وَ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ إِلَى اللّٰهِ تُرْجَعُ الْأُمُورُ");
        sor.Ayaat.Add("كُنْتُمْ خَيْرَ أُمَّةٍ أُخْرِجَتْ لِلنّٰاسِ تَأْمُرُونَ بِالْمَعْرُوفِ وَ تَنْهَوْنَ عَنِ الْمُنْكَرِ وَ تُؤْمِنُونَ بِاللّٰهِ وَ لَوْ آمَنَ أَهْلُ الْكِتٰابِ لَكٰانَ خَيْراً لَهُمْ مِنْهُمُ الْمُؤْمِنُونَ وَ أَكْثَرُهُمُ الْفٰاسِقُونَ");
        sor.Ayaat.Add("لَنْ يَضُرُّوكُمْ إِلاّٰ أَذىً وَ إِنْ يُقٰاتِلُوكُمْ يُوَلُّوكُمُ الْأَدْبٰارَ ثُمَّ لاٰ يُنْصَرُونَ");
        sor.Ayaat.Add("ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ أَيْنَ مٰا ثُقِفُوا إِلاّٰ بِحَبْلٍ مِنَ اللّٰهِ وَ حَبْلٍ مِنَ النّٰاسِ وَ بٰاؤُ بِغَضَبٍ مِنَ اللّٰهِ وَ ضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ذٰلِكَ بِأَنَّهُمْ كٰانُوا يَكْفُرُونَ بِآيٰاتِ اللّٰهِ وَ يَقْتُلُونَ الْأَنْبِيٰاءَ بِغَيْرِ حَقٍّ ذٰلِكَ بِمٰا عَصَوْا وَ كٰانُوا يَعْتَدُونَ");
        sor.Ayaat.Add("لَيْسُوا سَوٰاءً مِنْ أَهْلِ الْكِتٰابِ أُمَّةٌ قٰائِمَةٌ يَتْلُونَ آيٰاتِ اللّٰهِ آنٰاءَ اللَّيْلِ وَ هُمْ يَسْجُدُونَ");
        sor.Ayaat.Add("يُؤْمِنُونَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ يَأْمُرُونَ بِالْمَعْرُوفِ وَ يَنْهَوْنَ عَنِ الْمُنْكَرِ وَ يُسٰارِعُونَ فِي الْخَيْرٰاتِ وَ أُولٰئِكَ مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("وَ مٰا يَفْعَلُوا مِنْ خَيْرٍ فَلَنْ يُكْفَرُوهُ وَ اللّٰهُ عَلِيمٌ بِالْمُتَّقِينَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا لَنْ تُغْنِيَ عَنْهُمْ أَمْوٰالُهُمْ وَ لاٰ أَوْلاٰدُهُمْ مِنَ اللّٰهِ شَيْئاً وَ أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("مَثَلُ مٰا يُنْفِقُونَ فِي هٰذِهِ الْحَيٰاةِ الدُّنْيٰا كَمَثَلِ رِيحٍ فِيهٰا صِرٌّ أَصٰابَتْ حَرْثَ قَوْمٍ ظَلَمُوا أَنْفُسَهُمْ فَأَهْلَكَتْهُ وَ مٰا ظَلَمَهُمُ اللّٰهُ وَ لٰكِنْ أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَتَّخِذُوا بِطٰانَةً مِنْ دُونِكُمْ لاٰ يَأْلُونَكُمْ خَبٰالاً وَدُّوا مٰا عَنِتُّمْ قَدْ بَدَتِ الْبَغْضٰاءُ مِنْ أَفْوٰاهِهِمْ وَ مٰا تُخْفِي صُدُورُهُمْ أَكْبَرُ قَدْ بَيَّنّٰا لَكُمُ الْآيٰاتِ إِنْ كُنْتُمْ تَعْقِلُونَ");
        sor.Ayaat.Add("هٰا أَنْتُمْ أُولاٰءِ تُحِبُّونَهُمْ وَ لاٰ يُحِبُّونَكُمْ وَ تُؤْمِنُونَ بِالْكِتٰابِ كُلِّهِ وَ إِذٰا لَقُوكُمْ قٰالُوا آمَنّٰا وَ إِذٰا خَلَوْا عَضُّوا عَلَيْكُمُ الْأَنٰامِلَ مِنَ الْغَيْظِ قُلْ مُوتُوا بِغَيْظِكُمْ إِنَّ اللّٰهَ عَلِيمٌ بِذٰاتِ الصُّدُورِ");
        sor.Ayaat.Add("إِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْ وَ إِنْ تُصِبْكُمْ سَيِّئَةٌ يَفْرَحُوا بِهٰا وَ إِنْ تَصْبِرُوا وَ تَتَّقُوا لاٰ يَضُرُّكُمْ كَيْدُهُمْ شَيْئاً إِنَّ اللّٰهَ بِمٰا يَعْمَلُونَ مُحِيطٌ");
        sor.Ayaat.Add("وَ إِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّئُ الْمُؤْمِنِينَ مَقٰاعِدَ لِلْقِتٰالِ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("إِذْ هَمَّتْ طٰائِفَتٰانِ مِنْكُمْ أَنْ تَفْشَلاٰ وَ اللّٰهُ وَلِيُّهُمٰا وَ عَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ");
        sor.Ayaat.Add("وَ لَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَ أَنْتُمْ أَذِلَّةٌ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("إِذْ تَقُولُ لِلْمُؤْمِنِينَ أَ لَنْ يَكْفِيَكُمْ أَنْ يُمِدَّكُمْ رَبُّكُمْ بِثَلاٰثَةِ آلاٰفٍ مِنَ الْمَلاٰئِكَةِ مُنْزَلِينَ");
        sor.Ayaat.Add("بَلىٰ إِنْ تَصْبِرُوا وَ تَتَّقُوا وَ يَأْتُوكُمْ مِنْ فَوْرِهِمْ هٰذٰا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ آلاٰفٍ مِنَ الْمَلاٰئِكَةِ مُسَوِّمِينَ");
        sor.Ayaat.Add("وَ مٰا جَعَلَهُ اللّٰهُ إِلاّٰ بُشْرىٰ لَكُمْ وَ لِتَطْمَئِنَّ قُلُوبُكُمْ بِهِ وَ مَا النَّصْرُ إِلاّٰ مِنْ عِنْدِ اللّٰهِ الْعَزِيزِ الْحَكِيمِ");
        sor.Ayaat.Add("لِيَقْطَعَ طَرَفاً مِنَ الَّذِينَ كَفَرُوا أَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوا خٰائِبِينَ");
        sor.Ayaat.Add("لَيْسَ لَكَ مِنَ الْأَمْرِ شَيْءٌ أَوْ يَتُوبَ عَلَيْهِمْ أَوْ يُعَذِّبَهُمْ فَإِنَّهُمْ ظٰالِمُونَ");
        sor.Ayaat.Add("وَ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ يَغْفِرُ لِمَنْ يَشٰاءُ وَ يُعَذِّبُ مَنْ يَشٰاءُ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَأْكُلُوا الرِّبَوا أَضْعٰافاً مُضٰاعَفَةً وَ اتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("وَ اتَّقُوا النّٰارَ الَّتِي أُعِدَّتْ لِلْكٰافِرِينَ");
        sor.Ayaat.Add("وَ أَطِيعُوا اللّٰهَ وَ الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ");
        sor.Ayaat.Add("وَ سٰارِعُوا إِلىٰ مَغْفِرَةٍ مِنْ رَبِّكُمْ وَ جَنَّةٍ عَرْضُهَا السَّمٰاوٰاتُ وَ الْأَرْضُ أُعِدَّتْ لِلْمُتَّقِينَ");
        sor.Ayaat.Add("الَّذِينَ يُنْفِقُونَ فِي السَّرّٰاءِ وَ الضَّرّٰاءِ وَ الْكٰاظِمِينَ الْغَيْظَ وَ الْعٰافِينَ عَنِ النّٰاسِ وَ اللّٰهُ يُحِبُّ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ إِذٰا فَعَلُوا فٰاحِشَةً أَوْ ظَلَمُوا أَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوا لِذُنُوبِهِمْ وَ مَنْ يَغْفِرُ الذُّنُوبَ إِلاَّ اللّٰهُ وَ لَمْ يُصِرُّوا عَلىٰ مٰا فَعَلُوا وَ هُمْ يَعْلَمُونَ");
        sor.Ayaat.Add("أُولٰئِكَ جَزٰاؤُهُمْ مَغْفِرَةٌ مِنْ رَبِّهِمْ وَ جَنّٰاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا وَ نِعْمَ أَجْرُ الْعٰامِلِينَ");
        sor.Ayaat.Add("قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌ فَسِيرُوا فِي الْأَرْضِ فَانْظُروا كَيْفَ كٰانَ عٰاقِبَةُ الْمُكَذِّبِينَ");
        sor.Ayaat.Add("هٰذٰا بَيٰانٌ لِلنّٰاسِ وَ هُدىً وَ مَوْعِظَةٌ لِلْمُتَّقِينَ");
        sor.Ayaat.Add("وَ لاٰ تَهِنُوا وَ لاٰ تَحْزَنُوا وَ أَنْتُمُ الْأَعْلَوْنَ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("إِنْ يَمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِثْلُهُ وَ تِلْكَ الْأَيّٰامُ نُدٰاوِلُهٰا بَيْنَ النّٰاسِ وَ لِيَعْلَمَ اللّٰهُ الَّذِينَ آمَنُوا وَ يَتَّخِذَ مِنْكُمْ شُهَدٰاءَ وَ اللّٰهُ لاٰ يُحِبُّ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ لِيُمَحِّصَ اللّٰهُ الَّذِينَ آمَنُوا وَ يَمْحَقَ الْكٰافِرِينَ");
        sor.Ayaat.Add("أَمْ حَسِبْتُمْ أَنْ تَدْخُلُوا الْجَنَّةَ وَ لَمّٰا يَعْلَمِ اللّٰهُ الَّذِينَ جٰاهَدُوا مِنْكُمْ وَ يَعْلَمَ الصّٰابِرِينَ");
        sor.Ayaat.Add("وَ لَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ أَنْ تَلْقَوْهُ فَقَدْ رَأَيْتُمُوهُ وَ أَنْتُمْ تَنْظُرُونَ");
        sor.Ayaat.Add("وَ مٰا مُحَمَّدٌ إِلاّٰ رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ أَ فَإِنْ مٰاتَ أَوْ قُتِلَ انْقَلَبْتُمْ عَلىٰ أَعْقٰابِكُمْ وَ مَنْ يَنْقَلِبْ عَلىٰ عَقِبَيْهِ فَلَنْ يَضُرَّ اللّٰهَ شَيْئاً وَ سَيَجْزِي اللّٰهُ الشّٰاكِرِينَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ لِنَفْسٍ أَنْ تَمُوتَ إِلاّٰ بِإِذْنِ اللّٰهِ كِتٰاباً مُؤَجَّلاً وَ مَنْ يُرِدْ ثَوٰابَ الدُّنْيٰا نُؤْتِهِ مِنْهٰا وَ مَنْ يُرِدْ ثَوٰابَ الْآخِرَةِ نُؤْتِهِ مِنْهٰا وَ سَنَجْزِي الشّٰاكِرِينَ");
        sor.Ayaat.Add("وَ كَأَيِّنْ مِنْ نَبِيٍّ قٰاتَلَ مَعَهُ رِبِّيُّونَ كَثِيرٌ فَمٰا وَهَنُوا لِمٰا أَصٰابَهُمْ فِي سَبِيلِ اللّٰهِ وَ مٰا ضَعُفُوا وَ مَا اسْتَكٰانُوا وَ اللّٰهُ يُحِبُّ الصّٰابِرِينَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ قَوْلَهُمْ إِلاّٰ أَنْ قٰالُوا رَبَّنَا اغْفِرْ لَنٰا ذُنُوبَنٰا وَ إِسْرٰافَنٰا فِي أَمْرِنٰا وَ ثَبِّتْ أَقْدٰامَنٰا وَ انْصُرْنٰا عَلَى الْقَوْمِ الْكٰافِرِينَ");
        sor.Ayaat.Add("فَآتٰاهُمُ اللّٰهُ ثَوٰابَ الدُّنْيٰا وَ حُسْنَ ثَوٰابِ الْآخِرَةِ وَ اللّٰهُ يُحِبُّ الْمُحْسِنِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تُطِيعُوا الَّذِينَ كَفَرُوا يَرُدُّوكُمْ عَلىٰ أَعْقٰابِكُمْ فَتَنْقَلِبُوا خٰاسِرِينَ");
        sor.Ayaat.Add("بَلِ اللّٰهُ مَوْلاٰكُمْ وَ هُوَ خَيْرُ النّٰاصِرِينَ");
        sor.Ayaat.Add("سَنُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ بِمٰا أَشْرَكُوا بِاللّٰهِ مٰا لَمْ يُنَزِّلْ بِهِ سُلْطٰاناً وَ مَأْوٰاهُمُ النّٰارُ وَ بِئْسَ مَثْوَى الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ لَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهُ إِذْ تَحُسُّونَهُمْ بِإِذْنِهِ حَتّٰى إِذٰا فَشِلْتُمْ وَ تَنٰازَعْتُمْ فِي الْأَمْرِ وَ عَصَيْتُمْ مِنْ بَعْدِ مٰا أَرٰاكُمْ مٰا تُحِبُّونَ مِنْكُمْ مَنْ يُرِيدُ الدُّنْيٰا وَ مِنْكُمْ مَنْ يُرِيدُ الْآخِرَةَ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ وَ لَقَدْ عَفٰا عَنْكُمْ وَ اللّٰهُ ذُو فَضْلٍ عَلَى الْمُؤْمِنِينَ");
        sor.Ayaat.Add("إِذْ تُصْعِدُونَ وَ لاٰ تَلْوُونَ عَلىٰ أَحَدٍ وَ الرَّسُولُ يَدْعُوكُمْ فِي أُخْرٰاكُمْ فَأَثٰابَكُمْ غَمًّا بِغَمٍّ لِكَيْلاٰ تَحْزَنُوا عَلىٰ مٰا فٰاتَكُمْ وَ لاٰ مٰا أَصٰابَكُمْ وَ اللّٰهُ خَبِيرٌ بِمٰا تَعْمَلُونَ");
        sor.Ayaat.Add("ثُمَّ أَنْزَلَ عَلَيْكُمْ مِنْ بَعْدِ الْغَمِّ أَمَنَةً نُعٰاساً يَغْشىٰ طٰائِفَةً مِنْكُمْ وَ طٰائِفَةٌ قَدْ أَهَمَّتْهُمْ أَنْفُسُهُمْ يَظُنُّونَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجٰاهِلِيَّةِ يَقُولُونَ هَلْ لَنٰا مِنَ الْأَمْرِ مِنْ شَيْءٍ قُلْ إِنَّ الْأَمْرَ كُلَّهُ لِلّٰهِ يُخْفُونَ فِي أَنْفُسِهِمْ مٰا لاٰ يُبْدُونَ لَكَ يَقُولُونَ لَوْ كٰانَ لَنٰا مِنَ الْأَمْرِ شَيْءٌ مٰا قُتِلْنٰا هٰاهُنٰا قُلْ لَوْ كُنْتُمْ فِي بُيُوتِكُمْ لَبَرَزَ الَّذِينَ كُتِبَ عَلَيْهِمُ الْقَتْلُ إِلىٰ مَضٰاجِعِهِمْ وَ لِيَبْتَلِيَ اللّٰهُ مٰا فِي صُدُورِكُمْ وَ لِيُمَحِّصَ مٰا فِي قُلُوبِكُمْ وَ اللّٰهُ عَلِيمٌ بِذٰاتِ الصُّدُورِ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعٰانِ إِنَّمَا اسْتَزَلَّهُمُ الشَّيْطٰانُ بِبَعْضِ مٰا كَسَبُوا وَ لَقَدْ عَفَا اللّٰهُ عَنْهُمْ إِنَّ اللّٰهَ غَفُورٌ حَلِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَكُونُوا كَالَّذِينَ كَفَرُوا وَ قٰالُوا لِإِخْوٰانِهِمْ إِذٰا ضَرَبُوا فِي الْأَرْضِ أَوْ كٰانُوا غُزًّى لَوْ كٰانُوا عِنْدَنٰا مٰا مٰاتُوا وَ مٰا قُتِلُوا لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً فِي قُلُوبِهِمْ وَ اللّٰهُ يُحْيِي وَ يُمِيتُ وَ اللّٰهُ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("وَ لَئِنْ قُتِلْتُمْ فِي سَبِيلِ اللّٰهِ أَوْ مُتُّمْ لَمَغْفِرَةٌ مِنَ اللّٰهِ وَ رَحْمَةٌ خَيْرٌ مِمّٰا يَجْمَعُونَ");
        sor.Ayaat.Add("وَ لَئِنْ مُتُّمْ أَوْ قُتِلْتُمْ لَإِلَى اللّٰهِ تُحْشَرُونَ");
        sor.Ayaat.Add("فَبِمٰا رَحْمَةٍ مِنَ اللّٰهِ لِنْتَ لَهُمْ وَ لَوْ كُنْتَ فَظًّا غَلِيظَ الْقَلْبِ لاَنْفَضُّوا مِنْ حَوْلِكَ فَاعْفُ عَنْهُمْ وَ اسْتَغْفِرْ لَهُمْ وَ شٰاوِرْهُمْ فِي الْأَمْرِ فَإِذٰا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِ إِنَّ اللّٰهَ يُحِبُّ الْمُتَوَكِّلِينَ");
        sor.Ayaat.Add("إِنْ يَنْصُرْكُمُ اللّٰهُ فَلاٰ غٰالِبَ لَكُمْ وَ إِنْ يَخْذُلْكُمْ فَمَنْ ذَا الَّذِي يَنْصُرُكُمْ مِنْ بَعْدِهِ وَ عَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ لِنَبِيٍّ أَنْ يَغُلَّ وَ مَنْ يَغْلُلْ يَأْتِ بِمٰا غَلَّ يَوْمَ الْقِيٰامَةِ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مٰا كَسَبَتْ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("أَ فَمَنِ اتَّبَعَ رِضْوٰانَ اللّٰهِ كَمَنْ بٰاءَ بِسَخَطٍ مِنَ اللّٰهِ وَ مَأْوٰاهُ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ");
        sor.Ayaat.Add("هُمْ دَرَجٰاتٌ عِنْدَ اللّٰهِ وَ اللّٰهُ بَصِيرٌ بِمٰا يَعْمَلُونَ");
        sor.Ayaat.Add("لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِنِينَ إِذْ بَعَثَ فِيهِمْ رَسُولاً مِنْ أَنْفُسِهِمْ يَتْلُوا عَلَيْهِمْ آيٰاتِهِ وَ يُزَكِّيهِمْ وَ يُعَلِّمُهُمُ الْكِتٰابَ وَ الْحِكْمَةَ وَ إِنْ كٰانُوا مِنْ قَبْلُ لَفِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("أَ وَ لَمّٰا أَصٰابَتْكُمْ مُصِيبَةٌ قَدْ أَصَبْتُمْ مِثْلَيْهٰا قُلْتُمْ أَنّٰى هٰذٰا قُلْ هُوَ مِنْ عِنْدِ أَنْفُسِكُمْ إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ مٰا أَصٰابَكُمْ يَوْمَ الْتَقَى الْجَمْعٰانِ فَبِإِذْنِ اللّٰهِ وَ لِيَعْلَمَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ لِيَعْلَمَ الَّذِينَ نٰافَقُوا وَ قِيلَ لَهُمْ تَعٰالَوْا قٰاتِلُوا فِي سَبِيلِ اللّٰهِ أَوِ ادْفَعُوا قٰالُوا لَوْ نَعْلَمُ قِتٰالاً لاَتَّبَعْنٰاكُمْ هُمْ لِلْكُفْرِ يَوْمَئِذٍ أَقْرَبُ مِنْهُمْ لِلْإِيمٰانِ يَقُولُونَ بِأَفْوٰاهِهِمْ مٰا لَيْسَ فِي قُلُوبِهِمْ وَ اللّٰهُ أَعْلَمُ بِمٰا يَكْتُمُونَ");
        sor.Ayaat.Add("الَّذِينَ قٰالُوا لِإِخْوٰانِهِمْ وَ قَعَدُوا لَوْ أَطٰاعُونٰا مٰا قُتِلُوا قُلْ فَادْرَؤُا عَنْ أَنْفُسِكُمُ الْمَوْتَ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("وَ لاٰ تَحْسَبَنَّ الَّذِينَ قُتِلُوا فِي سَبِيلِ اللّٰهِ أَمْوٰاتاً بَلْ أَحْيٰاءٌ عِنْدَ رَبِّهِمْ يُرْزَقُونَ");
        sor.Ayaat.Add("فَرِحِينَ بِمٰا آتٰاهُمُ اللّٰهُ مِنْ فَضْلِهِ وَ يَسْتَبْشِرُونَ بِالَّذِينَ لَمْ يَلْحَقُوا بِهِمْ مِنْ خَلْفِهِمْ أَلاّٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("يَسْتَبْشِرُونَ بِنِعْمَةٍ مِنَ اللّٰهِ وَ فَضْلٍ وَ أَنَّ اللّٰهَ لاٰ يُضِيعُ أَجْرَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("الَّذِينَ اسْتَجٰابُوا لِلّٰهِ وَ الرَّسُولِ مِنْ بَعْدِ مٰا أَصٰابَهُمُ الْقَرْحُ لِلَّذِينَ أَحْسَنُوا مِنْهُمْ وَ اتَّقَوْا أَجْرٌ عَظِيمٌ");
        sor.Ayaat.Add("الَّذِينَ قٰالَ لَهُمُ النّٰاسُ إِنَّ النّٰاسَ قَدْ جَمَعُوا لَكُمْ فَاخْشَوْهُمْ فَزٰادَهُمْ إِيمٰاناً وَ قٰالُوا حَسْبُنَا اللّٰهُ وَ نِعْمَ الْوَكِيلُ");
        sor.Ayaat.Add("فَانْقَلَبُوا بِنِعْمَةٍ مِنَ اللّٰهِ وَ فَضْلٍ لَمْ يَمْسَسْهُمْ سُوءٌ وَ اتَّبَعُوا رِضْوٰانَ اللّٰهِ وَ اللّٰهُ ذُو فَضْلٍ عَظِيمٍ");
        sor.Ayaat.Add("إِنَّمٰا ذٰلِكُمُ الشَّيْطٰانُ يُخَوِّفُ أَوْلِيٰاءَهُ فَلاٰ تَخٰافُوهُمْ وَ خٰافُونِ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ لاٰ يَحْزُنْكَ الَّذِينَ يُسٰارِعُونَ فِي الْكُفْرِ إِنَّهُمْ لَنْ يَضُرُّوا اللّٰهَ شَيْئاً يُرِيدُ اللّٰهُ أَلاّٰ يَجْعَلَ لَهُمْ حَظًّا فِي الْآخِرَةِ وَ لَهُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ اشْتَرَوُا الْكُفْرَ بِالْإِيْمٰانِ لَنْ يَضُرُّوا اللّٰهَ شَيْئاً وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ لاٰ يَحْسَبَنَّ الَّذِينَ كَفَرُوا أَنَّمٰا نُمْلِي لَهُمْ خَيْرٌ لِأَنْفُسِهِمْ إِنَّمٰا نُمْلِي لَهُمْ لِيَزْدٰادُوا إِثْماً وَ لَهُمْ عَذٰابٌ مُهِينٌ");
        sor.Ayaat.Add("مٰا كٰانَ اللّٰهُ لِيَذَرَ الْمُؤْمِنِينَ عَلىٰ مٰا أَنْتُمْ عَلَيْهِ حَتّٰى يَمِيزَ الْخَبِيثَ مِنَ الطَّيِّبِ وَ مٰا كٰانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَ لٰكِنَّ اللّٰهَ يَجْتَبِي مِنْ رُسُلِهِ مَنْ يَشٰاءُ فَآمِنُوا بِاللّٰهِ وَ رُسُلِهِ وَ إِنْ تُؤْمِنُوا وَ تَتَّقُوا فَلَكُمْ أَجْرٌ عَظِيمٌ");
        sor.Ayaat.Add("وَ لاٰ يَحْسَبَنَّ الَّذِينَ يَبْخَلُونَ بِمٰا آتٰاهُمُ اللّٰهُ مِنْ فَضْلِهِ هُوَ خَيْراً لَهُمْ بَلْ هُوَ شَرٌّ لَهُمْ سَيُطَوَّقُونَ مٰا بَخِلُوا بِهِ يَوْمَ الْقِيٰامَةِ وَ لِلّٰهِ مِيرٰاثُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ اللّٰهُ بِمٰا تَعْمَلُونَ خَبِيرٌ");
        sor.Ayaat.Add("لَقَدْ سَمِعَ اللّٰهُ قَوْلَ الَّذِينَ قٰالُوا إِنَّ اللّٰهَ فَقِيرٌ وَ نَحْنُ أَغْنِيٰاءُ سَنَكْتُبُ مٰا قٰالُوا وَ قَتْلَهُمُ الْأَنْبِيٰاءَ بِغَيْرِ حَقٍّ وَ نَقُولُ ذُوقُوا عَذٰابَ الْحَرِيقِ");
        sor.Ayaat.Add("ذٰلِكَ بِمٰا قَدَّمَتْ أَيْدِيكُمْ وَ أَنَّ اللّٰهَ لَيْسَ بِظَلاّٰمٍ لِلْعَبِيدِ");
        sor.Ayaat.Add("الَّذِينَ قٰالُوا إِنَّ اللّٰهَ عَهِدَ إِلَيْنٰا أَلاّٰ نُؤْمِنَ لِرَسُولٍ حَتّٰى يَأْتِيَنٰا بِقُرْبٰانٍ تَأْكُلُهُ النّٰارُ قُلْ قَدْ جٰاءَكُمْ رُسُلٌ مِنْ قَبْلِي بِالْبَيِّنٰاتِ وَ بِالَّذِي قُلْتُمْ فَلِمَ قَتَلْتُمُوهُمْ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("فَإِنْ كَذَّبُوكَ فَقَدْ كُذِّبَ رُسُلٌ مِنْ قَبْلِكَ جٰاؤُ بِالْبَيِّنٰاتِ وَ الزُّبُرِ وَ الْكِتٰابِ الْمُنِيرِ");
        sor.Ayaat.Add("كُلُّ نَفْسٍ ذٰائِقَةُ الْمَوْتِ وَ إِنَّمٰا تُوَفَّوْنَ أُجُورَكُمْ يَوْمَ الْقِيٰامَةِ فَمَنْ زُحْزِحَ عَنِ النّٰارِ وَ أُدْخِلَ الْجَنَّةَ فَقَدْ فٰازَ وَ مَا الْحَيٰاةُ الدُّنْيٰا إِلاّٰ مَتٰاعُ الْغُرُورِ");
        sor.Ayaat.Add("لَتُبْلَوُنَّ فِي أَمْوٰالِكُمْ وَ أَنْفُسِكُمْ وَ لَتَسْمَعُنَّ مِنَ الَّذِينَ أُوتُوا الْكِتٰابَ مِنْ قَبْلِكُمْ وَ مِنَ الَّذِينَ أَشْرَكُوا أَذىً كَثِيراً وَ إِنْ تَصْبِرُوا وَ تَتَّقُوا فَإِنَّ ذٰلِكَ مِنْ عَزْمِ الْأُمُورِ");
        sor.Ayaat.Add("وَ إِذْ أَخَذَ اللّٰهُ مِيثٰاقَ الَّذِينَ أُوتُوا الْكِتٰابَ لَتُبَيِّنُنَّهُ لِلنّٰاسِ وَ لاٰ تَكْتُمُونَهُ فَنَبَذُوهُ وَرٰاءَ ظُهُورِهِمْ وَ اشْتَرَوْا بِهِ ثَمَناً قَلِيلاً فَبِئْسَ مٰا يَشْتَرُونَ");
        sor.Ayaat.Add("لاٰ تَحْسَبَنَّ الَّذِينَ يَفْرَحُونَ بِمٰا أَتَوْا وَ يُحِبُّونَ أَنْ يُحْمَدُوا بِمٰا لَمْ يَفْعَلُوا فَلاٰ تَحْسَبَنَّهُمْ بِمَفٰازَةٍ مِنَ الْعَذٰابِ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ لِلّٰهِ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("إِنَّ فِي خَلْقِ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ اخْتِلاٰفِ اللَّيْلِ وَ النَّهٰارِ لَآيٰاتٍ لِأُولِي الْأَلْبٰابِ");
        sor.Ayaat.Add("الَّذِينَ يَذْكُرُونَ اللّٰهَ قِيٰاماً وَ قُعُوداً وَ عَلىٰ جُنُوبِهِمْ وَ يَتَفَكَّرُونَ فِي خَلْقِ السَّمٰاوٰاتِ وَ الْأَرْضِ رَبَّنٰا مٰا خَلَقْتَ هٰذٰا بٰاطِلاً سُبْحٰانَكَ فَقِنٰا عَذٰابَ النّٰارِ");
        sor.Ayaat.Add("رَبَّنٰا إِنَّكَ مَنْ تُدْخِلِ النّٰارَ فَقَدْ أَخْزَيْتَهُ وَ مٰا لِلظّٰالِمِينَ مِنْ أَنْصٰارٍ");
        sor.Ayaat.Add("رَبَّنٰا إِنَّنٰا سَمِعْنٰا مُنٰادِياً يُنٰادِي لِلْإِيمٰانِ أَنْ آمِنُوا بِرَبِّكُمْ فَآمَنّٰا رَبَّنٰا فَاغْفِرْ لَنٰا ذُنُوبَنٰا وَ كَفِّرْ عَنّٰا سَيِّئٰاتِنٰا وَ تَوَفَّنٰا مَعَ الْأَبْرٰارِ");
        sor.Ayaat.Add("رَبَّنٰا وَ آتِنٰا مٰا وَعَدْتَنٰا عَلىٰ رُسُلِكَ وَ لاٰ تُخْزِنٰا يَوْمَ الْقِيٰامَةِ إِنَّكَ لاٰ تُخْلِفُ الْمِيعٰادَ");
        sor.Ayaat.Add("فَاسْتَجٰابَ لَهُمْ رَبُّهُمْ أَنِّي لاٰ أُضِيعُ عَمَلَ عٰامِلٍ مِنْكُمْ مِنْ ذَكَرٍ أَوْ أُنْثىٰ بَعْضُكُمْ مِنْ بَعْضٍ فَالَّذِينَ هٰاجَرُوا وَ أُخْرِجُوا مِنْ دِيٰارِهِمْ وَ أُوذُوا فِي سَبِيلِي وَ قٰاتَلُوا وَ قُتِلُوا لَأُكَفِّرَنَّ عَنْهُمْ سَيِّئٰاتِهِمْ وَ لَأُدْخِلَنَّهُمْ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ ثَوٰاباً مِنْ عِنْدِ اللّٰهِ وَ اللّٰهُ عِنْدَهُ حُسْنُ الثَّوٰابِ");
        sor.Ayaat.Add("لاٰ يَغُرَّنَّكَ تَقَلُّبُ الَّذِينَ كَفَرُوا فِي الْبِلاٰدِ");
        sor.Ayaat.Add("مَتٰاعٌ قَلِيلٌ ثُمَّ مَأْوٰاهُمْ جَهَنَّمُ وَ بِئْسَ الْمِهٰادُ");
        sor.Ayaat.Add("لٰكِنِ الَّذِينَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنّٰاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا نُزُلاً مِنْ عِنْدِ اللّٰهِ وَ مٰا عِنْدَ اللّٰهِ خَيْرٌ لِلْأَبْرٰارِ");
        sor.Ayaat.Add("وَ إِنَّ مِنْ أَهْلِ الْكِتٰابِ لَمَنْ يُؤْمِنُ بِاللّٰهِ وَ مٰا أُنْزِلَ إِلَيْكُمْ وَ مٰا أُنْزِلَ إِلَيْهِمْ خٰاشِعِينَ لِلّٰهِ لاٰ يَشْتَرُونَ بِآيٰاتِ اللّٰهِ ثَمَناً قَلِيلاً أُولٰئِكَ لَهُمْ أَجْرُهُمْ عِنْدَ رَبِّهِمْ إِنَّ اللّٰهَ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اصْبِرُوا وَ صٰابِرُوا وَ رٰابِطُوا وَ اتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُونَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "۴ - النساء";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ يٰا أَيُّهَا النّٰاسُ اتَّقُوا رَبَّكُمُ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ وٰاحِدَةٍ وَ خَلَقَ مِنْهٰا زَوْجَهٰا وَ بَثَّ مِنْهُمٰا رِجٰالاً كَثِيراً وَ نِسٰاءً وَ اتَّقُوا اللّٰهَ الَّذِي تَسٰائَلُونَ بِهِ وَ الْأَرْحٰامَ إِنَّ اللّٰهَ كٰانَ عَلَيْكُمْ رَقِيباً");
        sor.Ayaat.Add("وَ آتُوا الْيَتٰامىٰ أَمْوٰالَهُمْ وَ لاٰ تَتَبَدَّلُوا الْخَبِيثَ بِالطَّيِّبِ وَ لاٰ تَأْكُلُوا أَمْوٰالَهُمْ إِلىٰ أَمْوٰالِكُمْ إِنَّهُ كٰانَ حُوباً كَبِيراً");
        sor.Ayaat.Add("وَ إِنْ خِفْتُمْ أَلاّٰ تُقْسِطُوا فِي الْيَتٰامىٰ فَانْكِحُوا مٰا طٰابَ لَكُمْ مِنَ النِّسٰاءِ مَثْنىٰ وَ ثُلاٰثَ وَ رُبٰاعَ فَإِنْ خِفْتُمْ أَلاّٰ تَعْدِلُوا فَوٰاحِدَةً أَوْ مٰا مَلَكَتْ أَيْمٰانُكُمْ ذٰلِكَ أَدْنىٰ أَلاّٰ تَعُولُوا");
        sor.Ayaat.Add("وَ آتُوا النِّسٰاءَ صَدُقٰاتِهِنَّ نِحْلَةً فَإِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِنْهُ نَفْساً فَكُلُوهُ هَنِيئاً مَرِيئاً");
        sor.Ayaat.Add("وَ لاٰ تُؤْتُوا السُّفَهٰاءَ أَمْوٰالَكُمُ الَّتِي جَعَلَ اللّٰهُ لَكُمْ قِيٰاماً وَ ارْزُقُوهُمْ فِيهٰا وَ اكْسُوهُمْ وَ قُولُوا لَهُمْ قَوْلاً مَعْرُوفاً");
        sor.Ayaat.Add("وَ ابْتَلُوا الْيَتٰامىٰ حَتّٰى إِذٰا بَلَغُوا النِّكٰاحَ فَإِنْ آنَسْتُمْ مِنْهُمْ رُشْداً فَادْفَعُوا إِلَيْهِمْ أَمْوٰالَهُمْ وَ لاٰ تَأْكُلُوهٰا إِسْرٰافاً وَ بِدٰاراً أَنْ يَكْبَرُوا وَ مَنْ كٰانَ غَنِيًّا فَلْيَسْتَعْفِفْ وَ مَنْ كٰانَ فَقِيراً فَلْيَأْكُلْ بِالْمَعْرُوفِ فَإِذٰا دَفَعْتُمْ إِلَيْهِمْ أَمْوٰالَهُمْ فَأَشْهِدُوا عَلَيْهِمْ وَ كَفىٰ بِاللّٰهِ حَسِيباً");
        sor.Ayaat.Add("لِلرِّجٰالِ نَصِيبٌ مِمّٰا تَرَكَ الْوٰالِدٰانِ وَ الْأَقْرَبُونَ وَ لِلنِّسٰاءِ نَصِيبٌ مِمّٰا تَرَكَ الْوٰالِدٰانِ وَ الْأَقْرَبُونَ مِمّٰا قَلَّ مِنْهُ أَوْ كَثُرَ نَصِيباً مَفْرُوضاً");
        sor.Ayaat.Add("وَ إِذٰا حَضَرَ الْقِسْمَةَ أُولُوا الْقُرْبىٰ وَ الْيَتٰامىٰ وَ الْمَسٰاكِينُ فَارْزُقُوهُمْ مِنْهُ وَ قُولُوا لَهُمْ قَوْلاً مَعْرُوفاً");
        sor.Ayaat.Add("وَ لْيَخْشَ الَّذِينَ لَوْ تَرَكُوا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعٰافاً خٰافُوا عَلَيْهِمْ فَلْيَتَّقُوا اللّٰهَ وَ لْيَقُولُوا قَوْلاً سَدِيداً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَأْكُلُونَ أَمْوٰالَ الْيَتٰامىٰ ظُلْماً إِنَّمٰا يَأْكُلُونَ فِي بُطُونِهِمْ نٰاراً وَ سَيَصْلَوْنَ سَعِيراً");
        sor.Ayaat.Add("يُوصِيكُمُ اللّٰهُ فِي أَوْلاٰدِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْأُنْثَيَيْنِ فَإِنْ كُنَّ نِسٰاءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثٰا مٰا تَرَكَ وَ إِنْ كٰانَتْ وٰاحِدَةً فَلَهَا النِّصْفُ وَ لِأَبَوَيْهِ لِكُلِّ وٰاحِدٍ مِنْهُمَا السُّدُسُ مِمّٰا تَرَكَ إِنْ كٰانَ لَهُ وَلَدٌ فَإِنْ لَمْ يَكُنْ لَهُ وَلَدٌ وَ وَرِثَهُ أَبَوٰاهُ فَلِأُمِّهِ الثُّلُثُ فَإِنْ كٰانَ لَهُ إِخْوَةٌ فَلِأُمِّهِ السُّدُسُ مِنْ بَعْدِ وَصِيَّةٍ يُوصِي بِهٰا أَوْ دَيْنٍ آبٰاؤُكُمْ وَ أَبْنٰاؤُكُمْ لاٰ تَدْرُونَ أَيُّهُمْ أَقْرَبُ لَكُمْ نَفْعاً فَرِيضَةً مِنَ اللّٰهِ إِنَّ اللّٰهَ كٰانَ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("وَ لَكُمْ نِصْفُ مٰا تَرَكَ أَزْوٰاجُكُمْ إِنْ لَمْ يَكُنْ لَهُنَّ وَلَدٌ فَإِنْ كٰانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمّٰا تَرَكْنَ مِنْ بَعْدِ وَصِيَّةٍ يُوصِينَ بِهٰا أَوْ دَيْنٍ وَ لَهُنَّ الرُّبُعُ مِمّٰا تَرَكْتُمْ إِنْ لَمْ يَكُنْ لَكُمْ وَلَدٌ فَإِنْ كٰانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمّٰا تَرَكْتُمْ مِنْ بَعْدِ وَصِيَّةٍ تُوصُونَ بِهٰا أَوْ دَيْنٍ وَ إِنْ كٰانَ رَجُلٌ يُورَثُ كَلاٰلَةً أَوِ امْرَأَةٌ وَ لَهُ أَخٌ أَوْ أُخْتٌ فَلِكُلِّ وٰاحِدٍ مِنْهُمَا السُّدُسُ فَإِنْ كٰانُوا أَكْثَرَ مِنْ ذٰلِكَ فَهُمْ شُرَكٰاءُ فِي الثُّلُثِ مِنْ بَعْدِ وَصِيَّةٍ يُوصىٰ بِهٰا أَوْ دَيْنٍ غَيْرَ مُضَارٍّ وَصِيَّةً مِنَ اللّٰهِ وَ اللّٰهُ عَلِيمٌ حَلِيمٌ");
        sor.Ayaat.Add("تِلْكَ حُدُودُ اللّٰهِ وَ مَنْ يُطِعِ اللّٰهَ وَ رَسُولَهُ يُدْخِلْهُ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا وَ ذٰلِكَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("وَ مَنْ يَعْصِ اللّٰهَ وَ رَسُولَهُ وَ يَتَعَدَّ حُدُودَهُ يُدْخِلْهُ نٰاراً خٰالِداً فِيهٰا وَ لَهُ عَذٰابٌ مُهِينٌ");
        sor.Ayaat.Add("وَ اللاّٰتِي يَأْتِينَ الْفٰاحِشَةَ مِنْ نِسٰائِكُمْ فَاسْتَشْهِدُوا عَلَيْهِنَّ أَرْبَعَةً مِنْكُمْ فَإِنْ شَهِدُوا فَأَمْسِكُوهُنَّ فِي الْبُيُوتِ حَتّٰى يَتَوَفّٰاهُنَّ الْمَوْتُ أَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَبِيلاً");
        sor.Ayaat.Add("وَ الَّذٰانِ يَأْتِيٰانِهٰا مِنْكُمْ فَآذُوهُمٰا فَإِنْ تٰابٰا وَ أَصْلَحٰا فَأَعْرِضُوا عَنْهُمٰا إِنَّ اللّٰهَ كٰانَ تَوّٰاباً رَحِيماً");
        sor.Ayaat.Add("إِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذِينَ يَعْمَلُونَ السُّوءَ بِجَهٰالَةٍ ثُمَّ يَتُوبُونَ مِنْ قَرِيبٍ فَأُولٰئِكَ يَتُوبُ اللّٰهُ عَلَيْهِمْ وَ كٰانَ اللّٰهُ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("وَ لَيْسَتِ التَّوْبَةُ لِلَّذِينَ يَعْمَلُونَ السَّيِّئٰاتِ حَتّٰى إِذٰا حَضَرَ أَحَدَهُمُ الْمَوْتُ قٰالَ إِنِّي تُبْتُ الْآنَ وَ لاَ الَّذِينَ يَمُوتُونَ وَ هُمْ كُفّٰارٌ أُولٰئِكَ أَعْتَدْنٰا لَهُمْ عَذٰاباً أَلِيماً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ يَحِلُّ لَكُمْ أَنْ تَرِثُوا النِّسٰاءَ كَرْهاً وَ لاٰ تَعْضُلُوهُنَّ لِتَذْهَبُوا بِبَعْضِ مٰا آتَيْتُمُوهُنَّ إِلاّٰ أَنْ يَأْتِينَ بِفٰاحِشَةٍ مُبَيِّنَةٍ وَ عٰاشِرُوهُنَّ بِالْمَعْرُوفِ فَإِنْ كَرِهْتُمُوهُنَّ فَعَسىٰ أَنْ تَكْرَهُوا شَيْئاً وَ يَجْعَلَ اللّٰهُ فِيهِ خَيْراً كَثِيراً");
        sor.Ayaat.Add("وَ إِنْ أَرَدْتُمُ اسْتِبْدٰالَ زَوْجٍ مَكٰانَ زَوْجٍ وَ آتَيْتُمْ إِحْدٰاهُنَّ قِنْطٰاراً فَلاٰ تَأْخُذُوا مِنْهُ شَيْئاً أَ تَأْخُذُونَهُ بُهْتٰاناً وَ إِثْماً مُبِيناً");
        sor.Ayaat.Add("وَ كَيْفَ تَأْخُذُونَهُ وَ قَدْ أَفْضىٰ بَعْضُكُمْ إِلىٰ بَعْضٍ وَ أَخَذْنَ مِنْكُمْ مِيثٰاقاً غَلِيظاً");
        sor.Ayaat.Add("وَ لاٰ تَنْكِحُوا مٰا نَكَحَ آبٰاؤُكُمْ مِنَ النِّسٰاءِ إِلاّٰ مٰا قَدْ سَلَفَ إِنَّهُ كٰانَ فٰاحِشَةً وَ مَقْتاً وَ سٰاءَ سَبِيلاً");
        sor.Ayaat.Add("حُرِّمَتْ عَلَيْكُمْ أُمَّهٰاتُكُمْ وَ بَنٰاتُكُمْ وَ أَخَوٰاتُكُمْ وَ عَمّٰاتُكُمْ وَ خٰالاٰتُكُمْ وَ بَنٰاتُ الْأَخِ وَ بَنٰاتُ الْأُخْتِ وَ أُمَّهٰاتُكُمُ اللاّٰتِي أَرْضَعْنَكُمْ وَ أَخَوٰاتُكُمْ مِنَ الرَّضٰاعَةِ وَ أُمَّهٰاتُ نِسٰائِكُمْ وَ رَبٰائِبُكُمُ اللاّٰتِي فِي حُجُورِكُمْ مِنْ نِسٰائِكُمُ اللاّٰتِي دَخَلْتُمْ بِهِنَّ فَإِنْ لَمْ تَكُونُوا دَخَلْتُمْ بِهِنَّ فَلاٰ جُنٰاحَ عَلَيْكُمْ وَ حَلاٰئِلُ أَبْنٰائِكُمُ الَّذِينَ مِنْ أَصْلاٰبِكُمْ وَ أَنْ تَجْمَعُوا بَيْنَ الْأُخْتَيْنِ إِلاّٰ مٰا قَدْ سَلَفَ إِنَّ اللّٰهَ كٰانَ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("وَ الْمُحْصَنٰاتُ مِنَ النِّسٰاءِ إِلاّٰ مٰا مَلَكَتْ أَيْمٰانُكُمْ كِتٰابَ اللّٰهِ عَلَيْكُمْ وَ أُحِلَّ لَكُمْ مٰا وَرٰاءَ ذٰلِكُمْ أَنْ تَبْتَغُوا بِأَمْوٰالِكُمْ مُحْصِنِينَ غَيْرَ مُسٰافِحِينَ فَمَا اسْتَمْتَعْتُمْ بِهِ مِنْهُنَّ فَآتُوهُنَّ أُجُورَهُنَّ فَرِيضَةً وَ لاٰ جُنٰاحَ عَلَيْكُمْ فِيمٰا تَرٰاضَيْتُمْ بِهِ مِنْ بَعْدِ الْفَرِيضَةِ إِنَّ اللّٰهَ كٰانَ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("وَ مَنْ لَمْ يَسْتَطِعْ مِنْكُمْ طَوْلاً أَنْ يَنْكِحَ الْمُحْصَنٰاتِ الْمُؤْمِنٰاتِ فَمِنْ مٰا مَلَكَتْ أَيْمٰانُكُمْ مِنْ فَتَيٰاتِكُمُ الْمُؤْمِنٰاتِ وَ اللّٰهُ أَعْلَمُ بِإِيمٰانِكُمْ بَعْضُكُمْ مِنْ بَعْضٍ فَانْكِحُوهُنَّ بِإِذْنِ أَهْلِهِنَّ وَ آتُوهُنَّ أُجُورَهُنَّ بِالْمَعْرُوفِ مُحْصَنٰاتٍ غَيْرَ مُسٰافِحٰاتٍ وَ لاٰ مُتَّخِذٰاتِ أَخْدٰانٍ فَإِذٰا أُحْصِنَّ فَإِنْ أَتَيْنَ بِفٰاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مٰا عَلَى الْمُحْصَنٰاتِ مِنَ الْعَذٰابِ ذٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ وَ أَنْ تَصْبِرُوا خَيْرٌ لَكُمْ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يُرِيدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَ يَهْدِيَكُمْ سُنَنَ الَّذِينَ مِنْ قَبْلِكُمْ وَ يَتُوبَ عَلَيْكُمْ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ اللّٰهُ يُرِيدُ أَنْ يَتُوبَ عَلَيْكُمْ وَ يُرِيدُ الَّذِينَ يَتَّبِعُونَ الشَّهَوٰاتِ أَنْ تَمِيلُوا مَيْلاً عَظِيماً");
        sor.Ayaat.Add("يُرِيدُ اللّٰهُ أَنْ يُخَفِّفَ عَنْكُمْ وَ خُلِقَ الْإِنْسٰانُ ضَعِيفاً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَأْكُلُوا أَمْوٰالَكُمْ بَيْنَكُمْ بِالْبٰاطِلِ إِلاّٰ أَنْ تَكُونَ تِجٰارَةً عَنْ تَرٰاضٍ مِنْكُمْ وَ لاٰ تَقْتُلُوا أَنْفُسَكُمْ إِنَّ اللّٰهَ كٰانَ بِكُمْ رَحِيماً");
        sor.Ayaat.Add("وَ مَنْ يَفْعَلْ ذٰلِكَ عُدْوٰاناً وَ ظُلْماً فَسَوْفَ نُصْلِيهِ نٰاراً وَ كٰانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيراً");
        sor.Ayaat.Add("إِنْ تَجْتَنِبُوا كَبٰائِرَ مٰا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّئٰاتِكُمْ وَ نُدْخِلْكُمْ مُدْخَلاً كَرِيماً");
        sor.Ayaat.Add("وَ لاٰ تَتَمَنَّوْا مٰا فَضَّلَ اللّٰهُ بِهِ بَعْضَكُمْ عَلىٰ بَعْضٍ لِلرِّجٰالِ نَصِيبٌ مِمَّا اكْتَسَبُوا وَ لِلنِّسٰاءِ نَصِيبٌ مِمَّا اكْتَسَبْنَ وَ سْئَلُوا اللّٰهَ مِنْ فَضْلِهِ إِنَّ اللّٰهَ كٰانَ بِكُلِّ شَيْءٍ عَلِيماً");
        sor.Ayaat.Add("وَ لِكُلٍّ جَعَلْنٰا مَوٰالِيَ مِمّٰا تَرَكَ الْوٰالِدٰانِ وَ الْأَقْرَبُونَ وَ الَّذِينَ عَقَدَتْ أَيْمٰانُكُمْ فَآتُوهُمْ نَصِيبَهُمْ إِنَّ اللّٰهَ كٰانَ عَلىٰ كُلِّ شَيْءٍ شَهِيداً");
        sor.Ayaat.Add("الرِّجٰالُ قَوّٰامُونَ عَلَى النِّسٰاءِ بِمٰا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلىٰ بَعْضٍ وَ بِمٰا أَنْفَقُوا مِنْ أَمْوٰالِهِمْ فَالصّٰالِحٰاتُ قٰانِتٰاتٌ حٰافِظٰاتٌ لِلْغَيْبِ بِمٰا حَفِظَ اللّٰهُ وَ اللاّٰتِي تَخٰافُونَ نُشُوزَهُنَّ فَعِظُوهُنَّ وَ اهْجُرُوهُنَّ فِي الْمَضٰاجِعِ وَ اضْرِبُوهُنَّ فَإِنْ أَطَعْنَكُمْ فَلاٰ تَبْغُوا عَلَيْهِنَّ سَبِيلاً إِنَّ اللّٰهَ كٰانَ عَلِيًّا كَبِيراً");
        sor.Ayaat.Add("وَ إِنْ خِفْتُمْ شِقٰاقَ بَيْنِهِمٰا فَابْعَثُوا حَكَماً مِنْ أَهْلِهِ وَ حَكَماً مِنْ أَهْلِهٰا إِنْ يُرِيدٰا إِصْلاٰحاً يُوَفِّقِ اللّٰهُ بَيْنَهُمٰا إِنَّ اللّٰهَ كٰانَ عَلِيماً خَبِيراً");
        sor.Ayaat.Add("وَ اعْبُدُوا اللّٰهَ وَ لاٰ تُشْرِكُوا بِهِ شَيْئاً وَ بِالْوٰالِدَيْنِ إِحْسٰاناً وَ بِذِي الْقُرْبىٰ وَ الْيَتٰامىٰ وَ الْمَسٰاكِينِ وَ الْجٰارِ ذِي الْقُرْبىٰ وَ الْجٰارِ الْجُنُبِ وَ الصّٰاحِبِ بِالْجَنْبِ وَ ابْنِ السَّبِيلِ وَ مٰا مَلَكَتْ أَيْمٰانُكُمْ إِنَّ اللّٰهَ لاٰ يُحِبُّ مَنْ كٰانَ مُخْتٰالاً فَخُوراً");
        sor.Ayaat.Add("الَّذِينَ يَبْخَلُونَ وَ يَأْمُرُونَ النّٰاسَ بِالْبُخْلِ وَ يَكْتُمُونَ مٰا آتٰاهُمُ اللّٰهُ مِنْ فَضْلِهِ وَ أَعْتَدْنٰا لِلْكٰافِرِينَ عَذٰاباً مُهِيناً");
        sor.Ayaat.Add("وَ الَّذِينَ يُنْفِقُونَ أَمْوٰالَهُمْ رِئٰاءَ النّٰاسِ وَ لاٰ يُؤْمِنُونَ بِاللّٰهِ وَ لاٰ بِالْيَوْمِ الْآخِرِ وَ مَنْ يَكُنِ الشَّيْطٰانُ لَهُ قَرِيناً فَسٰاءَ قَرِيناً");
        sor.Ayaat.Add("وَ مٰاذٰا عَلَيْهِمْ لَوْ آمَنُوا بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ أَنْفَقُوا مِمّٰا رَزَقَهُمُ اللّٰهُ وَ كٰانَ اللّٰهُ بِهِمْ عَلِيماً");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لاٰ يَظْلِمُ مِثْقٰالَ ذَرَّةٍ وَ إِنْ تَكُ حَسَنَةً يُضٰاعِفْهٰا وَ يُؤْتِ مِنْ لَدُنْهُ أَجْراً عَظِيماً");
        sor.Ayaat.Add("فَكَيْفَ إِذٰا جِئْنٰا مِنْ كُلِّ أُمَّةٍ بِشَهِيدٍ وَ جِئْنٰا بِكَ عَلىٰ هٰؤُلاٰءِ شَهِيداً");
        sor.Ayaat.Add("يَوْمَئِذٍ يَوَدُّ الَّذِينَ كَفَرُوا وَ عَصَوُا الرَّسُولَ لَوْ تُسَوّٰى بِهِمُ الْأَرْضُ وَ لاٰ يَكْتُمُونَ اللّٰهَ حَدِيثاً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَقْرَبُوا الصَّلاٰةَ وَ أَنْتُمْ سُكٰارىٰ حَتّٰى تَعْلَمُوا مٰا تَقُولُونَ وَ لاٰ جُنُباً إِلاّٰ عٰابِرِي سَبِيلٍ حَتَّىٰ تَغْتَسِلُوا وَ إِنْ كُنْتُمْ مَرْضىٰ أَوْ عَلىٰ سَفَرٍ أَوْ جٰاءَ أَحَدٌ مِنْكُمْ مِنَ الْغٰائِطِ أَوْ لاٰمَسْتُمُ النِّسٰاءَ فَلَمْ تَجِدُوا مٰاءً فَتَيَمَّمُوا صَعِيداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَ أَيْدِيكُمْ إِنَّ اللّٰهَ كٰانَ عَفُوًّا غَفُوراً");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيباً مِنَ الْكِتٰابِ يَشْتَرُونَ الضَّلاٰلَةَ وَ يُرِيدُونَ أَنْ تَضِلُّوا السَّبِيلَ");
        sor.Ayaat.Add("وَ اللّٰهُ أَعْلَمُ بِأَعْدٰائِكُمْ وَ كَفىٰ بِاللّٰهِ وَلِيًّا وَ كَفىٰ بِاللّٰهِ نَصِيراً");
        sor.Ayaat.Add("مِنَ الَّذِينَ هٰادُوا يُحَرِّفُونَ الْكَلِمَ عَنْ مَوٰاضِعِهِ وَ يَقُولُونَ سَمِعْنٰا وَ عَصَيْنٰا وَ اسْمَعْ غَيْرَ مُسْمَعٍ وَ رٰاعِنٰا لَيًّا بِأَلْسِنَتِهِمْ وَ طَعْناً فِي الدِّينِ وَ لَوْ أَنَّهُمْ قٰالُوا سَمِعْنٰا وَ أَطَعْنٰا وَ اسْمَعْ وَ انْظُرْنٰا لَكٰانَ خَيْراً لَهُمْ وَ أَقْوَمَ وَ لٰكِنْ لَعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَلاٰ يُؤْمِنُونَ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ أُوتُوا الْكِتٰابَ آمِنُوا بِمٰا نَزَّلْنٰا مُصَدِّقاً لِمٰا مَعَكُمْ مِنْ قَبْلِ أَنْ نَطْمِسَ وُجُوهاً فَنَرُدَّهٰا عَلىٰ أَدْبٰارِهٰا أَوْ نَلْعَنَهُمْ كَمٰا لَعَنّٰا أَصْحٰابَ السَّبْتِ وَ كٰانَ أَمْرُ اللّٰهِ مَفْعُولاً");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لاٰ يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَ يَغْفِرُ مٰا دُونَ ذٰلِكَ لِمَنْ يَشٰاءُ وَ مَنْ يُشْرِكْ بِاللّٰهِ فَقَدِ افْتَرىٰ إِثْماً عَظِيماً");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ يُزَكُّونَ أَنْفُسَهُمْ بَلِ اللّٰهُ يُزَكِّي مَنْ يَشٰاءُ وَ لاٰ يُظْلَمُونَ فَتِيلاً");
        sor.Ayaat.Add("انْظُرْ كَيْفَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ وَ كَفىٰ بِهِ إِثْماً مُبِيناً");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ أُوتُوا نَصِيباً مِنَ الْكِتٰابِ يُؤْمِنُونَ بِالْجِبْتِ وَ الطّٰاغُوتِ وَ يَقُولُونَ لِلَّذِينَ كَفَرُوا هٰؤُلاٰءِ أَهْدىٰ مِنَ الَّذِينَ آمَنُوا سَبِيلاً");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ لَعَنَهُمُ اللّٰهُ وَ مَنْ يَلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهُ نَصِيراً");
        sor.Ayaat.Add("أَمْ لَهُمْ نَصِيبٌ مِنَ الْمُلْكِ فَإِذاً لاٰ يُؤْتُونَ النّٰاسَ نَقِيراً");
        sor.Ayaat.Add("أَمْ يَحْسُدُونَ النّٰاسَ عَلىٰ مٰا آتٰاهُمُ اللّٰهُ مِنْ فَضْلِهِ فَقَدْ آتَيْنٰا آلَ إِبْرٰاهِيمَ الْكِتٰابَ وَ الْحِكْمَةَ وَ آتَيْنٰاهُمْ مُلْكاً عَظِيماً");
        sor.Ayaat.Add("فَمِنْهُمْ مَنْ آمَنَ بِهِ وَ مِنْهُمْ مَنْ صَدَّ عَنْهُ وَ كَفىٰ بِجَهَنَّمَ سَعِيراً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا بِآيٰاتِنٰا سَوْفَ نُصْلِيهِمْ نٰاراً كُلَّمٰا نَضِجَتْ جُلُودُهُمْ بَدَّلْنٰاهُمْ جُلُوداً غَيْرَهٰا لِيَذُوقُوا الْعَذٰابَ إِنَّ اللّٰهَ كٰانَ عَزِيزاً حَكِيماً");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ سَنُدْخِلُهُمْ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا أَبَداً لَهُمْ فِيهٰا أَزْوٰاجٌ مُطَهَّرَةٌ وَ نُدْخِلُهُمْ ظِلاًّ ظَلِيلاً");
        sor.Ayaat.Add("إِنَّ اللّٰهَ يَأْمُرُكُمْ أَنْ تُؤَدُّوا الْأَمٰانٰاتِ إِلىٰ أَهْلِهٰا وَ إِذٰا حَكَمْتُمْ بَيْنَ النّٰاسِ أَنْ تَحْكُمُوا بِالْعَدْلِ إِنَّ اللّٰهَ نِعِمّٰا يَعِظُكُمْ بِهِ إِنَّ اللّٰهَ كٰانَ سَمِيعاً بَصِيراً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللّٰهَ وَ أَطِيعُوا الرَّسُولَ وَ أُولِي الْأَمْرِ مِنْكُمْ فَإِنْ تَنٰازَعْتُمْ فِي شَيْءٍ فَرُدُّوهُ إِلَى اللّٰهِ وَ الرَّسُولِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ ذٰلِكَ خَيْرٌ وَ أَحْسَنُ تَأْوِيلاً");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ يَزْعُمُونَ أَنَّهُمْ آمَنُوا بِمٰا أُنْزِلَ إِلَيْكَ وَ مٰا أُنْزِلَ مِنْ قَبْلِكَ يُرِيدُونَ أَنْ يَتَحٰاكَمُوا إِلَى الطّٰاغُوتِ وَ قَدْ أُمِرُوا أَنْ يَكْفُرُوا بِهِ وَ يُرِيدُ الشَّيْطٰانُ أَنْ يُضِلَّهُمْ ضَلاٰلاً بَعِيداً");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمْ تَعٰالَوْا إِلىٰ مٰا أَنْزَلَ اللّٰهُ وَ إِلَى الرَّسُولِ رَأَيْتَ الْمُنٰافِقِينَ يَصُدُّونَ عَنْكَ صُدُوداً");
        sor.Ayaat.Add("فَكَيْفَ إِذٰا أَصٰابَتْهُمْ مُصِيبَةٌ بِمٰا قَدَّمَتْ أَيْدِيهِمْ ثُمَّ جٰاؤُكَ يَحْلِفُونَ بِاللّٰهِ إِنْ أَرَدْنٰا إِلاّٰ إِحْسٰاناً وَ تَوْفِيقاً");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ يَعْلَمُ اللّٰهُ مٰا فِي قُلُوبِهِمْ فَأَعْرِضْ عَنْهُمْ وَ عِظْهُمْ وَ قُلْ لَهُمْ فِي أَنْفُسِهِمْ قَوْلاً بَلِيغاً");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا مِنْ رَسُولٍ إِلاّٰ لِيُطٰاعَ بِإِذْنِ اللّٰهِ وَ لَوْ أَنَّهُمْ إِذْ ظَلَمُوا أَنْفُسَهُمْ جٰاؤُكَ فَاسْتَغْفَرُوا اللّٰهَ وَ اسْتَغْفَرَ لَهُمُ الرَّسُولُ لَوَجَدُوا اللّٰهَ تَوّٰاباً رَحِيماً");
        sor.Ayaat.Add("فَلاٰ وَ رَبِّكَ لاٰ يُؤْمِنُونَ حَتّٰى يُحَكِّمُوكَ فِيمٰا شَجَرَ بَيْنَهُمْ ثُمَّ لاٰ يَجِدُوا فِي أَنْفُسِهِمْ حَرَجاً مِمّٰا قَضَيْتَ وَ يُسَلِّمُوا تَسْلِيماً");
        sor.Ayaat.Add("وَ لَوْ أَنّٰا كَتَبْنٰا عَلَيْهِمْ أَنِ اقْتُلُوا أَنْفُسَكُمْ أَوِ اخْرُجُوا مِنْ دِيٰارِكُمْ مٰا فَعَلُوهُ إِلاّٰ قَلِيلٌ مِنْهُمْ وَ لَوْ أَنَّهُمْ فَعَلُوا مٰا يُوعَظُونَ بِهِ لَكٰانَ خَيْراً لَهُمْ وَ أَشَدَّ تَثْبِيتاً");
        sor.Ayaat.Add("وَ إِذاً لَآتَيْنٰاهُمْ مِنْ لَدُنّٰا أَجْراً عَظِيماً");
        sor.Ayaat.Add("وَ لَهَدَيْنٰاهُمْ صِرٰاطاً مُسْتَقِيماً");
        sor.Ayaat.Add("وَ مَنْ يُطِعِ اللّٰهَ وَ الرَّسُولَ فَأُولٰئِكَ مَعَ الَّذِينَ أَنْعَمَ اللّٰهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ وَ الصِّدِّيقِينَ وَ الشُّهَدٰاءِ وَ الصّٰالِحِينَ وَ حَسُنَ أُولٰئِكَ رَفِيقاً");
        sor.Ayaat.Add("ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِ وَ كَفىٰ بِاللّٰهِ عَلِيماً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا خُذُوا حِذْرَكُمْ فَانْفِرُوا ثُبٰاتٍ أَوِ انْفِرُوا جَمِيعاً");
        sor.Ayaat.Add("وَ إِنَّ مِنْكُمْ لَمَنْ لَيُبَطِّئَنَّ فَإِنْ أَصٰابَتْكُمْ مُصِيبَةٌ قٰالَ قَدْ أَنْعَمَ اللّٰهُ عَلَيَّ إِذْ لَمْ أَكُنْ مَعَهُمْ شَهِيداً");
        sor.Ayaat.Add("وَ لَئِنْ أَصٰابَكُمْ فَضْلٌ مِنَ اللّٰهِ لَيَقُولَنَّ كَأَنْ لَمْ تَكُنْ بَيْنَكُمْ وَ بَيْنَهُ مَوَدَّةٌ يٰا لَيْتَنِي كُنْتُ مَعَهُمْ فَأَفُوزَ فَوْزاً عَظِيماً");
        sor.Ayaat.Add("فَلْيُقٰاتِلْ فِي سَبِيلِ اللّٰهِ الَّذِينَ يَشْرُونَ الْحَيٰاةَ الدُّنْيٰا بِالْآخِرَةِ وَ مَنْ يُقٰاتِلْ فِي سَبِيلِ اللّٰهِ فَيُقْتَلْ أَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيهِ أَجْراً عَظِيماً");
        sor.Ayaat.Add("وَ مٰا لَكُمْ لاٰ تُقٰاتِلُونَ فِي سَبِيلِ اللّٰهِ وَ الْمُسْتَضْعَفِينَ مِنَ الرِّجٰالِ وَ النِّسٰاءِ وَ الْوِلْدٰانِ الَّذِينَ يَقُولُونَ رَبَّنٰا أَخْرِجْنٰا مِنْ هٰذِهِ الْقَرْيَةِ الظّٰالِمِ أَهْلُهٰا وَ اجْعَلْ لَنٰا مِنْ لَدُنْكَ وَلِيًّا وَ اجْعَلْ لَنٰا مِنْ لَدُنْكَ نَصِيراً");
        sor.Ayaat.Add("الَّذِينَ آمَنُوا يُقٰاتِلُونَ فِي سَبِيلِ اللّٰهِ وَ الَّذِينَ كَفَرُوا يُقٰاتِلُونَ فِي سَبِيلِ الطّٰاغُوتِ فَقٰاتِلُوا أَوْلِيٰاءَ الشَّيْطٰانِ إِنَّ كَيْدَ الشَّيْطٰانِ كٰانَ ضَعِيفاً");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ قِيلَ لَهُمْ كُفُّوا أَيْدِيَكُمْ وَ أَقِيمُوا الصَّلاٰةَ وَ آتُوا الزَّكٰاةَ فَلَمّٰا كُتِبَ عَلَيْهِمُ الْقِتٰالُ إِذٰا فَرِيقٌ مِنْهُمْ يَخْشَوْنَ النّٰاسَ كَخَشْيَةِ اللّٰهِ أَوْ أَشَدَّ خَشْيَةً وَ قٰالُوا رَبَّنٰا لِمَ كَتَبْتَ عَلَيْنَا الْقِتٰالَ لَوْ لاٰ أَخَّرْتَنٰا إِلىٰ أَجَلٍ قَرِيبٍ قُلْ مَتٰاعُ الدُّنْيٰا قَلِيلٌ وَ الْآخِرَةُ خَيْرٌ لِمَنِ اتَّقىٰ وَ لاٰ تُظْلَمُونَ فَتِيلاً");
        sor.Ayaat.Add("أَيْنَمٰا تَكُونُوا يُدْرِكْكُمُ الْمَوْتُ وَ لَوْ كُنْتُمْ فِي بُرُوجٍ مُشَيَّدَةٍ وَ إِنْ تُصِبْهُمْ حَسَنَةٌ يَقُولُوا هٰذِهِ مِنْ عِنْدِ اللّٰهِ وَ إِنْ تُصِبْهُمْ سَيِّئَةٌ يَقُولُوا هٰذِهِ مِنْ عِنْدِكَ قُلْ كُلٌّ مِنْ عِنْدِ اللّٰهِ فَمٰا لِهٰؤُلاٰءِ الْقَوْمِ لاٰ يَكٰادُونَ يَفْقَهُونَ حَدِيثاً");
        sor.Ayaat.Add("مٰا أَصٰابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِ وَ مٰا أَصٰابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَفْسِكَ وَ أَرْسَلْنٰاكَ لِلنّٰاسِ رَسُولاً وَ كَفىٰ بِاللّٰهِ شَهِيداً");
        sor.Ayaat.Add("مَنْ يُطِعِ الرَّسُولَ فَقَدْ أَطٰاعَ اللّٰهَ وَ مَنْ تَوَلّٰى فَمٰا أَرْسَلْنٰاكَ عَلَيْهِمْ حَفِيظاً");
        sor.Ayaat.Add("وَ يَقُولُونَ طٰاعَةٌ فَإِذٰا بَرَزُوا مِنْ عِنْدِكَ بَيَّتَ طٰائِفَةٌ مِنْهُمْ غَيْرَ الَّذِي تَقُولُ وَ اللّٰهُ يَكْتُبُ مٰا يُبَيِّتُونَ فَأَعْرِضْ عَنْهُمْ وَ تَوَكَّلْ عَلَى اللّٰهِ وَ كَفىٰ بِاللّٰهِ وَكِيلاً");
        sor.Ayaat.Add("أَ فَلاٰ يَتَدَبَّرُونَ الْقُرْآنَ وَ لَوْ كٰانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوا فِيهِ اخْتِلاٰفاً كَثِيراً");
        sor.Ayaat.Add("وَ إِذٰا جٰاءَهُمْ أَمْرٌ مِنَ الْأَمْنِ أَوِ الْخَوْفِ أَذٰاعُوا بِهِ وَ لَوْ رَدُّوهُ إِلَى الرَّسُولِ وَ إِلىٰ أُولِي الْأَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِينَ يَسْتَنْبِطُونَهُ مِنْهُمْ وَ لَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكُمْ وَ رَحْمَتُهُ لاَتَّبَعْتُمُ الشَّيْطٰانَ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("فَقٰاتِلْ فِي سَبِيلِ اللّٰهِ لاٰ تُكَلَّفُ إِلاّٰ نَفْسَكَ وَ حَرِّضِ الْمُؤْمِنِينَ عَسَى اللّٰهُ أَنْ يَكُفَّ بَأْسَ الَّذِينَ كَفَرُوا وَ اللّٰهُ أَشَدُّ بَأْساً وَ أَشَدُّ تَنْكِيلاً");
        sor.Ayaat.Add("مَنْ يَشْفَعْ شَفٰاعَةً حَسَنَةً يَكُنْ لَهُ نَصِيبٌ مِنْهٰا وَ مَنْ يَشْفَعْ شَفٰاعَةً سَيِّئَةً يَكُنْ لَهُ كِفْلٌ مِنْهٰا وَ كٰانَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ مُقِيتاً");
        sor.Ayaat.Add("وَ إِذٰا حُيِّيتُمْ بِتَحِيَّةٍ فَحَيُّوا بِأَحْسَنَ مِنْهٰا أَوْ رُدُّوهٰا إِنَّ اللّٰهَ كٰانَ عَلىٰ كُلِّ شَيْءٍ حَسِيباً");
        sor.Ayaat.Add("اللّٰهُ لاٰ إِلٰهَ إِلاّٰ هُوَ لَيَجْمَعَنَّكُمْ إِلىٰ يَوْمِ الْقِيٰامَةِ لاٰ رَيْبَ فِيهِ وَ مَنْ أَصْدَقُ مِنَ اللّٰهِ حَدِيثاً");
        sor.Ayaat.Add("فَمٰا لَكُمْ فِي الْمُنٰافِقِينَ فِئَتَيْنِ وَ اللّٰهُ أَرْكَسَهُمْ بِمٰا كَسَبُوا أَ تُرِيدُونَ أَنْ تَهْدُوا مَنْ أَضَلَّ اللّٰهُ وَ مَنْ يُضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهُ سَبِيلاً");
        sor.Ayaat.Add("وَدُّوا لَوْ تَكْفُرُونَ كَمٰا كَفَرُوا فَتَكُونُونَ سَوٰاءً فَلاٰ تَتَّخِذُوا مِنْهُمْ أَوْلِيٰاءَ حَتّٰى يُهٰاجِرُوا فِي سَبِيلِ اللّٰهِ فَإِنْ تَوَلَّوْا فَخُذُوهُمْ وَ اقْتُلُوهُمْ حَيْثُ وَجَدْتُمُوهُمْ وَ لاٰ تَتَّخِذُوا مِنْهُمْ وَلِيًّا وَ لاٰ نَصِيراً");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ يَصِلُونَ إِلىٰ قَوْمٍ بَيْنَكُمْ وَ بَيْنَهُمْ مِيثٰاقٌ أَوْ جٰاؤُكُمْ حَصِرَتْ صُدُورُهُمْ أَنْ يُقٰاتِلُوكُمْ أَوْ يُقٰاتِلُوا قَوْمَهُمْ وَ لَوْ شٰاءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقٰاتَلُوكُمْ فَإِنِ اعْتَزَلُوكُمْ فَلَمْ يُقٰاتِلُوكُمْ وَ أَلْقَوْا إِلَيْكُمُ السَّلَمَ فَمٰا جَعَلَ اللّٰهُ لَكُمْ عَلَيْهِمْ سَبِيلاً");
        sor.Ayaat.Add("سَتَجِدُونَ آخَرِينَ يُرِيدُونَ أَنْ يَأْمَنُوكُمْ وَ يَأْمَنُوا قَوْمَهُمْ كُلَّمٰا رُدُّوا إِلَى الْفِتْنَةِ أُرْكِسُوا فِيهٰا فَإِنْ لَمْ يَعْتَزِلُوكُمْ وَ يُلْقُوا إِلَيْكُمُ السَّلَمَ وَ يَكُفُّوا أَيْدِيَهُمْ فَخُذُوهُمْ وَ اقْتُلُوهُمْ حَيْثُ ثَقِفْتُمُوهُمْ وَ أُولٰئِكُمْ جَعَلْنٰا لَكُمْ عَلَيْهِمْ سُلْطٰاناً مُبِيناً");
        sor.Ayaat.Add("وَ مٰا كٰانَ لِمُؤْمِنٍ أَنْ يَقْتُلَ مُؤْمِناً إِلاّٰ خَطَأً وَ مَنْ قَتَلَ مُؤْمِناً خَطَأً فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَ دِيَةٌ مُسَلَّمَةٌ إِلىٰ أَهْلِهِ إِلاّٰ أَنْ يَصَّدَّقُوا فَإِنْ كٰانَ مِنْ قَوْمٍ عَدُوٍّ لَكُمْ وَ هُوَ مُؤْمِنٌ فَتَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ وَ إِنْ كٰانَ مِنْ قَوْمٍ بَيْنَكُمْ وَ بَيْنَهُمْ مِيثٰاقٌ فَدِيَةٌ مُسَلَّمَةٌ إِلىٰ أَهْلِهِ وَ تَحْرِيرُ رَقَبَةٍ مُؤْمِنَةٍ فَمَنْ لَمْ يَجِدْ فَصِيٰامُ شَهْرَيْنِ مُتَتٰابِعَيْنِ تَوْبَةً مِنَ اللّٰهِ وَ كٰانَ اللّٰهُ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("وَ مَنْ يَقْتُلْ مُؤْمِناً مُتَعَمِّداً فَجَزٰاؤُهُ جَهَنَّمُ خٰالِداً فِيهٰا وَ غَضِبَ اللّٰهُ عَلَيْهِ وَ لَعَنَهُ وَ أَعَدَّ لَهُ عَذٰاباً عَظِيماً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِذٰا ضَرَبْتُمْ فِي سَبِيلِ اللّٰهِ فَتَبَيَّنُوا وَ لاٰ تَقُولُوا لِمَنْ أَلْقىٰ إِلَيْكُمُ السَّلاٰمَ لَسْتَ مُؤْمِناً تَبْتَغُونَ عَرَضَ الْحَيٰاةِ الدُّنْيٰا فَعِنْدَ اللّٰهِ مَغٰانِمُ كَثِيرَةٌ كَذٰلِكَ كُنْتُمْ مِنْ قَبْلُ فَمَنَّ اللّٰهُ عَلَيْكُمْ فَتَبَيَّنُوا إِنَّ اللّٰهَ كٰانَ بِمٰا تَعْمَلُونَ خَبِيراً");
        sor.Ayaat.Add("لاٰ يَسْتَوِي الْقٰاعِدُونَ مِنَ الْمُؤْمِنِينَ غَيْرُ أُولِي الضَّرَرِ وَ الْمُجٰاهِدُونَ فِي سَبِيلِ اللّٰهِ بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ فَضَّلَ اللّٰهُ الْمُجٰاهِدِينَ بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ عَلَى الْقٰاعِدِينَ دَرَجَةً وَ كُلاًّ وَعَدَ اللّٰهُ الْحُسْنىٰ وَ فَضَّلَ اللّٰهُ الْمُجٰاهِدِينَ عَلَى الْقٰاعِدِينَ أَجْراً عَظِيماً");
        sor.Ayaat.Add("دَرَجٰاتٍ مِنْهُ وَ مَغْفِرَةً وَ رَحْمَةً وَ كٰانَ اللّٰهُ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ تَوَفّٰاهُمُ الْمَلاٰئِكَةُ ظٰالِمِي أَنْفُسِهِمْ قٰالُوا فِيمَ كُنْتُمْ قٰالُوا كُنّٰا مُسْتَضْعَفِينَ فِي الْأَرْضِ قٰالُوا أَ لَمْ تَكُنْ أَرْضُ اللّٰهِ وٰاسِعَةً فَتُهٰاجِرُوا فِيهٰا فَأُولٰئِكَ مَأْوٰاهُمْ جَهَنَّمُ وَ سٰاءَتْ مَصِيراً");
        sor.Ayaat.Add("إِلاَّ الْمُسْتَضْعَفِينَ مِنَ الرِّجٰالِ وَ النِّسٰاءِ وَ الْوِلْدٰانِ لاٰ يَسْتَطِيعُونَ حِيلَةً وَ لاٰ يَهْتَدُونَ سَبِيلاً");
        sor.Ayaat.Add("فَأُولٰئِكَ عَسَى اللّٰهُ أَنْ يَعْفُوَ عَنْهُمْ وَ كٰانَ اللّٰهُ عَفُوًّا غَفُوراً");
        sor.Ayaat.Add("وَ مَنْ يُهٰاجِرْ فِي سَبِيلِ اللّٰهِ يَجِدْ فِي الْأَرْضِ مُرٰاغَماً كَثِيراً وَ سَعَةً وَ مَنْ يَخْرُجْ مِنْ بَيْتِهِ مُهٰاجِراً إِلَى اللّٰهِ وَ رَسُولِهِ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ أَجْرُهُ عَلَى اللّٰهِ وَ كٰانَ اللّٰهُ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("وَ إِذٰا ضَرَبْتُمْ فِي الْأَرْضِ فَلَيْسَ عَلَيْكُمْ جُنٰاحٌ أَنْ تَقْصُرُوا مِنَ الصَّلاٰةِ إِنْ خِفْتُمْ أَنْ يَفْتِنَكُمُ الَّذِينَ كَفَرُوا إِنَّ الْكٰافِرِينَ كٰانُوا لَكُمْ عَدُوًّا مُبِيناً");
        sor.Ayaat.Add("وَ إِذٰا كُنْتَ فِيهِمْ فَأَقَمْتَ لَهُمُ الصَّلاٰةَ فَلْتَقُمْ طٰائِفَةٌ مِنْهُمْ مَعَكَ وَ لْيَأْخُذُوا أَسْلِحَتَهُمْ فَإِذٰا سَجَدُوا فَلْيَكُونُوا مِنْ وَرٰائِكُمْ وَ لْتَأْتِ طٰائِفَةٌ أُخْرىٰ لَمْ يُصَلُّوا فَلْيُصَلُّوا مَعَكَ وَ لْيَأْخُذُوا حِذْرَهُمْ وَ أَسْلِحَتَهُمْ وَدَّ الَّذِينَ كَفَرُوا لَوْ تَغْفُلُونَ عَنْ أَسْلِحَتِكُمْ وَ أَمْتِعَتِكُمْ فَيَمِيلُونَ عَلَيْكُمْ مَيْلَةً وٰاحِدَةً وَ لاٰ جُنٰاحَ عَلَيْكُمْ إِنْ كٰانَ بِكُمْ أَذىً مِنْ مَطَرٍ أَوْ كُنْتُمْ مَرْضىٰ أَنْ تَضَعُوا أَسْلِحَتَكُمْ وَ خُذُوا حِذْرَكُمْ إِنَّ اللّٰهَ أَعَدَّ لِلْكٰافِرِينَ عَذٰاباً مُهِيناً");
        sor.Ayaat.Add("فَإِذٰا قَضَيْتُمُ الصَّلاٰةَ فَاذْكُرُوا اللّٰهَ قِيٰاماً وَ قُعُوداً وَ عَلىٰ جُنُوبِكُمْ فَإِذَا اطْمَأْنَنْتُمْ فَأَقِيمُوا الصَّلاٰةَ إِنَّ الصَّلاٰةَ كٰانَتْ عَلَى الْمُؤْمِنِينَ كِتٰاباً مَوْقُوتاً");
        sor.Ayaat.Add("وَ لاٰ تَهِنُوا فِي ابْتِغٰاءِ الْقَوْمِ إِنْ تَكُونُوا تَأْلَمُونَ فَإِنَّهُمْ يَأْلَمُونَ كَمٰا تَأْلَمُونَ وَ تَرْجُونَ مِنَ اللّٰهِ مٰا لاٰ يَرْجُونَ وَ كٰانَ اللّٰهُ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("إِنّٰا أَنْزَلْنٰا إِلَيْكَ الْكِتٰابَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النّٰاسِ بِمٰا أَرٰاكَ اللّٰهُ وَ لاٰ تَكُنْ لِلْخٰائِنِينَ خَصِيماً");
        sor.Ayaat.Add("وَ اسْتَغْفِرِ اللّٰهَ إِنَّ اللّٰهَ كٰانَ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("وَ لاٰ تُجٰادِلْ عَنِ الَّذِينَ يَخْتٰانُونَ أَنْفُسَهُمْ إِنَّ اللّٰهَ لاٰ يُحِبُّ مَنْ كٰانَ خَوّٰاناً أَثِيماً");
        sor.Ayaat.Add("يَسْتَخْفُونَ مِنَ النّٰاسِ وَ لاٰ يَسْتَخْفُونَ مِنَ اللّٰهِ وَ هُوَ مَعَهُمْ إِذْ يُبَيِّتُونَ مٰا لاٰ يَرْضىٰ مِنَ الْقَوْلِ وَ كٰانَ اللّٰهُ بِمٰا يَعْمَلُونَ مُحِيطاً");
        sor.Ayaat.Add("هٰا أَنْتُمْ هٰؤُلاٰءِ جٰادَلْتُمْ عَنْهُمْ فِي الْحَيٰاةِ الدُّنْيٰا فَمَنْ يُجٰادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰامَةِ أَمْ مَنْ يَكُونُ عَلَيْهِمْ وَكِيلاً");
        sor.Ayaat.Add("وَ مَنْ يَعْمَلْ سُوءاً أَوْ يَظْلِمْ نَفْسَهُ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("وَ مَنْ يَكْسِبْ إِثْماً فَإِنَّمٰا يَكْسِبُهُ عَلىٰ نَفْسِهِ وَ كٰانَ اللّٰهُ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("وَ مَنْ يَكْسِبْ خَطِيئَةً أَوْ إِثْماً ثُمَّ يَرْمِ بِهِ بَرِيئاً فَقَدِ احْتَمَلَ بُهْتٰاناً وَ إِثْماً مُبِيناً");
        sor.Ayaat.Add("وَ لَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكَ وَ رَحْمَتُهُ لَهَمَّتْ طٰائِفَةٌ مِنْهُمْ أَنْ يُضِلُّوكَ وَ مٰا يُضِلُّونَ إِلاّٰ أَنْفُسَهُمْ وَ مٰا يَضُرُّونَكَ مِنْ شَيْءٍ وَ أَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتٰابَ وَ الْحِكْمَةَ وَ عَلَّمَكَ مٰا لَمْ تَكُنْ تَعْلَمُ وَ كٰانَ فَضْلُ اللّٰهِ عَلَيْكَ عَظِيماً");
        sor.Ayaat.Add("لاٰ خَيْرَ فِي كَثِيرٍ مِنْ نَجْوٰاهُمْ إِلاّٰ مَنْ أَمَرَ بِصَدَقَةٍ أَوْ مَعْرُوفٍ أَوْ إِصْلاٰحٍ بَيْنَ النّٰاسِ وَ مَنْ يَفْعَلْ ذٰلِكَ ابْتِغٰاءَ مَرْضٰاتِ اللّٰهِ فَسَوْفَ نُؤْتِيهِ أَجْراً عَظِيماً");
        sor.Ayaat.Add("وَ مَنْ يُشٰاقِقِ الرَّسُولَ مِنْ بَعْدِ مٰا تَبَيَّنَ لَهُ الْهُدىٰ وَ يَتَّبِعْ غَيْرَ سَبِيلِ الْمُؤْمِنِينَ نُوَلِّهِ مٰا تَوَلّٰى وَ نُصْلِهِ جَهَنَّمَ وَ سٰاءَتْ مَصِيراً");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لاٰ يَغْفِرُ أَنْ يُشْرَكَ بِهِ وَ يَغْفِرُ مٰا دُونَ ذٰلِكَ لِمَنْ يَشٰاءُ وَ مَنْ يُشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلاٰلاً بَعِيداً");
        sor.Ayaat.Add("إِنْ يَدْعُونَ مِنْ دُونِهِ إِلاّٰ إِنٰاثاً وَ إِنْ يَدْعُونَ إِلاّٰ شَيْطٰاناً مَرِيداً");
        sor.Ayaat.Add("لَعَنَهُ اللّٰهُ وَ قٰالَ لَأَتَّخِذَنَّ مِنْ عِبٰادِكَ نَصِيباً مَفْرُوضاً");
        sor.Ayaat.Add("وَ لَأُضِلَّنَّهُمْ وَ لَأُمَنِّيَنَّهُمْ وَ لَآمُرَنَّهُمْ فَلَيُبَتِّكُنَّ آذٰانَ الْأَنْعٰامِ وَ لَآمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِ وَ مَنْ يَتَّخِذِ الشَّيْطٰانَ وَلِيًّا مِنْ دُونِ اللّٰهِ فَقَدْ خَسِرَ خُسْرٰاناً مُبِيناً");
        sor.Ayaat.Add("يَعِدُهُمْ وَ يُمَنِّيهِمْ وَ مٰا يَعِدُهُمُ الشَّيْطٰانُ إِلاّٰ غُرُوراً");
        sor.Ayaat.Add("أُولٰئِكَ مَأْوٰاهُمْ جَهَنَّمُ وَ لاٰ يَجِدُونَ عَنْهٰا مَحِيصاً");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ سَنُدْخِلُهُمْ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا أَبَداً وَعْدَ اللّٰهِ حَقًّا وَ مَنْ أَصْدَقُ مِنَ اللّٰهِ قِيلاً");
        sor.Ayaat.Add("لَيْسَ بِأَمٰانِيِّكُمْ وَ لاٰ أَمٰانِيِّ أَهْلِ الْكِتٰابِ مَنْ يَعْمَلْ سُوءاً يُجْزَ بِهِ وَ لاٰ يَجِدْ لَهُ مِنْ دُونِ اللّٰهِ وَلِيًّا وَ لاٰ نَصِيراً");
        sor.Ayaat.Add("وَ مَنْ يَعْمَلْ مِنَ الصّٰالِحٰاتِ مِنْ ذَكَرٍ أَوْ أُنْثىٰ وَ هُوَ مُؤْمِنٌ فَأُولٰئِكَ يَدْخُلُونَ الْجَنَّةَ وَ لاٰ يُظْلَمُونَ نَقِيراً");
        sor.Ayaat.Add("وَ مَنْ أَحْسَنُ دِيناً مِمَّنْ أَسْلَمَ وَجْهَهُ لِلّٰهِ وَ هُوَ مُحْسِنٌ وَ اتَّبَعَ مِلَّةَ إِبْرٰاهِيمَ حَنِيفاً وَ اتَّخَذَ اللّٰهُ إِبْرٰاهِيمَ خَلِيلاً");
        sor.Ayaat.Add("وَ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ كٰانَ اللّٰهُ بِكُلِّ شَيْءٍ مُحِيطاً");
        sor.Ayaat.Add("وَ يَسْتَفْتُونَكَ فِي النِّسٰاءِ قُلِ اللّٰهُ يُفْتِيكُمْ فِيهِنَّ وَ مٰا يُتْلىٰ عَلَيْكُمْ فِي الْكِتٰابِ فِي يَتٰامَى النِّسٰاءِ اللاّٰتِي لاٰ تُؤْتُونَهُنَّ مٰا كُتِبَ لَهُنَّ وَ تَرْغَبُونَ أَنْ تَنْكِحُوهُنَّ وَ الْمُسْتَضْعَفِينَ مِنَ الْوِلْدٰانِ وَ أَنْ تَقُومُوا لِلْيَتٰامىٰ بِالْقِسْطِ وَ مٰا تَفْعَلُوا مِنْ خَيْرٍ فَإِنَّ اللّٰهَ كٰانَ بِهِ عَلِيماً");
        sor.Ayaat.Add("وَ إِنِ امْرَأَةٌ خٰافَتْ مِنْ بَعْلِهٰا نُشُوزاً أَوْ إِعْرٰاضاً فَلاٰ جُنٰاحَ عَلَيْهِمٰا أَنْ يُصْلِحٰا بَيْنَهُمٰا صُلْحاً وَ الصُّلْحُ خَيْرٌ وَ أُحْضِرَتِ الْأَنْفُسُ الشُّحَّ وَ إِنْ تُحْسِنُوا وَ تَتَّقُوا فَإِنَّ اللّٰهَ كٰانَ بِمٰا تَعْمَلُونَ خَبِيراً");
        sor.Ayaat.Add("وَ لَنْ تَسْتَطِيعُوا أَنْ تَعْدِلُوا بَيْنَ النِّسٰاءِ وَ لَوْ حَرَصْتُمْ فَلاٰ تَمِيلُوا كُلَّ الْمَيْلِ فَتَذَرُوهٰا كَالْمُعَلَّقَةِ وَ إِنْ تُصْلِحُوا وَ تَتَّقُوا فَإِنَّ اللّٰهَ كٰانَ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("وَ إِنْ يَتَفَرَّقٰا يُغْنِ اللّٰهُ كُلاًّ مِنْ سَعَتِهِ وَ كٰانَ اللّٰهُ وٰاسِعاً حَكِيماً");
        sor.Ayaat.Add("وَ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ لَقَدْ وَصَّيْنَا الَّذِينَ أُوتُوا الْكِتٰابَ مِنْ قَبْلِكُمْ وَ إِيّٰاكُمْ أَنِ اتَّقُوا اللّٰهَ وَ إِنْ تَكْفُرُوا فَإِنَّ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ كٰانَ اللّٰهُ غَنِيًّا حَمِيداً");
        sor.Ayaat.Add("وَ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ كَفىٰ بِاللّٰهِ وَكِيلاً");
        sor.Ayaat.Add("إِنْ يَشَأْ يُذْهِبْكُمْ أَيُّهَا النّٰاسُ وَ يَأْتِ بِآخَرِينَ وَ كٰانَ اللّٰهُ عَلىٰ ذٰلِكَ قَدِيراً");
        sor.Ayaat.Add("مَنْ كٰانَ يُرِيدُ ثَوٰابَ الدُّنْيٰا فَعِنْدَ اللّٰهِ ثَوٰابُ الدُّنْيٰا وَ الْآخِرَةِ وَ كٰانَ اللّٰهُ سَمِيعاً بَصِيراً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوّٰامِينَ بِالْقِسْطِ شُهَدٰاءَ لِلّٰهِ وَ لَوْ عَلىٰ أَنْفُسِكُمْ أَوِ الْوٰالِدَيْنِ وَ الْأَقْرَبِينَ إِنْ يَكُنْ غَنِيًّا أَوْ فَقِيراً فَاللّٰهُ أَوْلىٰ بِهِمٰا فَلاٰ تَتَّبِعُوا الْهَوىٰ أَنْ تَعْدِلُوا وَ إِنْ تَلْوُوا أَوْ تُعْرِضُوا فَإِنَّ اللّٰهَ كٰانَ بِمٰا تَعْمَلُونَ خَبِيراً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا آمِنُوا بِاللّٰهِ وَ رَسُولِهِ وَ الْكِتٰابِ الَّذِي نَزَّلَ عَلىٰ رَسُولِهِ وَ الْكِتٰابِ الَّذِي أَنْزَلَ مِنْ قَبْلُ وَ مَنْ يَكْفُرْ بِاللّٰهِ وَ مَلاٰئِكَتِهِ وَ كُتُبِهِ وَ رُسُلِهِ وَ الْيَوْمِ الْآخِرِ فَقَدْ ضَلَّ ضَلاٰلاً بَعِيداً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ آمَنُوا ثُمَّ كَفَرُوا ثُمَّ ازْدٰادُوا كُفْراً لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَ لاٰ لِيَهْدِيَهُمْ سَبِيلاً");
        sor.Ayaat.Add("بَشِّرِ الْمُنٰافِقِينَ بِأَنَّ لَهُمْ عَذٰاباً أَلِيماً");
        sor.Ayaat.Add("الَّذِينَ يَتَّخِذُونَ الْكٰافِرِينَ أَوْلِيٰاءَ مِنْ دُونِ الْمُؤْمِنِينَ أَ يَبْتَغُونَ عِنْدَهُمُ الْعِزَّةَ فَإِنَّ الْعِزَّةَ لِلّٰهِ جَمِيعاً");
        sor.Ayaat.Add("وَ قَدْ نَزَّلَ عَلَيْكُمْ فِي الْكِتٰابِ أَنْ إِذٰا سَمِعْتُمْ آيٰاتِ اللّٰهِ يُكْفَرُ بِهٰا وَ يُسْتَهْزَأُ بِهٰا فَلاٰ تَقْعُدُوا مَعَهُمْ حَتّٰى يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ إِنَّكُمْ إِذاً مِثْلُهُمْ إِنَّ اللّٰهَ جٰامِعُ الْمُنٰافِقِينَ وَ الْكٰافِرِينَ فِي جَهَنَّمَ جَمِيعاً");
        sor.Ayaat.Add("الَّذِينَ يَتَرَبَّصُونَ بِكُمْ فَإِنْ كٰانَ لَكُمْ فَتْحٌ مِنَ اللّٰهِ قٰالُوا أَ لَمْ نَكُنْ مَعَكُمْ وَ إِنْ كٰانَ لِلْكٰافِرِينَ نَصِيبٌ قٰالُوا أَ لَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَ نَمْنَعْكُمْ مِنَ الْمُؤْمِنِينَ فَاللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰامَةِ وَ لَنْ يَجْعَلَ اللّٰهُ لِلْكٰافِرِينَ عَلَى الْمُؤْمِنِينَ سَبِيلاً");
        sor.Ayaat.Add("إِنَّ الْمُنٰافِقِينَ يُخٰادِعُونَ اللّٰهَ وَ هُوَ خٰادِعُهُمْ وَ إِذٰا قٰامُوا إِلَى الصَّلاٰةِ قٰامُوا كُسٰالىٰ يُرٰاؤُنَ النّٰاسَ وَ لاٰ يَذْكُرُونَ اللّٰهَ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("مُذَبْذَبِينَ بَيْنَ ذٰلِكَ لاٰ إِلىٰ هٰؤُلاٰءِ وَ لاٰ إِلىٰ هٰؤُلاٰءِ وَ مَنْ يُضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهُ سَبِيلاً");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَتَّخِذُوا الْكٰافِرِينَ أَوْلِيٰاءَ مِنْ دُونِ الْمُؤْمِنِينَ أَ تُرِيدُونَ أَنْ تَجْعَلُوا لِلّٰهِ عَلَيْكُمْ سُلْطٰاناً مُبِيناً");
        sor.Ayaat.Add("إِنَّ الْمُنٰافِقِينَ فِي الدَّرْكِ الْأَسْفَلِ مِنَ النّٰارِ وَ لَنْ تَجِدَ لَهُمْ نَصِيراً");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ تٰابُوا وَ أَصْلَحُوا وَ اعْتَصَمُوا بِاللّٰهِ وَ أَخْلَصُوا دِينَهُمْ لِلّٰهِ فَأُولٰئِكَ مَعَ الْمُؤْمِنِينَ وَ سَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِنِينَ أَجْراً عَظِيماً");
        sor.Ayaat.Add("مٰا يَفْعَلُ اللّٰهُ بِعَذٰابِكُمْ إِنْ شَكَرْتُمْ وَ آمَنْتُمْ وَ كٰانَ اللّٰهُ شٰاكِراً عَلِيماً");
        sor.Ayaat.Add("لاٰ يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّوءِ مِنَ الْقَوْلِ إِلاّٰ مَنْ ظُلِمَ وَ كٰانَ اللّٰهُ سَمِيعاً عَلِيماً");
        sor.Ayaat.Add("إِنْ تُبْدُوا خَيْراً أَوْ تُخْفُوهُ أَوْ تَعْفُوا عَنْ سُوءٍ فَإِنَّ اللّٰهَ كٰانَ عَفُوًّا قَدِيراً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَكْفُرُونَ بِاللّٰهِ وَ رُسُلِهِ وَ يُرِيدُونَ أَنْ يُفَرِّقُوا بَيْنَ اللّٰهِ وَ رُسُلِهِ وَ يَقُولُونَ نُؤْمِنُ بِبَعْضٍ وَ نَكْفُرُ بِبَعْضٍ وَ يُرِيدُونَ أَنْ يَتَّخِذُوا بَيْنَ ذٰلِكَ سَبِيلاً");
        sor.Ayaat.Add("أُولٰئِكَ هُمُ الْكٰافِرُونَ حَقًّا وَ أَعْتَدْنٰا لِلْكٰافِرِينَ عَذٰاباً مُهِيناً");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا بِاللّٰهِ وَ رُسُلِهِ وَ لَمْ يُفَرِّقُوا بَيْنَ أَحَدٍ مِنْهُمْ أُولٰئِكَ سَوْفَ يُؤْتِيهِمْ أُجُورَهُمْ وَ كٰانَ اللّٰهُ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("يَسْئَلُكَ أَهْلُ الْكِتٰابِ أَنْ تُنَزِّلَ عَلَيْهِمْ كِتٰاباً مِنَ السَّمٰاءِ فَقَدْ سَأَلُوا مُوسىٰ أَكْبَرَ مِنْ ذٰلِكَ فَقٰالُوا أَرِنَا اللّٰهَ جَهْرَةً فَأَخَذَتْهُمُ الصّٰاعِقَةُ بِظُلْمِهِمْ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْ بَعْدِ مٰا جٰاءَتْهُمُ الْبَيِّنٰاتُ فَعَفَوْنٰا عَنْ ذٰلِكَ وَ آتَيْنٰا مُوسىٰ سُلْطٰاناً مُبِيناً");
        sor.Ayaat.Add("وَ رَفَعْنٰا فَوْقَهُمُ الطُّورَ بِمِيثٰاقِهِمْ وَ قُلْنٰا لَهُمُ ادْخُلُوا الْبٰابَ سُجَّداً وَ قُلْنٰا لَهُمْ لاٰ تَعْدُوا فِي السَّبْتِ وَ أَخَذْنٰا مِنْهُمْ مِيثٰاقاً غَلِيظاً");
        sor.Ayaat.Add("فَبِمٰا نَقْضِهِمْ مِيثٰاقَهُمْ وَ كُفْرِهِمْ بِآيٰاتِ اللّٰهِ وَ قَتْلِهِمُ الْأَنْبِيٰاءَ بِغَيْرِ حَقٍّ وَ قَوْلِهِمْ قُلُوبُنٰا غُلْفٌ بَلْ طَبَعَ اللّٰهُ عَلَيْهٰا بِكُفْرِهِمْ فَلاٰ يُؤْمِنُونَ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("وَ بِكُفْرِهِمْ وَ قَوْلِهِمْ عَلىٰ مَرْيَمَ بُهْتٰاناً عَظِيماً");
        sor.Ayaat.Add("وَ قَوْلِهِمْ إِنّٰا قَتَلْنَا الْمَسِيحَ عِيسَى ابْنَ مَرْيَمَ رَسُولَ اللّٰهِ وَ مٰا قَتَلُوهُ وَ مٰا صَلَبُوهُ وَ لٰكِنْ شُبِّهَ لَهُمْ وَ إِنَّ الَّذِينَ اخْتَلَفُوا فِيهِ لَفِي شَكٍّ مِنْهُ مٰا لَهُمْ بِهِ مِنْ عِلْمٍ إِلاَّ اتِّبٰاعَ الظَّنِّ وَ مٰا قَتَلُوهُ يَقِيناً");
        sor.Ayaat.Add("بَلْ رَفَعَهُ اللّٰهُ إِلَيْهِ وَ كٰانَ اللّٰهُ عَزِيزاً حَكِيماً");
        sor.Ayaat.Add("وَ إِنْ مِنْ أَهْلِ الْكِتٰابِ إِلاّٰ لَيُؤْمِنَنَّ بِهِ قَبْلَ مَوْتِهِ وَ يَوْمَ الْقِيٰامَةِ يَكُونُ عَلَيْهِمْ شَهِيداً");
        sor.Ayaat.Add("فَبِظُلْمٍ مِنَ الَّذِينَ هٰادُوا حَرَّمْنٰا عَلَيْهِمْ طَيِّبٰاتٍ أُحِلَّتْ لَهُمْ وَ بِصَدِّهِمْ عَنْ سَبِيلِ اللّٰهِ كَثِيراً");
        sor.Ayaat.Add("وَ أَخْذِهِمُ الرِّبَوا وَ قَدْ نُهُوا عَنْهُ وَ أَكْلِهِمْ أَمْوٰالَ النّٰاسِ بِالْبٰاطِلِ وَ أَعْتَدْنٰا لِلْكٰافِرِينَ مِنْهُمْ عَذٰاباً أَلِيماً");
        sor.Ayaat.Add("لٰكِنِ الرّٰاسِخُونَ فِي الْعِلْمِ مِنْهُمْ وَ الْمُؤْمِنُونَ يُؤْمِنُونَ بِمٰا أُنْزِلَ إِلَيْكَ وَ مٰا أُنْزِلَ مِنْ قَبْلِكَ وَ الْمُقِيمِينَ الصَّلاٰةَ وَ الْمُؤْتُونَ الزَّكٰاةَ وَ الْمُؤْمِنُونَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ أُولٰئِكَ سَنُؤْتِيهِمْ أَجْراً عَظِيماً");
        sor.Ayaat.Add("إِنّٰا أَوْحَيْنٰا إِلَيْكَ كَمٰا أَوْحَيْنٰا إِلىٰ نُوحٍ وَ النَّبِيِّينَ مِنْ بَعْدِهِ وَ أَوْحَيْنٰا إِلىٰ إِبْرٰاهِيمَ وَ إِسْمٰاعِيلَ وَ إِسْحٰاقَ وَ يَعْقُوبَ وَ الْأَسْبٰاطِ وَ عِيسىٰ وَ أَيُّوبَ وَ يُونُسَ وَ هٰارُونَ وَ سُلَيْمٰانَ وَ آتَيْنٰا دٰاوُدَ زَبُوراً");
        sor.Ayaat.Add("وَ رُسُلاً قَدْ قَصَصْنٰاهُمْ عَلَيْكَ مِنْ قَبْلُ وَ رُسُلاً لَمْ نَقْصُصْهُمْ عَلَيْكَ وَ كَلَّمَ اللّٰهُ مُوسىٰ تَكْلِيماً");
        sor.Ayaat.Add("رُسُلاً مُبَشِّرِينَ وَ مُنْذِرِينَ لِئَلاّٰ يَكُونَ لِلنّٰاسِ عَلَى اللّٰهِ حُجَّةٌ بَعْدَ الرُّسُلِ وَ كٰانَ اللّٰهُ عَزِيزاً حَكِيماً");
        sor.Ayaat.Add("لٰكِنِ اللّٰهُ يَشْهَدُ بِمٰا أَنْزَلَ إِلَيْكَ أَنْزَلَهُ بِعِلْمِهِ وَ الْمَلاٰئِكَةُ يَشْهَدُونَ وَ كَفىٰ بِاللّٰهِ شَهِيداً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللّٰهِ قَدْ ضَلُّوا ضَلاٰلاً بَعِيداً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا وَ ظَلَمُوا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَ لاٰ لِيَهْدِيَهُمْ طَرِيقاً");
        sor.Ayaat.Add("إِلاّٰ طَرِيقَ جَهَنَّمَ خٰالِدِينَ فِيهٰا أَبَداً وَ كٰانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيراً");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ قَدْ جٰاءَكُمُ الرَّسُولُ بِالْحَقِّ مِنْ رَبِّكُمْ فَآمِنُوا خَيْراً لَكُمْ وَ إِنْ تَكْفُرُوا فَإِنَّ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ وَ كٰانَ اللّٰهُ عَلِيماً حَكِيماً");
        sor.Ayaat.Add("يٰا أَهْلَ الْكِتٰابِ لاٰ تَغْلُوا فِي دِينِكُمْ وَ لاٰ تَقُولُوا عَلَى اللّٰهِ إِلاَّ الْحَقَّ إِنَّمَا الْمَسِيحُ عِيسَى ابْنُ مَرْيَمَ رَسُولُ اللّٰهِ وَ كَلِمَتُهُ أَلْقٰاهٰا إِلىٰ مَرْيَمَ وَ رُوحٌ مِنْهُ فَآمِنُوا بِاللّٰهِ وَ رُسُلِهِ وَ لاٰ تَقُولُوا ثَلاٰثَةٌ انْتَهُوا خَيْراً لَكُمْ إِنَّمَا اللّٰهُ إِلٰهٌ وٰاحِدٌ سُبْحٰانَهُ أَنْ يَكُونَ لَهُ وَلَدٌ لَهُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ كَفىٰ بِاللّٰهِ وَكِيلاً");
        sor.Ayaat.Add("لَنْ يَسْتَنْكِفَ الْمَسِيحُ أَنْ يَكُونَ عَبْداً لِلّٰهِ وَ لاَ الْمَلاٰئِكَةُ الْمُقَرَّبُونَ وَ مَنْ يَسْتَنْكِفْ عَنْ عِبٰادَتِهِ وَ يَسْتَكْبِرْ فَسَيَحْشُرُهُمْ إِلَيْهِ جَمِيعاً");
        sor.Ayaat.Add("فَأَمَّا الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ فَيُوَفِّيهِمْ أُجُورَهُمْ وَ يَزِيدُهُمْ مِنْ فَضْلِهِ وَ أَمَّا الَّذِينَ اسْتَنْكَفُوا وَ اسْتَكْبَرُوا فَيُعَذِّبُهُمْ عَذٰاباً أَلِيماً وَ لاٰ يَجِدُونَ لَهُمْ مِنْ دُونِ اللّٰهِ وَلِيًّا وَ لاٰ نَصِيراً");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ قَدْ جٰاءَكُمْ بُرْهٰانٌ مِنْ رَبِّكُمْ وَ أَنْزَلْنٰا إِلَيْكُمْ نُوراً مُبِيناً");
        sor.Ayaat.Add("فَأَمَّا الَّذِينَ آمَنُوا بِاللّٰهِ وَ اعْتَصَمُوا بِهِ فَسَيُدْخِلُهُمْ فِي رَحْمَةٍ مِنْهُ وَ فَضْلٍ وَ يَهْدِيهِمْ إِلَيْهِ صِرٰاطاً مُسْتَقِيماً");
        sor.Ayaat.Add("يَسْتَفْتُونَكَ قُلِ اللّٰهُ يُفْتِيكُمْ فِي الْكَلاٰلَةِ إِنِ امْرُؤٌ هَلَكَ لَيْسَ لَهُ وَلَدٌ وَ لَهُ أُخْتٌ فَلَهٰا نِصْفُ مٰا تَرَكَ وَ هُوَ يَرِثُهٰا إِنْ لَمْ يَكُنْ لَهٰا وَلَدٌ فَإِنْ كٰانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثٰانِ مِمّٰا تَرَكَ وَ إِنْ كٰانُوا إِخْوَةً رِجٰالاً وَ نِسٰاءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْأُنْثَيَيْنِ يُبَيِّنُ اللّٰهُ لَكُمْ أَنْ تَضِلُّوا وَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيمٌ");
        salam(sor);
        sor = new Soore();
        sor.Name = "۵ - المایدة";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ يٰا أَيُّهَا الَّذِينَ آمَنُوا أَوْفُوا بِالْعُقُودِ أُحِلَّتْ لَكُمْ بَهِيمَةُ الْأَنْعٰامِ إِلاّٰ مٰا يُتْلىٰ عَلَيْكُمْ غَيْرَ مُحِلِّي الصَّيْدِ وَ أَنْتُمْ حُرُمٌ إِنَّ اللّٰهَ يَحْكُمُ مٰا يُرِيدُ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تُحِلُّوا شَعٰائِرَ اللّٰهِ وَ لاَ الشَّهْرَ الْحَرٰامَ وَ لاَ الْهَدْيَ وَ لاَ الْقَلاٰئِدَ وَ لاَ آمِّينَ الْبَيْتَ الْحَرٰامَ يَبْتَغُونَ فَضْلاً مِنْ رَبِّهِمْ وَ رِضْوٰاناً وَ إِذٰا حَلَلْتُمْ فَاصْطٰادُوا وَ لاٰ يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ أَنْ صَدُّوكُمْ عَنِ الْمَسْجِدِ الْحَرٰامِ أَنْ تَعْتَدُوا وَ تَعٰاوَنُوا عَلَى الْبِرِّ وَ التَّقْوىٰ وَ لاٰ تَعٰاوَنُوا عَلَى الْإِثْمِ وَ الْعُدْوٰانِ وَ اتَّقُوا اللّٰهَ إِنَّ اللّٰهَ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَ الدَّمُ وَ لَحْمُ الْخِنْزِيرِ وَ مٰا أُهِلَّ لِغَيْرِ اللّٰهِ بِهِ وَ الْمُنْخَنِقَةُ وَ الْمَوْقُوذَةُ وَ الْمُتَرَدِّيَةُ وَ النَّطِيحَةُ وَ مٰا أَكَلَ السَّبُعُ إِلاّٰ مٰا ذَكَّيْتُمْ وَ مٰا ذُبِحَ عَلَى النُّصُبِ وَ أَنْ تَسْتَقْسِمُوا بِالْأَزْلاٰمِ ذٰلِكُمْ فِسْقٌ الْيَوْمَ يَئِسَ الَّذِينَ كَفَرُوا مِنْ دِينِكُمْ فَلاٰ تَخْشَوْهُمْ وَ اخْشَوْنِ الْيَوْمَ أَكْمَلْتُ لَكُمْ دِينَكُمْ وَ أَتْمَمْتُ عَلَيْكُمْ نِعْمَتِي وَ رَضِيتُ لَكُمُ الْإِسْلاٰمَ دِيناً فَمَنِ اضْطُرَّ فِي مَخْمَصَةٍ غَيْرَ مُتَجٰانِفٍ لِإِثْمٍ فَإِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يَسْئَلُونَكَ مٰاذٰا أُحِلَّ لَهُمْ قُلْ أُحِلَّ لَكُمُ الطَّيِّبٰاتُ وَ مٰا عَلَّمْتُمْ مِنَ الْجَوٰارِحِ مُكَلِّبِينَ تُعَلِّمُونَهُنَّ مِمّٰا عَلَّمَكُمُ اللّٰهُ فَكُلُوا مِمّٰا أَمْسَكْنَ عَلَيْكُمْ وَ اذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِ وَ اتَّقُوا اللّٰهَ إِنَّ اللّٰهَ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("الْيَوْمَ أُحِلَّ لَكُمُ الطَّيِّبٰاتُ وَ طَعٰامُ الَّذِينَ أُوتُوا الْكِتٰابَ حِلٌّ لَكُمْ وَ طَعٰامُكُمْ حِلٌّ لَهُمْ وَ الْمُحْصَنٰاتُ مِنَ الْمُؤْمِنٰاتِ وَ الْمُحْصَنٰاتُ مِنَ الَّذِينَ أُوتُوا الْكِتٰابَ مِنْ قَبْلِكُمْ إِذٰا آتَيْتُمُوهُنَّ أُجُورَهُنَّ مُحْصِنِينَ غَيْرَ مُسٰافِحِينَ وَ لاٰ مُتَّخِذِي أَخْدٰانٍ وَ مَنْ يَكْفُرْ بِالْإِيمٰانِ فَقَدْ حَبِطَ عَمَلُهُ وَ هُوَ فِي الْآخِرَةِ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِذٰا قُمْتُمْ إِلَى الصَّلاٰةِ فَاغْسِلُوا وُجُوهَكُمْ وَ أَيْدِيَكُمْ إِلَى الْمَرٰافِقِ وَ امْسَحُوا بِرُؤُسِكُمْ وَ أَرْجُلَكُمْ إِلَى الْكَعْبَيْنِ وَ إِنْ كُنْتُمْ جُنُباً فَاطَّهَّرُوا وَ إِنْ كُنْتُمْ مَرْضىٰ أَوْ عَلىٰ سَفَرٍ أَوْ جٰاءَ أَحَدٌ مِنْكُمْ مِنَ الْغٰائِطِ أَوْ لاٰمَسْتُمُ النِّسٰاءَ فَلَمْ تَجِدُوا مٰاءً فَتَيَمَّمُوا صَعِيداً طَيِّباً فَامْسَحُوا بِوُجُوهِكُمْ وَ أَيْدِيكُمْ مِنْهُ مٰا يُرِيدُ اللّٰهُ لِيَجْعَلَ عَلَيْكُمْ مِنْ حَرَجٍ وَ لٰكِنْ يُرِيدُ لِيُطَهِّرَكُمْ وَ لِيُتِمَّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("وَ اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَ مِيثٰاقَهُ الَّذِي وٰاثَقَكُمْ بِهِ إِذْ قُلْتُمْ سَمِعْنٰا وَ أَطَعْنٰا وَ اتَّقُوا اللّٰهَ إِنَّ اللّٰهَ عَلِيمٌ بِذٰاتِ الصُّدُورِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا قَوّٰامِينَ لِلّٰهِ شُهَدٰاءَ بِالْقِسْطِ وَ لاٰ يَجْرِمَنَّكُمْ شَنَآنُ قَوْمٍ عَلىٰ أَلاّٰ تَعْدِلُوا اعْدِلُوا هُوَ أَقْرَبُ لِلتَّقْوىٰ وَ اتَّقُوا اللّٰهَ إِنَّ اللّٰهَ خَبِيرٌ بِمٰا تَعْمَلُونَ");
        sor.Ayaat.Add("وَعَدَ اللّٰهُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ عَظِيمٌ");
        sor.Ayaat.Add("وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآيٰاتِنٰا أُولٰئِكَ أَصْحٰابُ الْجَحِيمِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ إِذْ هَمَّ قَوْمٌ أَنْ يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنْكُمْ وَ اتَّقُوا اللّٰهَ وَ عَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَخَذَ اللّٰهُ مِيثٰاقَ بَنِي إِسْرٰائِيلَ وَ بَعَثْنٰا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيباً وَ قٰالَ اللّٰهُ إِنِّي مَعَكُمْ لَئِنْ أَقَمْتُمُ الصَّلاٰةَ وَ آتَيْتُمُ الزَّكٰاةَ وَ آمَنْتُمْ بِرُسُلِي وَ عَزَّرْتُمُوهُمْ وَ أَقْرَضْتُمُ اللّٰهَ قَرْضاً حَسَناً لَأُكَفِّرَنَّ عَنْكُمْ سَيِّئٰاتِكُمْ وَ لَأُدْخِلَنَّكُمْ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ فَمَنْ كَفَرَ بَعْدَ ذٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوٰاءَ السَّبِيلِ");
        sor.Ayaat.Add("فَبِمٰا نَقْضِهِمْ مِيثٰاقَهُمْ لَعَنّٰاهُمْ وَ جَعَلْنٰا قُلُوبَهُمْ قٰاسِيَةً يُحَرِّفُونَ الْكَلِمَ عَنْ مَوٰاضِعِهِ وَ نَسُوا حَظًّا مِمّٰا ذُكِّرُوا بِهِ وَ لاٰ تَزٰالُ تَطَّلِعُ عَلىٰ خٰائِنَةٍ مِنْهُمْ إِلاّٰ قَلِيلاً مِنْهُمْ فَاعْفُ عَنْهُمْ وَ اصْفَحْ إِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ مِنَ الَّذِينَ قٰالُوا إِنّٰا نَصٰارىٰ أَخَذْنٰا مِيثٰاقَهُمْ فَنَسُوا حَظًّا مِمّٰا ذُكِّرُوا بِهِ فَأَغْرَيْنٰا بَيْنَهُمُ الْعَدٰاوَةَ وَ الْبَغْضٰاءَ إِلىٰ يَوْمِ الْقِيٰامَةِ وَ سَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمٰا كٰانُوا يَصْنَعُونَ");
        sor.Ayaat.Add("يٰا أَهْلَ الْكِتٰابِ قَدْ جٰاءَكُمْ رَسُولُنٰا يُبَيِّنُ لَكُمْ كَثِيراً مِمّٰا كُنْتُمْ تُخْفُونَ مِنَ الْكِتٰابِ وَ يَعْفُوا عَنْ كَثِيرٍ قَدْ جٰاءَكُمْ مِنَ اللّٰهِ نُورٌ وَ كِتٰابٌ مُبِينٌ");
        sor.Ayaat.Add("يَهْدِي بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوٰانَهُ سُبُلَ السَّلاٰمِ وَ يُخْرِجُهُمْ مِنَ الظُّلُمٰاتِ إِلَى النُّورِ بِإِذْنِهِ وَ يَهْدِيهِمْ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("لَقَدْ كَفَرَ الَّذِينَ قٰالُوا إِنَّ اللّٰهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ قُلْ فَمَنْ يَمْلِكُ مِنَ اللّٰهِ شَيْئاً إِنْ أَرٰادَ أَنْ يُهْلِكَ الْمَسِيحَ ابْنَ مَرْيَمَ وَ أُمَّهُ وَ مَنْ فِي الْأَرْضِ جَمِيعاً وَ لِلّٰهِ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا بَيْنَهُمٰا يَخْلُقُ مٰا يَشٰاءُ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ قٰالَتِ الْيَهُودُ وَ النَّصٰارىٰ نَحْنُ أَبْنٰاءُ اللّٰهِ وَ أَحِبّٰاؤُهُ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوبِكُمْ بَلْ أَنْتُمْ بَشَرٌ مِمَّنْ خَلَقَ يَغْفِرُ لِمَنْ يَشٰاءُ وَ يُعَذِّبُ مَنْ يَشٰاءُ وَ لِلّٰهِ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا بَيْنَهُمٰا وَ إِلَيْهِ الْمَصِيرُ");
        sor.Ayaat.Add("يٰا أَهْلَ الْكِتٰابِ قَدْ جٰاءَكُمْ رَسُولُنٰا يُبَيِّنُ لَكُمْ عَلىٰ فَتْرَةٍ مِنَ الرُّسُلِ أَنْ تَقُولُوا مٰا جٰاءَنٰا مِنْ بَشِيرٍ وَ لاٰ نَذِيرٍ فَقَدْ جٰاءَكُمْ بَشِيرٌ وَ نَذِيرٌ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ مُوسىٰ لِقَوْمِهِ يٰا قَوْمِ اذْكُرُوا نِعْمَتَ اللّٰهِ عَلَيْكُمْ إِذْ جَعَلَ فِيكُمْ أَنْبِيٰاءَ وَ جَعَلَكُمْ مُلُوكاً وَ آتٰاكُمْ مٰا لَمْ يُؤْتِ أَحَداً مِنَ الْعٰالَمِينَ");
        sor.Ayaat.Add("يٰا قَوْمِ ادْخُلُوا الْأَرْضَ الْمُقَدَّسَةَ الَّتِي كَتَبَ اللّٰهُ لَكُمْ وَ لاٰ تَرْتَدُّوا عَلىٰ أَدْبٰارِكُمْ فَتَنْقَلِبُوا خٰاسِرِينَ");
        sor.Ayaat.Add("قٰالُوا يٰا مُوسىٰ إِنَّ فِيهٰا قَوْماً جَبّٰارِينَ وَ إِنّٰا لَنْ نَدْخُلَهٰا حَتّٰى يَخْرُجُوا مِنْهٰا فَإِنْ يَخْرُجُوا مِنْهٰا فَإِنّٰا دٰاخِلُونَ");
        sor.Ayaat.Add("قٰالَ رَجُلاٰنِ مِنَ الَّذِينَ يَخٰافُونَ أَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوا عَلَيْهِمُ الْبٰابَ فَإِذٰا دَخَلْتُمُوهُ فَإِنَّكُمْ غٰالِبُونَ وَ عَلَى اللّٰهِ فَتَوَكَّلُوا إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("قٰالُوا يٰا مُوسىٰ إِنّٰا لَنْ نَدْخُلَهٰا أَبَداً مٰا دٰامُوا فِيهٰا فَاذْهَبْ أَنْتَ وَ رَبُّكَ فَقٰاتِلاٰ إِنّٰا هٰاهُنٰا قٰاعِدُونَ");
        sor.Ayaat.Add("قٰالَ رَبِّ إِنِّي لاٰ أَمْلِكُ إِلاّٰ نَفْسِي وَ أَخِي فَافْرُقْ بَيْنَنٰا وَ بَيْنَ الْقَوْمِ الْفٰاسِقِينَ");
        sor.Ayaat.Add("قٰالَ فَإِنَّهٰا مُحَرَّمَةٌ عَلَيْهِمْ أَرْبَعِينَ سَنَةً يَتِيهُونَ فِي الْأَرْضِ فَلاٰ تَأْسَ عَلَى الْقَوْمِ الْفٰاسِقِينَ");
        sor.Ayaat.Add("وَ اتْلُ عَلَيْهِمْ نَبَأَ ابْنَيْ آدَمَ بِالْحَقِّ إِذْ قَرَّبٰا قُرْبٰاناً فَتُقُبِّلَ مِنْ أَحَدِهِمٰا وَ لَمْ يُتَقَبَّلْ مِنَ الْآخَرِ قٰالَ لَأَقْتُلَنَّكَ قٰالَ إِنَّمٰا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّقِينَ");
        sor.Ayaat.Add("لَئِنْ بَسَطْتَ إِلَيَّ يَدَكَ لِتَقْتُلَنِي مٰا أَنَا بِبٰاسِطٍ يَدِيَ إِلَيْكَ لِأَقْتُلَكَ إِنِّي أَخٰافُ اللّٰهَ رَبَّ الْعٰالَمِينَ");
        sor.Ayaat.Add("إِنِّي أُرِيدُ أَنْ تَبُوءَ بِإِثْمِي وَ إِثْمِكَ فَتَكُونَ مِنْ أَصْحٰابِ النّٰارِ وَ ذٰلِكَ جَزٰاءُ الظّٰالِمِينَ");
        sor.Ayaat.Add("فَطَوَّعَتْ لَهُ نَفْسُهُ قَتْلَ أَخِيهِ فَقَتَلَهُ فَأَصْبَحَ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("فَبَعَثَ اللّٰهُ غُرٰاباً يَبْحَثُ فِي الْأَرْضِ لِيُرِيَهُ كَيْفَ يُوٰارِي سَوْأَةَ أَخِيهِ قٰالَ يٰا وَيْلَتىٰ أَ عَجَزْتُ أَنْ أَكُونَ مِثْلَ هٰذَا الْغُرٰابِ فَأُوٰارِيَ سَوْأَةَ أَخِي فَأَصْبَحَ مِنَ النّٰادِمِينَ");
        sor.Ayaat.Add("مِنْ أَجْلِ ذٰلِكَ كَتَبْنٰا عَلىٰ بَنِي إِسْرٰائِيلَ أَنَّهُ مَنْ قَتَلَ نَفْساً بِغَيْرِ نَفْسٍ أَوْ فَسٰادٍ فِي الْأَرْضِ فَكَأَنَّمٰا قَتَلَ النّٰاسَ جَمِيعاً وَ مَنْ أَحْيٰاهٰا فَكَأَنَّمٰا أَحْيَا النّٰاسَ جَمِيعاً وَ لَقَدْ جٰاءَتْهُمْ رُسُلُنٰا بِالْبَيِّنٰاتِ ثُمَّ إِنَّ كَثِيراً مِنْهُمْ بَعْدَ ذٰلِكَ فِي الْأَرْضِ لَمُسْرِفُونَ");
        sor.Ayaat.Add("إِنَّمٰا جَزٰاءُ الَّذِينَ يُحٰارِبُونَ اللّٰهَ وَ رَسُولَهُ وَ يَسْعَوْنَ فِي الْأَرْضِ فَسٰاداً أَنْ يُقَتَّلُوا أَوْ يُصَلَّبُوا أَوْ تُقَطَّعَ أَيْدِيهِمْ وَ أَرْجُلُهُمْ مِنْ خِلاٰفٍ أَوْ يُنْفَوْا مِنَ الْأَرْضِ ذٰلِكَ لَهُمْ خِزْيٌ فِي الدُّنْيٰا وَ لَهُمْ فِي الْآخِرَةِ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ تٰابُوا مِنْ قَبْلِ أَنْ تَقْدِرُوا عَلَيْهِمْ فَاعْلَمُوا أَنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللّٰهَ وَ ابْتَغُوا إِلَيْهِ الْوَسِيلَةَ وَ جٰاهِدُوا فِي سَبِيلِهِ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا لَوْ أَنَّ لَهُمْ مٰا فِي الْأَرْضِ جَمِيعاً وَ مِثْلَهُ مَعَهُ لِيَفْتَدُوا بِهِ مِنْ عَذٰابِ يَوْمِ الْقِيٰامَةِ مٰا تُقُبِّلَ مِنْهُمْ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("يُرِيدُونَ أَنْ يَخْرُجُوا مِنَ النّٰارِ وَ مٰا هُمْ بِخٰارِجِينَ مِنْهٰا وَ لَهُمْ عَذٰابٌ مُقِيمٌ");
        sor.Ayaat.Add("وَ السّٰارِقُ وَ السّٰارِقَةُ فَاقْطَعُوا أَيْدِيَهُمٰا جَزٰاءً بِمٰا كَسَبٰا نَكٰالاً مِنَ اللّٰهِ وَ اللّٰهُ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("فَمَنْ تٰابَ مِنْ بَعْدِ ظُلْمِهِ وَ أَصْلَحَ فَإِنَّ اللّٰهَ يَتُوبُ عَلَيْهِ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("أَ لَمْ تَعْلَمْ أَنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ يُعَذِّبُ مَنْ يَشٰاءُ وَ يَغْفِرُ لِمَنْ يَشٰاءُ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الرَّسُولُ لاٰ يَحْزُنْكَ الَّذِينَ يُسٰارِعُونَ فِي الْكُفْرِ مِنَ الَّذِينَ قٰالُوا آمَنّٰا بِأَفْوٰاهِهِمْ وَ لَمْ تُؤْمِنْ قُلُوبُهُمْ وَ مِنَ الَّذِينَ هٰادُوا سَمّٰاعُونَ لِلْكَذِبِ سَمّٰاعُونَ لِقَوْمٍ آخَرِينَ لَمْ يَأْتُوكَ يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوٰاضِعِهِ يَقُولُونَ إِنْ أُوتِيتُمْ هٰذٰا فَخُذُوهُ وَ إِنْ لَمْ تُؤْتَوْهُ فَاحْذَرُوا وَ مَنْ يُرِدِ اللّٰهُ فِتْنَتَهُ فَلَنْ تَمْلِكَ لَهُ مِنَ اللّٰهِ شَيْئاً أُولٰئِكَ الَّذِينَ لَمْ يُرِدِ اللّٰهُ أَنْ يُطَهِّرَ قُلُوبَهُمْ لَهُمْ فِي الدُّنْيٰا خِزْيٌ وَ لَهُمْ فِي الْآخِرَةِ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("سَمّٰاعُونَ لِلْكَذِبِ أَكّٰالُونَ لِلسُّحْتِ فَإِنْ جٰاؤُكَ فَاحْكُمْ بَيْنَهُمْ أَوْ أَعْرِضْ عَنْهُمْ وَ إِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَضُرُّوكَ شَيْئاً وَ إِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِ إِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِينَ");
        sor.Ayaat.Add("وَ كَيْفَ يُحَكِّمُونَكَ وَ عِنْدَهُمُ التَّوْرٰاةُ فِيهٰا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْ بَعْدِ ذٰلِكَ وَ مٰا أُولٰئِكَ بِالْمُؤْمِنِينَ");
        sor.Ayaat.Add("إِنّٰا أَنْزَلْنَا التَّوْرٰاةَ فِيهٰا هُدىً وَ نُورٌ يَحْكُمُ بِهَا النَّبِيُّونَ الَّذِينَ أَسْلَمُوا لِلَّذِينَ هٰادُوا وَ الرَّبّٰانِيُّونَ وَ الْأَحْبٰارُ بِمَا اسْتُحْفِظُوا مِنْ كِتٰابِ اللّٰهِ وَ كٰانُوا عَلَيْهِ شُهَدٰاءَ فَلاٰ تَخْشَوُا النّٰاسَ وَ اخْشَوْنِ وَ لاٰ تَشْتَرُوا بِآيٰاتِي ثَمَناً قَلِيلاً وَ مَنْ لَمْ يَحْكُمْ بِمٰا أَنْزَلَ اللّٰهُ فَأُولٰئِكَ هُمُ الْكٰافِرُونَ");
        sor.Ayaat.Add("وَ كَتَبْنٰا عَلَيْهِمْ فِيهٰا أَنَّ النَّفْسَ بِالنَّفْسِ وَ الْعَيْنَ بِالْعَيْنِ وَ الْأَنْفَ بِالْأَنْفِ وَ الْأُذُنَ بِالْأُذُنِ وَ السِّنَّ بِالسِّنِّ وَ الْجُرُوحَ قِصٰاصٌ فَمَنْ تَصَدَّقَ بِهِ فَهُوَ كَفّٰارَةٌ لَهُ وَ مَنْ لَمْ يَحْكُمْ بِمٰا أَنْزَلَ اللّٰهُ فَأُولٰئِكَ هُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("وَ قَفَّيْنٰا عَلىٰ آثٰارِهِمْ بِعِيسَى ابْنِ مَرْيَمَ مُصَدِّقاً لِمٰا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰاةِ وَ آتَيْنٰاهُ الْإِنْجِيلَ فِيهِ هُدىً وَ نُورٌ وَ مُصَدِّقاً لِمٰا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰاةِ وَ هُدىً وَ مَوْعِظَةً لِلْمُتَّقِينَ");
        sor.Ayaat.Add("وَ لْيَحْكُمْ أَهْلُ الْإِنْجِيلِ بِمٰا أَنْزَلَ اللّٰهُ فِيهِ وَ مَنْ لَمْ يَحْكُمْ بِمٰا أَنْزَلَ اللّٰهُ فَأُولٰئِكَ هُمُ الْفٰاسِقُونَ");
        sor.Ayaat.Add("وَ أَنْزَلْنٰا إِلَيْكَ الْكِتٰابَ بِالْحَقِّ مُصَدِّقاً لِمٰا بَيْنَ يَدَيْهِ مِنَ الْكِتٰابِ وَ مُهَيْمِناً عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمٰا أَنْزَلَ اللّٰهُ وَ لاٰ تَتَّبِعْ أَهْوٰاءَهُمْ عَمّٰا جٰاءَكَ مِنَ الْحَقِّ لِكُلٍّ جَعَلْنٰا مِنْكُمْ شِرْعَةً وَ مِنْهٰاجاً وَ لَوْ شٰاءَ اللّٰهُ لَجَعَلَكُمْ أُمَّةً وٰاحِدَةً وَ لٰكِنْ لِيَبْلُوَكُمْ فِي مٰا آتٰاكُمْ فَاسْتَبِقُوا الْخَيْرٰاتِ إِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيعاً فَيُنَبِّئُكُمْ بِمٰا كُنْتُمْ فِيهِ تَخْتَلِفُونَ");
        sor.Ayaat.Add("وَ أَنِ احْكُمْ بَيْنَهُمْ بِمٰا أَنْزَلَ اللّٰهُ وَ لاٰ تَتَّبِعْ أَهْوٰاءَهُمْ وَ احْذَرْهُمْ أَنْ يَفْتِنُوكَ عَنْ بَعْضِ مٰا أَنْزَلَ اللّٰهُ إِلَيْكَ فَإِنْ تَوَلَّوْا فَاعْلَمْ أَنَّمٰا يُرِيدُ اللّٰهُ أَنْ يُصِيبَهُمْ بِبَعْضِ ذُنُوبِهِمْ وَ إِنَّ كَثِيراً مِنَ النّٰاسِ لَفٰاسِقُونَ");
        sor.Ayaat.Add("أَ فَحُكْمَ الْجٰاهِلِيَّةِ يَبْغُونَ وَ مَنْ أَحْسَنُ مِنَ اللّٰهِ حُكْماً لِقَوْمٍ يُوقِنُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَتَّخِذُوا الْيَهُودَ وَ النَّصٰارىٰ أَوْلِيٰاءَ بَعْضُهُمْ أَوْلِيٰاءُ بَعْضٍ وَ مَنْ يَتَوَلَّهُمْ مِنْكُمْ فَإِنَّهُ مِنْهُمْ إِنَّ اللّٰهَ لاٰ يَهْدِي الْقَوْمَ الظّٰالِمِينَ");
        sor.Ayaat.Add("فَتَرَى الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يُسٰارِعُونَ فِيهِمْ يَقُولُونَ نَخْشىٰ أَنْ تُصِيبَنٰا دٰائِرَةٌ فَعَسَى اللّٰهُ أَنْ يَأْتِيَ بِالْفَتْحِ أَوْ أَمْرٍ مِنْ عِنْدِهِ فَيُصْبِحُوا عَلىٰ مٰا أَسَرُّوا فِي أَنْفُسِهِمْ نٰادِمِينَ");
        sor.Ayaat.Add("وَ يَقُولُ الَّذِينَ آمَنُوا أَ هٰؤُلاٰءِ الَّذِينَ أَقْسَمُوا بِاللّٰهِ جَهْدَ أَيْمٰانِهِمْ إِنَّهُمْ لَمَعَكُمْ حَبِطَتْ أَعْمٰالُهُمْ فَأَصْبَحُوا خٰاسِرِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا مَنْ يَرْتَدَّ مِنْكُمْ عَنْ دِينِهِ فَسَوْفَ يَأْتِي اللّٰهُ بِقَوْمٍ يُحِبُّهُمْ وَ يُحِبُّونَهُ أَذِلَّةٍ عَلَى الْمُؤْمِنِينَ أَعِزَّةٍ عَلَى الْكٰافِرِينَ يُجٰاهِدُونَ فِي سَبِيلِ اللّٰهِ وَ لاٰ يَخٰافُونَ لَوْمَةَ لاٰئِمٍ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيهِ مَنْ يَشٰاءُ وَ اللّٰهُ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("إِنَّمٰا وَلِيُّكُمُ اللّٰهُ وَ رَسُولُهُ وَ الَّذِينَ آمَنُوا الَّذِينَ يُقِيمُونَ الصَّلاٰةَ وَ يُؤْتُونَ الزَّكٰاةَ وَ هُمْ رٰاكِعُونَ");
        sor.Ayaat.Add("وَ مَنْ يَتَوَلَّ اللّٰهَ وَ رَسُولَهُ وَ الَّذِينَ آمَنُوا فَإِنَّ حِزْبَ اللّٰهِ هُمُ الْغٰالِبُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَتَّخِذُوا الَّذِينَ اتَّخَذُوا دِينَكُمْ هُزُواً وَ لَعِباً مِنَ الَّذِينَ أُوتُوا الْكِتٰابَ مِنْ قَبْلِكُمْ وَ الْكُفّٰارَ أَوْلِيٰاءَ وَ اتَّقُوا اللّٰهَ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ إِذٰا نٰادَيْتُمْ إِلَى الصَّلاٰةِ اتَّخَذُوهٰا هُزُواً وَ لَعِباً ذٰلِكَ بِأَنَّهُمْ قَوْمٌ لاٰ يَعْقِلُونَ");
        sor.Ayaat.Add("قُلْ يٰا أَهْلَ الْكِتٰابِ هَلْ تَنْقِمُونَ مِنّٰا إِلاّٰ أَنْ آمَنّٰا بِاللّٰهِ وَ مٰا أُنْزِلَ إِلَيْنٰا وَ مٰا أُنْزِلَ مِنْ قَبْلُ وَ أَنَّ أَكْثَرَكُمْ فٰاسِقُونَ");
        sor.Ayaat.Add("قُلْ هَلْ أُنَبِّئُكُمْ بِشَرٍّ مِنْ ذٰلِكَ مَثُوبَةً عِنْدَ اللّٰهِ مَنْ لَعَنَهُ اللّٰهُ وَ غَضِبَ عَلَيْهِ وَ جَعَلَ مِنْهُمُ الْقِرَدَةَ وَ الْخَنٰازِيرَ وَ عَبَدَ الطّٰاغُوتَ أُولٰئِكَ شَرٌّ مَكٰاناً وَ أَضَلُّ عَنْ سَوٰاءِ السَّبِيلِ");
        sor.Ayaat.Add("وَ إِذٰا جٰاؤُكُمْ قٰالُوا آمَنّٰا وَ قَدْ دَخَلُوا بِالْكُفْرِ وَ هُمْ قَدْ خَرَجُوا بِهِ وَ اللّٰهُ أَعْلَمُ بِمٰا كٰانُوا يَكْتُمُونَ");
        sor.Ayaat.Add("وَ تَرىٰ كَثِيراً مِنْهُمْ يُسٰارِعُونَ فِي الْإِثْمِ وَ الْعُدْوٰانِ وَ أَكْلِهِمُ السُّحْتَ لَبِئْسَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("لَوْ لاٰ يَنْهٰاهُمُ الرَّبّٰانِيُّونَ وَ الْأَحْبٰارُ عَنْ قَوْلِهِمُ الْإِثْمَ وَ أَكْلِهِمُ السُّحْتَ لَبِئْسَ مٰا كٰانُوا يَصْنَعُونَ");
        sor.Ayaat.Add("وَ قٰالَتِ الْيَهُودُ يَدُ اللّٰهِ مَغْلُولَةٌ غُلَّتْ أَيْدِيهِمْ وَ لُعِنُوا بِمٰا قٰالُوا بَلْ يَدٰاهُ مَبْسُوطَتٰانِ يُنْفِقُ كَيْفَ يَشٰاءُ وَ لَيَزِيدَنَّ كَثِيراً مِنْهُمْ مٰا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْيٰاناً وَ كُفْراً وَ أَلْقَيْنٰا بَيْنَهُمُ الْعَدٰاوَةَ وَ الْبَغْضٰاءَ إِلىٰ يَوْمِ الْقِيٰامَةِ كُلَّمٰا أَوْقَدُوا نٰاراً لِلْحَرْبِ أَطْفَأَهَا اللّٰهُ وَ يَسْعَوْنَ فِي الْأَرْضِ فَسٰاداً وَ اللّٰهُ لاٰ يُحِبُّ الْمُفْسِدِينَ");
        sor.Ayaat.Add("وَ لَوْ أَنَّ أَهْلَ الْكِتٰابِ آمَنُوا وَ اتَّقَوْا لَكَفَّرْنٰا عَنْهُمْ سَيِّئٰاتِهِمْ وَ لَأَدْخَلْنٰاهُمْ جَنّٰاتِ النَّعِيمِ");
        sor.Ayaat.Add("وَ لَوْ أَنَّهُمْ أَقٰامُوا التَّوْرٰاةَ وَ الْإِنْجِيلَ وَ مٰا أُنْزِلَ إِلَيْهِمْ مِنْ رَبِّهِمْ لَأَكَلُوا مِنْ فَوْقِهِمْ وَ مِنْ تَحْتِ أَرْجُلِهِمْ مِنْهُمْ أُمَّةٌ مُقْتَصِدَةٌ وَ كَثِيرٌ مِنْهُمْ سٰاءَ مٰا يَعْمَلُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الرَّسُولُ بَلِّغْ مٰا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ وَ إِنْ لَمْ تَفْعَلْ فَمٰا بَلَّغْتَ رِسٰالَتَهُ وَ اللّٰهُ يَعْصِمُكَ مِنَ النّٰاسِ إِنَّ اللّٰهَ لاٰ يَهْدِي الْقَوْمَ الْكٰافِرِينَ");
        sor.Ayaat.Add("قُلْ يٰا أَهْلَ الْكِتٰابِ لَسْتُمْ عَلىٰ شَيْءٍ حَتّٰى تُقِيمُوا التَّوْرٰاةَ وَ الْإِنْجِيلَ وَ مٰا أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ وَ لَيَزِيدَنَّ كَثِيراً مِنْهُمْ مٰا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ طُغْيٰاناً وَ كُفْراً فَلاٰ تَأْسَ عَلَى الْقَوْمِ الْكٰافِرِينَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هٰادُوا وَ الصّٰابِئُونَ وَ النَّصٰارىٰ مَنْ آمَنَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ عَمِلَ صٰالِحاً فَلاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("لَقَدْ أَخَذْنٰا مِيثٰاقَ بَنِي إِسْرٰائِيلَ وَ أَرْسَلْنٰا إِلَيْهِمْ رُسُلاً كُلَّمٰا جٰاءَهُمْ رَسُولٌ بِمٰا لاٰ تَهْوىٰ أَنْفُسُهُمْ فَرِيقاً كَذَّبُوا وَ فَرِيقاً يَقْتُلُونَ");
        sor.Ayaat.Add("وَ حَسِبُوا أَلاّٰ تَكُونَ فِتْنَةٌ فَعَمُوا وَ صَمُّوا ثُمَّ تٰابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوا وَ صَمُّوا كَثِيرٌ مِنْهُمْ وَ اللّٰهُ بَصِيرٌ بِمٰا يَعْمَلُونَ");
        sor.Ayaat.Add("لَقَدْ كَفَرَ الَّذِينَ قٰالُوا إِنَّ اللّٰهَ هُوَ الْمَسِيحُ ابْنُ مَرْيَمَ وَ قٰالَ الْمَسِيحُ يٰا بَنِي إِسْرٰائِيلَ اعْبُدُوا اللّٰهَ رَبِّي وَ رَبَّكُمْ إِنَّهُ مَنْ يُشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَ مَأْوٰاهُ النّٰارُ وَ مٰا لِلظّٰالِمِينَ مِنْ أَنْصٰارٍ");
        sor.Ayaat.Add("لَقَدْ كَفَرَ الَّذِينَ قٰالُوا إِنَّ اللّٰهَ ثٰالِثُ ثَلاٰثَةٍ وَ مٰا مِنْ إِلٰهٍ إِلاّٰ إِلٰهٌ وٰاحِدٌ وَ إِنْ لَمْ يَنْتَهُوا عَمّٰا يَقُولُونَ لَيَمَسَّنَّ الَّذِينَ كَفَرُوا مِنْهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("أَ فَلاٰ يَتُوبُونَ إِلَى اللّٰهِ وَ يَسْتَغْفِرُونَهُ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("مَا الْمَسِيحُ ابْنُ مَرْيَمَ إِلاّٰ رَسُولٌ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ وَ أُمُّهُ صِدِّيقَةٌ كٰانٰا يَأْكُلاٰنِ الطَّعٰامَ انْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْآيٰاتِ ثُمَّ انْظُرْ أَنّٰى يُؤْفَكُونَ");
        sor.Ayaat.Add("قُلْ أَ تَعْبُدُونَ مِنْ دُونِ اللّٰهِ مٰا لاٰ يَمْلِكُ لَكُمْ ضَرًّا وَ لاٰ نَفْعاً وَ اللّٰهُ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("قُلْ يٰا أَهْلَ الْكِتٰابِ لاٰ تَغْلُوا فِي دِينِكُمْ غَيْرَ الْحَقِّ وَ لاٰ تَتَّبِعُوا أَهْوٰاءَ قَوْمٍ قَدْ ضَلُّوا مِنْ قَبْلُ وَ أَضَلُّوا كَثِيراً وَ ضَلُّوا عَنْ سَوٰاءِ السَّبِيلِ");
        sor.Ayaat.Add("لُعِنَ الَّذِينَ كَفَرُوا مِنْ بَنِي إِسْرٰائِيلَ عَلىٰ لِسٰانِ دٰاوُدَ وَ عِيسَى ابْنِ مَرْيَمَ ذٰلِكَ بِمٰا عَصَوْا وَ كٰانُوا يَعْتَدُونَ");
        sor.Ayaat.Add("كٰانُوا لاٰ يَتَنٰاهَوْنَ عَنْ مُنكَرٍ فَعَلُوهُ لَبِئْسَ مٰا كٰانُوا يَفْعَلُونَ");
        sor.Ayaat.Add("تَرىٰ كَثِيراً مِنْهُمْ يَتَوَلَّوْنَ الَّذِينَ كَفَرُوا لَبِئْسَ مٰا قَدَّمَتْ لَهُمْ أَنْفُسُهُمْ أَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَ فِي الْعَذٰابِ هُمْ خٰالِدُونَ");
        sor.Ayaat.Add("وَ لَوْ كٰانُوا يُؤْمِنُونَ بِاللّٰهِ وَ النَّبِيِّ وَ مٰا أُنْزِلَ إِلَيْهِ مَا اتَّخَذُوهُمْ أَوْلِيٰاءَ وَ لٰكِنَّ كَثِيراً مِنْهُمْ فٰاسِقُونَ");
        sor.Ayaat.Add("لَتَجِدَنَّ أَشَدَّ النّٰاسِ عَدٰاوَةً لِلَّذِينَ آمَنُوا الْيَهُودَ وَ الَّذِينَ أَشْرَكُوا وَ لَتَجِدَنَّ أَقْرَبَهُمْ مَوَدَّةً لِلَّذِينَ آمَنُوا الَّذِينَ قٰالُوا إِنّٰا نَصٰارىٰ ذٰلِكَ بِأَنَّ مِنْهُمْ قِسِّيسِينَ وَ رُهْبٰاناً وَ أَنَّهُمْ لاٰ يَسْتَكْبِرُونَ");
        sor.Ayaat.Add("وَ إِذٰا سَمِعُوا مٰا أُنْزِلَ إِلَى الرَّسُولِ تَرىٰ أَعْيُنَهُمْ تَفِيضُ مِنَ الدَّمْعِ مِمّٰا عَرَفُوا مِنَ الْحَقِّ يَقُولُونَ رَبَّنٰا آمَنّٰا فَاكْتُبْنٰا مَعَ الشّٰاهِدِينَ");
        sor.Ayaat.Add("وَ مٰا لَنٰا لاٰ نُؤْمِنُ بِاللّٰهِ وَ مٰا جٰاءَنٰا مِنَ الْحَقِّ وَ نَطْمَعُ أَنْ يُدْخِلَنٰا رَبُّنٰا مَعَ الْقَوْمِ الصّٰالِحِينَ");
        sor.Ayaat.Add("فَأَثٰابَهُمُ اللّٰهُ بِمٰا قٰالُوا جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا وَ ذٰلِكَ جَزٰاءُ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآيٰاتِنٰا أُولٰئِكَ أَصْحٰابُ الْجَحِيمِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تُحَرِّمُوا طَيِّبٰاتِ مٰا أَحَلَّ اللّٰهُ لَكُمْ وَ لاٰ تَعْتَدُوا إِنَّ اللّٰهَ لاٰ يُحِبُّ الْمُعْتَدِينَ");
        sor.Ayaat.Add("وَ كُلُوا مِمّٰا رَزَقَكُمُ اللّٰهُ حَلاٰلاً طَيِّباً وَ اتَّقُوا اللّٰهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ");
        sor.Ayaat.Add("لاٰ يُؤٰاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِي أَيْمٰانِكُمْ وَ لٰكِنْ يُؤٰاخِذُكُمْ بِمٰا عَقَّدْتُمُ الْأَيْمٰانَ فَكَفّٰارَتُهُ إِطْعٰامُ عَشَرَةِ مَسٰاكِينَ مِنْ أَوْسَطِ مٰا تُطْعِمُونَ أَهْلِيكُمْ أَوْ كِسْوَتُهُمْ أَوْ تَحْرِيرُ رَقَبَةٍ فَمَنْ لَمْ يَجِدْ فَصِيٰامُ ثَلاٰثَةِ أَيّٰامٍ ذٰلِكَ كَفّٰارَةُ أَيْمٰانِكُمْ إِذٰا حَلَفْتُمْ وَ احْفَظُوا أَيْمٰانَكُمْ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ آيٰاتِهِ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْخَمْرُ وَ الْمَيْسِرُ وَ الْأَنْصٰابُ وَ الْأَزْلاٰمُ رِجْسٌ مِنْ عَمَلِ الشَّيْطٰانِ فَاجْتَنِبُوهُ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("إِنَّمٰا يُرِيدُ الشَّيْطٰانُ أَنْ يُوقِعَ بَيْنَكُمُ الْعَدٰاوَةَ وَ الْبَغْضٰاءَ فِي الْخَمْرِ وَ الْمَيْسِرِ وَ يَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَ عَنِ الصَّلاٰةِ فَهَلْ أَنْتُمْ مُنْتَهُونَ");
        sor.Ayaat.Add("وَ أَطِيعُوا اللّٰهَ وَ أَطِيعُوا الرَّسُولَ وَ احْذَرُوا فَإِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّمٰا عَلىٰ رَسُولِنَا الْبَلاٰغُ الْمُبِينُ");
        sor.Ayaat.Add("لَيْسَ عَلَى الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ جُنٰاحٌ فِيمٰا طَعِمُوا إِذٰا مَا اتَّقَوْا وَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ ثُمَّ اتَّقَوْا وَ آمَنُوا ثُمَّ اتَّقَوْا وَ أَحْسَنُوا وَ اللّٰهُ يُحِبُّ الْمُحْسِنِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِنَ الصَّيْدِ تَنٰالُهُ أَيْدِيكُمْ وَ رِمٰاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَخٰافُهُ بِالْغَيْبِ فَمَنِ اعْتَدىٰ بَعْدَ ذٰلِكَ فَلَهُ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَقْتُلُوا الصَّيْدَ وَ أَنْتُمْ حُرُمٌ وَ مَنْ قَتَلَهُ مِنْكُمْ مُتَعَمِّداً فَجَزٰاءٌ مِثْلُ مٰا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهِ ذَوٰا عَدْلٍ مِنْكُمْ هَدْياً بٰالِغَ الْكَعْبَةِ أَوْ كَفّٰارَةٌ طَعٰامُ مَسٰاكِينَ أَوْ عَدْلُ ذٰلِكَ صِيٰاماً لِيَذُوقَ وَبٰالَ أَمْرِهِ عَفَا اللّٰهُ عَمّٰا سَلَفَ وَ مَنْ عٰادَ فَيَنْتَقِمُ اللّٰهُ مِنْهُ وَ اللّٰهُ عَزِيزٌ ذُو انْتِقٰامٍ");
        sor.Ayaat.Add("أُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَ طَعٰامُهُ مَتٰاعاً لَكُمْ وَ لِلسَّيّٰارَةِ وَ حُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مٰا دُمْتُمْ حُرُماً وَ اتَّقُوا اللّٰهَ الَّذِي إِلَيْهِ تُحْشَرُونَ");
        sor.Ayaat.Add("جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرٰامَ قِيٰاماً لِلنّٰاسِ وَ الشَّهْرَ الْحَرٰامَ وَ الْهَدْيَ وَ الْقَلاٰئِدَ ذٰلِكَ لِتَعْلَمُوا أَنَّ اللّٰهَ يَعْلَمُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ أَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("اعْلَمُوا أَنَّ اللّٰهَ شَدِيدُ الْعِقٰابِ وَ أَنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("مٰا عَلَى الرَّسُولِ إِلاَّ الْبَلاٰغُ وَ اللّٰهُ يَعْلَمُ مٰا تُبْدُونَ وَ مٰا تَكْتُمُونَ");
        sor.Ayaat.Add("قُلْ لاٰ يَسْتَوِي الْخَبِيثُ وَ الطَّيِّبُ وَ لَوْ أَعْجَبَكَ كَثْرَةُ الْخَبِيثِ فَاتَّقُوا اللّٰهَ يٰا أُولِي الْأَلْبٰابِ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَسْئَلُوا عَنْ أَشْيٰاءَ إِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ وَ إِنْ تَسْئَلُوا عَنْهٰا حِينَ يُنَزَّلُ الْقُرْآنُ تُبْدَ لَكُمْ عَفَا اللّٰهُ عَنْهٰا وَ اللّٰهُ غَفُورٌ حَلِيمٌ");
        sor.Ayaat.Add("قَدْ سَأَلَهٰا قَوْمٌ مِنْ قَبْلِكُمْ ثُمَّ أَصْبَحُوا بِهٰا كٰافِرِينَ");
        sor.Ayaat.Add("مٰا جَعَلَ اللّٰهُ مِنْ بَحِيرَةٍ وَ لاٰ سٰائِبَةٍ وَ لاٰ وَصِيلَةٍ وَ لاٰ حٰامٍ وَ لٰكِنَّ الَّذِينَ كَفَرُوا يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ وَ أَكْثَرُهُمْ لاٰ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمْ تَعٰالَوْا إِلىٰ مٰا أَنْزَلَ اللّٰهُ وَ إِلَى الرَّسُولِ قٰالُوا حَسْبُنٰا مٰا وَجَدْنٰا عَلَيْهِ آبٰاءَنٰا أَ وَ لَوْ كٰانَ آبٰاؤُهُمْ لاٰ يَعْلَمُونَ شَيْئاً وَ لاٰ يَهْتَدُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا عَلَيْكُمْ أَنْفُسَكُمْ لاٰ يَضُرُّكُمْ مَنْ ضَلَّ إِذَا اهْتَدَيْتُمْ إِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيعاً فَيُنَبِّئُكُمْ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا شَهٰادَةُ بَيْنِكُمْ إِذٰا حَضَرَ أَحَدَكُمُ الْمَوْتُ حِينَ الْوَصِيَّةِ اثْنٰانِ ذَوٰا عَدْلٍ مِنْكُمْ أَوْ آخَرٰانِ مِنْ غَيْرِكُمْ إِنْ أَنْتُمْ ضَرَبْتُمْ فِي الْأَرْضِ فَأَصٰابَتْكُمْ مُصِيبَةُ الْمَوْتِ تَحْبِسُونَهُمٰا مِنْ بَعْدِ الصَّلاٰةِ فَيُقْسِمٰانِ بِاللّٰهِ إِنِ ارْتَبْتُمْ لاٰ نَشْتَرِي بِهِ ثَمَناً وَ لَوْ كٰانَ ذٰا قُرْبىٰ وَ لاٰ نَكْتُمُ شَهٰادَةَ اللّٰهِ إِنّٰا إِذاً لَمِنَ الْآثِمِينَ");
        sor.Ayaat.Add("فَإِنْ عُثِرَ عَلىٰ أَنَّهُمَا اسْتَحَقّٰا إِثْماً فَآخَرٰانِ يَقُومٰانِ مَقٰامَهُمٰا مِنَ الَّذِينَ اسْتَحَقَّ عَلَيْهِمُ الْأَوْلَيٰانِ فَيُقْسِمٰانِ بِاللّٰهِ لَشَهٰادَتُنٰا أَحَقُّ مِنْ شَهٰادَتِهِمٰا وَ مَا اعْتَدَيْنٰا إِنّٰا إِذاً لَمِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("ذٰلِكَ أَدْنىٰ أَنْ يَأْتُوا بِالشَّهٰادَةِ عَلىٰ وَجْهِهٰا أَوْ يَخٰافُوا أَنْ تُرَدَّ أَيْمٰانٌ بَعْدَ أَيْمٰانِهِمْ وَ اتَّقُوا اللّٰهَ وَ اسْمَعُوا وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الْفٰاسِقِينَ");
        sor.Ayaat.Add("يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُولُ مٰاذٰا أُجِبْتُمْ قٰالُوا لاٰ عِلْمَ لَنٰا إِنَّكَ أَنْتَ عَلاّٰمُ الْغُيُوبِ");
        sor.Ayaat.Add("إِذْ قٰالَ اللّٰهُ يٰا عِيسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِي عَلَيْكَ وَ عَلىٰ وٰالِدَتِكَ إِذْ أَيَّدْتُكَ بِرُوحِ الْقُدُسِ تُكَلِّمُ النّٰاسَ فِي الْمَهْدِ وَ كَهْلاً وَ إِذْ عَلَّمْتُكَ الْكِتٰابَ وَ الْحِكْمَةَ وَ التَّوْرٰاةَ وَ الْإِنْجِيلَ وَ إِذْ تَخْلُقُ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ بِإِذْنِي فَتَنْفُخُ فِيهٰا فَتَكُونُ طَيْراً بِإِذْنِي وَ تُبْرِئُ الْأَكْمَهَ وَ الْأَبْرَصَ بِإِذْنِي وَ إِذْ تُخْرِجُ الْمَوْتىٰ بِإِذْنِي وَ إِذْ كَفَفْتُ بَنِي إِسْرٰائِيلَ عَنْكَ إِذْ جِئْتَهُمْ بِالْبَيِّنٰاتِ فَقٰالَ الَّذِينَ كَفَرُوا مِنْهُمْ إِنْ هٰذٰا إِلاّٰ سِحْرٌ مُبِينٌ");
        sor.Ayaat.Add("وَ إِذْ أَوْحَيْتُ إِلَى الْحَوٰارِيِّينَ أَنْ آمِنُوا بِي وَ بِرَسُولِي قٰالُوا آمَنّٰا وَ اشْهَدْ بِأَنَّنٰا مُسْلِمُونَ");
        sor.Ayaat.Add("إِذْ قٰالَ الْحَوٰارِيُّونَ يٰا عِيسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيعُ رَبُّكَ أَنْ يُنَزِّلَ عَلَيْنٰا مٰائِدَةً مِنَ السَّمٰاءِ قٰالَ اتَّقُوا اللّٰهَ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("قٰالُوا نُرِيدُ أَنْ نَأْكُلَ مِنْهٰا وَ تَطْمَئِنَّ قُلُوبُنٰا وَ نَعْلَمَ أَنْ قَدْ صَدَقْتَنٰا وَ نَكُونَ عَلَيْهٰا مِنَ الشّٰاهِدِينَ");
        sor.Ayaat.Add("قٰالَ عِيسَى ابْنُ مَرْيَمَ اللّٰهُمَّ رَبَّنٰا أَنْزِلْ عَلَيْنٰا مٰائِدَةً مِنَ السَّمٰاءِ تَكُونُ لَنٰا عِيداً لِأَوَّلِنٰا وَ آخِرِنٰا وَ آيَةً مِنْكَ وَ ارْزُقْنٰا وَ أَنْتَ خَيْرُ الرّٰازِقِينَ");
        sor.Ayaat.Add("قٰالَ اللّٰهُ إِنِّي مُنَزِّلُهٰا عَلَيْكُمْ فَمَنْ يَكْفُرْ بَعْدُ مِنْكُمْ فَإِنِّي أُعَذِّبُهُ عَذٰاباً لاٰ أُعَذِّبُهُ أَحَداً مِنَ الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ اللّٰهُ يٰا عِيسَى ابْنَ مَرْيَمَ أَ أَنْتَ قُلْتَ لِلنّٰاسِ اتَّخِذُونِي وَ أُمِّي إِلٰهَيْنِ مِنْ دُونِ اللّٰهِ قٰالَ سُبْحٰانَكَ مٰا يَكُونُ لِي أَنْ أَقُولَ مٰا لَيْسَ لِي بِحَقٍّ إِنْ كُنْتُ قُلْتُهُ فَقَدْ عَلِمْتَهُ تَعْلَمُ مٰا فِي نَفْسِي وَ لاٰ أَعْلَمُ مٰا فِي نَفْسِكَ إِنَّكَ أَنْتَ عَلاّٰمُ الْغُيُوبِ");
        sor.Ayaat.Add("مٰا قُلْتُ لَهُمْ إِلاّٰ مٰا أَمَرْتَنِي بِهِ أَنِ اعْبُدُوا اللّٰهَ رَبِّي وَ رَبَّكُمْ وَ كُنْتُ عَلَيْهِمْ شَهِيداً مٰا دُمْتُ فِيهِمْ فَلَمّٰا تَوَفَّيْتَنِي كُنْتَ أَنْتَ الرَّقِيبَ عَلَيْهِمْ وَ أَنْتَ عَلىٰ كُلِّ شَيْءٍ شَهِيدٌ");
        sor.Ayaat.Add("إِنْ تُعَذِّبْهُمْ فَإِنَّهُمْ عِبٰادُكَ وَ إِنْ تَغْفِرْ لَهُمْ فَإِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("قٰالَ اللّٰهُ هٰذٰا يَوْمُ يَنْفَعُ الصّٰادِقِينَ صِدْقُهُمْ لَهُمْ جَنّٰاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا أَبَداً رَضِيَ اللّٰهُ عَنْهُمْ وَ رَضُوا عَنْهُ ذٰلِكَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("لِلّٰهِ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا فِيهِنَّ وَ هُوَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        salam(sor);
        sor = new Soore();
        sor.Name = "۶ - الانعام";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الْحَمْدُ لِلّٰهِ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ وَ جَعَلَ الظُّلُمٰاتِ وَ النُّورَ ثُمَّ الَّذِينَ كَفَرُوا بِرَبِّهِمْ يَعْدِلُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي خَلَقَكُمْ مِنْ طِينٍ ثُمَّ قَضىٰ أَجَلاً وَ أَجَلٌ مُسَمًّى عِنْدَهُ ثُمَّ أَنْتُمْ تَمْتَرُونَ");
        sor.Ayaat.Add("وَ هُوَ اللّٰهُ فِي السَّمٰاوٰاتِ وَ فِي الْأَرْضِ يَعْلَمُ سِرَّكُمْ وَ جَهْرَكُمْ وَ يَعْلَمُ مٰا تَكْسِبُونَ");
        sor.Ayaat.Add("وَ مٰا تَأْتِيهِمْ مِنْ آيَةٍ مِنْ آيٰاتِ رَبِّهِمْ إِلاّٰ كٰانُوا عَنْهٰا مُعْرِضِينَ");
        sor.Ayaat.Add("فَقَدْ كَذَّبُوا بِالْحَقِّ لَمّٰا جٰاءَهُمْ فَسَوْفَ يَأْتِيهِمْ أَنْبٰاءُ مٰا كٰانُوا بِهِ يَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("أَ لَمْ يَرَوْا كَمْ أَهْلَكْنٰا مِنْ قَبْلِهِمْ مِنْ قَرْنٍ مَكَّنّٰاهُمْ فِي الْأَرْضِ مٰا لَمْ نُمَكِّنْ لَكُمْ وَ أَرْسَلْنَا السَّمٰاءَ عَلَيْهِمْ مِدْرٰاراً وَ جَعَلْنَا الْأَنْهٰارَ تَجْرِي مِنْ تَحْتِهِمْ فَأَهْلَكْنٰاهُمْ بِذُنُوبِهِمْ وَ أَنْشَأْنٰا مِنْ بَعْدِهِمْ قَرْناً آخَرِينَ");
        sor.Ayaat.Add("وَ لَوْ نَزَّلْنٰا عَلَيْكَ كِتٰاباً فِي قِرْطٰاسٍ فَلَمَسُوهُ بِأَيْدِيهِمْ لَقٰالَ الَّذِينَ كَفَرُوا إِنْ هٰذٰا إِلاّٰ سِحْرٌ مُبِينٌ");
        sor.Ayaat.Add("وَ قٰالُوا لَوْ لاٰ أُنْزِلَ عَلَيْهِ مَلَكٌ وَ لَوْ أَنْزَلْنٰا مَلَكاً لَقُضِيَ الْأَمْرُ ثُمَّ لاٰ يُنْظَرُونَ");
        sor.Ayaat.Add("وَ لَوْ جَعَلْنٰاهُ مَلَكاً لَجَعَلْنٰاهُ رَجُلاً وَ لَلَبَسْنٰا عَلَيْهِمْ مٰا يَلْبِسُونَ");
        sor.Ayaat.Add("وَ لَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحٰاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ مٰا كٰانُوا بِهِ يَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("قُلْ سِيرُوا فِي الْأَرْضِ ثُمَّ انْظُرُوا كَيْفَ كٰانَ عٰاقِبَةُ الْمُكَذِّبِينَ");
        sor.Ayaat.Add("قُلْ لِمَنْ مٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ قُلْ لِلّٰهِ كَتَبَ عَلىٰ نَفْسِهِ الرَّحْمَةَ لَيَجْمَعَنَّكُمْ إِلىٰ يَوْمِ الْقِيٰامَةِ لاٰ رَيْبَ فِيهِ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ لَهُ مٰا سَكَنَ فِي اللَّيْلِ وَ النَّهٰارِ وَ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("قُلْ أَ غَيْرَ اللّٰهِ أَتَّخِذُ وَلِيًّا فٰاطِرِ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ هُوَ يُطْعِمُ وَ لاٰ يُطْعَمُ قُلْ إِنِّي أُمِرْتُ أَنْ أَكُونَ أَوَّلَ مَنْ أَسْلَمَ وَ لاٰ تَكُونَنَّ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("قُلْ إِنِّي أَخٰافُ إِنْ عَصَيْتُ رَبِّي عَذٰابَ يَوْمٍ عَظِيمٍ");
        sor.Ayaat.Add("مَنْ يُصْرَفْ عَنْهُ يَوْمَئِذٍ فَقَدْ رَحِمَهُ وَ ذٰلِكَ الْفَوْزُ الْمُبِينُ");
        sor.Ayaat.Add("وَ إِنْ يَمْسَسْكَ اللّٰهُ بِضُرٍّ فَلاٰ كٰاشِفَ لَهُ إِلاّٰ هُوَ وَ إِنْ يَمْسَسْكَ بِخَيْرٍ فَهُوَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ هُوَ الْقٰاهِرُ فَوْقَ عِبٰادِهِ وَ هُوَ الْحَكِيمُ الْخَبِيرُ");
        sor.Ayaat.Add("قُلْ أَيُّ شَيْءٍ أَكْبَرُ شَهٰادَةً قُلِ اللّٰهُ شَهِيدٌ بَيْنِي وَ بَيْنَكُمْ وَ أُوحِيَ إِلَيَّ هٰذَا الْقُرْآنُ لِأُنْذِرَكُمْ بِهِ وَ مَنْ بَلَغَ أَ إِنَّكُمْ لَتَشْهَدُونَ أَنَّ مَعَ اللّٰهِ آلِهَةً أُخْرىٰ قُلْ لاٰ أَشْهَدُ قُلْ إِنَّمٰا هُوَ إِلٰهٌ وٰاحِدٌ وَ إِنَّنِي بَرِيءٌ مِمّٰا تُشْرِكُونَ");
        sor.Ayaat.Add("الَّذِينَ آتَيْنٰاهُمُ الْكِتٰابَ يَعْرِفُونَهُ كَمٰا يَعْرِفُونَ أَبْنٰاءَهُمُ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فَهُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً أَوْ كَذَّبَ بِآيٰاتِهِ إِنَّهُ لاٰ يُفْلِحُ الظّٰالِمُونَ");
        sor.Ayaat.Add("وَ يَوْمَ نَحْشُرُهُمْ جَمِيعاً ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا أَيْنَ شُرَكٰاؤُكُمُ الَّذِينَ كُنْتُمْ تَزْعُمُونَ");
        sor.Ayaat.Add("ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ إِلاّٰ أَنْ قٰالُوا وَ اللّٰهِ رَبِّنٰا مٰا كُنّٰا مُشْرِكِينَ");
        sor.Ayaat.Add("انْظُرْ كَيْفَ كَذَبُوا عَلىٰ أَنْفُسِهِمْ وَ ضَلَّ عَنْهُمْ مٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يَسْتَمِعُ إِلَيْكَ وَ جَعَلْنٰا عَلىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَ فِي آذٰانِهِمْ وَقْراً وَ إِنْ يَرَوْا كُلَّ آيَةٍ لاٰ يُؤْمِنُوا بِهٰا حَتّٰى إِذٰا جٰاؤُكَ يُجٰادِلُونَكَ يَقُولُ الَّذِينَ كَفَرُوا إِنْ هٰذٰا إِلاّٰ أَسٰاطِيرُ الْأَوَّلِينَ");
        sor.Ayaat.Add("وَ هُمْ يَنْهَوْنَ عَنْهُ وَ يَنْأَوْنَ عَنْهُ وَ إِنْ يُهْلِكُونَ إِلاّٰ أَنْفُسَهُمْ وَ مٰا يَشْعُرُونَ");
        sor.Ayaat.Add("وَ لَوْ تَرىٰ إِذْ وُقِفُوا عَلَى النّٰارِ فَقٰالُوا يٰا لَيْتَنٰا نُرَدُّ وَ لاٰ نُكَذِّبَ بِآيٰاتِ رَبِّنٰا وَ نَكُونَ مِنَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("بَلْ بَدٰا لَهُمْ مٰا كٰانُوا يُخْفُونَ مِنْ قَبْلُ وَ لَوْ رُدُّوا لَعٰادُوا لِمٰا نُهُوا عَنْهُ وَ إِنَّهُمْ لَكٰاذِبُونَ");
        sor.Ayaat.Add("وَ قٰالُوا إِنْ هِيَ إِلاّٰ حَيٰاتُنَا الدُّنْيٰا وَ مٰا نَحْنُ بِمَبْعُوثِينَ");
        sor.Ayaat.Add("وَ لَوْ تَرىٰ إِذْ وُقِفُوا عَلىٰ رَبِّهِمْ قٰالَ أَ لَيْسَ هٰذٰا بِالْحَقِّ قٰالُوا بَلىٰ وَ رَبِّنٰا قٰالَ فَذُوقُوا الْعَذٰابَ بِمٰا كُنْتُمْ تَكْفُرُونَ");
        sor.Ayaat.Add("قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقٰاءِ اللّٰهِ حَتّٰى إِذٰا جٰاءَتْهُمُ السّٰاعَةُ بَغْتَةً قٰالُوا يٰا حَسْرَتَنٰا عَلىٰ مٰا فَرَّطْنٰا فِيهٰا وَ هُمْ يَحْمِلُونَ أَوْزٰارَهُمْ عَلىٰ ظُهُورِهِمْ أَلاٰ سٰاءَ مٰا يَزِرُونَ");
        sor.Ayaat.Add("وَ مَا الْحَيٰاةُ الدُّنْيٰا إِلاّٰ لَعِبٌ وَ لَهْوٌ وَ لَلدّٰارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("قَدْ نَعْلَمُ إِنَّهُ لَيَحْزُنُكَ الَّذِي يَقُولُونَ فَإِنَّهُمْ لاٰ يُكَذِّبُونَكَ وَ لٰكِنَّ الظّٰالِمِينَ بِآيٰاتِ اللّٰهِ يَجْحَدُونَ");
        sor.Ayaat.Add("وَ لَقَدْ كُذِّبَتْ رُسُلٌ مِنْ قَبْلِكَ فَصَبَرُوا عَلىٰ مٰا كُذِّبُوا وَ أُوذُوا حَتّٰى أَتٰاهُمْ نَصْرُنٰا وَ لاٰ مُبَدِّلَ لِكَلِمٰاتِ اللّٰهِ وَ لَقَدْ جٰاءَكَ مِنْ نَبَإِ الْمُرْسَلِينَ");
        sor.Ayaat.Add("وَ إِنْ كٰانَ كَبُرَ عَلَيْكَ إِعْرٰاضُهُمْ فَإِنِ اسْتَطَعْتَ أَنْ تَبْتَغِيَ نَفَقاً فِي الْأَرْضِ أَوْ سُلَّماً فِي السَّمٰاءِ فَتَأْتِيَهُمْ بِآيَةٍ وَ لَوْ شٰاءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدىٰ فَلاٰ تَكُونَنَّ مِنَ الْجٰاهِلِينَ");
        sor.Ayaat.Add("إِنَّمٰا يَسْتَجِيبُ الَّذِينَ يَسْمَعُونَ وَ الْمَوْتىٰ يَبْعَثُهُمُ اللّٰهُ ثُمَّ إِلَيْهِ يُرْجَعُونَ");
        sor.Ayaat.Add("وَ قٰالُوا لَوْ لاٰ نُزِّلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ قُلْ إِنَّ اللّٰهَ قٰادِرٌ عَلىٰ أَنْ يُنَزِّلَ آيَةً وَ لٰكِنَّ أَكْثَرَهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ مٰا مِنْ دَابَّةٍ فِي الْأَرْضِ وَ لاٰ طٰائِرٍ يَطِيرُ بِجَنٰاحَيْهِ إِلاّٰ أُمَمٌ أَمْثٰالُكُمْ مٰا فَرَّطْنٰا فِي الْكِتٰابِ مِنْ شَيْءٍ ثُمَّ إِلىٰ رَبِّهِمْ يُحْشَرُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا صُمٌّ وَ بُكْمٌ فِي الظُّلُمٰاتِ مَنْ يَشَأِ اللّٰهُ يُضْلِلْهُ وَ مَنْ يَشَأْ يَجْعَلْهُ عَلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("قُلْ أَ رَأَيْتَكُمْ إِنْ أَتٰاكُمْ عَذٰابُ اللّٰهِ أَوْ أَتَتْكُمُ السّٰاعَةُ أَ غَيْرَ اللّٰهِ تَدْعُونَ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("بَلْ إِيّٰاهُ تَدْعُونَ فَيَكْشِفُ مٰا تَدْعُونَ إِلَيْهِ إِنْ شٰاءَ وَ تَنْسَوْنَ مٰا تُشْرِكُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا إِلىٰ أُمَمٍ مِنْ قَبْلِكَ فَأَخَذْنٰاهُمْ بِالْبَأْسٰاءِ وَ الضَّرّٰاءِ لَعَلَّهُمْ يَتَضَرَّعُونَ");
        sor.Ayaat.Add("فَلَوْ لاٰ إِذْ جٰاءَهُمْ بَأْسُنٰا تَضَرَّعُوا وَ لٰكِنْ قَسَتْ قُلُوبُهُمْ وَ زَيَّنَ لَهُمُ الشَّيْطٰانُ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("فَلَمّٰا نَسُوا مٰا ذُكِّرُوا بِهِ فَتَحْنٰا عَلَيْهِمْ أَبْوٰابَ كُلِّ شَيْءٍ حَتّٰى إِذٰا فَرِحُوا بِمٰا أُوتُوا أَخَذْنٰاهُمْ بَغْتَةً فَإِذٰا هُمْ مُبْلِسُونَ");
        sor.Ayaat.Add("فَقُطِعَ دٰابِرُ الْقَوْمِ الَّذِينَ ظَلَمُوا وَ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("قُلْ أَ رَأَيْتُمْ إِنْ أَخَذَ اللّٰهُ سَمْعَكُمْ وَ أَبْصٰارَكُمْ وَ خَتَمَ عَلىٰ قُلُوبِكُمْ مَنْ إِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيكُمْ بِهِ انْظُرْ كَيْفَ نُصَرِّفُ الْآيٰاتِ ثُمَّ هُمْ يَصْدِفُونَ");
        sor.Ayaat.Add("قُلْ أَ رَأَيْتَكُمْ إِنْ أَتٰاكُمْ عَذٰابُ اللّٰهِ بَغْتَةً أَوْ جَهْرَةً هَلْ يُهْلَكُ إِلاَّ الْقَوْمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("وَ مٰا نُرْسِلُ الْمُرْسَلِينَ إِلاّٰ مُبَشِّرِينَ وَ مُنْذِرِينَ فَمَنْ آمَنَ وَ أَصْلَحَ فَلاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا يَمَسُّهُمُ الْعَذٰابُ بِمٰا كٰانُوا يَفْسُقُونَ");
        sor.Ayaat.Add("قُلْ لاٰ أَقُولُ لَكُمْ عِنْدِي خَزٰائِنُ اللّٰهِ وَ لاٰ أَعْلَمُ الْغَيْبَ وَ لاٰ أَقُولُ لَكُمْ إِنِّي مَلَكٌ إِنْ أَتَّبِعُ إِلاّٰ مٰا يُوحىٰ إِلَيَّ قُلْ هَلْ يَسْتَوِي الْأَعْمىٰ وَ الْبَصِيرُ أَ فَلاٰ تَتَفَكَّرُونَ");
        sor.Ayaat.Add("وَ أَنْذِرْ بِهِ الَّذِينَ يَخٰافُونَ أَنْ يُحْشَرُوا إِلىٰ رَبِّهِمْ لَيْسَ لَهُمْ مِنْ دُونِهِ وَلِيٌّ وَ لاٰ شَفِيعٌ لَعَلَّهُمْ يَتَّقُونَ");
        sor.Ayaat.Add("وَ لاٰ تَطْرُدِ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدٰاةِ وَ الْعَشِيِّ يُرِيدُونَ وَجْهَهُ مٰا عَلَيْكَ مِنْ حِسٰابِهِمْ مِنْ شَيْءٍ وَ مٰا مِنْ حِسٰابِكَ عَلَيْهِمْ مِنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُونَ مِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ كَذٰلِكَ فَتَنّٰا بَعْضَهُمْ بِبَعْضٍ لِيَقُولُوا أَ هٰؤُلاٰءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِنْ بَيْنِنٰا أَ لَيْسَ اللّٰهُ بِأَعْلَمَ بِالشّٰاكِرِينَ");
        sor.Ayaat.Add("وَ إِذٰا جٰاءَكَ الَّذِينَ يُؤْمِنُونَ بِآيٰاتِنٰا فَقُلْ سَلاٰمٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلىٰ نَفْسِهِ الرَّحْمَةَ أَنَّهُ مَنْ عَمِلَ مِنْكُمْ سُوءاً بِجَهٰالَةٍ ثُمَّ تٰابَ مِنْ بَعْدِهِ وَ أَصْلَحَ فَأَنَّهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ كَذٰلِكَ نُفَصِّلُ الْآيٰاتِ وَ لِتَسْتَبِينَ سَبِيلُ الْمُجْرِمِينَ");
        sor.Ayaat.Add("قُلْ إِنِّي نُهِيتُ أَنْ أَعْبُدَ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ قُلْ لاٰ أَتَّبِعُ أَهْوٰاءَكُمْ قَدْ ضَلَلْتُ إِذاً وَ مٰا أَنَا مِنَ الْمُهْتَدِينَ");
        sor.Ayaat.Add("قُلْ إِنِّي عَلىٰ بَيِّنَةٍ مِنْ رَبِّي وَ كَذَّبْتُمْ بِهِ مٰا عِنْدِي مٰا تَسْتَعْجِلُونَ بِهِ إِنِ الْحُكْمُ إِلاّٰ لِلّٰهِ يَقُصُّ الْحَقَّ وَ هُوَ خَيْرُ الْفٰاصِلِينَ");
        sor.Ayaat.Add("قُلْ لَوْ أَنَّ عِنْدِي مٰا تَسْتَعْجِلُونَ بِهِ لَقُضِيَ الْأَمْرُ بَيْنِي وَ بَيْنَكُمْ وَ اللّٰهُ أَعْلَمُ بِالظّٰالِمِينَ");
        sor.Ayaat.Add("وَ عِنْدَهُ مَفٰاتِحُ الْغَيْبِ لاٰ يَعْلَمُهٰا إِلاّٰ هُوَ وَ يَعْلَمُ مٰا فِي الْبَرِّ وَ الْبَحْرِ وَ مٰا تَسْقُطُ مِنْ وَرَقَةٍ إِلاّٰ يَعْلَمُهٰا وَ لاٰ حَبَّةٍ فِي ظُلُمٰاتِ الْأَرْضِ وَ لاٰ رَطْبٍ وَ لاٰ يٰابِسٍ إِلاّٰ فِي كِتٰابٍ مُبِينٍ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي يَتَوَفّٰاكُمْ بِاللَّيْلِ وَ يَعْلَمُ مٰا جَرَحْتُمْ بِالنَّهٰارِ ثُمَّ يَبْعَثُكُمْ فِيهِ لِيُقْضىٰ أَجَلٌ مُسَمًّى ثُمَّ إِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("وَ هُوَ الْقٰاهِرُ فَوْقَ عِبٰادِهِ وَ يُرْسِلُ عَلَيْكُمْ حَفَظَةً حَتّٰى إِذٰا جٰاءَ أَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنٰا وَ هُمْ لاٰ يُفَرِّطُونَ");
        sor.Ayaat.Add("ثُمَّ رُدُّوا إِلَى اللّٰهِ مَوْلاٰهُمُ الْحَقِّ أَلاٰ لَهُ الْحُكْمُ وَ هُوَ أَسْرَعُ الْحٰاسِبِينَ");
        sor.Ayaat.Add("قُلْ مَنْ يُنَجِّيكُمْ مِنْ ظُلُمٰاتِ الْبَرِّ وَ الْبَحْرِ تَدْعُونَهُ تَضَرُّعاً وَ خُفْيَةً لَئِنْ أَنْجٰانٰا مِنْ هٰذِهِ لَنَكُونَنَّ مِنَ الشّٰاكِرِينَ");
        sor.Ayaat.Add("قُلِ اللّٰهُ يُنَجِّيكُمْ مِنْهٰا وَ مِنْ كُلِّ كَرْبٍ ثُمَّ أَنْتُمْ تُشْرِكُونَ");
        sor.Ayaat.Add("قُلْ هُوَ الْقٰادِرُ عَلىٰ أَنْ يَبْعَثَ عَلَيْكُمْ عَذٰاباً مِنْ فَوْقِكُمْ أَوْ مِنْ تَحْتِ أَرْجُلِكُمْ أَوْ يَلْبِسَكُمْ شِيَعاً وَ يُذِيقَ بَعْضَكُمْ بَأْسَ بَعْضٍ انْظُرْ كَيْفَ نُصَرِّفُ الْآيٰاتِ لَعَلَّهُمْ يَفْقَهُونَ");
        sor.Ayaat.Add("وَ كَذَّبَ بِهِ قَوْمُكَ وَ هُوَ الْحَقُّ قُلْ لَسْتُ عَلَيْكُمْ بِوَكِيلٍ");
        sor.Ayaat.Add("لِكُلِّ نَبَإٍ مُسْتَقَرٌّ وَ سَوْفَ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِذٰا رَأَيْتَ الَّذِينَ يَخُوضُونَ فِي آيٰاتِنٰا فَأَعْرِضْ عَنْهُمْ حَتّٰى يَخُوضُوا فِي حَدِيثٍ غَيْرِهِ وَ إِمّٰا يُنْسِيَنَّكَ الشَّيْطٰانُ فَلاٰ تَقْعُدْ بَعْدَ الذِّكْرىٰ مَعَ الْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ مٰا عَلَى الَّذِينَ يَتَّقُونَ مِنْ حِسٰابِهِمْ مِنْ شَيْءٍ وَ لٰكِنْ ذِكْرىٰ لَعَلَّهُمْ يَتَّقُونَ");
        sor.Ayaat.Add("وَ ذَرِ الَّذِينَ اتَّخَذُوا دِينَهُمْ لَعِباً وَ لَهْواً وَ غَرَّتْهُمُ الْحَيٰاةُ الدُّنْيٰا وَ ذَكِّرْ بِهِ أَنْ تُبْسَلَ نَفْسٌ بِمٰا كَسَبَتْ لَيْسَ لَهٰا مِنْ دُونِ اللّٰهِ وَلِيٌّ وَ لاٰ شَفِيعٌ وَ إِنْ تَعْدِلْ كُلَّ عَدْلٍ لاٰ يُؤْخَذْ مِنْهٰا أُولٰئِكَ الَّذِينَ أُبْسِلُوا بِمٰا كَسَبُوا لَهُمْ شَرٰابٌ مِنْ حَمِيمٍ وَ عَذٰابٌ أَلِيمٌ بِمٰا كٰانُوا يَكْفُرُونَ");
        sor.Ayaat.Add("قُلْ أَ نَدْعُوا مِنْ دُونِ اللّٰهِ مٰا لاٰ يَنْفَعُنٰا وَ لاٰ يَضُرُّنٰا وَ نُرَدُّ عَلىٰ أَعْقٰابِنٰا بَعْدَ إِذْ هَدٰانَا اللّٰهُ كَالَّذِي اسْتَهْوَتْهُ الشَّيٰاطِينُ فِي الْأَرْضِ حَيْرٰانَ لَهُ أَصْحٰابٌ يَدْعُونَهُ إِلَى الْهُدَى ائْتِنٰا قُلْ إِنَّ هُدَى اللّٰهِ هُوَ الْهُدىٰ وَ أُمِرْنٰا لِنُسْلِمَ لِرَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ أَنْ أَقِيمُوا الصَّلاٰةَ وَ اتَّقُوهُ وَ هُوَ الَّذِي إِلَيْهِ تُحْشَرُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ بِالْحَقِّ وَ يَوْمَ يَقُولُ كُنْ فَيَكُونُ قَوْلُهُ الْحَقُّ وَ لَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِي الصُّورِ عٰالِمُ الْغَيْبِ وَ الشَّهٰادَةِ وَ هُوَ الْحَكِيمُ الْخَبِيرُ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ إِبْرٰاهِيمُ لِأَبِيهِ آزَرَ أَ تَتَّخِذُ أَصْنٰاماً آلِهَةً إِنِّي أَرٰاكَ وَ قَوْمَكَ فِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("وَ كَذٰلِكَ نُرِي إِبْرٰاهِيمَ مَلَكُوتَ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ لِيَكُونَ مِنَ الْمُوقِنِينَ");
        sor.Ayaat.Add("فَلَمّٰا جَنَّ عَلَيْهِ اللَّيْلُ رَأىٰ كَوْكَباً قٰالَ هٰذٰا رَبِّي فَلَمّٰا أَفَلَ قٰالَ لاٰ أُحِبُّ الْآفِلِينَ");
        sor.Ayaat.Add("فَلَمّٰا رَأَى الْقَمَرَ بٰازِغاً قٰالَ هٰذٰا رَبِّي فَلَمّٰا أَفَلَ قٰالَ لَئِنْ لَمْ يَهْدِنِي رَبِّي لَأَكُونَنَّ مِنَ الْقَوْمِ الضّٰالِّينَ");
        sor.Ayaat.Add("فَلَمّٰا رَأَى الشَّمْسَ بٰازِغَةً قٰالَ هٰذٰا رَبِّي هٰذٰا أَكْبَرُ فَلَمّٰا أَفَلَتْ قٰالَ يٰا قَوْمِ إِنِّي بَرِيءٌ مِمّٰا تُشْرِكُونَ");
        sor.Ayaat.Add("إِنِّي وَجَّهْتُ وَجْهِيَ لِلَّذِي فَطَرَ السَّمٰاوٰاتِ وَ الْأَرْضَ حَنِيفاً وَ مٰا أَنَا مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("وَ حٰاجَّهُ قَوْمُهُ قٰالَ أَ تُحٰاجُّونِّي فِي اللّٰهِ وَ قَدْ هَدٰانِ وَ لاٰ أَخٰافُ مٰا تُشْرِكُونَ بِهِ إِلاّٰ أَنْ يَشٰاءَ رَبِّي شَيْئاً وَسِعَ رَبِّي كُلَّ شَيْءٍ عِلْماً أَ فَلاٰ تَتَذَكَّرُونَ");
        sor.Ayaat.Add("وَ كَيْفَ أَخٰافُ مٰا أَشْرَكْتُمْ وَ لاٰ تَخٰافُونَ أَنَّكُمْ أَشْرَكْتُمْ بِاللّٰهِ مٰا لَمْ يُنَزِّلْ بِهِ عَلَيْكُمْ سُلْطٰاناً فَأَيُّ الْفَرِيقَيْنِ أَحَقُّ بِالْأَمْنِ إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("الَّذِينَ آمَنُوا وَ لَمْ يَلْبِسُوا إِيمٰانَهُمْ بِظُلْمٍ أُولٰئِكَ لَهُمُ الْأَمْنُ وَ هُمْ مُهْتَدُونَ");
        sor.Ayaat.Add("وَ تِلْكَ حُجَّتُنٰا آتَيْنٰاهٰا إِبْرٰاهِيمَ عَلىٰ قَوْمِهِ نَرْفَعُ دَرَجٰاتٍ مَنْ نَشٰاءُ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ وَهَبْنٰا لَهُ إِسْحٰاقَ وَ يَعْقُوبَ كُلاًّ هَدَيْنٰا وَ نُوحاً هَدَيْنٰا مِنْ قَبْلُ وَ مِنْ ذُرِّيَّتِهِ دٰاوُدَ وَ سُلَيْمٰانَ وَ أَيُّوبَ وَ يُوسُفَ وَ مُوسىٰ وَ هٰارُونَ وَ كَذٰلِكَ نَجْزِي الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ زَكَرِيّٰا وَ يَحْيىٰ وَ عِيسىٰ وَ إِلْيٰاسَ كُلٌّ مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("وَ إِسْمٰاعِيلَ وَ الْيَسَعَ وَ يُونُسَ وَ لُوطاً وَ كلاًّ فَضَّلْنٰا عَلَى الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ مِنْ آبٰائِهِمْ وَ ذُرِّيّٰاتِهِمْ وَ إِخْوٰانِهِمْ وَ اجْتَبَيْنٰاهُمْ وَ هَدَيْنٰاهُمْ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("ذٰلِكَ هُدَى اللّٰهِ يَهْدِي بِهِ مَنْ يَشٰاءُ مِنْ عِبٰادِهِ وَ لَوْ أَشْرَكُوا لَحَبِطَ عَنْهُمْ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ آتَيْنٰاهُمُ الْكِتٰابَ وَ الْحُكْمَ وَ النُّبُوَّةَ فَإِنْ يَكْفُرْ بِهٰا هٰؤُلاٰءِ فَقَدْ وَكَّلْنٰا بِهٰا قَوْماً لَيْسُوا بِهٰا بِكٰافِرِينَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ هَدَى اللّٰهُ فَبِهُدٰاهُمُ اقْتَدِهْ قُلْ لاٰ أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِنْ هُوَ إِلاّٰ ذِكْرىٰ لِلْعٰالَمِينَ");
        sor.Ayaat.Add("وَ مٰا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهِ إِذْ قٰالُوا مٰا أَنْزَلَ اللّٰهُ عَلىٰ بَشَرٍ مِنْ شَيْءٍ قُلْ مَنْ أَنْزَلَ الْكِتٰابَ الَّذِي جٰاءَ بِهِ مُوسىٰ نُوراً وَ هُدىً لِلنّٰاسِ تَجْعَلُونَهُ قَرٰاطِيسَ تُبْدُونَهٰا وَ تُخْفُونَ كَثِيراً وَ عُلِّمْتُمْ مٰا لَمْ تَعْلَمُوا أَنْتُمْ وَ لاٰ آبٰاؤُكُمْ قُلِ اللّٰهُ ثُمَّ ذَرْهُمْ فِي خَوْضِهِمْ يَلْعَبُونَ");
        sor.Ayaat.Add("وَ هٰذٰا كِتٰابٌ أَنْزَلْنٰاهُ مُبٰارَكٌ مُصَدِّقُ الَّذِي بَيْنَ يَدَيْهِ وَ لِتُنْذِرَ أُمَّ الْقُرىٰ وَ مَنْ حَوْلَهٰا وَ الَّذِينَ يُؤْمِنُونَ بِالْآخِرَةِ يُؤْمِنُونَ بِهِ وَ هُمْ عَلىٰ صَلاٰتِهِمْ يُحٰافِظُونَ");
        sor.Ayaat.Add("وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً أَوْ قٰالَ أُوحِيَ إِلَيَّ وَ لَمْ يُوحَ إِلَيْهِ شَيْءٌ وَ مَنْ قٰالَ سَأُنْزِلُ مِثْلَ مٰا أَنْزَلَ اللّٰهُ وَ لَوْ تَرىٰ إِذِ الظّٰالِمُونَ فِي غَمَرٰاتِ الْمَوْتِ وَ الْمَلاٰئِكَةُ بٰاسِطُوا أَيْدِيهِمْ أَخْرِجُوا أَنْفُسَكُمُ الْيَوْمَ تُجْزَوْنَ عَذٰابَ الْهُونِ بِمٰا كُنْتُمْ تَقُولُونَ عَلَى اللّٰهِ غَيْرَ الْحَقِّ وَ كُنْتُمْ عَنْ آيٰاتِهِ تَسْتَكْبِرُونَ");
        sor.Ayaat.Add("وَ لَقَدْ جِئْتُمُونٰا فُرٰادىٰ كَمٰا خَلَقْنٰاكُمْ أَوَّلَ مَرَّةٍ وَ تَرَكْتُمْ مٰا خَوَّلْنٰاكُمْ وَرٰاءَ ظُهُورِكُمْ وَ مٰا نَرىٰ مَعَكُمْ شُفَعٰاءَكُمُ الَّذِينَ زَعَمْتُمْ أَنَّهُمْ فِيكُمْ شُرَكٰاءُ لَقَدْ تَقَطَّعَ بَيْنَكُمْ وَ ضَلَّ عَنْكُمْ مٰا كُنْتُمْ تَزْعُمُونَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ فٰالِقُ الْحَبِّ وَ النَّوىٰ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ مُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ذٰلِكُمُ اللّٰهُ فَأَنّٰى تُؤْفَكُونَ");
        sor.Ayaat.Add("فٰالِقُ الْإِصْبٰاحِ وَ جَعَلَ اللَّيْلَ سَكَناً وَ الشَّمْسَ وَ الْقَمَرَ حُسْبٰاناً ذٰلِكَ تَقْدِيرُ الْعَزِيزِ الْعَلِيمِ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي جَعَلَ لَكُمُ النُّجُومَ لِتَهْتَدُوا بِهٰا فِي ظُلُمٰاتِ الْبَرِّ وَ الْبَحْرِ قَدْ فَصَّلْنَا الْآيٰاتِ لِقَوْمٍ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي أَنْشَأَكُمْ مِنْ نَفْسٍ وٰاحِدَةٍ فَمُسْتَقَرٌّ وَ مُسْتَوْدَعٌ قَدْ فَصَّلْنَا الْآيٰاتِ لِقَوْمٍ يَفْقَهُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَأَخْرَجْنٰا بِهِ نَبٰاتَ كُلِّ شَيْءٍ فَأَخْرَجْنٰا مِنْهُ خَضِراً نُخْرِجُ مِنْهُ حَبًّا مُتَرٰاكِباً وَ مِنَ النَّخْلِ مِنْ طَلْعِهٰا قِنْوٰانٌ دٰانِيَةٌ وَ جَنّٰاتٍ مِنْ أَعْنٰابٍ وَ الزَّيْتُونَ وَ الرُّمّٰانَ مُشْتَبِهاً وَ غَيْرَ مُتَشٰابِهٍ انْظُرُوا إِلىٰ ثَمَرِهِ إِذٰا أَثْمَرَ وَ يَنْعِهِ إِنَّ فِي ذٰلِكُمْ لَآيٰاتٍ لِقَوْمٍ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ جَعَلُوا لِلّٰهِ شُرَكٰاءَ الْجِنَّ وَ خَلَقَهُمْ وَ خَرَقُوا لَهُ بَنِينَ وَ بَنٰاتٍ بِغَيْرِ عِلْمٍ سُبْحٰانَهُ وَ تَعٰالىٰ عَمّٰا يَصِفُونَ");
        sor.Ayaat.Add("بَدِيعُ السَّمٰاوٰاتِ وَ الْأَرْضِ أَنّٰى يَكُونُ لَهُ وَلَدٌ وَ لَمْ تَكُنْ لَهُ صٰاحِبَةٌ وَ خَلَقَ كُلَّ شَيْءٍ وَ هُوَ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لاٰ إِلٰهَ إِلاّٰ هُوَ خٰالِقُ كُلِّ شَيْءٍ فَاعْبُدُوهُ وَ هُوَ عَلىٰ كُلِّ شَيْءٍ وَكِيلٌ");
        sor.Ayaat.Add("لاٰ تُدْرِكُهُ الْأَبْصٰارُ وَ هُوَ يُدْرِكُ الْأَبْصٰارَ وَ هُوَ اللَّطِيفُ الْخَبِيرُ");
        sor.Ayaat.Add("قَدْ جٰاءَكُمْ بَصٰائِرُ مِنْ رَبِّكُمْ فَمَنْ أَبْصَرَ فَلِنَفْسِهِ وَ مَنْ عَمِيَ فَعَلَيْهٰا وَ مٰا أَنَا عَلَيْكُمْ بِحَفِيظٍ");
        sor.Ayaat.Add("وَ كَذٰلِكَ نُصَرِّفُ الْآيٰاتِ وَ لِيَقُولُوا دَرَسْتَ وَ لِنُبَيِّنَهُ لِقَوْمٍ يَعْلَمُونَ");
        sor.Ayaat.Add("اتَّبِعْ مٰا أُوحِيَ إِلَيْكَ مِنْ رَبِّكَ لاٰ إِلٰهَ إِلاّٰ هُوَ وَ أَعْرِضْ عَنِ الْمُشْرِكِينَ");
        sor.Ayaat.Add("وَ لَوْ شٰاءَ اللّٰهُ مٰا أَشْرَكُوا وَ مٰا جَعَلْنٰاكَ عَلَيْهِمْ حَفِيظاً وَ مٰا أَنْتَ عَلَيْهِمْ بِوَكِيلٍ");
        sor.Ayaat.Add("وَ لاٰ تَسُبُّوا الَّذِينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْواً بِغَيْرِ عِلْمٍ كَذٰلِكَ زَيَّنّٰا لِكُلِّ أُمَّةٍ عَمَلَهُمْ ثُمَّ إِلىٰ رَبِّهِمْ مَرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ أَقْسَمُوا بِاللّٰهِ جَهْدَ أَيْمٰانِهِمْ لَئِنْ جٰاءَتْهُمْ آيَةٌ لَيُؤْمِنُنَّ بِهٰا قُلْ إِنَّمَا الْآيٰاتُ عِنْدَ اللّٰهِ وَ مٰا يُشْعِرُكُمْ أَنَّهٰا إِذٰا جٰاءَتْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ نُقَلِّبُ أَفْئِدَتَهُمْ وَ أَبْصٰارَهُمْ كَمٰا لَمْ يُؤْمِنُوا بِهِ أَوَّلَ مَرَّةٍ وَ نَذَرُهُمْ فِي طُغْيٰانِهِمْ يَعْمَهُونَ");
        sor.Ayaat.Add("وَ لَوْ أَنَّنٰا نَزَّلْنٰا إِلَيْهِمُ الْمَلاٰئِكَةَ وَ كَلَّمَهُمُ الْمَوْتىٰ وَ حَشَرْنٰا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلاً مٰا كٰانُوا لِيُؤْمِنُوا إِلاّٰ أَنْ يَشٰاءَ اللّٰهُ وَ لٰكِنَّ أَكْثَرَهُمْ يَجْهَلُونَ");
        sor.Ayaat.Add("وَ كَذٰلِكَ جَعَلْنٰا لِكُلِّ نَبِيٍّ عَدُوًّا شَيٰاطِينَ الْإِنْسِ وَ الْجِنِّ يُوحِي بَعْضُهُمْ إِلىٰ بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوراً وَ لَوْ شٰاءَ رَبُّكَ مٰا فَعَلُوهُ فَذَرْهُمْ وَ مٰا يَفْتَرُونَ");
        sor.Ayaat.Add("وَ لِتَصْغىٰ إِلَيْهِ أَفْئِدَةُ الَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ وَ لِيَرْضَوْهُ وَ لِيَقْتَرِفُوا مٰا هُمْ مُقْتَرِفُونَ");
        sor.Ayaat.Add("أَ فَغَيْرَ اللّٰهِ أَبْتَغِي حَكَماً وَ هُوَ الَّذِي أَنْزَلَ إِلَيْكُمُ الْكِتٰابَ مُفَصَّلاً وَ الَّذِينَ آتَيْنٰاهُمُ الْكِتٰابَ يَعْلَمُونَ أَنَّهُ مُنَزَّلٌ مِنْ رَبِّكَ بِالْحَقِّ فَلاٰ تَكُونَنَّ مِنَ الْمُمْتَرِينَ");
        sor.Ayaat.Add("وَ تَمَّتْ كَلِمَةُ رَبِّكَ صِدْقاً وَ عَدْلاً لاٰ مُبَدِّلَ لِكَلِمٰاتِهِ وَ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("وَ إِنْ تُطِعْ أَكْثَرَ مَنْ فِي الْأَرْضِ يُضِلُّوكَ عَنْ سَبِيلِ اللّٰهِ إِنْ يَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنْ هُمْ إِلاّٰ يَخْرُصُونَ");
        sor.Ayaat.Add("إِنَّ رَبَّكَ هُوَ أَعْلَمُ مَنْ يَضِلُّ عَنْ سَبِيلِهِ وَ هُوَ أَعْلَمُ بِالْمُهْتَدِينَ");
        sor.Ayaat.Add("فَكُلُوا مِمّٰا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ إِنْ كُنْتُمْ بِآيٰاتِهِ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ مٰا لَكُمْ أَلاّٰ تَأْكُلُوا مِمّٰا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَ قَدْ فَصَّلَ لَكُمْ مٰا حَرَّمَ عَلَيْكُمْ إِلاّٰ مَا اضْطُرِرْتُمْ إِلَيْهِ وَ إِنَّ كَثِيراً لَيُضِلُّونَ بِأَهْوٰائِهِمْ بِغَيْرِ عِلْمٍ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِالْمُعْتَدِينَ");
        sor.Ayaat.Add("وَ ذَرُوا ظٰاهِرَ الْإِثْمِ وَ بٰاطِنَهُ إِنَّ الَّذِينَ يَكْسِبُونَ الْإِثْمَ سَيُجْزَوْنَ بِمٰا كٰانُوا يَقْتَرِفُونَ");
        sor.Ayaat.Add("وَ لاٰ تَأْكُلُوا مِمّٰا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَ إِنَّهُ لَفِسْقٌ وَ إِنَّ الشَّيٰاطِينَ لَيُوحُونَ إِلىٰ أَوْلِيٰائِهِمْ لِيُجٰادِلُوكُمْ وَ إِنْ أَطَعْتُمُوهُمْ إِنَّكُمْ لَمُشْرِكُونَ");
        sor.Ayaat.Add("أَ وَ مَنْ كٰانَ مَيْتاً فَأَحْيَيْنٰاهُ وَ جَعَلْنٰا لَهُ نُوراً يَمْشِي بِهِ فِي النّٰاسِ كَمَنْ مَثَلُهُ فِي الظُّلُمٰاتِ لَيْسَ بِخٰارِجٍ مِنْهٰا كَذٰلِكَ زُيِّنَ لِلْكٰافِرِينَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ كَذٰلِكَ جَعَلْنٰا فِي كُلِّ قَرْيَةٍ أَكٰابِرَ مُجْرِمِيهٰا لِيَمْكُرُوا فِيهٰا وَ مٰا يَمْكُرُونَ إِلاّٰ بِأَنْفُسِهِمْ وَ مٰا يَشْعُرُونَ");
        sor.Ayaat.Add("وَ إِذٰا جٰاءَتْهُمْ آيَةٌ قٰالُوا لَنْ نُؤْمِنَ حَتّٰى نُؤْتىٰ مِثْلَ مٰا أُوتِيَ رُسُلُ اللّٰهِ اللّٰهُ أَعْلَمُ حَيْثُ يَجْعَلُ رِسٰالَتَهُ سَيُصِيبُ الَّذِينَ أَجْرَمُوا صَغٰارٌ عِنْدَ اللّٰهِ وَ عَذٰابٌ شَدِيدٌ بِمٰا كٰانُوا يَمْكُرُونَ");
        sor.Ayaat.Add("فَمَنْ يُرِدِ اللّٰهُ أَنْ يَهْدِيَهُ يَشْرَحْ صَدْرَهُ لِلْإِسْلاٰمِ وَ مَنْ يُرِدْ أَنْ يُضِلَّهُ يَجْعَلْ صَدْرَهُ ضَيِّقاً حَرَجاً كَأَنَّمٰا يَصَّعَّدُ فِي السَّمٰاءِ كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذِينَ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ هٰذٰا صِرٰاطُ رَبِّكَ مُسْتَقِيماً قَدْ فَصَّلْنَا الْآيٰاتِ لِقَوْمٍ يَذَّكَّرُونَ");
        sor.Ayaat.Add("لَهُمْ دٰارُ السَّلاٰمِ عِنْدَ رَبِّهِمْ وَ هُوَ وَلِيُّهُمْ بِمٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ يَوْمَ يَحْشُرُهُمْ جَمِيعاً يٰا مَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِنَ الْإِنْسِ وَ قٰالَ أَوْلِيٰاؤُهُمْ مِنَ الْإِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنٰا بِبَعْضٍ وَ بَلَغْنٰا أَجَلَنَا الَّذِي أَجَّلْتَ لَنٰا قٰالَ النّٰارُ مَثْوٰاكُمْ خٰالِدِينَ فِيهٰا إِلاّٰ مٰا شٰاءَ اللّٰهُ إِنَّ رَبَّكَ حَكِيمٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ كَذٰلِكَ نُوَلِّي بَعْضَ الظّٰالِمِينَ بَعْضاً بِمٰا كٰانُوا يَكْسِبُونَ");
        sor.Ayaat.Add("يٰا مَعْشَرَ الْجِنِّ وَ الْإِنْسِ أَ لَمْ يَأْتِكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آيٰاتِي وَ يُنْذِرُونَكُمْ لِقٰاءَ يَوْمِكُمْ هٰذٰا قٰالُوا شَهِدْنٰا عَلىٰ أَنْفُسِنٰا وَ غَرَّتْهُمُ الْحَيٰاةُ الدُّنْيٰا وَ شَهِدُوا عَلىٰ أَنْفُسِهِمْ أَنَّهُمْ كٰانُوا كٰافِرِينَ");
        sor.Ayaat.Add("ذٰلِكَ أَنْ لَمْ يَكُنْ رَبُّكَ مُهْلِكَ الْقُرىٰ بِظُلْمٍ وَ أَهْلُهٰا غٰافِلُونَ");
        sor.Ayaat.Add("وَ لِكُلٍّ دَرَجٰاتٌ مِمّٰا عَمِلُوا وَ مٰا رَبُّكَ بِغٰافِلٍ عَمّٰا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ رَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ إِنْ يَشَأْ يُذْهِبْكُمْ وَ يَسْتَخْلِفْ مِنْ بَعْدِكُمْ مٰا يَشٰاءُ كَمٰا أَنْشَأَكُمْ مِنْ ذُرِّيَّةِ قَوْمٍ آخَرِينَ");
        sor.Ayaat.Add("إِنَّ مٰا تُوعَدُونَ لَآتٍ وَ مٰا أَنْتُمْ بِمُعْجِزِينَ");
        sor.Ayaat.Add("قُلْ يٰا قَوْمِ اعْمَلُوا عَلىٰ مَكٰانَتِكُمْ إِنِّي عٰامِلٌ فَسَوْفَ تَعْلَمُونَ مَنْ تَكُونُ لَهُ عٰاقِبَةُ الدّٰارِ إِنَّهُ لاٰ يُفْلِحُ الظّٰالِمُونَ");
        sor.Ayaat.Add("وَ جَعَلُوا لِلّٰهِ مِمّٰا ذَرَأَ مِنَ الْحَرْثِ وَ الْأَنْعٰامِ نَصِيباً فَقٰالُوا هٰذٰا لِلّٰهِ بِزَعْمِهِمْ وَ هٰذٰا لِشُرَكٰائِنٰا فَمٰا كٰانَ لِشُرَكٰائِهِمْ فَلاٰ يَصِلُ إِلَى اللّٰهِ وَ مٰا كٰانَ لِلّٰهِ فَهُوَ يَصِلُ إِلىٰ شُرَكٰائِهِمْ سٰاءَ مٰا يَحْكُمُونَ");
        sor.Ayaat.Add("وَ كَذٰلِكَ زَيَّنَ لِكَثِيرٍ مِنَ الْمُشْرِكِينَ قَتْلَ أَوْلاٰدِهِمْ شُرَكٰاؤُهُمْ لِيُرْدُوهُمْ وَ لِيَلْبِسُوا عَلَيْهِمْ دِينَهُمْ وَ لَوْ شٰاءَ اللّٰهُ مٰا فَعَلُوهُ فَذَرْهُمْ وَ مٰا يَفْتَرُونَ");
        sor.Ayaat.Add("وَ قٰالُوا هٰذِهِ أَنْعٰامٌ وَ حَرْثٌ حِجْرٌ لاٰ يَطْعَمُهٰا إِلاّٰ مَنْ نَشٰاءُ بِزَعْمِهِمْ وَ أَنْعٰامٌ حُرِّمَتْ ظُهُورُهٰا وَ أَنْعٰامٌ لاٰ يَذْكُرُونَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرٰاءً عَلَيْهِ سَيَجْزِيهِمْ بِمٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("وَ قٰالُوا مٰا فِي بُطُونِ هٰذِهِ الْأَنْعٰامِ خٰالِصَةٌ لِذُكُورِنٰا وَ مُحَرَّمٌ عَلىٰ أَزْوٰاجِنٰا وَ إِنْ يَكُنْ مَيْتَةً فَهُمْ فِيهِ شُرَكٰاءُ سَيَجْزِيهِمْ وَصْفَهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ");
        sor.Ayaat.Add("قَدْ خَسِرَ الَّذِينَ قَتَلُوا أَوْلاٰدَهُمْ سَفَهاً بِغَيْرِ عِلْمٍ وَ حَرَّمُوا مٰا رَزَقَهُمُ اللّٰهُ افْتِرٰاءً عَلَى اللّٰهِ قَدْ ضَلُّوا وَ مٰا كٰانُوا مُهْتَدِينَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي أَنْشَأَ جَنّٰاتٍ مَعْرُوشٰاتٍ وَ غَيْرَ مَعْرُوشٰاتٍ وَ النَّخْلَ وَ الزَّرْعَ مُخْتَلِفاً أُكُلُهُ وَ الزَّيْتُونَ وَ الرُّمّٰانَ مُتَشٰابِهاً وَ غَيْرَ مُتَشٰابِهٍ كُلُوا مِنْ ثَمَرِهِ إِذٰا أَثْمَرَ وَ آتُوا حَقَّهُ يَوْمَ حَصٰادِهِ وَ لاٰ تُسْرِفُوا إِنَّهُ لاٰ يُحِبُّ الْمُسْرِفِينَ");
        sor.Ayaat.Add("وَ مِنَ الْأَنْعٰامِ حَمُولَةً وَ فَرْشاً كُلُوا مِمّٰا رَزَقَكُمُ اللّٰهُ وَ لاٰ تَتَّبِعُوا خُطُوٰاتِ الشَّيْطٰانِ إِنَّهُ لَكُمْ عَدُوٌّ مُبِينٌ");
        sor.Ayaat.Add("ثَمٰانِيَةَ أَزْوٰاجٍ مِنَ الضَّأْنِ اثْنَيْنِ وَ مِنَ الْمَعْزِ اثْنَيْنِ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحٰامُ الْأُنْثَيَيْنِ نَبِّئُونِي بِعِلْمٍ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("وَ مِنَ الْإِبِلِ اثْنَيْنِ وَ مِنَ الْبَقَرِ اثْنَيْنِ قُلْ آلذَّكَرَيْنِ حَرَّمَ أَمِ الْأُنْثَيَيْنِ أَمَّا اشْتَمَلَتْ عَلَيْهِ أَرْحٰامُ الْأُنْثَيَيْنِ أَمْ كُنْتُمْ شُهَدٰاءَ إِذْ وَصّٰاكُمُ اللّٰهُ بِهٰذٰا فَمَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً لِيُضِلَّ النّٰاسَ بِغَيْرِ عِلْمٍ إِنَّ اللّٰهَ لاٰ يَهْدِي الْقَوْمَ الظّٰالِمِينَ");
        sor.Ayaat.Add("قُلْ لاٰ أَجِدُ فِي مٰا أُوحِيَ إِلَيَّ مُحَرَّماً عَلىٰ طٰاعِمٍ يَطْعَمُهُ إِلاّٰ أَنْ يَكُونَ مَيْتَةً أَوْ دَماً مَسْفُوحاً أَوْ لَحْمَ خِنزِيرٍ فَإِنَّهُ رِجْسٌ أَوْ فِسْقاً أُهِلَّ لِغَيْرِ اللّٰهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ بٰاغٍ وَ لاٰ عٰادٍ فَإِنَّ رَبَّكَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ عَلَى الَّذِينَ هٰادُوا حَرَّمْنٰا كُلَّ ذِي ظُفُرٍ وَ مِنَ الْبَقَرِ وَ الْغَنَمِ حَرَّمْنٰا عَلَيْهِمْ شُحُومَهُمٰا إِلاّٰ مٰا حَمَلَتْ ظُهُورُهُمٰا أَوِ الْحَوٰايٰا أَوْ مَا اخْتَلَطَ بِعَظْمٍ ذٰلِكَ جَزَيْنٰاهُمْ بِبَغْيِهِمْ وَ إِنّٰا لَصٰادِقُونَ");
        sor.Ayaat.Add("فَإِنْ كَذَّبُوكَ فَقُلْ رَبُّكُمْ ذُو رَحْمَةٍ وٰاسِعَةٍ وَ لاٰ يُرَدُّ بَأْسُهُ عَنِ الْقَوْمِ الْمُجْرِمِينَ");
        sor.Ayaat.Add("سَيَقُولُ الَّذِينَ أَشْرَكُوا لَوْ شٰاءَ اللّٰهُ مٰا أَشْرَكْنٰا وَ لاٰ آبٰاؤُنٰا وَ لاٰ حَرَّمْنٰا مِنْ شَيْءٍ كَذٰلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ حَتّٰى ذٰاقُوا بَأْسَنٰا قُلْ هَلْ عِنْدَكُمْ مِنْ عِلْمٍ فَتُخْرِجُوهُ لَنٰا إِنْ تَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنْ أَنْتُمْ إِلاّٰ تَخْرُصُونَ");
        sor.Ayaat.Add("قُلْ فَلِلّٰهِ الْحُجَّةُ الْبٰالِغَةُ فَلَوْ شٰاءَ لَهَدٰاكُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("قُلْ هَلُمَّ شُهَدٰاءَكُمُ الَّذِينَ يَشْهَدُونَ أَنَّ اللّٰهَ حَرَّمَ هٰذٰا فَإِنْ شَهِدُوا فَلاٰ تَشْهَدْ مَعَهُمْ وَ لاٰ تَتَّبِعْ أَهْوٰاءَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا وَ الَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ وَ هُمْ بِرَبِّهِمْ يَعْدِلُونَ");
        sor.Ayaat.Add("قُلْ تَعٰالَوْا أَتْلُ مٰا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ أَلاّٰ تُشْرِكُوا بِهِ شَيْئاً وَ بِالْوٰالِدَيْنِ إِحْسٰاناً وَ لاٰ تَقْتُلُوا أَوْلاٰدَكُمْ مِنْ إِمْلاٰقٍ نَحْنُ نَرْزُقُكُمْ وَ إِيّٰاهُمْ وَ لاٰ تَقْرَبُوا الْفَوٰاحِشَ مٰا ظَهَرَ مِنْهٰا وَ مٰا بَطَنَ وَ لاٰ تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللّٰهُ إِلاّٰ بِالْحَقِّ ذٰلِكُمْ وَصّٰاكُمْ بِهِ لَعَلَّكُمْ تَعْقِلُونَ");
        sor.Ayaat.Add("وَ لاٰ تَقْرَبُوا مٰالَ الْيَتِيمِ إِلاّٰ بِالَّتِي هِيَ أَحْسَنُ حَتّٰى يَبْلُغَ أَشُدَّهُ وَ أَوْفُوا الْكَيْلَ وَ الْمِيزٰانَ بِالْقِسْطِ لاٰ نُكَلِّفُ نَفْساً إِلاّٰ وُسْعَهٰا وَ إِذٰا قُلْتُمْ فَاعْدِلُوا وَ لَوْ كٰانَ ذٰا قُرْبىٰ وَ بِعَهْدِ اللّٰهِ أَوْفُوا ذٰلِكُمْ وَصّٰاكُمْ بِهِ لَعَلَّكُمْ تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ أَنَّ هٰذٰا صِرٰاطِي مُسْتَقِيماً فَاتَّبِعُوهُ وَ لاٰ تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيلِهِ ذٰلِكُمْ وَصّٰاكُمْ بِهِ لَعَلَّكُمْ تَتَّقُونَ");
        sor.Ayaat.Add("ثُمَّ آتَيْنٰا مُوسَى الْكِتٰابَ تَمٰاماً عَلَى الَّذِي أَحْسَنَ وَ تَفْصِيلاً لِكُلِّ شَيْءٍ وَ هُدىً وَ رَحْمَةً لَعَلَّهُمْ بِلِقٰاءِ رَبِّهِمْ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ هٰذٰا كِتٰابٌ أَنْزَلْنٰاهُ مُبٰارَكٌ فَاتَّبِعُوهُ وَ اتَّقُوا لَعَلَّكُمْ تُرْحَمُونَ");
        sor.Ayaat.Add("أَنْ تَقُولُوا إِنَّمٰا أُنْزِلَ الْكِتٰابُ عَلىٰ طٰائِفَتَيْنِ مِنْ قَبْلِنٰا وَ إِنْ كُنّٰا عَنْ دِرٰاسَتِهِمْ لَغٰافِلِينَ");
        sor.Ayaat.Add("أَوْ تَقُولُوا لَوْ أَنّٰا أُنْزِلَ عَلَيْنَا الْكِتٰابُ لَكُنّٰا أَهْدىٰ مِنْهُمْ فَقَدْ جٰاءَكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ وَ هُدىً وَ رَحْمَةٌ فَمَنْ أَظْلَمُ مِمَّنْ كَذَّبَ بِآيٰاتِ اللّٰهِ وَ صَدَفَ عَنْهٰا سَنَجْزِي الَّذِينَ يَصْدِفُونَ عَنْ آيٰاتِنٰا سُوءَ الْعَذٰابِ بِمٰا كٰانُوا يَصْدِفُونَ");
        sor.Ayaat.Add("هَلْ يَنْظُرُونَ إِلاّٰ أَنْ تَأْتِيَهُمُ الْمَلاٰئِكَةُ أَوْ يَأْتِيَ رَبُّكَ أَوْ يَأْتِيَ بَعْضُ آيٰاتِ رَبِّكَ يَوْمَ يَأْتِي بَعْضُ آيٰاتِ رَبِّكَ لاٰ يَنْفَعُ نَفْساً إِيمٰانُهٰا لَمْ تَكُنْ آمَنَتْ مِنْ قَبْلُ أَوْ كَسَبَتْ فِي إِيمٰانِهٰا خَيْراً قُلِ انْتَظِرُوا إِنّٰا مُنْتَظِرُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ فَرَّقُوا دِينَهُمْ وَ كٰانُوا شِيَعاً لَسْتَ مِنْهُمْ فِي شَيْءٍ إِنَّمٰا أَمْرُهُمْ إِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمٰا كٰانُوا يَفْعَلُونَ");
        sor.Ayaat.Add("مَنْ جٰاءَ بِالْحَسَنَةِ فَلَهُ عَشْرُ أَمْثٰالِهٰا وَ مَنْ جٰاءَ بِالسَّيِّئَةِ فَلاٰ يُجْزىٰ إِلاّٰ مِثْلَهٰا وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("قُلْ إِنَّنِي هَدٰانِي رَبِّي إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ دِيناً قِيَماً مِلَّةَ إِبْرٰاهِيمَ حَنِيفاً وَ مٰا كٰانَ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("قُلْ إِنَّ صَلاٰتِي وَ نُسُكِي وَ مَحْيٰايَ وَ مَمٰاتِي لِلّٰهِ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("لاٰ شَرِيكَ لَهُ وَ بِذٰلِكَ أُمِرْتُ وَ أَنَا أَوَّلُ الْمُسْلِمِينَ");
        sor.Ayaat.Add("قُلْ أَ غَيْرَ اللّٰهِ أَبْغِي رَبًّا وَ هُوَ رَبُّ كُلِّ شَيْءٍ وَ لاٰ تَكْسِبُ كُلُّ نَفْسٍ إِلاّٰ عَلَيْهٰا وَ لاٰ تَزِرُ وٰازِرَةٌ وِزْرَ أُخْرىٰ ثُمَّ إِلىٰ رَبِّكُمْ مَرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمٰا كُنْتُمْ فِيهِ تَخْتَلِفُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي جَعَلَكُمْ خَلاٰئِفَ الْأَرْضِ وَ رَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجٰاتٍ لِيَبْلُوَكُمْ فِي مٰا آتٰاكُمْ إِنَّ رَبَّكَ سَرِيعُ الْعِقٰابِ وَ إِنَّهُ لَغَفُورٌ رَحِيمٌ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٧ - الاعراف";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ المص");
        sor.Ayaat.Add("كِتٰابٌ أُنْزِلَ إِلَيْكَ فَلاٰ يَكُنْ فِي صَدْرِكَ حَرَجٌ مِنْهُ لِتُنْذِرَ بِهِ وَ ذِكْرىٰ لِلْمُؤْمِنِينَ");
        sor.Ayaat.Add("اتَّبِعُوا مٰا أُنْزِلَ إِلَيْكُمْ مِنْ رَبِّكُمْ وَ لاٰ تَتَّبِعُوا مِنْ دُونِهِ أَوْلِيٰاءَ قَلِيلاً مٰا تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ كَمْ مِنْ قَرْيَةٍ أَهْلَكْنٰاهٰا فَجٰاءَهٰا بَأْسُنٰا بَيٰاتاً أَوْ هُمْ قٰائِلُونَ");
        sor.Ayaat.Add("فَمٰا كٰانَ دَعْوٰاهُمْ إِذْ جٰاءَهُمْ بَأْسُنٰا إِلاّٰ أَنْ قٰالُوا إِنّٰا كُنّٰا ظٰالِمِينَ");
        sor.Ayaat.Add("فَلَنَسْئَلَنَّ الَّذِينَ أُرْسِلَ إِلَيْهِمْ وَ لَنَسْئَلَنَّ الْمُرْسَلِينَ");
        sor.Ayaat.Add("فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَ مٰا كُنّٰا غٰائِبِينَ");
        sor.Ayaat.Add("وَ الْوَزْنُ يَوْمَئِذٍ الْحَقُّ فَمَنْ ثَقُلَتْ مَوٰازِينُهُ فَأُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("وَ مَنْ خَفَّتْ مَوٰازِينُهُ فَأُولٰئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ بِمٰا كٰانُوا بِآيٰاتِنٰا يَظْلِمُونَ");
        sor.Ayaat.Add("وَ لَقَدْ مَكَّنّٰاكُمْ فِي الْأَرْضِ وَ جَعَلْنٰا لَكُمْ فِيهٰا مَعٰايِشَ قَلِيلاً مٰا تَشْكُرُونَ");
        sor.Ayaat.Add("وَ لَقَدْ خَلَقْنٰاكُمْ ثُمَّ صَوَّرْنٰاكُمْ ثُمَّ قُلْنٰا لِلْمَلاٰئِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاّٰ إِبْلِيسَ لَمْ يَكُنْ مِنَ السّٰاجِدِينَ");
        sor.Ayaat.Add("قٰالَ مٰا مَنَعَكَ أَلاّٰ تَسْجُدَ إِذْ أَمَرْتُكَ قٰالَ أَنَا خَيْرٌ مِنْهُ خَلَقْتَنِي مِنْ نٰارٍ وَ خَلَقْتَهُ مِنْ طِينٍ");
        sor.Ayaat.Add("قٰالَ فَاهْبِطْ مِنْهٰا فَمٰا يَكُونُ لَكَ أَنْ تَتَكَبَّرَ فِيهٰا فَاخْرُجْ إِنَّكَ مِنَ الصّٰاغِرِينَ");
        sor.Ayaat.Add("قٰالَ أَنْظِرْنِي إِلىٰ يَوْمِ يُبْعَثُونَ");
        sor.Ayaat.Add("قٰالَ إِنَّكَ مِنَ الْمُنْظَرِينَ");
        sor.Ayaat.Add("قٰالَ فَبِمٰا أَغْوَيْتَنِي لَأَقْعُدَنَّ لَهُمْ صِرٰاطَكَ الْمُسْتَقِيمَ");
        sor.Ayaat.Add("ثُمَّ لَآتِيَنَّهُمْ مِنْ بَيْنِ أَيْدِيهِمْ وَ مِنْ خَلْفِهِمْ وَ عَنْ أَيْمٰانِهِمْ وَ عَنْ شَمٰائِلِهِمْ وَ لاٰ تَجِدُ أَكْثَرَهُمْ شٰاكِرِينَ");
        sor.Ayaat.Add("قٰالَ اخْرُجْ مِنْهٰا مَذْؤُماً مَدْحُوراً لَمَنْ تَبِعَكَ مِنْهُمْ لَأَمْلَأَنَّ جَهَنَّمَ مِنْكُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("وَ يٰا آدَمُ اسْكُنْ أَنْتَ وَ زَوْجُكَ الْجَنَّةَ فَكُلاٰ مِنْ حَيْثُ شِئْتُمٰا وَ لاٰ تَقْرَبٰا هٰذِهِ الشَّجَرَةَ فَتَكُونٰا مِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("فَوَسْوَسَ لَهُمَا الشَّيْطٰانُ لِيُبْدِيَ لَهُمٰا مٰا وُورِيَ عَنْهُمٰا مِنْ سَوْآتِهِمٰا وَ قٰالَ مٰا نَهٰاكُمٰا رَبُّكُمٰا عَنْ هٰذِهِ الشَّجَرَةِ إِلاّٰ أَنْ تَكُونٰا مَلَكَيْنِ أَوْ تَكُونٰا مِنَ الْخٰالِدِينَ");
        sor.Ayaat.Add("وَ قٰاسَمَهُمٰا إِنِّي لَكُمٰا لَمِنَ النّٰاصِحِينَ");
        sor.Ayaat.Add("فَدَلاّٰهُمٰا بِغُرُورٍ فَلَمّٰا ذٰاقَا الشَّجَرَةَ بَدَتْ لَهُمٰا سَوْآتُهُمٰا وَ طَفِقٰا يَخْصِفٰانِ عَلَيْهِمٰا مِنْ وَرَقِ الْجَنَّةِ وَ نٰادٰاهُمٰا رَبُّهُمٰا أَ لَمْ أَنْهَكُمٰا عَنْ تِلْكُمَا الشَّجَرَةِ وَ أَقُلْ لَكُمٰا إِنَّ الشَّيْطٰانَ لَكُمٰا عَدُوٌّ مُبِينٌ");
        sor.Ayaat.Add("قٰالاٰ رَبَّنٰا ظَلَمْنٰا أَنْفُسَنٰا وَ إِنْ لَمْ تَغْفِرْ لَنٰا وَ تَرْحَمْنٰا لَنَكُونَنَّ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("قٰالَ اهْبِطُوا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ وَ لَكُمْ فِي الْأَرْضِ مُسْتَقَرٌّ وَ مَتٰاعٌ إِلىٰ حِينٍ");
        sor.Ayaat.Add("قٰالَ فِيهٰا تَحْيَوْنَ وَ فِيهٰا تَمُوتُونَ وَ مِنْهٰا تُخْرَجُونَ");
        sor.Ayaat.Add("يٰا بَنِي آدَمَ قَدْ أَنْزَلْنٰا عَلَيْكُمْ لِبٰاساً يُوٰارِي سَوْآتِكُمْ وَ رِيشاً وَ لِبٰاسُ التَّقْوىٰ ذٰلِكَ خَيْرٌ ذٰلِكَ مِنْ آيٰاتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُونَ");
        sor.Ayaat.Add("يٰا بَنِي آدَمَ لاٰ يَفْتِنَنَّكُمُ الشَّيْطٰانُ كَمٰا أَخْرَجَ أَبَوَيْكُمْ مِنَ الْجَنَّةِ يَنْزِعُ عَنْهُمٰا لِبٰاسَهُمٰا لِيُرِيَهُمٰا سَوْآتِهِمٰا إِنَّهُ يَرٰاكُمْ هُوَ وَ قَبِيلُهُ مِنْ حَيْثُ لاٰ تَرَوْنَهُمْ إِنّٰا جَعَلْنَا الشَّيٰاطِينَ أَوْلِيٰاءَ لِلَّذِينَ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ إِذٰا فَعَلُوا فٰاحِشَةً قٰالُوا وَجَدْنٰا عَلَيْهٰا آبٰاءَنٰا وَ اللّٰهُ أَمَرَنٰا بِهٰا قُلْ إِنَّ اللّٰهَ لاٰ يَأْمُرُ بِالْفَحْشٰاءِ أَ تَقُولُونَ عَلَى اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("قُلْ أَمَرَ رَبِّي بِالْقِسْطِ وَ أَقِيمُوا وُجُوهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَ ادْعُوهُ مُخْلِصِينَ لَهُ الدِّينَ كَمٰا بَدَأَكُمْ تَعُودُونَ");
        sor.Ayaat.Add("فَرِيقاً هَدىٰ وَ فَرِيقاً حَقَّ عَلَيْهِمُ الضَّلاٰلَةُ إِنَّهُمُ اتَّخَذُوا الشَّيٰاطِينَ أَوْلِيٰاءَ مِنْ دُونِ اللّٰهِ وَ يَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ");
        sor.Ayaat.Add("يٰا بَنِي آدَمَ خُذُوا زِينَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَ كُلُوا وَ اشْرَبُوا وَ لاٰ تُسْرِفُوا إِنَّهُ لاٰ يُحِبُّ الْمُسْرِفِينَ");
        sor.Ayaat.Add("قُلْ مَنْ حَرَّمَ زِينَةَ اللّٰهِ الَّتِي أَخْرَجَ لِعِبٰادِهِ وَ الطَّيِّبٰاتِ مِنَ الرِّزْقِ قُلْ هِيَ لِلَّذِينَ آمَنُوا فِي الْحَيٰاةِ الدُّنْيٰا خٰالِصَةً يَوْمَ الْقِيٰامَةِ كَذٰلِكَ نُفَصِّلُ الْآيٰاتِ لِقَوْمٍ يَعْلَمُونَ");
        sor.Ayaat.Add("قُلْ إِنَّمٰا حَرَّمَ رَبِّيَ الْفَوٰاحِشَ مٰا ظَهَرَ مِنْهٰا وَ مٰا بَطَنَ وَ الْإِثْمَ وَ الْبَغْيَ بِغَيْرِ الْحَقِّ وَ أَنْ تُشْرِكُوا بِاللّٰهِ مٰا لَمْ يُنَزِّلْ بِهِ سُلْطٰاناً وَ أَنْ تَقُولُوا عَلَى اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ لِكُلِّ أُمَّةٍ أَجَلٌ فَإِذٰا جٰاءَ أَجَلُهُمْ لاٰ يَسْتَأْخِرُونَ سٰاعَةً وَ لاٰ يَسْتَقْدِمُونَ");
        sor.Ayaat.Add("يٰا بَنِي آدَمَ إِمّٰا يَأْتِيَنَّكُمْ رُسُلٌ مِنْكُمْ يَقُصُّونَ عَلَيْكُمْ آيٰاتِي فَمَنِ اتَّقىٰ وَ أَصْلَحَ فَلاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا وَ اسْتَكْبَرُوا عَنْهٰا أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("فَمَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً أَوْ كَذَّبَ بِآيٰاتِهِ أُولٰئِكَ يَنٰالُهُمْ نَصِيبُهُمْ مِنَ الْكِتٰابِ حَتّٰى إِذٰا جٰاءَتْهُمْ رُسُلُنٰا يَتَوَفَّوْنَهُمْ قٰالُوا أَيْنَ مٰا كُنْتُمْ تَدْعُونَ مِنْ دُونِ اللّٰهِ قٰالُوا ضَلُّوا عَنّٰا وَ شَهِدُوا عَلىٰ أَنْفُسِهِمْ أَنَّهُمْ كٰانُوا كٰافِرِينَ");
        sor.Ayaat.Add("قٰالَ ادْخُلُوا فِي أُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِنَ الْجِنِّ وَ الْإِنْسِ فِي النّٰارِ كُلَّمٰا دَخَلَتْ أُمَّةٌ لَعَنَتْ أُخْتَهٰا حَتّٰى إِذَا ادّٰارَكُوا فِيهٰا جَمِيعاً قٰالَتْ أُخْرٰاهُمْ لِأُولاٰهُمْ رَبَّنٰا هٰؤُلاٰءِ أَضَلُّونٰا فَآتِهِمْ عَذٰاباً ضِعْفاً مِنَ النّٰارِ قٰالَ لِكُلٍّ ضِعْفٌ وَ لٰكِنْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ قٰالَتْ أُولاٰهُمْ لِأُخْرٰاهُمْ فَمٰا كٰانَ لَكُمْ عَلَيْنٰا مِنْ فَضْلٍ فَذُوقُوا الْعَذٰابَ بِمٰا كُنْتُمْ تَكْسِبُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا وَ اسْتَكْبَرُوا عَنْهٰا لاٰ تُفَتَّحُ لَهُمْ أَبْوٰابُ السَّمٰاءِ وَ لاٰ يَدْخُلُونَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ فِي سَمِّ الْخِيٰاطِ وَ كَذٰلِكَ نَجْزِي الْمُجْرِمِينَ");
        sor.Ayaat.Add("لَهُمْ مِنْ جَهَنَّمَ مِهٰادٌ وَ مِنْ فَوْقِهِمْ غَوٰاشٍ وَ كَذٰلِكَ نَجْزِي الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ لاٰ نُكَلِّفُ نَفْساً إِلاّٰ وُسْعَهٰا أُولٰئِكَ أَصْحٰابُ الْجَنَّةِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ نَزَعْنٰا مٰا فِي صُدُورِهِمْ مِنْ غِلٍّ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهٰارُ وَ قٰالُوا الْحَمْدُ لِلّٰهِ الَّذِي هَدٰانٰا لِهٰذٰا وَ مٰا كُنّٰا لِنَهْتَدِيَ لَوْ لاٰ أَنْ هَدٰانَا اللّٰهُ لَقَدْ جٰاءَتْ رُسُلُ رَبِّنٰا بِالْحَقِّ وَ نُودُوا أَنْ تِلْكُمُ الْجَنَّةُ أُورِثْتُمُوهٰا بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("وَ نٰادىٰ أَصْحٰابُ الْجَنَّةِ أَصْحٰابَ النّٰارِ أَنْ قَدْ وَجَدْنٰا مٰا وَعَدَنٰا رَبُّنٰا حَقًّا فَهَلْ وَجَدْتُمْ مٰا وَعَدَ رَبُّكُمْ حَقًّا قٰالُوا نَعَمْ فَأَذَّنَ مُؤَذِّنٌ بَيْنَهُمْ أَنْ لَعْنَةُ اللّٰهِ عَلَى الظّٰالِمِينَ");
        sor.Ayaat.Add("الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ وَ يَبْغُونَهٰا عِوَجاً وَ هُمْ بِالْآخِرَةِ كٰافِرُونَ");
        sor.Ayaat.Add("وَ بَيْنَهُمٰا حِجٰابٌ وَ عَلَى الْأَعْرٰافِ رِجٰالٌ يَعْرِفُونَ كُلاًّ بِسِيمٰاهُمْ وَ نٰادَوْا أَصْحٰابَ الْجَنَّةِ أَنْ سَلاٰمٌ عَلَيْكُمْ لَمْ يَدْخُلُوهٰا وَ هُمْ يَطْمَعُونَ");
        sor.Ayaat.Add("وَ إِذٰا صُرِفَتْ أَبْصٰارُهُمْ تِلْقٰاءَ أَصْحٰابِ النّٰارِ قٰالُوا رَبَّنٰا لاٰ تَجْعَلْنٰا مَعَ الْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ نٰادىٰ أَصْحٰابُ الْأَعْرٰافِ رِجٰالاً يَعْرِفُونَهُمْ بِسِيمٰاهُمْ قٰالُوا مٰا أَغْنىٰ عَنْكُمْ جَمْعُكُمْ وَ مٰا كُنْتُمْ تَسْتَكْبِرُونَ");
        sor.Ayaat.Add("أَ هٰؤُلاٰءِ الَّذِينَ أَقْسَمْتُمْ لاٰ يَنٰالُهُمُ اللّٰهُ بِرَحْمَةٍ ادْخُلُوا الْجَنَّةَ لاٰ خَوْفٌ عَلَيْكُمْ وَ لاٰ أَنْتُمْ تَحْزَنُونَ");
        sor.Ayaat.Add("وَ نٰادىٰ أَصْحٰابُ النّٰارِ أَصْحٰابَ الْجَنَّةِ أَنْ أَفِيضُوا عَلَيْنٰا مِنَ الْمٰاءِ أَوْ مِمّٰا رَزَقَكُمُ اللّٰهُ قٰالُوا إِنَّ اللّٰهَ حَرَّمَهُمٰا عَلَى الْكٰافِرِينَ");
        sor.Ayaat.Add("الَّذِينَ اتَّخَذُوا دِينَهُمْ لَهْواً وَ لَعِباً وَ غَرَّتْهُمُ الْحَيٰاةُ الدُّنْيٰا فَالْيَوْمَ نَنْسٰاهُمْ كَمٰا نَسُوا لِقٰاءَ يَوْمِهِمْ هٰذٰا وَ مٰا كٰانُوا بِآيٰاتِنٰا يَجْحَدُونَ");
        sor.Ayaat.Add("وَ لَقَدْ جِئْنٰاهُمْ بِكِتٰابٍ فَصَّلْنٰاهُ عَلىٰ عِلْمٍ هُدىً وَ رَحْمَةً لِقَوْمٍ يُؤْمِنُونَ");
        sor.Ayaat.Add("هَلْ يَنْظُرُونَ إِلاّٰ تَأْوِيلَهُ يَوْمَ يَأْتِي تَأْوِيلُهُ يَقُولُ الَّذِينَ نَسُوهُ مِنْ قَبْلُ قَدْ جٰاءَتْ رُسُلُ رَبِّنٰا بِالْحَقِّ فَهَلْ لَنٰا مِنْ شُفَعٰاءَ فَيَشْفَعُوا لَنٰا أَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِي كُنّٰا نَعْمَلُ قَدْ خَسِرُوا أَنْفُسَهُمْ وَ ضَلَّ عَنْهُمْ مٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("إِنَّ رَبَّكُمُ اللّٰهُ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ فِي سِتَّةِ أَيّٰامٍ ثُمَّ اسْتَوىٰ عَلَى الْعَرْشِ يُغْشِي اللَّيْلَ النَّهٰارَ يَطْلُبُهُ حَثِيثاً وَ الشَّمْسَ وَ الْقَمَرَ وَ النُّجُومَ مُسَخَّرٰاتٍ بِأَمْرِهِ أَلاٰ لَهُ الْخَلْقُ وَ الْأَمْرُ تَبٰارَكَ اللّٰهُ رَبُّ الْعٰالَمِينَ");
        sor.Ayaat.Add("ادْعُوا رَبَّكُمْ تَضَرُّعاً وَ خُفْيَةً إِنَّهُ لاٰ يُحِبُّ الْمُعْتَدِينَ");
        sor.Ayaat.Add("وَ لاٰ تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلاٰحِهٰا وَ ادْعُوهُ خَوْفاً وَ طَمَعاً إِنَّ رَحْمَتَ اللّٰهِ قَرِيبٌ مِنَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي يُرْسِلُ الرِّيٰاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِهِ حَتّٰى إِذٰا أَقَلَّتْ سَحٰاباً ثِقٰالاً سُقْنٰاهُ لِبَلَدٍ مَيِّتٍ فَأَنْزَلْنٰا بِهِ الْمٰاءَ فَأَخْرَجْنٰا بِهِ مِنْ كُلِّ الثَّمَرٰاتِ كَذٰلِكَ نُخْرِجُ الْمَوْتىٰ لَعَلَّكُمْ تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ الْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبٰاتُهُ بِإِذْنِ رَبِّهِ وَ الَّذِي خَبُثَ لاٰ يَخْرُجُ إِلاّٰ نَكِداً كَذٰلِكَ نُصَرِّفُ الْآيٰاتِ لِقَوْمٍ يَشْكُرُونَ");
        sor.Ayaat.Add("لَقَدْ أَرْسَلْنٰا نُوحاً إِلىٰ قَوْمِهِ فَقٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ إِنِّي أَخٰافُ عَلَيْكُمْ عَذٰابَ يَوْمٍ عَظِيمٍ");
        sor.Ayaat.Add("قٰالَ الْمَلَأُ مِنْ قَوْمِهِ إِنّٰا لَنَرٰاكَ فِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("قٰالَ يٰا قَوْمِ لَيْسَ بِي ضَلاٰلَةٌ وَ لٰكِنِّي رَسُولٌ مِنْ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("أُبَلِّغُكُمْ رِسٰالاٰتِ رَبِّي وَ أَنْصَحُ لَكُمْ وَ أَعْلَمُ مِنَ اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("أَ وَ عَجِبْتُمْ أَنْ جٰاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلىٰ رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَ لِتَتَّقُوا وَ لَعَلَّكُمْ تُرْحَمُونَ");
        sor.Ayaat.Add("فَكَذَّبُوهُ فَأَنْجَيْنٰاهُ وَ الَّذِينَ مَعَهُ فِي الْفُلْكِ وَ أَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا إِنَّهُمْ كٰانُوا قَوْماً عَمِينَ");
        sor.Ayaat.Add("وَ إِلىٰ عٰادٍ أَخٰاهُمْ هُوداً قٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ أَ فَلاٰ تَتَّقُونَ");
        sor.Ayaat.Add("قٰالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ إِنّٰا لَنَرٰاكَ فِي سَفٰاهَةٍ وَ إِنّٰا لَنَظُنُّكَ مِنَ الْكٰاذِبِينَ");
        sor.Ayaat.Add("قٰالَ يٰا قَوْمِ لَيْسَ بِي سَفٰاهَةٌ وَ لٰكِنِّي رَسُولٌ مِنْ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("أُبَلِّغُكُمْ رِسٰالاٰتِ رَبِّي وَ أَنَا لَكُمْ نٰاصِحٌ أَمِينٌ");
        sor.Ayaat.Add("أَ وَ عَجِبْتُمْ أَنْ جٰاءَكُمْ ذِكْرٌ مِنْ رَبِّكُمْ عَلىٰ رَجُلٍ مِنْكُمْ لِيُنْذِرَكُمْ وَ اذْكُرُوا إِذْ جَعَلَكُمْ خُلَفٰاءَ مِنْ بَعْدِ قَوْمِ نُوحٍ وَ زٰادَكُمْ فِي الْخَلْقِ بَصْطَةً فَاذْكُرُوا آلاٰءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("قٰالُوا أَ جِئْتَنٰا لِنَعْبُدَ اللّٰهَ وَحْدَهُ وَ نَذَرَ مٰا كٰانَ يَعْبُدُ آبٰاؤُنٰا فَأْتِنٰا بِمٰا تَعِدُنٰا إِنْ كُنْتَ مِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("قٰالَ قَدْ وَقَعَ عَلَيْكُمْ مِنْ رَبِّكُمْ رِجْسٌ وَ غَضَبٌ أَ تُجٰادِلُونَنِي فِي أَسْمٰاءٍ سَمَّيْتُمُوهٰا أَنْتُمْ وَ آبٰاؤُكُمْ مٰا نَزَّلَ اللّٰهُ بِهٰا مِنْ سُلْطٰانٍ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ");
        sor.Ayaat.Add("فَأَنْجَيْنٰاهُ وَ الَّذِينَ مَعَهُ بِرَحْمَةٍ مِنّٰا وَ قَطَعْنٰا دٰابِرَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا وَ مٰا كٰانُوا مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ إِلىٰ ثَمُودَ أَخٰاهُمْ صٰالِحاً قٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ قَدْ جٰاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ هٰذِهِ نٰاقَةُ اللّٰهِ لَكُمْ آيَةً فَذَرُوهٰا تَأْكُلْ فِي أَرْضِ اللّٰهِ وَ لاٰ تَمَسُّوهٰا بِسُوءٍ فَيَأْخُذَكُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ اذْكُرُوا إِذْ جَعَلَكُمْ خُلَفٰاءَ مِنْ بَعْدِ عٰادٍ وَ بَوَّأَكُمْ فِي الْأَرْضِ تَتَّخِذُونَ مِنْ سُهُولِهٰا قُصُوراً وَ تَنْحِتُونَ الْجِبٰالَ بُيُوتاً فَاذْكُرُوا آلاٰءَ اللّٰهِ وَ لاٰ تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ");
        sor.Ayaat.Add("قٰالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لِلَّذِينَ اسْتُضْعِفُوا لِمَنْ آمَنَ مِنْهُمْ أَ تَعْلَمُونَ أَنَّ صٰالِحاً مُرْسَلٌ مِنْ رَبِّهِ قٰالُوا إِنّٰا بِمٰا أُرْسِلَ بِهِ مُؤْمِنُونَ");
        sor.Ayaat.Add("قٰالَ الَّذِينَ اسْتَكْبَرُوا إِنّٰا بِالَّذِي آمَنْتُمْ بِهِ كٰافِرُونَ");
        sor.Ayaat.Add("فَعَقَرُوا النّٰاقَةَ وَ عَتَوْا عَنْ أَمْرِ رَبِّهِمْ وَ قٰالُوا يٰا صٰالِحُ ائْتِنٰا بِمٰا تَعِدُنٰا إِنْ كُنْتَ مِنَ الْمُرْسَلِينَ");
        sor.Ayaat.Add("فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دٰارِهِمْ جٰاثِمِينَ");
        sor.Ayaat.Add("فَتَوَلّٰى عَنْهُمْ وَ قٰالَ يٰا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسٰالَةَ رَبِّي وَ نَصَحْتُ لَكُمْ وَ لٰكِنْ لاٰ تُحِبُّونَ النّٰاصِحِينَ");
        sor.Ayaat.Add("وَ لُوطاً إِذْ قٰالَ لِقَوْمِهِ أَ تَأْتُونَ الْفٰاحِشَةَ مٰا سَبَقَكُمْ بِهٰا مِنْ أَحَدٍ مِنَ الْعٰالَمِينَ");
        sor.Ayaat.Add("إِنَّكُمْ لَتَأْتُونَ الرِّجٰالَ شَهْوَةً مِنْ دُونِ النِّسٰاءِ بَلْ أَنْتُمْ قَوْمٌ مُسْرِفُونَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ جَوٰابَ قَوْمِهِ إِلاّٰ أَنْ قٰالُوا أَخْرِجُوهُمْ مِنْ قَرْيَتِكُمْ إِنَّهُمْ أُنٰاسٌ يَتَطَهَّرُونَ");
        sor.Ayaat.Add("فَأَنْجَيْنٰاهُ وَ أَهْلَهُ إِلاَّ امْرَأَتَهُ كٰانَتْ مِنَ الْغٰابِرِينَ");
        sor.Ayaat.Add("وَ أَمْطَرْنٰا عَلَيْهِمْ مَطَراً فَانْظُرْ كَيْفَ كٰانَ عٰاقِبَةُ الْمُجْرِمِينَ");
        sor.Ayaat.Add("وَ إِلىٰ مَدْيَنَ أَخٰاهُمْ شُعَيْباً قٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ قَدْ جٰاءَتْكُمْ بَيِّنَةٌ مِنْ رَبِّكُمْ فَأَوْفُوا الْكَيْلَ وَ الْمِيزٰانَ وَ لاٰ تَبْخَسُوا النّٰاسَ أَشْيٰاءَهُمْ وَ لاٰ تُفْسِدُوا فِي الْأَرْضِ بَعْدَ إِصْلاٰحِهٰا ذٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ لاٰ تَقْعُدُوا بِكُلِّ صِرٰاطٍ تُوعِدُونَ وَ تَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ مَنْ آمَنَ بِهِ وَ تَبْغُونَهٰا عِوَجاً وَ اذْكُرُوا إِذْ كُنْتُمْ قَلِيلاً فَكَثَّرَكُمْ وَ انْظُرُوا كَيْفَ كٰانَ عٰاقِبَةُ الْمُفْسِدِينَ");
        sor.Ayaat.Add("وَ إِنْ كٰانَ طٰائِفَةٌ مِنْكُمْ آمَنُوا بِالَّذِي أُرْسِلْتُ بِهِ وَ طٰائِفَةٌ لَمْ يُؤْمِنُوا فَاصْبِرُوا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنٰا وَ هُوَ خَيْرُ الْحٰاكِمِينَ");
        sor.Ayaat.Add("قٰالَ الْمَلَأُ الَّذِينَ اسْتَكْبَرُوا مِنْ قَوْمِهِ لَنُخْرِجَنَّكَ يٰا شُعَيْبُ وَ الَّذِينَ آمَنُوا مَعَكَ مِنْ قَرْيَتِنٰا أَوْ لَتَعُودُنَّ فِي مِلَّتِنٰا قٰالَ أَ وَ لَوْ كُنّٰا كٰارِهِينَ");
        sor.Ayaat.Add("قَدِ افْتَرَيْنٰا عَلَى اللّٰهِ كَذِباً إِنْ عُدْنٰا فِي مِلَّتِكُمْ بَعْدَ إِذْ نَجّٰانَا اللّٰهُ مِنْهٰا وَ مٰا يَكُونُ لَنٰا أَنْ نَعُودَ فِيهٰا إِلاّٰ أَنْ يَشٰاءَ اللّٰهُ رَبُّنٰا وَسِعَ رَبُّنٰا كُلَّ شَيْءٍ عِلْماً عَلَى اللّٰهِ تَوَكَّلْنٰا رَبَّنَا افْتَحْ بَيْنَنٰا وَ بَيْنَ قَوْمِنٰا بِالْحَقِّ وَ أَنْتَ خَيْرُ الْفٰاتِحِينَ");
        sor.Ayaat.Add("وَ قٰالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ لَئِنِ اتَّبَعْتُمْ شُعَيْباً إِنَّكُمْ إِذاً لَخٰاسِرُونَ");
        sor.Ayaat.Add("فَأَخَذَتْهُمُ الرَّجْفَةُ فَأَصْبَحُوا فِي دٰارِهِمْ جٰاثِمِينَ");
        sor.Ayaat.Add("الَّذِينَ كَذَّبُوا شُعَيْباً كَأَنْ لَمْ يَغْنَوْا فِيهَا الَّذِينَ كَذَّبُوا شُعَيْباً كٰانُوا هُمُ الْخٰاسِرِينَ");
        sor.Ayaat.Add("فَتَوَلّٰى عَنْهُمْ وَ قٰالَ يٰا قَوْمِ لَقَدْ أَبْلَغْتُكُمْ رِسٰالاٰتِ رَبِّي وَ نَصَحْتُ لَكُمْ فَكَيْفَ آسىٰ عَلىٰ قَوْمٍ كٰافِرِينَ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا فِي قَرْيَةٍ مِنْ نَبِيٍّ إِلاّٰ أَخَذْنٰا أَهْلَهٰا بِالْبَأْسٰاءِ وَ الضَّرّٰاءِ لَعَلَّهُمْ يَضَّرَّعُونَ");
        sor.Ayaat.Add("ثُمَّ بَدَّلْنٰا مَكٰانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَ قٰالُوا قَدْ مَسَّ آبٰاءَنَا الضَّرّٰاءُ وَ السَّرّٰاءُ فَأَخَذْنٰاهُمْ بَغْتَةً وَ هُمْ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("وَ لَوْ أَنَّ أَهْلَ الْقُرىٰ آمَنُوا وَ اتَّقَوْا لَفَتَحْنٰا عَلَيْهِمْ بَرَكٰاتٍ مِنَ السَّمٰاءِ وَ الْأَرْضِ وَ لٰكِنْ كَذَّبُوا فَأَخَذْنٰاهُمْ بِمٰا كٰانُوا يَكْسِبُونَ");
        sor.Ayaat.Add("أَ فَأَمِنَ أَهْلُ الْقُرىٰ أَنْ يَأْتِيَهُمْ بَأْسُنٰا بَيٰاتاً وَ هُمْ نٰائِمُونَ");
        sor.Ayaat.Add("أَ وَ أَمِنَ أَهْلُ الْقُرىٰ أَنْ يَأْتِيَهُمْ بَأْسُنٰا ضُحًى وَ هُمْ يَلْعَبُونَ");
        sor.Ayaat.Add("أَ فَأَمِنُوا مَكْرَ اللّٰهِ فَلاٰ يَأْمَنُ مَكْرَ اللّٰهِ إِلاَّ الْقَوْمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("أَ وَ لَمْ يَهْدِ لِلَّذِينَ يَرِثُونَ الْأَرْضَ مِنْ بَعْدِ أَهْلِهٰا أَنْ لَوْ نَشٰاءُ أَصَبْنٰاهُمْ بِذُنُوبِهِمْ وَ نَطْبَعُ عَلىٰ قُلُوبِهِمْ فَهُمْ لاٰ يَسْمَعُونَ");
        sor.Ayaat.Add("تِلْكَ الْقُرىٰ نَقُصُّ عَلَيْكَ مِنْ أَنْبٰائِهٰا وَ لَقَدْ جٰاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰاتِ فَمٰا كٰانُوا لِيُؤْمِنُوا بِمٰا كَذَّبُوا مِنْ قَبْلُ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلىٰ قُلُوبِ الْكٰافِرِينَ");
        sor.Ayaat.Add("وَ مٰا وَجَدْنٰا لِأَكْثَرِهِمْ مِنْ عَهْدٍ وَ إِنْ وَجَدْنٰا أَكْثَرَهُمْ لَفٰاسِقِينَ");
        sor.Ayaat.Add("ثُمَّ بَعَثْنٰا مِنْ بَعْدِهِمْ مُوسىٰ بِآيٰاتِنٰا إِلىٰ فِرْعَوْنَ وَ مَلاَئِهِ فَظَلَمُوا بِهٰا فَانْظُرْ كَيْفَ كٰانَ عٰاقِبَةُ الْمُفْسِدِينَ");
        sor.Ayaat.Add("وَ قٰالَ مُوسىٰ يٰا فِرْعَوْنُ إِنِّي رَسُولٌ مِنْ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("حَقِيقٌ عَلىٰ أَنْ لاٰ أَقُولَ عَلَى اللّٰهِ إِلاَّ الْحَقَّ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِنْ رَبِّكُمْ فَأَرْسِلْ مَعِيَ بَنِي إِسْرٰائِيلَ");
        sor.Ayaat.Add("قٰالَ إِنْ كُنْتَ جِئْتَ بِآيَةٍ فَأْتِ بِهٰا إِنْ كُنْتَ مِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("فَأَلْقىٰ عَصٰاهُ فَإِذٰا هِيَ ثُعْبٰانٌ مُبِينٌ");
        sor.Ayaat.Add("وَ نَزَعَ يَدَهُ فَإِذٰا هِيَ بَيْضٰاءُ لِلنّٰاظِرِينَ");
        sor.Ayaat.Add("قٰالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ إِنَّ هٰذٰا لَسٰاحِرٌ عَلِيمٌ");
        sor.Ayaat.Add("يُرِيدُ أَنْ يُخْرِجَكُمْ مِنْ أَرْضِكُمْ فَمٰاذٰا تَأْمُرُونَ");
        sor.Ayaat.Add("قٰالُوا أَرْجِهْ وَ أَخٰاهُ وَ أَرْسِلْ فِي الْمَدٰائِنِ حٰاشِرِينَ");
        sor.Ayaat.Add("يَأْتُوكَ بِكُلِّ سٰاحِرٍ عَلِيمٍ");
        sor.Ayaat.Add("وَ جٰاءَ السَّحَرَةُ فِرْعَوْنَ قٰالُوا إِنَّ لَنٰا لَأَجْراً إِنْ كُنّٰا نَحْنُ الْغٰالِبِينَ");
        sor.Ayaat.Add("قٰالَ نَعَمْ وَ إِنَّكُمْ لَمِنَ الْمُقَرَّبِينَ");
        sor.Ayaat.Add("قٰالُوا يٰا مُوسىٰ إِمّٰا أَنْ تُلْقِيَ وَ إِمّٰا أَنْ نَكُونَ نَحْنُ الْمُلْقِينَ");
        sor.Ayaat.Add("قٰالَ أَلْقُوا فَلَمّٰا أَلْقَوْا سَحَرُوا أَعْيُنَ النّٰاسِ وَ اسْتَرْهَبُوهُمْ وَ جٰاؤُ بِسِحْرٍ عَظِيمٍ");
        sor.Ayaat.Add("وَ أَوْحَيْنٰا إِلىٰ مُوسىٰ أَنْ أَلْقِ عَصٰاكَ فَإِذٰا هِيَ تَلْقَفُ مٰا يَأْفِكُونَ");
        sor.Ayaat.Add("فَوَقَعَ الْحَقُّ وَ بَطَلَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("فَغُلِبُوا هُنٰالِكَ وَ انْقَلَبُوا صٰاغِرِينَ");
        sor.Ayaat.Add("وَ أُلْقِيَ السَّحَرَةُ سٰاجِدِينَ");
        sor.Ayaat.Add("قٰالُوا آمَنّٰا بِرَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("رَبِّ مُوسىٰ وَ هٰارُونَ");
        sor.Ayaat.Add("قٰالَ فِرْعَوْنُ آمَنْتُمْ بِهِ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّ هٰذٰا لَمَكْرٌ مَكَرْتُمُوهُ فِي الْمَدِينَةِ لِتُخْرِجُوا مِنْهٰا أَهْلَهٰا فَسَوْفَ تَعْلَمُونَ");
        sor.Ayaat.Add("لَأُقَطِّعَنَّ أَيْدِيَكُمْ وَ أَرْجُلَكُمْ مِنْ خِلاٰفٍ ثُمَّ لَأُصَلِّبَنَّكُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("قٰالُوا إِنّٰا إِلىٰ رَبِّنٰا مُنْقَلِبُونَ");
        sor.Ayaat.Add("وَ مٰا تَنْقِمُ مِنّٰا إِلاّٰ أَنْ آمَنّٰا بِآيٰاتِ رَبِّنٰا لَمّٰا جٰاءَتْنٰا رَبَّنٰا أَفْرِغْ عَلَيْنٰا صَبْراً وَ تَوَفَّنٰا مُسْلِمِينَ");
        sor.Ayaat.Add("وَ قٰالَ الْمَلَأُ مِنْ قَوْمِ فِرْعَوْنَ أَ تَذَرُ مُوسىٰ وَ قَوْمَهُ لِيُفْسِدُوا فِي الْأَرْضِ وَ يَذَرَكَ وَ آلِهَتَكَ قٰالَ سَنُقَتِّلُ أَبْنٰاءَهُمْ وَ نَسْتَحْيِي نِسٰاءَهُمْ وَ إِنّٰا فَوْقَهُمْ قٰاهِرُونَ");
        sor.Ayaat.Add("قٰالَ مُوسىٰ لِقَوْمِهِ اسْتَعِينُوا بِاللّٰهِ وَ اصْبِرُوا إِنَّ الْأَرْضَ لِلّٰهِ يُورِثُهٰا مَنْ يَشٰاءُ مِنْ عِبٰادِهِ وَ الْعٰاقِبَةُ لِلْمُتَّقِينَ");
        sor.Ayaat.Add("قٰالُوا أُوذِينٰا مِنْ قَبْلِ أَنْ تَأْتِيَنٰا وَ مِنْ بَعْدِ مٰا جِئْتَنٰا قٰالَ عَسىٰ رَبُّكُمْ أَنْ يُهْلِكَ عَدُوَّكُمْ وَ يَسْتَخْلِفَكُمْ فِي الْأَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَخَذْنٰا آلَ فِرْعَوْنَ بِالسِّنِينَ وَ نَقْصٍ مِنَ الثَّمَرٰاتِ لَعَلَّهُمْ يَذَّكَّرُونَ");
        sor.Ayaat.Add("فَإِذٰا جٰاءَتْهُمُ الْحَسَنَةُ قٰالُوا لَنٰا هٰذِهِ وَ إِنْ تُصِبْهُمْ سَيِّئَةٌ يَطَّيَّرُوا بِمُوسىٰ وَ مَنْ مَعَهُ أَلاٰ إِنَّمٰا طٰائِرُهُمْ عِنْدَ اللّٰهِ وَ لٰكِنَّ أَكْثَرَهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ قٰالُوا مَهْمٰا تَأْتِنٰا بِهِ مِنْ آيَةٍ لِتَسْحَرَنٰا بِهٰا فَمٰا نَحْنُ لَكَ بِمُؤْمِنِينَ");
        sor.Ayaat.Add("فَأَرْسَلْنٰا عَلَيْهِمُ الطُّوفٰانَ وَ الْجَرٰادَ وَ الْقُمَّلَ وَ الضَّفٰادِعَ وَ الدَّمَ آيٰاتٍ مُفَصَّلاٰتٍ فَاسْتَكْبَرُوا وَ كٰانُوا قَوْماً مُجْرِمِينَ");
        sor.Ayaat.Add("وَ لَمّٰا وَقَعَ عَلَيْهِمُ الرِّجْزُ قٰالُوا يٰا مُوسَى ادْعُ لَنٰا رَبَّكَ بِمٰا عَهِدَ عِنْدَكَ لَئِنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَ لَنُرْسِلَنَّ مَعَكَ بَنِي إِسْرٰائِيلَ");
        sor.Ayaat.Add("فَلَمّٰا كَشَفْنٰا عَنْهُمُ الرِّجْزَ إِلىٰ أَجَلٍ هُمْ بٰالِغُوهُ إِذٰا هُمْ يَنْكُثُونَ");
        sor.Ayaat.Add("فَانْتَقَمْنٰا مِنْهُمْ فَأَغْرَقْنٰاهُمْ فِي الْيَمِّ بِأَنَّهُمْ كَذَّبُوا بِآيٰاتِنٰا وَ كٰانُوا عَنْهٰا غٰافِلِينَ");
        sor.Ayaat.Add("وَ أَوْرَثْنَا الْقَوْمَ الَّذِينَ كٰانُوا يُسْتَضْعَفُونَ مَشٰارِقَ الْأَرْضِ وَ مَغٰارِبَهَا الَّتِي بٰارَكْنٰا فِيهٰا وَ تَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنىٰ عَلىٰ بَنِي إِسْرٰائِيلَ بِمٰا صَبَرُوا وَ دَمَّرْنٰا مٰا كٰانَ يَصْنَعُ فِرْعَوْنُ وَ قَوْمُهُ وَ مٰا كٰانُوا يَعْرِشُونَ");
        sor.Ayaat.Add("وَ جٰاوَزْنٰا بِبَنِي إِسْرٰائِيلَ الْبَحْرَ فَأَتَوْا عَلىٰ قَوْمٍ يَعْكُفُونَ عَلىٰ أَصْنٰامٍ لَهُمْ قٰالُوا يٰا مُوسَى اجْعَلْ لَنٰا إِلٰهاً كَمٰا لَهُمْ آلِهَةٌ قٰالَ إِنَّكُمْ قَوْمٌ تَجْهَلُونَ");
        sor.Ayaat.Add("إِنَّ هٰؤُلاٰءِ مُتَبَّرٌ مٰا هُمْ فِيهِ وَ بٰاطِلٌ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("قٰالَ أَ غَيْرَ اللّٰهِ أَبْغِيكُمْ إِلٰهاً وَ هُوَ فَضَّلَكُمْ عَلَى الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ إِذْ أَنْجَيْنٰاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذٰابِ يُقَتِّلُونَ أَبْنٰاءَكُمْ وَ يَسْتَحْيُونَ نِسٰاءَكُمْ وَ فِي ذٰلِكُمْ بَلاٰءٌ مِنْ رَبِّكُمْ عَظِيمٌ");
        sor.Ayaat.Add("وَ وٰاعَدْنٰا مُوسىٰ ثَلاٰثِينَ لَيْلَةً وَ أَتْمَمْنٰاهٰا بِعَشْرٍ فَتَمَّ مِيقٰاتُ رَبِّهِ أَرْبَعِينَ لَيْلَةً وَ قٰالَ مُوسىٰ لِأَخِيهِ هٰارُونَ اخْلُفْنِي فِي قَوْمِي وَ أَصْلِحْ وَ لاٰ تَتَّبِعْ سَبِيلَ الْمُفْسِدِينَ");
        sor.Ayaat.Add("وَ لَمّٰا جٰاءَ مُوسىٰ لِمِيقٰاتِنٰا وَ كَلَّمَهُ رَبُّهُ قٰالَ رَبِّ أَرِنِي أَنْظُرْ إِلَيْكَ قٰالَ لَنْ تَرٰانِي وَ لٰكِنِ انْظُرْ إِلَى الْجَبَلِ فَإِنِ اسْتَقَرَّ مَكٰانَهُ فَسَوْفَ تَرٰانِي فَلَمّٰا تَجَلّٰى رَبُّهُ لِلْجَبَلِ جَعَلَهُ دَكًّا وَ خَرَّ مُوسىٰ صَعِقاً فَلَمّٰا أَفٰاقَ قٰالَ سُبْحٰانَكَ تُبْتُ إِلَيْكَ وَ أَنَا أَوَّلُ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("قٰالَ يٰا مُوسىٰ إِنِّي اصْطَفَيْتُكَ عَلَى النّٰاسِ بِرِسٰالاٰتِي وَ بِكَلاٰمِي فَخُذْ مٰا آتَيْتُكَ وَ كُنْ مِنَ الشّٰاكِرِينَ");
        sor.Ayaat.Add("وَ كَتَبْنٰا لَهُ فِي الْأَلْوٰاحِ مِنْ كُلِّ شَيْءٍ مَوْعِظَةً وَ تَفْصِيلاً لِكُلِّ شَيْءٍ فَخُذْهٰا بِقُوَّةٍ وَ أْمُرْ قَوْمَكَ يَأْخُذُوا بِأَحْسَنِهٰا سَأُرِيكُمْ دٰارَ الْفٰاسِقِينَ");
        sor.Ayaat.Add("سَأَصْرِفُ عَنْ آيٰاتِيَ الَّذِينَ يَتَكَبَّرُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ وَ إِنْ يَرَوْا كُلَّ آيَةٍ لاٰ يُؤْمِنُوا بِهٰا وَ إِنْ يَرَوْا سَبِيلَ الرُّشْدِ لاٰ يَتَّخِذُوهُ سَبِيلاً وَ إِنْ يَرَوْا سَبِيلَ الغَيِّ يَتَّخِذُوهُ سَبِيلاً ذٰلِكَ بِأَنَّهُمْ كَذَّبُوا بِآيٰاتِنٰا وَ كٰانُوا عَنْهٰا غٰافِلِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا وَ لِقٰاءِ الْآخِرَةِ حَبِطَتْ أَعْمٰالُهُمْ هَلْ يُجْزَوْنَ إِلاّٰ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ اتَّخَذَ قَوْمُ مُوسىٰ مِنْ بَعْدِهِ مِنْ حُلِيِّهِمْ عِجْلاً جَسَداً لَهُ خُوٰارٌ أَ لَمْ يَرَوْا أَنَّهُ لاٰ يُكَلِّمُهُمْ وَ لاٰ يَهْدِيهِمْ سَبِيلاً اتَّخَذُوهُ وَ كٰانُوا ظٰالِمِينَ");
        sor.Ayaat.Add("وَ لَمّٰا سُقِطَ فِي أَيْدِيهِمْ وَ رَأَوْا أَنَّهُمْ قَدْ ضَلُّوا قٰالُوا لَئِنْ لَمْ يَرْحَمْنٰا رَبُّنٰا وَ يَغْفِرْ لَنٰا لَنَكُونَنَّ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("وَ لَمّٰا رَجَعَ مُوسىٰ إِلىٰ قَوْمِهِ غَضْبٰانَ أَسِفاً قٰالَ بِئْسَمٰا خَلَفْتُمُونِي مِنْ بَعْدِي أَ عَجِلْتُمْ أَمْرَ رَبِّكُمْ وَ أَلْقَى الْأَلْوٰاحَ وَ أَخَذَ بِرَأْسِ أَخِيهِ يَجُرُّهُ إِلَيْهِ قٰالَ ابْنَ أُمَّ إِنَّ الْقَوْمَ اسْتَضْعَفُونِي وَ كٰادُوا يَقْتُلُونَنِي فَلاٰ تُشْمِتْ بِيَ الْأَعْدٰاءَ وَ لاٰ تَجْعَلْنِي مَعَ الْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("قٰالَ رَبِّ اغْفِرْ لِي وَ لِأَخِي وَ أَدْخِلْنٰا فِي رَحْمَتِكَ وَ أَنْتَ أَرْحَمُ الرّٰاحِمِينَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ اتَّخَذُوا الْعِجْلَ سَيَنٰالُهُمْ غَضَبٌ مِنْ رَبِّهِمْ وَ ذِلَّةٌ فِي الْحَيٰاةِ الدُّنْيٰا وَ كَذٰلِكَ نَجْزِي الْمُفْتَرِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ عَمِلُوا السَّيِّئٰاتِ ثُمَّ تٰابُوا مِنْ بَعْدِهٰا وَ آمَنُوا إِنَّ رَبَّكَ مِنْ بَعْدِهٰا لَغَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ لَمّٰا سَكَتَ عَنْ مُوسَى الْغَضَبُ أَخَذَ الْأَلْوٰاحَ وَ فِي نُسْخَتِهٰا هُدىً وَ رَحْمَةٌ لِلَّذِينَ هُمْ لِرَبِّهِمْ يَرْهَبُونَ");
        sor.Ayaat.Add("وَ اخْتٰارَ مُوسىٰ قَوْمَهُ سَبْعِينَ رَجُلاً لِمِيقٰاتِنٰا فَلَمّٰا أَخَذَتْهُمُ الرَّجْفَةُ قٰالَ رَبِّ لَوْ شِئْتَ أَهْلَكْتَهُمْ مِنْ قَبْلُ وَ إِيّٰايَ أَ تُهْلِكُنٰا بِمٰا فَعَلَ السُّفَهٰاءُ مِنّٰا إِنْ هِيَ إِلاّٰ فِتْنَتُكَ تُضِلُّ بِهٰا مَنْ تَشٰاءُ وَ تَهْدِي مَنْ تَشٰاءُ أَنْتَ وَلِيُّنٰا فَاغْفِرْ لَنٰا وَ ارْحَمْنٰا وَ أَنْتَ خَيْرُ الْغٰافِرِينَ");
        sor.Ayaat.Add("وَ اكْتُبْ لَنٰا فِي هٰذِهِ الدُّنْيٰا حَسَنَةً وَ فِي الْآخِرَةِ إِنّٰا هُدْنٰا إِلَيْكَ قٰالَ عَذٰابِي أُصِيبُ بِهِ مَنْ أَشٰاءُ وَ رَحْمَتِي وَسِعَتْ كُلَّ شَيْءٍ فَسَأَكْتُبُهٰا لِلَّذِينَ يَتَّقُونَ وَ يُؤْتُونَ الزَّكٰاةَ وَ الَّذِينَ هُمْ بِآيٰاتِنٰا يُؤْمِنُونَ");
        sor.Ayaat.Add("الَّذِينَ يَتَّبِعُونَ الرَّسُولَ النَّبِيَّ الْأُمِّيَّ الَّذِي يَجِدُونَهُ مَكْتُوباً عِنْدَهُمْ فِي التَّوْرٰاةِ وَ الْإِنْجِيلِ يَأْمُرُهُمْ بِالْمَعْرُوفِ وَ يَنْهٰاهُمْ عَنِ الْمُنْكَرِ وَ يُحِلُّ لَهُمُ الطَّيِّبٰاتِ وَ يُحَرِّمُ عَلَيْهِمُ الْخَبٰائِثَ وَ يَضَعُ عَنْهُمْ إِصْرَهُمْ وَ الْأَغْلاٰلَ الَّتِي كٰانَتْ عَلَيْهِمْ فَالَّذِينَ آمَنُوا بِهِ وَ عَزَّرُوهُ وَ نَصَرُوهُ وَ اتَّبَعُوا النُّورَ الَّذِي أُنْزِلَ مَعَهُ أُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("قُلْ يٰا أَيُّهَا النّٰاسُ إِنِّي رَسُولُ اللّٰهِ إِلَيْكُمْ جَمِيعاً الَّذِي لَهُ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ لاٰ إِلٰهَ إِلاّٰ هُوَ يُحيِي وَ يُمِيتُ فَآمِنُوا بِاللّٰهِ وَ رَسُولِهِ النَّبِيِّ الْأُمِّيِّ الَّذِي يُؤْمِنُ بِاللّٰهِ وَ كَلِمٰاتِهِ وَ اتَّبِعُوهُ لَعَلَّكُمْ تَهْتَدُونَ");
        sor.Ayaat.Add("وَ مِنْ قَوْمِ مُوسىٰ أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَ بِهِ يَعْدِلُونَ");
        sor.Ayaat.Add("وَ قَطَّعْنٰاهُمُ اثْنَتَيْ عَشْرَةَ أَسْبٰاطاً أُمَماً وَ أَوْحَيْنٰا إِلىٰ مُوسىٰ إِذِ اسْتَسْقٰاهُ قَوْمُهُ أَنِ اضْرِبْ بِعَصٰاكَ الْحَجَرَ فَانْبَجَسَتْ مِنْهُ اثْنَتٰا عَشْرَةَ عَيْناً قَدْ عَلِمَ كُلُّ أُنٰاسٍ مَشْرَبَهُمْ وَ ظَلَّلْنٰا عَلَيْهِمُ الْغَمٰامَ وَ أَنْزَلْنٰا عَلَيْهِمُ الْمَنَّ وَ السَّلْوىٰ كُلُوا مِنْ طَيِّبٰاتِ مٰا رَزَقْنٰاكُمْ وَ مٰا ظَلَمُونٰا وَ لٰكِنْ كٰانُوا أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("وَ إِذْ قِيلَ لَهُمُ اسْكُنُوا هٰذِهِ الْقَرْيَةَ وَ كُلُوا مِنْهٰا حَيْثُ شِئْتُمْ وَ قُولُوا حِطَّةٌ وَ ادْخُلُوا الْبٰابَ سُجَّداً نَغْفِرْ لَكُمْ خَطِيئٰاتِكُمْ سَنَزِيدُ الْمُحْسِنِينَ");
        sor.Ayaat.Add("فَبَدَّلَ الَّذِينَ ظَلَمُوا مِنْهُمْ قَوْلاً غَيْرَ الَّذِي قِيلَ لَهُمْ فَأَرْسَلْنٰا عَلَيْهِمْ رِجْزاً مِنَ السَّمٰاءِ بِمٰا كٰانُوا يَظْلِمُونَ");
        sor.Ayaat.Add("وَ سْئَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كٰانَتْ حٰاضِرَةَ الْبَحْرِ إِذْ يَعْدُونَ فِي السَّبْتِ إِذْ تَأْتِيهِمْ حِيتٰانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعاً وَ يَوْمَ لاٰ يَسْبِتُونَ لاٰ تَأْتِيهِمْ كَذٰلِكَ نَبْلُوهُمْ بِمٰا كٰانُوا يَفْسُقُونَ");
        sor.Ayaat.Add("وَ إِذْ قٰالَتْ أُمَّةٌ مِنْهُمْ لِمَ تَعِظُونَ قَوْماً اللّٰهُ مُهْلِكُهُمْ أَوْ مُعَذِّبُهُمْ عَذٰاباً شَدِيداً قٰالُوا مَعْذِرَةً إِلىٰ رَبِّكُمْ وَ لَعَلَّهُمْ يَتَّقُونَ");
        sor.Ayaat.Add("فَلَمّٰا نَسُوا مٰا ذُكِّرُوا بِهِ أَنْجَيْنَا الَّذِينَ يَنْهَوْنَ عَنِ السُّوءِ وَ أَخَذْنَا الَّذِينَ ظَلَمُوا بِعَذٰابٍ بَئِيسٍ بِمٰا كٰانُوا يَفْسُقُونَ");
        sor.Ayaat.Add("فَلَمّٰا عَتَوْا عَنْ مٰا نُهُوا عَنْهُ قُلْنٰا لَهُمْ كُونُوا قِرَدَةً خٰاسِئِينَ");
        sor.Ayaat.Add("وَ إِذْ تَأَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ إِلىٰ يَوْمِ الْقِيٰامَةِ مَنْ يَسُومُهُمْ سُوءَ الْعَذٰابِ إِنَّ رَبَّكَ لَسَرِيعُ الْعِقٰابِ وَ إِنَّهُ لَغَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ قَطَّعْنٰاهُمْ فِي الْأَرْضِ أُمَماً مِنْهُمُ الصّٰالِحُونَ وَ مِنْهُمْ دُونَ ذٰلِكَ وَ بَلَوْنٰاهُمْ بِالْحَسَنٰاتِ وَ السَّيِّئٰاتِ لَعَلَّهُمْ يَرْجِعُونَ");
        sor.Ayaat.Add("فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ وَرِثُوا الْكِتٰابَ يَأْخُذُونَ عَرَضَ هٰذَا الْأَدْنىٰ وَ يَقُولُونَ سَيُغْفَرُ لَنٰا وَ إِنْ يَأْتِهِمْ عَرَضٌ مِثْلُهُ يَأْخُذُوهُ أَ لَمْ يُؤْخَذْ عَلَيْهِمْ مِيثٰاقُ الْكِتٰابِ أَنْ لاٰ يَقُولُوا عَلَى اللّٰهِ إِلاَّ الْحَقَّ وَ دَرَسُوا مٰا فِيهِ وَ الدّٰارُ الْآخِرَةُ خَيْرٌ لِلَّذِينَ يَتَّقُونَ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ يُمَسِّكُونَ بِالْكِتٰابِ وَ أَقٰامُوا الصَّلاٰةَ إِنّٰا لاٰ نُضِيعُ أَجْرَ الْمُصْلِحِينَ");
        sor.Ayaat.Add("وَ إِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَأَنَّهُ ظُلَّةٌ وَ ظَنُّوا أَنَّهُ وٰاقِعٌ بِهِمْ خُذُوا مٰا آتَيْنٰاكُمْ بِقُوَّةٍ وَ اذْكُرُوا مٰا فِيهِ لَعَلَّكُمْ تَتَّقُونَ");
        sor.Ayaat.Add("وَ إِذْ أَخَذَ رَبُّكَ مِنْ بَنِي آدَمَ مِنْ ظُهُورِهِمْ ذُرِّيَّتَهُمْ وَ أَشْهَدَهُمْ عَلىٰ أَنْفُسِهِمْ أَ لَسْتُ بِرَبِّكُمْ قٰالُوا بَلىٰ شَهِدْنٰا أَنْ تَقُولُوا يَوْمَ الْقِيٰامَةِ إِنّٰا كُنّٰا عَنْ هٰذٰا غٰافِلِينَ");
        sor.Ayaat.Add("أَوْ تَقُولُوا إِنَّمٰا أَشْرَكَ آبٰاؤُنٰا مِنْ قَبْلُ وَ كُنّٰا ذُرِّيَّةً مِنْ بَعْدِهِمْ أَ فَتُهْلِكُنٰا بِمٰا فَعَلَ الْمُبْطِلُونَ");
        sor.Ayaat.Add("وَ كَذٰلِكَ نُفَصِّلُ الْآيٰاتِ وَ لَعَلَّهُمْ يَرْجِعُونَ");
        sor.Ayaat.Add("وَ اتْلُ عَلَيْهِمْ نَبَأَ الَّذِي آتَيْنٰاهُ آيٰاتِنٰا فَانْسَلَخَ مِنْهٰا فَأَتْبَعَهُ الشَّيْطٰانُ فَكٰانَ مِنَ الْغٰاوِينَ");
        sor.Ayaat.Add("وَ لَوْ شِئْنٰا لَرَفَعْنٰاهُ بِهٰا وَ لٰكِنَّهُ أَخْلَدَ إِلَى الْأَرْضِ وَ اتَّبَعَ هَوٰاهُ فَمَثَلُهُ كَمَثَلِ الْكَلْبِ إِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ أَوْ تَتْرُكْهُ يَلْهَثْ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُونَ");
        sor.Ayaat.Add("سٰاءَ مَثَلاً الْقَوْمُ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا وَ أَنْفُسَهُمْ كٰانُوا يَظْلِمُونَ");
        sor.Ayaat.Add("مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِي وَ مَنْ يُضْلِلْ فَأُولٰئِكَ هُمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("وَ لَقَدْ ذَرَأْنٰا لِجَهَنَّمَ كَثِيراً مِنَ الْجِنِّ وَ الْإِنْسِ لَهُمْ قُلُوبٌ لاٰ يَفْقَهُونَ بِهٰا وَ لَهُمْ أَعْيُنٌ لاٰ يُبْصِرُونَ بِهٰا وَ لَهُمْ آذٰانٌ لاٰ يَسْمَعُونَ بِهٰا أُولٰئِكَ كَالْأَنْعٰامِ بَلْ هُمْ أَضَلُّ أُولٰئِكَ هُمُ الْغٰافِلُونَ");
        sor.Ayaat.Add("وَ لِلّٰهِ الْأَسْمٰاءُ الْحُسْنىٰ فَادْعُوهُ بِهٰا وَ ذَرُوا الَّذِينَ يُلْحِدُونَ فِي أَسْمٰائِهِ سَيُجْزَوْنَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ مِمَّنْ خَلَقْنٰا أُمَّةٌ يَهْدُونَ بِالْحَقِّ وَ بِهِ يَعْدِلُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا سَنَسْتَدْرِجُهُمْ مِنْ حَيْثُ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ أُمْلِي لَهُمْ إِنَّ كَيْدِي مَتِينٌ");
        sor.Ayaat.Add("أَ وَ لَمْ يَتَفَكَّرُوا مٰا بِصٰاحِبِهِمْ مِنْ جِنَّةٍ إِنْ هُوَ إِلاّٰ نَذِيرٌ مُبِينٌ");
        sor.Ayaat.Add("أَ وَ لَمْ يَنْظُرُوا فِي مَلَكُوتِ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا خَلَقَ اللّٰهُ مِنْ شَيْءٍ وَ أَنْ عَسىٰ أَنْ يَكُونَ قَدِ اقْتَرَبَ أَجَلُهُمْ فَبِأَيِّ حَدِيثٍ بَعْدَهُ يُؤْمِنُونَ");
        sor.Ayaat.Add("مَنْ يُضْلِلِ اللّٰهُ فَلاٰ هٰادِيَ لَهُ وَ يَذَرُهُمْ فِي طُغْيٰانِهِمْ يَعْمَهُونَ");
        sor.Ayaat.Add("يَسْئَلُونَكَ عَنِ السّٰاعَةِ أَيّٰانَ مُرْسٰاهٰا قُلْ إِنَّمٰا عِلْمُهٰا عِنْدَ رَبِّي لاٰ يُجَلِّيهٰا لِوَقْتِهٰا إِلاّٰ هُوَ ثَقُلَتْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ لاٰ تَأْتِيكُمْ إِلاّٰ بَغْتَةً يَسْئَلُونَكَ كَأَنَّكَ حَفِيٌّ عَنْهٰا قُلْ إِنَّمٰا عِلْمُهٰا عِنْدَ اللّٰهِ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("قُلْ لاٰ أَمْلِكُ لِنَفْسِي نَفْعاً وَ لاٰ ضَرًّا إِلاّٰ مٰا شٰاءَ اللّٰهُ وَ لَوْ كُنْتُ أَعْلَمُ الْغَيْبَ لاَسْتَكْثَرْتُ مِنَ الْخَيْرِ وَ مٰا مَسَّنِيَ السُّوءُ إِنْ أَنَا إِلاّٰ نَذِيرٌ وَ بَشِيرٌ لِقَوْمٍ يُؤْمِنُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي خَلَقَكُمْ مِنْ نَفْسٍ وٰاحِدَةٍ وَ جَعَلَ مِنْهٰا زَوْجَهٰا لِيَسْكُنَ إِلَيْهٰا فَلَمّٰا تَغَشّٰاهٰا حَمَلَتْ حَمْلاً خَفِيفاً فَمَرَّتْ بِهِ فَلَمّٰا أَثْقَلَتْ دَعَوَا اللّٰهَ رَبَّهُمٰا لَئِنْ آتَيْتَنٰا صٰالِحاً لَنَكُونَنَّ مِنَ الشّٰاكِرِينَ");
        sor.Ayaat.Add("فَلَمّٰا آتٰاهُمٰا صٰالِحاً جَعَلاٰ لَهُ شُرَكٰاءَ فِيمٰا آتٰاهُمٰا فَتَعٰالَى اللّٰهُ عَمّٰا يُشْرِكُونَ");
        sor.Ayaat.Add("أَ يُشْرِكُونَ مٰا لاٰ يَخْلُقُ شَيْئاً وَ هُمْ يُخْلَقُونَ");
        sor.Ayaat.Add("وَ لاٰ يَسْتَطِيعُونَ لَهُمْ نَصْراً وَ لاٰ أَنْفُسَهُمْ يَنْصُرُونَ");
        sor.Ayaat.Add("وَ إِنْ تَدْعُوهُمْ إِلَى الْهُدىٰ لاٰ يَتَّبِعُوكُمْ سَوٰاءٌ عَلَيْكُمْ أَ دَعَوْتُمُوهُمْ أَمْ أَنْتُمْ صٰامِتُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ عِبٰادٌ أَمْثٰالُكُمْ فَادْعُوهُمْ فَلْيَسْتَجِيبُوا لَكُمْ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("أَ لَهُمْ أَرْجُلٌ يَمْشُونَ بِهٰا أَمْ لَهُمْ أَيْدٍ يَبْطِشُونَ بِهٰا أَمْ لَهُمْ أَعْيُنٌ يُبْصِرُونَ بِهٰا أَمْ لَهُمْ آذٰانٌ يَسْمَعُونَ بِهٰا قُلِ ادْعُوا شُرَكٰاءَكُمْ ثُمَّ كِيدُونِ فَلاٰ تُنْظِرُونِ");
        sor.Ayaat.Add("إِنَّ وَلِيِّيَ اللّٰهُ الَّذِي نَزَّلَ الْكِتٰابَ وَ هُوَ يَتَوَلَّى الصّٰالِحِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ تَدْعُونَ مِنْ دُونِهِ لاٰ يَسْتَطِيعُونَ نَصْرَكُمْ وَ لاٰ أَنْفُسَهُمْ يَنْصُرُونَ");
        sor.Ayaat.Add("وَ إِنْ تَدْعُوهُمْ إِلَى الْهُدىٰ لاٰ يَسْمَعُوا وَ تَرٰاهُمْ يَنْظُرُونَ إِلَيْكَ وَ هُمْ لاٰ يُبْصِرُونَ");
        sor.Ayaat.Add("خُذِ الْعَفْوَ وَ أْمُرْ بِالْعُرْفِ وَ أَعْرِضْ عَنِ الْجٰاهِلِينَ");
        sor.Ayaat.Add("وَ إِمّٰا يَنْزَغَنَّكَ مِنَ الشَّيْطٰانِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ إِنَّهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ اتَّقَوْا إِذٰا مَسَّهُمْ طٰائِفٌ مِنَ الشَّيْطٰانِ تَذَكَّرُوا فَإِذٰا هُمْ مُبْصِرُونَ");
        sor.Ayaat.Add("وَ إِخْوٰانُهُمْ يَمُدُّونَهُمْ فِي الغَيِّ ثُمَّ لاٰ يُقْصِرُونَ");
        sor.Ayaat.Add("وَ إِذٰا لَمْ تَأْتِهِمْ بِآيَةٍ قٰالُوا لَوْ لاٰ اجْتَبَيْتَهٰا قُلْ إِنَّمٰا أَتَّبِعُ مٰا يُوحىٰ إِلَيَّ مِنْ رَبِّي هٰذٰا بَصٰائِرُ مِنْ رَبِّكُمْ وَ هُدىً وَ رَحْمَةٌ لِقَوْمٍ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ إِذٰا قُرِئَ الْقُرْآنُ فَاسْتَمِعُوا لَهُ وَ أَنْصِتُوا لَعَلَّكُمْ تُرْحَمُونَ");
        sor.Ayaat.Add("وَ اذْكُرْ رَبَّكَ فِي نَفْسِكَ تَضَرُّعاً وَ خِيفَةً وَ دُونَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَ الْآصٰالِ وَ لاٰ تَكُنْ مِنَ الْغٰافِلِينَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ عِنْدَ رَبِّكَ لاٰ يَسْتَكْبِرُونَ عَنْ عِبٰادَتِهِ وَ يُسَبِّحُونَهُ وَ لَهُ يَسْجُدُونَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٨ - الانفال";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ يَسْئَلُونَكَ عَنِ الْأَنْفٰالِ قُلِ الْأَنْفٰالُ لِلّٰهِ وَ الرَّسُولِ فَاتَّقُوا اللّٰهَ وَ أَصْلِحُوا ذٰاتَ بَيْنِكُمْ وَ أَطِيعُوا اللّٰهَ وَ رَسُولَهُ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ إِذٰا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوبُهُمْ وَ إِذٰا تُلِيَتْ عَلَيْهِمْ آيٰاتُهُ زٰادَتْهُمْ إِيمٰاناً وَ عَلىٰ رَبِّهِمْ يَتَوَكَّلُونَ");
        sor.Ayaat.Add("الَّذِينَ يُقِيمُونَ الصَّلاٰةَ وَ مِمّٰا رَزَقْنٰاهُمْ يُنْفِقُونَ");
        sor.Ayaat.Add("أُولٰئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا لَهُمْ دَرَجٰاتٌ عِنْدَ رَبِّهِمْ وَ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ");
        sor.Ayaat.Add("كَمٰا أَخْرَجَكَ رَبُّكَ مِنْ بَيْتِكَ بِالْحَقِّ وَ إِنَّ فَرِيقاً مِنَ الْمُؤْمِنِينَ لَكٰارِهُونَ");
        sor.Ayaat.Add("يُجٰادِلُونَكَ فِي الْحَقِّ بَعْدَ مٰا تَبَيَّنَ كَأَنَّمٰا يُسٰاقُونَ إِلَى الْمَوْتِ وَ هُمْ يَنْظُرُونَ");
        sor.Ayaat.Add("وَ إِذْ يَعِدُكُمُ اللّٰهُ إِحْدَى الطّٰائِفَتَيْنِ أَنَّهٰا لَكُمْ وَ تَوَدُّونَ أَنَّ غَيْرَ ذٰاتِ الشَّوْكَةِ تَكُونُ لَكُمْ وَ يُرِيدُ اللّٰهُ أَنْ يُحِقَّ الْحَقَّ بِكَلِمٰاتِهِ وَ يَقْطَعَ دٰابِرَ الْكٰافِرِينَ");
        sor.Ayaat.Add("لِيُحِقَّ الْحَقَّ وَ يُبْطِلَ الْبٰاطِلَ وَ لَوْ كَرِهَ الْمُجْرِمُونَ");
        sor.Ayaat.Add("إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجٰابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلاٰئِكَةِ مُرْدِفِينَ");
        sor.Ayaat.Add("وَ مٰا جَعَلَهُ اللّٰهُ إِلاّٰ بُشْرىٰ وَ لِتَطْمَئِنَّ بِهِ قُلُوبُكُمْ وَ مَا النَّصْرُ إِلاّٰ مِنْ عِنْدِ اللّٰهِ إِنَّ اللّٰهَ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("إِذْ يُغَشِّيكُمُ النُّعٰاسَ أَمَنَةً مِنْهُ وَ يُنَزِّلُ عَلَيْكُمْ مِنَ السَّمٰاءِ مٰاءً لِيُطَهِّرَكُمْ بِهِ وَ يُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطٰانِ وَ لِيَرْبِطَ عَلىٰ قُلُوبِكُمْ وَ يُثَبِّتَ بِهِ الْأَقْدٰامَ");
        sor.Ayaat.Add("إِذْ يُوحِي رَبُّكَ إِلَى الْمَلاٰئِكَةِ أَنِّي مَعَكُمْ فَثَبِّتُوا الَّذِينَ آمَنُوا سَأُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ فَاضْرِبُوا فَوْقَ الْأَعْنٰاقِ وَ اضْرِبُوا مِنْهُمْ كُلَّ بَنٰانٍ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّهُمْ شَاقُّوا اللّٰهَ وَ رَسُولَهُ وَ مَنْ يُشٰاقِقِ اللّٰهَ وَ رَسُولَهُ فَإِنَّ اللّٰهَ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("ذٰلِكُمْ فَذُوقُوهُ وَ أَنَّ لِلْكٰافِرِينَ عَذٰابَ النّٰارِ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِذٰا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفاً فَلاٰ تُوَلُّوهُمُ الْأَدْبٰارَ");
        sor.Ayaat.Add("وَ مَنْ يُوَلِّهِمْ يَوْمَئِذٍ دُبُرَهُ إِلاّٰ مُتَحَرِّفاً لِقِتٰالٍ أَوْ مُتَحَيِّزاً إِلىٰ فِئَةٍ فَقَدْ بٰاءَ بِغَضَبٍ مِنَ اللّٰهِ وَ مَأْوٰاهُ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ");
        sor.Ayaat.Add("فَلَمْ تَقْتُلُوهُمْ وَ لٰكِنَّ اللّٰهَ قَتَلَهُمْ وَ مٰا رَمَيْتَ إِذْ رَمَيْتَ وَ لٰكِنَّ اللّٰهَ رَمىٰ وَ لِيُبْلِيَ الْمُؤْمِنِينَ مِنْهُ بَلاٰءً حَسَناً إِنَّ اللّٰهَ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("ذٰلِكُمْ وَ أَنَّ اللّٰهَ مُوهِنُ كَيْدِ الْكٰافِرِينَ");
        sor.Ayaat.Add("إِنْ تَسْتَفْتِحُوا فَقَدْ جٰاءَكُمُ الْفَتْحُ وَ إِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْ وَ إِنْ تَعُودُوا نَعُدْ وَ لَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْئاً وَ لَوْ كَثُرَتْ وَ أَنَّ اللّٰهَ مَعَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللّٰهَ وَ رَسُولَهُ وَ لاٰ تَوَلَّوْا عَنْهُ وَ أَنْتُمْ تَسْمَعُونَ");
        sor.Ayaat.Add("وَ لاٰ تَكُونُوا كَالَّذِينَ قٰالُوا سَمِعْنٰا وَ هُمْ لاٰ يَسْمَعُونَ");
        sor.Ayaat.Add("إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذِينَ لاٰ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ لَوْ عَلِمَ اللّٰهُ فِيهِمْ خَيْراً لَأَسْمَعَهُمْ وَ لَوْ أَسْمَعَهُمْ لَتَوَلَّوْا وَ هُمْ مُعْرِضُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اسْتَجِيبُوا لِلّٰهِ وَ لِلرَّسُولِ إِذٰا دَعٰاكُمْ لِمٰا يُحْيِيكُمْ وَ اعْلَمُوا أَنَّ اللّٰهَ يَحُولُ بَيْنَ الْمَرْءِ وَ قَلْبِهِ وَ أَنَّهُ إِلَيْهِ تُحْشَرُونَ");
        sor.Ayaat.Add("وَ اتَّقُوا فِتْنَةً لاٰ تُصِيبَنَّ الَّذِينَ ظَلَمُوا مِنْكُمْ خَاصَّةً وَ اعْلَمُوا أَنَّ اللّٰهَ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("وَ اذْكُرُوا إِذْ أَنْتُمْ قَلِيلٌ مُسْتَضْعَفُونَ فِي الْأَرْضِ تَخٰافُونَ أَنْ يَتَخَطَّفَكُمُ النّٰاسُ فَآوٰاكُمْ وَ أَيَّدَكُمْ بِنَصْرِهِ وَ رَزَقَكُمْ مِنَ الطَّيِّبٰاتِ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَخُونُوا اللّٰهَ وَ الرَّسُولَ وَ تَخُونُوا أَمٰانٰاتِكُمْ وَ أَنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ اعْلَمُوا أَنَّمٰا أَمْوٰالُكُمْ وَ أَوْلاٰدُكُمْ فِتْنَةٌ وَ أَنَّ اللّٰهَ عِنْدَهُ أَجْرٌ عَظِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَكُمْ فُرْقٰاناً وَ يُكَفِّرْ عَنْكُمْ سَيِّئٰاتِكُمْ وَ يَغْفِرْ لَكُمْ وَ اللّٰهُ ذُو الْفَضْلِ الْعَظِيمِ");
        sor.Ayaat.Add("وَ إِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا لِيُثْبِتُوكَ أَوْ يَقْتُلُوكَ أَوْ يُخْرِجُوكَ وَ يَمْكُرُونَ وَ يَمْكُرُ اللّٰهُ وَ اللّٰهُ خَيْرُ الْمٰاكِرِينَ");
        sor.Ayaat.Add("وَ إِذٰا تُتْلىٰ عَلَيْهِمْ آيٰاتُنٰا قٰالُوا قَدْ سَمِعْنٰا لَوْ نَشٰاءُ لَقُلْنٰا مِثْلَ هٰذٰا إِنْ هٰذٰا إِلاّٰ أَسٰاطِيرُ الْأَوَّلِينَ");
        sor.Ayaat.Add("وَ إِذْ قٰالُوا اللّٰهُمَّ إِنْ كٰانَ هٰذٰا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَأَمْطِرْ عَلَيْنٰا حِجٰارَةً مِنَ السَّمٰاءِ أَوِ ائْتِنٰا بِعَذٰابٍ أَلِيمٍ");
        sor.Ayaat.Add("وَ مٰا كٰانَ اللّٰهُ لِيُعَذِّبَهُمْ وَ أَنْتَ فِيهِمْ وَ مٰا كٰانَ اللّٰهُ مُعَذِّبَهُمْ وَ هُمْ يَسْتَغْفِرُونَ");
        sor.Ayaat.Add("وَ مٰا لَهُمْ أَلاّٰ يُعَذِّبَهُمُ اللّٰهُ وَ هُمْ يَصُدُّونَ عَنِ الْمَسْجِدِ الْحَرٰامِ وَ مٰا كٰانُوا أَوْلِيٰاءَهُ إِنْ أَوْلِيٰاؤُهُ إِلاَّ الْمُتَّقُونَ وَ لٰكِنَّ أَكْثَرَهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ صَلاٰتُهُمْ عِنْدَ الْبَيْتِ إِلاّٰ مُكٰاءً وَ تَصْدِيَةً فَذُوقُوا الْعَذٰابَ بِمٰا كُنْتُمْ تَكْفُرُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا يُنْفِقُونَ أَمْوٰالَهُمْ لِيَصُدُّوا عَنْ سَبِيلِ اللّٰهِ فَسَيُنْفِقُونَهٰا ثُمَّ تَكُونُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُونَ وَ الَّذِينَ كَفَرُوا إِلىٰ جَهَنَّمَ يُحْشَرُونَ");
        sor.Ayaat.Add("لِيَمِيزَ اللّٰهُ الْخَبِيثَ مِنَ الطَّيِّبِ وَ يَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلىٰ بَعْضٍ فَيَرْكُمَهُ جَمِيعاً فَيَجْعَلَهُ فِي جَهَنَّمَ أُولٰئِكَ هُمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("قُلْ لِلَّذِينَ كَفَرُوا إِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مٰا قَدْ سَلَفَ وَ إِنْ يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْأَوَّلِينَ");
        sor.Ayaat.Add("وَ قٰاتِلُوهُمْ حَتّٰى لاٰ تَكُونَ فِتْنَةٌ وَ يَكُونَ الدِّينُ كُلُّهُ لِلّٰهِ فَإِنِ انْتَهَوْا فَإِنَّ اللّٰهَ بِمٰا يَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("وَ إِنْ تَوَلَّوْا فَاعْلَمُوا أَنَّ اللّٰهَ مَوْلاٰكُمْ نِعْمَ الْمَوْلىٰ وَ نِعْمَ النَّصِيرُ");
        sor.Ayaat.Add("وَ اعْلَمُوا أَنَّمٰا غَنِمْتُمْ مِنْ شَيْءٍ فَأَنَّ لِلّٰهِ خُمُسَهُ وَ لِلرَّسُولِ وَ لِذِي الْقُرْبىٰ وَ الْيَتٰامىٰ وَ الْمَسٰاكِينِ وَ ابْنِ السَّبِيلِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللّٰهِ وَ مٰا أَنْزَلْنٰا عَلىٰ عَبْدِنٰا يَوْمَ الْفُرْقٰانِ يَوْمَ الْتَقَى الْجَمْعٰانِ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("إِذْ أَنْتُمْ بِالْعُدْوَةِ الدُّنْيٰا وَ هُمْ بِالْعُدْوَةِ الْقُصْوىٰ وَ الرَّكْبُ أَسْفَلَ مِنْكُمْ وَ لَوْ تَوٰاعَدْتُمْ لاَخْتَلَفْتُمْ فِي الْمِيعٰادِ وَ لٰكِنْ لِيَقْضِيَ اللّٰهُ أَمْراً كٰانَ مَفْعُولاً لِيَهْلِكَ مَنْ هَلَكَ عَنْ بَيِّنَةٍ وَ يَحْيىٰ مَنْ حَيَّ عَنْ بَيِّنَةٍ وَ إِنَّ اللّٰهَ لَسَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("إِذْ يُرِيكَهُمُ اللّٰهُ فِي مَنٰامِكَ قَلِيلاً وَ لَوْ أَرٰاكَهُمْ كَثِيراً لَفَشِلْتُمْ وَ لَتَنٰازَعْتُمْ فِي الْأَمْرِ وَ لٰكِنَّ اللّٰهَ سَلَّمَ إِنَّهُ عَلِيمٌ بِذٰاتِ الصُّدُورِ");
        sor.Ayaat.Add("وَ إِذْ يُرِيكُمُوهُمْ إِذِ الْتَقَيْتُمْ فِي أَعْيُنِكُمْ قَلِيلاً وَ يُقَلِّلُكُمْ فِي أَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ أَمْراً كٰانَ مَفْعُولاً وَ إِلَى اللّٰهِ تُرْجَعُ الْأُمُورُ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِذٰا لَقِيتُمْ فِئَةً فَاثْبُتُوا وَ اذْكُرُوا اللّٰهَ كَثِيراً لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("وَ أَطِيعُوا اللّٰهَ وَ رَسُولَهُ وَ لاٰ تَنٰازَعُوا فَتَفْشَلُوا وَ تَذْهَبَ رِيحُكُمْ وَ اصْبِرُوا إِنَّ اللّٰهَ مَعَ الصّٰابِرِينَ");
        sor.Ayaat.Add("وَ لاٰ تَكُونُوا كَالَّذِينَ خَرَجُوا مِنْ دِيٰارِهِمْ بَطَراً وَ رِئٰاءَ النّٰاسِ وَ يَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ وَ اللّٰهُ بِمٰا يَعْمَلُونَ مُحِيطٌ");
        sor.Ayaat.Add("وَ إِذْ زَيَّنَ لَهُمُ الشَّيْطٰانُ أَعْمٰالَهُمْ وَ قٰالَ لاٰ غٰالِبَ لَكُمُ الْيَوْمَ مِنَ النّٰاسِ وَ إِنِّي جٰارٌ لَكُمْ فَلَمّٰا تَرٰاءَتِ الْفِئَتٰانِ نَكَصَ عَلىٰ عَقِبَيْهِ وَ قٰالَ إِنِّي بَرِيءٌ مِنْكُمْ إِنِّي أَرىٰ مٰا لاٰ تَرَوْنَ إِنِّي أَخٰافُ اللّٰهَ وَ اللّٰهُ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("إِذْ يَقُولُ الْمُنٰافِقُونَ وَ الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ غَرَّ هٰؤُلاٰءِ دِينُهُمْ وَ مَنْ يَتَوَكَّلْ عَلَى اللّٰهِ فَإِنَّ اللّٰهَ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ لَوْ تَرىٰ إِذْ يَتَوَفَّى الَّذِينَ كَفَرُوا الْمَلاٰئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَ أَدْبٰارَهُمْ وَ ذُوقُوا عَذٰابَ الْحَرِيقِ");
        sor.Ayaat.Add("ذٰلِكَ بِمٰا قَدَّمَتْ أَيْدِيكُمْ وَ أَنَّ اللّٰهَ لَيْسَ بِظَلاّٰمٍ لِلْعَبِيدِ");
        sor.Ayaat.Add("كَدَأْبِ آلِ فِرْعَوْنَ وَ الَّذِينَ مِنْ قَبْلِهِمْ كَفَرُوا بِآيٰاتِ اللّٰهِ فَأَخَذَهُمُ اللّٰهُ بِذُنُوبِهِمْ إِنَّ اللّٰهَ قَوِيٌّ شَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّراً نِعْمَةً أَنْعَمَهٰا عَلىٰ قَوْمٍ حَتّٰى يُغَيِّرُوا مٰا بِأَنْفُسِهِمْ وَ أَنَّ اللّٰهَ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("كَدَأْبِ آلِ فِرْعَوْنَ وَ الَّذِينَ مِنْ قَبْلِهِمْ كَذَّبُوا بِآيٰاتِ رَبِّهِمْ فَأَهْلَكْنٰاهُمْ بِذُنُوبِهِمْ وَ أَغْرَقْنٰا آلَ فِرْعَوْنَ وَ كُلٌّ كٰانُوا ظٰالِمِينَ");
        sor.Ayaat.Add("إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللّٰهِ الَّذِينَ كَفَرُوا فَهُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("الَّذِينَ عٰاهَدْتَ مِنْهُمْ ثُمَّ يَنْقُضُونَ عَهْدَهُمْ فِي كُلِّ مَرَّةٍ وَ هُمْ لاٰ يَتَّقُونَ");
        sor.Ayaat.Add("فَإِمّٰا تَثْقَفَنَّهُمْ فِي الْحَرْبِ فَشَرِّدْ بِهِمْ مَنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُونَ");
        sor.Ayaat.Add("وَ إِمّٰا تَخٰافَنَّ مِنْ قَوْمٍ خِيٰانَةً فَانْبِذْ إِلَيْهِمْ عَلىٰ سَوٰاءٍ إِنَّ اللّٰهَ لاٰ يُحِبُّ الْخٰائِنِينَ");
        sor.Ayaat.Add("وَ لاٰ يَحْسَبَنَّ الَّذِينَ كَفَرُوا سَبَقُوا إِنَّهُمْ لاٰ يُعْجِزُونَ");
        sor.Ayaat.Add("وَ أَعِدُّوا لَهُمْ مَا اسْتَطَعْتُمْ مِنْ قُوَّةٍ وَ مِنْ رِبٰاطِ الْخَيْلِ تُرْهِبُونَ بِهِ عَدُوَّ اللّٰهِ وَ عَدُوَّكُمْ وَ آخَرِينَ مِنْ دُونِهِمْ لاٰ تَعْلَمُونَهُمُ اللّٰهُ يَعْلَمُهُمْ وَ مٰا تُنْفِقُوا مِنْ شَيْءٍ فِي سَبِيلِ اللّٰهِ يُوَفَّ إِلَيْكُمْ وَ أَنْتُمْ لاٰ تُظْلَمُونَ");
        sor.Ayaat.Add("وَ إِنْ جَنَحُوا لِلسَّلْمِ فَاجْنَحْ لَهٰا وَ تَوَكَّلْ عَلَى اللّٰهِ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("وَ إِنْ يُرِيدُوا أَنْ يَخْدَعُوكَ فَإِنَّ حَسْبَكَ اللّٰهُ هُوَ الَّذِي أَيَّدَكَ بِنَصْرِهِ وَ بِالْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ أَلَّفَ بَيْنَ قُلُوبِهِمْ لَوْ أَنْفَقْتَ مٰا فِي الْأَرْضِ جَمِيعاً مٰا أَلَّفْتَ بَيْنَ قُلُوبِهِمْ وَ لٰكِنَّ اللّٰهَ أَلَّفَ بَيْنَهُمْ إِنَّهُ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَ مَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِينَ عَلَى الْقِتٰالِ إِنْ يَكُنْ مِنْكُمْ عِشْرُونَ صٰابِرُونَ يَغْلِبُوا مِائَتَيْنِ وَ إِنْ يَكُنْ مِنْكُمْ مِائَةٌ يَغْلِبُوا أَلْفاً مِنَ الَّذِينَ كَفَرُوا بِأَنَّهُمْ قَوْمٌ لاٰ يَفْقَهُونَ");
        sor.Ayaat.Add("الْآنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَ عَلِمَ أَنَّ فِيكُمْ ضَعْفاً فَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ صٰابِرَةٌ يَغْلِبُوا مِائَتَيْنِ وَ إِنْ يَكُنْ مِنْكُمْ أَلْفٌ يَغْلِبُوا أَلْفَيْنِ بِإِذْنِ اللّٰهِ وَ اللّٰهُ مَعَ الصّٰابِرِينَ");
        sor.Ayaat.Add("مٰا كٰانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرىٰ حَتّٰى يُثْخِنَ فِي الْأَرْضِ تُرِيدُونَ عَرَضَ الدُّنْيٰا وَ اللّٰهُ يُرِيدُ الْآخِرَةَ وَ اللّٰهُ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("لَوْ لاٰ كِتٰابٌ مِنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ فِيمٰا أَخَذْتُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("فَكُلُوا مِمّٰا غَنِمْتُمْ حَلاٰلاً طَيِّباً وَ اتَّقُوا اللّٰهَ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا النَّبِيُّ قُلْ لِمَنْ فِي أَيْدِيكُمْ مِنَ الْأَسْرىٰ إِنْ يَعْلَمِ اللّٰهُ فِي قُلُوبِكُمْ خَيْراً يُؤْتِكُمْ خَيْراً مِمّٰا أُخِذَ مِنْكُمْ وَ يَغْفِرْ لَكُمْ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ إِنْ يُرِيدُوا خِيٰانَتَكَ فَقَدْ خٰانُوا اللّٰهَ مِنْ قَبْلُ فَأَمْكَنَ مِنْهُمْ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ هٰاجَرُوا وَ جٰاهَدُوا بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ فِي سَبِيلِ اللّٰهِ وَ الَّذِينَ آوَوْا وَ نَصَرُوا أُولٰئِكَ بَعْضُهُمْ أَوْلِيٰاءُ بَعْضٍ وَ الَّذِينَ آمَنُوا وَ لَمْ يُهٰاجِرُوا مٰا لَكُمْ مِنْ وَلاٰيَتِهِمْ مِنْ شَيْءٍ حَتّٰى يُهٰاجِرُوا وَ إِنِ اسْتَنْصَرُوكُمْ فِي الدِّينِ فَعَلَيْكُمُ النَّصْرُ إِلاّٰ عَلىٰ قَوْمٍ بَيْنَكُمْ وَ بَيْنَهُمْ مِيثٰاقٌ وَ اللّٰهُ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("وَ الَّذِينَ كَفَرُوا بَعْضُهُمْ أَوْلِيٰاءُ بَعْضٍ إِلاّٰ تَفْعَلُوهُ تَكُنْ فِتْنَةٌ فِي الْأَرْضِ وَ فَسٰادٌ كَبِيرٌ");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا وَ هٰاجَرُوا وَ جٰاهَدُوا فِي سَبِيلِ اللّٰهِ وَ الَّذِينَ آوَوْا وَ نَصَرُوا أُولٰئِكَ هُمُ الْمُؤْمِنُونَ حَقًّا لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ");
        sor.Ayaat.Add("وَ الَّذِينَ آمَنُوا مِنْ بَعْدُ وَ هٰاجَرُوا وَ جٰاهَدُوا مَعَكُمْ فَأُولٰئِكَ مِنْكُمْ وَ أُولُوا الْأَرْحٰامِ بَعْضُهُمْ أَوْلىٰ بِبَعْضٍ فِي كِتٰابِ اللّٰهِ إِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيمٌ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٩ - التوبة";
        sor.Ayaat.Add("بَرٰاءَةٌ مِنَ اللّٰهِ وَ رَسُولِهِ إِلَى الَّذِينَ عٰاهَدْتُمْ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("فَسِيحُوا فِي الْأَرْضِ أَرْبَعَةَ أَشْهُرٍ وَ اعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللّٰهِ وَ أَنَّ اللّٰهَ مُخْزِي الْكٰافِرِينَ");
        sor.Ayaat.Add("وَ أَذٰانٌ مِنَ اللّٰهِ وَ رَسُولِهِ إِلَى النّٰاسِ يَوْمَ الْحَجِّ الْأَكْبَرِ أَنَّ اللّٰهَ بَرِيءٌ مِنَ الْمُشْرِكِينَ وَ رَسُولُهُ فَإِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَكُمْ وَ إِنْ تَوَلَّيْتُمْ فَاعْلَمُوا أَنَّكُمْ غَيْرُ مُعْجِزِي اللّٰهِ وَ بَشِّرِ الَّذِينَ كَفَرُوا بِعَذٰابٍ أَلِيمٍ");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ عٰاهَدْتُمْ مِنَ الْمُشْرِكِينَ ثُمَّ لَمْ يَنْقُصُوكُمْ شَيْئاً وَ لَمْ يُظٰاهِرُوا عَلَيْكُمْ أَحَداً فَأَتِمُّوا إِلَيْهِمْ عَهْدَهُمْ إِلىٰ مُدَّتِهِمْ إِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِينَ");
        sor.Ayaat.Add("فَإِذَا انْسَلَخَ الْأَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِينَ حَيْثُ وَجَدْتُمُوهُمْ وَ خُذُوهُمْ وَ احْصُرُوهُمْ وَ اقْعُدُوا لَهُمْ كُلَّ مَرْصَدٍ فَإِنْ تٰابُوا وَ أَقٰامُوا الصَّلاٰةَ وَ آتَوُا الزَّكٰاةَ فَخَلُّوا سَبِيلَهُمْ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ إِنْ أَحَدٌ مِنَ الْمُشْرِكِينَ اسْتَجٰارَكَ فَأَجِرْهُ حَتّٰى يَسْمَعَ كَلاٰمَ اللّٰهِ ثُمَّ أَبْلِغْهُ مَأْمَنَهُ ذٰلِكَ بِأَنَّهُمْ قَوْمٌ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِنْدَ اللّٰهِ وَ عِنْدَ رَسُولِهِ إِلاَّ الَّذِينَ عٰاهَدْتُمْ عِنْدَ الْمَسْجِدِ الْحَرٰامِ فَمَا اسْتَقٰامُوا لَكُمْ فَاسْتَقِيمُوا لَهُمْ إِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِينَ");
        sor.Ayaat.Add("كَيْفَ وَ إِنْ يَظْهَرُوا عَلَيْكُمْ لاٰ يَرْقُبُوا فِيكُمْ إِلاًّ وَ لاٰ ذِمَّةً يُرْضُونَكُمْ بِأَفْوٰاهِهِمْ وَ تَأْبىٰ قُلُوبُهُمْ وَ أَكْثَرُهُمْ فٰاسِقُونَ");
        sor.Ayaat.Add("اشْتَرَوْا بِآيٰاتِ اللّٰهِ ثَمَناً قَلِيلاً فَصَدُّوا عَنْ سَبِيلِهِ إِنَّهُمْ سٰاءَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("لاٰ يَرْقُبُونَ فِي مُؤْمِنٍ إِلاًّ وَ لاٰ ذِمَّةً وَ أُولٰئِكَ هُمُ الْمُعْتَدُونَ");
        sor.Ayaat.Add("فَإِنْ تٰابُوا وَ أَقٰامُوا الصَّلاٰةَ وَ آتَوُا الزَّكٰاةَ فَإِخْوٰانُكُمْ فِي الدِّينِ وَ نُفَصِّلُ الْآيٰاتِ لِقَوْمٍ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ إِنْ نَكَثُوا أَيْمٰانَهُمْ مِنْ بَعْدِ عَهْدِهِمْ وَ طَعَنُوا فِي دِينِكُمْ فَقٰاتِلُوا أَئِمَّةَ الْكُفْرِ إِنَّهُمْ لاٰ أَيْمٰانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُونَ");
        sor.Ayaat.Add("أَ لاٰ تُقٰاتِلُونَ قَوْماً نَكَثُوا أَيْمٰانَهُمْ وَ هَمُّوا بِإِخْرٰاجِ الرَّسُولِ وَ هُمْ بَدَؤُكُمْ أَوَّلَ مَرَّةٍ أَ تَخْشَوْنَهُمْ فَاللّٰهُ أَحَقُّ أَنْ تَخْشَوْهُ إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("قٰاتِلُوهُمْ يُعَذِّبْهُمُ اللّٰهُ بِأَيْدِيكُمْ وَ يُخْزِهِمْ وَ يَنْصُرْكُمْ عَلَيْهِمْ وَ يَشْفِ صُدُورَ قَوْمٍ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ يُذْهِبْ غَيْظَ قُلُوبِهِمْ وَ يَتُوبُ اللّٰهُ عَلىٰ مَنْ يَشٰاءُ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("أَمْ حَسِبْتُمْ أَنْ تُتْرَكُوا وَ لَمّٰا يَعْلَمِ اللّٰهُ الَّذِينَ جٰاهَدُوا مِنْكُمْ وَ لَمْ يَتَّخِذُوا مِنْ دُونِ اللّٰهِ وَ لاٰ رَسُولِهِ وَ لاَ الْمُؤْمِنِينَ وَلِيجَةً وَ اللّٰهُ خَبِيرٌ بِمٰا تَعْمَلُونَ");
        sor.Ayaat.Add("مٰا كٰانَ لِلْمُشْرِكِينَ أَنْ يَعْمُرُوا مَسٰاجِدَ اللّٰهِ شٰاهِدِينَ عَلىٰ أَنْفُسِهِمْ بِالْكُفْرِ أُولٰئِكَ حَبِطَتْ أَعْمٰالُهُمْ وَ فِي النّٰارِ هُمْ خٰالِدُونَ");
        sor.Ayaat.Add("إِنَّمٰا يَعْمُرُ مَسٰاجِدَ اللّٰهِ مَنْ آمَنَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ أَقٰامَ الصَّلاٰةَ وَ آتَى الزَّكٰاةَ وَ لَمْ يَخْشَ إِلاَّ اللّٰهَ فَعَسىٰ أُولٰئِكَ أَنْ يَكُونُوا مِنَ الْمُهْتَدِينَ");
        sor.Ayaat.Add("أَ جَعَلْتُمْ سِقٰايَةَ الْحٰاجِّ وَ عِمٰارَةَ الْمَسْجِدِ الْحَرٰامِ كَمَنْ آمَنَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ جٰاهَدَ فِي سَبِيلِ اللّٰهِ لاٰ يَسْتَوُونَ عِنْدَ اللّٰهِ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الظّٰالِمِينَ");
        sor.Ayaat.Add("الَّذِينَ آمَنُوا وَ هٰاجَرُوا وَ جٰاهَدُوا فِي سَبِيلِ اللّٰهِ بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ أَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِ وَ أُولٰئِكَ هُمُ الْفٰائِزُونَ");
        sor.Ayaat.Add("يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِنْهُ وَ رِضْوٰانٍ وَ جَنّٰاتٍ لَهُمْ فِيهٰا نَعِيمٌ مُقِيمٌ");
        sor.Ayaat.Add("خٰالِدِينَ فِيهٰا أَبَداً إِنَّ اللّٰهَ عِنْدَهُ أَجْرٌ عَظِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَتَّخِذُوا آبٰاءَكُمْ وَ إِخْوٰانَكُمْ أَوْلِيٰاءَ إِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْإِيمٰانِ وَ مَنْ يَتَوَلَّهُمْ مِنْكُمْ فَأُولٰئِكَ هُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("قُلْ إِنْ كٰانَ آبٰاؤُكُمْ وَ أَبْنٰاؤُكُمْ وَ إِخْوٰانُكُمْ وَ أَزْوٰاجُكُمْ وَ عَشِيرَتُكُمْ وَ أَمْوٰالٌ اقْتَرَفْتُمُوهٰا وَ تِجٰارَةٌ تَخْشَوْنَ كَسٰادَهٰا وَ مَسٰاكِنُ تَرْضَوْنَهٰا أَحَبَّ إِلَيْكُمْ مِنَ اللّٰهِ وَ رَسُولِهِ وَ جِهٰادٍ فِي سَبِيلِهِ فَتَرَبَّصُوا حَتّٰى يَأْتِيَ اللّٰهُ بِأَمْرِهِ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الْفٰاسِقِينَ");
        sor.Ayaat.Add("لَقَدْ نَصَرَكُمُ اللّٰهُ فِي مَوٰاطِنَ كَثِيرَةٍ وَ يَوْمَ حُنَيْنٍ إِذْ أَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْئاً وَ ضٰاقَتْ عَلَيْكُمُ الْأَرْضُ بِمٰا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُدْبِرِينَ");
        sor.Ayaat.Add("ثُمَّ أَنْزَلَ اللّٰهُ سَكِينَتَهُ عَلىٰ رَسُولِهِ وَ عَلَى الْمُؤْمِنِينَ وَ أَنْزَلَ جُنُوداً لَمْ تَرَوْهٰا وَ عَذَّبَ الَّذِينَ كَفَرُوا وَ ذٰلِكَ جَزٰاءُ الْكٰافِرِينَ");
        sor.Ayaat.Add("ثُمَّ يَتُوبُ اللّٰهُ مِنْ بَعْدِ ذٰلِكَ عَلىٰ مَنْ يَشٰاءُ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِنَّمَا الْمُشْرِكُونَ نَجَسٌ فَلاٰ يَقْرَبُوا الْمَسْجِدَ الْحَرٰامَ بَعْدَ عٰامِهِمْ هٰذٰا وَ إِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيكُمُ اللّٰهُ مِنْ فَضْلِهِ إِنْ شٰاءَ إِنَّ اللّٰهَ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("قٰاتِلُوا الَّذِينَ لاٰ يُؤْمِنُونَ بِاللّٰهِ وَ لاٰ بِالْيَوْمِ الْآخِرِ وَ لاٰ يُحَرِّمُونَ مٰا حَرَّمَ اللّٰهُ وَ رَسُولُهُ وَ لاٰ يَدِينُونَ دِينَ الْحَقِّ مِنَ الَّذِينَ أُوتُوا الْكِتٰابَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَدٍ وَ هُمْ صٰاغِرُونَ");
        sor.Ayaat.Add("وَ قٰالَتِ الْيَهُودُ عُزَيْرٌ ابْنُ اللّٰهِ وَ قٰالَتِ النَّصٰارىٰ الْمَسِيحُ ابْنُ اللّٰهِ ذٰلِكَ قَوْلُهُمْ بِأَفْوٰاهِهِمْ يُضٰاهِؤُنَ قَوْلَ الَّذِينَ كَفَرُوا مِنْ قَبْلُ قٰاتَلَهُمُ اللّٰهُ أَنّٰى يُؤْفَكُونَ");
        sor.Ayaat.Add("اتَّخَذُوا أَحْبٰارَهُمْ وَ رُهْبٰانَهُمْ أَرْبٰاباً مِنْ دُونِ اللّٰهِ وَ الْمَسِيحَ ابْنَ مَرْيَمَ وَ مٰا أُمِرُوا إِلاّٰ لِيَعْبُدُوا إِلٰهاً وٰاحِداً لاٰ إِلٰهَ إِلاّٰ هُوَ سُبْحٰانَهُ عَمّٰا يُشْرِكُونَ");
        sor.Ayaat.Add("يُرِيدُونَ أَنْ يُطْفِؤُا نُورَ اللّٰهِ بِأَفْوٰاهِهِمْ وَ يَأْبَى اللّٰهُ إِلاّٰ أَنْ يُتِمَّ نُورَهُ وَ لَوْ كَرِهَ الْكٰافِرُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدىٰ وَ دِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَ لَوْ كَرِهَ الْمُشْرِكُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ كَثِيراً مِنَ الْأَحْبٰارِ وَ الرُّهْبٰانِ لَيَأْكُلُونَ أَمْوٰالَ النّٰاسِ بِالْبٰاطِلِ وَ يَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ وَ الَّذِينَ يَكْنِزُونَ الذَّهَبَ وَ الْفِضَّةَ وَ لاٰ يُنْفِقُونَهٰا فِي سَبِيلِ اللّٰهِ فَبَشِّرْهُمْ بِعَذٰابٍ أَلِيمٍ");
        sor.Ayaat.Add("يَوْمَ يُحْمىٰ عَلَيْهٰا فِي نٰارِ جَهَنَّمَ فَتُكْوىٰ بِهٰا جِبٰاهُهُمْ وَ جُنُوبُهُمْ وَ ظُهُورُهُمْ هٰذٰا مٰا كَنَزْتُمْ لِأَنْفُسِكُمْ فَذُوقُوا مٰا كُنْتُمْ تَكْنِزُونَ");
        sor.Ayaat.Add("إِنَّ عِدَّةَ الشُّهُورِ عِنْدَ اللّٰهِ اثْنٰا عَشَرَ شَهْراً فِي كِتٰابِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ مِنْهٰا أَرْبَعَةٌ حُرُمٌ ذٰلِكَ الدِّينُ الْقَيِّمُ فَلاٰ تَظْلِمُوا فِيهِنَّ أَنْفُسَكُمْ وَ قٰاتِلُوا الْمُشْرِكِينَ كَافَّةً كَمٰا يُقٰاتِلُونَكُمْ كَافَّةً وَ اعْلَمُوا أَنَّ اللّٰهَ مَعَ الْمُتَّقِينَ");
        sor.Ayaat.Add("إِنَّمَا النَّسِيءُ زِيٰادَةٌ فِي الْكُفْرِ يُضَلُّ بِهِ الَّذِينَ كَفَرُوا يُحِلُّونَهُ عٰاماً وَ يُحَرِّمُونَهُ عٰاماً لِيُوٰاطِؤُا عِدَّةَ مٰا حَرَّمَ اللّٰهُ فَيُحِلُّوا مٰا حَرَّمَ اللّٰهُ زُيِّنَ لَهُمْ سُوءُ أَعْمٰالِهِمْ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الْكٰافِرِينَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا مٰا لَكُمْ إِذٰا قِيلَ لَكُمُ انْفِرُوا فِي سَبِيلِ اللّٰهِ اثّٰاقَلْتُمْ إِلَى الْأَرْضِ أَ رَضِيتُمْ بِالْحَيٰاةِ الدُّنْيٰا مِنَ الْآخِرَةِ فَمٰا مَتٰاعُ الْحَيٰاةِ الدُّنْيٰا فِي الْآخِرَةِ إِلاّٰ قَلِيلٌ");
        sor.Ayaat.Add("إِلاّٰ تَنْفِرُوا يُعَذِّبْكُمْ عَذٰاباً أَلِيماً وَ يَسْتَبْدِلْ قَوْماً غَيْرَكُمْ وَ لاٰ تَضُرُّوهُ شَيْئاً وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("إِلاّٰ تَنْصُرُوهُ فَقَدْ نَصَرَهُ اللّٰهُ إِذْ أَخْرَجَهُ الَّذِينَ كَفَرُوا ثٰانِيَ اثْنَيْنِ إِذْ هُمٰا فِي الْغٰارِ إِذْ يَقُولُ لِصٰاحِبِهِ لاٰ تَحْزَنْ إِنَّ اللّٰهَ مَعَنٰا فَأَنْزَلَ اللّٰهُ سَكِينَتَهُ عَلَيْهِ وَ أَيَّدَهُ بِجُنُودٍ لَمْ تَرَوْهٰا وَ جَعَلَ كَلِمَةَ الَّذِينَ كَفَرُوا السُّفْلىٰ وَ كَلِمَةُ اللّٰهِ هِيَ الْعُلْيٰا وَ اللّٰهُ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("انْفِرُوا خِفٰافاً وَ ثِقٰالاً وَ جٰاهِدُوا بِأَمْوٰالِكُمْ وَ أَنْفُسِكُمْ فِي سَبِيلِ اللّٰهِ ذٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("لَوْ كٰانَ عَرَضاً قَرِيباً وَ سَفَراً قٰاصِداً لاَتَّبَعُوكَ وَ لٰكِنْ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُ وَ سَيَحْلِفُونَ بِاللّٰهِ لَوِ اسْتَطَعْنٰا لَخَرَجْنٰا مَعَكُمْ يُهْلِكُونَ أَنْفُسَهُمْ وَ اللّٰهُ يَعْلَمُ إِنَّهُمْ لَكٰاذِبُونَ");
        sor.Ayaat.Add("عَفَا اللّٰهُ عَنْكَ لِمَ أَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذِينَ صَدَقُوا وَ تَعْلَمَ الْكٰاذِبِينَ");
        sor.Ayaat.Add("لاٰ يَسْتَأْذِنُكَ الَّذِينَ يُؤْمِنُونَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ أَنْ يُجٰاهِدُوا بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ وَ اللّٰهُ عَلِيمٌ بِالْمُتَّقِينَ");
        sor.Ayaat.Add("إِنَّمٰا يَسْتَأْذِنُكَ الَّذِينَ لاٰ يُؤْمِنُونَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ ارْتٰابَتْ قُلُوبُهُمْ فَهُمْ فِي رَيْبِهِمْ يَتَرَدَّدُونَ");
        sor.Ayaat.Add("وَ لَوْ أَرٰادُوا الْخُرُوجَ لَأَعَدُّوا لَهُ عُدَّةً وَ لٰكِنْ كَرِهَ اللّٰهُ انْبِعٰاثَهُمْ فَثَبَّطَهُمْ وَ قِيلَ اقْعُدُوا مَعَ الْقٰاعِدِينَ");
        sor.Ayaat.Add("لَوْ خَرَجُوا فِيكُمْ مٰا زٰادُوكُمْ إِلاّٰ خَبٰالاً وَ لَأَوْضَعُوا خِلاٰلَكُمْ يَبْغُونَكُمُ الْفِتْنَةَ وَ فِيكُمْ سَمّٰاعُونَ لَهُمْ وَ اللّٰهُ عَلِيمٌ بِالظّٰالِمِينَ");
        sor.Ayaat.Add("لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَ قَلَّبُوا لَكَ الْأُمُورَ حَتّٰى جٰاءَ الْحَقُّ وَ ظَهَرَ أَمْرُ اللّٰهِ وَ هُمْ كٰارِهُونَ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يَقُولُ ائْذَنْ لِي وَ لاٰ تَفْتِنِّي أَلاٰ فِي الْفِتْنَةِ سَقَطُوا وَ إِنَّ جَهَنَّمَ لَمُحِيطَةٌ بِالْكٰافِرِينَ");
        sor.Ayaat.Add("إِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْ وَ إِنْ تُصِبْكَ مُصِيبَةٌ يَقُولُوا قَدْ أَخَذْنٰا أَمْرَنٰا مِنْ قَبْلُ وَ يَتَوَلَّوْا وَ هُمْ فَرِحُونَ");
        sor.Ayaat.Add("قُلْ لَنْ يُصِيبَنٰا إِلاّٰ مٰا كَتَبَ اللّٰهُ لَنٰا هُوَ مَوْلاٰنٰا وَ عَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ");
        sor.Ayaat.Add("قُلْ هَلْ تَرَبَّصُونَ بِنٰا إِلاّٰ إِحْدَى الْحُسْنَيَيْنِ وَ نَحْنُ نَتَرَبَّصُ بِكُمْ أَنْ يُصِيبَكُمُ اللّٰهُ بِعَذٰابٍ مِنْ عِنْدِهِ أَوْ بِأَيْدِينٰا فَتَرَبَّصُوا إِنّٰا مَعَكُمْ مُتَرَبِّصُونَ");
        sor.Ayaat.Add("قُلْ أَنْفِقُوا طَوْعاً أَوْ كَرْهاً لَنْ يُتَقَبَّلَ مِنْكُمْ إِنَّكُمْ كُنْتُمْ قَوْماً فٰاسِقِينَ");
        sor.Ayaat.Add("وَ مٰا مَنَعَهُمْ أَنْ تُقْبَلَ مِنْهُمْ نَفَقٰاتُهُمْ إِلاّٰ أَنَّهُمْ كَفَرُوا بِاللّٰهِ وَ بِرَسُولِهِ وَ لاٰ يَأْتُونَ الصَّلاٰةَ إِلاّٰ وَ هُمْ كُسٰالىٰ وَ لاٰ يُنْفِقُونَ إِلاّٰ وَ هُمْ كٰارِهُونَ");
        sor.Ayaat.Add("فَلاٰ تُعْجِبْكَ أَمْوٰالُهُمْ وَ لاٰ أَوْلاٰدُهُمْ إِنَّمٰا يُرِيدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهٰا فِي الْحَيٰاةِ الدُّنْيٰا وَ تَزْهَقَ أَنْفُسُهُمْ وَ هُمْ كٰافِرُونَ");
        sor.Ayaat.Add("وَ يَحْلِفُونَ بِاللّٰهِ إِنَّهُمْ لَمِنْكُمْ وَ مٰا هُمْ مِنْكُمْ وَ لٰكِنَّهُمْ قَوْمٌ يَفْرَقُونَ");
        sor.Ayaat.Add("لَوْ يَجِدُونَ مَلْجَأً أَوْ مَغٰارٰاتٍ أَوْ مُدَّخَلاً لَوَلَّوْا إِلَيْهِ وَ هُمْ يَجْمَحُونَ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يَلْمِزُكَ فِي الصَّدَقٰاتِ فَإِنْ أُعْطُوا مِنْهٰا رَضُوا وَ إِنْ لَمْ يُعْطَوْا مِنْهٰا إِذٰا هُمْ يَسْخَطُونَ");
        sor.Ayaat.Add("وَ لَوْ أَنَّهُمْ رَضُوا مٰا آتٰاهُمُ اللّٰهُ وَ رَسُولُهُ وَ قٰالُوا حَسْبُنَا اللّٰهُ سَيُؤْتِينَا اللّٰهُ مِنْ فَضْلِهِ وَ رَسُولُهُ إِنّٰا إِلَى اللّٰهِ رٰاغِبُونَ");
        sor.Ayaat.Add("إِنَّمَا الصَّدَقٰاتُ لِلْفُقَرٰاءِ وَ الْمَسٰاكِينِ وَ الْعٰامِلِينَ عَلَيْهٰا وَ الْمُؤَلَّفَةِ قُلُوبُهُمْ وَ فِي الرِّقٰابِ وَ الْغٰارِمِينَ وَ فِي سَبِيلِ اللّٰهِ وَ ابْنِ السَّبِيلِ فَرِيضَةً مِنَ اللّٰهِ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ مِنْهُمُ الَّذِينَ يُؤْذُونَ النَّبِيَّ وَ يَقُولُونَ هُوَ أُذُنٌ قُلْ أُذُنُ خَيْرٍ لَكُمْ يُؤْمِنُ بِاللّٰهِ وَ يُؤْمِنُ لِلْمُؤْمِنِينَ وَ رَحْمَةٌ لِلَّذِينَ آمَنُوا مِنْكُمْ وَ الَّذِينَ يُؤْذُونَ رَسُولَ اللّٰهِ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("يَحْلِفُونَ بِاللّٰهِ لَكُمْ لِيُرْضُوكُمْ وَ اللّٰهُ وَ رَسُولُهُ أَحَقُّ أَنْ يُرْضُوهُ إِنْ كٰانُوا مُؤْمِنِينَ");
        sor.Ayaat.Add("أَ لَمْ يَعْلَمُوا أَنَّهُ مَنْ يُحٰادِدِ اللّٰهَ وَ رَسُولَهُ فَأَنَّ لَهُ نٰارَ جَهَنَّمَ خٰالِداً فِيهٰا ذٰلِكَ الْخِزْيُ الْعَظِيمُ");
        sor.Ayaat.Add("يَحْذَرُ الْمُنٰافِقُونَ أَنْ تُنَزَّلَ عَلَيْهِمْ سُورَةٌ تُنَبِّئُهُمْ بِمٰا فِي قُلُوبِهِمْ قُلِ اسْتَهْزِؤُا إِنَّ اللّٰهَ مُخْرِجٌ مٰا تَحْذَرُونَ");
        sor.Ayaat.Add("وَ لَئِنْ سَأَلْتَهُمْ لَيَقُولُنَّ إِنَّمٰا كُنّٰا نَخُوضُ وَ نَلْعَبُ قُلْ أَ بِاللّٰهِ وَ آيٰاتِهِ وَ رَسُولِهِ كُنْتُمْ تَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("لاٰ تَعْتَذِرُوا قَدْ كَفَرْتُمْ بَعْدَ إِيمٰانِكُمْ إِنْ نَعْفُ عَنْ طٰائِفَةٍ مِنْكُمْ نُعَذِّبْ طٰائِفَةً بِأَنَّهُمْ كٰانُوا مُجْرِمِينَ");
        sor.Ayaat.Add("الْمُنٰافِقُونَ وَ الْمُنٰافِقٰاتُ بَعْضُهُمْ مِنْ بَعْضٍ يَأْمُرُونَ بِالْمُنْكَرِ وَ يَنْهَوْنَ عَنِ الْمَعْرُوفِ وَ يَقْبِضُونَ أَيْدِيَهُمْ نَسُوا اللّٰهَ فَنَسِيَهُمْ إِنَّ الْمُنٰافِقِينَ هُمُ الْفٰاسِقُونَ");
        sor.Ayaat.Add("وَعَدَ اللّٰهُ الْمُنٰافِقِينَ وَ الْمُنٰافِقٰاتِ وَ الْكُفّٰارَ نٰارَ جَهَنَّمَ خٰالِدِينَ فِيهٰا هِيَ حَسْبُهُمْ وَ لَعَنَهُمُ اللّٰهُ وَ لَهُمْ عَذٰابٌ مُقِيمٌ");
        sor.Ayaat.Add("كَالَّذِينَ مِنْ قَبْلِكُمْ كٰانُوا أَشَدَّ مِنْكُمْ قُوَّةً وَ أَكْثَرَ أَمْوٰالاً وَ أَوْلاٰداً فَاسْتَمْتَعُوا بِخَلاٰقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلاٰقِكُمْ كَمَا اسْتَمْتَعَ الَّذِينَ مِنْ قَبْلِكُمْ بِخَلاٰقِهِمْ وَ خُضْتُمْ كَالَّذِي خٰاضُوا أُولٰئِكَ حَبِطَتْ أَعْمٰالُهُمْ فِي الدُّنْيٰا وَ الْآخِرَةِ وَ أُولٰئِكَ هُمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("أَ لَمْ يَأْتِهِمْ نَبَأُ الَّذِينَ مِنْ قَبْلِهِمْ قَوْمِ نُوحٍ وَ عٰادٍ وَ ثَمُودَ وَ قَوْمِ إِبْرٰاهِيمَ وَ أَصْحٰابِ مَدْيَنَ وَ الْمُؤْتَفِكٰاتِ أَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰاتِ فَمٰا كٰانَ اللّٰهُ لِيَظْلِمَهُمْ وَ لٰكِنْ كٰانُوا أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("وَ الْمُؤْمِنُونَ وَ الْمُؤْمِنٰاتُ بَعْضُهُمْ أَوْلِيٰاءُ بَعْضٍ يَأْمُرُونَ بِالْمَعْرُوفِ وَ يَنْهَوْنَ عَنِ الْمُنْكَرِ وَ يُقِيمُونَ الصَّلاٰةَ وَ يُؤْتُونَ الزَّكٰاةَ وَ يُطِيعُونَ اللّٰهَ وَ رَسُولَهُ أُولٰئِكَ سَيَرْحَمُهُمُ اللّٰهُ إِنَّ اللّٰهَ عَزِيزٌ حَكِيمٌ");
        sor.Ayaat.Add("وَعَدَ اللّٰهُ الْمُؤْمِنِينَ وَ الْمُؤْمِنٰاتِ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا وَ مَسٰاكِنَ طَيِّبَةً فِي جَنّٰاتِ عَدْنٍ وَ رِضْوٰانٌ مِنَ اللّٰهِ أَكْبَرُ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("يٰا أَيُّهَا النَّبِيُّ جٰاهِدِ الْكُفّٰارَ وَ الْمُنٰافِقِينَ وَ اغْلُظْ عَلَيْهِمْ وَ مَأْوٰاهُمْ جَهَنَّمُ وَ بِئْسَ الْمَصِيرُ");
        sor.Ayaat.Add("يَحْلِفُونَ بِاللّٰهِ مٰا قٰالُوا وَ لَقَدْ قٰالُوا كَلِمَةَ الْكُفْرِ وَ كَفَرُوا بَعْدَ إِسْلاٰمِهِمْ وَ هَمُّوا بِمٰا لَمْ يَنٰالُوا وَ مٰا نَقَمُوا إِلاّٰ أَنْ أَغْنٰاهُمُ اللّٰهُ وَ رَسُولُهُ مِنْ فَضْلِهِ فَإِنْ يَتُوبُوا يَكُ خَيْراً لَهُمْ وَ إِنْ يَتَوَلَّوْا يُعَذِّبْهُمُ اللّٰهُ عَذٰاباً أَلِيماً فِي الدُّنْيٰا وَ الْآخِرَةِ وَ مٰا لَهُمْ فِي الْأَرْضِ مِنْ وَلِيٍّ وَ لاٰ نَصِيرٍ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ عٰاهَدَ اللّٰهَ لَئِنْ آتٰانٰا مِنْ فَضْلِهِ لَنَصَّدَّقَنَّ وَ لَنَكُونَنَّ مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("فَلَمّٰا آتٰاهُمْ مِنْ فَضْلِهِ بَخِلُوا بِهِ وَ تَوَلَّوْا وَ هُمْ مُعْرِضُونَ");
        sor.Ayaat.Add("فَأَعْقَبَهُمْ نِفٰاقاً فِي قُلُوبِهِمْ إِلىٰ يَوْمِ يَلْقَوْنَهُ بِمٰا أَخْلَفُوا اللّٰهَ مٰا وَعَدُوهُ وَ بِمٰا كٰانُوا يَكْذِبُونَ");
        sor.Ayaat.Add("أَ لَمْ يَعْلَمُوا أَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَ نَجْوٰاهُمْ وَ أَنَّ اللّٰهَ عَلاّٰمُ الْغُيُوبِ");
        sor.Ayaat.Add("الَّذِينَ يَلْمِزُونَ الْمُطَّوِّعِينَ مِنَ الْمُؤْمِنِينَ فِي الصَّدَقٰاتِ وَ الَّذِينَ لاٰ يَجِدُونَ إِلاّٰ جُهْدَهُمْ فَيَسْخَرُونَ مِنْهُمْ سَخِرَ اللّٰهُ مِنْهُمْ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("اسْتَغْفِرْ لَهُمْ أَوْ لاٰ تَسْتَغْفِرْ لَهُمْ إِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِينَ مَرَّةً فَلَنْ يَغْفِرَ اللّٰهُ لَهُمْ ذٰلِكَ بِأَنَّهُمْ كَفَرُوا بِاللّٰهِ وَ رَسُولِهِ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الْفٰاسِقِينَ");
        sor.Ayaat.Add("فَرِحَ الْمُخَلَّفُونَ بِمَقْعَدِهِمْ خِلاٰفَ رَسُولِ اللّٰهِ وَ كَرِهُوا أَنْ يُجٰاهِدُوا بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ فِي سَبِيلِ اللّٰهِ وَ قٰالُوا لاٰ تَنْفِرُوا فِي الْحَرِّ قُلْ نٰارُ جَهَنَّمَ أَشَدُّ حَرًّا لَوْ كٰانُوا يَفْقَهُونَ");
        sor.Ayaat.Add("فَلْيَضْحَكُوا قَلِيلاً وَ لْيَبْكُوا كَثِيراً جَزٰاءً بِمٰا كٰانُوا يَكْسِبُونَ");
        sor.Ayaat.Add("فَإِنْ رَجَعَكَ اللّٰهُ إِلىٰ طٰائِفَةٍ مِنْهُمْ فَاسْتَأْذَنُوكَ لِلْخُرُوجِ فَقُلْ لَنْ تَخْرُجُوا مَعِيَ أَبَداً وَ لَنْ تُقٰاتِلُوا مَعِيَ عَدُوًّا إِنَّكُمْ رَضِيتُمْ بِالْقُعُودِ أَوَّلَ مَرَّةٍ فَاقْعُدُوا مَعَ الْخٰالِفِينَ");
        sor.Ayaat.Add("وَ لاٰ تُصَلِّ عَلىٰ أَحَدٍ مِنْهُمْ مٰاتَ أَبَداً وَ لاٰ تَقُمْ عَلىٰ قَبْرِهِ إِنَّهُمْ كَفَرُوا بِاللّٰهِ وَ رَسُولِهِ وَ مٰاتُوا وَ هُمْ فٰاسِقُونَ");
        sor.Ayaat.Add("وَ لاٰ تُعْجِبْكَ أَمْوٰالُهُمْ وَ أَوْلاٰدُهُمْ إِنَّمٰا يُرِيدُ اللّٰهُ أَنْ يُعَذِّبَهُمْ بِهٰا فِي الدُّنْيٰا وَ تَزْهَقَ أَنْفُسُهُمْ وَ هُمْ كٰافِرُونَ");
        sor.Ayaat.Add("وَ إِذٰا أُنْزِلَتْ سُورَةٌ أَنْ آمِنُوا بِاللّٰهِ وَ جٰاهِدُوا مَعَ رَسُولِهِ اسْتَأْذَنَكَ أُولُوا الطَّوْلِ مِنْهُمْ وَ قٰالُوا ذَرْنٰا نَكُنْ مَعَ الْقٰاعِدِينَ");
        sor.Ayaat.Add("رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوٰالِفِ وَ طُبِعَ عَلىٰ قُلُوبِهِمْ فَهُمْ لاٰ يَفْقَهُونَ");
        sor.Ayaat.Add("لٰكِنِ الرَّسُولُ وَ الَّذِينَ آمَنُوا مَعَهُ جٰاهَدُوا بِأَمْوٰالِهِمْ وَ أَنْفُسِهِمْ وَ أُولٰئِكَ لَهُمُ الْخَيْرٰاتُ وَ أُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("أَعَدَّ اللّٰهُ لَهُمْ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا ذٰلِكَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("وَ جٰاءَ الْمُعَذِّرُونَ مِنَ الْأَعْرٰابِ لِيُؤْذَنَ لَهُمْ وَ قَعَدَ الَّذِينَ كَذَبُوا اللّٰهَ وَ رَسُولَهُ سَيُصِيبُ الَّذِينَ كَفَرُوا مِنْهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("لَيْسَ عَلَى الضُّعَفٰاءِ وَ لاٰ عَلَى الْمَرْضىٰ وَ لاٰ عَلَى الَّذِينَ لاٰ يَجِدُونَ مٰا يُنْفِقُونَ حَرَجٌ إِذٰا نَصَحُوا لِلّٰهِ وَ رَسُولِهِ مٰا عَلَى الْمُحْسِنِينَ مِنْ سَبِيلٍ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ لاٰ عَلَى الَّذِينَ إِذٰا مٰا أَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لاٰ أَجِدُ مٰا أَحْمِلُكُمْ عَلَيْهِ تَوَلَّوْا وَ أَعْيُنُهُمْ تَفِيضُ مِنَ الدَّمْعِ حَزَناً أَلاّٰ يَجِدُوا مٰا يُنْفِقُونَ");
        sor.Ayaat.Add("إِنَّمَا السَّبِيلُ عَلَى الَّذِينَ يَسْتَأْذِنُونَكَ وَ هُمْ أَغْنِيٰاءُ رَضُوا بِأَنْ يَكُونُوا مَعَ الْخَوٰالِفِ وَ طَبَعَ اللّٰهُ عَلىٰ قُلُوبِهِمْ فَهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("يَعْتَذِرُونَ إِلَيْكُمْ إِذٰا رَجَعْتُمْ إِلَيْهِمْ قُلْ لاٰ تَعْتَذِرُوا لَنْ نُؤْمِنَ لَكُمْ قَدْ نَبَّأَنَا اللّٰهُ مِنْ أَخْبٰارِكُمْ وَ سَيَرَى اللّٰهُ عَمَلَكُمْ وَ رَسُولُهُ ثُمَّ تُرَدُّونَ إِلىٰ عٰالِمِ الْغَيْبِ وَ الشَّهٰادَةِ فَيُنَبِّئُكُمْ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("سَيَحْلِفُونَ بِاللّٰهِ لَكُمْ إِذَا انْقَلَبْتُمْ إِلَيْهِمْ لِتُعْرِضُوا عَنْهُمْ فَأَعْرِضُوا عَنْهُمْ إِنَّهُمْ رِجْسٌ وَ مَأْوٰاهُمْ جَهَنَّمُ جَزٰاءً بِمٰا كٰانُوا يَكْسِبُونَ");
        sor.Ayaat.Add("يَحْلِفُونَ لَكُمْ لِتَرْضَوْا عَنْهُمْ فَإِنْ تَرْضَوْا عَنْهُمْ فَإِنَّ اللّٰهَ لاٰ يَرْضىٰ عَنِ الْقَوْمِ الْفٰاسِقِينَ");
        sor.Ayaat.Add("الْأَعْرٰابُ أَشَدُّ كُفْراً وَ نِفٰاقاً وَ أَجْدَرُ أَلاّٰ يَعْلَمُوا حُدُودَ مٰا أَنْزَلَ اللّٰهُ عَلىٰ رَسُولِهِ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ مِنَ الْأَعْرٰابِ مَنْ يَتَّخِذُ مٰا يُنْفِقُ مَغْرَماً وَ يَتَرَبَّصُ بِكُمُ الدَّوٰائِرَ عَلَيْهِمْ دٰائِرَةُ السَّوْءِ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ مِنَ الْأَعْرٰابِ مَنْ يُؤْمِنُ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ يَتَّخِذُ مٰا يُنْفِقُ قُرُبٰاتٍ عِنْدَ اللّٰهِ وَ صَلَوٰاتِ الرَّسُولِ أَلاٰ إِنَّهٰا قُرْبَةٌ لَهُمْ سَيُدْخِلُهُمُ اللّٰهُ فِي رَحْمَتِهِ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ السّٰابِقُونَ الْأَوَّلُونَ مِنَ الْمُهٰاجِرِينَ وَ الْأَنْصٰارِ وَ الَّذِينَ اتَّبَعُوهُمْ بِإِحْسٰانٍ رَضِيَ اللّٰهُ عَنْهُمْ وَ رَضُوا عَنْهُ وَ أَعَدَّ لَهُمْ جَنّٰاتٍ تَجْرِي تَحْتَهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا أَبَداً ذٰلِكَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("وَ مِمَّنْ حَوْلَكُمْ مِنَ الْأَعْرٰابِ مُنٰافِقُونَ وَ مِنْ أَهْلِ الْمَدِينَةِ مَرَدُوا عَلَى النِّفٰاقِ لاٰ تَعْلَمُهُمْ نَحْنُ نَعْلَمُهُمْ سَنُعَذِّبُهُمْ مَرَّتَيْنِ ثُمَّ يُرَدُّونَ إِلىٰ عَذٰابٍ عَظِيمٍ");
        sor.Ayaat.Add("وَ آخَرُونَ اعْتَرَفُوا بِذُنُوبِهِمْ خَلَطُوا عَمَلاً صٰالِحاً وَ آخَرَ سَيِّئاً عَسَى اللّٰهُ أَنْ يَتُوبَ عَلَيْهِمْ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("خُذْ مِنْ أَمْوٰالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَ تُزَكِّيهِمْ بِهٰا وَ صَلِّ عَلَيْهِمْ إِنَّ صَلاٰتَكَ سَكَنٌ لَهُمْ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("أَ لَمْ يَعْلَمُوا أَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبٰادِهِ وَ يَأْخُذُ الصَّدَقٰاتِ وَ أَنَّ اللّٰهَ هُوَ التَّوّٰابُ الرَّحِيمُ");
        sor.Ayaat.Add("وَ قُلِ اعْمَلُوا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَ رَسُولُهُ وَ الْمُؤْمِنُونَ وَ سَتُرَدُّونَ إِلىٰ عٰالِمِ الْغَيْبِ وَ الشَّهٰادَةِ فَيُنَبِّئُكُمْ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("وَ آخَرُونَ مُرْجَوْنَ لِأَمْرِ اللّٰهِ إِمّٰا يُعَذِّبُهُمْ وَ إِمّٰا يَتُوبُ عَلَيْهِمْ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ الَّذِينَ اتَّخَذُوا مَسْجِداً ضِرٰاراً وَ كُفْراً وَ تَفْرِيقاً بَيْنَ الْمُؤْمِنِينَ وَ إِرْصٰاداً لِمَنْ حٰارَبَ اللّٰهَ وَ رَسُولَهُ مِنْ قَبْلُ وَ لَيَحْلِفُنَّ إِنْ أَرَدْنٰا إِلاَّ الْحُسْنىٰ وَ اللّٰهُ يَشْهَدُ إِنَّهُمْ لَكٰاذِبُونَ");
        sor.Ayaat.Add("لاٰ تَقُمْ فِيهِ أَبَداً لَمَسْجِدٌ أُسِّسَ عَلَى التَّقْوىٰ مِنْ أَوَّلِ يَوْمٍ أَحَقُّ أَنْ تَقُومَ فِيهِ فِيهِ رِجٰالٌ يُحِبُّونَ أَنْ يَتَطَهَّرُوا وَ اللّٰهُ يُحِبُّ الْمُطَّهِّرِينَ");
        sor.Ayaat.Add("أَ فَمَنْ أَسَّسَ بُنْيٰانَهُ عَلىٰ تَقْوىٰ مِنَ اللّٰهِ وَ رِضْوٰانٍ خَيْرٌ أَمْ مَنْ أَسَّسَ بُنْيٰانَهُ عَلىٰ شَفٰا جُرُفٍ هٰارٍ فَانْهٰارَ بِهِ فِي نٰارِ جَهَنَّمَ وَ اللّٰهُ لاٰ يَهْدِي الْقَوْمَ الظّٰالِمِينَ");
        sor.Ayaat.Add("لاٰ يَزٰالُ بُنْيٰانُهُمُ الَّذِي بَنَوْا رِيبَةً فِي قُلُوبِهِمْ إِلاّٰ أَنْ تَقَطَّعَ قُلُوبُهُمْ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ اشْتَرىٰ مِنَ الْمُؤْمِنِينَ أَنْفُسَهُمْ وَ أَمْوٰالَهُمْ بِأَنَّ لَهُمُ الْجَنَّةَ يُقٰاتِلُونَ فِي سَبِيلِ اللّٰهِ فَيَقْتُلُونَ وَ يُقْتَلُونَ وَعْداً عَلَيْهِ حَقًّا فِي التَّوْرٰاةِ وَ الْإِنْجِيلِ وَ الْقُرْآنِ وَ مَنْ أَوْفىٰ بِعَهْدِهِ مِنَ اللّٰهِ فَاسْتَبْشِرُوا بِبَيْعِكُمُ الَّذِي بٰايَعْتُمْ بِهِ وَ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("التّٰائِبُونَ الْعٰابِدُونَ الْحٰامِدُونَ السّٰائِحُونَ الرّٰاكِعُونَ السّٰاجِدُونَ الْآمِرُونَ بِالْمَعْرُوفِ وَ النّٰاهُونَ عَنِ الْمُنْكَرِ وَ الْحٰافِظُونَ لِحُدُودِ اللّٰهِ وَ بَشِّرِ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("مٰا كٰانَ لِلنَّبِيِّ وَ الَّذِينَ آمَنُوا أَنْ يَسْتَغْفِرُوا لِلْمُشْرِكِينَ وَ لَوْ كٰانُوا أُولِي قُرْبىٰ مِنْ بَعْدِ مٰا تَبَيَّنَ لَهُمْ أَنَّهُمْ أَصْحٰابُ الْجَحِيمِ");
        sor.Ayaat.Add("وَ مٰا كٰانَ اسْتِغْفٰارُ إِبْرٰاهِيمَ لِأَبِيهِ إِلاّٰ عَنْ مَوْعِدَةٍ وَعَدَهٰا إِيّٰاهُ فَلَمّٰا تَبَيَّنَ لَهُ أَنَّهُ عَدُوٌّ لِلّٰهِ تَبَرَّأَ مِنْهُ إِنَّ إِبْرٰاهِيمَ لَأَوّٰاهٌ حَلِيمٌ");
        sor.Ayaat.Add("وَ مٰا كٰانَ اللّٰهُ لِيُضِلَّ قَوْماً بَعْدَ إِذْ هَدٰاهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مٰا يَتَّقُونَ إِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لَهُ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ يُحْيِي وَ يُمِيتُ وَ مٰا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ وَلِيٍّ وَ لاٰ نَصِيرٍ");
        sor.Ayaat.Add("لَقَدْ تٰابَ اللّٰهُ عَلَى النَّبِيِّ وَ الْمُهٰاجِرِينَ وَ الْأَنْصٰارِ الَّذِينَ اتَّبَعُوهُ فِي سٰاعَةِ الْعُسْرَةِ مِنْ بَعْدِ مٰا كٰادَ يَزِيغُ قُلُوبُ فَرِيقٍ مِنْهُمْ ثُمَّ تٰابَ عَلَيْهِمْ إِنَّهُ بِهِمْ رَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ عَلَى الثَّلاٰثَةِ الَّذِينَ خُلِّفُوا حَتّٰى إِذٰا ضٰاقَتْ عَلَيْهِمُ الْأَرْضُ بِمٰا رَحُبَتْ وَ ضٰاقَتْ عَلَيْهِمْ أَنْفُسُهُمْ وَ ظَنُّوا أَنْ لاٰ مَلْجَأَ مِنَ اللّٰهِ إِلاّٰ إِلَيْهِ ثُمَّ تٰابَ عَلَيْهِمْ لِيَتُوبُوا إِنَّ اللّٰهَ هُوَ التَّوّٰابُ الرَّحِيمُ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللّٰهَ وَ كُونُوا مَعَ الصّٰادِقِينَ");
        sor.Ayaat.Add("مٰا كٰانَ لِأَهْلِ الْمَدِينَةِ وَ مَنْ حَوْلَهُمْ مِنَ الْأَعْرٰابِ أَنْ يَتَخَلَّفُوا عَنْ رَسُولِ اللّٰهِ وَ لاٰ يَرْغَبُوا بِأَنْفُسِهِمْ عَنْ نَفْسِهِ ذٰلِكَ بِأَنَّهُمْ لاٰ يُصِيبُهُمْ ظَمَأٌ وَ لاٰ نَصَبٌ وَ لاٰ مَخْمَصَةٌ فِي سَبِيلِ اللّٰهِ وَ لاٰ يَطَؤُنَ مَوْطِئاً يَغِيظُ الْكُفّٰارَ وَ لاٰ يَنٰالُونَ مِنْ عَدُوٍّ نَيْلاً إِلاّٰ كُتِبَ لَهُمْ بِهِ عَمَلٌ صٰالِحٌ إِنَّ اللّٰهَ لاٰ يُضِيعُ أَجْرَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ لاٰ يُنْفِقُونَ نَفَقَةً صَغِيرَةً وَ لاٰ كَبِيرَةً وَ لاٰ يَقْطَعُونَ وٰادِياً إِلاّٰ كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ أَحْسَنَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ الْمُؤْمِنُونَ لِيَنْفِرُوا كَافَّةً فَلَوْ لاٰ نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِنْهُمْ طٰائِفَةٌ لِيَتَفَقَّهُوا فِي الدِّينِ وَ لِيُنْذِرُوا قَوْمَهُمْ إِذٰا رَجَعُوا إِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا قٰاتِلُوا الَّذِينَ يَلُونَكُمْ مِنَ الْكُفّٰارِ وَ لْيَجِدُوا فِيكُمْ غِلْظَةً وَ اعْلَمُوا أَنَّ اللّٰهَ مَعَ الْمُتَّقِينَ");
        sor.Ayaat.Add("وَ إِذٰا مٰا أُنْزِلَتْ سُورَةٌ فَمِنْهُمْ مَنْ يَقُولُ أَيُّكُمْ زٰادَتْهُ هٰذِهِ إِيمٰاناً فَأَمَّا الَّذِينَ آمَنُوا فَزٰادَتْهُمْ إِيمٰاناً وَ هُمْ يَسْتَبْشِرُونَ");
        sor.Ayaat.Add("وَ أَمَّا الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ فَزٰادَتْهُمْ رِجْساً إِلَى رِجْسِهِمْ وَ مٰاتُوا وَ هُمْ كٰافِرُونَ");
        sor.Ayaat.Add("أَ وَ لاٰ يَرَوْنَ أَنَّهُمْ يُفْتَنُونَ فِي كُلِّ عٰامٍ مَرَّةً أَوْ مَرَّتَيْنِ ثُمَّ لاٰ يَتُوبُونَ وَ لاٰ هُمْ يَذَّكَّرُونَ");
        sor.Ayaat.Add("وَ إِذٰا مٰا أُنْزِلَتْ سُورَةٌ نَظَرَ بَعْضُهُمْ إِلىٰ بَعْضٍ هَلْ يَرٰاكُمْ مِنْ أَحَدٍ ثُمَّ انْصَرَفُوا صَرَفَ اللّٰهُ قُلُوبَهُمْ بِأَنَّهُمْ قَوْمٌ لاٰ يَفْقَهُونَ");
        sor.Ayaat.Add("لَقَدْ جٰاءَكُمْ رَسُولٌ مِنْ أَنْفُسِكُمْ عَزِيزٌ عَلَيْهِ مٰا عَنِتُّمْ حَرِيصٌ عَلَيْكُمْ بِالْمُؤْمِنِينَ رَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("فَإِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُ لاٰ إِلٰهَ إِلاّٰ هُوَ عَلَيْهِ تَوَكَّلْتُ وَ هُوَ رَبُّ الْعَرْشِ الْعَظِيمِ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١٠ - یونس";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الر تِلْكَ آيٰاتُ الْكِتٰابِ الْحَكِيمِ");
        sor.Ayaat.Add("أَ كٰانَ لِلنّٰاسِ عَجَباً أَنْ أَوْحَيْنٰا إِلىٰ رَجُلٍ مِنْهُمْ أَنْ أَنْذِرِ النّٰاسَ وَ بَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ قٰالَ الْكٰافِرُونَ إِنَّ هٰذٰا لَسٰاحِرٌ مُبِينٌ");
        sor.Ayaat.Add("إِنَّ رَبَّكُمُ اللّٰهُ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ فِي سِتَّةِ أَيّٰامٍ ثُمَّ اسْتَوىٰ عَلَى الْعَرْشِ يُدَبِّرُ الْأَمْرَ مٰا مِنْ شَفِيعٍ إِلاّٰ مِنْ بَعْدِ إِذْنِهِ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوهُ أَ فَلاٰ تَذَكَّرُونَ");
        sor.Ayaat.Add("إِلَيْهِ مَرْجِعُكُمْ جَمِيعاً وَعْدَ اللّٰهِ حَقًّا إِنَّهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ لِيَجْزِيَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ بِالْقِسْطِ وَ الَّذِينَ كَفَرُوا لَهُمْ شَرٰابٌ مِنْ حَمِيمٍ وَ عَذٰابٌ أَلِيمٌ بِمٰا كٰانُوا يَكْفُرُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي جَعَلَ الشَّمْسَ ضِيٰاءً وَ الْقَمَرَ نُوراً وَ قَدَّرَهُ مَنٰازِلَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَ الْحِسٰابَ مٰا خَلَقَ اللّٰهُ ذٰلِكَ إِلاّٰ بِالْحَقِّ يُفَصِّلُ الْآيٰاتِ لِقَوْمٍ يَعْلَمُونَ");
        sor.Ayaat.Add("إِنَّ فِي اخْتِلاٰفِ اللَّيْلِ وَ النَّهٰارِ وَ مٰا خَلَقَ اللّٰهُ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ لَآيٰاتٍ لِقَوْمٍ يَتَّقُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ لاٰ يَرْجُونَ لِقٰاءَنٰا وَ رَضُوا بِالْحَيٰاةِ الدُّنْيٰا وَ اطْمَأَنُّوا بِهٰا وَ الَّذِينَ هُمْ عَنْ آيٰاتِنٰا غٰافِلُونَ");
        sor.Ayaat.Add("أُولٰئِكَ مَأْوٰاهُمُ النّٰارُ بِمٰا كٰانُوا يَكْسِبُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ يَهْدِيهِمْ رَبُّهُمْ بِإِيمٰانِهِمْ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهٰارُ فِي جَنّٰاتِ النَّعِيمِ");
        sor.Ayaat.Add("دَعْوٰاهُمْ فِيهٰا سُبْحٰانَكَ اللّٰهُمَّ وَ تَحِيَّتُهُمْ فِيهٰا سَلاٰمٌ وَ آخِرُ دَعْوٰاهُمْ أَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("وَ لَوْ يُعَجِّلُ اللّٰهُ لِلنّٰاسِ الشَّرَّ اسْتِعْجٰالَهُمْ بِالْخَيْرِ لَقُضِيَ إِلَيْهِمْ أَجَلُهُمْ فَنَذَرُ الَّذِينَ لاٰ يَرْجُونَ لِقٰاءَنٰا فِي طُغْيٰانِهِمْ يَعْمَهُونَ");
        sor.Ayaat.Add("وَ إِذٰا مَسَّ الْإِنْسٰانَ الضُّرُّ دَعٰانٰا لِجَنْبِهِ أَوْ قٰاعِداً أَوْ قٰائِماً فَلَمّٰا كَشَفْنٰا عَنْهُ ضُرَّهُ مَرَّ كَأَنْ لَمْ يَدْعُنٰا إِلىٰ ضُرٍّ مَسَّهُ كَذٰلِكَ زُيِّنَ لِلْمُسْرِفِينَ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَهْلَكْنَا الْقُرُونَ مِنْ قَبْلِكُمْ لَمّٰا ظَلَمُوا وَ جٰاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰاتِ وَ مٰا كٰانُوا لِيُؤْمِنُوا كَذٰلِكَ نَجْزِي الْقَوْمَ الْمُجْرِمِينَ");
        sor.Ayaat.Add("ثُمَّ جَعَلْنٰاكُمْ خَلاٰئِفَ فِي الْأَرْضِ مِنْ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُونَ");
        sor.Ayaat.Add("وَ إِذٰا تُتْلىٰ عَلَيْهِمْ آيٰاتُنٰا بَيِّنٰاتٍ قٰالَ الَّذِينَ لاٰ يَرْجُونَ لِقٰاءَنَا ائْتِ بِقُرْآنٍ غَيْرِ هٰذٰا أَوْ بَدِّلْهُ قُلْ مٰا يَكُونُ لِي أَنْ أُبَدِّلَهُ مِنْ تِلْقٰاءِ نَفْسِي إِنْ أَتَّبِعُ إِلاّٰ مٰا يُوحىٰ إِلَيَّ إِنِّي أَخٰافُ إِنْ عَصَيْتُ رَبِّي عَذٰابَ يَوْمٍ عَظِيمٍ");
        sor.Ayaat.Add("قُلْ لَوْ شٰاءَ اللّٰهُ مٰا تَلَوْتُهُ عَلَيْكُمْ وَ لاٰ أَدْرٰاكُمْ بِهِ فَقَدْ لَبِثْتُ فِيكُمْ عُمُراً مِنْ قَبْلِهِ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("فَمَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً أَوْ كَذَّبَ بِآيٰاتِهِ إِنَّهُ لاٰ يُفْلِحُ الْمُجْرِمُونَ");
        sor.Ayaat.Add("وَ يَعْبُدُونَ مِنْ دُونِ اللّٰهِ مٰا لاٰ يَضُرُّهُمْ وَ لاٰ يَنْفَعُهُمْ وَ يَقُولُونَ هٰؤُلاٰءِ شُفَعٰاؤُنٰا عِنْدَ اللّٰهِ قُلْ أَ تُنَبِّئُونَ اللّٰهَ بِمٰا لاٰ يَعْلَمُ فِي السَّمٰاوٰاتِ وَ لاٰ فِي الْأَرْضِ سُبْحٰانَهُ وَ تَعٰالىٰ عَمّٰا يُشْرِكُونَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ النّٰاسُ إِلاّٰ أُمَّةً وٰاحِدَةً فَاخْتَلَفُوا وَ لَوْ لاٰ كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ فِيمٰا فِيهِ يَخْتَلِفُونَ");
        sor.Ayaat.Add("وَ يَقُولُونَ لَوْ لاٰ أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ فَقُلْ إِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ");
        sor.Ayaat.Add("وَ إِذٰا أَذَقْنَا النّٰاسَ رَحْمَةً مِنْ بَعْدِ ضَرّٰاءَ مَسَّتْهُمْ إِذٰا لَهُمْ مَكْرٌ فِي آيٰاتِنٰا قُلِ اللّٰهُ أَسْرَعُ مَكْراً إِنَّ رُسُلَنٰا يَكْتُبُونَ مٰا تَمْكُرُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي يُسَيِّرُكُمْ فِي الْبَرِّ وَ الْبَحْرِ حَتّٰى إِذٰا كُنْتُمْ فِي الْفُلْكِ وَ جَرَيْنَ بِهِمْ بِرِيحٍ طَيِّبَةٍ وَ فَرِحُوا بِهٰا جٰاءَتْهٰا رِيحٌ عٰاصِفٌ وَ جٰاءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكٰانٍ وَ ظَنُّوا أَنَّهُمْ أُحِيطَ بِهِمْ دَعَوُا اللّٰهَ مُخْلِصِينَ لَهُ الدِّينَ لَئِنْ أَنْجَيْتَنٰا مِنْ هٰذِهِ لَنَكُونَنَّ مِنَ الشّٰاكِرِينَ");
        sor.Ayaat.Add("فَلَمّٰا أَنْجٰاهُمْ إِذٰا هُمْ يَبْغُونَ فِي الْأَرْضِ بِغَيْرِ الْحَقِّ يٰا أَيُّهَا النّٰاسُ إِنَّمٰا بَغْيُكُمْ عَلىٰ أَنْفُسِكُمْ مَتٰاعَ الْحَيٰاةِ الدُّنْيٰا ثُمَّ إِلَيْنٰا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("إِنَّمٰا مَثَلُ الْحَيٰاةِ الدُّنْيٰا كَمٰاءٍ أَنْزَلْنٰاهُ مِنَ السَّمٰاءِ فَاخْتَلَطَ بِهِ نَبٰاتُ الْأَرْضِ مِمّٰا يَأْكُلُ النّٰاسُ وَ الْأَنْعٰامُ حَتّٰى إِذٰا أَخَذَتِ الْأَرْضُ زُخْرُفَهٰا وَ ازَّيَّنَتْ وَ ظَنَّ أَهْلُهٰا أَنَّهُمْ قٰادِرُونَ عَلَيْهٰا أَتٰاهٰا أَمْرُنٰا لَيْلاً أَوْ نَهٰاراً فَجَعَلْنٰاهٰا حَصِيداً كَأَنْ لَمْ تَغْنَ بِالْأَمْسِ كَذٰلِكَ نُفَصِّلُ الْآيٰاتِ لِقَوْمٍ يَتَفَكَّرُونَ");
        sor.Ayaat.Add("وَ اللّٰهُ يَدْعُوا إِلىٰ دٰارِ السَّلاٰمِ وَ يَهْدِي مَنْ يَشٰاءُ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("لِلَّذِينَ أَحْسَنُوا الْحُسْنىٰ وَ زِيٰادَةٌ وَ لاٰ يَرْهَقُ وُجُوهَهُمْ قَتَرٌ وَ لاٰ ذِلَّةٌ أُولٰئِكَ أَصْحٰابُ الْجَنَّةِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ كَسَبُوا السَّيِّئٰاتِ جَزٰاءُ سَيِّئَةٍ بِمِثْلِهٰا وَ تَرْهَقُهُمْ ذِلَّةٌ مٰا لَهُمْ مِنَ اللّٰهِ مِنْ عٰاصِمٍ كَأَنَّمٰا أُغْشِيَتْ وُجُوهُهُمْ قِطَعاً مِنَ اللَّيْلِ مُظْلِماً أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ يَوْمَ نَحْشُرُهُمْ جَمِيعاً ثُمَّ نَقُولُ لِلَّذِينَ أَشْرَكُوا مَكٰانَكُمْ أَنْتُمْ وَ شُرَكٰاؤُكُمْ فَزَيَّلْنٰا بَيْنَهُمْ وَ قٰالَ شُرَكٰاؤُهُمْ مٰا كُنْتُمْ إِيّٰانٰا تَعْبُدُونَ");
        sor.Ayaat.Add("فَكَفىٰ بِاللّٰهِ شَهِيداً بَيْنَنٰا وَ بَيْنَكُمْ إِنْ كُنّٰا عَنْ عِبٰادَتِكُمْ لَغٰافِلِينَ");
        sor.Ayaat.Add("هُنٰالِكَ تَبْلُوا كُلُّ نَفْسٍ مٰا أَسْلَفَتْ وَ رُدُّوا إِلَى اللّٰهِ مَوْلاٰهُمُ الْحَقِّ وَ ضَلَّ عَنْهُمْ مٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("قُلْ مَنْ يَرْزُقُكُمْ مِنَ السَّمٰاءِ وَ الْأَرْضِ أَمَّنْ يَمْلِكُ السَّمْعَ وَ الْأَبْصٰارَ وَ مَنْ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَ يُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَ مَنْ يُدَبِّرُ الْأَمْرَ فَسَيَقُولُونَ اللّٰهُ فَقُلْ أَ فَلاٰ تَتَّقُونَ");
        sor.Ayaat.Add("فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّ فَمٰاذٰا بَعْدَ الْحَقِّ إِلاَّ الضَّلاٰلُ فَأَنّٰى تُصْرَفُونَ");
        sor.Ayaat.Add("كَذٰلِكَ حَقَّتْ كَلِمَةُ رَبِّكَ عَلَى الَّذِينَ فَسَقُوا أَنَّهُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("قُلْ هَلْ مِنْ شُرَكٰائِكُمْ مَنْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيدُهُ فَأَنّٰى تُؤْفَكُونَ");
        sor.Ayaat.Add("قُلْ هَلْ مِنْ شُرَكٰائِكُمْ مَنْ يَهْدِي إِلَى الْحَقِّ قُلِ اللّٰهُ يَهْدِي لِلْحَقِّ أَ فَمَنْ يَهْدِي إِلَى الْحَقِّ أَحَقُّ أَنْ يُتَّبَعَ أَمَّنْ لاٰ يَهِدِّي إِلاّٰ أَنْ يُهْدىٰ فَمٰا لَكُمْ كَيْفَ تَحْكُمُونَ");
        sor.Ayaat.Add("وَ مٰا يَتَّبِعُ أَكْثَرُهُمْ إِلاّٰ ظَنًّا إِنَّ الظَّنَّ لاٰ يُغْنِي مِنَ الْحَقِّ شَيْئاً إِنَّ اللّٰهَ عَلِيمٌ بِمٰا يَفْعَلُونَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ هٰذَا الْقُرْآنُ أَنْ يُفْتَرىٰ مِنْ دُونِ اللّٰهِ وَ لٰكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَ تَفْصِيلَ الْكِتٰابِ لاٰ رَيْبَ فِيهِ مِنْ رَبِّ الْعٰالَمِينَ");
        sor.Ayaat.Add("أَمْ يَقُولُونَ افْتَرٰاهُ قُلْ فَأْتُوا بِسُورَةٍ مِثْلِهِ وَ ادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللّٰهِ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("بَلْ كَذَّبُوا بِمٰا لَمْ يُحِيطُوا بِعِلْمِهِ وَ لَمّٰا يَأْتِهِمْ تَأْوِيلُهُ كَذٰلِكَ كَذَّبَ الَّذِينَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كٰانَ عٰاقِبَةُ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يُؤْمِنُ بِهِ وَ مِنْهُمْ مَنْ لاٰ يُؤْمِنُ بِهِ وَ رَبُّكَ أَعْلَمُ بِالْمُفْسِدِينَ");
        sor.Ayaat.Add("وَ إِنْ كَذَّبُوكَ فَقُلْ لِي عَمَلِي وَ لَكُمْ عَمَلُكُمْ أَنْتُمْ بَرِيئُونَ مِمّٰا أَعْمَلُ وَ أَنَا بَرِيءٌ مِمّٰا تَعْمَلُونَ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يَسْتَمِعُونَ إِلَيْكَ أَ فَأَنْتَ تُسْمِعُ الصُّمَّ وَ لَوْ كٰانُوا لاٰ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ مِنْهُمْ مَنْ يَنْظُرُ إِلَيْكَ أَ فَأَنْتَ تَهْدِي الْعُمْيَ وَ لَوْ كٰانُوا لاٰ يُبْصِرُونَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ لاٰ يَظْلِمُ النّٰاسَ شَيْئاً وَ لٰكِنَّ النّٰاسَ أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("وَ يَوْمَ يَحْشُرُهُمْ كَأَنْ لَمْ يَلْبَثُوا إِلاّٰ سٰاعَةً مِنَ النَّهٰارِ يَتَعٰارَفُونَ بَيْنَهُمْ قَدْ خَسِرَ الَّذِينَ كَذَّبُوا بِلِقٰاءِ اللّٰهِ وَ مٰا كٰانُوا مُهْتَدِينَ");
        sor.Ayaat.Add("وَ إِمّٰا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنٰا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَهِيدٌ عَلىٰ مٰا يَفْعَلُونَ");
        sor.Ayaat.Add("وَ لِكُلِّ أُمَّةٍ رَسُولٌ فَإِذٰا جٰاءَ رَسُولُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("وَ يَقُولُونَ مَتىٰ هٰذَا الْوَعْدُ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("قُلْ لاٰ أَمْلِكُ لِنَفْسِي ضَرًّا وَ لاٰ نَفْعاً إِلاّٰ مٰا شٰاءَ اللّٰهُ لِكُلِّ أُمَّةٍ أَجَلٌ إِذٰا جٰاءَ أَجَلُهُمْ فَلاٰ يَسْتَأْخِرُونَ سٰاعَةً وَ لاٰ يَسْتَقْدِمُونَ");
        sor.Ayaat.Add("قُلْ أَ رَأَيْتُمْ إِنْ أَتٰاكُمْ عَذٰابُهُ بَيٰاتاً أَوْ نَهٰاراً مٰاذٰا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُونَ");
        sor.Ayaat.Add("أَ ثُمَّ إِذٰا مٰا وَقَعَ آمَنْتُمْ بِهِ آلْآنَ وَ قَدْ كُنْتُمْ بِهِ تَسْتَعْجِلُونَ");
        sor.Ayaat.Add("ثُمَّ قِيلَ لِلَّذِينَ ظَلَمُوا ذُوقُوا عَذٰابَ الْخُلْدِ هَلْ تُجْزَوْنَ إِلاّٰ بِمٰا كُنْتُمْ تَكْسِبُونَ");
        sor.Ayaat.Add("وَ يَسْتَنْبِئُونَكَ أَ حَقٌّ هُوَ قُلْ إِي وَ رَبِّي إِنَّهُ لَحَقٌّ وَ مٰا أَنْتُمْ بِمُعْجِزِينَ");
        sor.Ayaat.Add("وَ لَوْ أَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مٰا فِي الْأَرْضِ لاَفْتَدَتْ بِهِ وَ أَسَرُّوا النَّدٰامَةَ لَمّٰا رَأَوُا الْعَذٰابَ وَ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("أَلاٰ إِنَّ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ أَلاٰ إِنَّ وَعْدَ اللّٰهِ حَقٌّ وَ لٰكِنَّ أَكْثَرَهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("هُوَ يُحيِي وَ يُمِيتُ وَ إِلَيْهِ تُرْجَعُونَ");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ قَدْ جٰاءَتْكُمْ مَوْعِظَةٌ مِنْ رَبِّكُمْ وَ شِفٰاءٌ لِمٰا فِي الصُّدُورِ وَ هُدىً وَ رَحْمَةٌ لِلْمُؤْمِنِينَ");
        sor.Ayaat.Add("قُلْ بِفَضْلِ اللّٰهِ وَ بِرَحْمَتِهِ فَبِذٰلِكَ فَلْيَفْرَحُوا هُوَ خَيْرٌ مِمّٰا يَجْمَعُونَ");
        sor.Ayaat.Add("قُلْ أَ رَأَيْتُمْ مٰا أَنْزَلَ اللّٰهُ لَكُمْ مِنْ رِزْقٍ فَجَعَلْتُمْ مِنْهُ حَرٰاماً وَ حَلاٰلاً قُلْ آللّٰهُ أَذِنَ لَكُمْ أَمْ عَلَى اللّٰهِ تَفْتَرُونَ");
        sor.Ayaat.Add("وَ مٰا ظَنُّ الَّذِينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰامَةِ إِنَّ اللّٰهَ لَذُو فَضْلٍ عَلَى النّٰاسِ وَ لٰكِنَّ أَكْثَرَهُمْ لاٰ يَشْكُرُونَ");
        sor.Ayaat.Add("وَ مٰا تَكُونُ فِي شَأْنٍ وَ مٰا تَتْلُوا مِنْهُ مِنْ قُرْآنٍ وَ لاٰ تَعْمَلُونَ مِنْ عَمَلٍ إِلاّٰ كُنّٰا عَلَيْكُمْ شُهُوداً إِذْ تُفِيضُونَ فِيهِ وَ مٰا يَعْزُبُ عَنْ رَبِّكَ مِنْ مِثْقٰالِ ذَرَّةٍ فِي الْأَرْضِ وَ لاٰ فِي السَّمٰاءِ وَ لاٰ أَصْغَرَ مِنْ ذٰلِكَ وَ لاٰ أَكْبَرَ إِلاّٰ فِي كِتٰابٍ مُبِينٍ");
        sor.Ayaat.Add("أَلاٰ إِنَّ أَوْلِيٰاءَ اللّٰهِ لاٰ خَوْفٌ عَلَيْهِمْ وَ لاٰ هُمْ يَحْزَنُونَ");
        sor.Ayaat.Add("الَّذِينَ آمَنُوا وَ كٰانُوا يَتَّقُونَ");
        sor.Ayaat.Add("لَهُمُ الْبُشْرىٰ فِي الْحَيٰاةِ الدُّنْيٰا وَ فِي الْآخِرَةِ لاٰ تَبْدِيلَ لِكَلِمٰاتِ اللّٰهِ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ");
        sor.Ayaat.Add("وَ لاٰ يَحْزُنْكَ قَوْلُهُمْ إِنَّ الْعِزَّةَ لِلّٰهِ جَمِيعاً هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("أَلاٰ إِنَّ لِلّٰهِ مَنْ فِي السَّمٰاوٰاتِ وَ مَنْ فِي الْأَرْضِ وَ مٰا يَتَّبِعُ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ شُرَكٰاءَ إِنْ يَتَّبِعُونَ إِلاَّ الظَّنَّ وَ إِنْ هُمْ إِلاّٰ يَخْرُصُونَ");
        sor.Ayaat.Add("هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِتَسْكُنُوا فِيهِ وَ النَّهٰارَ مُبْصِراً إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِقَوْمٍ يَسْمَعُونَ");
        sor.Ayaat.Add("قٰالُوا اتَّخَذَ اللّٰهُ وَلَداً سُبْحٰانَهُ هُوَ الْغَنِيُّ لَهُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ إِنْ عِنْدَكُمْ مِنْ سُلْطٰانٍ بِهٰذٰا أَ تَقُولُونَ عَلَى اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("قُلْ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ لاٰ يُفْلِحُونَ");
        sor.Ayaat.Add("مَتٰاعٌ فِي الدُّنْيٰا ثُمَّ إِلَيْنٰا مَرْجِعُهُمْ ثُمَّ نُذِيقُهُمُ الْعَذٰابَ الشَّدِيدَ بِمٰا كٰانُوا يَكْفُرُونَ");
        sor.Ayaat.Add("وَ اتْلُ عَلَيْهِمْ نَبَأَ نُوحٍ إِذْ قٰالَ لِقَوْمِهِ يٰا قَوْمِ إِنْ كٰانَ كَبُرَ عَلَيْكُمْ مَقٰامِي وَ تَذْكِيرِي بِآيٰاتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَأَجْمِعُوا أَمْرَكُمْ وَ شُرَكٰاءَكُمْ ثُمَّ لاٰ يَكُنْ أَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوا إِلَيَّ وَ لاٰ تُنْظِرُونِ");
        sor.Ayaat.Add("فَإِنْ تَوَلَّيْتُمْ فَمٰا سَأَلْتُكُمْ مِنْ أَجْرٍ إِنْ أَجْرِيَ إِلاّٰ عَلَى اللّٰهِ وَ أُمِرْتُ أَنْ أَكُونَ مِنَ الْمُسْلِمِينَ");
        sor.Ayaat.Add("فَكَذَّبُوهُ فَنَجَّيْنٰاهُ وَ مَنْ مَعَهُ فِي الْفُلْكِ وَ جَعَلْنٰاهُمْ خَلاٰئِفَ وَ أَغْرَقْنَا الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا فَانْظُرْ كَيْفَ كٰانَ عٰاقِبَةُ الْمُنْذَرِينَ");
        sor.Ayaat.Add("ثُمَّ بَعَثْنٰا مِنْ بَعْدِهِ رُسُلاً إِلىٰ قَوْمِهِمْ فَجٰاؤُهُمْ بِالْبَيِّنٰاتِ فَمٰا كٰانُوا لِيُؤْمِنُوا بِمٰا كَذَّبُوا بِهِ مِنْ قَبْلُ كَذٰلِكَ نَطْبَعُ عَلىٰ قُلُوبِ الْمُعْتَدِينَ");
        sor.Ayaat.Add("ثُمَّ بَعَثْنٰا مِنْ بَعْدِهِمْ مُوسىٰ وَ هٰارُونَ إِلىٰ فِرْعَوْنَ وَ مَلاَئِهِ بِآيٰاتِنٰا فَاسْتَكْبَرُوا وَ كٰانُوا قَوْماً مُجْرِمِينَ");
        sor.Ayaat.Add("فَلَمّٰا جٰاءَهُمُ الْحَقُّ مِنْ عِنْدِنٰا قٰالُوا إِنَّ هٰذٰا لَسِحْرٌ مُبِينٌ");
        sor.Ayaat.Add("قٰالَ مُوسىٰ أَ تَقُولُونَ لِلْحَقِّ لَمّٰا جٰاءَكُمْ أَ سِحْرٌ هٰذٰا وَ لاٰ يُفْلِحُ السّٰاحِرُونَ");
        sor.Ayaat.Add("قٰالُوا أَ جِئْتَنٰا لِتَلْفِتَنٰا عَمّٰا وَجَدْنٰا عَلَيْهِ آبٰاءَنٰا وَ تَكُونَ لَكُمَا الْكِبْرِيٰاءُ فِي الْأَرْضِ وَ مٰا نَحْنُ لَكُمٰا بِمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ قٰالَ فِرْعَوْنُ ائْتُونِي بِكُلِّ سٰاحِرٍ عَلِيمٍ");
        sor.Ayaat.Add("فَلَمّٰا جٰاءَ السَّحَرَةُ قٰالَ لَهُمْ مُوسىٰ أَلْقُوا مٰا أَنْتُمْ مُلْقُونَ");
        sor.Ayaat.Add("فَلَمّٰا أَلْقَوْا قٰالَ مُوسىٰ مٰا جِئْتُمْ بِهِ السِّحْرُ إِنَّ اللّٰهَ سَيُبْطِلُهُ إِنَّ اللّٰهَ لاٰ يُصْلِحُ عَمَلَ الْمُفْسِدِينَ");
        sor.Ayaat.Add("وَ يُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمٰاتِهِ وَ لَوْ كَرِهَ الْمُجْرِمُونَ");
        sor.Ayaat.Add("فَمٰا آمَنَ لِمُوسىٰ إِلاّٰ ذُرِّيَّةٌ مِنْ قَوْمِهِ عَلىٰ خَوْفٍ مِنْ فِرْعَوْنَ وَ مَلاَئِهِمْ أَنْ يَفْتِنَهُمْ وَ إِنَّ فِرْعَوْنَ لَعٰالٍ فِي الْأَرْضِ وَ إِنَّهُ لَمِنَ الْمُسْرِفِينَ");
        sor.Ayaat.Add("وَ قٰالَ مُوسىٰ يٰا قَوْمِ إِنْ كُنْتُمْ آمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُوا إِنْ كُنْتُمْ مُسْلِمِينَ");
        sor.Ayaat.Add("فَقٰالُوا عَلَى اللّٰهِ تَوَكَّلْنٰا رَبَّنٰا لاٰ تَجْعَلْنٰا فِتْنَةً لِلْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ نَجِّنٰا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكٰافِرِينَ");
        sor.Ayaat.Add("وَ أَوْحَيْنٰا إِلىٰ مُوسىٰ وَ أَخِيهِ أَنْ تَبَوَّءٰا لِقَوْمِكُمٰا بِمِصْرَ بُيُوتاً وَ اجْعَلُوا بُيُوتَكُمْ قِبْلَةً وَ أَقِيمُوا الصَّلاٰةَ وَ بَشِّرِ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ قٰالَ مُوسىٰ رَبَّنٰا إِنَّكَ آتَيْتَ فِرْعَوْنَ وَ مَلَأَهُ زِينَةً وَ أَمْوٰالاً فِي الْحَيٰاةِ الدُّنْيٰا رَبَّنٰا لِيُضِلُّوا عَنْ سَبِيلِكَ رَبَّنَا اطْمِسْ عَلىٰ أَمْوٰالِهِمْ وَ اشْدُدْ عَلىٰ قُلُوبِهِمْ فَلاٰ يُؤْمِنُوا حَتّٰى يَرَوُا الْعَذٰابَ الْأَلِيمَ");
        sor.Ayaat.Add("قٰالَ قَدْ أُجِيبَتْ دَعْوَتُكُمٰا فَاسْتَقِيمٰا وَ لاٰ تَتَّبِعٰانِّ سَبِيلَ الَّذِينَ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ جٰاوَزْنٰا بِبَنِي إِسْرٰائِيلَ الْبَحْرَ فَأَتْبَعَهُمْ فِرْعَوْنُ وَ جُنُودُهُ بَغْياً وَ عَدْواً حَتّٰى إِذٰا أَدْرَكَهُ الْغَرَقُ قٰالَ آمَنْتُ أَنَّهُ لاٰ إِلٰهَ إِلاَّ الَّذِي آمَنَتْ بِهِ بَنُوا إِسْرٰائِيلَ وَ أَنَا مِنَ الْمُسْلِمِينَ");
        sor.Ayaat.Add("آلْآنَ وَ قَدْ عَصَيْتَ قَبْلُ وَ كُنْتَ مِنَ الْمُفْسِدِينَ");
        sor.Ayaat.Add("فَالْيَوْمَ نُنَجِّيكَ بِبَدَنِكَ لِتَكُونَ لِمَنْ خَلْفَكَ آيَةً وَ إِنَّ كَثِيراً مِنَ النّٰاسِ عَنْ آيٰاتِنٰا لَغٰافِلُونَ");
        sor.Ayaat.Add("وَ لَقَدْ بَوَّأْنٰا بَنِي إِسْرٰائِيلَ مُبَوَّأَ صِدْقٍ وَ رَزَقْنٰاهُمْ مِنَ الطَّيِّبٰاتِ فَمَا اخْتَلَفُوا حَتّٰى جٰاءَهُمُ الْعِلْمُ إِنَّ رَبَّكَ يَقْضِي بَيْنَهُمْ يَوْمَ الْقِيٰامَةِ فِيمٰا كٰانُوا فِيهِ يَخْتَلِفُونَ");
        sor.Ayaat.Add("فَإِنْ كُنْتَ فِي شَكٍّ مِمّٰا أَنْزَلْنٰا إِلَيْكَ فَسْئَلِ الَّذِينَ يَقْرَؤُنَ الْكِتٰابَ مِنْ قَبْلِكَ لَقَدْ جٰاءَكَ الْحَقُّ مِنْ رَبِّكَ فَلاٰ تَكُونَنَّ مِنَ المُمْتَرِينَ");
        sor.Ayaat.Add("وَ لاٰ تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآيٰاتِ اللّٰهِ فَتَكُونَ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ حَقَّتْ عَلَيْهِمْ كَلِمَتُ رَبِّكَ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ لَوْ جٰاءَتْهُمْ كُلُّ آيَةٍ حَتّٰى يَرَوُا الْعَذٰابَ الْأَلِيمَ");
        sor.Ayaat.Add("فَلَوْ لاٰ كٰانَتْ قَرْيَةٌ آمَنَتْ فَنَفَعَهٰا إِيمٰانُهٰا إِلاّٰ قَوْمَ يُونُسَ لَمّٰا آمَنُوا كَشَفْنٰا عَنْهُمْ عَذٰابَ الْخِزْيِ فِي الْحَيٰاةِ الدُّنْيٰا وَ مَتَّعْنٰاهُمْ إِلىٰ حِينٍ");
        sor.Ayaat.Add("وَ لَوْ شٰاءَ رَبُّكَ لَآمَنَ مَنْ فِي الْأَرْضِ كُلُّهُمْ جَمِيعاً أَ فَأَنْتَ تُكْرِهُ النّٰاسَ حَتّٰى يَكُونُوا مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ لِنَفْسٍ أَنْ تُؤْمِنَ إِلاّٰ بِإِذْنِ اللّٰهِ وَ يَجْعَلُ الرِّجْسَ عَلَى الَّذِينَ لاٰ يَعْقِلُونَ");
        sor.Ayaat.Add("قُلِ انْظُرُوا مٰاذٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا تُغْنِي الْآيٰاتُ وَ النُّذُرُ عَنْ قَوْمٍ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("فَهَلْ يَنْتَظِرُونَ إِلاّٰ مِثْلَ أَيّٰامِ الَّذِينَ خَلَوْا مِنْ قَبْلِهِمْ قُلْ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ");
        sor.Ayaat.Add("ثُمَّ نُنَجِّي رُسُلَنٰا وَ الَّذِينَ آمَنُوا كَذٰلِكَ حَقًّا عَلَيْنٰا نُنْجِ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("قُلْ يٰا أَيُّهَا النّٰاسُ إِنْ كُنْتُمْ فِي شَكٍّ مِنْ دِينِي فَلاٰ أَعْبُدُ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللّٰهِ وَ لٰكِنْ أَعْبُدُ اللّٰهَ الَّذِي يَتَوَفّٰاكُمْ وَ أُمِرْتُ أَنْ أَكُونَ مِنَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ أَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفاً وَ لاٰ تَكُونَنَّ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("وَ لاٰ تَدْعُ مِنْ دُونِ اللّٰهِ مٰا لاٰ يَنْفَعُكَ وَ لاٰ يَضُرُّكَ فَإِنْ فَعَلْتَ فَإِنَّكَ إِذاً مِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ إِنْ يَمْسَسْكَ اللّٰهُ بِضُرٍّ فَلاٰ كٰاشِفَ لَهُ إِلاّٰ هُوَ وَ إِنْ يُرِدْكَ بِخَيْرٍ فَلاٰ رَادَّ لِفَضْلِهِ يُصِيبُ بِهِ مَنْ يَشٰاءُ مِنْ عِبٰادِهِ وَ هُوَ الْغَفُورُ الرَّحِيمُ");
        sor.Ayaat.Add("قُلْ يٰا أَيُّهَا النّٰاسُ قَدْ جٰاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْ فَمَنِ اهْتَدىٰ فَإِنَّمٰا يَهْتَدِي لِنَفْسِهِ وَ مَنْ ضَلَّ فَإِنَّمٰا يَضِلُّ عَلَيْهٰا وَ مٰا أَنَا عَلَيْكُمْ بِوَكِيلٍ");
        sor.Ayaat.Add("وَ اتَّبِعْ مٰا يُوحىٰ إِلَيْكَ وَ اصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُ وَ هُوَ خَيْرُ الْحٰاكِمِينَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١١ - هود";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الر كِتٰابٌ أُحْكِمَتْ آيٰاتُهُ ثُمَّ فُصِّلَتْ مِنْ لَدُنْ حَكِيمٍ خَبِيرٍ");
        sor.Ayaat.Add("أَلاّٰ تَعْبُدُوا إِلاَّ اللّٰهَ إِنَّنِي لَكُمْ مِنْهُ نَذِيرٌ وَ بَشِيرٌ");
        sor.Ayaat.Add("وَ أَنِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُمَتِّعْكُمْ مَتٰاعاً حَسَناً إِلىٰ أَجَلٍ مُسَمًّى وَ يُؤْتِ كُلَّ ذِي فَضْلٍ فَضْلَهُ وَ إِنْ تَوَلَّوْا فَإِنِّي أَخٰافُ عَلَيْكُمْ عَذٰابَ يَوْمٍ كَبِيرٍ");
        sor.Ayaat.Add("إِلَى اللّٰهِ مَرْجِعُكُمْ وَ هُوَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("أَلاٰ إِنَّهُمْ يَثْنُونَ صُدُورَهُمْ لِيَسْتَخْفُوا مِنْهُ أَلاٰ حِينَ يَسْتَغْشُونَ ثِيٰابَهُمْ يَعْلَمُ مٰا يُسِرُّونَ وَ مٰا يُعْلِنُونَ إِنَّهُ عَلِيمٌ بِذٰاتِ الصُّدُورِ");
        sor.Ayaat.Add("وَ مٰا مِنْ دَابَّةٍ فِي الْأَرْضِ إِلاّٰ عَلَى اللّٰهِ رِزْقُهٰا وَ يَعْلَمُ مُسْتَقَرَّهٰا وَ مُسْتَوْدَعَهٰا كُلٌّ فِي كِتٰابٍ مُبِينٍ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ فِي سِتَّةِ أَيّٰامٍ وَ كٰانَ عَرْشُهُ عَلَى الْمٰاءِ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلاً وَ لَئِنْ قُلْتَ إِنَّكُمْ مَبْعُوثُونَ مِنْ بَعْدِ الْمَوْتِ لَيَقُولَنَّ الَّذِينَ كَفَرُوا إِنْ هٰذٰا إِلاّٰ سِحْرٌ مُبِينٌ");
        sor.Ayaat.Add("وَ لَئِنْ أَخَّرْنٰا عَنْهُمُ الْعَذٰابَ إِلىٰ أُمَّةٍ مَعْدُودَةٍ لَيَقُولُنَّ مٰا يَحْبِسُهُ أَلاٰ يَوْمَ يَأْتِيهِمْ لَيْسَ مَصْرُوفاً عَنْهُمْ وَ حٰاقَ بِهِمْ مٰا كٰانُوا بِهِ يَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("وَ لَئِنْ أَذَقْنَا الْإِنْسٰانَ مِنّٰا رَحْمَةً ثُمَّ نَزَعْنٰاهٰا مِنْهُ إِنَّهُ لَيَؤُسٌ كَفُورٌ");
        sor.Ayaat.Add("وَ لَئِنْ أَذَقْنٰاهُ نَعْمٰاءَ بَعْدَ ضَرّٰاءَ مَسَّتْهُ لَيَقُولَنَّ ذَهَبَ السَّيِّئٰاتُ عَنِّي إِنَّهُ لَفَرِحٌ فَخُورٌ");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ صَبَرُوا وَ عَمِلُوا الصّٰالِحٰاتِ أُولٰئِكَ لَهُمْ مَغْفِرَةٌ وَ أَجْرٌ كَبِيرٌ");
        sor.Ayaat.Add("فَلَعَلَّكَ تٰارِكٌ بَعْضَ مٰا يُوحىٰ إِلَيْكَ وَ ضٰائِقٌ بِهِ صَدْرُكَ أَنْ يَقُولُوا لَوْ لاٰ أُنْزِلَ عَلَيْهِ كَنْزٌ أَوْ جٰاءَ مَعَهُ مَلَكٌ إِنَّمٰا أَنْتَ نَذِيرٌ وَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ وَكِيلٌ");
        sor.Ayaat.Add("أَمْ يَقُولُونَ افْتَرٰاهُ قُلْ فَأْتُوا بِعَشْرِ سُوَرٍ مِثْلِهِ مُفْتَرَيٰاتٍ وَ ادْعُوا مَنِ اسْتَطَعْتُمْ مِنْ دُونِ اللّٰهِ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("فَإِلَّمْ يَسْتَجِيبُوا لَكُمْ فَاعْلَمُوا أَنَّمٰا أُنْزِلَ بِعِلْمِ اللّٰهِ وَ أَنْ لاٰ إِلٰهَ إِلاّٰ هُوَ فَهَلْ أَنْتُمْ مُسْلِمُونَ");
        sor.Ayaat.Add("مَنْ كٰانَ يُرِيدُ الْحَيٰاةَ الدُّنْيٰا وَ زِينَتَهٰا نُوَفِّ إِلَيْهِمْ أَعْمٰالَهُمْ فِيهٰا وَ هُمْ فِيهٰا لاٰ يُبْخَسُونَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ لَيْسَ لَهُمْ فِي الْآخِرَةِ إِلاَّ النّٰارُ وَ حَبِطَ مٰا صَنَعُوا فِيهٰا وَ بٰاطِلٌ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("أَ فَمَنْ كٰانَ عَلىٰ بَيِّنَةٍ مِنْ رَبِّهِ وَ يَتْلُوهُ شٰاهِدٌ مِنْهُ وَ مِنْ قَبْلِهِ كِتٰابُ مُوسىٰ إِمٰاماً وَ رَحْمَةً أُولٰئِكَ يُؤْمِنُونَ بِهِ وَ مَنْ يَكْفُرْ بِهِ مِنَ الْأَحْزٰابِ فَالنّٰارُ مَوْعِدُهُ فَلاٰ تَكُ فِي مِرْيَةٍ مِنْهُ إِنَّهُ الْحَقُّ مِنْ رَبِّكَ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ مَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً أُولٰئِكَ يُعْرَضُونَ عَلىٰ رَبِّهِمْ وَ يَقُولُ الْأَشْهٰادُ هٰؤُلاٰءِ الَّذِينَ كَذَبُوا عَلىٰ رَبِّهِمْ أَلاٰ لَعْنَةُ اللّٰهِ عَلَى الظّٰالِمِينَ");
        sor.Ayaat.Add("الَّذِينَ يَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ وَ يَبْغُونَهٰا عِوَجاً وَ هُمْ بِالْآخِرَةِ هُمْ كٰافِرُونَ");
        sor.Ayaat.Add("أُولٰئِكَ لَمْ يَكُونُوا مُعْجِزِينَ فِي الْأَرْضِ وَ مٰا كٰانَ لَهُمْ مِنْ دُونِ اللّٰهِ مِنْ أَوْلِيٰاءَ يُضٰاعَفُ لَهُمُ الْعَذٰابُ مٰا كٰانُوا يَسْتَطِيعُونَ السَّمْعَ وَ مٰا كٰانُوا يُبْصِرُونَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ وَ ضَلَّ عَنْهُمْ مٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("لاٰ جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْأَخْسَرُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ وَ أَخْبَتُوا إِلىٰ رَبِّهِمْ أُولٰئِكَ أَصْحٰابُ الْجَنَّةِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("مَثَلُ الْفَرِيقَيْنِ كَالْأَعْمىٰ وَ الْأَصَمِّ وَ الْبَصِيرِ وَ السَّمِيعِ هَلْ يَسْتَوِيٰانِ مَثَلاً أَ فَلاٰ تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا نُوحاً إِلىٰ قَوْمِهِ إِنِّي لَكُمْ نَذِيرٌ مُبِينٌ");
        sor.Ayaat.Add("أَنْ لاٰ تَعْبُدُوا إِلاَّ اللّٰهَ إِنِّي أَخٰافُ عَلَيْكُمْ عَذٰابَ يَوْمٍ أَلِيمٍ");
        sor.Ayaat.Add("فَقٰالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ مٰا نَرٰاكَ إِلاّٰ بَشَراً مِثْلَنٰا وَ مٰا نَرٰاكَ اتَّبَعَكَ إِلاَّ الَّذِينَ هُمْ أَرٰاذِلُنٰا بٰادِيَ الرَّأْيِ وَ مٰا نَرىٰ لَكُمْ عَلَيْنٰا مِنْ فَضْلٍ بَلْ نَظُنُّكُمْ كٰاذِبِينَ");
        sor.Ayaat.Add("قٰالَ يٰا قَوْمِ أَ رَأَيْتُمْ إِنْ كُنْتُ عَلىٰ بَيِّنَةٍ مِنْ رَبِّي وَ آتٰانِي رَحْمَةً مِنْ عِنْدِهِ فَعُمِّيَتْ عَلَيْكُمْ أَ نُلْزِمُكُمُوهٰا وَ أَنْتُمْ لَهٰا كٰارِهُونَ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ لاٰ أَسْئَلُكُمْ عَلَيْهِ مٰالاً إِنْ أَجرِيَ إِلاّٰ عَلَى اللّٰهِ وَ مٰا أَنَا بِطٰارِدِ الَّذِينَ آمَنُوا إِنَّهُمْ مُلاٰقُوا رَبِّهِمْ وَ لٰكِنِّي أَرٰاكُمْ قَوْماً تَجْهَلُونَ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ مَنْ يَنْصُرُنِي مِنَ اللّٰهِ إِنْ طَرَدْتُهُمْ أَ فَلاٰ تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ لاٰ أَقُولُ لَكُمْ عِنْدِي خَزٰائِنُ اللّٰهِ وَ لاٰ أَعْلَمُ الْغَيْبَ وَ لاٰ أَقُولُ إِنِّي مَلَكٌ وَ لاٰ أَقُولُ لِلَّذِينَ تَزْدَرِي أَعْيُنُكُمْ لَنْ يُؤْتِيَهُمُ اللّٰهُ خَيْراً اللّٰهُ أَعْلَمُ بِمٰا فِي أَنْفُسِهِمْ إِنِّي إِذاً لَمِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("قٰالُوا يٰا نُوحُ قَدْ جٰادَلْتَنٰا فَأَكْثَرْتَ جِدٰالَنٰا فَأْتِنٰا بِمٰا تَعِدُنٰا إِنْ كُنْتَ مِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("قٰالَ إِنَّمٰا يَأْتِيكُمْ بِهِ اللّٰهُ إِنْ شٰاءَ وَ مٰا أَنْتُمْ بِمُعْجِزِينَ");
        sor.Ayaat.Add("وَ لاٰ يَنْفَعُكُمْ نُصْحِي إِنْ أَرَدْتُ أَنْ أَنْصَحَ لَكُمْ إِنْ كٰانَ اللّٰهُ يُرِيدُ أَنْ يُغْوِيَكُمْ هُوَ رَبُّكُمْ وَ إِلَيْهِ تُرْجَعُونَ");
        sor.Ayaat.Add("أَمْ يَقُولُونَ افْتَرٰاهُ قُلْ إِنِ افْتَرَيْتُهُ فَعَلَيَّ إِجْرٰامِي وَ أَنَا بَرِيءٌ مِمّٰا تُجْرِمُونَ");
        sor.Ayaat.Add("وَ أُوحِيَ إِلىٰ نُوحٍ أَنَّهُ لَنْ يُؤْمِنَ مِنْ قَوْمِكَ إِلاّٰ مَنْ قَدْ آمَنَ فَلاٰ تَبْتَئِسْ بِمٰا كٰانُوا يَفْعَلُونَ");
        sor.Ayaat.Add("وَ اصْنَعِ الْفُلْكَ بِأَعْيُنِنٰا وَ وَحْيِنٰا وَ لاٰ تُخٰاطِبْنِي فِي الَّذِينَ ظَلَمُوا إِنَّهُمْ مُغْرَقُونَ");
        sor.Ayaat.Add("وَ يَصْنَعُ الْفُلْكَ وَ كُلَّمٰا مَرَّ عَلَيْهِ مَلَأٌ مِنْ قَوْمِهِ سَخِرُوا مِنْهُ قٰالَ إِنْ تَسْخَرُوا مِنّٰا فَإِنّٰا نَسْخَرُ مِنْكُمْ كَمٰا تَسْخَرُونَ");
        sor.Ayaat.Add("فَسَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذٰابٌ يُخْزِيهِ وَ يَحِلُّ عَلَيْهِ عَذٰابٌ مُقِيمٌ");
        sor.Ayaat.Add("حَتّٰى إِذٰا جٰاءَ أَمْرُنٰا وَ فٰارَ التَّنُّورُ قُلْنَا احْمِلْ فِيهٰا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَ أَهْلَكَ إِلاّٰ مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَ مَنْ آمَنَ وَ مٰا آمَنَ مَعَهُ إِلاّٰ قَلِيلٌ");
        sor.Ayaat.Add("وَ قٰالَ ارْكَبُوا فِيهٰا بِسْمِ اللّٰهِ مَجْرٰاهٰا وَ مُرْسٰاهٰا إِنَّ رَبِّي لَغَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ هِيَ تَجْرِي بِهِمْ فِي مَوْجٍ كَالْجِبٰالِ وَ نٰادىٰ نُوحٌ ابْنَهُ وَ كٰانَ فِي مَعْزِلٍ يٰا بُنَيَّ ارْكَبْ مَعَنٰا وَ لاٰ تَكُنْ مَعَ الْكٰافِرِينَ");
        sor.Ayaat.Add("قٰالَ سَآوِي إِلىٰ جَبَلٍ يَعْصِمُنِي مِنَ الْمٰاءِ قٰالَ لاٰ عٰاصِمَ الْيَوْمَ مِنْ أَمْرِ اللّٰهِ إِلاّٰ مَنْ رَحِمَ وَ حٰالَ بَيْنَهُمَا الْمَوْجُ فَكٰانَ مِنَ الْمُغْرَقِينَ");
        sor.Ayaat.Add("وَ قِيلَ يٰا أَرْضُ ابْلَعِي مٰاءَكِ وَ يٰا سَمٰاءُ أَقْلِعِي وَ غِيضَ الْمٰاءُ وَ قُضِيَ الْأَمْرُ وَ اسْتَوَتْ عَلَى الْجُودِيِّ وَ قِيلَ بُعْداً لِلْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ نٰادىٰ نُوحٌ رَبَّهُ فَقٰالَ رَبِّ إِنَّ ابْنِي مِنْ أَهْلِي وَ إِنَّ وَعْدَكَ الْحَقُّ وَ أَنْتَ أَحْكَمُ الْحٰاكِمِينَ");
        sor.Ayaat.Add("قٰالَ يٰا نُوحُ إِنَّهُ لَيْسَ مِنْ أَهْلِكَ إِنَّهُ عَمَلٌ غَيْرُ صٰالِحٍ فَلاٰ تَسْئَلْنِ مٰا لَيْسَ لَكَ بِهِ عِلْمٌ إِنِّي أَعِظُكَ أَنْ تَكُونَ مِنَ الْجٰاهِلِينَ");
        sor.Ayaat.Add("قٰالَ رَبِّ إِنِّي أَعُوذُ بِكَ أَنْ أَسْئَلَكَ مٰا لَيْسَ لِي بِهِ عِلْمٌ وَ إِلاّٰ تَغْفِرْ لِي وَ تَرْحَمْنِي أَكُنْ مِنَ الْخٰاسِرِينَ");
        sor.Ayaat.Add("قِيلَ يٰا نُوحُ اهْبِطْ بِسَلاٰمٍ مِنّٰا وَ بَرَكٰاتٍ عَلَيْكَ وَ عَلىٰ أُمَمٍ مِمَّنْ مَعَكَ وَ أُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِنّٰا عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("تِلْكَ مِنْ أَنْبٰاءِ الْغَيْبِ نُوحِيهٰا إِلَيْكَ مٰا كُنْتَ تَعْلَمُهٰا أَنْتَ وَ لاٰ قَوْمُكَ مِنْ قَبْلِ هٰذٰا فَاصْبِرْ إِنَّ الْعاقِبَةَ لِلْمُتَّقِينَ");
        sor.Ayaat.Add("وَ إِلىٰ عٰادٍ أَخٰاهُمْ هُوداً قٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ إِنْ أَنْتُمْ إِلاّٰ مُفْتَرُونَ");
        sor.Ayaat.Add("يٰا قَوْمِ لاٰ أَسْئَلُكُمْ عَلَيْهِ أَجْراً إِنْ أَجْرِيَ إِلاّٰ عَلَى الَّذِي فَطَرَنِي أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ يُرْسِلِ السَّمٰاءَ عَلَيْكُمْ مِدْرٰاراً وَ يَزِدْكُمْ قُوَّةً إِلىٰ قُوَّتِكُمْ وَ لاٰ تَتَوَلَّوْا مُجْرِمِينَ");
        sor.Ayaat.Add("قٰالُوا يٰا هُودُ مٰا جِئْتَنٰا بِبَيِّنَةٍ وَ مٰا نَحْنُ بِتٰارِكِي آلِهَتِنٰا عَنْ قَوْلِكَ وَ مٰا نَحْنُ لَكَ بِمُؤْمِنِينَ");
        sor.Ayaat.Add("إِنْ نَقُولُ إِلاَّ اعْتَرٰاكَ بَعْضُ آلِهَتِنٰا بِسُوءٍ قٰالَ إِنِّي أُشْهِدُ اللّٰهَ وَ اشْهَدُوا أَنِّي بَرِيءٌ مِمّٰا تُشْرِكُونَ");
        sor.Ayaat.Add("مِنْ دُونِهِ فَكِيدُونِي جَمِيعاً ثُمَّ لاٰ تُنْظِرُونِ");
        sor.Ayaat.Add("إِنِّي تَوَكَّلْتُ عَلَى اللّٰهِ رَبِّي وَ رَبِّكُمْ مٰا مِنْ دَابَّةٍ إِلاّٰ هُوَ آخِذٌ بِنٰاصِيَتِهٰا إِنَّ رَبِّي عَلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("فَإِنْ تَوَلَّوْا فَقَدْ أَبْلَغْتُكُمْ مٰا أُرْسِلْتُ بِهِ إِلَيْكُمْ وَ يَسْتَخْلِفُ رَبِّي قَوْماً غَيْرَكُمْ وَ لاٰ تَضُرُّونَهُ شَيْئاً إِنَّ رَبِّي عَلىٰ كُلِّ شَيْءٍ حَفِيظٌ");
        sor.Ayaat.Add("وَ لَمّٰا جٰاءَ أَمْرُنٰا نَجَّيْنٰا هُوداً وَ الَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنّٰا وَ نَجَّيْنٰاهُمْ مِنْ عَذٰابٍ غَلِيظٍ");
        sor.Ayaat.Add("وَ تِلْكَ عٰادٌ جَحَدُوا بِآيٰاتِ رَبِّهِمْ وَ عَصَوْا رُسُلَهُ وَ اتَّبَعُوا أَمْرَ كُلِّ جَبّٰارٍ عَنِيدٍ");
        sor.Ayaat.Add("وَ أُتْبِعُوا فِي هٰذِهِ الدُّنْيٰا لَعْنَةً وَ يَوْمَ الْقِيٰامَةِ أَلاٰ إِنَّ عٰاداً كَفَرُوا رَبَّهُمْ أَلاٰ بُعْداً لِعٰادٍ قَوْمِ هُودٍ");
        sor.Ayaat.Add("وَ إِلىٰ ثَمُودَ أَخٰاهُمْ صٰالِحاً قٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ هُوَ أَنْشَأَكُمْ مِنَ الْأَرْضِ وَ اسْتَعْمَرَكُمْ فِيهٰا فَاسْتَغْفِرُوهُ ثُمَّ تُوبُوا إِلَيْهِ إِنَّ رَبِّي قَرِيبٌ مُجِيبٌ");
        sor.Ayaat.Add("قٰالُوا يٰا صٰالِحُ قَدْ كُنْتَ فِينٰا مَرْجُوًّا قَبْلَ هٰذٰا أَ تَنْهٰانٰا أَنْ نَعْبُدَ مٰا يَعْبُدُ آبٰاؤُنٰا وَ إِنَّنٰا لَفِي شَكٍّ مِمّٰا تَدْعُونٰا إِلَيْهِ مُرِيبٍ");
        sor.Ayaat.Add("قٰالَ يٰا قَوْمِ أَ رَأَيْتُمْ إِنْ كُنْتُ عَلىٰ بَيِّنَةٍ مِنْ رَبِّي وَ آتٰانِي مِنْهُ رَحْمَةً فَمَنْ يَنْصُرُنِي مِنَ اللّٰهِ إِنْ عَصَيْتُهُ فَمٰا تَزِيدُونَنِي غَيْرَ تَخْسِيرٍ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ هٰذِهِ نٰاقَةُ اللّٰهِ لَكُمْ آيَةً فَذَرُوهٰا تَأْكُلْ فِي أَرْضِ اللّٰهِ وَ لاٰ تَمَسُّوهٰا بِسُوءٍ فَيَأْخُذَكُمْ عَذٰابٌ قَرِيبٌ");
        sor.Ayaat.Add("فَعَقَرُوهٰا فَقٰالَ تَمَتَّعُوا فِي دٰارِكُمْ ثَلاٰثَةَ أَيّٰامٍ ذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوبٍ");
        sor.Ayaat.Add("فَلَمّٰا جٰاءَ أَمْرُنٰا نَجَّيْنٰا صٰالِحاً وَ الَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنّٰا وَ مِنْ خِزْيِ يَوْمِئِذٍ إِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيزُ");
        sor.Ayaat.Add("وَ أَخَذَ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيٰارِهِمْ جٰاثِمِينَ");
        sor.Ayaat.Add("كَأَنْ لَمْ يَغْنَوْا فِيهٰا أَلاٰ إِنَّ ثَمُودَ كَفَرُوا رَبَّهُمْ أَلاٰ بُعْداً لِثَمُودَ");
        sor.Ayaat.Add("وَ لَقَدْ جٰاءَتْ رُسُلُنٰا إِبْرٰاهِيمَ بِالْبُشْرىٰ قٰالُوا سَلاٰماً قٰالَ سَلاٰمٌ فَمٰا لَبِثَ أَنْ جٰاءَ بِعِجْلٍ حَنِيذٍ");
        sor.Ayaat.Add("فَلَمّٰا رَأىٰ أَيْدِيَهُمْ لاٰ تَصِلُ إِلَيْهِ نَكِرَهُمْ وَ أَوْجَسَ مِنْهُمْ خِيفَةً قٰالُوا لاٰ تَخَفْ إِنّٰا أُرْسِلْنٰا إِلىٰ قَوْمِ لُوطٍ");
        sor.Ayaat.Add("وَ امْرَأَتُهُ قٰائِمَةٌ فَضَحِكَتْ فَبَشَّرْنٰاهٰا بِإِسْحٰاقَ وَ مِنْ وَرٰاءِ إِسْحٰاقَ يَعْقُوبَ");
        sor.Ayaat.Add("قٰالَتْ يٰا وَيْلَتىٰ أَ أَلِدُ وَ أَنَا عَجُوزٌ وَ هٰذٰا بَعْلِي شَيْخاً إِنَّ هٰذٰا لَشَيْءٌ عَجِيبٌ");
        sor.Ayaat.Add("قٰالُوا أَ تَعْجَبِينَ مِنْ أَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَ بَرَكٰاتُهُ عَلَيْكُمْ أَهْلَ الْبَيْتِ إِنَّهُ حَمِيدٌ مَجِيدٌ");
        sor.Ayaat.Add("فَلَمّٰا ذَهَبَ عَنْ إِبْرٰاهِيمَ الرَّوْعُ وَ جٰاءَتْهُ الْبُشْرىٰ يُجٰادِلُنٰا فِي قَوْمِ لُوطٍ");
        sor.Ayaat.Add("إِنَّ إِبْرٰاهِيمَ لَحَلِيمٌ أَوّٰاهٌ مُنِيبٌ");
        sor.Ayaat.Add("يٰا إِبْرٰاهِيمُ أَعْرِضْ عَنْ هٰذٰا إِنَّهُ قَدْ جٰاءَ أَمْرُ رَبِّكَ وَ إِنَّهُمْ آتِيهِمْ عَذٰابٌ غَيْرُ مَرْدُودٍ");
        sor.Ayaat.Add("وَ لَمّٰا جٰاءَتْ رُسُلُنٰا لُوطاً سِيءَ بِهِمْ وَ ضٰاقَ بِهِمْ ذَرْعاً وَ قٰالَ هٰذٰا يَوْمٌ عَصِيبٌ");
        sor.Ayaat.Add("وَ جٰاءَهُ قَوْمُهُ يُهْرَعُونَ إِلَيْهِ وَ مِنْ قَبْلُ كٰانُوا يَعْمَلُونَ السَّيِّئٰاتِ قٰالَ يٰا قَوْمِ هٰؤُلاٰءِ بَنٰاتِي هُنَّ أَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَ لاٰ تُخْزُونِ فِي ضَيْفِي أَ لَيْسَ مِنْكُمْ رَجُلٌ رَشِيدٌ");
        sor.Ayaat.Add("قٰالُوا لَقَدْ عَلِمْتَ مٰا لَنٰا فِي بَنٰاتِكَ مِنْ حَقٍّ وَ إِنَّكَ لَتَعْلَمُ مٰا نُرِيدُ");
        sor.Ayaat.Add("قٰالَ لَوْ أَنَّ لِي بِكُمْ قُوَّةً أَوْ آوِي إِلىٰ رُكْنٍ شَدِيدٍ");
        sor.Ayaat.Add("قٰالُوا يٰا لُوطُ إِنّٰا رُسُلُ رَبِّكَ لَنْ يَصِلُوا إِلَيْكَ فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَ لاٰ يَلْتَفِتْ مِنْكُمْ أَحَدٌ إِلاَّ امْرَأَتَكَ إِنَّهُ مُصِيبُهٰا مٰا أَصٰابَهُمْ إِنَّ مَوْعِدَهُمُ الصُّبْحُ أَ لَيْسَ الصُّبْحُ بِقَرِيبٍ");
        sor.Ayaat.Add("فَلَمّٰا جٰاءَ أَمْرُنٰا جَعَلْنٰا عٰالِيَهٰا سٰافِلَهٰا وَ أَمْطَرْنٰا عَلَيْهٰا حِجٰارَةً مِنْ سِجِّيلٍ مَنْضُودٍ");
        sor.Ayaat.Add("مُسَوَّمَةً عِنْدَ رَبِّكَ وَ مٰا هِيَ مِنَ الظّٰالِمِينَ بِبَعِيدٍ");
        sor.Ayaat.Add("وَ إِلىٰ مَدْيَنَ أَخٰاهُمْ شُعَيْباً قٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ وَ لاٰ تَنْقُصُوا الْمِكْيٰالَ وَ الْمِيزٰانَ إِنِّي أَرٰاكُمْ بِخَيْرٍ وَ إِنِّي أَخٰافُ عَلَيْكُمْ عَذٰابَ يَوْمٍ مُحِيطٍ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ أَوْفُوا الْمِكْيٰالَ وَ الْمِيزٰانَ بِالْقِسْطِ وَ لاٰ تَبْخَسُوا النّٰاسَ أَشْيٰاءَهُمْ وَ لاٰ تَعْثَوْا فِي الْأَرْضِ مُفْسِدِينَ");
        sor.Ayaat.Add("بَقِيَّتُ اللّٰهِ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ وَ مٰا أَنَا عَلَيْكُمْ بِحَفِيظٍ");
        sor.Ayaat.Add("قٰالُوا يٰا شُعَيْبُ أَ صَلاٰتُكَ تَأْمُرُكَ أَنْ نَتْرُكَ مٰا يَعْبُدُ آبٰاؤُنٰا أَوْ أَنْ نَفْعَلَ فِي أَمْوٰالِنٰا مٰا نَشٰؤُا إِنَّكَ لَأَنْتَ الْحَلِيمُ الرَّشِيدُ");
        sor.Ayaat.Add("قٰالَ يٰا قَوْمِ أَ رَأَيْتُمْ إِنْ كُنْتُ عَلىٰ بَيِّنَةٍ مِنْ رَبِّي وَ رَزَقَنِي مِنْهُ رِزْقاً حَسَناً وَ مٰا أُرِيدُ أَنْ أُخٰالِفَكُمْ إِلىٰ مٰا أَنْهٰاكُمْ عَنْهُ إِنْ أُرِيدُ إِلاَّ الْإِصْلاٰحَ مَا اسْتَطَعْتُ وَ مٰا تَوْفِيقِي إِلاّٰ بِاللّٰهِ عَلَيْهِ تَوَكَّلْتُ وَ إِلَيْهِ أُنِيبُ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ لاٰ يَجْرِمَنَّكُمْ شِقٰاقِي أَنْ يُصِيبَكُمْ مِثْلُ مٰا أَصٰابَ قَوْمَ نُوحٍ أَوْ قَوْمَ هُودٍ أَوْ قَوْمَ صٰالِحٍ وَ مٰا قَوْمُ لُوطٍ مِنْكُمْ بِبَعِيدٍ");
        sor.Ayaat.Add("وَ اسْتَغْفِرُوا رَبَّكُمْ ثُمَّ تُوبُوا إِلَيْهِ إِنَّ رَبِّي رَحِيمٌ وَدُودٌ");
        sor.Ayaat.Add("قٰالُوا يٰا شُعَيْبُ مٰا نَفْقَهُ كَثِيراً مِمّٰا تَقُولُ وَ إِنّٰا لَنَرٰاكَ فِينٰا ضَعِيفاً وَ لَوْ لاٰ رَهْطُكَ لَرَجَمْنٰاكَ وَ مٰا أَنْتَ عَلَيْنٰا بِعَزِيزٍ");
        sor.Ayaat.Add("قٰالَ يٰا قَوْمِ أَ رَهْطِي أَعَزُّ عَلَيْكُمْ مِنَ اللّٰهِ وَ اتَّخَذْتُمُوهُ وَرٰاءَكُمْ ظِهْرِيًّا إِنَّ رَبِّي بِمٰا تَعْمَلُونَ مُحِيطٌ");
        sor.Ayaat.Add("وَ يٰا قَوْمِ اعْمَلُوا عَلىٰ مَكٰانَتِكُمْ إِنِّي عٰامِلٌ سَوْفَ تَعْلَمُونَ مَنْ يَأْتِيهِ عَذٰابٌ يُخْزِيهِ وَ مَنْ هُوَ كٰاذِبٌ وَ ارْتَقِبُوا إِنِّي مَعَكُمْ رَقِيبٌ");
        sor.Ayaat.Add("وَ لَمّٰا جٰاءَ أَمْرُنٰا نَجَّيْنٰا شُعَيْباً وَ الَّذِينَ آمَنُوا مَعَهُ بِرَحْمَةٍ مِنّٰا وَ أَخَذَتِ الَّذِينَ ظَلَمُوا الصَّيْحَةُ فَأَصْبَحُوا فِي دِيٰارِهِمْ جٰاثِمِينَ");
        sor.Ayaat.Add("كَأَنْ لَمْ يَغْنَوْا فِيهٰا أَلاٰ بُعْداً لِمَدْيَنَ كَمٰا بَعِدَتْ ثَمُودُ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا مُوسىٰ بِآيٰاتِنٰا وَ سُلْطٰانٍ مُبِينٍ");
        sor.Ayaat.Add("إِلىٰ فِرْعَوْنَ وَ مَلاَئِهِ فَاتَّبَعُوا أَمْرَ فِرْعَوْنَ وَ مٰا أَمْرُ فِرْعَوْنَ بِرَشِيدٍ");
        sor.Ayaat.Add("يَقْدُمُ قَوْمَهُ يَوْمَ الْقِيٰامَةِ فَأَوْرَدَهُمُ النّٰارَ وَ بِئْسَ الْوِرْدُ الْمَوْرُودُ");
        sor.Ayaat.Add("وَ أُتْبِعُوا فِي هٰذِهِ لَعْنَةً وَ يَوْمَ الْقِيٰامَةِ بِئْسَ الرِّفْدُ الْمَرْفُودُ");
        sor.Ayaat.Add("ذٰلِكَ مِنْ أَنْبٰاءِ الْقُرىٰ نَقُصُّهُ عَلَيْكَ مِنْهٰا قٰائِمٌ وَ حَصِيدٌ");
        sor.Ayaat.Add("وَ مٰا ظَلَمْنٰاهُمْ وَ لٰكِنْ ظَلَمُوا أَنْفُسَهُمْ فَمٰا أَغْنَتْ عَنْهُمْ آلِهَتُهُمُ الَّتِي يَدْعُونَ مِنْ دُونِ اللّٰهِ مِنْ شَيْءٍ لَمّٰا جٰاءَ أَمْرُ رَبِّكَ وَ مٰا زٰادُوهُمْ غَيْرَ تَتْبِيبٍ");
        sor.Ayaat.Add("وَ كَذٰلِكَ أَخْذُ رَبِّكَ إِذٰا أَخَذَ الْقُرىٰ وَ هِيَ ظٰالِمَةٌ إِنَّ أَخْذَهُ أَلِيمٌ شَدِيدٌ");
        sor.Ayaat.Add("إِنَّ فِي ذٰلِكَ لَآيَةً لِمَنْ خٰافَ عَذٰابَ الْآخِرَةِ ذٰلِكَ يَوْمٌ مَجْمُوعٌ لَهُ النّٰاسُ وَ ذٰلِكَ يَوْمٌ مَشْهُودٌ");
        sor.Ayaat.Add("وَ مٰا نُؤَخِّرُهُ إِلاّٰ لِأَجَلٍ مَعْدُودٍ");
        sor.Ayaat.Add("يَوْمَ يَأْتِ لاٰ تَكَلَّمُ نَفْسٌ إِلاّٰ بِإِذْنِهِ فَمِنْهُمْ شَقِيٌّ وَ سَعِيدٌ");
        sor.Ayaat.Add("فَأَمَّا الَّذِينَ شَقُوا فَفِي النّٰارِ لَهُمْ فِيهٰا زَفِيرٌ وَ شَهِيقٌ");
        sor.Ayaat.Add("خٰالِدِينَ فِيهٰا مٰا دٰامَتِ السَّمٰاوٰاتُ وَ الْأَرْضُ إِلاّٰ مٰا شٰاءَ رَبُّكَ إِنَّ رَبَّكَ فَعّٰالٌ لِمٰا يُرِيدُ");
        sor.Ayaat.Add("وَ أَمَّا الَّذِينَ سُعِدُوا فَفِي الْجَنَّةِ خٰالِدِينَ فِيهٰا مٰا دٰامَتِ السَّمٰاوٰاتُ وَ الْأَرْضُ إِلاّٰ مٰا شٰاءَ رَبُّكَ عَطٰاءً غَيْرَ مَجْذُوذٍ");
        sor.Ayaat.Add("فَلاٰ تَكُ فِي مِرْيَةٍ مِمّٰا يَعْبُدُ هٰؤُلاٰءِ مٰا يَعْبُدُونَ إِلاّٰ كَمٰا يَعْبُدُ آبٰاؤُهُمْ مِنْ قَبْلُ وَ إِنّٰا لَمُوَفُّوهُمْ نَصِيبَهُمْ غَيْرَ مَنْقُوصٍ");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا مُوسَى الْكِتٰابَ فَاخْتُلِفَ فِيهِ وَ لَوْ لاٰ كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَقُضِيَ بَيْنَهُمْ وَ إِنَّهُمْ لَفِي شَكٍّ مِنْهُ مُرِيبٍ");
        sor.Ayaat.Add("وَ إِنَّ كُلاًّ لَمّٰا لَيُوَفِّيَنَّهُمْ رَبُّكَ أَعْمٰالَهُمْ إِنَّهُ بِمٰا يَعْمَلُونَ خَبِيرٌ");
        sor.Ayaat.Add("فَاسْتَقِمْ كَمٰا أُمِرْتَ وَ مَنْ تٰابَ مَعَكَ وَ لاٰ تَطْغَوْا إِنَّهُ بِمٰا تَعْمَلُونَ بَصِيرٌ");
        sor.Ayaat.Add("وَ لاٰ تَرْكَنُوا إِلَى الَّذِينَ ظَلَمُوا فَتَمَسَّكُمُ النّٰارُ وَ مٰا لَكُمْ مِنْ دُونِ اللّٰهِ مِنْ أَوْلِيٰاءَ ثُمَّ لاٰ تُنْصَرُونَ");
        sor.Ayaat.Add("وَ أَقِمِ الصَّلاٰةَ طَرَفَيِ النَّهٰارِ وَ زُلَفاً مِنَ اللَّيْلِ إِنَّ الْحَسَنٰاتِ يُذْهِبْنَ السَّيِّئٰاتِ ذٰلِكَ ذِكْرىٰ لِلذّٰاكِرِينَ");
        sor.Ayaat.Add("وَ اصْبِرْ فَإِنَّ اللّٰهَ لاٰ يُضِيعُ أَجْرَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("فَلَوْ لاٰ كٰانَ مِنَ الْقُرُونِ مِنْ قَبْلِكُمْ أُولُوا بَقِيَّةٍ يَنْهَوْنَ عَنِ الْفَسٰادِ فِي الْأَرْضِ إِلاّٰ قَلِيلاً مِمَّنْ أَنْجَيْنٰا مِنْهُمْ وَ اتَّبَعَ الَّذِينَ ظَلَمُوا مٰا أُتْرِفُوا فِيهِ وَ كٰانُوا مُجْرِمِينَ");
        sor.Ayaat.Add("وَ مٰا كٰانَ رَبُّكَ لِيُهْلِكَ الْقُرىٰ بِظُلْمٍ وَ أَهْلُهٰا مُصْلِحُونَ");
        sor.Ayaat.Add("وَ لَوْ شٰاءَ رَبُّكَ لَجَعَلَ النّٰاسَ أُمَّةً وٰاحِدَةً وَ لاٰ يَزٰالُونَ مُخْتَلِفِينَ");
        sor.Ayaat.Add("إِلاّٰ مَنْ رَحِمَ رَبُّكَ وَ لِذٰلِكَ خَلَقَهُمْ وَ تَمَّتْ كَلِمَةُ رَبِّكَ لَأَمْلَأَنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَ النّٰاسِ أَجْمَعِينَ");
        sor.Ayaat.Add("وَ كُلاًّ نَقُصُّ عَلَيْكَ مِنْ أَنْبٰاءِ الرُّسُلِ مٰا نُثَبِّتُ بِهِ فُؤٰادَكَ وَ جٰاءَكَ فِي هٰذِهِ الْحَقُّ وَ مَوْعِظَةٌ وَ ذِكْرىٰ لِلْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ قُلْ لِلَّذِينَ لاٰ يُؤْمِنُونَ اعْمَلُوا عَلىٰ مَكٰانَتِكُمْ إِنّٰا عٰامِلُونَ");
        sor.Ayaat.Add("وَ انْتَظِرُوا إِنّٰا مُنْتَظِرُونَ");
        sor.Ayaat.Add("وَ لِلّٰهِ غَيْبُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ إِلَيْهِ يُرْجَعُ الْأَمْرُ كُلُّهُ فَاعْبُدْهُ وَ تَوَكَّلْ عَلَيْهِ وَ مٰا رَبُّكَ بِغٰافِلٍ عَمّٰا تَعْمَلُونَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١٢ - یوسف";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الر تِلْكَ آيٰاتُ الْكِتٰابِ الْمُبِينِ");
        sor.Ayaat.Add("إِنّٰا أَنْزَلْنٰاهُ قُرْآناً عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ");
        sor.Ayaat.Add("نَحْنُ نَقُصُّ عَلَيْكَ أَحْسَنَ الْقَصَصِ بِمٰا أَوْحَيْنٰا إِلَيْكَ هٰذَا الْقُرْآنَ وَ إِنْ كُنْتَ مِنْ قَبْلِهِ لَمِنَ الْغٰافِلِينَ");
        sor.Ayaat.Add("إِذْ قٰالَ يُوسُفُ لِأَبِيهِ يٰا أَبَتِ إِنِّي رَأَيْتُ أَحَدَ عَشَرَ كَوْكَباً وَ الشَّمْسَ وَ الْقَمَرَ رَأَيْتُهُمْ لِي سٰاجِدِينَ");
        sor.Ayaat.Add("قٰالَ يٰا بُنَيَّ لاٰ تَقْصُصْ رُؤْيٰاكَ عَلىٰ إِخْوَتِكَ فَيَكِيدُوا لَكَ كَيْداً إِنَّ الشَّيْطٰانَ لِلْإِنْسٰانِ عَدُوٌّ مُبِينٌ");
        sor.Ayaat.Add("وَ كَذٰلِكَ يَجْتَبِيكَ رَبُّكَ وَ يُعَلِّمُكَ مِنْ تَأْوِيلِ الْأَحٰادِيثِ وَ يُتِمُّ نِعْمَتَهُ عَلَيْكَ وَ عَلىٰ آلِ يَعْقُوبَ كَمٰا أَتَمَّهٰا عَلىٰ أَبَوَيْكَ مِنْ قَبْلُ إِبْرٰاهِيمَ وَ إِسْحٰاقَ إِنَّ رَبَّكَ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("لَقَدْ كٰانَ فِي يُوسُفَ وَ إِخْوَتِهِ آيٰاتٌ لِلسّٰائِلِينَ");
        sor.Ayaat.Add("إِذْ قٰالُوا لَيُوسُفُ وَ أَخُوهُ أَحَبُّ إِلىٰ أَبِينٰا مِنّٰا وَ نَحْنُ عُصْبَةٌ إِنَّ أَبٰانٰا لَفِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("اقْتُلُوا يُوسُفَ أَوِ اطْرَحُوهُ أَرْضاً يَخْلُ لَكُمْ وَجْهُ أَبِيكُمْ وَ تَكُونُوا مِنْ بَعْدِهِ قَوْماً صٰالِحِينَ");
        sor.Ayaat.Add("قٰالَ قٰائِلٌ مِنْهُمْ لاٰ تَقْتُلُوا يُوسُفَ وَ أَلْقُوهُ فِي غَيٰابَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيّٰارَةِ إِنْ كُنْتُمْ فٰاعِلِينَ");
        sor.Ayaat.Add("قٰالُوا يٰا أَبٰانٰا مٰا لَكَ لاٰ تَأْمَنّٰا عَلىٰ يُوسُفَ وَ إِنّٰا لَهُ لَنٰاصِحُونَ");
        sor.Ayaat.Add("أَرْسِلْهُ مَعَنٰا غَداً يَرْتَعْ وَ يَلْعَبْ وَ إِنّٰا لَهُ لَحٰافِظُونَ");
        sor.Ayaat.Add("قٰالَ إِنِّي لَيَحْزُنُنِي أَنْ تَذْهَبُوا بِهِ وَ أَخٰافُ أَنْ يَأْكُلَهُ الذِّئْبُ وَ أَنْتُمْ عَنْهُ غٰافِلُونَ");
        sor.Ayaat.Add("قٰالُوا لَئِنْ أَكَلَهُ الذِّئْبُ وَ نَحْنُ عُصْبَةٌ إِنّٰا إِذاً لَخٰاسِرُونَ");
        sor.Ayaat.Add("فَلَمّٰا ذَهَبُوا بِهِ وَ أَجْمَعُوا أَنْ يَجْعَلُوهُ فِي غَيٰابَتِ الْجُبِّ وَ أَوْحَيْنٰا إِلَيْهِ لَتُنَبِّئَنَّهُمْ بِأَمْرِهِمْ هٰذٰا وَ هُمْ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("وَ جٰاؤُ أَبٰاهُمْ عِشٰاءً يَبْكُونَ");
        sor.Ayaat.Add("قٰالُوا يٰا أَبٰانٰا إِنّٰا ذَهَبْنٰا نَسْتَبِقُ وَ تَرَكْنٰا يُوسُفَ عِنْدَ مَتٰاعِنٰا فَأَكَلَهُ الذِّئْبُ وَ مٰا أَنْتَ بِمُؤْمِنٍ لَنٰا وَ لَوْ كُنّٰا صٰادِقِينَ");
        sor.Ayaat.Add("وَ جٰاؤُ عَلىٰ قَمِيصِهِ بِدَمٍ كَذِبٍ قٰالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْراً فَصَبْرٌ جَمِيلٌ وَ اللّٰهُ الْمُسْتَعٰانُ عَلىٰ مٰا تَصِفُونَ");
        sor.Ayaat.Add("وَ جٰاءَتْ سَيّٰارَةٌ فَأَرْسَلُوا وٰارِدَهُمْ فَأَدْلىٰ دَلْوَهُ قٰالَ يٰا بُشْرىٰ هٰذٰا غُلاٰمٌ وَ أَسَرُّوهُ بِضٰاعَةً وَ اللّٰهُ عَلِيمٌ بِمٰا يَعْمَلُونَ");
        sor.Ayaat.Add("وَ شَرَوْهُ بِثَمَنٍ بَخْسٍ دَرٰاهِمَ مَعْدُودَةٍ وَ كٰانُوا فِيهِ مِنَ الزّٰاهِدِينَ");
        sor.Ayaat.Add("وَ قٰالَ الَّذِي اشْتَرٰاهُ مِنْ مِصْرَ لاِمْرَأَتِهِ أَكْرِمِي مَثْوٰاهُ عَسىٰ أَنْ يَنْفَعَنٰا أَوْ نَتَّخِذَهُ وَلَداً وَ كَذٰلِكَ مَكَّنّٰا لِيُوسُفَ فِي الْأَرْضِ وَ لِنُعَلِّمَهُ مِنْ تَأْوِيلِ الْأَحٰادِيثِ وَ اللّٰهُ غٰالِبٌ عَلىٰ أَمْرِهِ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ لَمّٰا بَلَغَ أَشُدَّهُ آتَيْنٰاهُ حُكْماً وَ عِلْماً وَ كَذٰلِكَ نَجْزِي الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ رٰاوَدَتْهُ الَّتِي هُوَ فِي بَيْتِهٰا عَنْ نَفْسِهِ وَ غَلَّقَتِ الْأَبْوٰابَ وَ قٰالَتْ هَيْتَ لَكَ قٰالَ مَعٰاذَ اللّٰهِ إِنَّهُ رَبِّي أَحْسَنَ مَثْوٰايَ إِنَّهُ لاٰ يُفْلِحُ الظّٰالِمُونَ");
        sor.Ayaat.Add("وَ لَقَدْ هَمَّتْ بِهِ وَ هَمَّ بِهٰا لَوْ لاٰ أَنْ رَأىٰ بُرْهٰانَ رَبِّهِ كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّوءَ وَ الْفَحْشٰاءَ إِنَّهُ مِنْ عِبٰادِنَا الْمُخْلَصِينَ");
        sor.Ayaat.Add("وَ اسْتَبَقَا الْبٰابَ وَ قَدَّتْ قَمِيصَهُ مِنْ دُبُرٍ وَ أَلْفَيٰا سَيِّدَهٰا لَدَى الْبٰابِ قٰالَتْ مٰا جَزٰاءُ مَنْ أَرٰادَ بِأَهْلِكَ سُوءاً إِلاّٰ أَنْ يُسْجَنَ أَوْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("قٰالَ هِيَ رٰاوَدَتْنِي عَنْ نَفْسِي وَ شَهِدَ شٰاهِدٌ مِنْ أَهْلِهٰا إِنْ كٰانَ قَمِيصُهُ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَ هُوَ مِنَ الْكٰاذِبِينَ");
        sor.Ayaat.Add("وَ إِنْ كٰانَ قَمِيصُهُ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَ هُوَ مِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("فَلَمّٰا رَأىٰ قَمِيصَهُ قُدَّ مِنْ دُبُرٍ قٰالَ إِنَّهُ مِنْ كَيْدِكُنَّ إِنَّ كَيْدَكُنَّ عَظِيمٌ");
        sor.Ayaat.Add("يُوسُفُ أَعْرِضْ عَنْ هٰذٰا وَ اسْتَغْفِرِي لِذَنْبِكِ إِنَّكِ كُنْتِ مِنَ الْخٰاطِئِينَ");
        sor.Ayaat.Add("وَ قٰالَ نِسْوَةٌ فِي الْمَدِينَةِ امْرَأَتُ الْعَزِيزِ تُرٰاوِدُ فَتٰاهٰا عَنْ نَفْسِهِ قَدْ شَغَفَهٰا حُبًّا إِنّٰا لَنَرٰاهٰا فِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("فَلَمّٰا سَمِعَتْ بِمَكْرِهِنَّ أَرْسَلَتْ إِلَيْهِنَّ وَ أَعْتَدَتْ لَهُنَّ مُتَّكَأً وَ آتَتْ كُلَّ وٰاحِدَةٍ مِنْهُنَّ سِكِّيناً وَ قٰالَتِ اخْرُجْ عَلَيْهِنَّ فَلَمّٰا رَأَيْنَهُ أَكْبَرْنَهُ وَ قَطَّعْنَ أَيْدِيَهُنَّ وَ قُلْنَ حٰاشَ لِلّٰهِ مٰا هٰذٰا بَشَراً إِنْ هٰذٰا إِلاّٰ مَلَكٌ كَرِيمٌ");
        sor.Ayaat.Add("قٰالَتْ فَذٰلِكُنَّ الَّذِي لُمْتُنَّنِي فِيهِ وَ لَقَدْ رٰاوَدْتُهُ عَنْ نَفْسِهِ فَاسْتَعْصَمَ وَ لَئِنْ لَمْ يَفْعَلْ مٰا آمُرُهُ لَيُسْجَنَنَّ وَ لَيَكُوناً مِنَ الصّٰاغِرِينَ");
        sor.Ayaat.Add("قٰالَ رَبِّ السِّجْنُ أَحَبُّ إِلَيَّ مِمّٰا يَدْعُونَنِي إِلَيْهِ وَ إِلاّٰ تَصْرِفْ عَنِّي كَيْدَهُنَّ أَصْبُ إِلَيْهِنَّ وَ أَكُنْ مِنَ الْجٰاهِلِينَ");
        sor.Ayaat.Add("فَاسْتَجٰابَ لَهُ رَبُّهُ فَصَرَفَ عَنْهُ كَيْدَهُنَّ إِنَّهُ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("ثُمَّ بَدٰا لَهُمْ مِنْ بَعْدِ مٰا رَأَوُا الْآيٰاتِ لَيَسْجُنُنَّهُ حَتّٰى حِينٍ");
        sor.Ayaat.Add("وَ دَخَلَ مَعَهُ السِّجْنَ فَتَيٰانِ قٰالَ أَحَدُهُمٰا إِنِّي أَرٰانِي أَعْصِرُ خَمْراً وَ قٰالَ الْآخَرُ إِنِّي أَرٰانِي أَحْمِلُ فَوْقَ رَأْسِي خُبْزاً تَأْكُلُ الطَّيْرُ مِنْهُ نَبِّئْنٰا بِتَأْوِيلِهِ إِنّٰا نَرٰاكَ مِنَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("قٰالَ لاٰ يَأْتِيكُمٰا طَعٰامٌ تُرْزَقٰانِهِ إِلاّٰ نَبَّأْتُكُمٰا بِتَأْوِيلِهِ قَبْلَ أَنْ يَأْتِيَكُمٰا ذٰلِكُمٰا مِمّٰا عَلَّمَنِي رَبِّي إِنِّي تَرَكْتُ مِلَّةَ قَوْمٍ لاٰ يُؤْمِنُونَ بِاللّٰهِ وَ هُمْ بِالْآخِرَةِ هُمْ كٰافِرُونَ");
        sor.Ayaat.Add("وَ اتَّبَعْتُ مِلَّةَ آبٰائِي إِبْرٰاهِيمَ وَ إِسْحٰاقَ وَ يَعْقُوبَ مٰا كٰانَ لَنٰا أَنْ نُشْرِكَ بِاللّٰهِ مِنْ شَيْءٍ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنٰا وَ عَلَى النّٰاسِ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَشْكُرُونَ");
        sor.Ayaat.Add("يٰا صٰاحِبَيِ السِّجْنِ أَ أَرْبٰابٌ مُتَفَرِّقُونَ خَيْرٌ أَمِ اللّٰهُ الْوٰاحِدُ الْقَهّٰارُ");
        sor.Ayaat.Add("مٰا تَعْبُدُونَ مِنْ دُونِهِ إِلاّٰ أَسْمٰاءً سَمَّيْتُمُوهٰا أَنْتُمْ وَ آبٰاؤُكُمْ مٰا أَنْزَلَ اللّٰهُ بِهٰا مِنْ سُلْطٰانٍ إِنِ الْحُكْمُ إِلاّٰ لِلّٰهِ أَمَرَ أَلاّٰ تَعْبُدُوا إِلاّٰ إِيّٰاهُ ذٰلِكَ الدِّينُ الْقَيِّمُ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("يٰا صٰاحِبَيِ السِّجْنِ أَمّٰا أَحَدُكُمٰا فَيَسْقِي رَبَّهُ خَمْراً وَ أَمَّا الْآخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَأْسِهِ قُضِيَ الْأَمْرُ الَّذِي فِيهِ تَسْتَفْتِيٰانِ");
        sor.Ayaat.Add("وَ قٰالَ لِلَّذِي ظَنَّ أَنَّهُ نٰاجٍ مِنْهُمَا اذْكُرْنِي عِنْدَ رَبِّكَ فَأَنْسٰاهُ الشَّيْطٰانُ ذِكْرَ رَبِّهِ فَلَبِثَ فِي السِّجْنِ بِضْعَ سِنِينَ");
        sor.Ayaat.Add("وَ قٰالَ الْمَلِكُ إِنِّي أَرىٰ سَبْعَ بَقَرٰاتٍ سِمٰانٍ يَأْكُلُهُنَّ سَبْعٌ عِجٰافٌ وَ سَبْعَ سُنْبُلاٰتٍ خُضْرٍ وَ أُخَرَ يٰابِسٰاتٍ يٰا أَيُّهَا الْمَلَأُ أَفْتُونِي فِي رُءْيٰايَ إِنْ كُنْتُمْ لِلرُّءْيٰا تَعْبُرُونَ");
        sor.Ayaat.Add("قٰالُوا أَضْغٰاثُ أَحْلاٰمٍ وَ مٰا نَحْنُ بِتَأْوِيلِ الْأَحْلاٰمِ بِعٰالِمِينَ");
        sor.Ayaat.Add("وَ قٰالَ الَّذِي نَجٰا مِنْهُمٰا وَ ادَّكَرَ بَعْدَ أُمَّةٍ أَنَا أُنَبِّئُكُمْ بِتَأْوِيلِهِ فَأَرْسِلُونِ");
        sor.Ayaat.Add("يُوسُفُ أَيُّهَا الصِّدِّيقُ أَفْتِنٰا فِي سَبْعِ بَقَرٰاتٍ سِمٰانٍ يَأْكُلُهُنَّ سَبْعٌ عِجٰافٌ وَ سَبْعِ سُنْبُلاٰتٍ خُضْرٍ وَ أُخَرَ يٰابِسٰاتٍ لَعَلِّي أَرْجِعُ إِلَى النّٰاسِ لَعَلَّهُمْ يَعْلَمُونَ");
        sor.Ayaat.Add("قٰالَ تَزْرَعُونَ سَبْعَ سِنِينَ دَأَباً فَمٰا حَصَدْتُمْ فَذَرُوهُ فِي سُنْبُلِهِ إِلاّٰ قَلِيلاً مِمّٰا تَأْكُلُونَ");
        sor.Ayaat.Add("ثُمَّ يَأْتِي مِنْ بَعْدِ ذٰلِكَ سَبْعٌ شِدٰادٌ يَأْكُلْنَ مٰا قَدَّمْتُمْ لَهُنَّ إِلاّٰ قَلِيلاً مِمّٰا تُحْصِنُونَ");
        sor.Ayaat.Add("ثُمَّ يَأْتِي مِنْ بَعْدِ ذٰلِكَ عٰامٌ فِيهِ يُغٰاثُ النّٰاسُ وَ فِيهِ يَعْصِرُونَ");
        sor.Ayaat.Add("وَ قٰالَ الْمَلِكُ ائْتُونِي بِهِ فَلَمّٰا جٰاءَهُ الرَّسُولُ قٰالَ ارْجِعْ إِلىٰ رَبِّكَ فَسْئَلْهُ مٰا بٰالُ النِّسْوَةِ اللاّٰتِي قَطَّعْنَ أَيْدِيَهُنَّ إِنَّ رَبِّي بِكَيْدِهِنَّ عَلِيمٌ");
        sor.Ayaat.Add("قٰالَ مٰا خَطْبُكُنَّ إِذْ رٰاوَدْتُنَّ يُوسُفَ عَنْ نَفْسِهِ قُلْنَ حٰاشَ لِلّٰهِ مٰا عَلِمْنٰا عَلَيْهِ مِنْ سُوءٍ قٰالَتِ امْرَأَةُ الْعَزِيزِ الْآنَ حَصْحَصَ الْحَقُّ أَنَا رٰاوَدْتُهُ عَنْ نَفْسِهِ وَ إِنَّهُ لَمِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("ذٰلِكَ لِيَعْلَمَ أَنِّي لَمْ أَخُنْهُ بِالْغَيْبِ وَ أَنَّ اللّٰهَ لاٰ يَهْدِي كَيْدَ الْخٰائِنِينَ");
        sor.Ayaat.Add("وَ مٰا أُبَرِّئُ نَفْسِي إِنَّ النَّفْسَ لَأَمّٰارَةٌ بِالسُّوءِ إِلاّٰ مٰا رَحِمَ رَبِّي إِنَّ رَبِّي غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ قٰالَ الْمَلِكُ ائْتُونِي بِهِ أَسْتَخْلِصْهُ لِنَفْسِي فَلَمّٰا كَلَّمَهُ قٰالَ إِنَّكَ الْيَوْمَ لَدَيْنٰا مَكِينٌ أَمِينٌ");
        sor.Ayaat.Add("قٰالَ اجْعَلْنِي عَلىٰ خَزٰائِنِ الْأَرْضِ إِنِّي حَفِيظٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ كَذٰلِكَ مَكَّنّٰا لِيُوسُفَ فِي الْأَرْضِ يَتَبَوَّأُ مِنْهٰا حَيْثُ يَشٰاءُ نُصِيبُ بِرَحْمَتِنٰا مَنْ نَشٰاءُ وَ لاٰ نُضِيعُ أَجْرَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("وَ لَأَجْرُ الْآخِرَةِ خَيْرٌ لِلَّذِينَ آمَنُوا وَ كٰانُوا يَتَّقُونَ");
        sor.Ayaat.Add("وَ جٰاءَ إِخْوَةُ يُوسُفَ فَدَخَلُوا عَلَيْهِ فَعَرَفَهُمْ وَ هُمْ لَهُ مُنْكِرُونَ");
        sor.Ayaat.Add("وَ لَمّٰا جَهَّزَهُمْ بِجَهٰازِهِمْ قٰالَ ائْتُونِي بِأَخٍ لَكُمْ مِنْ أَبِيكُمْ أَ لاٰ تَرَوْنَ أَنِّي أُوفِي الْكَيْلَ وَ أَنَا خَيْرُ الْمُنْزِلِينَ");
        sor.Ayaat.Add("فَإِنْ لَمْ تَأْتُونِي بِهِ فَلاٰ كَيْلَ لَكُمْ عِنْدِي وَ لاٰ تَقْرَبُونِ");
        sor.Ayaat.Add("قٰالُوا سَنُرٰاوِدُ عَنْهُ أَبٰاهُ وَ إِنّٰا لَفٰاعِلُونَ");
        sor.Ayaat.Add("وَ قٰالَ لِفِتْيٰانِهِ اجْعَلُوا بِضٰاعَتَهُمْ فِي رِحٰالِهِمْ لَعَلَّهُمْ يَعْرِفُونَهٰا إِذَا انْقَلَبُوا إِلىٰ أَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُونَ");
        sor.Ayaat.Add("فَلَمّٰا رَجَعُوا إِلىٰ أَبِيهِمْ قٰالُوا يٰا أَبٰانٰا مُنِعَ مِنَّا الْكَيْلُ فَأَرْسِلْ مَعَنٰا أَخٰانٰا نَكْتَلْ وَ إِنّٰا لَهُ لَحٰافِظُونَ");
        sor.Ayaat.Add("قٰالَ هَلْ آمَنُكُمْ عَلَيْهِ إِلاّٰ كَمٰا أَمِنْتُكُمْ عَلىٰ أَخِيهِ مِنْ قَبْلُ فَاللّٰهُ خَيْرٌ حٰافِظاً وَ هُوَ أَرْحَمُ الرّٰاحِمِينَ");
        sor.Ayaat.Add("وَ لَمّٰا فَتَحُوا مَتٰاعَهُمْ وَجَدُوا بِضٰاعَتَهُمْ رُدَّتْ إِلَيْهِمْ قٰالُوا يٰا أَبٰانٰا مٰا نَبْغِي هٰذِهِ بِضٰاعَتُنٰا رُدَّتْ إِلَيْنٰا وَ نَمِيرُ أَهْلَنٰا وَ نَحْفَظُ أَخٰانٰا وَ نَزْدٰادُ كَيْلَ بَعِيرٍ ذٰلِكَ كَيْلٌ يَسِيرٌ");
        sor.Ayaat.Add("قٰالَ لَنْ أُرْسِلَهُ مَعَكُمْ حَتّٰى تُؤْتُونِ مَوْثِقاً مِنَ اللّٰهِ لَتَأْتُنَّنِي بِهِ إِلاّٰ أَنْ يُحٰاطَ بِكُمْ فَلَمّٰا آتَوْهُ مَوْثِقَهُمْ قٰالَ اللّٰهُ عَلىٰ مٰا نَقُولُ وَكِيلٌ");
        sor.Ayaat.Add("وَ قٰالَ يٰا بَنِيَّ لاٰ تَدْخُلُوا مِنْ بٰابٍ وٰاحِدٍ وَ ادْخُلُوا مِنْ أَبْوٰابٍ مُتَفَرِّقَةٍ وَ مٰا أُغْنِي عَنْكُمْ مِنَ اللّٰهِ مِنْ شَيْءٍ إِنِ الْحُكْمُ إِلاّٰ لِلّٰهِ عَلَيْهِ تَوَكَّلْتُ وَ عَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ");
        sor.Ayaat.Add("وَ لَمّٰا دَخَلُوا مِنْ حَيْثُ أَمَرَهُمْ أَبُوهُمْ مٰا كٰانَ يُغْنِي عَنْهُمْ مِنَ اللّٰهِ مِنْ شَيْءٍ إِلاّٰ حٰاجَةً فِي نَفْسِ يَعْقُوبَ قَضٰاهٰا وَ إِنَّهُ لَذُو عِلْمٍ لِمٰا عَلَّمْنٰاهُ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ لَمّٰا دَخَلُوا عَلىٰ يُوسُفَ آوىٰ إِلَيْهِ أَخٰاهُ قٰالَ إِنِّي أَنَا أَخُوكَ فَلاٰ تَبْتَئِسْ بِمٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("فَلَمّٰا جَهَّزَهُمْ بِجَهٰازِهِمْ جَعَلَ السِّقٰايَةَ فِي رَحْلِ أَخِيهِ ثُمَّ أَذَّنَ مُؤَذِّنٌ أَيَّتُهَا الْعِيرُ إِنَّكُمْ لَسٰارِقُونَ");
        sor.Ayaat.Add("قٰالُوا وَ أَقْبَلُوا عَلَيْهِمْ مٰاذٰا تَفْقِدُونَ");
        sor.Ayaat.Add("قٰالُوا نَفْقِدُ صُوٰاعَ الْمَلِكِ وَ لِمَنْ جٰاءَ بِهِ حِمْلُ بَعِيرٍ وَ أَنَا بِهِ زَعِيمٌ");
        sor.Ayaat.Add("قٰالُوا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مٰا جِئْنٰا لِنُفْسِدَ فِي الْأَرْضِ وَ مٰا كُنّٰا سٰارِقِينَ");
        sor.Ayaat.Add("قٰالُوا فَمٰا جَزٰاؤُهُ إِنْ كُنْتُمْ كٰاذِبِينَ");
        sor.Ayaat.Add("قٰالُوا جَزٰاؤُهُ مَنْ وُجِدَ فِي رَحْلِهِ فَهُوَ جَزٰاؤُهُ كَذٰلِكَ نَجْزِي الظّٰالِمِينَ");
        sor.Ayaat.Add("فَبَدَأَ بِأَوْعِيَتِهِمْ قَبْلَ وِعٰاءِ أَخِيهِ ثُمَّ اسْتَخْرَجَهٰا مِنْ وِعٰاءِ أَخِيهِ كَذٰلِكَ كِدْنٰا لِيُوسُفَ مٰا كٰانَ لِيَأْخُذَ أَخٰاهُ فِي دِينِ الْمَلِكِ إِلاّٰ أَنْ يَشٰاءَ اللّٰهُ نَرْفَعُ دَرَجٰاتٍ مَنْ نَشٰاءُ وَ فَوْقَ كُلِّ ذِي عِلْمٍ عَلِيمٌ");
        sor.Ayaat.Add("قٰالُوا إِنْ يَسْرِقْ فَقَدْ سَرَقَ أَخٌ لَهُ مِنْ قَبْلُ فَأَسَرَّهٰا يُوسُفُ فِي نَفْسِهِ وَ لَمْ يُبْدِهٰا لَهُمْ قٰالَ أَنْتُمْ شَرٌّ مَكٰاناً وَ اللّٰهُ أَعْلَمُ بِمٰا تَصِفُونَ");
        sor.Ayaat.Add("قٰالُوا يٰا أَيُّهَا الْعَزِيزُ إِنَّ لَهُ أَباً شَيْخاً كَبِيراً فَخُذْ أَحَدَنٰا مَكٰانَهُ إِنّٰا نَرٰاكَ مِنَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("قٰالَ مَعٰاذَ اللّٰهِ أَنْ نَأْخُذَ إِلاّٰ مَنْ وَجَدْنٰا مَتٰاعَنٰا عِنْدَهُ إِنّٰا إِذاً لَظٰالِمُونَ");
        sor.Ayaat.Add("فَلَمَّا اسْتَيْأَسُوا مِنْهُ خَلَصُوا نَجِيًّا قٰالَ كَبِيرُهُمْ أَ لَمْ تَعْلَمُوا أَنَّ أَبٰاكُمْ قَدْ أَخَذَ عَلَيْكُمْ مَوْثِقاً مِنَ اللّٰهِ وَ مِنْ قَبْلُ مٰا فَرَّطْتُمْ فِي يُوسُفَ فَلَنْ أَبْرَحَ الْأَرْضَ حَتّٰى يَأْذَنَ لِي أَبِي أَوْ يَحْكُمَ اللّٰهُ لِي وَ هُوَ خَيْرُ الْحٰاكِمِينَ");
        sor.Ayaat.Add("ارْجِعُوا إِلىٰ أَبِيكُمْ فَقُولُوا يٰا أَبٰانٰا إِنَّ ابْنَكَ سَرَقَ وَ مٰا شَهِدْنٰا إِلاّٰ بِمٰا عَلِمْنٰا وَ مٰا كُنّٰا لِلْغَيْبِ حٰافِظِينَ");
        sor.Ayaat.Add("وَ سْئَلِ الْقَرْيَةَ الَّتِي كُنّٰا فِيهٰا وَ الْعِيرَ الَّتِي أَقْبَلْنٰا فِيهٰا وَ إِنّٰا لَصٰادِقُونَ");
        sor.Ayaat.Add("قٰالَ بَلْ سَوَّلَتْ لَكُمْ أَنْفُسُكُمْ أَمْراً فَصَبْرٌ جَمِيلٌ عَسَى اللّٰهُ أَنْ يَأْتِيَنِي بِهِمْ جَمِيعاً إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ");
        sor.Ayaat.Add("وَ تَوَلّٰى عَنْهُمْ وَ قٰالَ يٰا أَسَفىٰ عَلىٰ يُوسُفَ وَ ابْيَضَّتْ عَيْنٰاهُ مِنَ الْحُزْنِ فَهُوَ كَظِيمٌ");
        sor.Ayaat.Add("قٰالُوا تَاللّٰهِ تَفْتَؤُا تَذْكُرُ يُوسُفَ حَتّٰى تَكُونَ حَرَضاً أَوْ تَكُونَ مِنَ الْهٰالِكِينَ");
        sor.Ayaat.Add("قٰالَ إِنَّمٰا أَشْكُوا بَثِّي وَ حُزْنِي إِلَى اللّٰهِ وَ أَعْلَمُ مِنَ اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("يٰا بَنِيَّ اذْهَبُوا فَتَحَسَّسُوا مِنْ يُوسُفَ وَ أَخِيهِ وَ لاٰ تَيْأَسُوا مِنْ رَوْحِ اللّٰهِ إِنَّهُ لاٰ يَيْأَسُ مِنْ رَوْحِ اللّٰهِ إِلاَّ الْقَوْمُ الْكٰافِرُونَ");
        sor.Ayaat.Add("فَلَمّٰا دَخَلُوا عَلَيْهِ قٰالُوا يٰا أَيُّهَا الْعَزِيزُ مَسَّنٰا وَ أَهْلَنَا الضُّرُّ وَ جِئْنٰا بِبِضٰاعَةٍ مُزْجٰاةٍ فَأَوْفِ لَنَا الْكَيْلَ وَ تَصَدَّقْ عَلَيْنٰا إِنَّ اللّٰهَ يَجْزِي الْمُتَصَدِّقِينَ");
        sor.Ayaat.Add("قٰالَ هَلْ عَلِمْتُمْ مٰا فَعَلْتُمْ بِيُوسُفَ وَ أَخِيهِ إِذْ أَنْتُمْ جٰاهِلُونَ");
        sor.Ayaat.Add("قٰالُوا أَ إِنَّكَ لَأَنْتَ يُوسُفُ قٰالَ أَنَا يُوسُفُ وَ هٰذٰا أَخِي قَدْ مَنَّ اللّٰهُ عَلَيْنٰا إِنَّهُ مَنْ يَتَّقِ وَ يَصْبِرْ فَإِنَّ اللّٰهَ لاٰ يُضِيعُ أَجْرَ الْمُحْسِنِينَ");
        sor.Ayaat.Add("قٰالُوا تَاللّٰهِ لَقَدْ آثَرَكَ اللّٰهُ عَلَيْنٰا وَ إِنْ كُنّٰا لَخٰاطِئِينَ");
        sor.Ayaat.Add("قٰالَ لاٰ تَثْرِيبَ عَلَيْكُمُ الْيَوْمَ يَغْفِرُ اللّٰهُ لَكُمْ وَ هُوَ أَرْحَمُ الرّٰاحِمِينَ");
        sor.Ayaat.Add("اذْهَبُوا بِقَمِيصِي هٰذٰا فَأَلْقُوهُ عَلىٰ وَجْهِ أَبِي يَأْتِ بَصِيراً وَ أْتُونِي بِأَهْلِكُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("وَ لَمّٰا فَصَلَتِ الْعِيرُ قٰالَ أَبُوهُمْ إِنِّي لَأَجِدُ رِيحَ يُوسُفَ لَوْ لاٰ أَنْ تُفَنِّدُونِ");
        sor.Ayaat.Add("قٰالُوا تَاللّٰهِ إِنَّكَ لَفِي ضَلاٰلِكَ الْقَدِيمِ");
        sor.Ayaat.Add("فَلَمّٰا أَنْ جٰاءَ الْبَشِيرُ أَلْقٰاهُ عَلىٰ وَجْهِهِ فَارْتَدَّ بَصِيراً قٰالَ أَ لَمْ أَقُلْ لَكُمْ إِنِّي أَعْلَمُ مِنَ اللّٰهِ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("قٰالُوا يٰا أَبٰانَا اسْتَغْفِرْ لَنٰا ذُنُوبَنٰا إِنّٰا كُنّٰا خٰاطِئِينَ");
        sor.Ayaat.Add("قٰالَ سَوْفَ أَسْتَغْفِرُ لَكُمْ رَبِّي إِنَّهُ هُوَ الْغَفُورُ الرَّحِيمُ");
        sor.Ayaat.Add("فَلَمّٰا دَخَلُوا عَلىٰ يُوسُفَ آوىٰ إِلَيْهِ أَبَوَيْهِ وَ قٰالَ ادْخُلُوا مِصْرَ إِنْ شٰاءَ اللّٰهُ آمِنِينَ");
        sor.Ayaat.Add("وَ رَفَعَ أَبَوَيْهِ عَلَى الْعَرْشِ وَ خَرُّوا لَهُ سُجَّداً وَ قٰالَ يٰا أَبَتِ هٰذٰا تَأْوِيلُ رُءْيٰايَ مِنْ قَبْلُ قَدْ جَعَلَهٰا رَبِّي حَقًّا وَ قَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ وَ جٰاءَ بِكُمْ مِنَ الْبَدْوِ مِنْ بَعْدِ أَنْ نَزَغَ الشَّيْطٰانُ بَيْنِي وَ بَيْنَ إِخْوَتِي إِنَّ رَبِّي لَطِيفٌ لِمٰا يَشٰاءُ إِنَّهُ هُوَ الْعَلِيمُ الْحَكِيمُ");
        sor.Ayaat.Add("رَبِّ قَدْ آتَيْتَنِي مِنَ الْمُلْكِ وَ عَلَّمْتَنِي مِنْ تَأْوِيلِ الْأَحٰادِيثِ فٰاطِرَ السَّمٰاوٰاتِ وَ الْأَرْضِ أَنْتَ وَلِيِّي فِي الدُّنْيٰا وَ الْآخِرَةِ تَوَفَّنِي مُسْلِماً وَ أَلْحِقْنِي بِالصّٰالِحِينَ");
        sor.Ayaat.Add("ذٰلِكَ مِنْ أَنْبٰاءِ الْغَيْبِ نُوحِيهِ إِلَيْكَ وَ مٰا كُنْتَ لَدَيْهِمْ إِذْ أَجْمَعُوا أَمْرَهُمْ وَ هُمْ يَمْكُرُونَ");
        sor.Ayaat.Add("وَ مٰا أَكْثَرُ النّٰاسِ وَ لَوْ حَرَصْتَ بِمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ مٰا تَسْئَلُهُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ هُوَ إِلاّٰ ذِكْرٌ لِلْعٰالَمِينَ");
        sor.Ayaat.Add("وَ كَأَيِّنْ مِنْ آيَةٍ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ يَمُرُّونَ عَلَيْهٰا وَ هُمْ عَنْهٰا مُعْرِضُونَ");
        sor.Ayaat.Add("وَ مٰا يُؤْمِنُ أَكْثَرُهُمْ بِاللّٰهِ إِلاّٰ وَ هُمْ مُشْرِكُونَ");
        sor.Ayaat.Add("أَ فَأَمِنُوا أَنْ تَأْتِيَهُمْ غٰاشِيَةٌ مِنْ عَذٰابِ اللّٰهِ أَوْ تَأْتِيَهُمُ السّٰاعَةُ بَغْتَةً وَ هُمْ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("قُلْ هٰذِهِ سَبِيلِي أَدْعُوا إِلَى اللّٰهِ عَلىٰ بَصِيرَةٍ أَنَا وَ مَنِ اتَّبَعَنِي وَ سُبْحٰانَ اللّٰهِ وَ مٰا أَنَا مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا مِنْ قَبْلِكَ إِلاّٰ رِجٰالاً نُوحِي إِلَيْهِمْ مِنْ أَهْلِ الْقُرىٰ أَ فَلَمْ يَسِيرُوا فِي الْأَرْضِ فَيَنْظُرُوا كَيْفَ كٰانَ عٰاقِبَةُ الَّذِينَ مِنْ قَبْلِهِمْ وَ لَدٰارُ الْآخِرَةِ خَيْرٌ لِلَّذِينَ اتَّقَوْا أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("حَتّٰى إِذَا اسْتَيْأَسَ الرُّسُلُ وَ ظَنُّوا أَنَّهُمْ قَدْ كُذِبُوا جٰاءَهُمْ نَصْرُنٰا فَنُجِّيَ مَنْ نَشٰاءُ وَ لاٰ يُرَدُّ بَأْسُنٰا عَنِ الْقَوْمِ الْمُجْرِمِينَ");
        sor.Ayaat.Add("لَقَدْ كٰانَ فِي قَصَصِهِمْ عِبْرَةٌ لِأُولِي الْأَلْبٰابِ مٰا كٰانَ حَدِيثاً يُفْتَرىٰ وَ لٰكِنْ تَصْدِيقَ الَّذِي بَيْنَ يَدَيْهِ وَ تَفْصِيلَ كُلِّ شَيْءٍ وَ هُدىً وَ رَحْمَةً لِقَوْمٍ يُؤْمِنُونَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١٣ - الرعد";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ المر تِلْكَ آيٰاتُ الْكِتٰابِ وَ الَّذِي أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("اللّٰهُ الَّذِي رَفَعَ السَّمٰاوٰاتِ بِغَيْرِ عَمَدٍ تَرَوْنَهٰا ثُمَّ اسْتَوىٰ عَلَى الْعَرْشِ وَ سَخَّرَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ يَجْرِي لِأَجَلٍ مُسَمًّى يُدَبِّرُ الْأَمْرَ يُفَصِّلُ الْآيٰاتِ لَعَلَّكُمْ بِلِقٰاءِ رَبِّكُمْ تُوقِنُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي مَدَّ الْأَرْضَ وَ جَعَلَ فِيهٰا رَوٰاسِيَ وَ أَنْهٰاراً وَ مِنْ كُلِّ الثَّمَرٰاتِ جَعَلَ فِيهٰا زَوْجَيْنِ اثْنَيْنِ يُغْشِي اللَّيْلَ النَّهٰارَ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِقَوْمٍ يَتَفَكَّرُونَ");
        sor.Ayaat.Add("وَ فِي الْأَرْضِ قِطَعٌ مُتَجٰاوِرٰاتٌ وَ جَنّٰاتٌ مِنْ أَعْنٰابٍ وَ زَرْعٌ وَ نَخِيلٌ صِنْوٰانٌ وَ غَيْرُ صِنْوٰانٍ يُسْقىٰ بِمٰاءٍ وٰاحِدٍ وَ نُفَضِّلُ بَعْضَهٰا عَلىٰ بَعْضٍ فِي الْأُكُلِ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِقَوْمٍ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ إِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ أَ إِذٰا كُنّٰا تُرٰاباً أَ إِنّٰا لَفِي خَلْقٍ جَدِيدٍ أُولٰئِكَ الَّذِينَ كَفَرُوا بِرَبِّهِمْ وَ أُولٰئِكَ الْأَغْلاٰلُ فِي أَعْنٰاقِهِمْ وَ أُولٰئِكَ أَصْحٰابُ النّٰارِ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ يَسْتَعْجِلُونَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَ قَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلاٰتُ وَ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ لِلنّٰاسِ عَلىٰ ظُلْمِهِمْ وَ إِنَّ رَبَّكَ لَشَدِيدُ الْعِقٰابِ");
        sor.Ayaat.Add("وَ يَقُولُ الَّذِينَ كَفَرُوا لَوْ لاٰ أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ إِنَّمٰا أَنْتَ مُنْذِرٌ وَ لِكُلِّ قَوْمٍ هٰادٍ");
        sor.Ayaat.Add("اللّٰهُ يَعْلَمُ مٰا تَحْمِلُ كُلُّ أُنْثىٰ وَ مٰا تَغِيضُ الْأَرْحٰامُ وَ مٰا تَزْدٰادُ وَ كُلُّ شَيْءٍ عِنْدَهُ بِمِقْدٰارٍ");
        sor.Ayaat.Add("عٰالِمُ الْغَيْبِ وَ الشَّهٰادَةِ الْكَبِيرُ الْمُتَعٰالِ");
        sor.Ayaat.Add("سَوٰاءٌ مِنْكُمْ مَنْ أَسَرَّ الْقَوْلَ وَ مَنْ جَهَرَ بِهِ وَ مَنْ هُوَ مُسْتَخْفٍ بِاللَّيْلِ وَ سٰارِبٌ بِالنَّهٰارِ");
        sor.Ayaat.Add("لَهُ مُعَقِّبٰاتٌ مِنْ بَيْنِ يَدَيْهِ وَ مِنْ خَلْفِهِ يَحْفَظُونَهُ مِنْ أَمْرِ اللّٰهِ إِنَّ اللّٰهَ لاٰ يُغَيِّرُ مٰا بِقَوْمٍ حَتّٰى يُغَيِّرُوا مٰا بِأَنْفُسِهِمْ وَ إِذٰا أَرٰادَ اللّٰهُ بِقَوْمٍ سُوْءاً فَلاٰ مَرَدَّ لَهُ وَ مٰا لَهُمْ مِنْ دُونِهِ مِنْ وٰالٍ");
        sor.Ayaat.Add("هُوَ الَّذِي يُرِيكُمُ الْبَرْقَ خَوْفاً وَ طَمَعاً وَ يُنْشِئُ السَّحٰابَ الثِّقٰالَ");
        sor.Ayaat.Add("وَ يُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَ الْمَلاٰئِكَةُ مِنْ خِيفَتِهِ وَ يُرْسِلُ الصَّوٰاعِقَ فَيُصِيبُ بِهٰا مَنْ يَشٰاءُ وَ هُمْ يُجٰادِلُونَ فِي اللّٰهِ وَ هُوَ شَدِيدُ الْمِحٰالِ");
        sor.Ayaat.Add("لَهُ دَعْوَةُ الْحَقِّ وَ الَّذِينَ يَدْعُونَ مِنْ دُونِهِ لاٰ يَسْتَجِيبُونَ لَهُمْ بِشَيْءٍ إِلاّٰ كَبٰاسِطِ كَفَّيْهِ إِلَى الْمٰاءِ لِيَبْلُغَ فٰاهُ وَ مٰا هُوَ بِبٰالِغِهِ وَ مٰا دُعٰاءُ الْكٰافِرِينَ إِلاّٰ فِي ضَلاٰلٍ");
        sor.Ayaat.Add("وَ لِلّٰهِ يَسْجُدُ مَنْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ طَوْعاً وَ كَرْهاً وَ ظِلاٰلُهُمْ بِالْغُدُوِّ وَ الْآصٰالِ");
        sor.Ayaat.Add("قُلْ مَنْ رَبُّ السَّمٰاوٰاتِ وَ الْأَرْضِ قُلِ اللّٰهُ قُلْ أَ فَاتَّخَذْتُمْ مِنْ دُونِهِ أَوْلِيٰاءَ لاٰ يَمْلِكُونَ لِأَنْفُسِهِمْ نَفْعاً وَ لاٰ ضَرًّا قُلْ هَلْ يَسْتَوِي الْأَعْمىٰ وَ الْبَصِيرُ أَمْ هَلْ تَسْتَوِي الظُّلُمٰاتُ وَ النُّورُ أَمْ جَعَلُوا لِلّٰهِ شُرَكٰاءَ خَلَقُوا كَخَلْقِهِ فَتَشٰابَهَ الْخَلْقُ عَلَيْهِمْ قُلِ اللّٰهُ خٰالِقُ كُلِّ شَيْءٍ وَ هُوَ الْوٰاحِدُ الْقَهّٰارُ");
        sor.Ayaat.Add("أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَسٰالَتْ أَوْدِيَةٌ بِقَدَرِهٰا فَاحْتَمَلَ السَّيْلُ زَبَداً رٰابِياً وَ مِمّٰا يُوقِدُونَ عَلَيْهِ فِي النّٰارِ ابْتِغٰاءَ حِلْيَةٍ أَوْ مَتٰاعٍ زَبَدٌ مِثْلُهُ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَ الْبٰاطِلَ فَأَمَّا الزَّبَدُ فَيَذْهَبُ جُفٰاءً وَ أَمّٰا مٰا يَنْفَعُ النّٰاسَ فَيَمْكُثُ فِي الْأَرْضِ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْأَمْثٰالَ");
        sor.Ayaat.Add("لِلَّذِينَ اسْتَجٰابُوا لِرَبِّهِمُ الْحُسْنىٰ وَ الَّذِينَ لَمْ يَسْتَجِيبُوا لَهُ لَوْ أَنَّ لَهُمْ مٰا فِي الْأَرْضِ جَمِيعاً وَ مِثْلَهُ مَعَهُ لاَفْتَدَوْا بِهِ أُولٰئِكَ لَهُمْ سُوءُ الْحِسٰابِ وَ مَأْوٰاهُمْ جَهَنَّمُ وَ بِئْسَ الْمِهٰادُ");
        sor.Ayaat.Add("أَ فَمَنْ يَعْلَمُ أَنَّمٰا أُنْزِلَ إِلَيْكَ مِنْ رَبِّكَ الْحَقُّ كَمَنْ هُوَ أَعْمىٰ إِنَّمٰا يَتَذَكَّرُ أُولُوا الْأَلْبٰابِ");
        sor.Ayaat.Add("الَّذِينَ يُوفُونَ بِعَهْدِ اللّٰهِ وَ لاٰ يَنْقُضُونَ الْمِيثٰاقَ");
        sor.Ayaat.Add("وَ الَّذِينَ يَصِلُونَ مٰا أَمَرَ اللّٰهُ بِهِ أَنْ يُوصَلَ وَ يَخْشَوْنَ رَبَّهُمْ وَ يَخٰافُونَ سُوءَ الْحِسٰابِ");
        sor.Ayaat.Add("وَ الَّذِينَ صَبَرُوا ابْتِغٰاءَ وَجْهِ رَبِّهِمْ وَ أَقٰامُوا الصَّلاٰةَ وَ أَنْفَقُوا مِمّٰا رَزَقْنٰاهُمْ سِرًّا وَ عَلاٰنِيَةً وَ يَدْرَؤُنَ بِالْحَسَنَةِ السَّيِّئَةَ أُولٰئِكَ لَهُمْ عُقْبَى الدّٰارِ");
        sor.Ayaat.Add("جَنّٰاتُ عَدْنٍ يَدْخُلُونَهٰا وَ مَنْ صَلَحَ مِنْ آبٰائِهِمْ وَ أَزْوٰاجِهِمْ وَ ذُرِّيّٰاتِهِمْ وَ الْمَلاٰئِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بٰابٍ");
        sor.Ayaat.Add("سَلاٰمٌ عَلَيْكُمْ بِمٰا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدّٰارِ");
        sor.Ayaat.Add("وَ الَّذِينَ يَنْقُضُونَ عَهْدَ اللّٰهِ مِنْ بَعْدِ مِيثٰاقِهِ وَ يَقْطَعُونَ مٰا أَمَرَ اللّٰهُ بِهِ أَنْ يُوصَلَ وَ يُفْسِدُونَ فِي الْأَرْضِ أُولٰئِكَ لَهُمُ اللَّعْنَةُ وَ لَهُمْ سُوءُ الدّٰارِ");
        sor.Ayaat.Add("اللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَشٰاءُ وَ يَقْدِرُ وَ فَرِحُوا بِالْحَيٰاةِ الدُّنْيٰا وَ مَا الْحَيٰاةُ الدُّنْيٰا فِي الْآخِرَةِ إِلاّٰ مَتٰاعٌ");
        sor.Ayaat.Add("وَ يَقُولُ الَّذِينَ كَفَرُوا لَوْ لاٰ أُنْزِلَ عَلَيْهِ آيَةٌ مِنْ رَبِّهِ قُلْ إِنَّ اللّٰهَ يُضِلُّ مَنْ يَشٰاءُ وَ يَهْدِي إِلَيْهِ مَنْ أَنٰابَ");
        sor.Ayaat.Add("الَّذِينَ آمَنُوا وَ تَطْمَئِنُّ قُلُوبُهُمْ بِذِكْرِ اللّٰهِ أَلاٰ بِذِكْرِ اللّٰهِ تَطْمَئِنُّ الْقُلُوبُ");
        sor.Ayaat.Add("الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ طُوبىٰ لَهُمْ وَ حُسْنُ مَآبٍ");
        sor.Ayaat.Add("كَذٰلِكَ أَرْسَلْنٰاكَ فِي أُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهٰا أُمَمٌ لِتَتْلُوَا عَلَيْهِمُ الَّذِي أَوْحَيْنٰا إِلَيْكَ وَ هُمْ يَكْفُرُونَ بِالرَّحْمٰنِ قُلْ هُوَ رَبِّي لاٰ إِلٰهَ إِلاّٰ هُوَ عَلَيْهِ تَوَكَّلْتُ وَ إِلَيْهِ مَتٰابِ");
        sor.Ayaat.Add("وَ لَوْ أَنَّ قُرْآناً سُيِّرَتْ بِهِ الْجِبٰالُ أَوْ قُطِّعَتْ بِهِ الْأَرْضُ أَوْ كُلِّمَ بِهِ الْمَوْتىٰ بَلْ لِلّٰهِ الْأَمْرُ جَمِيعاً أَ فَلَمْ يَيْأَسِ الَّذِينَ آمَنُوا أَنْ لَوْ يَشٰاءُ اللّٰهُ لَهَدَى النّٰاسَ جَمِيعاً وَ لاٰ يَزٰالُ الَّذِينَ كَفَرُوا تُصِيبُهُمْ بِمٰا صَنَعُوا قٰارِعَةٌ أَوْ تَحُلُّ قَرِيباً مِنْ دٰارِهِمْ حَتّٰى يَأْتِيَ وَعْدُ اللّٰهِ إِنَّ اللّٰهَ لاٰ يُخْلِفُ الْمِيعٰادَ");
        sor.Ayaat.Add("وَ لَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَأَمْلَيْتُ لِلَّذِينَ كَفَرُوا ثُمَّ أَخَذْتُهُمْ فَكَيْفَ كٰانَ عِقٰابِ");
        sor.Ayaat.Add("أَ فَمَنْ هُوَ قٰائِمٌ عَلىٰ كُلِّ نَفْسٍ بِمٰا كَسَبَتْ وَ جَعَلُوا لِلّٰهِ شُرَكٰاءَ قُلْ سَمُّوهُمْ أَمْ تُنَبِّئُونَهُ بِمٰا لاٰ يَعْلَمُ فِي الْأَرْضِ أَمْ بِظٰاهِرٍ مِنَ الْقَوْلِ بَلْ زُيِّنَ لِلَّذِينَ كَفَرُوا مَكْرُهُمْ وَ صُدُّوا عَنِ السَّبِيلِ وَ مَنْ يُضْلِلِ اللّٰهُ فَمٰا لَهُ مِنْ هٰادٍ");
        sor.Ayaat.Add("لَهُمْ عَذٰابٌ فِي الْحَيٰاةِ الدُّنْيٰا وَ لَعَذٰابُ الْآخِرَةِ أَشَقُّ وَ مٰا لَهُمْ مِنَ اللّٰهِ مِنْ وٰاقٍ");
        sor.Ayaat.Add("مَثَلُ الْجَنَّةِ الَّتِي وُعِدَ الْمُتَّقُونَ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ أُكُلُهٰا دٰائِمٌ وَ ظِلُّهٰا تِلْكَ عُقْبَى الَّذِينَ اتَّقَوْا وَ عُقْبَى الْكٰافِرِينَ النّٰارُ");
        sor.Ayaat.Add("وَ الَّذِينَ آتَيْنٰاهُمُ الْكِتٰابَ يَفْرَحُونَ بِمٰا أُنْزِلَ إِلَيْكَ وَ مِنَ الْأَحْزٰابِ مَنْ يُنْكِرُ بَعْضَهُ قُلْ إِنَّمٰا أُمِرْتُ أَنْ أَعْبُدَ اللّٰهَ وَ لاٰ أُشْرِكَ بِهِ إِلَيْهِ أَدْعُوا وَ إِلَيْهِ مَآبِ");
        sor.Ayaat.Add("وَ كَذٰلِكَ أَنْزَلْنٰاهُ حُكْماً عَرَبِيًّا وَ لَئِنِ اتَّبَعْتَ أَهْوٰاءَهُمْ بَعْدَ مٰا جٰاءَكَ مِنَ الْعِلْمِ مٰا لَكَ مِنَ اللّٰهِ مِنْ وَلِيٍّ وَ لاٰ وٰاقٍ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا رُسُلاً مِنْ قَبْلِكَ وَ جَعَلْنٰا لَهُمْ أَزْوٰاجاً وَ ذُرِّيَّةً وَ مٰا كٰانَ لِرَسُولٍ أَنْ يَأْتِيَ بِآيَةٍ إِلاّٰ بِإِذْنِ اللّٰهِ لِكُلِّ أَجَلٍ كِتٰابٌ");
        sor.Ayaat.Add("يَمْحُوا اللّٰهُ مٰا يَشٰاءُ وَ يُثْبِتُ وَ عِنْدَهُ أُمُّ الْكِتٰابِ");
        sor.Ayaat.Add("وَ إِنْ مٰا نُرِيَنَّكَ بَعْضَ الَّذِي نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِنَّمٰا عَلَيْكَ الْبَلاٰغُ وَ عَلَيْنَا الْحِسٰابُ");
        sor.Ayaat.Add("أَ وَ لَمْ يَرَوْا أَنّٰا نَأْتِي الْأَرْضَ نَنْقُصُهٰا مِنْ أَطْرٰافِهٰا وَ اللّٰهُ يَحْكُمُ لاٰ مُعَقِّبَ لِحُكْمِهِ وَ هُوَ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("وَ قَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَمِيعاً يَعْلَمُ مٰا تَكْسِبُ كُلُّ نَفْسٍ وَ سَيَعْلَمُ الْكُفّٰارُ لِمَنْ عُقْبَى الدّٰارِ");
        sor.Ayaat.Add("وَ يَقُولُ الَّذِينَ كَفَرُوا لَسْتَ مُرْسَلاً قُلْ كَفىٰ بِاللّٰهِ شَهِيداً بَيْنِي وَ بَيْنَكُمْ وَ مَنْ عِنْدَهُ عِلْمُ الْكِتٰابِ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١۴ - ابراهیم";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الر كِتٰابٌ أَنْزَلْنٰاهُ إِلَيْكَ لِتُخْرِجَ النّٰاسَ مِنَ الظُّلُمٰاتِ إِلَى النُّورِ بِإِذْنِ رَبِّهِمْ إِلىٰ صِرٰاطِ الْعَزِيزِ الْحَمِيدِ");
        sor.Ayaat.Add("اللّٰهِ الَّذِي لَهُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ وَيْلٌ لِلْكٰافِرِينَ مِنْ عَذٰابٍ شَدِيدٍ");
        sor.Ayaat.Add("الَّذِينَ يَسْتَحِبُّونَ الْحَيٰاةَ الدُّنْيٰا عَلَى الْآخِرَةِ وَ يَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ وَ يَبْغُونَهٰا عِوَجاً أُولٰئِكَ فِي ضَلاٰلٍ بَعِيدٍ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا مِنْ رَسُولٍ إِلاّٰ بِلِسٰانِ قَوْمِهِ لِيُبَيِّنَ لَهُمْ فَيُضِلُّ اللّٰهُ مَنْ يَشٰاءُ وَ يَهْدِي مَنْ يَشٰاءُ وَ هُوَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا مُوسىٰ بِآيٰاتِنٰا أَنْ أَخْرِجْ قَوْمَكَ مِنَ الظُّلُمٰاتِ إِلَى النُّورِ وَ ذَكِّرْهُمْ بِأَيّٰامِ اللّٰهِ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِكُلِّ صَبّٰارٍ شَكُورٍ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ مُوسىٰ لِقَوْمِهِ اذْكُرُوا نِعْمَةَ اللّٰهِ عَلَيْكُمْ إِذْ أَنْجٰاكُمْ مِنْ آلِ فِرْعَوْنَ يَسُومُونَكُمْ سُوءَ الْعَذٰابِ وَ يُذَبِّحُونَ أَبْنٰاءَكُمْ وَ يَسْتَحْيُونَ نِسٰاءَكُمْ وَ فِي ذٰلِكُمْ بَلاٰءٌ مِنْ رَبِّكُمْ عَظِيمٌ");
        sor.Ayaat.Add("وَ إِذْ تَأَذَّنَ رَبُّكُمْ لَئِنْ شَكَرْتُمْ لَأَزِيدَنَّكُمْ وَ لَئِنْ كَفَرْتُمْ إِنَّ عَذٰابِي لَشَدِيدٌ");
        sor.Ayaat.Add("وَ قٰالَ مُوسىٰ إِنْ تَكْفُرُوا أَنْتُمْ وَ مَنْ فِي الْأَرْضِ جَمِيعاً فَإِنَّ اللّٰهَ لَغَنِيٌّ حَمِيدٌ");
        sor.Ayaat.Add("أَ لَمْ يَأْتِكُمْ نَبَؤُا الَّذِينَ مِنْ قَبْلِكُمْ قَوْمِ نُوحٍ وَ عٰادٍ وَ ثَمُودَ وَ الَّذِينَ مِنْ بَعْدِهِمْ لاٰ يَعْلَمُهُمْ إِلاَّ اللّٰهُ جٰاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰاتِ فَرَدُّوا أَيْدِيَهُمْ فِي أَفْوٰاهِهِمْ وَ قٰالُوا إِنّٰا كَفَرْنٰا بِمٰا أُرْسِلْتُمْ بِهِ وَ إِنّٰا لَفِي شَكٍّ مِمّٰا تَدْعُونَنٰا إِلَيْهِ مُرِيبٍ");
        sor.Ayaat.Add("قٰالَتْ رُسُلُهُمْ أَ فِي اللّٰهِ شَكٌّ فٰاطِرِ السَّمٰاوٰاتِ وَ الْأَرْضِ يَدْعُوكُمْ لِيَغْفِرَ لَكُمْ مِنْ ذُنُوبِكُمْ وَ يُؤَخِّرَكُمْ إِلىٰ أَجَلٍ مُسَمًّى قٰالُوا إِنْ أَنْتُمْ إِلاّٰ بَشَرٌ مِثْلُنٰا تُرِيدُونَ أَنْ تَصُدُّونٰا عَمّٰا كٰانَ يَعْبُدُ آبٰاؤُنٰا فَأْتُونٰا بِسُلْطٰانٍ مُبِينٍ");
        sor.Ayaat.Add("قٰالَتْ لَهُمْ رُسُلُهُمْ إِنْ نَحْنُ إِلاّٰ بَشَرٌ مِثْلُكُمْ وَ لٰكِنَّ اللّٰهَ يَمُنُّ عَلىٰ مَنْ يَشٰاءُ مِنْ عِبٰادِهِ وَ مٰا كٰانَ لَنٰا أَنْ نَأْتِيَكُمْ بِسُلْطٰانٍ إِلاّٰ بِإِذْنِ اللّٰهِ وَ عَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ");
        sor.Ayaat.Add("وَ مٰا لَنٰا أَلاّٰ نَتَوَكَّلَ عَلَى اللّٰهِ وَ قَدْ هَدٰانٰا سُبُلَنٰا وَ لَنَصْبِرَنَّ عَلىٰ مٰا آذَيْتُمُونٰا وَ عَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ كَفَرُوا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِنْ أَرْضِنٰا أَوْ لَتَعُودُنَّ فِي مِلَّتِنٰا فَأَوْحىٰ إِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ لَنُسْكِنَنَّكُمُ الْأَرْضَ مِنْ بَعْدِهِمْ ذٰلِكَ لِمَنْ خٰافَ مَقٰامِي وَ خٰافَ وَعِيدِ");
        sor.Ayaat.Add("وَ اسْتَفْتَحُوا وَ خٰابَ كُلُّ جَبّٰارٍ عَنِيدٍ");
        sor.Ayaat.Add("مِنْ وَرٰائِهِ جَهَنَّمُ وَ يُسْقىٰ مِنْ مٰاءٍ صَدِيدٍ");
        sor.Ayaat.Add("يَتَجَرَّعُهُ وَ لاٰ يَكٰادُ يُسِيغُهُ وَ يَأْتِيهِ الْمَوْتُ مِنْ كُلِّ مَكٰانٍ وَ مٰا هُوَ بِمَيِّتٍ وَ مِنْ وَرٰائِهِ عَذٰابٌ غَلِيظٌ");
        sor.Ayaat.Add("مَثَلُ الَّذِينَ كَفَرُوا بِرَبِّهِمْ أَعْمٰالُهُمْ كَرَمٰادٍ اشْتَدَّتْ بِهِ الرِّيحُ فِي يَوْمٍ عٰاصِفٍ لاٰ يَقْدِرُونَ مِمّٰا كَسَبُوا عَلىٰ شَيْءٍ ذٰلِكَ هُوَ الضَّلاٰلُ الْبَعِيدُ");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنَّ اللّٰهَ خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ بِالْحَقِّ إِنْ يَشَأْ يُذْهِبْكُمْ وَ يَأْتِ بِخَلْقٍ جَدِيدٍ");
        sor.Ayaat.Add("وَ مٰا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيزٍ");
        sor.Ayaat.Add("وَ بَرَزُوا لِلّٰهِ جَمِيعاً فَقٰالَ الضُّعَفٰاءُ لِلَّذِينَ اسْتَكْبَرُوا إِنّٰا كُنّٰا لَكُمْ تَبَعاً فَهَلْ أَنْتُمْ مُغْنُونَ عَنّٰا مِنْ عَذٰابِ اللّٰهِ مِنْ شَيْءٍ قٰالُوا لَوْ هَدٰانَا اللّٰهُ لَهَدَيْنٰاكُمْ سَوٰاءٌ عَلَيْنٰا أَ جَزِعْنٰا أَمْ صَبَرْنٰا مٰا لَنٰا مِنْ مَحِيصٍ");
        sor.Ayaat.Add("وَ قٰالَ الشَّيْطٰانُ لَمّٰا قُضِيَ الْأَمْرُ إِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَ وَعَدْتُكُمْ فَأَخْلَفْتُكُمْ وَ مٰا كٰانَ لِي عَلَيْكُمْ مِنْ سُلْطٰانٍ إِلاّٰ أَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِي فَلاٰ تَلُومُونِي وَ لُومُوا أَنْفُسَكُمْ مٰا أَنَا بِمُصْرِخِكُمْ وَ مٰا أَنْتُمْ بِمُصْرِخِيَّ إِنِّي كَفَرْتُ بِمٰا أَشْرَكْتُمُونِ مِنْ قَبْلُ إِنَّ الظّٰالِمِينَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ أُدْخِلَ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا بِإِذْنِ رَبِّهِمْ تَحِيَّتُهُمْ فِيهٰا سَلاٰمٌ");
        sor.Ayaat.Add("أَ لَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلاً كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ أَصْلُهٰا ثٰابِتٌ وَ فَرْعُهٰا فِي السَّمٰاءِ");
        sor.Ayaat.Add("تُؤْتِي أُكُلَهٰا كُلَّ حِينٍ بِإِذْنِ رَبِّهٰا وَ يَضْرِبُ اللّٰهُ الْأَمْثٰالَ لِلنّٰاسِ لَعَلَّهُمْ يَتَذَكَّرُونَ");
        sor.Ayaat.Add("وَ مَثَلُ كَلِمَةٍ خَبِيثَةٍ كَشَجَرَةٍ خَبِيثَةٍ اجْتُثَّتْ مِنْ فَوْقِ الْأَرْضِ مٰا لَهٰا مِنْ قَرٰارٍ");
        sor.Ayaat.Add("يُثَبِّتُ اللّٰهُ الَّذِينَ آمَنُوا بِالْقَوْلِ الثّٰابِتِ فِي الْحَيٰاةِ الدُّنْيٰا وَ فِي الْآخِرَةِ وَ يُضِلُّ اللّٰهُ الظّٰالِمِينَ وَ يَفْعَلُ اللّٰهُ مٰا يَشٰاءُ");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلَى الَّذِينَ بَدَّلُوا نِعْمَتَ اللّٰهِ كُفْراً وَ أَحَلُّوا قَوْمَهُمْ دٰارَ الْبَوٰارِ");
        sor.Ayaat.Add("جَهَنَّمَ يَصْلَوْنَهٰا وَ بِئْسَ الْقَرٰارُ");
        sor.Ayaat.Add("وَ جَعَلُوا لِلّٰهِ أَنْدٰاداً لِيُضِلُّوا عَنْ سَبِيلِهِ قُلْ تَمَتَّعُوا فَإِنَّ مَصِيرَكُمْ إِلَى النّٰارِ");
        sor.Ayaat.Add("قُلْ لِعِبٰادِيَ الَّذِينَ آمَنُوا يُقِيمُوا الصَّلاٰةَ وَ يُنْفِقُوا مِمّٰا رَزَقْنٰاهُمْ سِرًّا وَ عَلاٰنِيَةً مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لاٰ بَيْعٌ فِيهِ وَ لاٰ خِلاٰلٌ");
        sor.Ayaat.Add("اللّٰهُ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ وَ أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَأَخْرَجَ بِهِ مِنَ الثَّمَرٰاتِ رِزْقاً لَكُمْ وَ سَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِي الْبَحْرِ بِأَمْرِهِ وَ سَخَّرَ لَكُمُ الْأَنْهٰارَ");
        sor.Ayaat.Add("وَ سَخَّرَ لَكُمُ الشَّمْسَ وَ الْقَمَرَ دٰائِبَيْنِ وَ سَخَّرَ لَكُمُ اللَّيْلَ وَ النَّهٰارَ");
        sor.Ayaat.Add("وَ آتٰاكُمْ مِنْ كُلِّ مٰا سَأَلْتُمُوهُ وَ إِنْ تَعُدُّوا نِعْمَتَ اللّٰهِ لاٰ تُحْصُوهٰا إِنَّ الْإِنْسٰانَ لَظَلُومٌ كَفّٰارٌ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ إِبْرٰاهِيمُ رَبِّ اجْعَلْ هَذَا الْبَلَدَ آمِناً وَ اجْنُبْنِي وَ بَنِيَّ أَنْ نَعْبُدَ الْأَصْنٰامَ");
        sor.Ayaat.Add("رَبِّ إِنَّهُنَّ أَضْلَلْنَ كَثِيراً مِنَ النّٰاسِ فَمَنْ تَبِعَنِي فَإِنَّهُ مِنِّي وَ مَنْ عَصٰانِي فَإِنَّكَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("رَبَّنٰا إِنِّي أَسْكَنْتُ مِنْ ذُرِّيَّتِي بِوٰادٍ غَيْرِ ذِي زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِ رَبَّنٰا لِيُقِيمُوا الصَّلاٰةَ فَاجْعَلْ أَفْئِدَةً مِنَ النّٰاسِ تَهْوِي إِلَيْهِمْ وَ ارْزُقْهُمْ مِنَ الثَّمَرٰاتِ لَعَلَّهُمْ يَشْكُرُونَ");
        sor.Ayaat.Add("رَبَّنٰا إِنَّكَ تَعْلَمُ مٰا نُخْفِي وَ مٰا نُعْلِنُ وَ مٰا يَخْفىٰ عَلَى اللّٰهِ مِنْ شَيْءٍ فِي الْأَرْضِ وَ لاٰ فِي السَّمٰاءِ");
        sor.Ayaat.Add("الْحَمْدُ لِلّٰهِ الَّذِي وَهَبَ لِي عَلَى الْكِبَرِ إِسْمٰاعِيلَ وَ إِسْحٰاقَ إِنَّ رَبِّي لَسَمِيعُ الدُّعٰاءِ");
        sor.Ayaat.Add("رَبِّ اجْعَلْنِي مُقِيمَ الصَّلاٰةِ وَ مِنْ ذُرِّيَّتِي رَبَّنٰا وَ تَقَبَّلْ دُعٰاءِ");
        sor.Ayaat.Add("رَبَّنَا اغْفِرْ لِي وَ لِوٰالِدَيَّ وَ لِلْمُؤْمِنِينَ يَوْمَ يَقُومُ الْحِسٰابُ");
        sor.Ayaat.Add("وَ لاٰ تَحْسَبَنَّ اللّٰهَ غٰافِلاً عَمّٰا يَعْمَلُ الظّٰالِمُونَ إِنَّمٰا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيهِ الْأَبْصٰارُ");
        sor.Ayaat.Add("مُهْطِعِينَ مُقْنِعِي رُؤُسِهِمْ لاٰ يَرْتَدُّ إِلَيْهِمْ طَرْفُهُمْ وَ أَفْئِدَتُهُمْ هَوٰاءٌ");
        sor.Ayaat.Add("وَ أَنْذِرِ النّٰاسَ يَوْمَ يَأْتِيهِمُ الْعَذٰابُ فَيَقُولُ الَّذِينَ ظَلَمُوا رَبَّنٰا أَخِّرْنٰا إِلىٰ أَجَلٍ قَرِيبٍ نُجِبْ دَعْوَتَكَ وَ نَتَّبِعِ الرُّسُلَ أَ وَ لَمْ تَكُونُوا أَقْسَمْتُمْ مِنْ قَبْلُ مٰا لَكُمْ مِنْ زَوٰالٍ");
        sor.Ayaat.Add("وَ سَكَنْتُمْ فِي مَسٰاكِنِ الَّذِينَ ظَلَمُوا أَنْفُسَهُمْ وَ تَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنٰا بِهِمْ وَ ضَرَبْنٰا لَكُمُ الْأَمْثٰالَ");
        sor.Ayaat.Add("وَ قَدْ مَكَرُوا مَكْرَهُمْ وَ عِنْدَ اللّٰهِ مَكْرُهُمْ وَ إِنْ كٰانَ مَكْرُهُمْ لِتَزُولَ مِنْهُ الْجِبٰالُ");
        sor.Ayaat.Add("فَلاٰ تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِهِ رُسُلَهُ إِنَّ اللّٰهَ عَزِيزٌ ذُو انتِقٰامٍ");
        sor.Ayaat.Add("يَوْمَ تُبَدَّلُ الْأَرْضُ غَيْرَ الْأَرْضِ وَ السَّمٰاوٰاتُ وَ بَرَزُوا لِلّٰهِ الْوٰاحِدِ الْقَهّٰارِ");
        sor.Ayaat.Add("وَ تَرَى الْمُجْرِمِينَ يَوْمَئِذٍ مُقَرَّنِينَ فِي الْأَصْفٰادِ");
        sor.Ayaat.Add("سَرٰابِيلُهُمْ مِنْ قَطِرٰانٍ وَ تَغْشىٰ وُجُوهَهُمُ النّٰارُ");
        sor.Ayaat.Add("لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مٰا كَسَبَتْ إِنَّ اللّٰهَ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("هٰذٰا بَلاٰغٌ لِلنّٰاسِ وَ لِيُنْذَرُوا بِهِ وَ لِيَعْلَمُوا أَنَّمٰا هُوَ إِلٰهٌ وٰاحِدٌ وَ لِيَذَّكَّرَ أُولُوا الْأَلْبٰابِ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١۵ - الحجر";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الر تِلْكَ آيٰاتُ الْكِتٰابِ وَ قُرْآنٍ مُبِينٍ");
        sor.Ayaat.Add("رُبَمٰا يَوَدُّ الَّذِينَ كَفَرُوا لَوْ كٰانُوا مُسْلِمِينَ");
        sor.Ayaat.Add("ذَرْهُمْ يَأْكُلُوا وَ يَتَمَتَّعُوا وَ يُلْهِهِمُ الْأَمَلُ فَسَوْفَ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ مٰا أَهْلَكْنٰا مِنْ قَرْيَةٍ إِلاّٰ وَ لَهٰا كِتٰابٌ مَعْلُومٌ");
        sor.Ayaat.Add("مٰا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهٰا وَ مٰا يَسْتَأْخِرُونَ");
        sor.Ayaat.Add("وَ قٰالُوا يٰا أَيُّهَا الَّذِي نُزِّلَ عَلَيْهِ الذِّكْرُ إِنَّكَ لَمَجْنُونٌ");
        sor.Ayaat.Add("لَوْ مٰا تَأْتِينٰا بِالْمَلاٰئِكَةِ إِنْ كُنْتَ مِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("مٰا نُنَزِّلُ الْمَلاٰئِكَةَ إِلاّٰ بِالْحَقِّ وَ مٰا كٰانُوا إِذاً مُنْظَرِينَ");
        sor.Ayaat.Add("إِنّٰا نَحْنُ نَزَّلْنَا الذِّكْرَ وَ إِنّٰا لَهُ لَحٰافِظُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا مِنْ قَبْلِكَ فِي شِيَعِ الْأَوَّلِينَ");
        sor.Ayaat.Add("وَ مٰا يَأْتِيهِمْ مِنْ رَسُولٍ إِلاّٰ كٰانُوا بِهِ يَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("كَذٰلِكَ نَسْلُكُهُ فِي قُلُوبِ الْمُجْرِمِينَ");
        sor.Ayaat.Add("لاٰ يُؤْمِنُونَ بِهِ وَ قَدْ خَلَتْ سُنَّةُ الْأَوَّلِينَ");
        sor.Ayaat.Add("وَ لَوْ فَتَحْنٰا عَلَيْهِمْ بٰاباً مِنَ السَّمٰاءِ فَظَلُّوا فِيهِ يَعْرُجُونَ");
        sor.Ayaat.Add("لَقٰالُوا إِنَّمٰا سُكِّرَتْ أَبْصٰارُنٰا بَلْ نَحْنُ قَوْمٌ مَسْحُورُونَ");
        sor.Ayaat.Add("وَ لَقَدْ جَعَلْنٰا فِي السَّمٰاءِ بُرُوجاً وَ زَيَّنّٰاهٰا لِلنّٰاظِرِينَ");
        sor.Ayaat.Add("وَ حَفِظْنٰاهٰا مِنْ كُلِّ شَيْطٰانٍ رَجِيمٍ");
        sor.Ayaat.Add("إِلاّٰ مَنِ اسْتَرَقَ السَّمْعَ فَأَتْبَعَهُ شِهٰابٌ مُبِينٌ");
        sor.Ayaat.Add("وَ الْأَرْضَ مَدَدْنٰاهٰا وَ أَلْقَيْنٰا فِيهٰا رَوٰاسِيَ وَ أَنْبَتْنٰا فِيهٰا مِنْ كُلِّ شَيْءٍ مَوْزُونٍ");
        sor.Ayaat.Add("وَ جَعَلْنٰا لَكُمْ فِيهٰا مَعٰايِشَ وَ مَنْ لَسْتُمْ لَهُ بِرٰازِقِينَ");
        sor.Ayaat.Add("وَ إِنْ مِنْ شَيْءٍ إِلاّٰ عِنْدَنٰا خَزٰائِنُهُ وَ مٰا نُنَزِّلُهُ إِلاّٰ بِقَدَرٍ مَعْلُومٍ");
        sor.Ayaat.Add("وَ أَرْسَلْنَا الرِّيٰاحَ لَوٰاقِحَ فَأَنْزَلْنٰا مِنَ السَّمٰاءِ مٰاءً فَأَسْقَيْنٰاكُمُوهُ وَ مٰا أَنْتُمْ لَهُ بِخٰازِنِينَ");
        sor.Ayaat.Add("وَ إِنّٰا لَنَحْنُ نُحْيِي وَ نُمِيتُ وَ نَحْنُ الْوٰارِثُونَ");
        sor.Ayaat.Add("وَ لَقَدْ عَلِمْنَا الْمُسْتَقْدِمِينَ مِنْكُمْ وَ لَقَدْ عَلِمْنَا الْمُسْتَأْخِرِينَ");
        sor.Ayaat.Add("وَ إِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْ إِنَّهُ حَكِيمٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ لَقَدْ خَلَقْنَا الْإِنْسٰانَ مِنْ صَلْصٰالٍ مِنْ حَمَإٍ مَسْنُونٍ");
        sor.Ayaat.Add("وَ الْجَانَّ خَلَقْنٰاهُ مِنْ قَبْلُ مِنْ نٰارِ السَّمُومِ");
        sor.Ayaat.Add("وَ إِذْ قٰالَ رَبُّكَ لِلْمَلاٰئِكَةِ إِنِّي خٰالِقٌ بَشَراً مِنْ صَلْصٰالٍ مِنْ حَمَإٍ مَسْنُونٍ");
        sor.Ayaat.Add("فَإِذٰا سَوَّيْتُهُ وَ نَفَخْتُ فِيهِ مِنْ رُوحِي فَقَعُوا لَهُ سٰاجِدِينَ");
        sor.Ayaat.Add("فَسَجَدَ الْمَلاٰئِكَةُ كُلُّهُمْ أَجْمَعُونَ");
        sor.Ayaat.Add("إِلاّٰ إِبْلِيسَ أَبىٰ أَنْ يَكُونَ مَعَ السّٰاجِدِينَ");
        sor.Ayaat.Add("قٰالَ يٰا إِبْلِيسُ مٰا لَكَ أَلاّٰ تَكُونَ مَعَ السّٰاجِدِينَ");
        sor.Ayaat.Add("قٰالَ لَمْ أَكُنْ لِأَسْجُدَ لِبَشَرٍ خَلَقْتَهُ مِنْ صَلْصٰالٍ مِنْ حَمَإٍ مَسْنُونٍ");
        sor.Ayaat.Add("قٰالَ فَاخْرُجْ مِنْهٰا فَإِنَّكَ رَجِيمٌ");
        sor.Ayaat.Add("وَ إِنَّ عَلَيْكَ اللَّعْنَةَ إِلىٰ يَوْمِ الدِّينِ");
        sor.Ayaat.Add("قٰالَ رَبِّ فَأَنْظِرْنِي إِلىٰ يَوْمِ يُبْعَثُونَ");
        sor.Ayaat.Add("قٰالَ فَإِنَّكَ مِنَ الْمُنْظَرِينَ");
        sor.Ayaat.Add("إِلىٰ يَوْمِ الْوَقْتِ الْمَعْلُومِ");
        sor.Ayaat.Add("قٰالَ رَبِّ بِمٰا أَغْوَيْتَنِي لَأُزَيِّنَنَّ لَهُمْ فِي الْأَرْضِ وَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("إِلاّٰ عِبٰادَكَ مِنْهُمُ الْمُخْلَصِينَ");
        sor.Ayaat.Add("قٰالَ هٰذٰا صِرٰاطٌ عَلَيَّ مُسْتَقِيمٌ");
        sor.Ayaat.Add("إِنَّ عِبٰادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰانٌ إِلاّٰ مَنِ اتَّبَعَكَ مِنَ الْغٰاوِينَ");
        sor.Ayaat.Add("وَ إِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("لَهٰا سَبْعَةُ أَبْوٰابٍ لِكُلِّ بٰابٍ مِنْهُمْ جُزْءٌ مَقْسُومٌ");
        sor.Ayaat.Add("إِنَّ الْمُتَّقِينَ فِي جَنّٰاتٍ وَ عُيُونٍ");
        sor.Ayaat.Add("ادْخُلُوهٰا بِسَلاٰمٍ آمِنِينَ");
        sor.Ayaat.Add("وَ نَزَعْنٰا مٰا فِي صُدُورِهِمْ مِنْ غِلٍّ إِخْوٰاناً عَلىٰ سُرُرٍ مُتَقٰابِلِينَ");
        sor.Ayaat.Add("لاٰ يَمَسُّهُمْ فِيهٰا نَصَبٌ وَ مٰا هُمْ مِنْهٰا بِمُخْرَجِينَ");
        sor.Ayaat.Add("نَبِّئْ عِبٰادِي أَنِّي أَنَا الْغَفُورُ الرَّحِيمُ");
        sor.Ayaat.Add("وَ أَنَّ عَذٰابِي هُوَ الْعَذٰابُ الْأَلِيمُ");
        sor.Ayaat.Add("وَ نَبِّئْهُمْ عَنْ ضَيْفِ إِبْرٰاهِيمَ");
        sor.Ayaat.Add("إِذْ دَخَلُوا عَلَيْهِ فَقٰالُوا سَلاٰماً قٰالَ إِنّٰا مِنْكُمْ وَجِلُونَ");
        sor.Ayaat.Add("قٰالُوا لاٰ تَوْجَلْ إِنّٰا نُبَشِّرُكَ بِغُلاٰمٍ عَلِيمٍ");
        sor.Ayaat.Add("قٰالَ أَ بَشَّرْتُمُونِي عَلىٰ أَنْ مَسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُونَ");
        sor.Ayaat.Add("قٰالُوا بَشَّرْنٰاكَ بِالْحَقِّ فَلاٰ تَكُنْ مِنَ الْقٰانِطِينَ");
        sor.Ayaat.Add("قٰالَ وَ مَنْ يَقْنَطُ مِنْ رَحْمَةِ رَبِّهِ إِلاَّ الضّٰالُّونَ");
        sor.Ayaat.Add("قٰالَ فَمٰا خَطْبُكُمْ أَيُّهَا الْمُرْسَلُونَ");
        sor.Ayaat.Add("قٰالُوا إِنّٰا أُرْسِلْنٰا إِلىٰ قَوْمٍ مُجْرِمِينَ");
        sor.Ayaat.Add("إِلاّٰ آلَ لُوطٍ إِنّٰا لَمُنَجُّوهُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("إِلاَّ امْرَأَتَهُ قَدَّرْنٰا إِنَّهٰا لَمِنَ الْغٰابِرِينَ");
        sor.Ayaat.Add("فَلَمّٰا جٰاءَ آلَ لُوطٍ الْمُرْسَلُونَ");
        sor.Ayaat.Add("قٰالَ إِنَّكُمْ قَوْمٌ مُنْكَرُونَ");
        sor.Ayaat.Add("قٰالُوا بَلْ جِئْنٰاكَ بِمٰا كٰانُوا فِيهِ يَمْتَرُونَ");
        sor.Ayaat.Add("وَ أَتَيْنٰاكَ بِالْحَقِّ وَ إِنّٰا لَصٰادِقُونَ");
        sor.Ayaat.Add("فَأَسْرِ بِأَهْلِكَ بِقِطْعٍ مِنَ اللَّيْلِ وَ اتَّبِعْ أَدْبٰارَهُمْ وَ لاٰ يَلْتَفِتْ مِنْكُمْ أَحَدٌ وَ امْضُوا حَيْثُ تُؤْمَرُونَ");
        sor.Ayaat.Add("وَ قَضَيْنٰا إِلَيْهِ ذٰلِكَ الْأَمْرَ أَنَّ دٰابِرَ هٰؤُلاٰءِ مَقْطُوعٌ مُصْبِحِينَ");
        sor.Ayaat.Add("وَ جٰاءَ أَهْلُ الْمَدِينَةِ يَسْتَبْشِرُونَ");
        sor.Ayaat.Add("قٰالَ إِنَّ هٰؤُلاٰءِ ضَيْفِي فَلاٰ تَفْضَحُونِ");
        sor.Ayaat.Add("وَ اتَّقُوا اللّٰهَ وَ لاٰ تُخْزُونِ");
        sor.Ayaat.Add("قٰالُوا أَ وَ لَمْ نَنْهَكَ عَنِ الْعٰالَمِينَ");
        sor.Ayaat.Add("قٰالَ هٰؤُلاٰءِ بَنٰاتِي إِنْ كُنْتُمْ فٰاعِلِينَ");
        sor.Ayaat.Add("لَعَمْرُكَ إِنَّهُمْ لَفِي سَكْرَتِهِمْ يَعْمَهُونَ");
        sor.Ayaat.Add("فَأَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِينَ");
        sor.Ayaat.Add("فَجَعَلْنٰا عٰالِيَهٰا سٰافِلَهٰا وَ أَمْطَرْنٰا عَلَيْهِمْ حِجٰارَةً مِنْ سِجِّيلٍ");
        sor.Ayaat.Add("إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِلْمُتَوَسِّمِينَ");
        sor.Ayaat.Add("وَ إِنَّهٰا لَبِسَبِيلٍ مُقِيمٍ");
        sor.Ayaat.Add("إِنَّ فِي ذٰلِكَ لَآيَةً لِلْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ إِنْ كٰانَ أَصْحٰابُ الْأَيْكَةِ لَظٰالِمِينَ");
        sor.Ayaat.Add("فَانْتَقَمْنٰا مِنْهُمْ وَ إِنَّهُمٰا لَبِإِمٰامٍ مُبِينٍ");
        sor.Ayaat.Add("وَ لَقَدْ كَذَّبَ أَصْحٰابُ الْحِجْرِ الْمُرْسَلِينَ");
        sor.Ayaat.Add("وَ آتَيْنٰاهُمْ آيٰاتِنٰا فَكٰانُوا عَنْهٰا مُعْرِضِينَ");
        sor.Ayaat.Add("وَ كٰانُوا يَنْحِتُونَ مِنَ الْجِبٰالِ بُيُوتاً آمِنِينَ");
        sor.Ayaat.Add("فَأَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِينَ");
        sor.Ayaat.Add("فَمٰا أَغْنىٰ عَنْهُمْ مٰا كٰانُوا يَكْسِبُونَ");
        sor.Ayaat.Add("وَ مٰا خَلَقْنَا السَّمٰاوٰاتِ وَ الْأَرْضَ وَ مٰا بَيْنَهُمٰا إِلاّٰ بِالْحَقِّ وَ إِنَّ السّٰاعَةَ لَآتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيلَ");
        sor.Ayaat.Add("إِنَّ رَبَّكَ هُوَ الْخَلاّٰقُ الْعَلِيمُ");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰاكَ سَبْعاً مِنَ الْمَثٰانِي وَ الْقُرْآنَ الْعَظِيمَ");
        sor.Ayaat.Add("لاٰ تَمُدَّنَّ عَيْنَيْكَ إِلىٰ مٰا مَتَّعْنٰا بِهِ أَزْوٰاجاً مِنْهُمْ وَ لاٰ تَحْزَنْ عَلَيْهِمْ وَ اخْفِضْ جَنٰاحَكَ لِلْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ قُلْ إِنِّي أَنَا النَّذِيرُ الْمُبِينُ");
        sor.Ayaat.Add("كَمٰا أَنْزَلْنٰا عَلَى الْمُقْتَسِمِينَ");
        sor.Ayaat.Add("الَّذِينَ جَعَلُوا الْقُرْآنَ عِضِينَ");
        sor.Ayaat.Add("فَوَ رَبِّكَ لَنَسْئَلَنَّهُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("عَمّٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("فَاصْدَعْ بِمٰا تُؤْمَرُ وَ أَعْرِضْ عَنِ الْمُشْرِكِينَ");
        sor.Ayaat.Add("إِنّٰا كَفَيْنٰاكَ الْمُسْتَهْزِئِينَ");
        sor.Ayaat.Add("الَّذِينَ يَجْعَلُونَ مَعَ اللّٰهِ إِلٰهاً آخَرَ فَسَوْفَ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ لَقَدْ نَعْلَمُ أَنَّكَ يَضِيقُ صَدْرُكَ بِمٰا يَقُولُونَ");
        sor.Ayaat.Add("فَسَبِّحْ بِحَمْدِ رَبِّكَ وَ كُنْ مِنَ السّٰاجِدِينَ");
        sor.Ayaat.Add("وَ اعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَقِينُ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١۶ - النحل";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ أَتىٰ أَمْرُ اللّٰهِ فَلاٰ تَسْتَعْجِلُوهُ سُبْحٰانَهُ وَ تَعٰالىٰ عَمّٰا يُشْرِكُونَ");
        sor.Ayaat.Add("يُنَزِّلُ الْمَلاٰئِكَةَ بِالرُّوحِ مِنْ أَمْرِهِ عَلىٰ مَنْ يَشٰاءُ مِنْ عِبٰادِهِ أَنْ أَنْذِرُوا أَنَّهُ لاٰ إِلٰهَ إِلاّٰ أَنَا فَاتَّقُونِ");
        sor.Ayaat.Add("خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ بِالْحَقِّ تَعٰالىٰ عَمّٰا يُشْرِكُونَ");
        sor.Ayaat.Add("خَلَقَ الْإِنْسٰانَ مِنْ نُطْفَةٍ فَإِذٰا هُوَ خَصِيمٌ مُبِينٌ");
        sor.Ayaat.Add("وَ الْأَنْعٰامَ خَلَقَهٰا لَكُمْ فِيهٰا دِفْءٌ وَ مَنٰافِعُ وَ مِنْهٰا تَأْكُلُونَ");
        sor.Ayaat.Add("وَ لَكُمْ فِيهٰا جَمٰالٌ حِينَ تُرِيحُونَ وَ حِينَ تَسْرَحُونَ");
        sor.Ayaat.Add("وَ تَحْمِلُ أَثْقٰالَكُمْ إِلىٰ بَلَدٍ لَمْ تَكُونُوا بٰالِغِيهِ إِلاّٰ بِشِقِّ الْأَنْفُسِ إِنَّ رَبَّكُمْ لَرَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ الْخَيْلَ وَ الْبِغٰالَ وَ الْحَمِيرَ لِتَرْكَبُوهٰا وَ زِينَةً وَ يَخْلُقُ مٰا لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ عَلَى اللّٰهِ قَصْدُ السَّبِيلِ وَ مِنْهٰا جٰائِرٌ وَ لَوْ شٰاءَ لَهَدٰاكُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("هُوَ الَّذِي أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً لَكُمْ مِنْهُ شَرٰابٌ وَ مِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ");
        sor.Ayaat.Add("يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَ الزَّيْتُونَ وَ النَّخِيلَ وَ الْأَعْنٰابَ وَ مِنْ كُلِّ الثَّمَرٰاتِ إِنَّ فِي ذٰلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ");
        sor.Ayaat.Add("وَ سَخَّرَ لَكُمُ اللَّيْلَ وَ النَّهٰارَ وَ الشَّمْسَ وَ الْقَمَرَ وَ النُّجُومُ مُسَخَّرٰاتٌ بِأَمْرِهِ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِقَوْمٍ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ مٰا ذَرَأَ لَكُمْ فِي الْأَرْضِ مُخْتَلِفاً أَلْوٰانُهُ إِنَّ فِي ذٰلِكَ لَآيَةً لِقَوْمٍ يَذَّكَّرُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي سَخَّرَ الْبَحْرَ لِتَأْكُلُوا مِنْهُ لَحْماً طَرِيًّا وَ تَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهٰا وَ تَرَى الْفُلْكَ مَوٰاخِرَ فِيهِ وَ لِتَبْتَغُوا مِنْ فَضْلِهِ وَ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("وَ أَلْقىٰ فِي الْأَرْضِ رَوٰاسِيَ أَنْ تَمِيدَ بِكُمْ وَ أَنْهٰاراً وَ سُبُلاً لَعَلَّكُمْ تَهْتَدُونَ");
        sor.Ayaat.Add("وَ عَلاٰمٰاتٍ وَ بِالنَّجْمِ هُمْ يَهْتَدُونَ");
        sor.Ayaat.Add("أَ فَمَنْ يَخْلُقُ كَمَنْ لاٰ يَخْلُقُ أَ فَلاٰ تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ إِنْ تَعُدُّوا نِعْمَةَ اللّٰهِ لاٰ تُحْصُوهٰا إِنَّ اللّٰهَ لَغَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ اللّٰهُ يَعْلَمُ مٰا تُسِرُّونَ وَ مٰا تُعْلِنُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ يَدْعُونَ مِنْ دُونِ اللّٰهِ لاٰ يَخْلُقُونَ شَيْئاً وَ هُمْ يُخْلَقُونَ");
        sor.Ayaat.Add("أَمْوٰاتٌ غَيْرُ أَحْيٰاءٍ وَ مٰا يَشْعُرُونَ أَيّٰانَ يُبْعَثُونَ");
        sor.Ayaat.Add("إِلٰهُكُمْ إِلٰهٌ وٰاحِدٌ فَالَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ قُلُوبُهُمْ مُنْكِرَةٌ وَ هُمْ مُسْتَكْبِرُونَ");
        sor.Ayaat.Add("لاٰ جَرَمَ أَنَّ اللّٰهَ يَعْلَمُ مٰا يُسِرُّونَ وَ مٰا يُعْلِنُونَ إِنَّهُ لاٰ يُحِبُّ الْمُسْتَكْبِرِينَ");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمْ مٰاذٰا أَنْزَلَ رَبُّكُمْ قٰالُوا أَسٰاطِيرُ الْأَوَّلِينَ");
        sor.Ayaat.Add("لِيَحْمِلُوا أَوْزٰارَهُمْ كٰامِلَةً يَوْمَ الْقِيٰامَةِ وَ مِنْ أَوْزٰارِ الَّذِينَ يُضِلُّونَهُمْ بِغَيْرِ عِلْمٍ أَلاٰ سٰاءَ مٰا يَزِرُونَ");
        sor.Ayaat.Add("قَدْ مَكَرَ الَّذِينَ مِنْ قَبْلِهِمْ فَأَتَى اللّٰهُ بُنْيٰانَهُمْ مِنَ الْقَوٰاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَ أَتٰاهُمُ الْعَذٰابُ مِنْ حَيْثُ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("ثُمَّ يَوْمَ الْقِيٰامَةِ يُخْزِيهِمْ وَ يَقُولُ أَيْنَ شُرَكٰائِيَ الَّذِينَ كُنْتُمْ تُشَاقُّونَ فِيهِمْ قٰالَ الَّذِينَ أُوتُوا الْعِلْمَ إِنَّ الْخِزْيَ الْيَوْمَ وَ السُّوءَ عَلَى الْكٰافِرِينَ");
        sor.Ayaat.Add("الَّذِينَ تَتَوَفّٰاهُمُ الْمَلاٰئِكَةُ ظٰالِمِي أَنْفُسِهِمْ فَأَلْقَوُا السَّلَمَ مٰا كُنّٰا نَعْمَلُ مِنْ سُوءٍ بَلىٰ إِنَّ اللّٰهَ عَلِيمٌ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("فَادْخُلُوا أَبْوٰابَ جَهَنَّمَ خٰالِدِينَ فِيهٰا فَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِينَ");
        sor.Ayaat.Add("وَ قِيلَ لِلَّذِينَ اتَّقَوْا مٰاذٰا أَنْزَلَ رَبُّكُمْ قٰالُوا خَيْراً لِلَّذِينَ أَحْسَنُوا فِي هٰذِهِ الدُّنْيٰا حَسَنَةٌ وَ لَدٰارُ الْآخِرَةِ خَيْرٌ وَ لَنِعْمَ دٰارُ الْمُتَّقِينَ");
        sor.Ayaat.Add("جَنّٰاتُ عَدْنٍ يَدْخُلُونَهٰا تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ لَهُمْ فِيهٰا مٰا يَشٰاؤُنَ كَذٰلِكَ يَجْزِي اللّٰهُ الْمُتَّقِينَ");
        sor.Ayaat.Add("الَّذِينَ تَتَوَفّٰاهُمُ الْمَلاٰئِكَةُ طَيِّبِينَ يَقُولُونَ سَلاٰمٌ عَلَيْكُمْ ادْخُلُوا الْجَنَّةَ بِمٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("هَلْ يَنْظُرُونَ إِلاّٰ أَنْ تَأْتِيَهُمُ الْمَلاٰئِكَةُ أَوْ يَأْتِيَ أَمْرُ رَبِّكَ كَذٰلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ وَ مٰا ظَلَمَهُمُ اللّٰهُ وَ لٰكِنْ كٰانُوا أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("فَأَصٰابَهُمْ سَيِّئٰاتُ مٰا عَمِلُوا وَ حٰاقَ بِهِمْ مٰا كٰانُوا بِهِ يَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ أَشْرَكُوا لَوْ شٰاءَ اللّٰهُ مٰا عَبَدْنٰا مِنْ دُونِهِ مِنْ شَيْءٍ نَحْنُ وَ لاٰ آبٰاؤُنٰا وَ لاٰ حَرَّمْنٰا مِنْ دُونِهِ مِنْ شَيْءٍ كَذٰلِكَ فَعَلَ الَّذِينَ مِنْ قَبْلِهِمْ فَهَلْ عَلَى الرُّسُلِ إِلاَّ الْبَلاٰغُ الْمُبِينُ");
        sor.Ayaat.Add("وَ لَقَدْ بَعَثْنٰا فِي كُلِّ أُمَّةٍ رَسُولاً أَنِ اعْبُدُوا اللّٰهَ وَ اجْتَنِبُوا الطّٰاغُوتَ فَمِنْهُمْ مَنْ هَدَى اللّٰهُ وَ مِنْهُمْ مَنْ حَقَّتْ عَلَيْهِ الضَّلاٰلَةُ فَسِيرُوا فِي الْأَرْضِ فَانْظُرُوا كَيْفَ كٰانَ عٰاقِبَةُ الْمُكَذِّبِينَ");
        sor.Ayaat.Add("إِنْ تَحْرِصْ عَلىٰ هُدٰاهُمْ فَإِنَّ اللّٰهَ لاٰ يَهْدِي مَنْ يُضِلُّ وَ مٰا لَهُمْ مِنْ نٰاصِرِينَ");
        sor.Ayaat.Add("وَ أَقْسَمُوا بِاللّٰهِ جَهْدَ أَيْمٰانِهِمْ لاٰ يَبْعَثُ اللّٰهُ مَنْ يَمُوتُ بَلىٰ وَعْداً عَلَيْهِ حَقًّا وَ لٰكِنَّ أَكْثَرَ النّٰاسِ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("لِيُبَيِّنَ لَهُمُ الَّذِي يَخْتَلِفُونَ فِيهِ وَ لِيَعْلَمَ الَّذِينَ كَفَرُوا أَنَّهُمْ كٰانُوا كٰاذِبِينَ");
        sor.Ayaat.Add("إِنَّمٰا قَوْلُنٰا لِشَيْءٍ إِذٰا أَرَدْنٰاهُ أَنْ نَقُولَ لَهُ كُنْ فَيَكُونُ");
        sor.Ayaat.Add("وَ الَّذِينَ هٰاجَرُوا فِي اللّٰهِ مِنْ بَعْدِ مٰا ظُلِمُوا لَنُبَوِّئَنَّهُمْ فِي الدُّنْيٰا حَسَنَةً وَ لَأَجْرُ الْآخِرَةِ أَكْبَرُ لَوْ كٰانُوا يَعْلَمُونَ");
        sor.Ayaat.Add("الَّذِينَ صَبَرُوا وَ عَلىٰ رَبِّهِمْ يَتَوَكَّلُونَ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا مِنْ قَبْلِكَ إِلاّٰ رِجٰالاً نُوحِي إِلَيْهِمْ فَسْئَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("بِالْبَيِّنٰاتِ وَ الزُّبُرِ وَ أَنْزَلْنٰا إِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنّٰاسِ مٰا نُزِّلَ إِلَيْهِمْ وَ لَعَلَّهُمْ يَتَفَكَّرُونَ");
        sor.Ayaat.Add("أَ فَأَمِنَ الَّذِينَ مَكَرُوا السَّيِّئٰاتِ أَنْ يَخْسِفَ اللّٰهُ بِهِمُ الْأَرْضَ أَوْ يَأْتِيَهُمُ الْعَذٰابُ مِنْ حَيْثُ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("أَوْ يَأْخُذَهُمْ فِي تَقَلُّبِهِمْ فَمٰا هُمْ بِمُعْجِزِينَ");
        sor.Ayaat.Add("أَوْ يَأْخُذَهُمْ عَلىٰ تَخَوُّفٍ فَإِنَّ رَبَّكُمْ لَرَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("أَ وَ لَمْ يَرَوْا إِلىٰ مٰا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَتَفَيَّؤُا ظِلاٰلُهُ عَنِ الْيَمِينِ وَ الشَّمٰائِلِ سُجَّداً لِلّٰهِ وَ هُمْ دٰاخِرُونَ");
        sor.Ayaat.Add("وَ لِلّٰهِ يَسْجُدُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ مِنْ دٰابَّةٍ وَ الْمَلاٰئِكَةُ وَ هُمْ لاٰ يَسْتَكْبِرُونَ");
        sor.Ayaat.Add("يَخٰافُونَ رَبَّهُمْ مِنْ فَوْقِهِمْ وَ يَفْعَلُونَ مٰا يُؤْمَرُونَ");
        sor.Ayaat.Add("وَ قٰالَ اللّٰهُ لاٰ تَتَّخِذُوا إِلٰهَيْنِ اثْنَيْنِ إِنَّمٰا هُوَ إِلٰهٌ وٰاحِدٌ فَإِيّٰايَ فَارْهَبُونِ");
        sor.Ayaat.Add("وَ لَهُ مٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ وَ لَهُ الدِّينُ وٰاصِباً أَ فَغَيْرَ اللّٰهِ تَتَّقُونَ");
        sor.Ayaat.Add("وَ مٰا بِكُمْ مِنْ نِعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ إِذٰا مَسَّكُمُ الضُّرُّ فَإِلَيْهِ تَجْئَرُونَ");
        sor.Ayaat.Add("ثُمَّ إِذٰا كَشَفَ الضُّرَّ عَنْكُمْ إِذٰا فَرِيقٌ مِنْكُمْ بِرَبِّهِمْ يُشْرِكُونَ");
        sor.Ayaat.Add("لِيَكْفُرُوا بِمٰا آتَيْنٰاهُمْ فَتَمَتَّعُوا فَسَوْفَ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ يَجْعَلُونَ لِمٰا لاٰ يَعْلَمُونَ نَصِيباً مِمّٰا رَزَقْنٰاهُمْ تَاللّٰهِ لَتُسْئَلُنَّ عَمّٰا كُنْتُمْ تَفْتَرُونَ");
        sor.Ayaat.Add("وَ يَجْعَلُونَ لِلّٰهِ الْبَنٰاتِ سُبْحٰانَهُ وَ لَهُمْ مٰا يَشْتَهُونَ");
        sor.Ayaat.Add("وَ إِذٰا بُشِّرَ أَحَدُهُمْ بِالْأُنْثىٰ ظَلَّ وَجْهُهُ مُسْوَدًّا وَ هُوَ كَظِيمٌ");
        sor.Ayaat.Add("يَتَوٰارىٰ مِنَ الْقَوْمِ مِنْ سُوءِ مٰا بُشِّرَ بِهِ أَ يُمْسِكُهُ عَلىٰ هُونٍ أَمْ يَدُسُّهُ فِي التُّرٰابِ أَلاٰ سٰاءَ مٰا يَحْكُمُونَ");
        sor.Ayaat.Add("لِلَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ مَثَلُ السَّوْءِ وَ لِلّٰهِ الْمَثَلُ الْأَعْلىٰ وَ هُوَ الْعَزِيزُ الْحَكِيمُ");
        sor.Ayaat.Add("وَ لَوْ يُؤٰاخِذُ اللّٰهُ النّٰاسَ بِظُلْمِهِمْ مٰا تَرَكَ عَلَيْهٰا مِنْ دَابَّةٍ وَ لٰكِنْ يُؤَخِّرُهُمْ إِلىٰ أَجَلٍ مُسَمًّى فَإِذٰا جٰاءَ أَجَلُهُمْ لاٰ يَسْتَأْخِرُونَ سٰاعَةً وَ لاٰ يَسْتَقْدِمُونَ");
        sor.Ayaat.Add("وَ يَجْعَلُونَ لِلّٰهِ مٰا يَكْرَهُونَ وَ تَصِفُ أَلْسِنَتُهُمُ الْكَذِبَ أَنَّ لَهُمُ الْحُسْنىٰ لاٰ جَرَمَ أَنَّ لَهُمُ النّٰارَ وَ أَنَّهُمْ مُفْرَطُونَ");
        sor.Ayaat.Add("تَاللّٰهِ لَقَدْ أَرْسَلْنٰا إِلىٰ أُمَمٍ مِنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطٰانُ أَعْمٰالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ مٰا أَنْزَلْنٰا عَلَيْكَ الْكِتٰابَ إِلاّٰ لِتُبَيِّنَ لَهُمُ الَّذِي اخْتَلَفُوا فِيهِ وَ هُدىً وَ رَحْمَةً لِقَوْمٍ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ اللّٰهُ أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَأَحْيٰا بِهِ الْأَرْضَ بَعْدَ مَوْتِهٰا إِنَّ فِي ذٰلِكَ لَآيَةً لِقَوْمٍ يَسْمَعُونَ");
        sor.Ayaat.Add("وَ إِنَّ لَكُمْ فِي الْأَنْعٰامِ لَعِبْرَةً نُسْقِيكُمْ مِمّٰا فِي بُطُونِهِ مِنْ بَيْنِ فَرْثٍ وَ دَمٍ لَبَناً خٰالِصاً سٰائِغاً لِلشّٰارِبِينَ");
        sor.Ayaat.Add("وَ مِنْ ثَمَرٰاتِ النَّخِيلِ وَ الْأَعْنٰابِ تَتَّخِذُونَ مِنْهُ سَكَراً وَ رِزْقاً حَسَناً إِنَّ فِي ذٰلِكَ لَآيَةً لِقَوْمٍ يَعْقِلُونَ");
        sor.Ayaat.Add("وَ أَوْحىٰ رَبُّكَ إِلَى النَّحْلِ أَنِ اتَّخِذِي مِنَ الْجِبٰالِ بُيُوتاً وَ مِنَ الشَّجَرِ وَ مِمّٰا يَعْرِشُونَ");
        sor.Ayaat.Add("ثُمَّ كُلِي مِنْ كُلِّ الثَّمَرٰاتِ فَاسْلُكِي سُبُلَ رَبِّكِ ذُلُلاً يَخْرُجُ مِنْ بُطُونِهٰا شَرٰابٌ مُخْتَلِفٌ أَلْوٰانُهُ فِيهِ شِفٰاءٌ لِلنّٰاسِ إِنَّ فِي ذٰلِكَ لَآيَةً لِقَوْمٍ يَتَفَكَّرُونَ");
        sor.Ayaat.Add("وَ اللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰاكُمْ وَ مِنْكُمْ مَنْ يُرَدُّ إِلىٰ أَرْذَلِ الْعُمُرِ لِكَيْ لاٰ يَعْلَمَ بَعْدَ عِلْمٍ شَيْئاً إِنَّ اللّٰهَ عَلِيمٌ قَدِيرٌ");
        sor.Ayaat.Add("وَ اللّٰهُ فَضَّلَ بَعْضَكُمْ عَلىٰ بَعْضٍ فِي الرِّزْقِ فَمَا الَّذِينَ فُضِّلُوا بِرَادِّي رِزْقِهِمْ عَلىٰ مٰا مَلَكَتْ أَيْمٰانُهُمْ فَهُمْ فِيهِ سَوٰاءٌ أَ فَبِنِعْمَةِ اللّٰهِ يَجْحَدُونَ");
        sor.Ayaat.Add("وَ اللّٰهُ جَعَلَ لَكُمْ مِنْ أَنْفُسِكُمْ أَزْوٰاجاً وَ جَعَلَ لَكُمْ مِنْ أَزْوٰاجِكُمْ بَنِينَ وَ حَفَدَةً وَ رَزَقَكُمْ مِنَ الطَّيِّبٰاتِ أَ فَبِالْبٰاطِلِ يُؤْمِنُونَ وَ بِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُونَ");
        sor.Ayaat.Add("وَ يَعْبُدُونَ مِنْ دُونِ اللّٰهِ مٰا لاٰ يَمْلِكُ لَهُمْ رِزْقاً مِنَ السَّمٰاوٰاتِ وَ الْأَرْضِ شَيْئاً وَ لاٰ يَسْتَطِيعُونَ");
        sor.Ayaat.Add("فَلاٰ تَضْرِبُوا لِلّٰهِ الْأَمْثٰالَ إِنَّ اللّٰهَ يَعْلَمُ وَ أَنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("ضَرَبَ اللّٰهُ مَثَلاً عَبْداً مَمْلُوكاً لاٰ يَقْدِرُ عَلىٰ شَيْءٍ وَ مَنْ رَزَقْنٰاهُ مِنّٰا رِزْقاً حَسَناً فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَ جَهْراً هَلْ يَسْتَوُونَ الْحَمْدُ لِلّٰهِ بَلْ أَكْثَرُهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("وَ ضَرَبَ اللّٰهُ مَثَلاً رَجُلَيْنِ أَحَدُهُمٰا أَبْكَمُ لاٰ يَقْدِرُ عَلىٰ شَيْءٍ وَ هُوَ كَلٌّ عَلىٰ مَوْلاٰهُ أَيْنَمٰا يُوَجِّهْهُ لاٰ يَأْتِ بِخَيْرٍ هَلْ يَسْتَوِي هُوَ وَ مَنْ يَأْمُرُ بِالْعَدْلِ وَ هُوَ عَلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ لِلّٰهِ غَيْبُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا أَمْرُ السّٰاعَةِ إِلاّٰ كَلَمْحِ الْبَصَرِ أَوْ هُوَ أَقْرَبُ إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ اللّٰهُ أَخْرَجَكُمْ مِنْ بُطُونِ أُمَّهٰاتِكُمْ لاٰ تَعْلَمُونَ شَيْئاً وَ جَعَلَ لَكُمُ السَّمْعَ وَ الْأَبْصٰارَ وَ الْأَفْئِدَةَ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("أَ لَمْ يَرَوْا إِلَى الطَّيْرِ مُسَخَّرٰاتٍ فِي جَوِّ السَّمٰاءِ مٰا يُمْسِكُهُنَّ إِلاَّ اللّٰهُ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِقَوْمٍ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ اللّٰهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَناً وَ جَعَلَ لَكُمْ مِنْ جُلُودِ الْأَنْعٰامِ بُيُوتاً تَسْتَخِفُّونَهٰا يَوْمَ ظَعْنِكُمْ وَ يَوْمَ إِقٰامَتِكُمْ وَ مِنْ أَصْوٰافِهٰا وَ أَوْبٰارِهٰا وَ أَشْعٰارِهٰا أَثٰاثاً وَ مَتٰاعاً إِلىٰ حِينٍ");
        sor.Ayaat.Add("وَ اللّٰهُ جَعَلَ لَكُمْ مِمّٰا خَلَقَ ظِلاٰلاً وَ جَعَلَ لَكُمْ مِنَ الْجِبٰالِ أَكْنٰاناً وَ جَعَلَ لَكُمْ سَرٰابِيلَ تَقِيكُمُ الْحَرَّ وَ سَرٰابِيلَ تَقِيكُمْ بَأْسَكُمْ كَذٰلِكَ يُتِمُّ نِعْمَتَهُ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُونَ");
        sor.Ayaat.Add("فَإِنْ تَوَلَّوْا فَإِنَّمٰا عَلَيْكَ الْبَلاٰغُ الْمُبِينُ");
        sor.Ayaat.Add("يَعْرِفُونَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُونَهٰا وَ أَكْثَرُهُمُ الْكٰافِرُونَ");
        sor.Ayaat.Add("وَ يَوْمَ نَبْعَثُ مِنْ كُلِّ أُمَّةٍ شَهِيداً ثُمَّ لاٰ يُؤْذَنُ لِلَّذِينَ كَفَرُوا وَ لاٰ هُمْ يُسْتَعْتَبُونَ");
        sor.Ayaat.Add("وَ إِذٰا رَأَى الَّذِينَ ظَلَمُوا الْعَذٰابَ فَلاٰ يُخَفَّفُ عَنْهُمْ وَ لاٰ هُمْ يُنْظَرُونَ");
        sor.Ayaat.Add("وَ إِذٰا رَأَى الَّذِينَ أَشْرَكُوا شُرَكٰاءَهُمْ قٰالُوا رَبَّنٰا هٰؤُلاٰءِ شُرَكٰاؤُنَا الَّذِينَ كُنّٰا نَدْعُوا مِنْ دُونِكَ فَأَلْقَوْا إِلَيْهِمُ الْقَوْلَ إِنَّكُمْ لَكٰاذِبُونَ");
        sor.Ayaat.Add("وَ أَلْقَوْا إِلَى اللّٰهِ يَوْمَئِذٍ السَّلَمَ وَ ضَلَّ عَنْهُمْ مٰا كٰانُوا يَفْتَرُونَ");
        sor.Ayaat.Add("الَّذِينَ كَفَرُوا وَ صَدُّوا عَنْ سَبِيلِ اللّٰهِ زِدْنٰاهُمْ عَذٰاباً فَوْقَ الْعَذٰابِ بِمٰا كٰانُوا يُفْسِدُونَ");
        sor.Ayaat.Add("وَ يَوْمَ نَبْعَثُ فِي كُلِّ أُمَّةٍ شَهِيداً عَلَيْهِمْ مِنْ أَنْفُسِهِمْ وَ جِئْنٰا بِكَ شَهِيداً عَلىٰ هٰؤُلاٰءِ وَ نَزَّلْنٰا عَلَيْكَ الْكِتٰابَ تِبْيٰاناً لِكُلِّ شَيْءٍ وَ هُدىً وَ رَحْمَةً وَ بُشْرىٰ لِلْمُسْلِمِينَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَ الْإِحْسٰانِ وَ إِيتٰاءِ ذِي الْقُرْبىٰ وَ يَنْهىٰ عَنِ الْفَحْشٰاءِ وَ الْمُنْكَرِ وَ الْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُونَ");
        sor.Ayaat.Add("وَ أَوْفُوا بِعَهْدِ اللّٰهِ إِذٰا عٰاهَدْتُمْ وَ لاٰ تَنْقُضُوا الْأَيْمٰانَ بَعْدَ تَوْكِيدِهٰا وَ قَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَفِيلاً إِنَّ اللّٰهَ يَعْلَمُ مٰا تَفْعَلُونَ");
        sor.Ayaat.Add("وَ لاٰ تَكُونُوا كَالَّتِي نَقَضَتْ غَزْلَهٰا مِنْ بَعْدِ قُوَّةٍ أَنْكٰاثاً تَتَّخِذُونَ أَيْمٰانَكُمْ دَخَلاً بَيْنَكُمْ أَنْ تَكُونَ أُمَّةٌ هِيَ أَرْبىٰ مِنْ أُمَّةٍ إِنَّمٰا يَبْلُوكُمُ اللّٰهُ بِهِ وَ لَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰامَةِ مٰا كُنْتُمْ فِيهِ تَخْتَلِفُونَ");
        sor.Ayaat.Add("وَ لَوْ شٰاءَ اللّٰهُ لَجَعَلَكُمْ أُمَّةً وٰاحِدَةً وَ لٰكِنْ يُضِلُّ مَنْ يَشٰاءُ وَ يَهْدِي مَنْ يَشٰاءُ وَ لَتُسْئَلُنَّ عَمّٰا كُنْتُمْ تَعْمَلُونَ");
        sor.Ayaat.Add("وَ لاٰ تَتَّخِذُوا أَيْمٰانَكُمْ دَخَلاً بَيْنَكُمْ فَتَزِلَّ قَدَمٌ بَعْدَ ثُبُوتِهٰا وَ تَذُوقُوا السُّوءَ بِمٰا صَدَدْتُمْ عَنْ سَبِيلِ اللّٰهِ وَ لَكُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("وَ لاٰ تَشْتَرُوا بِعَهْدِ اللّٰهِ ثَمَناً قَلِيلاً إِنَّمٰا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("مٰا عِنْدَكُمْ يَنْفَدُ وَ مٰا عِنْدَ اللّٰهِ بٰاقٍ وَ لَنَجْزِيَنَّ الَّذِينَ صَبَرُوا أَجْرَهُمْ بِأَحْسَنِ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("مَنْ عَمِلَ صٰالِحاً مِنْ ذَكَرٍ أَوْ أُنْثىٰ وَ هُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهُ حَيٰاةً طَيِّبَةً وَ لَنَجْزِيَنَّهُمْ أَجْرَهُمْ بِأَحْسَنِ مٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("فَإِذٰا قَرَأْتَ الْقُرْآنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطٰانِ الرَّجِيمِ");
        sor.Ayaat.Add("إِنَّهُ لَيْسَ لَهُ سُلْطٰانٌ عَلَى الَّذِينَ آمَنُوا وَ عَلىٰ رَبِّهِمْ يَتَوَكَّلُونَ");
        sor.Ayaat.Add("إِنَّمٰا سُلْطٰانُهُ عَلَى الَّذِينَ يَتَوَلَّوْنَهُ وَ الَّذِينَ هُمْ بِهِ مُشْرِكُونَ");
        sor.Ayaat.Add("وَ إِذٰا بَدَّلْنٰا آيَةً مَكٰانَ آيَةٍ وَ اللّٰهُ أَعْلَمُ بِمٰا يُنَزِّلُ قٰالُوا إِنَّمٰا أَنْتَ مُفْتَرٍ بَلْ أَكْثَرُهُمْ لاٰ يَعْلَمُونَ");
        sor.Ayaat.Add("قُلْ نَزَّلَهُ رُوحُ الْقُدُسِ مِنْ رَبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِينَ آمَنُوا وَ هُدىً وَ بُشْرىٰ لِلْمُسْلِمِينَ");
        sor.Ayaat.Add("وَ لَقَدْ نَعْلَمُ أَنَّهُمْ يَقُولُونَ إِنَّمٰا يُعَلِّمُهُ بَشَرٌ لِسٰانُ الَّذِي يُلْحِدُونَ إِلَيْهِ أَعْجَمِيٌّ وَ هٰذٰا لِسٰانٌ عَرَبِيٌّ مُبِينٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ لاٰ يُؤْمِنُونَ بِآيٰاتِ اللّٰهِ لاٰ يَهْدِيهِمُ اللّٰهُ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("إِنَّمٰا يَفْتَرِي الْكَذِبَ الَّذِينَ لاٰ يُؤْمِنُونَ بِآيٰاتِ اللّٰهِ وَ أُولٰئِكَ هُمُ الْكٰاذِبُونَ");
        sor.Ayaat.Add("مَنْ كَفَرَ بِاللّٰهِ مِنْ بَعْدِ إِيمٰانِهِ إِلاّٰ مَنْ أُكْرِهَ وَ قَلْبُهُ مُطْمَئِنٌّ بِالْإِيمٰانِ وَ لٰكِنْ مَنْ شَرَحَ بِالْكُفْرِ صَدْراً فَعَلَيْهِمْ غَضَبٌ مِنَ اللّٰهِ وَ لَهُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّهُمُ اسْتَحَبُّوا الْحَيٰاةَ الدُّنْيٰا عَلَى الْآخِرَةِ وَ أَنَّ اللّٰهَ لاٰ يَهْدِي الْقَوْمَ الْكٰافِرِينَ");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ طَبَعَ اللّٰهُ عَلىٰ قُلُوبِهِمْ وَ سَمْعِهِمْ وَ أَبْصٰارِهِمْ وَ أُولٰئِكَ هُمُ الْغٰافِلُونَ");
        sor.Ayaat.Add("لاٰ جَرَمَ أَنَّهُمْ فِي الْآخِرَةِ هُمُ الْخٰاسِرُونَ");
        sor.Ayaat.Add("ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ هٰاجَرُوا مِنْ بَعْدِ مٰا فُتِنُوا ثُمَّ جٰاهَدُوا وَ صَبَرُوا إِنَّ رَبَّكَ مِنْ بَعْدِهٰا لَغَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("يَوْمَ تَأْتِي كُلُّ نَفْسٍ تُجٰادِلُ عَنْ نَفْسِهٰا وَ تُوَفّٰى كُلُّ نَفْسٍ مٰا عَمِلَتْ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("وَ ضَرَبَ اللّٰهُ مَثَلاً قَرْيَةً كٰانَتْ آمِنَةً مُطْمَئِنَّةً يَأْتِيهٰا رِزْقُهٰا رَغَداً مِنْ كُلِّ مَكٰانٍ فَكَفَرَتْ بِأَنْعُمِ اللّٰهِ فَأَذٰاقَهَا اللّٰهُ لِبٰاسَ الْجُوعِ وَ الْخَوْفِ بِمٰا كٰانُوا يَصْنَعُونَ");
        sor.Ayaat.Add("وَ لَقَدْ جٰاءَهُمْ رَسُولٌ مِنْهُمْ فَكَذَّبُوهُ فَأَخَذَهُمُ الْعَذٰابُ وَ هُمْ ظٰالِمُونَ");
        sor.Ayaat.Add("فَكُلُوا مِمّٰا رَزَقَكُمُ اللّٰهُ حَلاٰلاً طَيِّباً وَ اشْكُرُوا نِعْمَتَ اللّٰهِ إِنْ كُنْتُمْ إِيّٰاهُ تَعْبُدُونَ");
        sor.Ayaat.Add("إِنَّمٰا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَ الدَّمَ وَ لَحْمَ الْخِنْزِيرِ وَ مٰا أُهِلَّ لِغَيْرِ اللّٰهِ بِهِ فَمَنِ اضْطُرَّ غَيْرَ بٰاغٍ وَ لاٰ عٰادٍ فَإِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ لاٰ تَقُولُوا لِمٰا تَصِفُ أَلْسِنَتُكُمُ الْكَذِبَ هٰذٰا حَلاٰلٌ وَ هٰذٰا حَرٰامٌ لِتَفْتَرُوا عَلَى اللّٰهِ الْكَذِبَ إِنَّ الَّذِينَ يَفْتَرُونَ عَلَى اللّٰهِ الْكَذِبَ لاٰ يُفْلِحُونَ");
        sor.Ayaat.Add("مَتٰاعٌ قَلِيلٌ وَ لَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("وَ عَلَى الَّذِينَ هٰادُوا حَرَّمْنٰا مٰا قَصَصْنٰا عَلَيْكَ مِنْ قَبْلُ وَ مٰا ظَلَمْنٰاهُمْ وَ لٰكِنْ كٰانُوا أَنْفُسَهُمْ يَظْلِمُونَ");
        sor.Ayaat.Add("ثُمَّ إِنَّ رَبَّكَ لِلَّذِينَ عَمِلُوا السُّوءَ بِجَهٰالَةٍ ثُمَّ تٰابُوا مِنْ بَعْدِ ذٰلِكَ وَ أَصْلَحُوا إِنَّ رَبَّكَ مِنْ بَعْدِهٰا لَغَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("إِنَّ إِبْرٰاهِيمَ كٰانَ أُمَّةً قٰانِتاً لِلّٰهِ حَنِيفاً وَ لَمْ يَكُ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("شٰاكِراً لِأَنْعُمِهِ اجْتَبٰاهُ وَ هَدٰاهُ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ آتَيْنٰاهُ فِي الدُّنْيٰا حَسَنَةً وَ إِنَّهُ فِي الْآخِرَةِ لَمِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("ثُمَّ أَوْحَيْنٰا إِلَيْكَ أَنِ اتَّبِعْ مِلَّةَ إِبْرٰاهِيمَ حَنِيفاً وَ مٰا كٰانَ مِنَ الْمُشْرِكِينَ");
        sor.Ayaat.Add("إِنَّمٰا جُعِلَ السَّبْتُ عَلَى الَّذِينَ اخْتَلَفُوا فِيهِ وَ إِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰامَةِ فِيمٰا كٰانُوا فِيهِ يَخْتَلِفُونَ");
        sor.Ayaat.Add("ادْعُ إِلىٰ سَبِيلِ رَبِّكَ بِالْحِكْمَةِ وَ الْمَوْعِظَةِ الْحَسَنَةِ وَ جٰادِلْهُمْ بِالَّتِي هِيَ أَحْسَنُ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيلِهِ وَ هُوَ أَعْلَمُ بِالْمُهْتَدِينَ");
        sor.Ayaat.Add("وَ إِنْ عٰاقَبْتُمْ فَعٰاقِبُوا بِمِثْلِ مٰا عُوقِبْتُمْ بِهِ وَ لَئِنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِلصّٰابِرِينَ");
        sor.Ayaat.Add("وَ اصْبِرْ وَ مٰا صَبْرُكَ إِلاّٰ بِاللّٰهِ وَ لاٰ تَحْزَنْ عَلَيْهِمْ وَ لاٰ تَكُ فِي ضَيْقٍ مِمّٰا يَمْكُرُونَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ مَعَ الَّذِينَ اتَّقَوْا وَ الَّذِينَ هُمْ مُحْسِنُونَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١٧ - الإسراء";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ سُبْحٰانَ الَّذِي أَسْرىٰ بِعَبْدِهِ لَيْلاً مِنَ الْمَسْجِدِ الْحَرٰامِ إِلَى الْمَسْجِدِ الْأَقْصَى الَّذِي بٰارَكْنٰا حَوْلَهُ لِنُرِيَهُ مِنْ آيٰاتِنٰا إِنَّهُ هُوَ السَّمِيعُ الْبَصِيرُ");
        sor.Ayaat.Add("وَ آتَيْنٰا مُوسَى الْكِتٰابَ وَ جَعَلْنٰاهُ هُدىً لِبَنِي إِسْرٰائِيلَ أَلاّٰ تَتَّخِذُوا مِنْ دُونِي وَكِيلاً");
        sor.Ayaat.Add("ذُرِّيَّةَ مَنْ حَمَلْنٰا مَعَ نُوحٍ إِنَّهُ كٰانَ عَبْداً شَكُوراً");
        sor.Ayaat.Add("وَ قَضَيْنٰا إِلىٰ بَنِي إِسْرٰائِيلَ فِي الْكِتٰابِ لَتُفْسِدُنَّ فِي الْأَرْضِ مَرَّتَيْنِ وَ لَتَعْلُنَّ عُلُوًّا كَبِيراً");
        sor.Ayaat.Add("فَإِذٰا جٰاءَ وَعْدُ أُولاٰهُمٰا بَعَثْنٰا عَلَيْكُمْ عِبٰاداً لَنٰا أُولِي بَأْسٍ شَدِيدٍ فَجٰاسُوا خِلاٰلَ الدِّيٰارِ وَ كٰانَ وَعْداً مَفْعُولاً");
        sor.Ayaat.Add("ثُمَّ رَدَدْنٰا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَ أَمْدَدْنٰاكُمْ بِأَمْوٰالٍ وَ بَنِينَ وَ جَعَلْنٰاكُمْ أَكْثَرَ نَفِيراً");
        sor.Ayaat.Add("إِنْ أَحْسَنْتُمْ أَحْسَنْتُمْ لِأَنْفُسِكُمْ وَ إِنْ أَسَأْتُمْ فَلَهٰا فَإِذٰا جٰاءَ وَعْدُ الْآخِرَةِ لِيَسُوؤُا وُجُوهَكُمْ وَ لِيَدْخُلُوا الْمَسْجِدَ كَمٰا دَخَلُوهُ أَوَّلَ مَرَّةٍ وَ لِيُتَبِّرُوا مٰا عَلَوْا تَتْبِيراً");
        sor.Ayaat.Add("عَسىٰ رَبُّكُمْ أَنْ يَرْحَمَكُمْ وَ إِنْ عُدْتُمْ عُدْنٰا وَ جَعَلْنٰا جَهَنَّمَ لِلْكٰافِرِينَ حَصِيراً");
        sor.Ayaat.Add("إِنَّ هٰذَا الْقُرْآنَ يَهْدِي لِلَّتِي هِيَ أَقْوَمُ وَ يُبَشِّرُ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصّٰالِحٰاتِ أَنَّ لَهُمْ أَجْراً كَبِيراً");
        sor.Ayaat.Add("وَ أَنَّ الَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ أَعْتَدْنٰا لَهُمْ عَذٰاباً أَلِيماً");
        sor.Ayaat.Add("وَ يَدْعُ الْإِنْسٰانُ بِالشَّرِّ دُعٰاءَهُ بِالْخَيْرِ وَ كٰانَ الْإِنْسٰانُ عَجُولاً");
        sor.Ayaat.Add("وَ جَعَلْنَا اللَّيْلَ وَ النَّهٰارَ آيَتَيْنِ فَمَحَوْنٰا آيَةَ اللَّيْلِ وَ جَعَلْنٰا آيَةَ النَّهٰارِ مُبْصِرَةً لِتَبْتَغُوا فَضْلاً مِنْ رَبِّكُمْ وَ لِتَعْلَمُوا عَدَدَ السِّنِينَ وَ الْحِسٰابَ وَ كُلَّ شَيْءٍ فَصَّلْنٰاهُ تَفْصِيلاً");
        sor.Ayaat.Add("وَ كُلَّ إِنسٰانٍ أَلْزَمْنٰاهُ طٰائِرَهُ فِي عُنُقِهِ وَ نُخْرِجُ لَهُ يَوْمَ الْقِيٰامَةِ كِتٰاباً يَلْقٰاهُ مَنْشُوراً");
        sor.Ayaat.Add("اقْرَأْ كِتٰابَكَ كَفىٰ بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيباً");
        sor.Ayaat.Add("مَنِ اهْتَدىٰ فَإِنَّمٰا يَهْتَدِي لِنَفْسِهِ وَ مَنْ ضَلَّ فَإِنَّمٰا يَضِلُّ عَلَيْهٰا وَ لاٰ تَزِرُ وٰازِرَةٌ وِزْرَ أُخْرىٰ وَ مٰا كُنّٰا مُعَذِّبِينَ حَتّٰى نَبْعَثَ رَسُولاً");
        sor.Ayaat.Add("وَ إِذٰا أَرَدْنٰا أَنْ نُهْلِكَ قَرْيَةً أَمَرْنٰا مُتْرَفِيهٰا فَفَسَقُوا فِيهٰا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنٰاهٰا تَدْمِيراً");
        sor.Ayaat.Add("وَ كَمْ أَهْلَكْنٰا مِنَ الْقُرُونِ مِنْ بَعْدِ نُوحٍ وَ كَفىٰ بِرَبِّكَ بِذُنُوبِ عِبٰادِهِ خَبِيراً بَصِيراً");
        sor.Ayaat.Add("مَنْ كٰانَ يُرِيدُ الْعٰاجِلَةَ عَجَّلْنٰا لَهُ فِيهٰا مٰا نَشٰاءُ لِمَنْ نُرِيدُ ثُمَّ جَعَلْنٰا لَهُ جَهَنَّمَ يَصْلاٰهٰا مَذْمُوماً مَدْحُوراً");
        sor.Ayaat.Add("وَ مَنْ أَرٰادَ الْآخِرَةَ وَ سَعىٰ لَهٰا سَعْيَهٰا وَ هُوَ مُؤْمِنٌ فَأُولٰئِكَ كٰانَ سَعْيُهُمْ مَشْكُوراً");
        sor.Ayaat.Add("كُلاًّ نُمِدُّ هٰؤُلاٰءِ وَ هَؤُلاٰءِ مِنْ عَطٰاءِ رَبِّكَ وَ مٰا كٰانَ عَطٰاءُ رَبِّكَ مَحْظُوراً");
        sor.Ayaat.Add("انْظُرْ كَيْفَ فَضَّلْنٰا بَعْضَهُمْ عَلىٰ بَعْضٍ وَ لَلْآخِرَةُ أَكْبَرُ دَرَجٰاتٍ وَ أَكْبَرُ تَفْضِيلاً");
        sor.Ayaat.Add("لاٰ تَجْعَلْ مَعَ اللّٰهِ إِلٰهاً آخَرَ فَتَقْعُدَ مَذْمُوماً مَخْذُولاً");
        sor.Ayaat.Add("وَ قَضىٰ رَبُّكَ أَلاّٰ تَعْبُدُوا إِلاّٰ إِيّٰاهُ وَ بِالْوٰالِدَيْنِ إِحْسٰاناً إِمّٰا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ أَحَدُهُمٰا أَوْ كِلاٰهُمٰا فَلاٰ تَقُلْ لَهُمٰا أُفٍّ وَ لاٰ تَنْهَرْهُمٰا وَ قُلْ لَهُمٰا قَوْلاً كَرِيماً");
        sor.Ayaat.Add("وَ اخْفِضْ لَهُمٰا جَنٰاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَ قُلْ رَبِّ ارْحَمْهُمٰا كَمٰا رَبَّيٰانِي صَغِيراً");
        sor.Ayaat.Add("رَبُّكُمْ أَعْلَمُ بِمٰا فِي نُفُوسِكُمْ إِنْ تَكُونُوا صٰالِحِينَ فَإِنَّهُ كٰانَ لِلْأَوّٰابِينَ غَفُوراً");
        sor.Ayaat.Add("وَ آتِ ذَا الْقُرْبىٰ حَقَّهُ وَ الْمِسْكِينَ وَ ابْنَ السَّبِيلِ وَ لاٰ تُبَذِّرْ تَبْذِيراً");
        sor.Ayaat.Add("إِنَّ الْمُبَذِّرِينَ كٰانُوا إِخْوٰانَ الشَّيٰاطِينِ وَ كٰانَ الشَّيْطٰانُ لِرَبِّهِ كَفُوراً");
        sor.Ayaat.Add("وَ إِمّٰا تُعْرِضَنَّ عَنْهُمُ ابْتِغٰاءَ رَحْمَةٍ مِنْ رَبِّكَ تَرْجُوهٰا فَقُلْ لَهُمْ قَوْلاً مَيْسُوراً");
        sor.Ayaat.Add("وَ لاٰ تَجْعَلْ يَدَكَ مَغْلُولَةً إِلىٰ عُنُقِكَ وَ لاٰ تَبْسُطْهٰا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوماً مَحْسُوراً");
        sor.Ayaat.Add("إِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَشٰاءُ وَ يَقْدِرُ إِنَّهُ كٰانَ بِعِبٰادِهِ خَبِيراً بَصِيراً");
        sor.Ayaat.Add("وَ لاٰ تَقْتُلُوا أَوْلاٰدَكُمْ خَشْيَةَ إِمْلاٰقٍ نَحْنُ نَرْزُقُهُمْ وَ إِيّٰاكُمْ إِنَّ قَتْلَهُمْ كٰانَ خِطْأً كَبِيراً");
        sor.Ayaat.Add("وَ لاٰ تَقْرَبُوا الزِّنىٰ إِنَّهُ كٰانَ فٰاحِشَةً وَ سٰاءَ سَبِيلاً");
        sor.Ayaat.Add("وَ لاٰ تَقْتُلُوا النَّفْسَ الَّتِي حَرَّمَ اللّٰهُ إِلاّٰ بِالْحَقِّ وَ مَنْ قُتِلَ مَظْلُوماً فَقَدْ جَعَلْنٰا لِوَلِيِّهِ سُلْطٰاناً فَلاٰ يُسْرِفْ فِي الْقَتْلِ إِنَّهُ كٰانَ مَنْصُوراً");
        sor.Ayaat.Add("وَ لاٰ تَقْرَبُوا مٰالَ الْيَتِيمِ إِلاّٰ بِالَّتِي هِيَ أَحْسَنُ حَتّٰى يَبْلُغَ أَشُدَّهُ وَ أَوْفُوا بِالْعَهْدِ إِنَّ الْعَهْدَ كٰانَ مَسْؤُلاً");
        sor.Ayaat.Add("وَ أَوْفُوا الْكَيْلَ إِذٰا كِلْتُمْ وَ زِنُوا بِالْقِسْطٰاسِ الْمُسْتَقِيمِ ذٰلِكَ خَيْرٌ وَ أَحْسَنُ تَأْوِيلاً");
        sor.Ayaat.Add("وَ لاٰ تَقْفُ مٰا لَيْسَ لَكَ بِهِ عِلْمٌ إِنَّ السَّمْعَ وَ الْبَصَرَ وَ الْفُؤٰادَ كُلُّ أُولٰئِكَ كٰانَ عَنْهُ مَسْؤُلاً");
        sor.Ayaat.Add("وَ لاٰ تَمْشِ فِي الْأَرْضِ مَرَحاً إِنَّكَ لَنْ تَخْرِقَ الْأَرْضَ وَ لَنْ تَبْلُغَ الْجِبٰالَ طُولاً");
        sor.Ayaat.Add("كُلُّ ذٰلِكَ كٰانَ سَيِّئُهُ عِنْدَ رَبِّكَ مَكْرُوهاً");
        sor.Ayaat.Add("ذٰلِكَ مِمّٰا أَوْحىٰ إِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِ وَ لاٰ تَجْعَلْ مَعَ اللّٰهِ إِلٰهاً آخَرَ فَتُلْقىٰ فِي جَهَنَّمَ مَلُوماً مَدْحُوراً");
        sor.Ayaat.Add("أَ فَأَصْفٰاكُمْ رَبُّكُمْ بِالْبَنِينَ وَ اتَّخَذَ مِنَ الْمَلاٰئِكَةِ إِنٰاثاً إِنَّكُمْ لَتَقُولُونَ قَوْلاً عَظِيماً");
        sor.Ayaat.Add("وَ لَقَدْ صَرَّفْنٰا فِي هٰذَا الْقُرْآنِ لِيَذَّكَّرُوا وَ مٰا يَزِيدُهُمْ إِلاّٰ نُفُوراً");
        sor.Ayaat.Add("قُلْ لَوْ كٰانَ مَعَهُ آلِهَةٌ كَمٰا يَقُولُونَ إِذاً لاَبْتَغَوْا إِلىٰ ذِي الْعَرْشِ سَبِيلاً");
        sor.Ayaat.Add("سُبْحٰانَهُ وَ تَعٰالىٰ عَمّٰا يَقُولُونَ عُلُوًّا كَبِيراً");
        sor.Ayaat.Add("تُسَبِّحُ لَهُ السَّمٰاوٰاتُ السَّبْعُ وَ الْأَرْضُ وَ مَنْ فِيهِنَّ وَ إِنْ مِنْ شَيْءٍ إِلاّٰ يُسَبِّحُ بِحَمْدِهِ وَ لٰكِنْ لاٰ تَفْقَهُونَ تَسْبِيحَهُمْ إِنَّهُ كٰانَ حَلِيماً غَفُوراً");
        sor.Ayaat.Add("وَ إِذٰا قَرَأْتَ الْقُرْآنَ جَعَلْنٰا بَيْنَكَ وَ بَيْنَ الَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ حِجٰاباً مَسْتُوراً");
        sor.Ayaat.Add("وَ جَعَلْنٰا عَلىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَ فِي آذٰانِهِمْ وَقْراً وَ إِذٰا ذَكَرْتَ رَبَّكَ فِي الْقُرْآنِ وَحْدَهُ وَلَّوْا عَلىٰ أَدْبٰارِهِمْ نُفُوراً");
        sor.Ayaat.Add("نَحْنُ أَعْلَمُ بِمٰا يَسْتَمِعُونَ بِهِ إِذْ يَسْتَمِعُونَ إِلَيْكَ وَ إِذْ هُمْ نَجْوىٰ إِذْ يَقُولُ الظّٰالِمُونَ إِنْ تَتَّبِعُونَ إِلاّٰ رَجُلاً مَسْحُوراً");
        sor.Ayaat.Add("انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثٰالَ فَضَلُّوا فَلاٰ يَسْتَطِيعُونَ سَبِيلاً");
        sor.Ayaat.Add("وَ قٰالُوا أَ إِذٰا كُنّٰا عِظٰاماً وَ رُفٰاتاً أَ إِنّٰا لَمَبْعُوثُونَ خَلْقاً جَدِيداً");
        sor.Ayaat.Add("قُلْ كُونُوا حِجٰارَةً أَوْ حَدِيداً");
        sor.Ayaat.Add("أَوْ خَلْقاً مِمّٰا يَكْبُرُ فِي صُدُورِكُمْ فَسَيَقُولُونَ مَنْ يُعِيدُنٰا قُلِ الَّذِي فَطَرَكُمْ أَوَّلَ مَرَّةٍ فَسَيُنْغِضُونَ إِلَيْكَ رُؤُسَهُمْ وَ يَقُولُونَ مَتىٰ هُوَ قُلْ عَسىٰ أَنْ يَكُونَ قَرِيباً");
        sor.Ayaat.Add("يَوْمَ يَدْعُوكُمْ فَتَسْتَجِيبُونَ بِحَمْدِهِ وَ تَظُنُّونَ إِنْ لَبِثْتُمْ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("وَ قُلْ لِعِبٰادِي يَقُولُوا الَّتِي هِيَ أَحْسَنُ إِنَّ الشَّيْطٰانَ يَنْزَغُ بَيْنَهُمْ إِنَّ الشَّيْطٰانَ كٰانَ لِلْإِنْسٰانِ عَدُوًّا مُبِيناً");
        sor.Ayaat.Add("رَبُّكُمْ أَعْلَمُ بِكُمْ إِنْ يَشَأْ يَرْحَمْكُمْ أَوْ إِنْ يَشَأْ يُعَذِّبْكُمْ وَ مٰا أَرْسَلْنٰاكَ عَلَيْهِمْ وَكِيلاً");
        sor.Ayaat.Add("وَ رَبُّكَ أَعْلَمُ بِمَنْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ وَ لَقَدْ فَضَّلْنٰا بَعْضَ النَّبِيِّينَ عَلىٰ بَعْضٍ وَ آتَيْنٰا دٰاوُدَ زَبُوراً");
        sor.Ayaat.Add("قُلِ ادْعُوا الَّذِينَ زَعَمْتُمْ مِنْ دُونِهِ فَلاٰ يَمْلِكُونَ كَشْفَ الضُّرِّ عَنْكُمْ وَ لاٰ تَحْوِيلاً");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ يَدْعُونَ يَبْتَغُونَ إِلىٰ رَبِّهِمُ الْوَسِيلَةَ أَيُّهُمْ أَقْرَبُ وَ يَرْجُونَ رَحْمَتَهُ وَ يَخٰافُونَ عَذٰابَهُ إِنَّ عَذٰابَ رَبِّكَ كٰانَ مَحْذُوراً");
        sor.Ayaat.Add("وَ إِنْ مِنْ قَرْيَةٍ إِلاّٰ نَحْنُ مُهْلِكُوهٰا قَبْلَ يَوْمِ الْقِيٰامَةِ أَوْ مُعَذِّبُوهٰا عَذٰاباً شَدِيداً كٰانَ ذٰلِكَ فِي الْكِتٰابِ مَسْطُوراً");
        sor.Ayaat.Add("وَ مٰا مَنَعَنٰا أَنْ نُرْسِلَ بِالْآيٰاتِ إِلاّٰ أَنْ كَذَّبَ بِهَا الْأَوَّلُونَ وَ آتَيْنٰا ثَمُودَ النّٰاقَةَ مُبْصِرَةً فَظَلَمُوا بِهٰا وَ مٰا نُرْسِلُ بِالْآيٰاتِ إِلاّٰ تَخْوِيفاً");
        sor.Ayaat.Add("وَ إِذْ قُلْنٰا لَكَ إِنَّ رَبَّكَ أَحٰاطَ بِالنّٰاسِ وَ مٰا جَعَلْنَا الرُّؤْيَا الَّتِي أَرَيْنٰاكَ إِلاّٰ فِتْنَةً لِلنّٰاسِ وَ الشَّجَرَةَ الْمَلْعُونَةَ فِي الْقُرْآنِ وَ نُخَوِّفُهُمْ فَمٰا يَزِيدُهُمْ إِلاّٰ طُغْيٰاناً كَبِيراً");
        sor.Ayaat.Add("وَ إِذْ قُلْنٰا لِلْمَلاٰئِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاّٰ إِبْلِيسَ قٰالَ أَ أَسْجُدُ لِمَنْ خَلَقْتَ طِيناً");
        sor.Ayaat.Add("قٰالَ أَ رَأَيْتَكَ هٰذَا الَّذِي كَرَّمْتَ عَلَيَّ لَئِنْ أَخَّرْتَنِ إِلىٰ يَوْمِ الْقِيٰامَةِ لَأَحْتَنِكَنَّ ذُرِّيَّتَهُ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("قٰالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَإِنَّ جَهَنَّمَ جَزٰاؤُكُمْ جَزٰاءً مَوْفُوراً");
        sor.Ayaat.Add("وَ اسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَ أَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَ رَجِلِكَ وَ شٰارِكْهُمْ فِي الْأَمْوٰالِ وَ الْأَوْلاٰدِ وَ عِدْهُمْ وَ مٰا يَعِدُهُمُ الشَّيْطٰانُ إِلاّٰ غُرُوراً");
        sor.Ayaat.Add("إِنَّ عِبٰادِي لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰانٌ وَ كَفىٰ بِرَبِّكَ وَكِيلاً");
        sor.Ayaat.Add("رَبُّكُمُ الَّذِي يُزْجِي لَكُمُ الْفُلْكَ فِي الْبَحْرِ لِتَبْتَغُوا مِنْ فَضْلِهِ إِنَّهُ كٰانَ بِكُمْ رَحِيماً");
        sor.Ayaat.Add("وَ إِذٰا مَسَّكُمُ الضُّرُّ فِي الْبَحْرِ ضَلَّ مَنْ تَدْعُونَ إِلاّٰ إِيّٰاهُ فَلَمّٰا نَجّٰاكُمْ إِلَى الْبَرِّ أَعْرَضْتُمْ وَ كٰانَ الْإِنْسٰانُ كَفُوراً");
        sor.Ayaat.Add("أَ فَأَمِنْتُمْ أَنْ يَخْسِفَ بِكُمْ جٰانِبَ الْبَرِّ أَوْ يُرْسِلَ عَلَيْكُمْ حٰاصِباً ثُمَّ لاٰ تَجِدُوا لَكُمْ وَكِيلاً");
        sor.Ayaat.Add("أَمْ أَمِنْتُمْ أَنْ يُعِيدَكُمْ فِيهِ تٰارَةً أُخْرىٰ فَيُرْسِلَ عَلَيْكُمْ قٰاصِفاً مِنَ الرِّيحِ فَيُغْرِقَكُمْ بِمٰا كَفَرْتُمْ ثُمَّ لاٰ تَجِدُوا لَكُمْ عَلَيْنٰا بِهِ تَبِيعاً");
        sor.Ayaat.Add("وَ لَقَدْ كَرَّمْنٰا بَنِي آدَمَ وَ حَمَلْنٰاهُمْ فِي الْبَرِّ وَ الْبَحْرِ وَ رَزَقْنٰاهُمْ مِنَ الطَّيِّبٰاتِ وَ فَضَّلْنٰاهُمْ عَلىٰ كَثِيرٍ مِمَّنْ خَلَقْنٰا تَفْضِيلاً");
        sor.Ayaat.Add("يَوْمَ نَدْعُوا كُلَّ أُنٰاسٍ بِإِمٰامِهِمْ فَمَنْ أُوتِيَ كِتٰابَهُ بِيَمِينِهِ فَأُولٰئِكَ يَقْرَؤُنَ كِتٰابَهُمْ وَ لاٰ يُظْلَمُونَ فَتِيلاً");
        sor.Ayaat.Add("وَ مَنْ كٰانَ فِي هٰذِهِ أَعْمىٰ فَهُوَ فِي الْآخِرَةِ أَعْمىٰ وَ أَضَلُّ سَبِيلاً");
        sor.Ayaat.Add("وَ إِنْ كٰادُوا لَيَفْتِنُونَكَ عَنِ الَّذِي أَوْحَيْنٰا إِلَيْكَ لِتَفْتَرِيَ عَلَيْنٰا غَيْرَهُ وَ إِذاً لاَتَّخَذُوكَ خَلِيلاً");
        sor.Ayaat.Add("وَ لَوْ لاٰ أَنْ ثَبَّتْنٰاكَ لَقَدْ كِدْتَ تَرْكَنُ إِلَيْهِمْ شَيْئاً قَلِيلاً");
        sor.Ayaat.Add("إِذاً لَأَذَقْنٰاكَ ضِعْفَ الْحَيٰاةِ وَ ضِعْفَ الْمَمٰاتِ ثُمَّ لاٰ تَجِدُ لَكَ عَلَيْنٰا نَصِيراً");
        sor.Ayaat.Add("وَ إِنْ كٰادُوا لَيَسْتَفِزُّونَكَ مِنَ الْأَرْضِ لِيُخْرِجُوكَ مِنْهٰا وَ إِذاً لاٰ يَلْبَثُونَ خِلاٰفَكَ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("سُنَّةَ مَنْ قَدْ أَرْسَلْنٰا قَبْلَكَ مِنْ رُسُلِنٰا وَ لاٰ تَجِدُ لِسُنَّتِنٰا تَحْوِيلاً");
        sor.Ayaat.Add("أَقِمِ الصَّلاٰةَ لِدُلُوكِ الشَّمْسِ إِلىٰ غَسَقِ اللَّيْلِ وَ قُرْآنَ الْفَجْرِ إِنَّ قُرْآنَ الْفَجْرِ كٰانَ مَشْهُوداً");
        sor.Ayaat.Add("وَ مِنَ اللَّيْلِ فَتَهَجَّدْ بِهِ نٰافِلَةً لَكَ عَسىٰ أَنْ يَبْعَثَكَ رَبُّكَ مَقٰاماً مَحْمُوداً");
        sor.Ayaat.Add("وَ قُلْ رَبِّ أَدْخِلْنِي مُدْخَلَ صِدْقٍ وَ أَخْرِجْنِي مُخْرَجَ صِدْقٍ وَ اجْعَلْ لِي مِنْ لَدُنْكَ سُلْطٰاناً نَصِيراً");
        sor.Ayaat.Add("وَ قُلْ جٰاءَ الْحَقُّ وَ زَهَقَ الْبٰاطِلُ إِنَّ الْبٰاطِلَ كٰانَ زَهُوقاً");
        sor.Ayaat.Add("وَ نُنَزِّلُ مِنَ الْقُرْآنِ مٰا هُوَ شِفٰاءٌ وَ رَحْمَةٌ لِلْمُؤْمِنِينَ وَ لاٰ يَزِيدُ الظّٰالِمِينَ إِلاّٰ خَسٰاراً");
        sor.Ayaat.Add("وَ إِذٰا أَنْعَمْنٰا عَلَى الْإِنْسٰانِ أَعْرَضَ وَ نَأىٰ بِجٰانِبِهِ وَ إِذٰا مَسَّهُ الشَّرُّ كٰانَ يَؤُساً");
        sor.Ayaat.Add("قُلْ كُلٌّ يَعْمَلُ عَلىٰ شٰاكِلَتِهِ فَرَبُّكُمْ أَعْلَمُ بِمَنْ هُوَ أَهْدىٰ سَبِيلاً");
        sor.Ayaat.Add("وَ يَسْئَلُونَكَ عَنِ الرُّوحِ قُلِ الرُّوحُ مِنْ أَمْرِ رَبِّي وَ مٰا أُوتِيتُمْ مِنَ الْعِلْمِ إِلاّٰ قَلِيلاً");
        sor.Ayaat.Add("وَ لَئِنْ شِئْنٰا لَنَذْهَبَنَّ بِالَّذِي أَوْحَيْنٰا إِلَيْكَ ثُمَّ لاٰ تَجِدُ لَكَ بِهِ عَلَيْنٰا وَكِيلاً");
        sor.Ayaat.Add("إِلاّٰ رَحْمَةً مِنْ رَبِّكَ إِنَّ فَضْلَهُ كٰانَ عَلَيْكَ كَبِيراً");
        sor.Ayaat.Add("قُلْ لَئِنِ اجْتَمَعَتِ الْإِنْسُ وَ الْجِنُّ عَلىٰ أَنْ يَأْتُوا بِمِثْلِ هٰذَا الْقُرْآنِ لاٰ يَأْتُونَ بِمِثْلِهِ وَ لَوْ كٰانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيراً");
        sor.Ayaat.Add("وَ لَقَدْ صَرَّفْنٰا لِلنّٰاسِ فِي هٰذَا الْقُرْآنِ مِنْ كُلِّ مَثَلٍ فَأَبىٰ أَكْثَرُ النّٰاسِ إِلاّٰ كُفُوراً");
        sor.Ayaat.Add("وَ قٰالُوا لَنْ نُؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنٰا مِنَ الْأَرْضِ يَنْبُوعاً");
        sor.Ayaat.Add("أَوْ تَكُونَ لَكَ جَنَّةٌ مِنْ نَخِيلٍ وَ عِنَبٍ فَتُفَجِّرَ الْأَنْهٰارَ خِلاٰلَهٰا تَفْجِيراً");
        sor.Ayaat.Add("أَوْ تُسْقِطَ السَّمٰاءَ كَمٰا زَعَمْتَ عَلَيْنٰا كِسَفاً أَوْ تَأْتِيَ بِاللّٰهِ وَ الْمَلاٰئِكَةِ قَبِيلاً");
        sor.Ayaat.Add("أَوْ يَكُونَ لَكَ بَيْتٌ مِنْ زُخْرُفٍ أَوْ تَرْقىٰ فِي السَّمٰاءِ وَ لَنْ نُؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنٰا كِتٰاباً نَقْرَؤُهُ قُلْ سُبْحٰانَ رَبِّي هَلْ كُنْتُ إِلاّٰ بَشَراً رَسُولاً");
        sor.Ayaat.Add("وَ مٰا مَنَعَ النّٰاسَ أَنْ يُؤْمِنُوا إِذْ جٰاءَهُمُ الْهُدىٰ إِلاّٰ أَنْ قٰالُوا أَ بَعَثَ اللّٰهُ بَشَراً رَسُولاً");
        sor.Ayaat.Add("قُلْ لَوْ كٰانَ فِي الْأَرْضِ مَلاٰئِكَةٌ يَمْشُونَ مُطْمَئِنِّينَ لَنَزَّلْنٰا عَلَيْهِمْ مِنَ السَّمٰاءِ مَلَكاً رَسُولاً");
        sor.Ayaat.Add("قُلْ كَفىٰ بِاللّٰهِ شَهِيداً بَيْنِي وَ بَيْنَكُمْ إِنَّهُ كٰانَ بِعِبٰادِهِ خَبِيراً بَصِيراً");
        sor.Ayaat.Add("وَ مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِ وَ مَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُمْ أَوْلِيٰاءَ مِنْ دُونِهِ وَ نَحْشُرُهُمْ يَوْمَ الْقِيٰامَةِ عَلىٰ وُجُوهِهِمْ عُمْياً وَ بُكْماً وَ صُمًّا مَأْوٰاهُمْ جَهَنَّمُ كُلَّمٰا خَبَتْ زِدْنٰاهُمْ سَعِيراً");
        sor.Ayaat.Add("ذٰلِكَ جَزٰاؤُهُمْ بِأَنَّهُمْ كَفَرُوا بِآيٰاتِنٰا وَ قٰالُوا أَ إِذٰا كُنّٰا عِظٰاماً وَ رُفٰاتاً أَ إِنّٰا لَمَبْعُوثُونَ خَلْقاً جَدِيداً");
        sor.Ayaat.Add("أَ وَ لَمْ يَرَوْا أَنَّ اللّٰهَ الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ قٰادِرٌ عَلىٰ أَنْ يَخْلُقَ مِثْلَهُمْ وَ جَعَلَ لَهُمْ أَجَلاً لاٰ رَيْبَ فِيهِ فَأَبَى الظّٰالِمُونَ إِلاّٰ كُفُوراً");
        sor.Ayaat.Add("قُلْ لَوْ أَنْتُمْ تَمْلِكُونَ خَزٰائِنَ رَحْمَةِ رَبِّي إِذاً لَأَمْسَكْتُمْ خَشْيَةَ الْإِنْفٰاقِ وَ كٰانَ الْإِنْسٰانُ قَتُوراً");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا مُوسىٰ تِسْعَ آيٰاتٍ بَيِّنٰاتٍ فَسْئَلْ بَنِي إِسْرٰائِيلَ إِذْ جٰاءَهُمْ فَقٰالَ لَهُ فِرْعَوْنُ إِنِّي لَأَظُنُّكَ يٰا مُوسىٰ مَسْحُوراً");
        sor.Ayaat.Add("قٰالَ لَقَدْ عَلِمْتَ مٰا أَنْزَلَ هٰؤُلاٰءِ إِلاّٰ رَبُّ السَّمٰاوٰاتِ وَ الْأَرْضِ بَصٰائِرَ وَ إِنِّي لَأَظُنُّكَ يٰا فِرْعَوْنُ مَثْبُوراً");
        sor.Ayaat.Add("فَأَرٰادَ أَنْ يَسْتَفِزَّهُمْ مِنَ الْأَرْضِ فَأَغْرَقْنٰاهُ وَ مَنْ مَعَهُ جَمِيعاً");
        sor.Ayaat.Add("وَ قُلْنٰا مِنْ بَعْدِهِ لِبَنِي إِسْرٰائِيلَ اسْكُنُوا الْأَرْضَ فَإِذٰا جٰاءَ وَعْدُ الْآخِرَةِ جِئْنٰا بِكُمْ لَفِيفاً");
        sor.Ayaat.Add("وَ بِالْحَقِّ أَنْزَلْنٰاهُ وَ بِالْحَقِّ نَزَلَ وَ مٰا أَرْسَلْنٰاكَ إِلاّٰ مُبَشِّراً وَ نَذِيراً");
        sor.Ayaat.Add("وَ قُرْآناً فَرَقْنٰاهُ لِتَقْرَأَهُ عَلَى النّٰاسِ عَلىٰ مُكْثٍ وَ نَزَّلْنٰاهُ تَنْزِيلاً");
        sor.Ayaat.Add("قُلْ آمِنُوا بِهِ أَوْ لاٰ تُؤْمِنُوا إِنَّ الَّذِينَ أُوتُوا الْعِلْمَ مِنْ قَبْلِهِ إِذٰا يُتْلىٰ عَلَيْهِمْ يَخِرُّونَ لِلْأَذْقٰانِ سُجَّداً");
        sor.Ayaat.Add("وَ يَقُولُونَ سُبْحٰانَ رَبِّنٰا إِنْ كٰانَ وَعْدُ رَبِّنٰا لَمَفْعُولاً");
        sor.Ayaat.Add("وَ يَخِرُّونَ لِلْأَذْقٰانِ يَبْكُونَ وَ يَزِيدُهُمْ خُشُوعاً");
        sor.Ayaat.Add("قُلِ ادْعُوا اللّٰهَ أَوِ ادْعُوا الرَّحْمٰنَ أَيًّا مٰا تَدْعُوا فَلَهُ الْأَسْمٰاءُ الْحُسْنىٰ وَ لاٰ تَجْهَرْ بِصَلاٰتِكَ وَ لاٰ تُخٰافِتْ بِهٰا وَ ابْتَغِ بَيْنَ ذٰلِكَ سَبِيلاً");
        sor.Ayaat.Add("وَ قُلِ الْحَمْدُ لِلّٰهِ الَّذِي لَمْ يَتَّخِذْ وَلَداً وَ لَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَ لَمْ يَكُنْ لَهُ وَلِيٌّ مِنَ الذُّلِّ وَ كَبِّرْهُ تَكْبِيراً");
        salam(sor);
        sor = new Soore();
        sor.Name = "١٨ - الکهف";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ الْحَمْدُ لِلّٰهِ الَّذِي أَنْزَلَ عَلىٰ عَبْدِهِ الْكِتٰابَ وَ لَمْ يَجْعَلْ لَهُ عِوَجاً");
        sor.Ayaat.Add("قَيِّماً لِيُنْذِرَ بَأْساً شَدِيداً مِنْ لَدُنْهُ وَ يُبَشِّرَ الْمُؤْمِنِينَ الَّذِينَ يَعْمَلُونَ الصّٰالِحٰاتِ أَنَّ لَهُمْ أَجْراً حَسَناً");
        sor.Ayaat.Add("مٰاكِثِينَ فِيهِ أَبَداً");
        sor.Ayaat.Add("وَ يُنْذِرَ الَّذِينَ قٰالُوا اتَّخَذَ اللّٰهُ وَلَداً");
        sor.Ayaat.Add("مٰا لَهُمْ بِهِ مِنْ عِلْمٍ وَ لاٰ لِآبٰائِهِمْ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ أَفْوٰاهِهِمْ إِنْ يَقُولُونَ إِلاّٰ كَذِباً");
        sor.Ayaat.Add("فَلَعَلَّكَ بٰاخِعٌ نَفْسَكَ عَلىٰ آثٰارِهِمْ إِنْ لَمْ يُؤْمِنُوا بِهٰذَا الْحَدِيثِ أَسَفاً");
        sor.Ayaat.Add("إِنّٰا جَعَلْنٰا مٰا عَلَى الْأَرْضِ زِينَةً لَهٰا لِنَبْلُوَهُمْ أَيُّهُمْ أَحْسَنُ عَمَلاً");
        sor.Ayaat.Add("وَ إِنّٰا لَجٰاعِلُونَ مٰا عَلَيْهٰا صَعِيداً جُرُزاً");
        sor.Ayaat.Add("أَمْ حَسِبْتَ أَنَّ أَصْحٰابَ الْكَهْفِ وَ الرَّقِيمِ كٰانُوا مِنْ آيٰاتِنٰا عَجَباً");
        sor.Ayaat.Add("إِذْ أَوَى الْفِتْيَةُ إِلَى الْكَهْفِ فَقٰالُوا رَبَّنٰا آتِنٰا مِنْ لَدُنْكَ رَحْمَةً وَ هَيِّئْ لَنٰا مِنْ أَمْرِنٰا رَشَداً");
        sor.Ayaat.Add("فَضَرَبْنٰا عَلَى آذٰانِهِمْ فِي الْكَهْفِ سِنِينَ عَدَداً");
        sor.Ayaat.Add("ثُمَّ بَعَثْنٰاهُمْ لِنَعْلَمَ أَيُّ الْحِزْبَيْنِ أَحْصىٰ لِمٰا لَبِثُوا أَمَداً");
        sor.Ayaat.Add("نَحْنُ نَقُصُّ عَلَيْكَ نَبَأَهُمْ بِالْحَقِّ إِنَّهُمْ فِتْيَةٌ آمَنُوا بِرَبِّهِمْ وَ زِدْنٰاهُمْ هُدىً");
        sor.Ayaat.Add("وَ رَبَطْنٰا عَلىٰ قُلُوبِهِمْ إِذْ قٰامُوا فَقٰالُوا رَبُّنٰا رَبُّ السَّمٰاوٰاتِ وَ الْأَرْضِ لَنْ نَدْعُوَا مِنْ دُونِهِ إِلٰهاً لَقَدْ قُلْنٰا إِذاً شَطَطاً");
        sor.Ayaat.Add("هٰؤُلاٰءِ قَوْمُنَا اتَّخَذُوا مِنْ دُونِهِ آلِهَةً لَوْ لاٰ يَأْتُونَ عَلَيْهِمْ بِسُلْطٰانٍ بَيِّنٍ فَمَنْ أَظْلَمُ مِمَّنِ افْتَرىٰ عَلَى اللّٰهِ كَذِباً");
        sor.Ayaat.Add("وَ إِذِ اعْتَزَلْتُمُوهُمْ وَ مٰا يَعْبُدُونَ إِلاَّ اللّٰهَ فَأْوُوا إِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِنْ رَحْمَتِهِ وَ يُهَيِّئْ لَكُمْ مِنْ أَمْرِكُمْ مِرْفَقاً");
        sor.Ayaat.Add("وَ تَرَى الشَّمْسَ إِذٰا طَلَعَتْ تَزٰاوَرُ عَنْ كَهْفِهِمْ ذٰاتَ الْيَمِينِ وَ إِذٰا غَرَبَتْ تَقْرِضُهُمْ ذٰاتَ الشِّمٰالِ وَ هُمْ فِي فَجْوَةٍ مِنْهُ ذٰلِكَ مِنْ آيٰاتِ اللّٰهِ مَنْ يَهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِ وَ مَنْ يُضْلِلْ فَلَنْ تَجِدَ لَهُ وَلِيًّا مُرْشِداً");
        sor.Ayaat.Add("وَ تَحْسَبُهُمْ أَيْقٰاظاً وَ هُمْ رُقُودٌ وَ نُقَلِّبُهُمْ ذٰاتَ الْيَمِينِ وَ ذٰاتَ الشِّمٰالِ وَ كَلْبُهُمْ بٰاسِطٌ ذِرٰاعَيْهِ بِالْوَصِيدِ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرٰاراً وَ لَمُلِئْتَ مِنْهُمْ رُعْباً");
        sor.Ayaat.Add("وَ كَذٰلِكَ بَعَثْنٰاهُمْ لِيَتَسٰاءَلُوا بَيْنَهُمْ قٰالَ قٰائِلٌ مِنْهُمْ كَمْ لَبِثْتُمْ قٰالُوا لَبِثْنٰا يَوْماً أَوْ بَعْضَ يَوْمٍ قٰالُوا رَبُّكُمْ أَعْلَمُ بِمٰا لَبِثْتُمْ فَابْعَثُوا أَحَدَكُمْ بِوَرِقِكُمْ هٰذِهِ إِلَى الْمَدِينَةِ فَلْيَنْظُرْ أَيُّهٰا أَزْكىٰ طَعٰاماً فَلْيَأْتِكُمْ بِرِزْقٍ مِنْهُ وَ لْيَتَلَطَّفْ وَ لاٰ يُشْعِرَنَّ بِكُمْ أَحَداً");
        sor.Ayaat.Add("إِنَّهُمْ إِنْ يَظْهَرُوا عَلَيْكُمْ يَرْجُمُوكُمْ أَوْ يُعِيدُوكُمْ فِي مِلَّتِهِمْ وَ لَنْ تُفْلِحُوا إِذاً أَبَداً");
        sor.Ayaat.Add("وَ كَذٰلِكَ أَعْثَرْنٰا عَلَيْهِمْ لِيَعْلَمُوا أَنَّ وَعْدَ اللّٰهِ حَقٌّ وَ أَنَّ السّٰاعَةَ لاٰ رَيْبَ فِيهٰا إِذْ يَتَنٰازَعُونَ بَيْنَهُمْ أَمْرَهُمْ فَقٰالُوا ابْنُوا عَلَيْهِمْ بُنْيٰاناً رَبُّهُمْ أَعْلَمُ بِهِمْ قٰالَ الَّذِينَ غَلَبُوا عَلىٰ أَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَسْجِداً");
        sor.Ayaat.Add("سَيَقُولُونَ ثَلاٰثَةٌ رٰابِعُهُمْ كَلْبُهُمْ وَ يَقُولُونَ خَمْسَةٌ سٰادِسُهُمْ كَلْبُهُمْ رَجْماً بِالْغَيْبِ وَ يَقُولُونَ سَبْعَةٌ وَ ثٰامِنُهُمْ كَلْبُهُمْ قُلْ رَبِّي أَعْلَمُ بِعِدَّتِهِمْ مٰا يَعْلَمُهُمْ إِلاّٰ قَلِيلٌ فَلاٰ تُمٰارِ فِيهِمْ إِلاّٰ مِرٰاءً ظٰاهِراً وَ لاٰ تَسْتَفْتِ فِيهِمْ مِنْهُمْ أَحَداً");
        sor.Ayaat.Add("وَ لاٰ تَقُولَنَّ لِشَيْءٍ إِنِّي فٰاعِلٌ ذٰلِكَ غَداً");
        sor.Ayaat.Add("إِلاّٰ أَنْ يَشٰاءَ اللّٰهُ وَ اذْكُرْ رَبَّكَ إِذٰا نَسِيتَ وَ قُلْ عَسىٰ أَنْ يَهْدِيَنِ رَبِّي لِأَقْرَبَ مِنْ هٰذٰا رَشَداً");
        sor.Ayaat.Add("وَ لَبِثُوا فِي كَهْفِهِمْ ثَلاٰثَ مِائَةٍ سِنِينَ وَ ازْدَادُوا تِسْعاً");
        sor.Ayaat.Add("قُلِ اللّٰهُ أَعْلَمُ بِمٰا لَبِثُوا لَهُ غَيْبُ السَّمٰاوٰاتِ وَ الْأَرْضِ أَبْصِرْ بِهِ وَ أَسْمِعْ مٰا لَهُمْ مِنْ دُونِهِ مِنْ وَلِيٍّ وَ لاٰ يُشْرِكُ فِي حُكْمِهِ أَحَداً");
        sor.Ayaat.Add("وَ اتْلُ مٰا أُوحِيَ إِلَيْكَ مِنْ كِتٰابِ رَبِّكَ لاٰ مُبَدِّلَ لِكَلِمٰاتِهِ وَ لَنْ تَجِدَ مِنْ دُونِهِ مُلْتَحَداً");
        sor.Ayaat.Add("وَ اصْبِرْ نَفْسَكَ مَعَ الَّذِينَ يَدْعُونَ رَبَّهُمْ بِالْغَدٰاةِ وَ الْعَشِيِّ يُرِيدُونَ وَجْهَهُ وَ لاٰ تَعْدُ عَيْنٰاكَ عَنْهُمْ تُرِيدُ زِينَةَ الْحَيٰاةِ الدُّنْيٰا وَ لاٰ تُطِعْ مَنْ أَغْفَلْنٰا قَلْبَهُ عَنْ ذِكْرِنٰا وَ اتَّبَعَ هَوٰاهُ وَ كٰانَ أَمْرُهُ فُرُطاً");
        sor.Ayaat.Add("وَ قُلِ الْحَقُّ مِنْ رَبِّكُمْ فَمَنْ شٰاءَ فَلْيُؤْمِنْ وَ مَنْ شٰاءَ فَلْيَكْفُرْ إِنّٰا أَعْتَدْنٰا لِلظّٰالِمِينَ نٰاراً أَحٰاطَ بِهِمْ سُرٰادِقُهٰا وَ إِنْ يَسْتَغِيثُوا يُغٰاثُوا بِمٰاءٍ كَالْمُهْلِ يَشْوِي الْوُجُوهَ بِئْسَ الشَّرٰابُ وَ سٰاءَتْ مُرْتَفَقاً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ إِنّٰا لاٰ نُضِيعُ أَجْرَ مَنْ أَحْسَنَ عَمَلاً");
        sor.Ayaat.Add("أُولٰئِكَ لَهُمْ جَنّٰاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهِمُ الْأَنْهٰارُ يُحَلَّوْنَ فِيهٰا مِنْ أَسٰاوِرَ مِنْ ذَهَبٍ وَ يَلْبَسُونَ ثِيٰاباً خُضْراً مِنْ سُنْدُسٍ وَ إِسْتَبْرَقٍ مُتَّكِئِينَ فِيهٰا عَلَى الْأَرٰائِكِ نِعْمَ الثَّوٰابُ وَ حَسُنَتْ مُرْتَفَقاً");
        sor.Ayaat.Add("وَ اضْرِبْ لَهُمْ مَثَلاً رَجُلَيْنِ جَعَلْنٰا لِأَحَدِهِمٰا جَنَّتَيْنِ مِنْ أَعْنٰابٍ وَ حَفَفْنٰاهُمٰا بِنَخْلٍ وَ جَعَلْنٰا بَيْنَهُمٰا زَرْعاً");
        sor.Ayaat.Add("كِلْتَا الْجَنَّتَيْنِ آتَتْ أُكُلَهٰا وَ لَمْ تَظْلِمْ مِنْهُ شَيْئاً وَ فَجَّرْنٰا خِلاٰلَهُمٰا نَهَراً");
        sor.Ayaat.Add("وَ كٰانَ لَهُ ثَمَرٌ فَقٰالَ لِصٰاحِبِهِ وَ هُوَ يُحٰاوِرُهُ أَنَا أَكْثَرُ مِنْكَ مٰالاً وَ أَعَزُّ نَفَراً");
        sor.Ayaat.Add("وَ دَخَلَ جَنَّتَهُ وَ هُوَ ظٰالِمٌ لِنَفْسِهِ قٰالَ مٰا أَظُنُّ أَنْ تَبِيدَ هٰذِهِ أَبَداً");
        sor.Ayaat.Add("وَ مٰا أَظُنُّ السّٰاعَةَ قٰائِمَةً وَ لَئِنْ رُدِدْتُ إِلىٰ رَبِّي لَأَجِدَنَّ خَيْراً مِنْهٰا مُنْقَلَباً");
        sor.Ayaat.Add("قٰالَ لَهُ صٰاحِبُهُ وَ هُوَ يُحٰاوِرُهُ أَ كَفَرْتَ بِالَّذِي خَلَقَكَ مِنْ تُرٰابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ سَوّٰاكَ رَجُلاً");
        sor.Ayaat.Add("لٰكِنَّا هُوَ اللّٰهُ رَبِّي وَ لاٰ أُشْرِكُ بِرَبِّي أَحَداً");
        sor.Ayaat.Add("وَ لَوْ لاٰ إِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مٰا شٰاءَ اللّٰهُ لاٰ قُوَّةَ إِلاّٰ بِاللّٰهِ إِنْ تَرَنِ أَنَا أَقَلَّ مِنْكَ مٰالاً وَ وَلَداً");
        sor.Ayaat.Add("فَعَسىٰ رَبِّي أَنْ يُؤْتِيَنِ خَيْراً مِنْ جَنَّتِكَ وَ يُرْسِلَ عَلَيْهٰا حُسْبٰاناً مِنَ السَّمٰاءِ فَتُصْبِحَ صَعِيداً زَلَقاً");
        sor.Ayaat.Add("أَوْ يُصْبِحَ مٰاؤُهٰا غَوْراً فَلَنْ تَسْتَطِيعَ لَهُ طَلَباً");
        sor.Ayaat.Add("وَ أُحِيطَ بِثَمَرِهِ فَأَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلىٰ مٰا أَنْفَقَ فِيهٰا وَ هِيَ خٰاوِيَةٌ عَلىٰ عُرُوشِهٰا وَ يَقُولُ يٰا لَيْتَنِي لَمْ أُشْرِكْ بِرَبِّي أَحَداً");
        sor.Ayaat.Add("وَ لَمْ تَكُنْ لَهُ فِئَةٌ يَنْصُرُونَهُ مِنْ دُونِ اللّٰهِ وَ مٰا كٰانَ مُنْتَصِراً");
        sor.Ayaat.Add("هُنٰالِكَ الْوَلاٰيَةُ لِلّٰهِ الْحَقِّ هُوَ خَيْرٌ ثَوٰاباً وَ خَيْرٌ عُقْباً");
        sor.Ayaat.Add("وَ اضْرِبْ لَهُمْ مَثَلَ الْحَيٰاةِ الدُّنْيٰا كَمٰاءٍ أَنْزَلْنٰاهُ مِنَ السَّمٰاءِ فَاخْتَلَطَ بِهِ نَبٰاتُ الْأَرْضِ فَأَصْبَحَ هَشِيماً تَذْرُوهُ الرِّيٰاحُ وَ كٰانَ اللّٰهُ عَلىٰ كُلِّ شَيْءٍ مُقْتَدِراً");
        sor.Ayaat.Add("الْمٰالُ وَ الْبَنُونَ زِينَةُ الْحَيٰاةِ الدُّنْيٰا وَ الْبٰاقِيٰاتُ الصّٰالِحٰاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوٰاباً وَ خَيْرٌ أَمَلاً");
        sor.Ayaat.Add("وَ يَوْمَ نُسَيِّرُ الْجِبٰالَ وَ تَرَى الْأَرْضَ بٰارِزَةً وَ حَشَرْنٰاهُمْ فَلَمْ نُغٰادِرْ مِنْهُمْ أَحَداً");
        sor.Ayaat.Add("وَ عُرِضُوا عَلىٰ رَبِّكَ صَفًّا لَقَدْ جِئْتُمُونٰا كَمٰا خَلَقْنٰاكُمْ أَوَّلَ مَرَّةٍ بَلْ زَعَمْتُمْ أَلَّنْ نَجْعَلَ لَكُمْ مَوْعِداً");
        sor.Ayaat.Add("وَ وُضِعَ الْكِتٰابُ فَتَرَى الْمُجْرِمِينَ مُشْفِقِينَ مِمّٰا فِيهِ وَ يَقُولُونَ يٰا وَيْلَتَنٰا مٰا لِهٰذَا الْكِتٰابِ لاٰ يُغٰادِرُ صَغِيرَةً وَ لاٰ كَبِيرَةً إِلاّٰ أَحْصٰاهٰا وَ وَجَدُوا مٰا عَمِلُوا حٰاضِراً وَ لاٰ يَظْلِمُ رَبُّكَ أَحَداً");
        sor.Ayaat.Add("وَ إِذْ قُلْنٰا لِلْمَلاٰئِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاّٰ إِبْلِيسَ كٰانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ أَمْرِ رَبِّهِ أَ فَتَتَّخِذُونَهُ وَ ذُرِّيَّتَهُ أَوْلِيٰاءَ مِنْ دُونِي وَ هُمْ لَكُمْ عَدُوٌّ بِئْسَ لِلظّٰالِمِينَ بَدَلاً");
        sor.Ayaat.Add("مٰا أَشْهَدْتُهُمْ خَلْقَ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ لاٰ خَلْقَ أَنْفُسِهِمْ وَ مٰا كُنْتُ مُتَّخِذَ الْمُضِلِّينَ عَضُداً");
        sor.Ayaat.Add("وَ يَوْمَ يَقُولُ نٰادُوا شُرَكٰائِيَ الَّذِينَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا لَهُمْ وَ جَعَلْنٰا بَيْنَهُمْ مَوْبِقاً");
        sor.Ayaat.Add("وَ رَأَى الْمُجْرِمُونَ النّٰارَ فَظَنُّوا أَنَّهُمْ مُوٰاقِعُوهٰا وَ لَمْ يَجِدُوا عَنْهٰا مَصْرِفاً");
        sor.Ayaat.Add("وَ لَقَدْ صَرَّفْنٰا فِي هٰذَا الْقُرْآنِ لِلنّٰاسِ مِنْ كُلِّ مَثَلٍ وَ كٰانَ الْإِنْسٰانُ أَكْثَرَ شَيْءٍ جَدَلاً");
        sor.Ayaat.Add("وَ مٰا مَنَعَ النّٰاسَ أَنْ يُؤْمِنُوا إِذْ جٰاءَهُمُ الْهُدىٰ وَ يَسْتَغْفِرُوا رَبَّهُمْ إِلاّٰ أَنْ تَأْتِيَهُمْ سُنَّةُ الْأَوَّلِينَ أَوْ يَأْتِيَهُمُ الْعَذٰابُ قُبُلاً");
        sor.Ayaat.Add("وَ مٰا نُرْسِلُ الْمُرْسَلِينَ إِلاّٰ مُبَشِّرِينَ وَ مُنْذِرِينَ وَ يُجٰادِلُ الَّذِينَ كَفَرُوا بِالْبٰاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ وَ اتَّخَذُوا آيٰاتِي وَ مٰا أُنْذِرُوا هُزُواً");
        sor.Ayaat.Add("وَ مَنْ أَظْلَمُ مِمَّنْ ذُكِّرَ بِآيٰاتِ رَبِّهِ فَأَعْرَضَ عَنْهٰا وَ نَسِيَ مٰا قَدَّمَتْ يَدٰاهُ إِنّٰا جَعَلْنٰا عَلىٰ قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ وَ فِي آذٰانِهِمْ وَقْراً وَ إِنْ تَدْعُهُمْ إِلَى الْهُدىٰ فَلَنْ يَهْتَدُوا إِذاً أَبَداً");
        sor.Ayaat.Add("وَ رَبُّكَ الْغَفُورُ ذُو الرَّحْمَةِ لَوْ يُؤٰاخِذُهُمْ بِمٰا كَسَبُوا لَعَجَّلَ لَهُمُ الْعَذٰابَ بَلْ لَهُمْ مَوْعِدٌ لَنْ يَجِدُوا مِنْ دُونِهِ مَوْئِلاً");
        sor.Ayaat.Add("وَ تِلْكَ الْقُرىٰ أَهْلَكْنٰاهُمْ لَمّٰا ظَلَمُوا وَ جَعَلْنٰا لِمَهْلِكِهِمْ مَوْعِداً");
        sor.Ayaat.Add("وَ إِذْ قٰالَ مُوسىٰ لِفَتٰاهُ لاٰ أَبْرَحُ حَتّٰى أَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ أَوْ أَمْضِيَ حُقُباً");
        sor.Ayaat.Add("فَلَمّٰا بَلَغٰا مَجْمَعَ بَيْنِهِمٰا نَسِيٰا حُوتَهُمٰا فَاتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ سَرَباً");
        sor.Ayaat.Add("فَلَمّٰا جٰاوَزٰا قٰالَ لِفَتٰاهُ آتِنٰا غَدٰاءَنٰا لَقَدْ لَقِينٰا مِنْ سَفَرِنٰا هٰذٰا نَصَباً");
        sor.Ayaat.Add("قٰالَ أَ رَأَيْتَ إِذْ أَوَيْنٰا إِلَى الصَّخْرَةِ فَإِنِّي نَسِيتُ الْحُوتَ وَ مٰا أَنْسٰانِيهُ إِلاَّ الشَّيْطٰانُ أَنْ أَذْكُرَهُ وَ اتَّخَذَ سَبِيلَهُ فِي الْبَحْرِ عَجَباً");
        sor.Ayaat.Add("قٰالَ ذٰلِكَ مٰا كُنّٰا نَبْغِ فَارْتَدّٰا عَلىٰ آثٰارِهِمٰا قَصَصاً");
        sor.Ayaat.Add("فَوَجَدٰا عَبْداً مِنْ عِبٰادِنٰا آتَيْنٰاهُ رَحْمَةً مِنْ عِنْدِنٰا وَ عَلَّمْنٰاهُ مِنْ لَدُنّٰا عِلْماً");
        sor.Ayaat.Add("قٰالَ لَهُ مُوسىٰ هَلْ أَتَّبِعُكَ عَلىٰ أَنْ تُعَلِّمَنِ مِمّٰا عُلِّمْتَ رُشْداً");
        sor.Ayaat.Add("قٰالَ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْراً");
        sor.Ayaat.Add("وَ كَيْفَ تَصْبِرُ عَلىٰ مٰا لَمْ تُحِطْ بِهِ خُبْراً");
        sor.Ayaat.Add("قٰالَ سَتَجِدُنِي إِنْ شٰاءَ اللّٰهُ صٰابِراً وَ لاٰ أَعْصِي لَكَ أَمْراً");
        sor.Ayaat.Add("قٰالَ فَإِنِ اتَّبَعْتَنِي فَلاٰ تَسْئَلْنِي عَنْ شَيْءٍ حَتّٰى أُحْدِثَ لَكَ مِنْهُ ذِكْراً");
        sor.Ayaat.Add("فَانْطَلَقٰا حَتّٰى إِذٰا رَكِبٰا فِي السَّفِينَةِ خَرَقَهٰا قٰالَ أَ خَرَقْتَهٰا لِتُغْرِقَ أَهْلَهٰا لَقَدْ جِئْتَ شَيْئاً إِمْراً");
        sor.Ayaat.Add("قٰالَ أَ لَمْ أَقُلْ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْراً");
        sor.Ayaat.Add("قٰالَ لاٰ تُؤٰاخِذْنِي بِمٰا نَسِيتُ وَ لاٰ تُرْهِقْنِي مِنْ أَمْرِي عُسْراً");
        sor.Ayaat.Add("فَانْطَلَقٰا حَتّٰى إِذٰا لَقِيٰا غُلاٰماً فَقَتَلَهُ قٰالَ أَ قَتَلْتَ نَفْساً زَكِيَّةً بِغَيْرِ نَفْسٍ لَقَدْ جِئْتَ شَيْئاً نُكْراً");
        sor.Ayaat.Add("قٰالَ أَ لَمْ أَقُلْ لَكَ إِنَّكَ لَنْ تَسْتَطِيعَ مَعِيَ صَبْراً");
        sor.Ayaat.Add("قٰالَ إِنْ سَأَلْتُكَ عَنْ شَيْءٍ بَعْدَهٰا فَلاٰ تُصٰاحِبْنِي قَدْ بَلَغْتَ مِنْ لَدُنِّي عُذْراً");
        sor.Ayaat.Add("فَانْطَلَقٰا حَتّٰى إِذٰا أَتَيٰا أَهْلَ قَرْيَةٍ اسْتَطْعَمٰا أَهْلَهٰا فَأَبَوْا أَنْ يُضَيِّفُوهُمٰا فَوَجَدٰا فِيهٰا جِدٰاراً يُرِيدُ أَنْ يَنْقَضَّ فَأَقٰامَهُ قٰالَ لَوْ شِئْتَ لاَتَّخَذْتَ عَلَيْهِ أَجْراً");
        sor.Ayaat.Add("قٰالَ هٰذٰا فِرٰاقُ بَيْنِي وَ بَيْنِكَ سَأُنَبِّئُكَ بِتَأْوِيلِ مٰا لَمْ تَسْتَطِعْ عَلَيْهِ صَبْراً");
        sor.Ayaat.Add("أَمَّا السَّفِينَةُ فَكٰانَتْ لِمَسٰاكِينَ يَعْمَلُونَ فِي الْبَحْرِ فَأَرَدْتُ أَنْ أَعِيبَهٰا وَ كٰانَ وَرٰاءَهُمْ مَلِكٌ يَأْخُذُ كُلَّ سَفِينَةٍ غَصْباً");
        sor.Ayaat.Add("وَ أَمَّا الْغُلاٰمُ فَكٰانَ أَبَوٰاهُ مُؤْمِنَيْنِ فَخَشِينٰا أَنْ يُرْهِقَهُمٰا طُغْيٰاناً وَ كُفْراً");
        sor.Ayaat.Add("فَأَرَدْنٰا أَنْ يُبْدِلَهُمٰا رَبُّهُمٰا خَيْراً مِنْهُ زَكٰاةً وَ أَقْرَبَ رُحْماً");
        sor.Ayaat.Add("وَ أَمَّا الْجِدٰارُ فَكٰانَ لِغُلاٰمَيْنِ يَتِيمَيْنِ فِي الْمَدِينَةِ وَ كٰانَ تَحْتَهُ كَنْزٌ لَهُمٰا وَ كٰانَ أَبُوهُمٰا صٰالِحاً فَأَرٰادَ رَبُّكَ أَنْ يَبْلُغٰا أَشُدَّهُمٰا وَ يَسْتَخْرِجٰا كَنزَهُمٰا رَحْمَةً مِنْ رَبِّكَ وَ مٰا فَعَلْتُهُ عَنْ أَمْرِي ذٰلِكَ تَأْوِيلُ مٰا لَمْ تَسْطِعْ عَلَيْهِ صَبْراً");
        sor.Ayaat.Add("وَ يَسْئَلُونَكَ عَنْ ذِي الْقَرْنَيْنِ قُلْ سَأَتْلُوا عَلَيْكُمْ مِنْهُ ذِكْراً");
        sor.Ayaat.Add("إِنّٰا مَكَّنّٰا لَهُ فِي الْأَرْضِ وَ آتَيْنٰاهُ مِنْ كُلِّ شَيْءٍ سَبَباً");
        sor.Ayaat.Add("فَأَتْبَعَ سَبَباً");
        sor.Ayaat.Add("حَتّٰى إِذٰا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهٰا تَغْرُبُ فِي عَيْنٍ حَمِئَةٍ وَ وَجَدَ عِنْدَهٰا قَوْماً قُلْنٰا يٰا ذَا الْقَرْنَيْنِ إِمّٰا أَنْ تُعَذِّبَ وَ إِمّٰا أَنْ تَتَّخِذَ فِيهِمْ حُسْناً");
        sor.Ayaat.Add("قٰالَ أَمّٰا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهُ ثُمَّ يُرَدُّ إِلىٰ رَبِّهِ فَيُعَذِّبُهُ عَذٰاباً نُكْراً");
        sor.Ayaat.Add("وَ أَمّٰا مَنْ آمَنَ وَ عَمِلَ صٰالِحاً فَلَهُ جَزٰاءً الْحُسْنىٰ وَ سَنَقُولُ لَهُ مِنْ أَمْرِنٰا يُسْراً");
        sor.Ayaat.Add("ثُمَّ أَتْبَعَ سَبَباً");
        sor.Ayaat.Add("حَتّٰى إِذٰا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهٰا تَطْلُعُ عَلىٰ قَوْمٍ لَمْ نَجْعَلْ لَهُمْ مِنْ دُونِهٰا سِتْراً");
        sor.Ayaat.Add("كَذٰلِكَ وَ قَدْ أَحَطْنٰا بِمٰا لَدَيْهِ خُبْراً");
        sor.Ayaat.Add("ثُمَّ أَتْبَعَ سَبَباً");
        sor.Ayaat.Add("حَتّٰى إِذٰا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُونِهِمٰا قَوْماً لاٰ يَكٰادُونَ يَفْقَهُونَ قَوْلاً");
        sor.Ayaat.Add("قٰالُوا يٰا ذَا الْقَرْنَيْنِ إِنَّ يَأْجُوجَ وَ مَأْجُوجَ مُفْسِدُونَ فِي الْأَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجاً عَلىٰ أَنْ تَجْعَلَ بَيْنَنٰا وَ بَيْنَهُمْ سَدًّا");
        sor.Ayaat.Add("قٰالَ مٰا مَكَّنِّي فِيهِ رَبِّي خَيْرٌ فَأَعِينُونِي بِقُوَّةٍ أَجْعَلْ بَيْنَكُمْ وَ بَيْنَهُمْ رَدْماً");
        sor.Ayaat.Add("آتُونِي زُبَرَ الْحَدِيدِ حَتّٰى إِذٰا سٰاوىٰ بَيْنَ الصَّدَفَيْنِ قٰالَ انْفُخُوا حَتّٰى إِذٰا جَعَلَهُ نٰاراً قٰالَ آتُونِي أُفْرِغْ عَلَيْهِ قِطْراً");
        sor.Ayaat.Add("فَمَا اسْطٰاعُوا أَنْ يَظْهَرُوهُ وَ مَا اسْتَطٰاعُوا لَهُ نَقْباً");
        sor.Ayaat.Add("قٰالَ هٰذٰا رَحْمَةٌ مِنْ رَبِّي فَإِذٰا جٰاءَ وَعْدُ رَبِّي جَعَلَهُ دَكّٰاءَ وَ كٰانَ وَعْدُ رَبِّي حَقًّا");
        sor.Ayaat.Add("وَ تَرَكْنٰا بَعْضَهُمْ يَوْمَئِذٍ يَمُوجُ فِي بَعْضٍ وَ نُفِخَ فِي الصُّورِ فَجَمَعْنٰاهُمْ جَمْعاً");
        sor.Ayaat.Add("وَ عَرَضْنٰا جَهَنَّمَ يَوْمَئِذٍ لِلْكٰافِرِينَ عَرْضاً");
        sor.Ayaat.Add("الَّذِينَ كٰانَتْ أَعْيُنُهُمْ فِي غِطٰاءٍ عَنْ ذِكْرِي وَ كٰانُوا لاٰ يَسْتَطِيعُونَ سَمْعاً");
        sor.Ayaat.Add("أَ فَحَسِبَ الَّذِينَ كَفَرُوا أَنْ يَتَّخِذُوا عِبٰادِي مِنْ دُونِي أَوْلِيٰاءَ إِنّٰا أَعْتَدْنٰا جَهَنَّمَ لِلْكٰافِرِينَ نُزُلاً");
        sor.Ayaat.Add("قُلْ هَلْ نُنَبِّئُكُمْ بِالْأَخْسَرِينَ أَعْمٰالاً");
        sor.Ayaat.Add("الَّذِينَ ضَلَّ سَعْيُهُمْ فِي الْحَيٰاةِ الدُّنْيٰا وَ هُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صُنْعاً");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ كَفَرُوا بِآيٰاتِ رَبِّهِمْ وَ لِقٰائِهِ فَحَبِطَتْ أَعْمٰالُهُمْ فَلاٰ نُقِيمُ لَهُمْ يَوْمَ الْقِيٰامَةِ وَزْناً");
        sor.Ayaat.Add("ذٰلِكَ جَزٰاؤُهُمْ جَهَنَّمُ بِمٰا كَفَرُوا وَ اتَّخَذُوا آيٰاتِي وَ رُسُلِي هُزُواً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ كٰانَتْ لَهُمْ جَنّٰاتُ الْفِرْدَوْسِ نُزُلاً");
        sor.Ayaat.Add("خٰالِدِينَ فِيهٰا لاٰ يَبْغُونَ عَنْهٰا حِوَلاً");
        sor.Ayaat.Add("قُلْ لَوْ كٰانَ الْبَحْرُ مِدٰاداً لِكَلِمٰاتِ رَبِّي لَنَفِدَ الْبَحْرُ قَبْلَ أَنْ تَنْفَدَ كَلِمٰاتُ رَبِّي وَ لَوْ جِئْنٰا بِمِثْلِهِ مَدَداً");
        sor.Ayaat.Add("قُلْ إِنَّمٰا أَنَا بَشَرٌ مِثْلُكُمْ يُوحىٰ إِلَيَّ أَنَّمٰا إِلٰهُكُمْ إِلٰهٌ وٰاحِدٌ فَمَنْ كٰانَ يَرْجُوا لِقٰاءَ رَبِّهِ فَلْيَعْمَلْ عَمَلاً صٰالِحاً وَ لاٰ يُشْرِكْ بِعِبٰادَةِ رَبِّهِ أَحَداً");
        salam(sor);
        sor = new Soore();
        sor.Name = "١٩ - مریم";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ كهيعص");
        sor.Ayaat.Add("ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهُ زَكَرِيّٰا");
        sor.Ayaat.Add("إِذْ نٰادىٰ رَبَّهُ نِدٰاءً خَفِيًّا");
        sor.Ayaat.Add("قٰالَ رَبِّ إِنِّي وَهَنَ الْعَظْمُ مِنِّي وَ اشْتَعَلَ الرَّأْسُ شَيْباً وَ لَمْ أَكُنْ بِدُعٰائِكَ رَبِّ شَقِيًّا");
        sor.Ayaat.Add("وَ إِنِّي خِفْتُ الْمَوٰالِيَ مِنْ وَرٰائِي وَ كٰانَتِ امْرَأَتِي عٰاقِراً فَهَبْ لِي مِنْ لَدُنْكَ وَلِيًّا");
        sor.Ayaat.Add("يَرِثُنِي وَ يَرِثُ مِنْ آلِ يَعْقُوبَ وَ اجْعَلْهُ رَبِّ رَضِيًّا");
        sor.Ayaat.Add("يٰا زَكَرِيّٰا إِنّٰا نُبَشِّرُكَ بِغُلاٰمٍ اسْمُهُ يَحْيىٰ لَمْ نَجْعَلْ لَهُ مِنْ قَبْلُ سَمِيًّا");
        sor.Ayaat.Add("قٰالَ رَبِّ أَنّٰى يَكُونُ لِي غُلاٰمٌ وَ كٰانَتِ امْرَأَتِي عٰاقِراً وَ قَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا");
        sor.Ayaat.Add("قٰالَ كَذٰلِكَ قٰالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَ قَدْ خَلَقْتُكَ مِنْ قَبْلُ وَ لَمْ تَكُ شَيْئاً");
        sor.Ayaat.Add("قٰالَ رَبِّ اجْعَلْ لِي آيَةً قٰالَ آيَتُكَ أَلاّٰ تُكَلِّمَ النّٰاسَ ثَلاٰثَ لَيٰالٍ سَوِيًّا");
        sor.Ayaat.Add("فَخَرَجَ عَلىٰ قَوْمِهِ مِنَ الْمِحْرٰابِ فَأَوْحىٰ إِلَيْهِمْ أَنْ سَبِّحُوا بُكْرَةً وَ عَشِيًّا");
        sor.Ayaat.Add("يٰا يَحْيىٰ خُذِ الْكِتٰابَ بِقُوَّةٍ وَ آتَيْنٰاهُ الْحُكْمَ صَبِيًّا");
        sor.Ayaat.Add("وَ حَنٰاناً مِنْ لَدُنّٰا وَ زَكٰاةً وَ كٰانَ تَقِيًّا");
        sor.Ayaat.Add("وَ بَرًّا بِوٰالِدَيْهِ وَ لَمْ يَكُنْ جَبّٰاراً عَصِيًّا");
        sor.Ayaat.Add("وَ سَلاٰمٌ عَلَيْهِ يَوْمَ وُلِدَ وَ يَوْمَ يَمُوتُ وَ يَوْمَ يُبْعَثُ حَيًّا");
        sor.Ayaat.Add("وَ اذْكُرْ فِي الْكِتٰابِ مَرْيَمَ إِذِ انْتَبَذَتْ مِنْ أَهْلِهٰا مَكٰاناً شَرْقِيًّا");
        sor.Ayaat.Add("فَاتَّخَذَتْ مِنْ دُونِهِمْ حِجٰاباً فَأَرْسَلْنٰا إِلَيْهٰا رُوحَنٰا فَتَمَثَّلَ لَهٰا بَشَراً سَوِيًّا");
        sor.Ayaat.Add("قٰالَتْ إِنِّي أَعُوذُ بِالرَّحْمٰنِ مِنْكَ إِنْ كُنْتَ تَقِيًّا");
        sor.Ayaat.Add("قٰالَ إِنَّمٰا أَنَا رَسُولُ رَبِّكِ لِأَهَبَ لَكِ غُلاٰماً زَكِيًّا");
        sor.Ayaat.Add("قٰالَتْ أَنّٰى يَكُونُ لِي غُلاٰمٌ وَ لَمْ يَمْسَسْنِي بَشَرٌ وَ لَمْ أَكُ بَغِيًّا");
        sor.Ayaat.Add("قٰالَ كَذٰلِكِ قٰالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌ وَ لِنَجْعَلَهُ آيَةً لِلنّٰاسِ وَ رَحْمَةً مِنّٰا وَ كٰانَ أَمْراً مَقْضِيًّا");
        sor.Ayaat.Add("فَحَمَلَتْهُ فَانْتَبَذَتْ بِهِ مَكٰاناً قَصِيًّا");
        sor.Ayaat.Add("فَأَجٰاءَهَا الْمَخٰاضُ إِلىٰ جِذْعِ النَّخْلَةِ قٰالَتْ يٰا لَيْتَنِي مِتُّ قَبْلَ هٰذٰا وَ كُنْتُ نَسْياً مَنْسِيًّا");
        sor.Ayaat.Add("فَنٰادٰاهٰا مِنْ تَحْتِهٰا أَلاّٰ تَحْزَنِي قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا");
        sor.Ayaat.Add("وَ هُزِّي إِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسٰاقِطْ عَلَيْكِ رُطَباً جَنِيًّا");
        sor.Ayaat.Add("فَكُلِي وَ اشْرَبِي وَ قَرِّي عَيْناً فَإِمّٰا تَرَيِنَّ مِنَ الْبَشَرِ أَحَداً فَقُولِي إِنِّي نَذَرْتُ لِلرَّحْمٰنِ صَوْماً فَلَنْ أُكَلِّمَ الْيَوْمَ إِنْسِيًّا");
        sor.Ayaat.Add("فَأَتَتْ بِهِ قَوْمَهٰا تَحْمِلُهُ قٰالُوا يٰا مَرْيَمُ لَقَدْ جِئْتِ شَيْئاً فَرِيًّا");
        sor.Ayaat.Add("يٰا أُخْتَ هٰارُونَ مٰا كٰانَ أَبُوكِ امْرَأَ سَوْءٍ وَ مٰا كٰانَتْ أُمُّكِ بَغِيًّا");
        sor.Ayaat.Add("فَأَشٰارَتْ إِلَيْهِ قٰالُوا كَيْفَ نُكَلِّمُ مَنْ كٰانَ فِي الْمَهْدِ صَبِيًّا");
        sor.Ayaat.Add("قٰالَ إِنِّي عَبْدُ اللّٰهِ آتٰانِيَ الْكِتٰابَ وَ جَعَلَنِي نَبِيًّا");
        sor.Ayaat.Add("وَ جَعَلَنِي مُبٰارَكاً أَيْنَ مٰا كُنْتُ وَ أَوْصٰانِي بِالصَّلاٰةِ وَ الزَّكٰاةِ مٰا دُمْتُ حَيًّا");
        sor.Ayaat.Add("وَ بَرًّا بِوٰالِدَتِي وَ لَمْ يَجْعَلْنِي جَبّٰاراً شَقِيًّا");
        sor.Ayaat.Add("وَ السَّلاٰمُ عَلَيَّ يَوْمَ وُلِدْتُ وَ يَوْمَ أَمُوتُ وَ يَوْمَ أُبْعَثُ حَيًّا");
        sor.Ayaat.Add("ذٰلِكَ عِيسَى ابْنُ مَرْيَمَ قَوْلَ الْحَقِّ الَّذِي فِيهِ يَمْتَرُونَ");
        sor.Ayaat.Add("مٰا كٰانَ لِلّٰهِ أَنْ يَتَّخِذَ مِنْ وَلَدٍ سُبْحٰانَهُ إِذٰا قَضىٰ أَمْراً فَإِنَّمٰا يَقُولُ لَهُ كُنْ فَيَكُونُ");
        sor.Ayaat.Add("وَ إِنَّ اللّٰهَ رَبِّي وَ رَبُّكُمْ فَاعْبُدُوهُ هٰذٰا صِرٰاطٌ مُسْتَقِيمٌ");
        sor.Ayaat.Add("فَاخْتَلَفَ الْأَحْزٰابُ مِنْ بَيْنِهِمْ فَوَيْلٌ لِلَّذِينَ كَفَرُوا مِنْ مَشْهَدِ يَوْمٍ عَظِيمٍ");
        sor.Ayaat.Add("أَسْمِعْ بِهِمْ وَ أَبْصِرْ يَوْمَ يَأْتُونَنٰا لٰكِنِ الظّٰالِمُونَ الْيَوْمَ فِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("وَ أَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ إِذْ قُضِيَ الْأَمْرُ وَ هُمْ فِي غَفْلَةٍ وَ هُمْ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("إِنّٰا نَحْنُ نَرِثُ الْأَرْضَ وَ مَنْ عَلَيْهٰا وَ إِلَيْنٰا يُرْجَعُونَ");
        sor.Ayaat.Add("وَ اذْكُرْ فِي الْكِتٰابِ إِبْرٰاهِيمَ إِنَّهُ كٰانَ صِدِّيقاً نَبِيًّا");
        sor.Ayaat.Add("إِذْ قٰالَ لِأَبِيهِ يٰا أَبَتِ لِمَ تَعْبُدُ مٰا لاٰ يَسْمَعُ وَ لاٰ يُبْصِرُ وَ لاٰ يُغْنِي عَنْكَ شَيْئاً");
        sor.Ayaat.Add("يٰا أَبَتِ إِنِّي قَدْ جٰاءَنِي مِنَ الْعِلْمِ مٰا لَمْ يَأْتِكَ فَاتَّبِعْنِي أَهْدِكَ صِرٰاطاً سَوِيًّا");
        sor.Ayaat.Add("يٰا أَبَتِ لاٰ تَعْبُدِ الشَّيْطٰانَ إِنَّ الشَّيْطٰانَ كٰانَ لِلرَّحْمٰنِ عَصِيًّا");
        sor.Ayaat.Add("يٰا أَبَتِ إِنِّي أَخٰافُ أَنْ يَمَسَّكَ عَذٰابٌ مِنَ الرَّحْمٰنِ فَتَكُونَ لِلشَّيْطٰانِ وَلِيًّا");
        sor.Ayaat.Add("قٰالَ أَ رٰاغِبٌ أَنْتَ عَنْ آلِهَتِي يٰا إِبْرٰاهِيمُ لَئِنْ لَمْ تَنْتَهِ لَأَرْجُمَنَّكَ وَ اهْجُرْنِي مَلِيًّا");
        sor.Ayaat.Add("قٰالَ سَلاٰمٌ عَلَيْكَ سَأَسْتَغْفِرُ لَكَ رَبِّي إِنَّهُ كٰانَ بِي حَفِيًّا");
        sor.Ayaat.Add("وَ أَعْتَزِلُكُمْ وَ مٰا تَدْعُونَ مِنْ دُونِ اللّٰهِ وَ أَدْعُوا رَبِّي عَسىٰ أَلاّٰ أَكُونَ بِدُعٰاءِ رَبِّي شَقِيًّا");
        sor.Ayaat.Add("فَلَمَّا اعْتَزَلَهُمْ وَ مٰا يَعْبُدُونَ مِنْ دُونِ اللّٰهِ وَهَبْنٰا لَهُ إِسْحٰاقَ وَ يَعْقُوبَ وَ كُلاًّ جَعَلْنٰا نَبِيًّا");
        sor.Ayaat.Add("وَ وَهَبْنٰا لَهُمْ مِنْ رَحْمَتِنٰا وَ جَعَلْنٰا لَهُمْ لِسٰانَ صِدْقٍ عَلِيًّا");
        sor.Ayaat.Add("وَ اذْكُرْ فِي الْكِتٰابِ مُوسىٰ إِنَّهُ كٰانَ مُخْلَصاً وَ كٰانَ رَسُولاً نَبِيًّا");
        sor.Ayaat.Add("وَ نٰادَيْنٰاهُ مِنْ جٰانِبِ الطُّورِ الْأَيْمَنِ وَ قَرَّبْنٰاهُ نَجِيًّا");
        sor.Ayaat.Add("وَ وَهَبْنٰا لَهُ مِنْ رَحْمَتِنٰا أَخٰاهُ هٰارُونَ نَبِيًّا");
        sor.Ayaat.Add("وَ اذْكُرْ فِي الْكِتٰابِ إِسْمٰاعِيلَ إِنَّهُ كٰانَ صٰادِقَ الْوَعْدِ وَ كٰانَ رَسُولاً نَبِيًّا");
        sor.Ayaat.Add("وَ كٰانَ يَأْمُرُ أَهْلَهُ بِالصَّلاٰةِ وَ الزَّكٰاةِ وَ كٰانَ عِنْدَ رَبِّهِ مَرْضِيًّا");
        sor.Ayaat.Add("وَ اذْكُرْ فِي الْكِتٰابِ إِدْرِيسَ إِنَّهُ كٰانَ صِدِّيقاً نَبِيًّا");
        sor.Ayaat.Add("وَ رَفَعْنٰاهُ مَكٰاناً عَلِيًّا");
        sor.Ayaat.Add("أُولٰئِكَ الَّذِينَ أَنْعَمَ اللّٰهُ عَلَيْهِمْ مِنَ النَّبِيِّينَ مِنْ ذُرِّيَّةِ آدَمَ وَ مِمَّنْ حَمَلْنٰا مَعَ نُوحٍ وَ مِنْ ذُرِّيَّةِ إِبْرٰاهِيمَ وَ إِسْرٰائِيلَ وَ مِمَّنْ هَدَيْنٰا وَ اجْتَبَيْنٰا إِذٰا تُتْلىٰ عَلَيْهِمْ آيٰاتُ الرَّحْمٰنِ خَرُّوا سُجَّداً وَ بُكِيًّا");
        sor.Ayaat.Add("فَخَلَفَ مِنْ بَعْدِهِمْ خَلْفٌ أَضٰاعُوا الصَّلاٰةَ وَ اتَّبَعُوا الشَّهَوٰاتِ فَسَوْفَ يَلْقَوْنَ غَيًّا");
        sor.Ayaat.Add("إِلاّٰ مَنْ تٰابَ وَ آمَنَ وَ عَمِلَ صٰالِحاً فَأُولٰئِكَ يَدْخُلُونَ الْجَنَّةَ وَ لاٰ يُظْلَمُونَ شَيْئاً");
        sor.Ayaat.Add("جَنّٰاتِ عَدْنٍ الَّتِي وَعَدَ الرَّحْمٰنُ عِبٰادَهُ بِالْغَيْبِ إِنَّهُ كٰانَ وَعْدُهُ مَأْتِيًّا");
        sor.Ayaat.Add("لاٰ يَسْمَعُونَ فِيهٰا لَغْواً إِلاّٰ سَلاٰماً وَ لَهُمْ رِزْقُهُمْ فِيهٰا بُكْرَةً وَ عَشِيًّا");
        sor.Ayaat.Add("تِلْكَ الْجَنَّةُ الَّتِي نُورِثُ مِنْ عِبٰادِنٰا مَنْ كٰانَ تَقِيًّا");
        sor.Ayaat.Add("وَ مٰا نَتَنَزَّلُ إِلاّٰ بِأَمْرِ رَبِّكَ لَهُ مٰا بَيْنَ أَيْدِينٰا وَ مٰا خَلْفَنٰا وَ مٰا بَيْنَ ذٰلِكَ وَ مٰا كٰانَ رَبُّكَ نَسِيًّا");
        sor.Ayaat.Add("رَبُّ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مٰا بَيْنَهُمٰا فَاعْبُدْهُ وَ اصْطَبِرْ لِعِبٰادَتِهِ هَلْ تَعْلَمُ لَهُ سَمِيًّا");
        sor.Ayaat.Add("وَ يَقُولُ الْإِنْسٰانُ أَ إِذٰا مٰا مِتُّ لَسَوْفَ أُخْرَجُ حَيًّا");
        sor.Ayaat.Add("أَ وَ لاٰ يَذْكُرُ الْإِنْسٰانُ أَنّٰا خَلَقْنٰاهُ مِنْ قَبْلُ وَ لَمْ يَكُ شَيْئاً");
        sor.Ayaat.Add("فَوَ رَبِّكَ لَنَحْشُرَنَّهُمْ وَ الشَّيٰاطِينَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا");
        sor.Ayaat.Add("ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيعَةٍ أَيُّهُمْ أَشَدُّ عَلَى الرَّحْمٰنِ عِتِيًّا");
        sor.Ayaat.Add("ثُمَّ لَنَحْنُ أَعْلَمُ بِالَّذِينَ هُمْ أَوْلىٰ بِهٰا صِلِيًّا");
        sor.Ayaat.Add("وَ إِنْ مِنْكُمْ إِلاّٰ وٰارِدُهٰا كٰانَ عَلىٰ رَبِّكَ حَتْماً مَقْضِيًّا");
        sor.Ayaat.Add("ثُمَّ نُنَجِّي الَّذِينَ اتَّقَوْا وَ نَذَرُ الظّٰالِمِينَ فِيهٰا جِثِيًّا");
        sor.Ayaat.Add("وَ إِذٰا تُتْلىٰ عَلَيْهِمْ آيٰاتُنٰا بَيِّنٰاتٍ قٰالَ الَّذِينَ كَفَرُوا لِلَّذِينَ آمَنُوا أَيُّ الْفَرِيقَيْنِ خَيْرٌ مَقٰاماً وَ أَحْسَنُ نَدِيًّا");
        sor.Ayaat.Add("وَ كَمْ أَهْلَكْنٰا قَبْلَهُمْ مِنْ قَرْنٍ هُمْ أَحْسَنُ أَثٰاثاً وَ رِءْياً");
        sor.Ayaat.Add("قُلْ مَنْ كٰانَ فِي الضَّلاٰلَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَدًّا حَتّٰى إِذٰا رَأَوْا مٰا يُوعَدُونَ إِمَّا الْعَذٰابَ وَ إِمَّا السّٰاعَةَ فَسَيَعْلَمُونَ مَنْ هُوَ شَرٌّ مَكٰاناً وَ أَضْعَفُ جُنْداً");
        sor.Ayaat.Add("وَ يَزِيدُ اللّٰهُ الَّذِينَ اهْتَدَوْا هُدىً وَ الْبٰاقِيٰاتُ الصّٰالِحٰاتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوٰاباً وَ خَيْرٌ مَرَدًّا");
        sor.Ayaat.Add("أَ فَرَأَيْتَ الَّذِي كَفَرَ بِآيٰاتِنٰا وَ قٰالَ لَأُوتَيَنَّ مٰالاً وَ وَلَداً");
        sor.Ayaat.Add("أَطَّلَعَ الْغَيْبَ أَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْداً");
        sor.Ayaat.Add("كَلاّٰ سَنَكْتُبُ مٰا يَقُولُ وَ نَمُدُّ لَهُ مِنَ الْعَذٰابِ مَدًّا");
        sor.Ayaat.Add("وَ نَرِثُهُ مٰا يَقُولُ وَ يَأْتِينٰا فَرْداً");
        sor.Ayaat.Add("وَ اتَّخَذُوا مِنْ دُونِ اللّٰهِ آلِهَةً لِيَكُونُوا لَهُمْ عِزًّا");
        sor.Ayaat.Add("كَلاّٰ سَيَكْفُرُونَ بِعِبٰادَتِهِمْ وَ يَكُونُونَ عَلَيْهِمْ ضِدًّا");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنّٰا أَرْسَلْنَا الشَّيٰاطِينَ عَلَى الْكٰافِرِينَ تَؤُزُّهُمْ أَزًّا");
        sor.Ayaat.Add("فَلاٰ تَعْجَلْ عَلَيْهِمْ إِنَّمٰا نَعُدُّ لَهُمْ عَدًّا");
        sor.Ayaat.Add("يَوْمَ نَحْشُرُ الْمُتَّقِينَ إِلَى الرَّحْمٰنِ وَفْداً");
        sor.Ayaat.Add("وَ نَسُوقُ الْمُجْرِمِينَ إِلىٰ جَهَنَّمَ وِرْداً");
        sor.Ayaat.Add("لاٰ يَمْلِكُونَ الشَّفٰاعَةَ إِلاّٰ مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْداً");
        sor.Ayaat.Add("وَ قٰالُوا اتَّخَذَ الرَّحْمٰنُ وَلَداً");
        sor.Ayaat.Add("لَقَدْ جِئْتُمْ شَيْئاً إِدًّا");
        sor.Ayaat.Add("تَكٰادُ السَّمٰاوٰاتُ يَتَفَطَّرْنَ مِنْهُ وَ تَنْشَقُّ الْأَرْضُ وَ تَخِرُّ الْجِبٰالُ هَدًّا");
        sor.Ayaat.Add("أَنْ دَعَوْا لِلرَّحْمٰنِ وَلَداً");
        sor.Ayaat.Add("وَ مٰا يَنْبَغِي لِلرَّحْمٰنِ أَنْ يَتَّخِذَ وَلَداً");
        sor.Ayaat.Add("إِنْ كُلُّ مَنْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ إِلاّٰ آتِي الرَّحْمٰنِ عَبْداً");
        sor.Ayaat.Add("لَقَدْ أَحْصٰاهُمْ وَ عَدَّهُمْ عَدًّا");
        sor.Ayaat.Add("وَ كُلُّهُمْ آتِيهِ يَوْمَ الْقِيٰامَةِ فَرْداً");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُدًّا");
        sor.Ayaat.Add("فَإِنَّمٰا يَسَّرْنٰاهُ بِلِسٰانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِينَ وَ تُنْذِرَ بِهِ قَوْماً لُدًّا");
        sor.Ayaat.Add("وَ كَمْ أَهْلَكْنٰا قَبْلَهُمْ مِنْ قَرْنٍ هَلْ تُحِسُّ مِنْهُمْ مِنْ أَحَدٍ أَوْ تَسْمَعُ لَهُمْ رِكْزاً");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢٠ - طه";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ طه");
        sor.Ayaat.Add("مٰا أَنْزَلْنٰا عَلَيْكَ الْقُرْآنَ لِتَشْقىٰ");
        sor.Ayaat.Add("إِلاّٰ تَذْكِرَةً لِمَنْ يَخْشىٰ");
        sor.Ayaat.Add("تَنْزِيلاً مِمَّنْ خَلَقَ الْأَرْضَ وَ السَّمٰاوٰاتِ الْعُلىٰ");
        sor.Ayaat.Add("الرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوىٰ");
        sor.Ayaat.Add("لَهُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ مٰا بَيْنَهُمٰا وَ مٰا تَحْتَ الثَّرىٰ");
        sor.Ayaat.Add("وَ إِنْ تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَ أَخْفىٰ");
        sor.Ayaat.Add("اللّٰهُ لاٰ إِلٰهَ إِلاّٰ هُوَ لَهُ الْأَسْمٰاءُ الْحُسْنىٰ");
        sor.Ayaat.Add("وَ هَلْ أَتٰاكَ حَدِيثُ مُوسىٰ");
        sor.Ayaat.Add("إِذْ رَأىٰ نٰاراً فَقٰالَ لِأَهْلِهِ امْكُثُوا إِنِّي آنَسْتُ نٰاراً لَعَلِّي آتِيكُمْ مِنْهٰا بِقَبَسٍ أَوْ أَجِدُ عَلَى النّٰارِ هُدىً");
        sor.Ayaat.Add("فَلَمّٰا أَتٰاهٰا نُودِيَ يٰا مُوسىٰ");
        sor.Ayaat.Add("إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ إِنَّكَ بِالْوٰادِ الْمُقَدَّسِ طُوىً");
        sor.Ayaat.Add("وَ أَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمٰا يُوحىٰ");
        sor.Ayaat.Add("إِنَّنِي أَنَا اللّٰهُ لاٰ إِلٰهَ إِلاّٰ أَنَا فَاعْبُدْنِي وَ أَقِمِ الصَّلاٰةَ لِذِكْرِي");
        sor.Ayaat.Add("إِنَّ السّٰاعَةَ آتِيَةٌ أَكٰادُ أُخْفِيهٰا لِتُجْزىٰ كُلُّ نَفْسٍ بِمٰا تَسْعىٰ");
        sor.Ayaat.Add("فَلاٰ يَصُدَّنَّكَ عَنْهٰا مَنْ لاٰ يُؤْمِنُ بِهٰا وَ اتَّبَعَ هَوٰاهُ فَتَرْدىٰ");
        sor.Ayaat.Add("وَ مٰا تِلْكَ بِيَمِينِكَ يٰا مُوسىٰ");
        sor.Ayaat.Add("قٰالَ هِيَ عَصٰايَ أَتَوَكَّؤُا عَلَيْهٰا وَ أَهُشُّ بِهٰا عَلىٰ غَنَمِي وَ لِيَ فِيهٰا مَآرِبُ أُخْرىٰ");
        sor.Ayaat.Add("قٰالَ أَلْقِهٰا يٰا مُوسىٰ");
        sor.Ayaat.Add("فَأَلْقٰاهٰا فَإِذٰا هِيَ حَيَّةٌ تَسْعىٰ");
        sor.Ayaat.Add("قٰالَ خُذْهٰا وَ لاٰ تَخَفْ سَنُعِيدُهٰا سِيرَتَهَا الْأُولىٰ");
        sor.Ayaat.Add("وَ اضْمُمْ يَدَكَ إِلىٰ جَنٰاحِكَ تَخْرُجْ بَيْضٰاءَ مِنْ غَيْرِ سُوءٍ آيَةً أُخْرىٰ");
        sor.Ayaat.Add("لِنُرِيَكَ مِنْ آيٰاتِنَا الْكُبْرىٰ");
        sor.Ayaat.Add("اذْهَبْ إِلىٰ فِرْعَوْنَ إِنَّهُ طَغىٰ");
        sor.Ayaat.Add("قٰالَ رَبِّ اشْرَحْ لِي صَدْرِي");
        sor.Ayaat.Add("وَ يَسِّرْ لِي أَمْرِي");
        sor.Ayaat.Add("وَ احْلُلْ عُقْدَةً مِنْ لِسٰانِي");
        sor.Ayaat.Add("يَفْقَهُوا قَوْلِي");
        sor.Ayaat.Add("وَ اجْعَلْ لِي وَزِيراً مِنْ أَهْلِي");
        sor.Ayaat.Add("هٰارُونَ أَخِي");
        sor.Ayaat.Add("اشْدُدْ بِهِ أَزْرِي");
        sor.Ayaat.Add("وَ أَشْرِكْهُ فِي أَمْرِي");
        sor.Ayaat.Add("كَيْ نُسَبِّحَكَ كَثِيراً");
        sor.Ayaat.Add("وَ نَذْكُرَكَ كَثِيراً");
        sor.Ayaat.Add("إِنَّكَ كُنْتَ بِنٰا بَصِيراً");
        sor.Ayaat.Add("قٰالَ قَدْ أُوتِيتَ سُؤْلَكَ يٰا مُوسىٰ");
        sor.Ayaat.Add("وَ لَقَدْ مَنَنّٰا عَلَيْكَ مَرَّةً أُخْرىٰ");
        sor.Ayaat.Add("إِذْ أَوْحَيْنٰا إِلىٰ أُمِّكَ مٰا يُوحىٰ");
        sor.Ayaat.Add("أَنِ اقْذِفِيهِ فِي التّٰابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسّٰاحِلِ يَأْخُذْهُ عَدُوٌّ لِي وَ عَدُوٌّ لَهُ وَ أَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي وَ لِتُصْنَعَ عَلىٰ عَيْنِي");
        sor.Ayaat.Add("إِذْ تَمْشِي أُخْتُكَ فَتَقُولُ هَلْ أَدُلُّكُمْ عَلىٰ مَنْ يَكْفُلُهُ فَرَجَعْنٰاكَ إِلىٰ أُمِّكَ كَيْ تَقَرَّ عَيْنُهٰا وَ لاٰ تَحْزَنَ وَ قَتَلْتَ نَفْساً فَنَجَّيْنٰاكَ مِنَ الْغَمِّ وَ فَتَنّٰاكَ فُتُوناً فَلَبِثْتَ سِنِينَ فِي أَهْلِ مَدْيَنَ ثُمَّ جِئْتَ عَلىٰ قَدَرٍ يٰا مُوسىٰ");
        sor.Ayaat.Add("وَ اصْطَنَعْتُكَ لِنَفْسِي");
        sor.Ayaat.Add("اذْهَبْ أَنْتَ وَ أَخُوكَ بِآيٰاتِي وَ لاٰ تَنِيٰا فِي ذِكْرِي");
        sor.Ayaat.Add("اذْهَبٰا إِلىٰ فِرْعَوْنَ إِنَّهُ طَغىٰ");
        sor.Ayaat.Add("فَقُولاٰ لَهُ قَوْلاً لَيِّناً لَعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشىٰ");
        sor.Ayaat.Add("قٰالاٰ رَبَّنٰا إِنَّنٰا نَخٰافُ أَنْ يَفْرُطَ عَلَيْنٰا أَوْ أَنْ يَطْغىٰ");
        sor.Ayaat.Add("قٰالَ لاٰ تَخٰافٰا إِنَّنِي مَعَكُمٰا أَسْمَعُ وَ أَرىٰ");
        sor.Ayaat.Add("فَأْتِيٰاهُ فَقُولاٰ إِنّٰا رَسُولاٰ رَبِّكَ فَأَرْسِلْ مَعَنٰا بَنِي إِسْرٰائِيلَ وَ لاٰ تُعَذِّبْهُمْ قَدْ جِئْنٰاكَ بِآيَةٍ مِنْ رَبِّكَ وَ السَّلاٰمُ عَلىٰ مَنِ اتَّبَعَ الْهُدىٰ");
        sor.Ayaat.Add("إِنّٰا قَدْ أُوحِيَ إِلَيْنٰا أَنَّ الْعَذٰابَ عَلىٰ مَنْ كَذَّبَ وَ تَوَلّٰى");
        sor.Ayaat.Add("قٰالَ فَمَنْ رَبُّكُمٰا يٰا مُوسىٰ");
        sor.Ayaat.Add("قٰالَ رَبُّنَا الَّذِي أَعْطىٰ كُلَّ شَيْءٍ خَلْقَهُ ثُمَّ هَدىٰ");
        sor.Ayaat.Add("قٰالَ فَمٰا بٰالُ الْقُرُونِ الْأُولىٰ");
        sor.Ayaat.Add("قٰالَ عِلْمُهٰا عِنْدَ رَبِّي فِي كِتٰابٍ لاٰ يَضِلُّ رَبِّي وَ لاٰ يَنْسىٰ");
        sor.Ayaat.Add("الَّذِي جَعَلَ لَكُمُ الْأَرْضَ مَهْداً وَ سَلَكَ لَكُمْ فِيهٰا سُبُلاً وَ أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَأَخْرَجْنٰا بِهِ أَزْوٰاجاً مِنْ نَبٰاتٍ شَتّٰى");
        sor.Ayaat.Add("كُلُوا وَ ارْعَوْا أَنْعٰامَكُمْ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِأُولِي النُّهىٰ");
        sor.Ayaat.Add("مِنْهٰا خَلَقْنٰاكُمْ وَ فِيهٰا نُعِيدُكُمْ وَ مِنْهٰا نُخْرِجُكُمْ تٰارَةً أُخْرىٰ");
        sor.Ayaat.Add("وَ لَقَدْ أَرَيْنٰاهُ آيٰاتِنٰا كُلَّهٰا فَكَذَّبَ وَ أَبىٰ");
        sor.Ayaat.Add("قٰالَ أَ جِئْتَنٰا لِتُخْرِجَنٰا مِنْ أَرْضِنٰا بِسِحْرِكَ يٰا مُوسىٰ");
        sor.Ayaat.Add("فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِثْلِهِ فَاجْعَلْ بَيْنَنٰا وَ بَيْنَكَ مَوْعِداً لاٰ نُخْلِفُهُ نَحْنُ وَ لاٰ أَنْتَ مَكٰاناً سُوىً");
        sor.Ayaat.Add("قٰالَ مَوْعِدُكُمْ يَوْمُ الزِّينَةِ وَ أَنْ يُحْشَرَ النّٰاسُ ضُحًى");
        sor.Ayaat.Add("فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهُ ثُمَّ أَتىٰ");
        sor.Ayaat.Add("قٰالَ لَهُمْ مُوسىٰ وَيْلَكُمْ لاٰ تَفْتَرُوا عَلَى اللّٰهِ كَذِباً فَيُسْحِتَكُمْ بِعَذٰابٍ وَ قَدْ خٰابَ مَنِ افْتَرىٰ");
        sor.Ayaat.Add("فَتَنٰازَعُوا أَمْرَهُمْ بَيْنَهُمْ وَ أَسَرُّوا النَّجْوىٰ");
        sor.Ayaat.Add("قٰالُوا إِنْ هٰذٰانِ لَسٰاحِرٰانِ يُرِيدٰانِ أَنْ يُخْرِجٰاكُمْ مِنْ أَرْضِكُمْ بِسِحْرِهِمٰا وَ يَذْهَبٰا بِطَرِيقَتِكُمُ الْمُثْلىٰ");
        sor.Ayaat.Add("فَأَجْمِعُوا كَيْدَكُمْ ثُمَّ ائْتُوا صَفًّا وَ قَدْ أَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلىٰ");
        sor.Ayaat.Add("قٰالُوا يٰا مُوسىٰ إِمّٰا أَنْ تُلْقِيَ وَ إِمّٰا أَنْ نَكُونَ أَوَّلَ مَنْ أَلْقىٰ");
        sor.Ayaat.Add("قٰالَ بَلْ أَلْقُوا فَإِذٰا حِبٰالُهُمْ وَ عِصِيُّهُمْ يُخَيَّلُ إِلَيْهِ مِنْ سِحْرِهِمْ أَنَّهٰا تَسْعىٰ");
        sor.Ayaat.Add("فَأَوْجَسَ فِي نَفْسِهِ خِيفَةً مُوسىٰ");
        sor.Ayaat.Add("قُلْنٰا لاٰ تَخَفْ إِنَّكَ أَنْتَ الْأَعْلىٰ");
        sor.Ayaat.Add("وَ أَلْقِ مٰا فِي يَمِينِكَ تَلْقَفْ مٰا صَنَعُوا إِنَّمٰا صَنَعُوا كَيْدُ سٰاحِرٍ وَ لاٰ يُفْلِحُ السّٰاحِرُ حَيْثُ أَتىٰ");
        sor.Ayaat.Add("فَأُلْقِيَ السَّحَرَةُ سُجَّداً قٰالُوا آمَنّٰا بِرَبِّ هٰارُونَ وَ مُوسىٰ");
        sor.Ayaat.Add("قٰالَ آمَنْتُمْ لَهُ قَبْلَ أَنْ آذَنَ لَكُمْ إِنَّهُ لَكَبِيرُكُمُ الَّذِي عَلَّمَكُمُ السِّحْرَ فَلَأُقَطِّعَنَّ أَيْدِيَكُمْ وَ أَرْجُلَكُمْ مِنْ خِلاٰفٍ وَ لَأُصَلِّبَنَّكُمْ فِي جُذُوعِ النَّخْلِ وَ لَتَعْلَمُنَّ أَيُّنٰا أَشَدُّ عَذٰاباً وَ أَبْقىٰ");
        sor.Ayaat.Add("قٰالُوا لَنْ نُؤْثِرَكَ عَلىٰ مٰا جٰاءَنٰا مِنَ الْبَيِّنٰاتِ وَ الَّذِي فَطَرَنٰا فَاقْضِ مٰا أَنْتَ قٰاضٍ إِنَّمٰا تَقْضِي هٰذِهِ الْحَيٰاةَ الدُّنْيٰا");
        sor.Ayaat.Add("إِنّٰا آمَنّٰا بِرَبِّنٰا لِيَغْفِرَ لَنٰا خَطٰايٰانٰا وَ مٰا أَكْرَهْتَنٰا عَلَيْهِ مِنَ السِّحْرِ وَ اللّٰهُ خَيْرٌ وَ أَبْقىٰ");
        sor.Ayaat.Add("إِنَّهُ مَنْ يَأْتِ رَبَّهُ مُجْرِماً فَإِنَّ لَهُ جَهَنَّمَ لاٰ يَمُوتُ فِيهٰا وَ لاٰ يَحْيىٰ");
        sor.Ayaat.Add("وَ مَنْ يَأْتِهِ مُؤْمِناً قَدْ عَمِلَ الصّٰالِحٰاتِ فَأُولٰئِكَ لَهُمُ الدَّرَجٰاتُ الْعُلىٰ");
        sor.Ayaat.Add("جَنّٰاتُ عَدْنٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ خٰالِدِينَ فِيهٰا وَ ذٰلِكَ جَزٰاءُ مَنْ تَزَكّٰى");
        sor.Ayaat.Add("وَ لَقَدْ أَوْحَيْنٰا إِلىٰ مُوسىٰ أَنْ أَسْرِ بِعِبٰادِي فَاضْرِبْ لَهُمْ طَرِيقاً فِي الْبَحْرِ يَبَساً لاٰ تَخٰافُ دَرَكاً وَ لاٰ تَخْشىٰ");
        sor.Ayaat.Add("فَأَتْبَعَهُمْ فِرْعَوْنُ بِجُنُودِهِ فَغَشِيَهُمْ مِنَ الْيَمِّ مٰا غَشِيَهُمْ");
        sor.Ayaat.Add("وَ أَضَلَّ فِرْعَوْنُ قَوْمَهُ وَ مٰا هَدىٰ");
        sor.Ayaat.Add("يٰا بَنِي إِسْرٰائِيلَ قَدْ أَنْجَيْنٰاكُمْ مِنْ عَدُوِّكُمْ وَ وٰاعَدْنٰاكُمْ جٰانِبَ الطُّورِ الْأَيْمَنَ وَ نَزَّلْنٰا عَلَيْكُمُ الْمَنَّ وَ السَّلْوىٰ");
        sor.Ayaat.Add("كُلُوا مِنْ طَيِّبٰاتِ مٰا رَزَقْنٰاكُمْ وَ لاٰ تَطْغَوْا فِيهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِي وَ مَنْ يَحْلِلْ عَلَيْهِ غَضَبِي فَقَدْ هَوىٰ");
        sor.Ayaat.Add("وَ إِنِّي لَغَفّٰارٌ لِمَنْ تٰابَ وَ آمَنَ وَ عَمِلَ صٰالِحاً ثُمَّ اهْتَدىٰ");
        sor.Ayaat.Add("وَ مٰا أَعْجَلَكَ عَنْ قَوْمِكَ يٰا مُوسىٰ");
        sor.Ayaat.Add("قٰالَ هُمْ أُولاٰءِ عَلىٰ أَثَرِي وَ عَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضىٰ");
        sor.Ayaat.Add("قٰالَ فَإِنّٰا قَدْ فَتَنّٰا قَوْمَكَ مِنْ بَعْدِكَ وَ أَضَلَّهُمُ السّٰامِرِيُّ");
        sor.Ayaat.Add("فَرَجَعَ مُوسىٰ إِلىٰ قَوْمِهِ غَضْبٰانَ أَسِفاً قٰالَ يٰا قَوْمِ أَ لَمْ يَعِدْكُمْ رَبُّكُمْ وَعْداً حَسَناً أَ فَطٰالَ عَلَيْكُمُ الْعَهْدُ أَمْ أَرَدْتُمْ أَنْ يَحِلَّ عَلَيْكُمْ غَضَبٌ مِنْ رَبِّكُمْ فَأَخْلَفْتُمْ مَوْعِدِي");
        sor.Ayaat.Add("قٰالُوا مٰا أَخْلَفْنٰا مَوْعِدَكَ بِمَلْكِنٰا وَ لٰكِنّٰا حُمِّلْنٰا أَوْزٰاراً مِنْ زِينَةِ الْقَوْمِ فَقَذَفْنٰاهٰا فَكَذٰلِكَ أَلْقَى السّٰامِرِيُّ");
        sor.Ayaat.Add("فَأَخْرَجَ لَهُمْ عِجْلاً جَسَداً لَهُ خُوٰارٌ فَقٰالُوا هٰذٰا إِلٰهُكُمْ وَ إِلٰهُ مُوسىٰ فَنَسِيَ");
        sor.Ayaat.Add("أَ فَلاٰ يَرَوْنَ أَلاّٰ يَرْجِعُ إِلَيْهِمْ قَوْلاً وَ لاٰ يَمْلِكُ لَهُمْ ضَرًّا وَ لاٰ نَفْعاً");
        sor.Ayaat.Add("وَ لَقَدْ قٰالَ لَهُمْ هٰارُونُ مِنْ قَبْلُ يٰا قَوْمِ إِنَّمٰا فُتِنْتُمْ بِهِ وَ إِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُونِي وَ أَطِيعُوا أَمْرِي");
        sor.Ayaat.Add("قٰالُوا لَنْ نَبْرَحَ عَلَيْهِ عٰاكِفِينَ حَتّٰى يَرْجِعَ إِلَيْنٰا مُوسىٰ");
        sor.Ayaat.Add("قٰالَ يٰا هٰارُونُ مٰا مَنَعَكَ إِذْ رَأَيْتَهُمْ ضَلُّوا");
        sor.Ayaat.Add("أَلاّٰ تَتَّبِعَنِ أَ فَعَصَيْتَ أَمْرِي");
        sor.Ayaat.Add("قٰالَ يَا بْنَ أُمَّ لاٰ تَأْخُذْ بِلِحْيَتِي وَ لاٰ بِرَأْسِي إِنِّي خَشِيتُ أَنْ تَقُولَ فَرَّقْتَ بَيْنَ بَنِي إِسْرٰائِيلَ وَ لَمْ تَرْقُبْ قَوْلِي");
        sor.Ayaat.Add("قٰالَ فَمٰا خَطْبُكَ يٰا سٰامِرِيُّ");
        sor.Ayaat.Add("قٰالَ بَصُرْتُ بِمٰا لَمْ يَبْصُرُوا بِهِ فَقَبَضْتُ قَبْضَةً مِنْ أَثَرِ الرَّسُولِ فَنَبَذْتُهٰا وَ كَذٰلِكَ سَوَّلَتْ لِي نَفْسِي");
        sor.Ayaat.Add("قٰالَ فَاذْهَبْ فَإِنَّ لَكَ فِي الْحَيٰاةِ أَنْ تَقُولَ لاٰ مِسٰاسَ وَ إِنَّ لَكَ مَوْعِداً لَنْ تُخْلَفَهُ وَ انْظُرْ إِلىٰ إِلٰهِكَ الَّذِي ظَلْتَ عَلَيْهِ عٰاكِفاً لَنُحَرِّقَنَّهُ ثُمَّ لَنَنْسِفَنَّهُ فِي الْيَمِّ نَسْفاً");
        sor.Ayaat.Add("إِنَّمٰا إِلٰهُكُمُ اللّٰهُ الَّذِي لاٰ إِلٰهَ إِلاّٰ هُوَ وَسِعَ كُلَّ شَيْءٍ عِلْماً");
        sor.Ayaat.Add("كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ أَنْبٰاءِ مٰا قَدْ سَبَقَ وَ قَدْ آتَيْنٰاكَ مِنْ لَدُنّٰا ذِكْراً");
        sor.Ayaat.Add("مَنْ أَعْرَضَ عَنْهُ فَإِنَّهُ يَحْمِلُ يَوْمَ الْقِيٰامَةِ وِزْراً");
        sor.Ayaat.Add("خٰالِدِينَ فِيهِ وَ سٰاءَ لَهُمْ يَوْمَ الْقِيٰامَةِ حِمْلاً");
        sor.Ayaat.Add("يَوْمَ يُنْفَخُ فِي الصُّورِ وَ نَحْشُرُ الْمُجْرِمِينَ يَوْمَئِذٍ زُرْقاً");
        sor.Ayaat.Add("يَتَخٰافَتُونَ بَيْنَهُمْ إِنْ لَبِثْتُمْ إِلاّٰ عَشْراً");
        sor.Ayaat.Add("نَحْنُ أَعْلَمُ بِمٰا يَقُولُونَ إِذْ يَقُولُ أَمْثَلُهُمْ طَرِيقَةً إِنْ لَبِثْتُمْ إِلاّٰ يَوْماً");
        sor.Ayaat.Add("وَ يَسْئَلُونَكَ عَنِ الْجِبٰالِ فَقُلْ يَنْسِفُهٰا رَبِّي نَسْفاً");
        sor.Ayaat.Add("فَيَذَرُهٰا قٰاعاً صَفْصَفاً");
        sor.Ayaat.Add("لاٰ تَرىٰ فِيهٰا عِوَجاً وَ لاٰ أَمْتاً");
        sor.Ayaat.Add("يَوْمَئِذٍ يَتَّبِعُونَ الدّٰاعِيَ لاٰ عِوَجَ لَهُ وَ خَشَعَتِ الْأَصْوٰاتُ لِلرَّحْمٰنِ فَلاٰ تَسْمَعُ إِلاّٰ هَمْساً");
        sor.Ayaat.Add("يَوْمَئِذٍ لاٰ تَنْفَعُ الشَّفٰاعَةُ إِلاّٰ مَنْ أَذِنَ لَهُ الرَّحْمٰنُ وَ رَضِيَ لَهُ قَوْلاً");
        sor.Ayaat.Add("يَعْلَمُ مٰا بَيْنَ أَيْدِيهِمْ وَ مٰا خَلْفَهُمْ وَ لاٰ يُحِيطُونَ بِهِ عِلْماً");
        sor.Ayaat.Add("وَ عَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ وَ قَدْ خٰابَ مَنْ حَمَلَ ظُلْماً");
        sor.Ayaat.Add("وَ مَنْ يَعْمَلْ مِنَ الصّٰالِحٰاتِ وَ هُوَ مُؤْمِنٌ فَلاٰ يَخٰافُ ظُلْماً وَ لاٰ هَضْماً");
        sor.Ayaat.Add("وَ كَذٰلِكَ أَنْزَلْنٰاهُ قُرْآناً عَرَبِيًّا وَ صَرَّفْنٰا فِيهِ مِنَ الْوَعِيدِ لَعَلَّهُمْ يَتَّقُونَ أَوْ يُحْدِثُ لَهُمْ ذِكْراً");
        sor.Ayaat.Add("فَتَعٰالَى اللّٰهُ الْمَلِكُ الْحَقُّ وَ لاٰ تَعْجَلْ بِالْقُرْآنِ مِنْ قَبْلِ أَنْ يُقْضىٰ إِلَيْكَ وَحْيُهُ وَ قُلْ رَبِّ زِدْنِي عِلْماً");
        sor.Ayaat.Add("وَ لَقَدْ عَهِدْنٰا إِلىٰ آدَمَ مِنْ قَبْلُ فَنَسِيَ وَ لَمْ نَجِدْ لَهُ عَزْماً");
        sor.Ayaat.Add("وَ إِذْ قُلْنٰا لِلْمَلاٰئِكَةِ اسْجُدُوا لِآدَمَ فَسَجَدُوا إِلاّٰ إِبْلِيسَ أَبىٰ");
        sor.Ayaat.Add("فَقُلْنٰا يٰا آدَمُ إِنَّ هٰذٰا عَدُوٌّ لَكَ وَ لِزَوْجِكَ فَلاٰ يُخْرِجَنَّكُمٰا مِنَ الْجَنَّةِ فَتَشْقىٰ");
        sor.Ayaat.Add("إِنَّ لَكَ أَلاّٰ تَجُوعَ فِيهٰا وَ لاٰ تَعْرىٰ");
        sor.Ayaat.Add("وَ أَنَّكَ لاٰ تَظْمَؤُا فِيهٰا وَ لاٰ تَضْحىٰ");
        sor.Ayaat.Add("فَوَسْوَسَ إِلَيْهِ الشَّيْطٰانُ قٰالَ يٰا آدَمُ هَلْ أَدُلُّكَ عَلىٰ شَجَرَةِ الْخُلْدِ وَ مُلْكٍ لاٰ يَبْلىٰ");
        sor.Ayaat.Add("فَأَكَلاٰ مِنْهٰا فَبَدَتْ لَهُمٰا سَوْآتُهُمٰا وَ طَفِقٰا يَخْصِفٰانِ عَلَيْهِمٰا مِنْ وَرَقِ الْجَنَّةِ وَ عَصىٰ آدَمُ رَبَّهُ فَغَوىٰ");
        sor.Ayaat.Add("ثُمَّ اجْتَبٰاهُ رَبُّهُ فَتٰابَ عَلَيْهِ وَ هَدىٰ");
        sor.Ayaat.Add("قٰالَ اهْبِطٰا مِنْهٰا جَمِيعاً بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ فَإِمّٰا يَأْتِيَنَّكُمْ مِنِّي هُدىً فَمَنِ اتَّبَعَ هُدٰايَ فَلاٰ يَضِلُّ وَ لاٰ يَشْقىٰ");
        sor.Ayaat.Add("وَ مَنْ أَعْرَضَ عَنْ ذِكْرِي فَإِنَّ لَهُ مَعِيشَةً ضَنْكاً وَ نَحْشُرُهُ يَوْمَ الْقِيٰامَةِ أَعْمىٰ");
        sor.Ayaat.Add("قٰالَ رَبِّ لِمَ حَشَرْتَنِي أَعْمىٰ وَ قَدْ كُنْتُ بَصِيراً");
        sor.Ayaat.Add("قٰالَ كَذٰلِكَ أَتَتْكَ آيٰاتُنٰا فَنَسِيتَهٰا وَ كَذٰلِكَ الْيَوْمَ تُنْسىٰ");
        sor.Ayaat.Add("وَ كَذٰلِكَ نَجْزِي مَنْ أَسْرَفَ وَ لَمْ يُؤْمِنْ بِآيٰاتِ رَبِّهِ وَ لَعَذٰابُ الْآخِرَةِ أَشَدُّ وَ أَبْقىٰ");
        sor.Ayaat.Add("أَ فَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنٰا قَبْلَهُمْ مِنَ الْقُرُونِ يَمْشُونَ فِي مَسٰاكِنِهِمْ إِنَّ فِي ذٰلِكَ لَآيٰاتٍ لِأُولِي النُّهىٰ");
        sor.Ayaat.Add("وَ لَوْ لاٰ كَلِمَةٌ سَبَقَتْ مِنْ رَبِّكَ لَكٰانَ لِزٰاماً وَ أَجَلٌ مُسَمًّى");
        sor.Ayaat.Add("فَاصْبِرْ عَلىٰ مٰا يَقُولُونَ وَ سَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ الشَّمْسِ وَ قَبْلَ غُرُوبِهٰا وَ مِنْ آنٰاءِ اللَّيْلِ فَسَبِّحْ وَ أَطْرٰافَ النَّهٰارِ لَعَلَّكَ تَرْضىٰ");
        sor.Ayaat.Add("وَ لاٰ تَمُدَّنَّ عَيْنَيْكَ إِلىٰ مٰا مَتَّعْنٰا بِهِ أَزْوٰاجاً مِنْهُمْ زَهْرَةَ الْحَيٰاةِ الدُّنْيٰا لِنَفْتِنَهُمْ فِيهِ وَ رِزْقُ رَبِّكَ خَيْرٌ وَ أَبْقىٰ");
        sor.Ayaat.Add("وَ أْمُرْ أَهْلَكَ بِالصَّلاٰةِ وَ اصْطَبِرْ عَلَيْهٰا لاٰ نَسْئَلُكَ رِزْقاً نَحْنُ نَرْزُقُكَ وَ الْعٰاقِبَةُ لِلتَّقْوىٰ");
        sor.Ayaat.Add("وَ قٰالُوا لَوْ لاٰ يَأْتِينٰا بِآيَةٍ مِنْ رَبِّهِ أَ وَ لَمْ تَأْتِهِمْ بَيِّنَةُ مٰا فِي الصُّحُفِ الْأُولىٰ");
        sor.Ayaat.Add("وَ لَوْ أَنّٰا أَهْلَكْنٰاهُمْ بِعَذٰابٍ مِنْ قَبْلِهِ لَقٰالُوا رَبَّنٰا لَوْ لاٰ أَرْسَلْتَ إِلَيْنٰا رَسُولاً فَنَتَّبِعَ آيٰاتِكَ مِنْ قَبْلِ أَنْ نَذِلَّ وَ نَخْزىٰ");
        sor.Ayaat.Add("قُلْ كُلٌّ مُتَرَبِّصٌ فَتَرَبَّصُوا فَسَتَعْلَمُونَ مَنْ أَصْحٰابُ الصِّرٰاطِ السَّوِيِّ وَ مَنِ اهْتَدىٰ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢١ - الأنبیاء";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ اقْتَرَبَ لِلنّٰاسِ حِسٰابُهُمْ وَ هُمْ فِي غَفْلَةٍ مُعْرِضُونَ");
        sor.Ayaat.Add("مٰا يَأْتِيهِمْ مِنْ ذِكْرٍ مِنْ رَبِّهِمْ مُحْدَثٍ إِلاَّ اسْتَمَعُوهُ وَ هُمْ يَلْعَبُونَ");
        sor.Ayaat.Add("لاٰهِيَةً قُلُوبُهُمْ وَ أَسَرُّوا النَّجْوَى الَّذِينَ ظَلَمُوا هَلْ هٰذٰا إِلاّٰ بَشَرٌ مِثْلُكُمْ أَ فَتَأْتُونَ السِّحْرَ وَ أَنْتُمْ تُبْصِرُونَ");
        sor.Ayaat.Add("قٰالَ رَبِّي يَعْلَمُ الْقَوْلَ فِي السَّمٰاءِ وَ الْأَرْضِ وَ هُوَ السَّمِيعُ الْعَلِيمُ");
        sor.Ayaat.Add("بَلْ قٰالُوا أَضْغٰاثُ أَحْلاٰمٍ بَلِ افْتَرٰاهُ بَلْ هُوَ شٰاعِرٌ فَلْيَأْتِنٰا بِآيَةٍ كَمٰا أُرْسِلَ الْأَوَّلُونَ");
        sor.Ayaat.Add("مٰا آمَنَتْ قَبْلَهُمْ مِنْ قَرْيَةٍ أَهْلَكْنٰاهٰا أَ فَهُمْ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا قَبْلَكَ إِلاّٰ رِجٰالاً نُوحِي إِلَيْهِمْ فَسْئَلُوا أَهْلَ الذِّكْرِ إِنْ كُنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ مٰا جَعَلْنٰاهُمْ جَسَداً لاٰ يَأْكُلُونَ الطَّعٰامَ وَ مٰا كٰانُوا خٰالِدِينَ");
        sor.Ayaat.Add("ثُمَّ صَدَقْنٰاهُمُ الْوَعْدَ فَأَنْجَيْنٰاهُمْ وَ مَنْ نَشٰاءُ وَ أَهْلَكْنَا الْمُسْرِفِينَ");
        sor.Ayaat.Add("لَقَدْ أَنْزَلْنٰا إِلَيْكُمْ كِتٰاباً فِيهِ ذِكْرُكُمْ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("وَ كَمْ قَصَمْنٰا مِنْ قَرْيَةٍ كٰانَتْ ظٰالِمَةً وَ أَنْشَأْنٰا بَعْدَهٰا قَوْماً آخَرِينَ");
        sor.Ayaat.Add("فَلَمّٰا أَحَسُّوا بَأْسَنٰا إِذٰا هُمْ مِنْهٰا يَرْكُضُونَ");
        sor.Ayaat.Add("لاٰ تَرْكُضُوا وَ ارْجِعُوا إِلىٰ مٰا أُتْرِفْتُمْ فِيهِ وَ مَسٰاكِنِكُمْ لَعَلَّكُمْ تُسْئَلُونَ");
        sor.Ayaat.Add("قٰالُوا يٰا وَيْلَنٰا إِنّٰا كُنّٰا ظٰالِمِينَ");
        sor.Ayaat.Add("فَمٰا زٰاَلَتْ تِلْكَ دَعْوٰاهُمْ حَتّٰى جَعَلْنٰاهُمْ حَصِيداً خٰامِدِينَ");
        sor.Ayaat.Add("وَ مٰا خَلَقْنَا السَّمٰاءَ وَ الْأَرْضَ وَ مٰا بَيْنَهُمٰا لاٰعِبِينَ");
        sor.Ayaat.Add("لَوْ أَرَدْنٰا أَنْ نَتَّخِذَ لَهْواً لاَتَّخَذْنٰاهُ مِنْ لَدُنّٰا إِنْ كُنّٰا فٰاعِلِينَ");
        sor.Ayaat.Add("بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبٰاطِلِ فَيَدْمَغُهُ فَإِذٰا هُوَ زٰاهِقٌ وَ لَكُمُ الْوَيْلُ مِمّٰا تَصِفُونَ");
        sor.Ayaat.Add("وَ لَهُ مَنْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ وَ مَنْ عِنْدَهُ لاٰ يَسْتَكْبِرُونَ عَنْ عِبٰادَتِهِ وَ لاٰ يَسْتَحْسِرُونَ");
        sor.Ayaat.Add("يُسَبِّحُونَ اللَّيْلَ وَ النَّهٰارَ لاٰ يَفْتُرُونَ");
        sor.Ayaat.Add("أَمِ اتَّخَذُوا آلِهَةً مِنَ الْأَرْضِ هُمْ يُنْشِرُونَ");
        sor.Ayaat.Add("لَوْ كٰانَ فِيهِمٰا آلِهَةٌ إِلاَّ اللّٰهُ لَفَسَدَتٰا فَسُبْحٰانَ اللّٰهِ رَبِّ الْعَرْشِ عَمّٰا يَصِفُونَ");
        sor.Ayaat.Add("لاٰ يُسْئَلُ عَمّٰا يَفْعَلُ وَ هُمْ يُسْئَلُونَ");
        sor.Ayaat.Add("أَمِ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً قُلْ هٰاتُوا بُرْهٰانَكُمْ هٰذٰا ذِكْرُ مَنْ مَعِيَ وَ ذِكْرُ مَنْ قَبْلِي بَلْ أَكْثَرُهُمْ لاٰ يَعْلَمُونَ الْحَقَّ فَهُمْ مُعْرِضُونَ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا مِنْ قَبْلِكَ مِنْ رَسُولٍ إِلاّٰ نُوحِي إِلَيْهِ أَنَّهُ لاٰ إِلٰهَ إِلاّٰ أَنَا فَاعْبُدُونِ");
        sor.Ayaat.Add("وَ قٰالُوا اتَّخَذَ الرَّحْمٰنُ وَلَداً سُبْحٰانَهُ بَلْ عِبٰادٌ مُكْرَمُونَ");
        sor.Ayaat.Add("لاٰ يَسْبِقُونَهُ بِالْقَوْلِ وَ هُمْ بِأَمْرِهِ يَعْمَلُونَ");
        sor.Ayaat.Add("يَعْلَمُ مٰا بَيْنَ أَيْدِيهِمْ وَ مٰا خَلْفَهُمْ وَ لاٰ يَشْفَعُونَ إِلاّٰ لِمَنِ ارْتَضىٰ وَ هُمْ مِنْ خَشْيَتِهِ مُشْفِقُونَ");
        sor.Ayaat.Add("وَ مَنْ يَقُلْ مِنْهُمْ إِنِّي إِلٰهٌ مِنْ دُونِهِ فَذٰلِكَ نَجْزِيهِ جَهَنَّمَ كَذٰلِكَ نَجْزِي الظّٰالِمِينَ");
        sor.Ayaat.Add("أَ وَ لَمْ يَرَ الَّذِينَ كَفَرُوا أَنَّ السَّمٰاوٰاتِ وَ الْأَرْضَ كٰانَتٰا رَتْقاً فَفَتَقْنٰاهُمٰا وَ جَعَلْنٰا مِنَ الْمٰاءِ كُلَّ شَيْءٍ حَيٍّ أَ فَلاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ جَعَلْنٰا فِي الْأَرْضِ رَوٰاسِيَ أَنْ تَمِيدَ بِهِمْ وَ جَعَلْنٰا فِيهٰا فِجٰاجاً سُبُلاً لَعَلَّهُمْ يَهْتَدُونَ");
        sor.Ayaat.Add("وَ جَعَلْنَا السَّمٰاءَ سَقْفاً مَحْفُوظاً وَ هُمْ عَنْ آيٰاتِهٰا مُعْرِضُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي خَلَقَ اللَّيْلَ وَ النَّهٰارَ وَ الشَّمْسَ وَ الْقَمَرَ كُلٌّ فِي فَلَكٍ يَسْبَحُونَ");
        sor.Ayaat.Add("وَ مٰا جَعَلْنٰا لِبَشَرٍ مِنْ قَبْلِكَ الْخُلْدَ أَ فَإِنْ مِتَّ فَهُمُ الْخٰالِدُونَ");
        sor.Ayaat.Add("كُلُّ نَفْسٍ ذٰائِقَةُ الْمَوْتِ وَ نَبْلُوكُمْ بِالشَّرِّ وَ الْخَيْرِ فِتْنَةً وَ إِلَيْنٰا تُرْجَعُونَ");
        sor.Ayaat.Add("وَ إِذٰا رَآكَ الَّذِينَ كَفَرُوا إِنْ يَتَّخِذُونَكَ إِلاّٰ هُزُواً أَ هٰذَا الَّذِي يَذْكُرُ آلِهَتَكُمْ وَ هُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كٰافِرُونَ");
        sor.Ayaat.Add("خُلِقَ الْإِنْسٰانُ مِنْ عَجَلٍ سَأُرِيكُمْ آيٰاتِي فَلاٰ تَسْتَعْجِلُونِ");
        sor.Ayaat.Add("وَ يَقُولُونَ مَتىٰ هٰذَا الْوَعْدُ إِنْ كُنْتُمْ صٰادِقِينَ");
        sor.Ayaat.Add("لَوْ يَعْلَمُ الَّذِينَ كَفَرُوا حِينَ لاٰ يَكُفُّونَ عَنْ وُجُوهِهِمُ النّٰارَ وَ لاٰ عَنْ ظُهُورِهِمْ وَ لاٰ هُمْ يُنْصَرُونَ");
        sor.Ayaat.Add("بَلْ تَأْتِيهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلاٰ يَسْتَطِيعُونَ رَدَّهٰا وَ لاٰ هُمْ يُنْظَرُونَ");
        sor.Ayaat.Add("وَ لَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِنْ قَبْلِكَ فَحٰاقَ بِالَّذِينَ سَخِرُوا مِنْهُمْ مٰا كٰانُوا بِهِ يَسْتَهْزِؤُنَ");
        sor.Ayaat.Add("قُلْ مَنْ يَكْلَؤُكُمْ بِاللَّيْلِ وَ النَّهٰارِ مِنَ الرَّحْمٰنِ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُعْرِضُونَ");
        sor.Ayaat.Add("أَمْ لَهُمْ آلِهَةٌ تَمْنَعُهُمْ مِنْ دُونِنٰا لاٰ يَسْتَطِيعُونَ نَصْرَ أَنْفُسِهِمْ وَ لاٰ هُمْ مِنّٰا يُصْحَبُونَ");
        sor.Ayaat.Add("بَلْ مَتَّعْنٰا هٰؤُلاٰءِ وَ آبٰاءَهُمْ حَتّٰى طٰالَ عَلَيْهِمُ الْعُمُرُ أَ فَلاٰ يَرَوْنَ أَنّٰا نَأْتِي الْأَرْضَ نَنْقُصُهٰا مِنْ أَطْرٰافِهٰا أَ فَهُمُ الْغٰالِبُونَ");
        sor.Ayaat.Add("قُلْ إِنَّمٰا أُنْذِرُكُمْ بِالْوَحْيِ وَ لاٰ يَسْمَعُ الصُّمُّ الدُّعٰاءَ إِذٰا مٰا يُنْذَرُونَ");
        sor.Ayaat.Add("وَ لَئِنْ مَسَّتْهُمْ نَفْحَةٌ مِنْ عَذٰابِ رَبِّكَ لَيَقُولُنَّ يٰا وَيْلَنٰا إِنّٰا كُنّٰا ظٰالِمِينَ");
        sor.Ayaat.Add("وَ نَضَعُ الْمَوٰازِينَ الْقِسْطَ لِيَوْمِ الْقِيٰامَةِ فَلاٰ تُظْلَمُ نَفْسٌ شَيْئاً وَ إِنْ كٰانَ مِثْقٰالَ حَبَّةٍ مِنْ خَرْدَلٍ أَتَيْنٰا بِهٰا وَ كَفىٰ بِنٰا حٰاسِبِينَ");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا مُوسىٰ وَ هٰارُونَ الْفُرْقٰانَ وَ ضِيٰاءً وَ ذِكْراً لِلْمُتَّقِينَ");
        sor.Ayaat.Add("الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَ هُمْ مِنَ السّٰاعَةِ مُشْفِقُونَ");
        sor.Ayaat.Add("وَ هٰذٰا ذِكْرٌ مُبٰارَكٌ أَنْزَلْنٰاهُ أَ فَأَنْتُمْ لَهُ مُنْكِرُونَ");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا إِبْرٰاهِيمَ رُشْدَهُ مِنْ قَبْلُ وَ كُنّٰا بِهِ عٰالِمِينَ");
        sor.Ayaat.Add("إِذْ قٰالَ لِأَبِيهِ وَ قَوْمِهِ مٰا هٰذِهِ التَّمٰاثِيلُ الَّتِي أَنْتُمْ لَهٰا عٰاكِفُونَ");
        sor.Ayaat.Add("قٰالُوا وَجَدْنٰا آبٰاءَنٰا لَهٰا عٰابِدِينَ");
        sor.Ayaat.Add("قٰالَ لَقَدْ كُنْتُمْ أَنْتُمْ وَ آبٰاؤُكُمْ فِي ضَلاٰلٍ مُبِينٍ");
        sor.Ayaat.Add("قٰالُوا أَ جِئْتَنٰا بِالْحَقِّ أَمْ أَنْتَ مِنَ اللاّٰعِبِينَ");
        sor.Ayaat.Add("قٰالَ بَلْ رَبُّكُمْ رَبُّ السَّمٰاوٰاتِ وَ الْأَرْضِ الَّذِي فَطَرَهُنَّ وَ أَنَا عَلىٰ ذٰلِكُمْ مِنَ الشّٰاهِدِينَ");
        sor.Ayaat.Add("وَ تَاللّٰهِ لَأَكِيدَنَّ أَصْنٰامَكُمْ بَعْدَ أَنْ تُوَلُّوا مُدْبِرِينَ");
        sor.Ayaat.Add("فَجَعَلَهُمْ جُذٰاذاً إِلاّٰ كَبِيراً لَهُمْ لَعَلَّهُمْ إِلَيْهِ يَرْجِعُونَ");
        sor.Ayaat.Add("قٰالُوا مَنْ فَعَلَ هٰذٰا بِآلِهَتِنٰا إِنَّهُ لَمِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("قٰالُوا سَمِعْنٰا فَتًى يَذْكُرُهُمْ يُقٰالُ لَهُ إِبْرٰاهِيمُ");
        sor.Ayaat.Add("قٰالُوا فَأْتُوا بِهِ عَلىٰ أَعْيُنِ النّٰاسِ لَعَلَّهُمْ يَشْهَدُونَ");
        sor.Ayaat.Add("قٰالُوا أَ أَنْتَ فَعَلْتَ هٰذٰا بِآلِهَتِنٰا يٰا إِبْرٰاهِيمُ");
        sor.Ayaat.Add("قٰالَ بَلْ فَعَلَهُ كَبِيرُهُمْ هٰذٰا فَسْئَلُوهُمْ إِنْ كٰانُوا يَنْطِقُونَ");
        sor.Ayaat.Add("فَرَجَعُوا إِلىٰ أَنْفُسِهِمْ فَقٰالُوا إِنَّكُمْ أَنْتُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("ثُمَّ نُكِسُوا عَلىٰ رُؤُسِهِمْ لَقَدْ عَلِمْتَ مٰا هٰؤُلاٰءِ يَنْطِقُونَ");
        sor.Ayaat.Add("قٰالَ أَ فَتَعْبُدُونَ مِنْ دُونِ اللّٰهِ مٰا لاٰ يَنْفَعُكُمْ شَيْئاً وَ لاٰ يَضُرُّكُمْ");
        sor.Ayaat.Add("أُفٍّ لَكُمْ وَ لِمٰا تَعْبُدُونَ مِنْ دُونِ اللّٰهِ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("قٰالُوا حَرِّقُوهُ وَ انْصُرُوا آلِهَتَكُمْ إِنْ كُنْتُمْ فٰاعِلِينَ");
        sor.Ayaat.Add("قُلْنٰا يٰا نٰارُ كُونِي بَرْداً وَ سَلاٰماً عَلىٰ إِبْرٰاهِيمَ");
        sor.Ayaat.Add("وَ أَرٰادُوا بِهِ كَيْداً فَجَعَلْنٰاهُمُ الْأَخْسَرِينَ");
        sor.Ayaat.Add("وَ نَجَّيْنٰاهُ وَ لُوطاً إِلَى الْأَرْضِ الَّتِي بٰارَكْنٰا فِيهٰا لِلْعٰالَمِينَ");
        sor.Ayaat.Add("وَ وَهَبْنٰا لَهُ إِسْحٰاقَ وَ يَعْقُوبَ نٰافِلَةً وَ كُلاًّ جَعَلْنٰا صٰالِحِينَ");
        sor.Ayaat.Add("وَ جَعَلْنٰاهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنٰا وَ أَوْحَيْنٰا إِلَيْهِمْ فِعْلَ الْخَيْرٰاتِ وَ إِقٰامَ الصَّلاٰةِ وَ إِيتٰاءَ الزَّكٰاةِ وَ كٰانُوا لَنٰا عٰابِدِينَ");
        sor.Ayaat.Add("وَ لُوطاً آتَيْنٰاهُ حُكْماً وَ عِلْماً وَ نَجَّيْنٰاهُ مِنَ الْقَرْيَةِ الَّتِي كٰانَتْ تَعْمَلُ الْخَبٰائِثَ إِنَّهُمْ كٰانُوا قَوْمَ سَوْءٍ فٰاسِقِينَ");
        sor.Ayaat.Add("وَ أَدْخَلْنٰاهُ فِي رَحْمَتِنٰا إِنَّهُ مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("وَ نُوحاً إِذْ نٰادىٰ مِنْ قَبْلُ فَاسْتَجَبْنٰا لَهُ فَنَجَّيْنٰاهُ وَ أَهْلَهُ مِنَ الْكَرْبِ الْعَظِيمِ");
        sor.Ayaat.Add("وَ نَصَرْنٰاهُ مِنَ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا إِنَّهُمْ كٰانُوا قَوْمَ سَوْءٍ فَأَغْرَقْنٰاهُمْ أَجْمَعِينَ");
        sor.Ayaat.Add("وَ دٰاوُدَ وَ سُلَيْمٰانَ إِذْ يَحْكُمٰانِ فِي الْحَرْثِ إِذْ نَفَشَتْ فِيهِ غَنَمُ الْقَوْمِ وَ كُنّٰا لِحُكْمِهِمْ شٰاهِدِينَ");
        sor.Ayaat.Add("فَفَهَّمْنٰاهٰا سُلَيْمٰانَ وَ كُلاًّ آتَيْنٰا حُكْماً وَ عِلْماً وَ سَخَّرْنٰا مَعَ دٰاوُدَ الْجِبٰالَ يُسَبِّحْنَ وَ الطَّيْرَ وَ كُنّٰا فٰاعِلِينَ");
        sor.Ayaat.Add("وَ عَلَّمْنٰاهُ صَنْعَةَ لَبُوسٍ لَكُمْ لِتُحْصِنَكُمْ مِنْ بَأْسِكُمْ فَهَلْ أَنْتُمْ شٰاكِرُونَ");
        sor.Ayaat.Add("وَ لِسُلَيْمٰانَ الرِّيحَ عٰاصِفَةً تَجْرِي بِأَمْرِهِ إِلىٰ الْأَرْضِ الَّتِي بٰارَكْنٰا فِيهٰا وَ كُنّٰا بِكُلِّ شَيْءٍ عٰالِمِينَ");
        sor.Ayaat.Add("وَ مِنَ الشَّيٰاطِينِ مَنْ يَغُوصُونَ لَهُ وَ يَعْمَلُونَ عَمَلاً دُونَ ذٰلِكَ وَ كُنّٰا لَهُمْ حٰافِظِينَ");
        sor.Ayaat.Add("وَ أَيُّوبَ إِذْ نٰادىٰ رَبَّهُ أَنِّي مَسَّنِيَ الضُّرُّ وَ أَنْتَ أَرْحَمُ الرّٰاحِمِينَ");
        sor.Ayaat.Add("فَاسْتَجَبْنٰا لَهُ فَكَشَفْنٰا مٰا بِهِ مِنْ ضُرٍّ وَ آتَيْنٰاهُ أَهْلَهُ وَ مِثْلَهُمْ مَعَهُمْ رَحْمَةً مِنْ عِنْدِنٰا وَ ذِكْرىٰ لِلْعٰابِدِينَ");
        sor.Ayaat.Add("وَ إِسْمٰاعِيلَ وَ إِدْرِيسَ وَ ذَا الْكِفْلِ كُلٌّ مِنَ الصّٰابِرِينَ");
        sor.Ayaat.Add("وَ أَدْخَلْنٰاهُمْ فِي رَحْمَتِنٰا إِنَّهُمْ مِنَ الصّٰالِحِينَ");
        sor.Ayaat.Add("وَ ذَا النُّونِ إِذْ ذَهَبَ مُغٰاضِباً فَظَنَّ أَنْ لَنْ نَقْدِرَ عَلَيْهِ فَنٰادىٰ فِي الظُّلُمٰاتِ أَنْ لاٰ إِلٰهَ إِلاّٰ أَنْتَ سُبْحٰانَكَ إِنِّي كُنْتُ مِنَ الظّٰالِمِينَ");
        sor.Ayaat.Add("فَاسْتَجَبْنٰا لَهُ وَ نَجَّيْنٰاهُ مِنَ الْغَمِّ وَ كَذٰلِكَ نُنْجِي الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ زَكَرِيّٰا إِذْ نٰادىٰ رَبَّهُ رَبِّ لاٰ تَذَرْنِي فَرْداً وَ أَنْتَ خَيْرُ الْوٰارِثِينَ");
        sor.Ayaat.Add("فَاسْتَجَبْنٰا لَهُ وَ وَهَبْنٰا لَهُ يَحْيىٰ وَ أَصْلَحْنٰا لَهُ زَوْجَهُ إِنَّهُمْ كٰانُوا يُسٰارِعُونَ فِي الْخَيْرٰاتِ وَ يَدْعُونَنٰا رَغَباً وَ رَهَباً وَ كٰانُوا لَنٰا خٰاشِعِينَ");
        sor.Ayaat.Add("وَ الَّتِي أَحْصَنَتْ فَرْجَهٰا فَنَفَخْنٰا فِيهٰا مِنْ رُوحِنٰا وَ جَعَلْنٰاهٰا وَ ابْنَهٰا آيَةً لِلْعٰالَمِينَ");
        sor.Ayaat.Add("إِنَّ هٰذِهِ أُمَّتُكُمْ أُمَّةً وٰاحِدَةً وَ أَنَا رَبُّكُمْ فَاعْبُدُونِ");
        sor.Ayaat.Add("وَ تَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ كُلٌّ إِلَيْنٰا رٰاجِعُونَ");
        sor.Ayaat.Add("فَمَنْ يَعْمَلْ مِنَ الصّٰالِحٰاتِ وَ هُوَ مُؤْمِنٌ فَلاٰ كُفْرٰانَ لِسَعْيِهِ وَ إِنّٰا لَهُ كٰاتِبُونَ");
        sor.Ayaat.Add("وَ حَرٰامٌ عَلىٰ قَرْيَةٍ أَهْلَكْنٰاهٰا أَنَّهُمْ لاٰ يَرْجِعُونَ");
        sor.Ayaat.Add("حَتّٰى إِذٰا فُتِحَتْ يَأْجُوجُ وَ مَأْجُوجُ وَ هُمْ مِنْ كُلِّ حَدَبٍ يَنْسِلُونَ");
        sor.Ayaat.Add("وَ اقْتَرَبَ الْوَعْدُ الْحَقُّ فَإِذٰا هِيَ شٰاخِصَةٌ أَبْصٰارُ الَّذِينَ كَفَرُوا يٰا وَيْلَنٰا قَدْ كُنّٰا فِي غَفْلَةٍ مِنْ هٰذٰا بَلْ كُنّٰا ظٰالِمِينَ");
        sor.Ayaat.Add("إِنَّكُمْ وَ مٰا تَعْبُدُونَ مِنْ دُونِ اللّٰهِ حَصَبُ جَهَنَّمَ أَنْتُمْ لَهٰا وٰارِدُونَ");
        sor.Ayaat.Add("لَوْ كٰانَ هٰؤُلاٰءِ آلِهَةً مٰا وَرَدُوهٰا وَ كُلٌّ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("لَهُمْ فِيهٰا زَفِيرٌ وَ هُمْ فِيهٰا لاٰ يَسْمَعُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ سَبَقَتْ لَهُمْ مِنَّا الْحُسْنىٰ أُولٰئِكَ عَنْهٰا مُبْعَدُونَ");
        sor.Ayaat.Add("لاٰ يَسْمَعُونَ حَسِيسَهٰا وَ هُمْ فِي مَا اشْتَهَتْ أَنْفُسُهُمْ خٰالِدُونَ");
        sor.Ayaat.Add("لاٰ يَحْزُنُهُمُ الْفَزَعُ الْأَكْبَرُ وَ تَتَلَقّٰاهُمُ الْمَلاٰئِكَةُ هٰذٰا يَوْمُكُمُ الَّذِي كُنْتُمْ تُوعَدُونَ");
        sor.Ayaat.Add("يَوْمَ نَطْوِي السَّمٰاءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِ كَمٰا بَدَأْنٰا أَوَّلَ خَلْقٍ نُعِيدُهُ وَعْداً عَلَيْنٰا إِنّٰا كُنّٰا فٰاعِلِينَ");
        sor.Ayaat.Add("وَ لَقَدْ كَتَبْنٰا فِي الزَّبُورِ مِنْ بَعْدِ الذِّكْرِ أَنَّ الْأَرْضَ يَرِثُهٰا عِبٰادِيَ الصّٰالِحُونَ");
        sor.Ayaat.Add("إِنَّ فِي هٰذٰا لَبَلاٰغاً لِقَوْمٍ عٰابِدِينَ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰاكَ إِلاّٰ رَحْمَةً لِلْعٰالَمِينَ");
        sor.Ayaat.Add("قُلْ إِنَّمٰا يُوحىٰ إِلَيَّ أَنَّمٰا إِلٰهُكُمْ إِلٰهٌ وٰاحِدٌ فَهَلْ أَنْتُمْ مُسْلِمُونَ");
        sor.Ayaat.Add("فَإِنْ تَوَلَّوْا فَقُلْ آذَنْتُكُمْ عَلىٰ سَوٰاءٍ وَ إِنْ أَدْرِي أَ قَرِيبٌ أَمْ بَعِيدٌ مٰا تُوعَدُونَ");
        sor.Ayaat.Add("إِنَّهُ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَ يَعْلَمُ مٰا تَكْتُمُونَ");
        sor.Ayaat.Add("وَ إِنْ أَدْرِي لَعَلَّهُ فِتْنَةٌ لَكُمْ وَ مَتٰاعٌ إِلىٰ حِينٍ");
        sor.Ayaat.Add("قٰالَ رَبِّ احْكُمْ بِالْحَقِّ وَ رَبُّنَا الرَّحْمٰنُ الْمُسْتَعٰانُ عَلىٰ مٰا تَصِفُونَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢٢ - الحج";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ يٰا أَيُّهَا النّٰاسُ اتَّقُوا رَبَّكُمْ إِنَّ زَلْزَلَةَ السّٰاعَةِ شَيْءٌ عَظِيمٌ");
        sor.Ayaat.Add("يَوْمَ تَرَوْنَهٰا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمّٰا أَرْضَعَتْ وَ تَضَعُ كُلُّ ذٰاتِ حَمْلٍ حَمْلَهٰا وَ تَرَى النّٰاسَ سُكٰارىٰ وَ مٰا هُمْ بِسُكٰارىٰ وَ لٰكِنَّ عَذٰابَ اللّٰهِ شَدِيدٌ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يُجٰادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَ يَتَّبِعُ كُلَّ شَيْطٰانٍ مَرِيدٍ");
        sor.Ayaat.Add("كُتِبَ عَلَيْهِ أَنَّهُ مَنْ تَوَلاّٰهُ فَأَنَّهُ يُضِلُّهُ وَ يَهْدِيهِ إِلىٰ عَذٰابِ السَّعِيرِ");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ إِنْ كُنْتُمْ فِي رَيْبٍ مِنَ الْبَعْثِ فَإِنّٰا خَلَقْنٰاكُمْ مِنْ تُرٰابٍ ثُمَّ مِنْ نُطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُضْغَةٍ مُخَلَّقَةٍ وَ غَيْرِ مُخَلَّقَةٍ لِنُبَيِّنَ لَكُمْ وَ نُقِرُّ فِي الْأَرْحٰامِ مٰا نَشٰاءُ إِلىٰ أَجَلٍ مُسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلاً ثُمَّ لِتَبْلُغُوا أَشُدَّكُمْ وَ مِنْكُمْ مَنْ يُتَوَفّٰى وَ مِنْكُمْ مَنْ يُرَدُّ إِلىٰ أَرْذَلِ الْعُمُرِ لِكَيْلاٰ يَعْلَمَ مِنْ بَعْدِ عِلْمٍ شَيْئاً وَ تَرَى الْأَرْضَ هٰامِدَةً فَإِذٰا أَنْزَلْنٰا عَلَيْهَا الْمٰاءَ اهْتَزَّتْ وَ رَبَتْ وَ أَنْبَتَتْ مِنْ كُلِّ زَوْجٍ بَهِيجٍ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّ اللّٰهَ هُوَ الْحَقُّ وَ أَنَّهُ يُحْيِ الْمَوْتىٰ وَ أَنَّهُ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("وَ أَنَّ السّٰاعَةَ آتِيَةٌ لاٰ رَيْبَ فِيهٰا وَ أَنَّ اللّٰهَ يَبْعَثُ مَنْ فِي الْقُبُورِ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يُجٰادِلُ فِي اللّٰهِ بِغَيْرِ عِلْمٍ وَ لاٰ هُدىً وَ لاٰ كِتٰابٍ مُنِيرٍ");
        sor.Ayaat.Add("ثٰانِيَ عِطْفِهِ لِيُضِلَّ عَنْ سَبِيلِ اللّٰهِ لَهُ فِي الدُّنْيٰا خِزْيٌ وَ نُذِيقُهُ يَوْمَ الْقِيٰامَةِ عَذٰابَ الْحَرِيقِ");
        sor.Ayaat.Add("ذٰلِكَ بِمٰا قَدَّمَتْ يَدٰاكَ وَ أَنَّ اللّٰهَ لَيْسَ بِظَلاّٰمٍ لِلْعَبِيدِ");
        sor.Ayaat.Add("وَ مِنَ النّٰاسِ مَنْ يَعْبُدُ اللّٰهَ عَلىٰ حَرْفٍ فَإِنْ أَصٰابَهُ خَيْرٌ اطْمَأَنَّ بِهِ وَ إِنْ أَصٰابَتْهُ فِتْنَةٌ انْقَلَبَ عَلىٰ وَجْهِهِ خَسِرَ الدُّنْيٰا وَ الْآخِرَةَ ذٰلِكَ هُوَ الْخُسْرٰانُ الْمُبِينُ");
        sor.Ayaat.Add("يَدْعُوا مِنْ دُونِ اللّٰهِ مٰا لاٰ يَضُرُّهُ وَ مٰا لاٰ يَنْفَعُهُ ذٰلِكَ هُوَ الضَّلاٰلُ الْبَعِيدُ");
        sor.Ayaat.Add("يَدْعُوا لَمَنْ ضَرُّهُ أَقْرَبُ مِنْ نَفْعِهِ لَبِئْسَ الْمَوْلىٰ وَ لَبِئْسَ الْعَشِيرُ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ يُدْخِلُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ إِنَّ اللّٰهَ يَفْعَلُ مٰا يُرِيدُ");
        sor.Ayaat.Add("مَنْ كٰانَ يَظُنُّ أَنْ لَنْ يَنْصُرَهُ اللّٰهُ فِي الدُّنْيٰا وَ الْآخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ إِلَى السَّمٰاءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهُ مٰا يَغِيظُ");
        sor.Ayaat.Add("وَ كَذٰلِكَ أَنْزَلْنٰاهُ آيٰاتٍ بَيِّنٰاتٍ وَ أَنَّ اللّٰهَ يَهْدِي مَنْ يُرِيدُ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ آمَنُوا وَ الَّذِينَ هٰادُوا وَ الصّٰابِئِينَ وَ النَّصٰارىٰ وَ الْمَجُوسَ وَ الَّذِينَ أَشْرَكُوا إِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰامَةِ إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ شَهِيدٌ");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنَّ اللّٰهَ يَسْجُدُ لَهُ مَنْ فِي السَّمٰاوٰاتِ وَ مَنْ فِي الْأَرْضِ وَ الشَّمْسُ وَ الْقَمَرُ وَ النُّجُومُ وَ الْجِبٰالُ وَ الشَّجَرُ وَ الدَّوَابُّ وَ كَثِيرٌ مِنَ النّٰاسِ وَ كَثِيرٌ حَقَّ عَلَيْهِ الْعَذٰابُ وَ مَنْ يُهِنِ اللّٰهُ فَمٰا لَهُ مِنْ مُكْرِمٍ إِنَّ اللّٰهَ يَفْعَلُ مٰا يَشٰاءُ");
        sor.Ayaat.Add("هٰذٰانِ خَصْمٰانِ اخْتَصَمُوا فِي رَبِّهِمْ فَالَّذِينَ كَفَرُوا قُطِّعَتْ لَهُمْ ثِيٰابٌ مِنْ نٰارٍ يُصَبُّ مِنْ فَوْقِ رُؤُسِهِمُ الْحَمِيمُ");
        sor.Ayaat.Add("يُصْهَرُ بِهِ مٰا فِي بُطُونِهِمْ وَ الْجُلُودُ");
        sor.Ayaat.Add("وَ لَهُمْ مَقٰامِعُ مِنْ حَدِيدٍ");
        sor.Ayaat.Add("كُلَّمٰا أَرٰادُوا أَنْ يَخْرُجُوا مِنْهٰا مِنْ غَمٍّ أُعِيدُوا فِيهٰا وَ ذُوقُوا عَذٰابَ الْحَرِيقِ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ يُدْخِلُ الَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ يُحَلَّوْنَ فِيهٰا مِنْ أَسٰاوِرَ مِنْ ذَهَبٍ وَ لُؤْلُؤاً وَ لِبٰاسُهُمْ فِيهٰا حَرِيرٌ");
        sor.Ayaat.Add("وَ هُدُوا إِلَى الطَّيِّبِ مِنَ الْقَوْلِ وَ هُدُوا إِلىٰ صِرٰاطِ الْحَمِيدِ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ كَفَرُوا وَ يَصُدُّونَ عَنْ سَبِيلِ اللّٰهِ وَ الْمَسْجِدِ الْحَرٰامِ الَّذِي جَعَلْنٰاهُ لِلنّٰاسِ سَوٰاءً الْعٰاكِفُ فِيهِ وَ الْبٰادِ وَ مَنْ يُرِدْ فِيهِ بِإِلْحٰادٍ بِظُلْمٍ نُذِقْهُ مِنْ عَذٰابٍ أَلِيمٍ");
        sor.Ayaat.Add("وَ إِذْ بَوَّأْنٰا لِإِبْرٰاهِيمَ مَكٰانَ الْبَيْتِ أَنْ لاٰ تُشْرِكْ بِي شَيْئاً وَ طَهِّرْ بَيْتِيَ لِلطّٰائِفِينَ وَ الْقٰائِمِينَ وَ الرُّكَّعِ السُّجُودِ");
        sor.Ayaat.Add("وَ أَذِّنْ فِي النّٰاسِ بِالْحَجِّ يَأْتُوكَ رِجٰالاً وَ عَلىٰ كُلِّ ضٰامِرٍ يَأْتِينَ مِنْ كُلِّ فَجٍّ عَمِيقٍ");
        sor.Ayaat.Add("لِيَشْهَدُوا مَنٰافِعَ لَهُمْ وَ يَذْكُرُوا اسْمَ اللّٰهِ فِي أَيّٰامٍ مَعْلُومٰاتٍ عَلىٰ مٰا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعٰامِ فَكُلُوا مِنْهٰا وَ أَطْعِمُوا الْبٰائِسَ الْفَقِيرَ");
        sor.Ayaat.Add("ثُمَّ لْيَقْضُوا تَفَثَهُمْ وَ لْيُوفُوا نُذُورَهُمْ وَ لْيَطَّوَّفُوا بِالْبَيْتِ الْعَتِيقِ");
        sor.Ayaat.Add("ذٰلِكَ وَ مَنْ يُعَظِّمْ حُرُمٰاتِ اللّٰهِ فَهُوَ خَيْرٌ لَهُ عِنْدَ رَبِّهِ وَ أُحِلَّتْ لَكُمُ الْأَنْعٰامُ إِلاّٰ مٰا يُتْلىٰ عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْأَوْثٰانِ وَ اجْتَنِبُوا قَوْلَ الزُّورِ");
        sor.Ayaat.Add("حُنَفٰاءَ لِلّٰهِ غَيْرَ مُشْرِكِينَ بِهِ وَ مَنْ يُشْرِكْ بِاللّٰهِ فَكَأَنَّمٰا خَرَّ مِنَ السَّمٰاءِ فَتَخْطَفُهُ الطَّيْرُ أَوْ تَهْوِي بِهِ الرِّيحُ فِي مَكٰانٍ سَحِيقٍ");
        sor.Ayaat.Add("ذٰلِكَ وَ مَنْ يُعَظِّمْ شَعٰائِرَ اللّٰهِ فَإِنَّهٰا مِنْ تَقْوَى الْقُلُوبِ");
        sor.Ayaat.Add("لَكُمْ فِيهٰا مَنٰافِعُ إِلىٰ أَجَلٍ مُسَمًّى ثُمَّ مَحِلُّهٰا إِلَى الْبَيْتِ الْعَتِيقِ");
        sor.Ayaat.Add("وَ لِكُلِّ أُمَّةٍ جَعَلْنٰا مَنْسَكاً لِيَذْكُرُوا اسْمَ اللّٰهِ عَلىٰ مٰا رَزَقَهُمْ مِنْ بَهِيمَةِ الْأَنْعٰامِ فَإِلٰهُكُمْ إِلٰهٌ وٰاحِدٌ فَلَهُ أَسْلِمُوا وَ بَشِّرِ الْمُخْبِتِينَ");
        sor.Ayaat.Add("الَّذِينَ إِذٰا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوبُهُمْ وَ الصّٰابِرِينَ عَلىٰ مٰا أَصٰابَهُمْ وَ الْمُقِيمِي الصَّلاٰةِ وَ مِمّٰا رَزَقْنٰاهُمْ يُنْفِقُونَ");
        sor.Ayaat.Add("وَ الْبُدْنَ جَعَلْنٰاهٰا لَكُمْ مِنْ شَعٰائِرِ اللّٰهِ لَكُمْ فِيهٰا خَيْرٌ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهٰا صَوٰافَّ فَإِذٰا وَجَبَتْ جُنُوبُهٰا فَكُلُوا مِنْهٰا وَ أَطْعِمُوا الْقٰانِعَ وَ الْمُعْتَرَّ كَذٰلِكَ سَخَّرْنٰاهٰا لَكُمْ لَعَلَّكُمْ تَشْكُرُونَ");
        sor.Ayaat.Add("لَنْ يَنٰالَ اللّٰهَ لُحُومُهٰا وَ لاٰ دِمٰاؤُهٰا وَ لٰكِنْ يَنٰالُهُ التَّقْوىٰ مِنْكُمْ كَذٰلِكَ سَخَّرَهٰا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلىٰ مٰا هَدٰاكُمْ وَ بَشِّرِ الْمُحْسِنِينَ");
        sor.Ayaat.Add("إِنَّ اللّٰهَ يُدٰافِعُ عَنِ الَّذِينَ آمَنُوا إِنَّ اللّٰهَ لاٰ يُحِبُّ كُلَّ خَوّٰانٍ كَفُورٍ");
        sor.Ayaat.Add("أُذِنَ لِلَّذِينَ يُقٰاتَلُونَ بِأَنَّهُمْ ظُلِمُوا وَ إِنَّ اللّٰهَ عَلىٰ نَصْرِهِمْ لَقَدِيرٌ");
        sor.Ayaat.Add("الَّذِينَ أُخْرِجُوا مِنْ دِيٰارِهِمْ بِغَيْرِ حَقٍّ إِلاّٰ أَنْ يَقُولُوا رَبُّنَا اللّٰهُ وَ لَوْ لاٰ دَفْعُ اللّٰهِ النّٰاسَ بَعْضَهُمْ بِبَعْضٍ لَهُدِّمَتْ صَوٰامِعُ وَ بِيَعٌ وَ صَلَوٰاتٌ وَ مَسٰاجِدُ يُذْكَرُ فِيهَا اسْمُ اللّٰهِ كَثِيراً وَ لَيَنْصُرَنَّ اللّٰهُ مَنْ يَنْصُرُهُ إِنَّ اللّٰهَ لَقَوِيٌّ عَزِيزٌ");
        sor.Ayaat.Add("الَّذِينَ إِنْ مَكَّنّٰاهُمْ فِي الْأَرْضِ أَقٰامُوا الصَّلاٰةَ وَ آتَوُا الزَّكٰاةَ وَ أَمَرُوا بِالْمَعْرُوفِ وَ نَهَوْا عَنِ الْمُنْكَرِ وَ لِلّٰهِ عٰاقِبَةُ الْأُمُورِ");
        sor.Ayaat.Add("وَ إِنْ يُكَذِّبُوكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَ عٰادٌ وَ ثَمُودُ");
        sor.Ayaat.Add("وَ قَوْمُ إِبْرٰاهِيمَ وَ قَوْمُ لُوطٍ");
        sor.Ayaat.Add("وَ أَصْحٰابُ مَدْيَنَ وَ كُذِّبَ مُوسىٰ فَأَمْلَيْتُ لِلْكٰافِرِينَ ثُمَّ أَخَذْتُهُمْ فَكَيْفَ كٰانَ نَكِيرِ");
        sor.Ayaat.Add("فَكَأَيِّنْ مِنْ قَرْيَةٍ أَهْلَكْنٰاهٰا وَ هِيَ ظٰالِمَةٌ فَهِيَ خٰاوِيَةٌ عَلىٰ عُرُوشِهٰا وَ بِئْرٍ مُعَطَّلَةٍ وَ قَصْرٍ مَشِيدٍ");
        sor.Ayaat.Add("أَ فَلَمْ يَسِيرُوا فِي الْأَرْضِ فَتَكُونَ لَهُمْ قُلُوبٌ يَعْقِلُونَ بِهٰا أَوْ آذٰانٌ يَسْمَعُونَ بِهٰا فَإِنَّهٰا لاٰ تَعْمَى الْأَبْصٰارُ وَ لٰكِنْ تَعْمَى الْقُلُوبُ الَّتِي فِي الصُّدُورِ");
        sor.Ayaat.Add("وَ يَسْتَعْجِلُونَكَ بِالْعَذٰابِ وَ لَنْ يُخْلِفَ اللّٰهُ وَعْدَهُ وَ إِنَّ يَوْماً عِنْدَ رَبِّكَ كَأَلْفِ سَنَةٍ مِمّٰا تَعُدُّونَ");
        sor.Ayaat.Add("وَ كَأَيِّنْ مِنْ قَرْيَةٍ أَمْلَيْتُ لَهٰا وَ هِيَ ظٰالِمَةٌ ثُمَّ أَخَذْتُهٰا وَ إِلَيَّ الْمَصِيرُ");
        sor.Ayaat.Add("قُلْ يٰا أَيُّهَا النّٰاسُ إِنَّمٰا أَنَا لَكُمْ نَذِيرٌ مُبِينٌ");
        sor.Ayaat.Add("فَالَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ");
        sor.Ayaat.Add("وَ الَّذِينَ سَعَوْا فِي آيٰاتِنٰا مُعٰاجِزِينَ أُولٰئِكَ أَصْحٰابُ الْجَحِيمِ");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا مِنْ قَبْلِكَ مِنْ رَسُولٍ وَ لاٰ نَبِيٍّ إِلاّٰ إِذٰا تَمَنّٰى أَلْقَى الشَّيْطٰانُ فِي أُمْنِيَّتِهِ فَيَنْسَخُ اللّٰهُ مٰا يُلْقِي الشَّيْطٰانُ ثُمَّ يُحْكِمُ اللّٰهُ آيٰاتِهِ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("لِيَجْعَلَ مٰا يُلْقِي الشَّيْطٰانُ فِتْنَةً لِلَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ وَ الْقٰاسِيَةِ قُلُوبُهُمْ وَ إِنَّ الظّٰالِمِينَ لَفِي شِقٰاقٍ بَعِيدٍ");
        sor.Ayaat.Add("وَ لِيَعْلَمَ الَّذِينَ أُوتُوا الْعِلْمَ أَنَّهُ الْحَقُّ مِنْ رَبِّكَ فَيُؤْمِنُوا بِهِ فَتُخْبِتَ لَهُ قُلُوبُهُمْ وَ إِنَّ اللّٰهَ لَهٰادِ الَّذِينَ آمَنُوا إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ لاٰ يَزٰالُ الَّذِينَ كَفَرُوا فِي مِرْيَةٍ مِنْهُ حَتّٰى تَأْتِيَهُمُ السّٰاعَةُ بَغْتَةً أَوْ يَأْتِيَهُمْ عَذٰابُ يَوْمٍ عَقِيمٍ");
        sor.Ayaat.Add("الْمُلْكُ يَوْمَئِذٍ لِلّٰهِ يَحْكُمُ بَيْنَهُمْ فَالَّذِينَ آمَنُوا وَ عَمِلُوا الصّٰالِحٰاتِ فِي جَنّٰاتِ النَّعِيمِ");
        sor.Ayaat.Add("وَ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِآيٰاتِنٰا فَأُولٰئِكَ لَهُمْ عَذٰابٌ مُهِينٌ");
        sor.Ayaat.Add("وَ الَّذِينَ هٰاجَرُوا فِي سَبِيلِ اللّٰهِ ثُمَّ قُتِلُوا أَوْ مٰاتُوا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقاً حَسَناً وَ إِنَّ اللّٰهَ لَهُوَ خَيْرُ الرّٰازِقِينَ");
        sor.Ayaat.Add("لَيُدْخِلَنَّهُمْ مُدْخَلاً يَرْضَوْنَهُ وَ إِنَّ اللّٰهَ لَعَلِيمٌ حَلِيمٌ");
        sor.Ayaat.Add("ذٰلِكَ وَ مَنْ عٰاقَبَ بِمِثْلِ مٰا عُوقِبَ بِهِ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُ إِنَّ اللّٰهَ لَعَفُوٌّ غَفُورٌ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّ اللّٰهَ يُولِجُ اللَّيْلَ فِي النَّهٰارِ وَ يُولِجُ النَّهٰارَ فِي اللَّيْلِ وَ أَنَّ اللّٰهَ سَمِيعٌ بَصِيرٌ");
        sor.Ayaat.Add("ذٰلِكَ بِأَنَّ اللّٰهَ هُوَ الْحَقُّ وَ أَنَّ مٰا يَدْعُونَ مِنْ دُونِهِ هُوَ الْبٰاطِلُ وَ أَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيرُ");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنَّ اللّٰهَ أَنْزَلَ مِنَ السَّمٰاءِ مٰاءً فَتُصْبِحُ الْأَرْضُ مُخْضَرَّةً إِنَّ اللّٰهَ لَطِيفٌ خَبِيرٌ");
        sor.Ayaat.Add("لَهُ مٰا فِي السَّمٰاوٰاتِ وَ مٰا فِي الْأَرْضِ وَ إِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَمِيدُ");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنَّ اللّٰهَ سَخَّرَ لَكُمْ مٰا فِي الْأَرْضِ وَ الْفُلْكَ تَجْرِي فِي الْبَحْرِ بِأَمْرِهِ وَ يُمْسِكُ السَّمٰاءَ أَنْ تَقَعَ عَلَى الْأَرْضِ إِلاّٰ بِإِذْنِهِ إِنَّ اللّٰهَ بِالنّٰاسِ لَرَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي أَحْيٰاكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ إِنَّ الْإِنْسٰانَ لَكَفُورٌ");
        sor.Ayaat.Add("لِكُلِّ أُمَّةٍ جَعَلْنٰا مَنْسَكاً هُمْ نٰاسِكُوهُ فَلاٰ يُنٰازِعُنَّكَ فِي الْأَمْرِ وَ ادْعُ إِلىٰ رَبِّكَ إِنَّكَ لَعَلىٰ هُدىً مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ إِنْ جٰادَلُوكَ فَقُلِ اللّٰهُ أَعْلَمُ بِمٰا تَعْمَلُونَ");
        sor.Ayaat.Add("اللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰامَةِ فِيمٰا كُنْتُمْ فِيهِ تَخْتَلِفُونَ");
        sor.Ayaat.Add("أَ لَمْ تَعْلَمْ أَنَّ اللّٰهَ يَعْلَمُ مٰا فِي السَّمٰاءِ وَ الْأَرْضِ إِنَّ ذٰلِكَ فِي كِتٰابٍ إِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيرٌ");
        sor.Ayaat.Add("وَ يَعْبُدُونَ مِنْ دُونِ اللّٰهِ مٰا لَمْ يُنَزِّلْ بِهِ سُلْطٰاناً وَ مٰا لَيْسَ لَهُمْ بِهِ عِلْمٌ وَ مٰا لِلظّٰالِمِينَ مِنْ نَصِيرٍ");
        sor.Ayaat.Add("وَ إِذٰا تُتْلىٰ عَلَيْهِمْ آيٰاتُنٰا بَيِّنٰاتٍ تَعْرِفُ فِي وُجُوهِ الَّذِينَ كَفَرُوا الْمُنْكَرَ يَكٰادُونَ يَسْطُونَ بِالَّذِينَ يَتْلُونَ عَلَيْهِمْ آيٰاتِنٰا قُلْ أَ فَأُنَبِّئُكُمْ بِشَرٍّ مِنْ ذٰلِكُمُ النّٰارُ وَعَدَهَا اللّٰهُ الَّذِينَ كَفَرُوا وَ بِئْسَ الْمَصِيرُ");
        sor.Ayaat.Add("يٰا أَيُّهَا النّٰاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوا لَهُ إِنَّ الَّذِينَ تَدْعُونَ مِنْ دُونِ اللّٰهِ لَنْ يَخْلُقُوا ذُبٰاباً وَ لَوِ اجْتَمَعُوا لَهُ وَ إِنْ يَسْلُبْهُمُ الذُّبٰابُ شَيْئاً لاٰ يَسْتَنْقِذُوهُ مِنْهُ ضَعُفَ الطّٰالِبُ وَ الْمَطْلُوبُ");
        sor.Ayaat.Add("مٰا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهِ إِنَّ اللّٰهَ لَقَوِيٌّ عَزِيزٌ");
        sor.Ayaat.Add("اللّٰهُ يَصْطَفِي مِنَ الْمَلاٰئِكَةِ رُسُلاً وَ مِنَ النّٰاسِ إِنَّ اللّٰهَ سَمِيعٌ بَصِيرٌ");
        sor.Ayaat.Add("يَعْلَمُ مٰا بَيْنَ أَيْدِيهِمْ وَ مٰا خَلْفَهُمْ وَ إِلَى اللّٰهِ تُرْجَعُ الْأُمُورُ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا ارْكَعُوا وَ اسْجُدُوا وَ اعْبُدُوا رَبَّكُمْ وَ افْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("وَ جٰاهِدُوا فِي اللّٰهِ حَقَّ جِهٰادِهِ هُوَ اجْتَبٰاكُمْ وَ مٰا جَعَلَ عَلَيْكُمْ فِي الدِّينِ مِنْ حَرَجٍ مِلَّةَ أَبِيكُمْ إِبْرٰاهِيمَ هُوَ سَمّٰاكُمُ الْمُسْلِمِينَ مِنْ قَبْلُ وَ فِي هٰذٰا لِيَكُونَ الرَّسُولُ شَهِيداً عَلَيْكُمْ وَ تَكُونُوا شُهَدٰاءَ عَلَى النّٰاسِ فَأَقِيمُوا الصَّلاٰةَ وَ آتُوا الزَّكٰاةَ وَ اعْتَصِمُوا بِاللّٰهِ هُوَ مَوْلاٰكُمْ فَنِعْمَ الْمَوْلىٰ وَ نِعْمَ النَّصِيرُ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢٣ - المؤمنون";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ قَدْ أَفْلَحَ الْمُؤْمِنُونَ");
        sor.Ayaat.Add("الَّذِينَ هُمْ فِي صَلاٰتِهِمْ خٰاشِعُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ عَنِ اللَّغْوِ مُعْرِضُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ لِلزَّكٰاةِ فٰاعِلُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ لِفُرُوجِهِمْ حٰافِظُونَ");
        sor.Ayaat.Add("إِلاّٰ عَلىٰ أَزْوٰاجِهِمْ أَوْ مٰا مَلَكَتْ أَيْمٰانُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ");
        sor.Ayaat.Add("فَمَنِ ابْتَغىٰ وَرٰاءَ ذٰلِكَ فَأُولٰئِكَ هُمُ العٰادُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ لِأَمٰانٰاتِهِمْ وَ عَهْدِهِمْ رٰاعُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ عَلىٰ صَلَوٰاتِهِمْ يُحٰافِظُونَ");
        sor.Ayaat.Add("أُولٰئِكَ هُمُ الْوٰارِثُونَ");
        sor.Ayaat.Add("الَّذِينَ يَرِثُونَ الْفِرْدَوْسَ هُمْ فِيهٰا خٰالِدُونَ");
        sor.Ayaat.Add("وَ لَقَدْ خَلَقْنَا الْإِنْسٰانَ مِنْ سُلاٰلَةٍ مِنْ طِينٍ");
        sor.Ayaat.Add("ثُمَّ جَعَلْنٰاهُ نُطْفَةً فِي قَرٰارٍ مَكِينٍ");
        sor.Ayaat.Add("ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظٰاماً فَكَسَوْنَا الْعِظٰامَ لَحْماً ثُمَّ أَنْشَأْنٰاهُ خَلْقاً آخَرَ فَتَبٰارَكَ اللّٰهُ أَحْسَنُ الْخٰالِقِينَ");
        sor.Ayaat.Add("ثُمَّ إِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُونَ");
        sor.Ayaat.Add("ثُمَّ إِنَّكُمْ يَوْمَ الْقِيٰامَةِ تُبْعَثُونَ");
        sor.Ayaat.Add("وَ لَقَدْ خَلَقْنٰا فَوْقَكُمْ سَبْعَ طَرٰائِقَ وَ مٰا كُنّٰا عَنِ الْخَلْقِ غٰافِلِينَ");
        sor.Ayaat.Add("وَ أَنْزَلْنٰا مِنَ السَّمٰاءِ مٰاءً بِقَدَرٍ فَأَسْكَنّٰاهُ فِي الْأَرْضِ وَ إِنّٰا عَلىٰ ذَهٰابٍ بِهِ لَقٰادِرُونَ");
        sor.Ayaat.Add("فَأَنْشَأْنٰا لَكُمْ بِهِ جَنّٰاتٍ مِنْ نَخِيلٍ وَ أَعْنٰابٍ لَكُمْ فِيهٰا فَوٰاكِهُ كَثِيرَةٌ وَ مِنْهٰا تَأْكُلُونَ");
        sor.Ayaat.Add("وَ شَجَرَةً تَخْرُجُ مِنْ طُورِ سَيْنٰاءَ تَنْبُتُ بِالدُّهْنِ وَ صِبْغٍ لِلْآكِلِينَ");
        sor.Ayaat.Add("وَ إِنَّ لَكُمْ فِي الْأَنْعٰامِ لَعِبْرَةً نُسْقِيكُمْ مِمّٰا فِي بُطُونِهٰا وَ لَكُمْ فِيهٰا مَنٰافِعُ كَثِيرَةٌ وَ مِنْهٰا تَأْكُلُونَ");
        sor.Ayaat.Add("وَ عَلَيْهٰا وَ عَلَى الْفُلْكِ تُحْمَلُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَرْسَلْنٰا نُوحاً إِلىٰ قَوْمِهِ فَقٰالَ يٰا قَوْمِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ أَ فَلاٰ تَتَّقُونَ");
        sor.Ayaat.Add("فَقٰالَ الْمَلَأُ الَّذِينَ كَفَرُوا مِنْ قَوْمِهِ مٰا هٰذٰا إِلاّٰ بَشَرٌ مِثْلُكُمْ يُرِيدُ أَنْ يَتَفَضَّلَ عَلَيْكُمْ وَ لَوْ شٰاءَ اللّٰهُ لَأَنْزَلَ مَلاٰئِكَةً مٰا سَمِعْنٰا بِهٰذٰا فِي آبٰائِنَا الْأَوَّلِينَ");
        sor.Ayaat.Add("إِنْ هُوَ إِلاّٰ رَجُلٌ بِهِ جِنَّةٌ فَتَرَبَّصُوا بِهِ حَتّٰى حِينٍ");
        sor.Ayaat.Add("قٰالَ رَبِّ انْصُرْنِي بِمٰا كَذَّبُونِ");
        sor.Ayaat.Add("فَأَوْحَيْنٰا إِلَيْهِ أَنِ اصْنَعِ الْفُلْكَ بِأَعْيُنِنٰا وَ وَحْيِنٰا فَإِذٰا جٰاءَ أَمْرُنٰا وَ فٰارَ التَّنُّورُ فَاسْلُكْ فِيهٰا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَ أَهْلَكَ إِلاّٰ مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْ وَ لاٰ تُخٰاطِبْنِي فِي الَّذِينَ ظَلَمُوا إِنَّهُمْ مُغْرَقُونَ");
        sor.Ayaat.Add("فَإِذَا اسْتَوَيْتَ أَنْتَ وَ مَنْ مَعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذِي نَجّٰانٰا مِنَ الْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ قُلْ رَبِّ أَنْزِلْنِي مُنْزَلاً مُبٰارَكاً وَ أَنْتَ خَيْرُ الْمُنْزِلِينَ");
        sor.Ayaat.Add("إِنَّ فِي ذٰلِكَ لَآيٰاتٍ وَ إِنْ كُنّٰا لَمُبْتَلِينَ");
        sor.Ayaat.Add("ثُمَّ أَنْشَأْنٰا مِنْ بَعْدِهِمْ قَرْناً آخَرِينَ");
        sor.Ayaat.Add("فَأَرْسَلْنٰا فِيهِمْ رَسُولاً مِنْهُمْ أَنِ اعْبُدُوا اللّٰهَ مٰا لَكُمْ مِنْ إِلٰهٍ غَيْرُهُ أَ فَلاٰ تَتَّقُونَ");
        sor.Ayaat.Add("وَ قٰالَ الْمَلَأُ مِنْ قَوْمِهِ الَّذِينَ كَفَرُوا وَ كَذَّبُوا بِلِقٰاءِ الْآخِرَةِ وَ أَتْرَفْنٰاهُمْ فِي الْحَيٰاةِ الدُّنْيٰا مٰا هٰذٰا إِلاّٰ بَشَرٌ مِثْلُكُمْ يَأْكُلُ مِمّٰا تَأْكُلُونَ مِنْهُ وَ يَشْرَبُ مِمّٰا تَشْرَبُونَ");
        sor.Ayaat.Add("وَ لَئِنْ أَطَعْتُمْ بَشَراً مِثْلَكُمْ إِنَّكُمْ إِذاً لَخٰاسِرُونَ");
        sor.Ayaat.Add("أَ يَعِدُكُمْ أَنَّكُمْ إِذٰا مِتُّمْ وَ كُنْتُمْ تُرٰاباً وَ عِظٰاماً أَنَّكُمْ مُخْرَجُونَ");
        sor.Ayaat.Add("هَيْهٰاتَ هَيْهٰاتَ لِمٰا تُوعَدُونَ");
        sor.Ayaat.Add("إِنْ هِيَ إِلاّٰ حَيٰاتُنَا الدُّنْيٰا نَمُوتُ وَ نَحْيٰا وَ مٰا نَحْنُ بِمَبْعُوثِينَ");
        sor.Ayaat.Add("إِنْ هُوَ إِلاّٰ رَجُلٌ افْتَرىٰ عَلَى اللّٰهِ كَذِباً وَ مٰا نَحْنُ لَهُ بِمُؤْمِنِينَ");
        sor.Ayaat.Add("قٰالَ رَبِّ انْصُرْنِي بِمٰا كَذَّبُونِ");
        sor.Ayaat.Add("قٰالَ عَمّٰا قَلِيلٍ لَيُصْبِحُنَّ نٰادِمِينَ");
        sor.Ayaat.Add("فَأَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنٰاهُمْ غُثٰاءً فَبُعْداً لِلْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("ثُمَّ أَنْشَأْنٰا مِنْ بَعْدِهِمْ قُرُوناً آخَرِينَ");
        sor.Ayaat.Add("مٰا تَسْبِقُ مِنْ أُمَّةٍ أَجَلَهٰا وَ مٰا يَسْتَأْخِرُونَ");
        sor.Ayaat.Add("ثُمَّ أَرْسَلْنٰا رُسُلَنٰا تَتْرٰا كُلَّ مٰا جٰاءَ أُمَّةً رَسُولُهٰا كَذَّبُوهُ فَأَتْبَعْنٰا بَعْضَهُمْ بَعْضاً وَ جَعَلْنٰاهُمْ أَحٰادِيثَ فَبُعْداً لِقَوْمٍ لاٰ يُؤْمِنُونَ");
        sor.Ayaat.Add("ثُمَّ أَرْسَلْنٰا مُوسىٰ وَ أَخٰاهُ هٰارُونَ بِآيٰاتِنٰا وَ سُلْطٰانٍ مُبِينٍ");
        sor.Ayaat.Add("إِلىٰ فِرْعَوْنَ وَ مَلاَئِهِ فَاسْتَكْبَرُوا وَ كٰانُوا قَوْماً عٰالِينَ");
        sor.Ayaat.Add("فَقٰالُوا أَ نُؤْمِنُ لِبَشَرَيْنِ مِثْلِنٰا وَ قَوْمُهُمٰا لَنٰا عٰابِدُونَ");
        sor.Ayaat.Add("فَكَذَّبُوهُمٰا فَكٰانُوا مِنَ الْمُهْلَكِينَ");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا مُوسَى الْكِتٰابَ لَعَلَّهُمْ يَهْتَدُونَ");
        sor.Ayaat.Add("وَ جَعَلْنَا ابْنَ مَرْيَمَ وَ أُمَّهُ آيَةً وَ آوَيْنٰاهُمٰا إِلىٰ رَبْوَةٍ ذٰاتِ قَرٰارٍ وَ مَعِينٍ");
        sor.Ayaat.Add("يٰا أَيُّهَا الرُّسُلُ كُلُوا مِنَ الطَّيِّبٰاتِ وَ اعْمَلُوا صٰالِحاً إِنِّي بِمٰا تَعْمَلُونَ عَلِيمٌ");
        sor.Ayaat.Add("وَ إِنَّ هٰذِهِ أُمَّتُكُمْ أُمَّةً وٰاحِدَةً وَ أَنَا رَبُّكُمْ فَاتَّقُونِ");
        sor.Ayaat.Add("فَتَقَطَّعُوا أَمْرَهُمْ بَيْنَهُمْ زُبُراً كُلُّ حِزْبٍ بِمٰا لَدَيْهِمْ فَرِحُونَ");
        sor.Ayaat.Add("فَذَرْهُمْ فِي غَمْرَتِهِمْ حَتّٰى حِينٍ");
        sor.Ayaat.Add("أَ يَحْسَبُونَ أَنَّمٰا نُمِدُّهُمْ بِهِ مِنْ مٰالٍ وَ بَنِينَ");
        sor.Ayaat.Add("نُسٰارِعُ لَهُمْ فِي الْخَيْرٰاتِ بَلْ لاٰ يَشْعُرُونَ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ هُمْ مِنْ خَشْيَةِ رَبِّهِمْ مُشْفِقُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ بِآيٰاتِ رَبِّهِمْ يُؤْمِنُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ هُمْ بِرَبِّهِمْ لاٰ يُشْرِكُونَ");
        sor.Ayaat.Add("وَ الَّذِينَ يُؤْتُونَ مٰا آتَوْا وَ قُلُوبُهُمْ وَجِلَةٌ أَنَّهُمْ إِلىٰ رَبِّهِمْ رٰاجِعُونَ");
        sor.Ayaat.Add("أُولٰئِكَ يُسٰارِعُونَ فِي الْخَيْرٰاتِ وَ هُمْ لَهٰا سٰابِقُونَ");
        sor.Ayaat.Add("وَ لاٰ نُكَلِّفُ نَفْساً إِلاّٰ وُسْعَهٰا وَ لَدَيْنٰا كِتٰابٌ يَنْطِقُ بِالْحَقِّ وَ هُمْ لاٰ يُظْلَمُونَ");
        sor.Ayaat.Add("بَلْ قُلُوبُهُمْ فِي غَمْرَةٍ مِنْ هٰذٰا وَ لَهُمْ أَعْمٰالٌ مِنْ دُونِ ذٰلِكَ هُمْ لَهٰا عٰامِلُونَ");
        sor.Ayaat.Add("حَتّٰى إِذٰا أَخَذْنٰا مُتْرَفِيهِمْ بِالْعَذٰابِ إِذٰا هُمْ يَجْأَرُونَ");
        sor.Ayaat.Add("لاٰ تَجْأَرُوا الْيَوْمَ إِنَّكُمْ مِنّٰا لاٰ تُنْصَرُونَ");
        sor.Ayaat.Add("قَدْ كٰانَتْ آيٰاتِي تُتْلىٰ عَلَيْكُمْ فَكُنْتُمْ عَلىٰ أَعْقٰابِكُمْ تَنْكِصُونَ");
        sor.Ayaat.Add("مُسْتَكْبِرِينَ بِهِ سٰامِراً تَهْجُرُونَ");
        sor.Ayaat.Add("أَ فَلَمْ يَدَّبَّرُوا الْقَوْلَ أَمْ جٰاءَهُمْ مٰا لَمْ يَأْتِ آبٰاءَهُمُ الْأَوَّلِينَ");
        sor.Ayaat.Add("أَمْ لَمْ يَعْرِفُوا رَسُولَهُمْ فَهُمْ لَهُ مُنْكِرُونَ");
        sor.Ayaat.Add("أَمْ يَقُولُونَ بِهِ جِنَّةٌ بَلْ جٰاءَهُمْ بِالْحَقِّ وَ أَكْثَرُهُمْ لِلْحَقِّ كٰارِهُونَ");
        sor.Ayaat.Add("وَ لَوِ اتَّبَعَ الْحَقُّ أَهْوٰاءَهُمْ لَفَسَدَتِ السَّمٰاوٰاتُ وَ الْأَرْضُ وَ مَنْ فِيهِنَّ بَلْ أَتَيْنٰاهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُعْرِضُونَ");
        sor.Ayaat.Add("أَمْ تَسْأَلُهُمْ خَرْجاً فَخَرٰاجُ رَبِّكَ خَيْرٌ وَ هُوَ خَيْرُ الرّٰازِقِينَ");
        sor.Ayaat.Add("وَ إِنَّكَ لَتَدْعُوهُمْ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ إِنَّ الَّذِينَ لاٰ يُؤْمِنُونَ بِالْآخِرَةِ عَنِ الصِّرٰاطِ لَنٰاكِبُونَ");
        sor.Ayaat.Add("وَ لَوْ رَحِمْنٰاهُمْ وَ كَشَفْنٰا مٰا بِهِمْ مِنْ ضُرٍّ لَلَجُّوا فِي طُغْيٰانِهِمْ يَعْمَهُونَ");
        sor.Ayaat.Add("وَ لَقَدْ أَخَذْنٰاهُمْ بِالْعَذٰابِ فَمَا اسْتَكٰانُوا لِرَبِّهِمْ وَ مٰا يَتَضَرَّعُونَ");
        sor.Ayaat.Add("حَتّٰى إِذٰا فَتَحْنٰا عَلَيْهِمْ بٰاباً ذٰا عَذٰابٍ شَدِيدٍ إِذٰا هُمْ فِيهِ مُبْلِسُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي أَنْشَأَ لَكُمُ السَّمْعَ وَ الْأَبْصٰارَ وَ الْأَفْئِدَةَ قَلِيلاً مٰا تَشْكُرُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَ إِلَيْهِ تُحْشَرُونَ");
        sor.Ayaat.Add("وَ هُوَ الَّذِي يُحْيِي وَ يُمِيتُ وَ لَهُ اخْتِلاٰفُ اللَّيْلِ وَ النَّهٰارِ أَ فَلاٰ تَعْقِلُونَ");
        sor.Ayaat.Add("بَلْ قٰالُوا مِثْلَ مٰا قٰالَ الْأَوَّلُونَ");
        sor.Ayaat.Add("قٰالُوا أَ إِذٰا مِتْنٰا وَ كُنّٰا تُرٰاباً وَ عِظٰاماً أَ إِنّٰا لَمَبْعُوثُونَ");
        sor.Ayaat.Add("لَقَدْ وُعِدْنٰا نَحْنُ وَ آبٰاؤُنٰا هٰذٰا مِنْ قَبْلُ إِنْ هٰذٰا إِلاّٰ أَسٰاطِيرُ الْأَوَّلِينَ");
        sor.Ayaat.Add("قُلْ لِمَنِ الْأَرْضُ وَ مَنْ فِيهٰا إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("سَيَقُولُونَ لِلّٰهِ قُلْ أَ فَلاٰ تَذَكَّرُونَ");
        sor.Ayaat.Add("قُلْ مَنْ رَبُّ السَّمٰاوٰاتِ السَّبْعِ وَ رَبُّ الْعَرْشِ الْعَظِيمِ");
        sor.Ayaat.Add("سَيَقُولُونَ لِلّٰهِ قُلْ أَ فَلاٰ تَتَّقُونَ");
        sor.Ayaat.Add("قُلْ مَنْ بِيَدِهِ مَلَكُوتُ كُلِّ شَيْءٍ وَ هُوَ يُجِيرُ وَ لاٰ يُجٰارُ عَلَيْهِ إِنْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("سَيَقُولُونَ لِلّٰهِ قُلْ فَأَنّٰى تُسْحَرُونَ");
        sor.Ayaat.Add("بَلْ أَتَيْنٰاهُمْ بِالْحَقِّ وَ إِنَّهُمْ لَكٰاذِبُونَ");
        sor.Ayaat.Add("مَا اتَّخَذَ اللّٰهُ مِنْ وَلَدٍ وَ مٰا كٰانَ مَعَهُ مِنْ إِلٰهٍ إِذاً لَذَهَبَ كُلُّ إِلٰهٍ بِمٰا خَلَقَ وَ لَعَلاٰ بَعْضُهُمْ عَلىٰ بَعْضٍ سُبْحٰانَ اللّٰهِ عَمّٰا يَصِفُونَ");
        sor.Ayaat.Add("عٰالِمِ الْغَيْبِ وَ الشَّهٰادَةِ فَتَعٰالىٰ عَمّٰا يُشْرِكُونَ");
        sor.Ayaat.Add("قُلْ رَبِّ إِمّٰا تُرِيَنِّي مٰا يُوعَدُونَ");
        sor.Ayaat.Add("رَبِّ فَلاٰ تَجْعَلْنِي فِي الْقَوْمِ الظّٰالِمِينَ");
        sor.Ayaat.Add("وَ إِنّٰا عَلىٰ أَنْ نُرِيَكَ مٰا نَعِدُهُمْ لَقٰادِرُونَ");
        sor.Ayaat.Add("ادْفَعْ بِالَّتِي هِيَ أَحْسَنُ السَّيِّئَةَ نَحْنُ أَعْلَمُ بِمٰا يَصِفُونَ");
        sor.Ayaat.Add("وَ قُلْ رَبِّ أَعُوذُ بِكَ مِنْ هَمَزٰاتِ الشَّيٰاطِينِ");
        sor.Ayaat.Add("وَ أَعُوذُ بِكَ رَبِّ أَنْ يَحْضُرُونِ");
        sor.Ayaat.Add("حَتّٰى إِذٰا جٰاءَ أَحَدَهُمُ الْمَوْتُ قٰالَ رَبِّ ارْجِعُونِ");
        sor.Ayaat.Add("لَعَلِّي أَعْمَلُ صٰالِحاً فِيمٰا تَرَكْتُ كَلاّٰ إِنَّهٰا كَلِمَةٌ هُوَ قٰائِلُهٰا وَ مِنْ وَرٰائِهِمْ بَرْزَخٌ إِلىٰ يَوْمِ يُبْعَثُونَ");
        sor.Ayaat.Add("فَإِذٰا نُفِخَ فِي الصُّورِ فَلاٰ أَنْسٰابَ بَيْنَهُمْ يَوْمَئِذٍ وَ لاٰ يَتَسٰاءَلُونَ");
        sor.Ayaat.Add("فَمَنْ ثَقُلَتْ مَوٰازِينُهُ فَأُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("وَ مَنْ خَفَّتْ مَوٰازِينُهُ فَأُولٰئِكَ الَّذِينَ خَسِرُوا أَنْفُسَهُمْ فِي جَهَنَّمَ خٰالِدُونَ");
        sor.Ayaat.Add("تَلْفَحُ وُجُوهَهُمُ النّٰارُ وَ هُمْ فِيهٰا كٰالِحُونَ");
        sor.Ayaat.Add("أَ لَمْ تَكُنْ آيٰاتِي تُتْلىٰ عَلَيْكُمْ فَكُنْتُمْ بِهٰا تُكَذِّبُونَ");
        sor.Ayaat.Add("قٰالُوا رَبَّنٰا غَلَبَتْ عَلَيْنٰا شِقْوَتُنٰا وَ كُنّٰا قَوْماً ضٰالِّينَ");
        sor.Ayaat.Add("رَبَّنٰا أَخْرِجْنٰا مِنْهٰا فَإِنْ عُدْنٰا فَإِنّٰا ظٰالِمُونَ");
        sor.Ayaat.Add("قٰالَ اخْسَؤُا فِيهٰا وَ لاٰ تُكَلِّمُونِ");
        sor.Ayaat.Add("إِنَّهُ كٰانَ فَرِيقٌ مِنْ عِبٰادِي يَقُولُونَ رَبَّنٰا آمَنّٰا فَاغْفِرْ لَنٰا وَ ارْحَمْنٰا وَ أَنْتَ خَيْرُ الرّٰاحِمِينَ");
        sor.Ayaat.Add("فَاتَّخَذْتُمُوهُمْ سِخْرِيًّا حَتّٰى أَنْسَوْكُمْ ذِكْرِي وَ كُنْتُمْ مِنْهُمْ تَضْحَكُونَ");
        sor.Ayaat.Add("إِنِّي جَزَيْتُهُمُ الْيَوْمَ بِمٰا صَبَرُوا أَنَّهُمْ هُمُ الْفٰائِزُونَ");
        sor.Ayaat.Add("قٰالَ كَمْ لَبِثْتُمْ فِي الْأَرْضِ عَدَدَ سِنِينَ");
        sor.Ayaat.Add("قٰالُوا لَبِثْنٰا يَوْماً أَوْ بَعْضَ يَوْمٍ فَسْئَلِ الْعٰادِّينَ");
        sor.Ayaat.Add("قٰالَ إِنْ لَبِثْتُمْ إِلاّٰ قَلِيلاً لَوْ أَنَّكُمْ كُنْتُمْ تَعْلَمُونَ");
        sor.Ayaat.Add("أَ فَحَسِبْتُمْ أَنَّمٰا خَلَقْنٰاكُمْ عَبَثاً وَ أَنَّكُمْ إِلَيْنٰا لاٰ تُرْجَعُونَ");
        sor.Ayaat.Add("فَتَعٰالَى اللّٰهُ الْمَلِكُ الْحَقُّ لاٰ إِلٰهَ إِلاّٰ هُوَ رَبُّ الْعَرْشِ الْكَرِيمِ");
        sor.Ayaat.Add("وَ مَنْ يَدْعُ مَعَ اللّٰهِ إِلٰهاً آخَرَ لاٰ بُرْهٰانَ لَهُ بِهِ فَإِنَّمٰا حِسٰابُهُ عِنْدَ رَبِّهِ إِنَّهُ لاٰ يُفْلِحُ الْكٰافِرُونَ");
        sor.Ayaat.Add("وَ قُلْ رَبِّ اغْفِرْ وَ ارْحَمْ وَ أَنْتَ خَيْرُ الرّٰاحِمِينَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢۴ - النور";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ سُورَةٌ أَنْزَلْنٰاهٰا وَ فَرَضْنٰاهٰا وَ أَنْزَلْنٰا فِيهٰا آيٰاتٍ بَيِّنٰاتٍ لَعَلَّكُمْ تَذَكَّرُونَ");
        sor.Ayaat.Add("الزّٰانِيَةُ وَ الزّٰانِي فَاجْلِدُوا كُلَّ وٰاحِدٍ مِنْهُمٰا مِائَةَ جَلْدَةٍ وَ لاٰ تَأْخُذْكُمْ بِهِمٰا رَأْفَةٌ فِي دِينِ اللّٰهِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللّٰهِ وَ الْيَوْمِ الْآخِرِ وَ لْيَشْهَدْ عَذٰابَهُمٰا طٰائِفَةٌ مِنَ الْمُؤْمِنِينَ");
        sor.Ayaat.Add("الزّٰانِي لاٰ يَنْكِحُ إِلاّٰ زٰانِيَةً أَوْ مُشْرِكَةً وَ الزّٰانِيَةُ لاٰ يَنْكِحُهٰا إِلاّٰ زٰانٍ أَوْ مُشْرِكٌ وَ حُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ الَّذِينَ يَرْمُونَ الْمُحْصَنٰاتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَدٰاءَ فَاجْلِدُوهُمْ ثَمٰانِينَ جَلْدَةً وَ لاٰ تَقْبَلُوا لَهُمْ شَهٰادَةً أَبَداً وَ أُولٰئِكَ هُمُ الْفٰاسِقُونَ");
        sor.Ayaat.Add("إِلاَّ الَّذِينَ تٰابُوا مِنْ بَعْدِ ذٰلِكَ وَ أَصْلَحُوا فَإِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ الَّذِينَ يَرْمُونَ أَزْوٰاجَهُمْ وَ لَمْ يَكُنْ لَهُمْ شُهَدٰاءُ إِلاّٰ أَنْفُسُهُمْ فَشَهٰادَةُ أَحَدِهِمْ أَرْبَعُ شَهٰادٰاتٍ بِاللّٰهِ إِنَّهُ لَمِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("وَ الْخٰامِسَةُ أَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ إِنْ كٰانَ مِنَ الْكٰاذِبِينَ");
        sor.Ayaat.Add("وَ يَدْرَؤُا عَنْهَا الْعَذٰابَ أَنْ تَشْهَدَ أَرْبَعَ شَهٰادٰاتٍ بِاللّٰهِ إِنَّهُ لَمِنَ الْكٰاذِبِينَ");
        sor.Ayaat.Add("وَ الْخٰامِسَةَ أَنَّ غَضَبَ اللّٰهِ عَلَيْهٰا إِنْ كٰانَ مِنَ الصّٰادِقِينَ");
        sor.Ayaat.Add("وَ لَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكُمْ وَ رَحْمَتُهُ وَ أَنَّ اللّٰهَ تَوّٰابٌ حَكِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ جٰاؤُ بِالْإِفْكِ عُصْبَةٌ مِنْكُمْ لاٰ تَحْسَبُوهُ شَرًّا لَكُمْ بَلْ هُوَ خَيْرٌ لَكُمْ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الْإِثْمِ وَ الَّذِي تَوَلّٰى كِبْرَهُ مِنْهُمْ لَهُ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("لَوْ لاٰ إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَ الْمُؤْمِنٰاتُ بِأَنْفُسِهِمْ خَيْراً وَ قٰالُوا هٰذٰا إِفْكٌ مُبِينٌ");
        sor.Ayaat.Add("لَوْ لاٰ جٰاؤُ عَلَيْهِ بِأَرْبَعَةِ شُهَدٰاءَ فَإِذْ لَمْ يَأْتُوا بِالشُّهَدٰاءِ فَأُولٰئِكَ عِنْدَ اللّٰهِ هُمُ الْكٰاذِبُونَ");
        sor.Ayaat.Add("وَ لَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكُمْ وَ رَحْمَتُهُ فِي الدُّنْيٰا وَ الْآخِرَةِ لَمَسَّكُمْ فِيمٰا أَفَضْتُمْ فِيهِ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("إِذْ تَلَقَّوْنَهُ بِأَلْسِنَتِكُمْ وَ تَقُولُونَ بِأَفْوٰاهِكُمْ مٰا لَيْسَ لَكُمْ بِهِ عِلْمٌ وَ تَحْسَبُونَهُ هَيِّناً وَ هُوَ عِنْدَ اللّٰهِ عَظِيمٌ");
        sor.Ayaat.Add("وَ لَوْ لاٰ إِذْ سَمِعْتُمُوهُ قُلْتُمْ مٰا يَكُونُ لَنٰا أَنْ نَتَكَلَّمَ بِهٰذٰا سُبْحٰانَكَ هٰذٰا بُهْتٰانٌ عَظِيمٌ");
        sor.Ayaat.Add("يَعِظُكُمُ اللّٰهُ أَنْ تَعُودُوا لِمِثْلِهِ أَبَداً إِنْ كُنْتُمْ مُؤْمِنِينَ");
        sor.Ayaat.Add("وَ يُبَيِّنُ اللّٰهُ لَكُمُ الْآيٰاتِ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يُحِبُّونَ أَنْ تَشِيعَ الْفٰاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذٰابٌ أَلِيمٌ فِي الدُّنْيٰا وَ الْآخِرَةِ وَ اللّٰهُ يَعْلَمُ وَ أَنْتُمْ لاٰ تَعْلَمُونَ");
        sor.Ayaat.Add("وَ لَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكُمْ وَ رَحْمَتُهُ وَ أَنَّ اللّٰهَ رَؤُفٌ رَحِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَتَّبِعُوا خُطُوٰاتِ الشَّيْطٰانِ وَ مَنْ يَتَّبِعْ خُطُوٰاتِ الشَّيْطٰانِ فَإِنَّهُ يَأْمُرُ بِالْفَحْشٰاءِ وَ الْمُنْكَرِ وَ لَوْ لاٰ فَضْلُ اللّٰهِ عَلَيْكُمْ وَ رَحْمَتُهُ مٰا زَكىٰ مِنْكُمْ مِنْ أَحَدٍ أَبَداً وَ لٰكِنَّ اللّٰهَ يُزَكِّي مَنْ يَشٰاءُ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ لاٰ يَأْتَلِ أُولُوا الْفَضْلِ مِنْكُمْ وَ السَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبىٰ وَ الْمَسٰاكِينَ وَ الْمُهٰاجِرِينَ فِي سَبِيلِ اللّٰهِ وَ لْيَعْفُوا وَ لْيَصْفَحُوا أَ لاٰ تُحِبُّونَ أَنْ يَغْفِرَ اللّٰهُ لَكُمْ وَ اللّٰهُ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("إِنَّ الَّذِينَ يَرْمُونَ الْمُحْصَنٰاتِ الْغٰافِلاٰتِ الْمُؤْمِنٰاتِ لُعِنُوا فِي الدُّنْيٰا وَ الْآخِرَةِ وَ لَهُمْ عَذٰابٌ عَظِيمٌ");
        sor.Ayaat.Add("يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَ أَيْدِيهِمْ وَ أَرْجُلُهُمْ بِمٰا كٰانُوا يَعْمَلُونَ");
        sor.Ayaat.Add("يَوْمَئِذٍ يُوَفِّيهِمُ اللّٰهُ دِينَهُمُ الْحَقَّ وَ يَعْلَمُونَ أَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُبِينُ");
        sor.Ayaat.Add("الْخَبِيثٰاتُ لِلْخَبِيثِينَ وَ الْخَبِيثُونَ لِلْخَبِيثٰاتِ وَ الطَّيِّبٰاتُ لِلطَّيِّبِينَ وَ الطَّيِّبُونَ لِلطَّيِّبٰاتِ أُولٰئِكَ مُبَرَّؤُنَ مِمّٰا يَقُولُونَ لَهُمْ مَغْفِرَةٌ وَ رِزْقٌ كَرِيمٌ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لاٰ تَدْخُلُوا بُيُوتاً غَيْرَ بُيُوتِكُمْ حَتّٰى تَسْتَأْنِسُوا وَ تُسَلِّمُوا عَلىٰ أَهْلِهٰا ذٰلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ");
        sor.Ayaat.Add("فَإِنْ لَمْ تَجِدُوا فِيهٰا أَحَداً فَلاٰ تَدْخُلُوهٰا حَتّٰى يُؤْذَنَ لَكُمْ وَ إِنْ قِيلَ لَكُمُ ارْجِعُوا فَارْجِعُوا هُوَ أَزْكىٰ لَكُمْ وَ اللّٰهُ بِمٰا تَعْمَلُونَ عَلِيمٌ");
        sor.Ayaat.Add("لَيْسَ عَلَيْكُمْ جُنٰاحٌ أَنْ تَدْخُلُوا بُيُوتاً غَيْرَ مَسْكُونَةٍ فِيهٰا مَتٰاعٌ لَكُمْ وَ اللّٰهُ يَعْلَمُ مٰا تُبْدُونَ وَ مٰا تَكْتُمُونَ");
        sor.Ayaat.Add("قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصٰارِهِمْ وَ يَحْفَظُوا فُرُوجَهُمْ ذٰلِكَ أَزْكىٰ لَهُمْ إِنَّ اللّٰهَ خَبِيرٌ بِمٰا يَصْنَعُونَ");
        sor.Ayaat.Add("وَ قُلْ لِلْمُؤْمِنٰاتِ يَغْضُضْنَ مِنْ أَبْصٰارِهِنَّ وَ يَحْفَظْنَ فُرُوجَهُنَّ وَ لاٰ يُبْدِينَ زِينَتَهُنَّ إِلاّٰ مٰا ظَهَرَ مِنْهٰا وَ لْيَضْرِبْنَ بِخُمُرِهِنَّ عَلىٰ جُيُوبِهِنَّ وَ لاٰ يُبْدِينَ زِينَتَهُنَّ إِلاّٰ لِبُعُولَتِهِنَّ أَوْ آبٰائِهِنَّ أَوْ آبٰاءِ بُعُولَتِهِنَّ أَوْ أَبْنٰائِهِنَّ أَوْ أَبْنٰاءِ بُعُولَتِهِنَّ أَوْ إِخْوٰانِهِنَّ أَوْ بَنِي إِخْوٰانِهِنَّ أَوْ بَنِي أَخَوٰاتِهِنَّ أَوْ نِسٰائِهِنَّ أَوْ مٰا مَلَكَتْ أَيْمٰانُهُنَّ أَوِ التّٰابِعِينَ غَيْرِ أُولِي الْإِرْبَةِ مِنَ الرِّجٰالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلىٰ عَوْرٰاتِ النِّسٰاءِ وَ لاٰ يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مٰا يُخْفِينَ مِنْ زِينَتِهِنَّ وَ تُوبُوا إِلَى اللّٰهِ جَمِيعاً أَيُّهَا الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ");
        sor.Ayaat.Add("وَ أَنْكِحُوا الْأَيٰامىٰ مِنْكُمْ وَ الصّٰالِحِينَ مِنْ عِبٰادِكُمْ وَ إِمٰائِكُمْ إِنْ يَكُونُوا فُقَرٰاءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِهِ وَ اللّٰهُ وٰاسِعٌ عَلِيمٌ");
        sor.Ayaat.Add("وَ لْيَسْتَعْفِفِ الَّذِينَ لاٰ يَجِدُونَ نِكٰاحاً حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِهِ وَ الَّذِينَ يَبْتَغُونَ الْكِتٰابَ مِمّٰا مَلَكَتْ أَيْمٰانُكُمْ فَكٰاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْراً وَ آتُوهُمْ مِنْ مٰالِ اللّٰهِ الَّذِي آتٰاكُمْ وَ لاٰ تُكْرِهُوا فَتَيٰاتِكُمْ عَلَى الْبِغٰاءِ إِنْ أَرَدْنَ تَحَصُّناً لِتَبْتَغُوا عَرَضَ الْحَيٰاةِ الدُّنْيٰا وَ مَنْ يُكْرِهْهُنَّ فَإِنَّ اللّٰهَ مِنْ بَعْدِ إِكْرٰاهِهِنَّ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("وَ لَقَدْ أَنْزَلْنٰا إِلَيْكُمْ آيٰاتٍ مُبَيِّنٰاتٍ وَ مَثَلاً مِنَ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ وَ مَوْعِظَةً لِلْمُتَّقِينَ");
        sor.Ayaat.Add("اللّٰهُ نُورُ السَّمٰاوٰاتِ وَ الْأَرْضِ مَثَلُ نُورِهِ كَمِشْكٰاةٍ فِيهٰا مِصْبٰاحٌ الْمِصْبٰاحُ فِي زُجٰاجَةٍ الزُّجٰاجَةُ كَأَنَّهٰا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبٰارَكَةٍ زَيْتُونَةٍ لاٰ شَرْقِيَّةٍ وَ لاٰ غَرْبِيَّةٍ يَكٰادُ زَيْتُهٰا يُضِيءُ وَ لَوْ لَمْ تَمْسَسْهُ نٰارٌ نُورٌ عَلىٰ نُورٍ يَهْدِي اللّٰهُ لِنُورِهِ مَنْ يَشٰاءُ وَ يَضْرِبُ اللّٰهُ الْأَمْثٰالَ لِلنّٰاسِ وَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيمٌ");
        sor.Ayaat.Add("فِي بُيُوتٍ أَذِنَ اللّٰهُ أَنْ تُرْفَعَ وَ يُذْكَرَ فِيهَا اسْمُهُ يُسَبِّحُ لَهُ فِيهٰا بِالْغُدُوِّ وَ الْآصٰالِ");
        sor.Ayaat.Add("رِجٰالٌ لاٰ تُلْهِيهِمْ تِجٰارَةٌ وَ لاٰ بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَ إِقٰامِ الصَّلاٰةِ وَ إِيتٰاءِ الزَّكٰاةِ يَخٰافُونَ يَوْماً تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَ الْأَبْصٰارُ");
        sor.Ayaat.Add("لِيَجْزِيَهُمُ اللّٰهُ أَحْسَنَ مٰا عَمِلُوا وَ يَزِيدَهُمْ مِنْ فَضْلِهِ وَ اللّٰهُ يَرْزُقُ مَنْ يَشٰاءُ بِغَيْرِ حِسٰابٍ");
        sor.Ayaat.Add("وَ الَّذِينَ كَفَرُوا أَعْمٰالُهُمْ كَسَرٰابٍ بِقِيعَةٍ يَحْسَبُهُ الظَّمْآنُ مٰاءً حَتّٰى إِذٰا جٰاءَهُ لَمْ يَجِدْهُ شَيْئاً وَ وَجَدَ اللّٰهَ عِنْدَهُ فَوَفّٰاهُ حِسٰابَهُ وَ اللّٰهُ سَرِيعُ الْحِسٰابِ");
        sor.Ayaat.Add("أَوْ كَظُلُمٰاتٍ فِي بَحْرٍ لُجِّيٍّ يَغْشٰاهُ مَوْجٌ مِنْ فَوْقِهِ مَوْجٌ مِنْ فَوْقِهِ سَحٰابٌ ظُلُمٰاتٌ بَعْضُهٰا فَوْقَ بَعْضٍ إِذٰا أَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرٰاهٰا وَ مَنْ لَمْ يَجْعَلِ اللّٰهُ لَهُ نُوراً فَمٰا لَهُ مِنْ نُورٍ");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنَّ اللّٰهَ يُسَبِّحُ لَهُ مَنْ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ وَ الطَّيْرُ صَافّٰاتٍ كُلٌّ قَدْ عَلِمَ صَلاٰتَهُ وَ تَسْبِيحَهُ وَ اللّٰهُ عَلِيمٌ بِمٰا يَفْعَلُونَ");
        sor.Ayaat.Add("وَ لِلّٰهِ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ إِلَى اللّٰهِ الْمَصِيرُ");
        sor.Ayaat.Add("أَ لَمْ تَرَ أَنَّ اللّٰهَ يُزْجِي سَحٰاباً ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكٰاماً فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلاٰلِهِ وَ يُنَزِّلُ مِنَ السَّمٰاءِ مِنْ جِبٰالٍ فِيهٰا مِنْ بَرَدٍ فَيُصِيبُ بِهِ مَنْ يَشٰاءُ وَ يَصْرِفُهُ عَنْ مَنْ يَشٰاءُ يَكٰادُ سَنٰا بَرْقِهِ يَذْهَبُ بِالْأَبْصٰارِ");
        sor.Ayaat.Add("يُقَلِّبُ اللّٰهُ اللَّيْلَ وَ النَّهٰارَ إِنَّ فِي ذٰلِكَ لَعِبْرَةً لِأُولِي الْأَبْصٰارِ");
        sor.Ayaat.Add("وَ اللّٰهُ خَلَقَ كُلَّ دَابَّةٍ مِنْ مٰاءٍ فَمِنْهُمْ مَنْ يَمْشِي عَلىٰ بَطْنِهِ وَ مِنْهُمْ مَنْ يَمْشِي عَلىٰ رِجْلَيْنِ وَ مِنْهُمْ مَنْ يَمْشِي عَلىٰ أَرْبَعٍ يَخْلُقُ اللّٰهُ مٰا يَشٰاءُ إِنَّ اللّٰهَ عَلىٰ كُلِّ شَيْءٍ قَدِيرٌ");
        sor.Ayaat.Add("لَقَدْ أَنْزَلْنٰا آيٰاتٍ مُبَيِّنٰاتٍ وَ اللّٰهُ يَهْدِي مَنْ يَشٰاءُ إِلىٰ صِرٰاطٍ مُسْتَقِيمٍ");
        sor.Ayaat.Add("وَ يَقُولُونَ آمَنّٰا بِاللّٰهِ وَ بِالرَّسُولِ وَ أَطَعْنٰا ثُمَّ يَتَوَلّٰى فَرِيقٌ مِنْهُمْ مِنْ بَعْدِ ذٰلِكَ وَ مٰا أُولٰئِكَ بِالْمُؤْمِنِينَ");
        sor.Ayaat.Add("وَ إِذٰا دُعُوا إِلَى اللّٰهِ وَ رَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ إِذٰا فَرِيقٌ مِنْهُمْ مُعْرِضُونَ");
        sor.Ayaat.Add("وَ إِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ");
        sor.Ayaat.Add("أَ فِي قُلُوبِهِمْ مَرَضٌ أَمِ ارْتٰابُوا أَمْ يَخٰافُونَ أَنْ يَحِيفَ اللّٰهُ عَلَيْهِمْ وَ رَسُولُهُ بَلْ أُولٰئِكَ هُمُ الظّٰالِمُونَ");
        sor.Ayaat.Add("إِنَّمٰا كٰانَ قَوْلَ الْمُؤْمِنِينَ إِذٰا دُعُوا إِلَى اللّٰهِ وَ رَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَنْ يَقُولُوا سَمِعْنٰا وَ أَطَعْنٰا وَ أُولٰئِكَ هُمُ الْمُفْلِحُونَ");
        sor.Ayaat.Add("وَ مَنْ يُطِعِ اللّٰهَ وَ رَسُولَهُ وَ يَخْشَ اللّٰهَ وَ يَتَّقْهِ فَأُولٰئِكَ هُمُ الْفٰائِزُونَ");
        sor.Ayaat.Add("وَ أَقْسَمُوا بِاللّٰهِ جَهْدَ أَيْمٰانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ قُلْ لاٰ تُقْسِمُوا طٰاعَةٌ مَعْرُوفَةٌ إِنَّ اللّٰهَ خَبِيرٌ بِمٰا تَعْمَلُونَ");
        sor.Ayaat.Add("قُلْ أَطِيعُوا اللّٰهَ وَ أَطِيعُوا الرَّسُولَ فَإِنْ تَوَلَّوْا فَإِنَّمٰا عَلَيْهِ مٰا حُمِّلَ وَ عَلَيْكُمْ مٰا حُمِّلْتُمْ وَ إِنْ تُطِيعُوهُ تَهْتَدُوا وَ مٰا عَلَى الرَّسُولِ إِلاَّ الْبَلاٰغُ الْمُبِينُ");
        sor.Ayaat.Add("وَعَدَ اللّٰهُ الَّذِينَ آمَنُوا مِنْكُمْ وَ عَمِلُوا الصّٰالِحٰاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْأَرْضِ كَمَا اسْتَخْلَفَ الَّذِينَ مِنْ قَبْلِهِمْ وَ لَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ الَّذِي ارْتَضىٰ لَهُمْ وَ لَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ أَمْناً يَعْبُدُونَنِي لاٰ يُشْرِكُونَ بِي شَيْئاً وَ مَنْ كَفَرَ بَعْدَ ذٰلِكَ فَأُولٰئِكَ هُمُ الْفٰاسِقُونَ");
        sor.Ayaat.Add("وَ أَقِيمُوا الصَّلاٰةَ وَ آتُوا الزَّكٰاةَ وَ أَطِيعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ");
        sor.Ayaat.Add("لاٰ تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الْأَرْضِ وَ مَأْوٰاهُمُ النّٰارُ وَ لَبِئْسَ الْمَصِيرُ");
        sor.Ayaat.Add("يٰا أَيُّهَا الَّذِينَ آمَنُوا لِيَسْتَأْذِنْكُمُ الَّذِينَ مَلَكَتْ أَيْمٰانُكُمْ وَ الَّذِينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلاٰثَ مَرّٰاتٍ مِنْ قَبْلِ صَلاٰةِ الْفَجْرِ وَ حِينَ تَضَعُونَ ثِيٰابَكُمْ مِنَ الظَّهِيرَةِ وَ مِنْ بَعْدِ صَلاٰةِ الْعِشٰاءِ ثَلاٰثُ عَوْرٰاتٍ لَكُمْ لَيْسَ عَلَيْكُمْ وَ لاٰ عَلَيْهِمْ جُنٰاحٌ بَعْدَهُنَّ طَوّٰافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلىٰ بَعْضٍ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْآيٰاتِ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ إِذٰا بَلَغَ الْأَطْفٰالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذِينَ مِنْ قَبْلِهِمْ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ آيٰاتِهِ وَ اللّٰهُ عَلِيمٌ حَكِيمٌ");
        sor.Ayaat.Add("وَ الْقَوٰاعِدُ مِنَ النِّسٰاءِ اللاّٰتِي لاٰ يَرْجُونَ نِكٰاحاً فَلَيْسَ عَلَيْهِنَّ جُنٰاحٌ أَنْ يَضَعْنَ ثِيٰابَهُنَّ غَيْرَ مُتَبَرِّجٰاتٍ بِزِينَةٍ وَ أَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّ وَ اللّٰهُ سَمِيعٌ عَلِيمٌ");
        sor.Ayaat.Add("لَيْسَ عَلَى الْأَعْمىٰ حَرَجٌ وَ لاٰ عَلَى الْأَعْرَجِ حَرَجٌ وَ لاٰ عَلَى الْمَرِيضِ حَرَجٌ وَ لاٰ عَلىٰ أَنْفُسِكُمْ أَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ أَوْ بُيُوتِ آبٰائِكُمْ أَوْ بُيُوتِ أُمَّهٰاتِكُمْ أَوْ بُيُوتِ إِخْوٰانِكُمْ أَوْ بُيُوتِ أَخَوٰاتِكُمْ أَوْ بُيُوتِ أَعْمٰامِكُمْ أَوْ بُيُوتِ عَمّٰاتِكُمْ أَوْ بُيُوتِ أَخْوٰالِكُمْ أَوْ بُيُوتِ خٰالاٰتِكُمْ أَوْ مٰا مَلَكْتُمْ مَفٰاتِحَهُ أَوْ صَدِيقِكُمْ لَيْسَ عَلَيْكُمْ جُنٰاحٌ أَنْ تَأْكُلُوا جَمِيعاً أَوْ أَشْتٰاتاً فَإِذٰا دَخَلْتُمْ بُيُوتاً فَسَلِّمُوا عَلىٰ أَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللّٰهِ مُبٰارَكَةً طَيِّبَةً كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْآيٰاتِ لَعَلَّكُمْ تَعْقِلُونَ");
        sor.Ayaat.Add("إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللّٰهِ وَ رَسُولِهِ وَ إِذٰا كٰانُوا مَعَهُ عَلىٰ أَمْرٍ جٰامِعٍ لَمْ يَذْهَبُوا حَتّٰى يَسْتَأْذِنُوهُ إِنَّ الَّذِينَ يَسْتَأْذِنُونَكَ أُولٰئِكَ الَّذِينَ يُؤْمِنُونَ بِاللّٰهِ وَ رَسُولِهِ فَإِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ وَ اسْتَغْفِرْ لَهُمُ اللّٰهَ إِنَّ اللّٰهَ غَفُورٌ رَحِيمٌ");
        sor.Ayaat.Add("لاٰ تَجْعَلُوا دُعٰاءَ الرَّسُولِ بَيْنَكُمْ كَدُعٰاءِ بَعْضِكُمْ بَعْضاً قَدْ يَعْلَمُ اللّٰهُ الَّذِينَ يَتَسَلَّلُونَ مِنْكُمْ لِوٰاذاً فَلْيَحْذَرِ الَّذِينَ يُخٰالِفُونَ عَنْ أَمْرِهِ أَنْ تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذٰابٌ أَلِيمٌ");
        sor.Ayaat.Add("أَلاٰ إِنَّ لِلّٰهِ مٰا فِي السَّمٰاوٰاتِ وَ الْأَرْضِ قَدْ يَعْلَمُ مٰا أَنْتُمْ عَلَيْهِ وَ يَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُمْ بِمٰا عَمِلُوا وَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيمٌ");
        salam(sor);
        sor = new Soore();
        sor.Name = "٢۵ - الفرقان";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ تَبٰارَكَ الَّذِي نَزَّلَ الْفُرْقٰانَ عَلىٰ عَبْدِهِ لِيَكُونَ لِلْعٰالَمِينَ نَذِيراً");
        sor.Ayaat.Add("الَّذِي لَهُ مُلْكُ السَّمٰاوٰاتِ وَ الْأَرْضِ وَ لَمْ يَتَّخِذْ وَلَداً وَ لَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَ خَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْدِيراً");
        sor.Ayaat.Add("وَ اتَّخَذُوا مِنْ دُونِهِ آلِهَةً لاٰ يَخْلُقُونَ شَيْئاً وَ هُمْ يُخْلَقُونَ وَ لاٰ يَمْلِكُونَ لِأَنْفُسِهِمْ ضَرًّا وَ لاٰ نَفْعاً وَ لاٰ يَمْلِكُونَ مَوْتاً وَ لاٰ حَيٰاةً وَ لاٰ نُشُوراً");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ كَفَرُوا إِنْ هَذٰا إِلاّٰ إِفْكٌ افْتَرٰاهُ وَ أَعٰانَهُ عَلَيْهِ قَوْمٌ آخَرُونَ فَقَدْ جٰاؤُ ظُلْماً وَ زُوراً");
        sor.Ayaat.Add("وَ قٰالُوا أَسٰاطِيرُ الْأَوَّلِينَ اكْتَتَبَهٰا فَهِيَ تُمْلىٰ عَلَيْهِ بُكْرَةً وَ أَصِيلاً");
        sor.Ayaat.Add("قُلْ أَنْزَلَهُ الَّذِي يَعْلَمُ السِّرَّ فِي السَّمٰاوٰاتِ وَ الْأَرْضِ إِنَّهُ كٰانَ غَفُوراً رَحِيماً");
        sor.Ayaat.Add("وَ قٰالُوا مٰا لِهٰذَا الرَّسُولِ يَأْكُلُ الطَّعٰامَ وَ يَمْشِي فِي الْأَسْوٰاقِ لَوْ لاٰ أُنْزِلَ إِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذِيراً");
        sor.Ayaat.Add("أَوْ يُلْقىٰ إِلَيْهِ كَنْزٌ أَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْهٰا وَ قٰالَ الظّٰالِمُونَ إِنْ تَتَّبِعُونَ إِلاّٰ رَجُلاً مَسْحُوراً");
        sor.Ayaat.Add("انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثٰالَ فَضَلُّوا فَلاٰ يَسْتَطِيعُونَ سَبِيلاً");
        sor.Ayaat.Add("تَبٰارَكَ الَّذِي إِنْ شٰاءَ جَعَلَ لَكَ خَيْراً مِنْ ذٰلِكَ جَنّٰاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهٰارُ وَ يَجْعَلْ لَكَ قُصُوراً");
        sor.Ayaat.Add("بَلْ كَذَّبُوا بِالسّٰاعَةِ وَ أَعْتَدْنٰا لِمَنْ كَذَّبَ بِالسّٰاعَةِ سَعِيراً");
        sor.Ayaat.Add("إِذٰا رَأَتْهُمْ مِنْ مَكٰانٍ بَعِيدٍ سَمِعُوا لَهٰا تَغَيُّظاً وَ زَفِيراً");
        sor.Ayaat.Add("وَ إِذٰا أُلْقُوا مِنْهٰا مَكٰاناً ضَيِّقاً مُقَرَّنِينَ دَعَوْا هُنٰالِكَ ثُبُوراً");
        sor.Ayaat.Add("لاٰ تَدْعُوا الْيَوْمَ ثُبُوراً وٰاحِداً وَ ادْعُوا ثُبُوراً كَثِيراً");
        sor.Ayaat.Add("قُلْ أَ ذٰلِكَ خَيْرٌ أَمْ جَنَّةُ الْخُلْدِ الَّتِي وُعِدَ الْمُتَّقُونَ كٰانَتْ لَهُمْ جَزٰاءً وَ مَصِيراً");
        sor.Ayaat.Add("لَهُمْ فِيهٰا مٰا يَشٰاؤُنَ خٰالِدِينَ كٰانَ عَلىٰ رَبِّكَ وَعْداً مَسْؤُلاً");
        sor.Ayaat.Add("وَ يَوْمَ يَحْشُرُهُمْ وَ مٰا يَعْبُدُونَ مِنْ دُونِ اللّٰهِ فَيَقُولُ أَ أَنْتُمْ أَضْلَلْتُمْ عِبٰادِي هٰؤُلاٰءِ أَمْ هُمْ ضَلُّوا السَّبِيلَ");
        sor.Ayaat.Add("قٰالُوا سُبْحٰانَكَ مٰا كٰانَ يَنْبَغِي لَنٰا أَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ أَوْلِيٰاءَ وَ لٰكِنْ مَتَّعْتَهُمْ وَ آبٰاءَهُمْ حَتّٰى نَسُوا الذِّكْرَ وَ كٰانُوا قَوْماً بُوراً");
        sor.Ayaat.Add("فَقَدْ كَذَّبُوكُمْ بِمٰا تَقُولُونَ فَمٰا تَسْتَطِيعُونَ صَرْفاً وَ لاٰ نَصْراً وَ مَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذٰاباً كَبِيراً");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلاّٰ إِنَّهُمْ لَيَأْكُلُونَ الطَّعٰامَ وَ يَمْشُونَ فِي الْأَسْوٰاقِ وَ جَعَلْنٰا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَ تَصْبِرُونَ وَ كٰانَ رَبُّكَ بَصِيراً");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ لاٰ يَرْجُونَ لِقٰاءَنٰا لَوْ لاٰ أُنْزِلَ عَلَيْنَا الْمَلاٰئِكَةُ أَوْ نَرىٰ رَبَّنٰا لَقَدِ اسْتَكْبَرُوا فِي أَنْفُسِهِمْ وَ عَتَوْا عُتُوًّا كَبِيراً");
        sor.Ayaat.Add("يَوْمَ يَرَوْنَ الْمَلاٰئِكَةَ لاٰ بُشْرىٰ يَوْمَئِذٍ لِلْمُجْرِمِينَ وَ يَقُولُونَ حِجْراً مَحْجُوراً");
        sor.Ayaat.Add("وَ قَدِمْنٰا إِلىٰ مٰا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنٰاهُ هَبٰاءً مَنْثُوراً");
        sor.Ayaat.Add("أَصْحٰابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَرًّا وَ أَحْسَنُ مَقِيلاً");
        sor.Ayaat.Add("وَ يَوْمَ تَشَقَّقُ السَّمٰاءُ بِالْغَمٰامِ وَ نُزِّلَ الْمَلاٰئِكَةُ تَنْزِيلاً");
        sor.Ayaat.Add("الْمُلْكُ يَوْمَئِذٍ الْحَقُّ لِلرَّحْمٰنِ وَ كٰانَ يَوْماً عَلَى الْكٰافِرِينَ عَسِيراً");
        sor.Ayaat.Add("وَ يَوْمَ يَعَضُّ الظّٰالِمُ عَلىٰ يَدَيْهِ يَقُولُ يٰا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلاً");
        sor.Ayaat.Add("يٰا وَيْلَتىٰ لَيْتَنِي لَمْ أَتَّخِذْ فُلاٰناً خَلِيلاً");
        sor.Ayaat.Add("لَقَدْ أَضَلَّنِي عَنِ الذِّكْرِ بَعْدَ إِذْ جٰاءَنِي وَ كٰانَ الشَّيْطٰانُ لِلْإِنْسٰانِ خَذُولاً");
        sor.Ayaat.Add("وَ قٰالَ الرَّسُولُ يٰا رَبِّ إِنَّ قَوْمِي اتَّخَذُوا هٰذَا الْقُرْآنَ مَهْجُوراً");
        sor.Ayaat.Add("وَ كَذٰلِكَ جَعَلْنٰا لِكُلِّ نَبِيٍّ عَدُوًّا مِنَ الْمُجْرِمِينَ وَ كَفىٰ بِرَبِّكَ هٰادِياً وَ نَصِيراً");
        sor.Ayaat.Add("وَ قٰالَ الَّذِينَ كَفَرُوا لَوْ لاٰ نُزِّلَ عَلَيْهِ الْقُرْآنُ جُمْلَةً وٰاحِدَةً كَذٰلِكَ لِنُثَبِّتَ بِهِ فُؤٰادَكَ وَ رَتَّلْنٰاهُ تَرْتِيلاً");
        sor.Ayaat.Add("وَ لاٰ يَأْتُونَكَ بِمَثَلٍ إِلاّٰ جِئْنٰاكَ بِالْحَقِّ وَ أَحْسَنَ تَفْسِيراً");
        sor.Ayaat.Add("الَّذِينَ يُحْشَرُونَ عَلىٰ وُجُوهِهِمْ إِلىٰ جَهَنَّمَ أُوْلٰئِكَ شَرٌّ مَكٰاناً وَ أَضَلُّ سَبِيلاً");
        sor.Ayaat.Add("وَ لَقَدْ آتَيْنٰا مُوسَى الْكِتٰابَ وَ جَعَلْنٰا مَعَهُ أَخٰاهُ هٰارُونَ وَزِيراً");
        sor.Ayaat.Add("فَقُلْنَا اذْهَبٰا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيٰاتِنٰا فَدَمَّرْنٰاهُمْ تَدْمِيراً");
        sor.Ayaat.Add("وَ قَوْمَ نُوحٍ لَمّٰا كَذَّبُوا الرُّسُلَ أَغْرَقْنٰاهُمْ وَ جَعَلْنٰاهُمْ لِلنّٰاسِ آيَةً وَ أَعْتَدْنٰا لِلظّٰالِمِينَ عَذٰاباً أَلِيماً");
        sor.Ayaat.Add("وَ عٰاداً وَ ثَمُودَ وَ أَصْحٰابَ الرَّسِّ وَ قُرُوناً بَيْنَ ذٰلِكَ كَثِيراً");
        sor.Ayaat.Add("وَ كُلاًّ ضَرَبْنٰا لَهُ الْأَمْثٰالَ وَ كُلاًّ تَبَّرْنٰا تَتْبِيراً");
        sor.Ayaat.Add("وَ لَقَدْ أَتَوْا عَلَى الْقَرْيَةِ الَّتِي أُمْطِرَتْ مَطَرَ السَّوْءِ أَ فَلَمْ يَكُونُوا يَرَوْنَهٰا بَلْ كٰانُوا لاٰ يَرْجُونَ نُشُوراً");
        sor.Ayaat.Add("وَ إِذٰا رَأَوْكَ إِنْ يَتَّخِذُونَكَ إِلاّٰ هُزُواً أَ هٰذَا الَّذِي بَعَثَ اللّٰهُ رَسُولاً");
        sor.Ayaat.Add("إِنْ كٰادَ لَيُضِلُّنٰا عَنْ آلِهَتِنٰا لَوْ لاٰ أَنْ صَبَرْنٰا عَلَيْهٰا وَ سَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ الْعَذٰابَ مَنْ أَضَلُّ سَبِيلاً");
        sor.Ayaat.Add("أَ رَأَيْتَ مَنِ اتَّخَذَ إِلٰهَهُ هَوٰاهُ أَ فَأَنْتَ تَكُونُ عَلَيْهِ وَكِيلاً");
        sor.Ayaat.Add("أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ إِنْ هُمْ إِلاّٰ كَالْأَنْعٰامِ بَلْ هُمْ أَضَلُّ سَبِيلاً");
        sor.Ayaat.Add("أَ لَمْ تَرَ إِلىٰ رَبِّكَ كَيْفَ مَدَّ الظِّلَّ وَ لَوْ شٰاءَ لَجَعَلَهُ سٰاكِناً ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيلاً");
        sor.Ayaat.Add("ثُمَّ قَبَضْنٰاهُ إِلَيْنٰا قَبْضاً يَسِيراً");
        sor.Ayaat.Add("وَ هُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِبٰاساً وَ النَّوْمَ سُبٰاتاً وَ جَعَلَ النَّهٰارَ نُشُوراً");
        sor.Ayaat.Add("وَ هُوَ الَّذِي أَرْسَلَ الرِّيٰاحَ بُشْراً بَيْنَ يَدَيْ رَحْمَتِهِ وَ أَنْزَلْنٰا مِنَ السَّمٰاءِ مٰاءً طَهُوراً");
        sor.Ayaat.Add("لِنُحْيِيَ بِهِ بَلْدَةً مَيْتاً وَ نُسْقِيَهُ مِمّٰا خَلَقْنٰا أَنْعٰاماً وَ أَنٰاسِيَّ كَثِيراً");
        sor.Ayaat.Add("وَ لَقَدْ صَرَّفْنٰاهُ بَيْنَهُمْ لِيَذَّكَّرُوا فَأَبىٰ أَكْثَرُ النّٰاسِ إِلاّٰ كُفُوراً");
        sor.Ayaat.Add("وَ لَوْ شِئْنٰا لَبَعَثْنٰا فِي كُلِّ قَرْيَةٍ نَذِيراً");
        sor.Ayaat.Add("فَلاٰ تُطِعِ الْكٰافِرِينَ وَ جٰاهِدْهُمْ بِهِ جِهٰاداً كَبِيراً");
        sor.Ayaat.Add("وَ هُوَ الَّذِي مَرَجَ الْبَحْرَيْنِ هٰذٰا عَذْبٌ فُرٰاتٌ وَ هٰذٰا مِلْحٌ أُجٰاجٌ وَ جَعَلَ بَيْنَهُمٰا بَرْزَخاً وَ حِجْراً مَحْجُوراً");
        sor.Ayaat.Add("وَ هُوَ الَّذِي خَلَقَ مِنَ الْمٰاءِ بَشَراً فَجَعَلَهُ نَسَباً وَ صِهْراً وَ كٰانَ رَبُّكَ قَدِيراً");
        sor.Ayaat.Add("وَ يَعْبُدُونَ مِنْ دُونِ اللّٰهِ مٰا لاٰ يَنْفَعُهُمْ وَ لاٰ يَضُرُّهُمْ وَ كٰانَ الْكٰافِرُ عَلىٰ رَبِّهِ ظَهِيراً");
        sor.Ayaat.Add("وَ مٰا أَرْسَلْنٰاكَ إِلاّٰ مُبَشِّراً وَ نَذِيراً");
        sor.Ayaat.Add("قُلْ مٰا أَسْئَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلاّٰ مَنْ شٰاءَ أَنْ يَتَّخِذَ إِلىٰ رَبِّهِ سَبِيلاً");
        sor.Ayaat.Add("وَ تَوَكَّلْ عَلَى الْحَيِّ الَّذِي لاٰ يَمُوتُ وَ سَبِّحْ بِحَمْدِهِ وَ كَفىٰ بِهِ بِذُنُوبِ عِبٰادِهِ خَبِيراً");
        sor.Ayaat.Add("الَّذِي خَلَقَ السَّمٰاوٰاتِ وَ الْأَرْضَ وَ مٰا بَيْنَهُمٰا فِي سِتَّةِ أَيّٰامٍ ثُمَّ اسْتَوىٰ عَلَى الْعَرْشِ الرَّحْمٰنُ فَسْئَلْ بِهِ خَبِيراً");
        sor.Ayaat.Add("وَ إِذٰا قِيلَ لَهُمُ اسْجُدُوا لِلرَّحْمٰنِ قٰالُوا وَ مَا الرَّحْمٰنُ أَ نَسْجُدُ لِمٰا تَأْمُرُنٰا وَ زٰادَهُمْ نُفُوراً");
        sor.Ayaat.Add("تَبٰارَكَ الَّذِي جَعَلَ فِي السَّمٰاءِ بُرُوجاً وَ جَعَلَ فِيهٰا سِرٰاجاً وَ قَمَراً مُنِيراً");
        sor.Ayaat.Add("وَ هُوَ الَّذِي جَعَلَ اللَّيْلَ وَ النَّهٰارَ خِلْفَةً لِمَنْ أَرٰادَ أَنْ يَذَّكَّرَ أَوْ أَرٰادَ شُكُوراً");
        sor.Ayaat.Add("وَ عِبٰادُ الرَّحْمٰنِ الَّذِينَ يَمْشُونَ عَلَى الْأَرْضِ هَوْناً وَ إِذٰا خٰاطَبَهُمُ الْجٰاهِلُونَ قٰالُوا سَلاٰماً");
        sor.Ayaat.Add("وَ الَّذِينَ يَبِيتُونَ لِرَبِّهِمْ سُجَّداً وَ قِيٰاماً");
        sor.Ayaat.Add("وَ الَّذِينَ يَقُولُونَ رَبَّنَا اصْرِفْ عَنّٰا عَذٰابَ جَهَنَّمَ إِنَّ عَذٰابَهٰا كٰانَ غَرٰاماً");
