        sor = new Soore();
        sor.Name = "١١٠ - النصر";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ إِذٰا جٰاءَ نَصْرُ اللّٰهِ وَ الْفَتْحُ");
        sor.Ayaat.Add("وَ رَأَيْتَ النّٰاسَ يَدْخُلُونَ فِي دِينِ اللّٰهِ أَفْوٰاجاً");
        sor.Ayaat.Add("فَسَبِّحْ بِحَمْدِ رَبِّكَ وَ اسْتَغْفِرْهُ إِنَّهُ كٰانَ تَوّٰاباً");
        salam(sor);
        sor = new Soore();
        sor.Name = "١١١ - المسد | تبت";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ تَبَّتْ يَدٰا أَبِي لَهَبٍ وَ تَبَّ");
        sor.Ayaat.Add("مٰا أَغْنىٰ عَنْهُ مٰالُهُ وَ مٰا كَسَبَ");
        sor.Ayaat.Add("سَيَصْلىٰ نٰاراً ذٰاتَ لَهَبٍ");
        sor.Ayaat.Add("وَ امْرَأَتُهُ حَمّٰالَةَ الْحَطَبِ");
        sor.Ayaat.Add("فِي جِيدِهٰا حَبْلٌ مِنْ مَسَدٍ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١١٢ - الإخلاص | توحید";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ قُلْ هُوَ اللّٰهُ أَحَدٌ");
        sor.Ayaat.Add("اللّٰهُ الصَّمَدُ");
        sor.Ayaat.Add("لَمْ يَلِدْ وَ لَمْ يُولَدْ");
        sor.Ayaat.Add("وَ لَمْ يَكُنْ لَهُ كُفُواً أَحَدٌ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١١٣ - الفلق";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ الْفَلَقِ");
        sor.Ayaat.Add("مِنْ شَرِّ مٰا خَلَقَ");
        sor.Ayaat.Add("وَ مِنْ شَرِّ غٰاسِقٍ إِذٰا وَقَبَ");
        sor.Ayaat.Add("وَ مِنْ شَرِّ النَّفّٰاثٰاتِ فِي الْعُقَدِ");
        sor.Ayaat.Add("وَ مِنْ شَرِّ حٰاسِدٍ إِذٰا حَسَدَ");
        salam(sor);
        sor = new Soore();
        sor.Name = "١١۴ - الناس";
        sor.Ayaat.Add("بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيمِ قُلْ أَعُوذُ بِرَبِّ النّٰاسِ");
        sor.Ayaat.Add("مَلِكِ النّٰاسِ");
        sor.Ayaat.Add("إِلٰهِ النّٰاسِ");
        sor.Ayaat.Add("مِنْ شَرِّ الْوَسْوٰاسِ الْخَنّٰاسِ");
        sor.Ayaat.Add("الَّذِي يُوَسْوِسُ فِي صُدُورِ النّٰاسِ");
        sor.Ayaat.Add("مِنَ الْجِنَّةِ وَ النّٰاسِ");
        salam(sor);
    }
}
